  [3095] = {
    [[Einfache Rolle]],
    { [[Ach ja, während Ihr unterwegs wart, um Euch um die hirnlose Geißel zu kümmern, kam diese Schriftrolle für Euch an. Ich würde meinen, dass es sich um etwas Wichtiges handelt, denn sie scheint das Siegel des Kriegerlehrers Dannal zu tragen. Ich würde mir die Zeit nehmen, sie zu lesen, bevor ich wieder losziehe.]], },
    { [[Lest die einfache Schriftrolle und sprecht mit Dannal Stern in Todesend.]], },
  },
  [3096] = {
    [[Verschlüsselte Rolle]],
    { [[Ach ja, während Ihr unterwegs wart, um Euch um die hirnlose Geißel zu kümmern, kam diese Schriftrolle für Euch an. Ich würde meinen, dass es sich um etwas Wichtiges handelt, denn sie scheint das Siegel des Schurkenlehrers David Trias zu tragen. Ich würde mir die Zeit nehmen, sie zu lesen, bevor ich wieder losziehe.]], },
    { [[Lest die verschlüsselte Schriftrolle und sprecht mit David Trias in Todesend.]], },
  },
