  [3100] = {
    [[Einfacher Brief]],
    { [[Ich wurde darum gebeten, Euch hierauf aufmerksam zu machen, sobald Ihr mit den Schwarzfelsspionen fertiggeworden seid, <name>. Es scheint sich um einen versiegelten Brief mit den Insignien von Llane, unserem örtlichen Kriegerlehrer, zu handeln. Ich würde nicht zögern, ihn zu lesen, bevor Ihr irgendwelchen anderen Geschäften hier in der Abtei nachgeht.]], },
    { [[Lest den einfachen Brief und sprecht mit Llane Beshere in der Abtei von Nordhain.]], },
  },
  [3101] = {
    [[Geweihter Brief]],
    { [[Ich wurde darum gebeten, Euch hierauf aufmerksam zu machen, sobald Ihr mit den Schwarzfelsspionen fertiggeworden seid, <name>. Es scheint sich um einen versiegelten Brief mit den Insignien von Bruder Sammuel, unserem örtlichen Paladinlehrer, zu handeln. Ich würde nicht zögern, ihn zu lesen, bevor Ihr irgendwelchen anderen Geschäften hier in der Abtei nachgeht.]], },
    { [[Lest den geweihten Brief und sprecht mit Bruder Sammuel in der Abtei von Nordhain.]], },
  },
  [3102] = {
    [[Verschlüsselter Brief]],
    { [[Ich wurde darum gebeten, Euch hierauf aufmerksam zu machen, sobald Ihr mit den Schwarzfelsspionen fertiggeworden seid, <name>. Es scheint sich um einen versiegelten Brief mit den Insignien von Jorik, einem unserer örtlichen Ausbilder, zu handeln. Ich würde nicht zögern, ihn zu lesen, bevor Ihr irgendwelchen anderen Geschäften hier in der Abtei nachgeht.]], },
    { [[Lest den verschlüsselten Brief und sprecht mit Jorik Kerridan im Stall hinter der Abtei von Nordhain.]], },
  },
  [3103] = {
    [[Geheiligter Brief]],
    { [[Ich wurde darum gebeten, Euch hierauf aufmerksam zu machen, sobald Ihr mit den Schwarzfelsspionen fertiggeworden seid, <name>. Es scheint sich um einen versiegelten Brief mit den Insignien von Priesterin Anetta, unserer örtlichen Priesterlehrerin, zu handeln. Ich würde nicht zögern, ihn zu lesen, bevor Ihr irgendwelchen anderen Geschäften hier in der Abtei nachgeht.]], },
    { [[Lest den geheiligten Brief und sprecht mit Priesterin Anetta in der Abtei von Nordhain.]], },
  },
  [3104] = {
    [[Glyphenbrief]],
    { [[Ich wurde darum gebeten, Euch hierauf aufmerksam zu machen, sobald Ihr mit den Schwarzfelsspionen fertiggeworden seid, <name>. Es scheint sich um einen versiegelten Brief mit den Insignien von Khelden, unserem örtlichen Magierlehrer, zu handeln. Ich würde nicht zögern, ihn zu lesen, bevor Ihr irgendwelchen anderen Geschäften hier in der Abtei nachgeht.]], },
