  },
  [3105] = {
    [[Besudelter Brief]],
    { [[Ich wurde darum gebeten, Euch hierauf aufmerksam zu machen, sobald Ihr mit den Schwarzfelsspionen fertiggeworden seid, <name>. Es scheint sich um einen versiegelten Brief mit den Insignien von Drusilla, einer unserer örtlichen Ausbilderinnen, zu handeln. Ich würde nicht zögern, ihn zu lesen, bevor Ihr irgendwelchen anderen Geschäften hier in der Abtei nachgeht.]], },
    { [[Lest den besudelten Brief und sprecht mit Drusilla La Salle nahe der Abtei von Nordhain.]], },
  },
  [3106] = {
    [[Einfache Rune]],
    { [[<name>, diese Rune wurde mir gegeben, damit ich sie an Euch weitergebe. Nehmt Euch einen Moment Zeit, sie zu lesen, wenn Ihr könnt. Ich denke mal, es kommt von dem Kriegerlehrer Thran. Werft doch am besten einen Blick darauf und sucht ihn in Ambossar auf.]], },
    { [[Lest die einfache Rune und sprecht mit Thran Khorman im Eisklammtal.]], },
  },
  [3107] = {
    [[Geweihte Rune]],
    { [[<name>, diese Rune wurde mir gegeben, damit ich sie an Euch weitergebe. Nehmt Euch einen Moment Zeit, sie zu lesen, wenn Ihr könnt. Ich denke mal, es kommt von dem Paladinlehrer Bromos. Werft doch am besten ein Blick darauf und sucht ihn in Ambossar auf.]], },
    { [[Lest die geweihte Rune und sprecht mit Bromos Grummler in Ambossar.]], },
  },
  [3108] = {
    [[Geätzte Rune]],
    { [[<name>, diese Rune wurde mir gegeben, damit ich sie an Euch weitergebe. Nehmt Euch einen Moment Zeit, sie zu lesen, wenn Ihr könnt. Ich denke mal, es kommt von dem Jägerlehrer Thorgas. Werft doch am besten einen Blick darauf und sucht ihn in Ambossar auf.]], },
    { [[Lest die geätzte Rune und sprecht mit Thorgas Grimson im Ambossar.]], },
  },
  [3109] = {
    [[Verschlüsselte Rune]],
    { [[<name>, diese Rune wurde mir gegeben, damit ich sie an Euch weitergebe. Nehmt Euch einen Moment Zeit, sie zu lesen, wenn Ihr könnt. Ich denke mal, es kommt von dem Schurkenlehrer Solm. Werft doch am besten ein Blick darauf und sucht ihn in Ambossar auf.]], },
    { [[Lest die verschlüsselte Rune und sprecht mit Solm Hargrin im Ambossar.]], },
  },
  [3110] = {
    [[Geheiligte Rune]],
    { [[<name>, diese Rune wurde mir gegeben, damit ich sie an Euch weitergebe. Nehmt Euch einen Moment Zeit, sie zu lesen, wenn Ihr könnt. Ich denke mal, es kommt von dem Priesterlehrer Branstock. Werft doch am besten ein Blick darauf und sucht ihn in Ambossar auf.]], },
    { [[Lest die geheiligte Rune und sprecht mit Branstock Khalder in Ambossar.]], },
  },
  [3112] = {
    [[Einfaches Memorandum]],
    { [[Während Ihr unterwegs wart, um mir zu helfen, wurde mir dieses Memorandum für Euch gegeben. Nehmt Euch die Zeit, es zu lesen, wenn Ihr die Gelegenheit dazu habt. Ich glaube, es kommt vom Kriegerlehrer Thran. Werft einen Blick darauf und sucht ihn dann in Ambossar auf, sobald Ihr die Gelegenheit dazu habt.]], },
    { [[Lest das einfache Memorandum und sprecht mit Thran Khorman im Eisklammtal.]], },
  },
  [3113] = {
    [[Verschlüsseltes Memorandum]],
    { [[Während Ihr unterwegs wart, um mir zu helfen, wurde mir dieses Memorandum für Euch gegeben. Nehmt Euch die Zeit, es zu lesen, wenn Ihr die Gelegenheit dazu habt. Ich glaube, es kommt vom Schurkenlehrer Solm. Werft einen Blick darauf und sucht ihn dann in Ambossar auf, sobald Ihr die Gelegenheit dazu habt.]], },
    { [[Lest das verschlüsselte Memorandum und sprecht mit Solm Hargrin im Eisklammtal.]], },
  },
  [3114] = {
    [[Glyphenmemorandum]],
    { [[Während Ihr unterwegs wart, um mir zu helfen, wurde mir dieses Memorandum für Euch gegeben. Nehmt Euch die Zeit, es zu lesen, wenn Ihr die Gelegenheit dazu habt. Ich glaube, es kommt vom Magierlehrer Marryk. Werft einen Blick darauf und sucht ihn dann in Ambossar auf, sobald Ihr die Gelegenheit dazu habt.]], },
    { [[Lest das mit Glyphen versehene Memorandum und sprecht mit Marryk Nurribit in Ambossar oberhalb des Eisklammtals.]], },
  },
  [3115] = {
    [[Besudelte Rune]],
    { [[Während Ihr unterwegs wart, um mir zu helfen, wurde mir dieses Memorandum für Euch gegeben. Nehmt Euch die Zeit, es zu lesen, wenn Ihr die Gelegenheit dazu habt. Ich glaube, es kommt vom Hexenmeisterlehrerin Saripal. Werft einen Blick darauf und sucht ihn dann in Ambossar auf, sobald Ihr die Gelegenheit dazu habt.]], [[Und passt auf Euch auf, <name>, man traut Leuten wie Euch hierzulande nicht gerade.]], },
    { [[Lest die besudelte Rune und sprecht mit Saripal Schmauchbräu in Ambossar oberhalb des Eisklammtals.]], },
  },
  [3116] = {
    [[Einfaches Siegelzeichen]],
    { [[Dieses Siegelzeichen wurde mir durch einen Boten von unserer Kriegerlehrerin Alyissia überreicht. Es sieht so aus, als wollte Alyissia ein paar Worte mit Euch wechseln, wenn Ihr Zeit habt. Lest es und bringt es dann zu ihr.]], },
    { [[Sprecht mit Alyissia in Aldrassil im Laubschattental.]], },
  },
  [3117] = {
    [[Geätztes Siegelzeichen]],
    { [[Dieses Siegelzeichen wurde mir durch einen Boten von unserer Jägerlehrerin Ayanna überreicht. Es sieht so aus, als wollte Ayanna ein paar Worte mit Euch wechseln, wenn Ihr Zeit habt. Lest es und bringt es dann zu ihr.]], },
    { [[Sprecht mit Ayanna Immerlauf in Aldrassil im Laubschattental.]], },
  },
  [3118] = {
    [[Verschlüsseltes Siegelzeichen]],
    { [[Dieses Siegelzeichen wurde mir durch einen Boten von unserem Schurkenlehrer Frahun überreicht. Es sieht so aus, als wollte Frahun ein paar Worte mit Euch wechseln, wenn Ihr Zeit habt. Lest es und bringt es dann zu ihm.]], },
    { [[Sprecht mit Frahun Schattenflüsterer in Aldrassil im Laubschattental.]], },
  },
  [3119] = {
    [[Geheiligtes Siegelzeichen]],
    { [[Dieses Siegelzeichen wurde mir durch einen Boten von unserer Priesterlehrerin Shanda überreicht. Es sieht so aus, als wollte Shanda ein paar Worte mit Euch wechseln, wenn Ihr Zeit habt. Lest es und bringt es dann zu ihr.]], },
    { [[Sprecht mit Shanda in Aldrassil im Laubschattental.]], },
  },
  [3120] = {
    [[Tiefgrünes Siegelzeichen]],
    { [[Dieses Siegelzeichen wurde mir durch einen Boten von unserem Druidenlehrer Mardant überreicht. Es sieht so aus, als wollte Mardant ein paar Worte mit Euch wechseln, wenn Ihr einen Moment Zeit habt. Lest es und bringt es dann zu ihm.]], },
    { [[Sprecht mit Mardant Starkeiche in Aldrassil im Laubschattental.]], },
  },
  [3121] = {
    [[Eine seltsame Bitte]],
    { [[Bringt den Schrumpfkopf zu Xerash Feuerklinge beim Larisspavillon.]], },
    { [[Bringt den Schrumpfkopf zu Xerash Feuerklinge beim Larisspavillon.]], },
  },
  [3122] = {
    [[Kehrt zu Hexendoktor Uzer'i zurück]],
    { [[Hier sind die Kräuter, <name>. Bringt sie zu Hexendoktor Uzer'i.]], [[Und sagt ihm, dass ich mit dem Schrumpfkopf sehr zufrieden bin.]], },
    { [[Bringt Xerashs Kräuterbeutel zu Hexendoktor Uzer'i in Feralas.]], },
  },
  [3123] = {
    [[Das Gefäß wird getestet]],
    { [[Tötet 10 Eisenfellbären oder Langzahnläufer und benutzt dann das Gefäß für Bestienmuisek, um die gefallenen Tiere zu schrumpfen und einzufangen.]], [[Bringt anschließend 10 Einheiten Bestienmuisek und das Gefäß für Bestienmuisek zu Hexendoktor Uzer'i in Feralas.]], },
    { [[Tötet 10 Eisenfellbären oder Langzahnläufer und benutzt dann das Gefäß für Bestienmuisek, um die gefallenen Tiere zu schrumpfen und einzufangen.]], [[Bringt anschließend 10 Einheiten Bestienmuisek und das Gefäß für Bestienmuisek zu Hexendoktor Uzer'i in Feralas.]], },
  },
  [3124] = {
    [[Muisek von Hippogryphen]],
    { [[Da Ihr bewiesen habt, dass Ihr vertrauenswürdig und auch in der Lage seid, das Muisekgefäß zu benutzen, möchte ich, dass Ihr eine weitere Kreatur einfangt.]], [[Hippogryphen sind seit vielen Jahren die treuen Gefährten der Nachtelfen. Ihre Geister sind loyal und unerschütterlich.]], [[Eure Aufgabe besteht darin, 10 Fransenfederhippogryphen auszuschalten, sie schnell zu schrumpfen und mit dem Gefäß einzufangen, sodass ihr Muisek erhalten bleibt. Ihr findet die Hippogryphen in der Oberen Wildnis, südlich von hier.]], },
    { [[Schaltet 10 Fransenfederhippogryphen eines beliebigen Typs aus und benutzt dann das Muisekgefäß, um sie zu schrumpfen und einzufangen.]], },
  },
  [3125] = {
    [[Muisek von Feendrachen]],
    { [[<name>, als Nächstes müsst Ihr für mich eine Kreatur fangen, die vom Aussehen her sehr zerbrechlich scheint, aber ihr mächtiger Geist ist das, was wir eigentlich besitzen wollen. Die Feendrachen, von denen ich spreche, dienen den Nachtelfen als Verbündete. Wir müssen einen von ihnen einfangen, damit auch die Horde von seiner Geisteskraft profitieren kann.]], [[Hier ist das Muisekgefäß, <name>. Schaltet 8 Grimmlingflitzer oder Grimmlingdrachen aus und fangt ihr Muisek ein. Ihr könnt sie westlich von hier finden.]], },
    { [[Schaltet 8 Grimmlingflitzer oder Grimmlingdrachen aus. Benutzt das Muisekgefäß, um die gefallenen Feendrachen zu schrumpfen und einzufangen.]], },
  },
  [3126] = {
    [[Muisek von Treants]],
    { [[Die umherwandernden Waldgänger, die überall im Wald zu finden sind, sind unser nächstes Ziel, <name>. Nachtelfen haben Treants als Verbündete rekrutiert, um gegen die Horde zu kämpfen. Sie mögen zwar langsam sein, aber als Beschützer sind sie nicht zu unterschätzen.]], [[Bald werden wir diese Macht für unsere eigenen Zwecke nutzen können.]], [[Findet 3 umherwandernde Waldgänger, schaltet sie aus und benutzt das Muisekgefäß, um sie zu schrumpfen und einzufangen.]], },
    { [[Schaltet 3 umherwandernde Waldgänger aus. Benutzt das Muisekgefäß, um die gefallenen Treants zu schrumpfen und einzufangen.]], },
  },
  [3127] = {
    [[Bergriesenmuisek]],
    { [[Bei der letzten Aufgabe, die ich Euch stelle, sollt Ihr nach Nordwesten reisen und den letzten Feind finden.]], [[Als die Brennende Legion in der Welt erschien, haben die Bergriesen ihre Berge verlassen, um den Nachtelfen zu helfen. Ihr erkennt sie an ihrer enormen Größe und daran, wie der Boden erzittert, wenn Ihr durch ihr felsiges Gelände reist.]], [[Ich kann es kaum erwarten, eine Macht von dieser Größe zu zähmen. Schaltet 7 Bergriesen aus und benutzt dann das Muisekgefäß, um ihr mächtiges Muisek einzufangen.]], },
    { [[Schaltet 7 Landgänger oder Klippenriesen aus. Benutzt das Muisekgefäß, um die gefallenen Bergriesen zu schrumpfen und einzufangen.]], },
  },
  [3128] = {
    [[Natürliche Materialien]],
    { [[Ich benötige bestimmte Materialien, um mein Vorhaben zu vervollständigen... Ihr könnt das für mich erledigen, zusammen mit den anderen Aufgaben, die ich Euch stellen werde.]], [[Ich brauche einen abgesplitterten Stamm eines Treants aus Feralas. Verkrustete Mineralien könnt Ihr an den Bergriesen und Feendrachen im Westen finden. Sucht mir einige widerstandsfähige Sehnen von den Eulenbestien im Hinterland oder den Hippogryphen von Feralas. Als Letztes benötige ich eine große Menge metallischer Fragmente von einer der Bestien, die ich erwähnt habe.]], },
    { [[Bringt 2 abgesplitterte Baumstämme, 6 verkrustete Mineralien, 20 Stücke widerstandsfähige Sehne und 40 metallische Fragmente zu Hexendoktor Uzer'i ins Camp Mojache.]], },
  },
  [3129] = {
    [[Waffen des Geistes]],
    { [[Es ist ein äußerst komplizierter Vorgang, die Waffen mit dem Muisek zu erfüllen. Aber da ich jetzt sowohl über alle erforderlichen Materialien als auch über das Muisek der von Euch eingefangenen Kreaturen verfüge, kann ich endlich beginnen. Bitte wartet hier, ich werde gleich zurück sein.]], },
    { [[Hexendoktor Uzer'i will, dass Ihr einen Moment wartet, während er die Waffen vorbereitet.]], },
  },
  [3130] = {
    [[Gegen die Hasskämme]],
    { [[Ich werde Euch jetzt zu meinem Stellvertreter Lactronicus schicken. Er hat bisher ein wachsames Auge auf die Nagastreitkräfte gehabt. Ich werde die allgemeine Lage hier in Bezug auf die Naga bewerten. Liebend gern würde ich eine ganze Armee gegen sie aussenden, aber es ist nicht unbedingt ratsam, die Festung einem direkten Angriff der Horde - oder Schlimmerem - schutzlos auszusetzen.]], [[Wie dem auch sei, Latronicus erwartet Euch. Gute Arbeit bisher, <name>. Nur weiter so.]], [[Ihr könnt wegtreten.]], },
    { [[Meldet Euch bei Latronicus Mondspeer in der Mondfederfeste.]], },
  },
  [3141] = {
    [[Loramus]],
    { [[Ihr wollt also gegen Dämonen kämpfen? Dann tätet Ihr gut daran, Euch meine Geschichte anzuhören.]], },
    { [[Lasst Loramus Thalipedes seine Geschichte erzählen.]], },
  },
  [3161] = {
    [[Gahz'ridian]],
    { [[<name>, ich bin zwar Wissenschaftler, aber im Grunde meines Herzens bin ich auch Schatzsucher!]], [[Vor langer Zeit, als Trolle dieses Land besetzt hielten, schufen sie eine große Anzahl von Ornamenten nach dem Vorbild der Gahz'rilla, einer Hydra, die sie als Göttin verehrten. Diese Ornamente werden aus einem Element herausgeschnitzt, das ich nach der Hydragöttin "Gahz'ridian" nenne.]], [[Ich selbst habe schon einige dieser Ornamente gefunden, aber ich hätte gern noch mehr, um sie zu untersuchen - vielleicht findet Ihr ja noch welche...]], [[Ich habe sogar einen Detektor erfunden, der es zu einem Kinderspiel macht, Gahz'ridian zu finden! Legt ihn einfach an und die Suche kann beginnen!]], },
    { [[Marvon Nietensucher in Tanaris möchte, dass Ihr 30 Ornamente von Gahz'ridian sammelt.]], },
  },
  [3181] = {
    [[Das Horn der Bestie]],
    { [[Das gigantische Horn hat viele Kratzer und Risse. Ein ungewöhnliches Metallobjekt klemmt in der rauen Oberfläche fest.]], [[Beim näheren Hinschauen entdeckt Ihr, dass das Metall in Wirklichkeit ein zerbrochener Axtkopf ist. In das Metall wurden diese Wörter eingeätzt:]], [[Eigentum von Gebirgsjägerin Feinkiesel.]], [[Als Ihr versucht, den zerbrochenen Axtkopf herauszuziehen, zerfällt er in unzählige Teile.]], [[Ups!]], },
    { [[Bringt Margols gigantisches Horn zu Gebirgsjägerin Feinkiesel.]], },
  },
  [3182] = {
    [[Besitznachweis]],
    { [[Ihr erwartet wirklich, dass ich eine solche Geschichte glaube? Wenn es das echte Horn wäre, dann würde meine zerbrochene Axt ja immer noch drinstecken.]], [[Hört zu, bringt diese dreiste Fälschung zu Kurator Thorius in Eisenschmiede. Falls er Eure Behauptung bestätigt, werdet Ihr eine Beweisurkunde für Eure Tat erhalten. Bringt mir diesen Beweis und ich werde Euch den Schlüssel für die Sengende Schlucht geben.]], [[Geht schon!]], },
    { [[Bringt Margols gigantisches Horn zu Kurator Thorius in Eisenschmiede.]], },
  },
  [3201] = {
    [[Endlich!]],
    { [[Ich kann kaum glauben, dass Feinkiesel die Echtheit des Horns angezweifelt hat. Unter uns, ich denke, sie ist einfach nur eifersüchtig. Ich weiß, dass sie diese Bestie gern selbst getötet hätte, nach dem, was bei dem 'Ereignis' so vorgefallen ist.]], [[Ihr braucht also eine Urkunde für Eure Tat? Nachdem Ihr nun gerade dem Museum ein solches Geschenk gemacht habt, sollte das kein Problem sein.]], [[<Kurator Thorius fängt an, ein großes Dokument auszufüllen.>]], [[Hier bitte! Das könnt Ihr Feinkiesel zurückbringen.]], },
    { [[Bringt die Urkunde für die Tat zurück zu Gebirgsjägerin Feinkiesel.]], },
  },
  [3221] = {
    [[Sprecht mit Renferrel]],
    { [[Während Ihr fort wart, um unseren Todespirschern zu helfen, hat Apotheker Renferrel Euch eine Einladung geschickt. Er hat mir keinerlei Einzelheiten mitgeteilt, aber er wollte mit Euch über die Wolfherzen sprechen, die Ihr ihm gegeben habt.]], },
    { [[Sprecht mit Apotheker Renferrel in dem Grabmal.]], },
  },
  [3261] = {
    [[Jorn Himmelsdeuter]],
    { [[Eure Zeit bei mir ist vorbei, <name>. Wenn Ihr mehr lernen wollt, müsst Ihr ins Camp Taurajo gehen und mit Jorn Himmelsdeuter sprechen. Er wird Eure Weiterbildung übernehmen.]], },
    { [[Sprecht im Camp Taurajo mit Jorn Himmelsdeuter.]], },
  },
  [3281] = {
    [[Gestohlenes Silber]],
    { [[Ihr habt zwar die Raptoren getötet, aber das Silber, das sie gestohlen haben, muss wiederbeschafft werden! Ich habe Berichte darüber, dass die Raptoren eine große Ansammlung von Nestern südlich von Ratschet haben, Raptorgründe genannt. Es ist sehr wahrscheinlich, dass sie unser gestohlenes Silber dorthin gebracht haben.]], [[Geht zu dieser Raptorzuflucht und haltet nach dem gestohlenen Silber Ausschau. Wenn Ihr es gefunden habt, kommt damit zu mir zurück.]], },
    { [[Bringt das gestohlene Silber zu Gazrog beim Wegekreuz.]], },
  },
  [3301] = {
    [[Mura Runentotem]],
    { [[Meine Klangefährtin, Mura Runentotem, ist nach Silberwald gereist, um unseren untoten Verbündeten zu helfen. Der Silberwald ist ein kranker Wald, ein sterbender Wald, und Muras Hoffnung war, ihm neues Leben zu bringen. Vielleicht kann dieses seltsame Leben, das wir im Brachland gefunden haben, ihr bei ihrer Arbeit im Silberwald helfen.]], [[Bringt eine Probe der von Euch gesammelten Schalen zu Mura nach Silberwald. Sie hält sich in dem Grabmal auf. Mit etwas Glück vermittelt ihr das Studium der Schalen den Einblick, den sie benötigt, um diesen sterbenden Ort zu heilen.]], },
    { [[Sprecht mit Mura Runentotem im Grabmal im Silberwald.]], },
  },
  [3321] = {
    [[Habt Ihr das verloren?]],
    { [[Ihr habt viel für den Orden getan, <name>. Allein die Hingabe, derer es bedarf, um hierher zu gelangen, hätte jede normale Person abgehalten. Galvan und auch ich haben keinerlei Zweifel, dass Ihr einer von uns seid.]], [[Aus diesem Grund denke ich, dass Ihr Euch eine kleine Belohnung verdient habt. Es ist nichts Besonderes, nur eine Kleinigkeit. Vielleicht sind Euch seine Eigenschaften vertraut, denn ich glaube, dass es der Grund war, warum Ihr überhaupt zu uns gekommen seid.]], },
    { [[Wartet, bis Trenton seine Arbeit erledigt hat.]], },
  },
  [3341] = {
    [[Das Ende bringen]],
    { [[Im Brachland liegt ein schreckliches Dickicht aus ineinander verschlungenen Ranken, Hügel der Klingenhauer genannt. Unsere ersten Beobachtungen haben zwar auf keine große Bedrohung schließen lassen, aber die neuesten Funde geben doch allen Grund zur Sorge...]], [[Die Stacheleber der Hügel der Klingenhauer haben sich mit der Geißel zusammengetan. Ein Lich namens Amnennar der Kältebringer beherrscht sie jetzt und nutzt die Macht seines riesigen Bewusstseins, um jede ihrer Bewegungen zu kontrollieren.]], [[Amnennar verfügt über eine direkte telepathische Verbindung zu Ner'zhul. Diese Bindung müssen wir durchtrennen, <name>. Dem Kältebringer muss ein Ende gemacht werden.]], },
    { [[Andrew Braunell will, dass Ihr Amnennar den Kältebringer tötet und ihm dessen Schädel bringt.]], },
  },
  [3361] = {
    [[Dilemma eines Flüchtlings]],
    { [[Ich war gerade auf dem Weg, zu den anderen Gnomen in Gnomeregan, aber dann ging alles so schrecklich schief!]], [[Ich hatte Ambossar mit gepackten Taschen verlassen und stapfte durch hüfthohen Schnee. Dann kam dieses alberne Erdbeben wodurch ich völlig die Orientierung verlor. Letzten Endes bin ich hier gelandet und was noch schlimmer ist, die Trolle haben all mein Zeug gestohlen!]], [[<name>, ich habe mich offensichtlich während der letzten eineinhalb Tage als völlig inkompetent erwiesen. Hättet Ihr etwas dagegen, die Lager der Trolle südwestlich von hier zu infiltrieren und meine Habseligkeiten zurückzubringen?]], },
    { [[Beschafft Euch Felix' Kasten, Felix' Truhe und Felix' Bolzeneimer.]], },
  },
  [3362] = {
    [[Disteltal]],
    { [[Habt Ihr jemals das Disteltal gesehen? Es ist ein sehr gefährlicher Ort, <name>.]], [[Diesen merkwürdigen Bestien dort traue ich nicht für eine Minute! Ich glaube, man nennt sie knorrige Distelwandler und Distelwurzelformer...]], [[Ich möchte in das Tal hinein, aber solange es dort von Distelwandlern wimmelt, kehre ich auf keinen Fall dorthin zurück. Und an dieser Stelle kommt Ihr ins Spiel - wenn Ihr einige von ihnen erledigt, würde ich vielleicht doch dorthin zurückkehren. Was meint Ihr, <name>? Helft Ihr mir?]], },
    { [[Tran'rek in Gadgetzan möchte, dass Ihr 8 knorrige Distelwandler und 8 Distelwurzelformer erledigt.]], },
  },
  [3363] = {
    [[Verderbte Liedblume]],
  },
  [3364] = {
    [[Lieferung des kochend heißen Morgengebräus]],
    { [[Bringt ein kochend heißes Morgengebräu zu Durnan Pelznaht in Ambossar hinein, bevor es innerhalb von fünf Minuten kalt wird!]], },
    { [[Bringt ein kochend heißes Morgengebräu zu Durnan Pelznaht in Ambossar hinein, bevor es innerhalb von fünf Minuten kalt wird!]], },
  },
  [3365] = {
    [[Den Becher zurückbringen]],
    { [[Das kam genau zur rechten Zeit! Es geht doch nichts über ein heißes - neee, KOCHEND HEISSES Morgengebräu an einem kalten Wintertag, um einem das Herz zu wärmen.]], [[Hier, bitte <name>... seid so nett und bringt für mich den leeren Becher zurück zu Nori, ja?]], },
    { [[Bringt Nori Stolzmut im Eisklammtal den Becher zurück.]], },
  },
  [3366] = {
    [[Der leuchtende Splitter]],
    { [[Der Splitter ist von einem glänzenden Grün, und obwohl er massiv ist, scheint etwas wie Rauch knapp unterhalb der Oberfläche herumzuwirbeln. Ihr fühlt den Zwang, in die Tiefen des Kristalls zu schauen, aber gleichzeitig fühlt Ihr auch, dass etwas sehr Beunruhigendes von ihm ausgeht.]], [[Vielleicht solltet Ihr in Ratschet nach jemandem suchen, der Euch mehr über diesen seltsamen Splitter erzählen kann.]], },
    { [[Reist nach Ratschet, um die Bedeutung des Alptraumsplitters herauszufinden.]], },
  },
  [3367] = {
    [[Suntarasteine]],
    { [[Helft Dorius, zurück nach Eisenschmiede zu kommen.]], },
    { [[Helft Dorius, zurück nach Eisenschmiede zu kommen.]], },
  },
  [3368] = {
    [[Suntarasteine]],
    { [[Bringt den angesengten Brief zu Thorius in Eisenschmiede. Er befindet sich in der Halle der Forscher.]], },
    { [[Bringt den angesengten Brief zu Thorius in Eisenschmiede. Er befindet sich in der Halle der Forscher.]], },
  },
  [3369] = {
    [[Alptraum]],
    { [[Dieser Splitter enthält große Geheimnisse; er ist die pure Essenz des Smaragdgrünen Traums. Was ich jedoch in diesem Splitter sehe, ist kein Traum, diese Art der Vision ist eher ein Alptraum.]], [[Ich möchte Euch nicht beunruhigen, <name>, aber ich denke, es ist am besten, wenn Ihr diesen Splitter so schnell wie möglich in die Hände eines weisen Druiden gebt.]], [[Ihr müsst Hamuul Runentotem auf der Anhöhe der Ältesten finden. Geht nach Donnerfels. Mehr vermag ich Euch nicht zu sagen.]], },
    { [[Bringt den Alptraumsplitter zu Hamuul Runentotem auf der Anhöhe der Ältesten.]], },
  },
  [3370] = {
    [[Alptraum]],
    { [[Dieser Splitter trägt große Geheimnisse in sich; es ist die pure Essenz des Smaragdgrünen Traums. Was ich jedoch in diesem Splitter sehe ist kein Traum, diese Art der Vision ist eher ein Alptraum.]], [[Ich möchte Euch nicht beunruhigen, <name>, aber ich denke, es ist am besten, wenn Ihr diesen Splitter in die Hände eines weisen Druiden gebt, sobald Ihr könnt...]], [[Ihr müsst Mathrengyl Bärenfährte im Zirkel des Cenarius finden. Geht nach Darnassus. Mehr vermag ich Euch nicht zu sagen.]], },
    { [[Bringt den Alptraumsplitter zu Mathrengyl Bärenfährte in Darnassus.]], },
  },
  [3371] = {
    [[Zwergengerechtigkeit]],
    { [[Wo mein Herz einst schlug, ist jetzt ein leeres Loch, <name>. Über den Verlust von Dorius werde ich so schnell nicht hinwegkommen. Ich muss seinen letzten Wunsch erfüllt sehen - diejenigen, die diesen kaltblütigen Mord durchführten, müssen der Gerechtigkeit zugeführt werden.]], [[Kehrt zur Sengenden Schlucht zurück und erfüllt seine letzte Bitte. Befreit seine Männer. Die Dunkeleisenzwerge zwingen ihre Sklaven dazu, in den schwelenden Tiefen der Schlackengrube zu arbeiten; einem ausgedehnten Minenkomplex. Sucht dort nach Dorius Männern.]], },
    { [[Kehrt zur Sengenden Schlucht zurück und sucht Dorius' Archäologenteam.]], },
  },
  [3372] = {
    [[Erlöst sie]],
    { [[Befreit sie, <race>. Selbst nachdem ihre Körper aufgegeben haben, zwingt die Macht der Suntara sie, ihre Aufgaben weiter zu erfüllen.]], [[Der Altar von Suntara ist der Schlüssel. Geht nach Norden, in die Hügel oberhalb der Höhle... Die Schattenhammerkultisten bewachen dort ein Relikt. Bringt es herbei und stellt es auf den Altar von Suntara.]], [[<Der sterbende Archäologe bricht zusammen.>]], },
    { [[Sucht das mysteriöse Artefakt, das im nordwestlichen Teil der Sengenden Schlucht von den Zwielichtverehrern bewacht wird und bringt es zum Altar von Suntara in der Schlackengrube.]], },
  },
  [3373] = {
    [[Die Essenz des Eranikus]],
    { [[Als Ihr den Edelstein berührt, ertönt eine tiefe Stimme in Eurem Geist.]], [["Sterbliches Wesen, hört diese Bitte. Ich bin Eranikus vom grünen Drachenschwarm, der Hüter dieses verfluchten Tempels. Meine Aufgabe, sicherzustellen, dass die Trolle nie wieder ihre Monstrosität von einem Gott in diese Welt bringen, ist nun fehlgeschlagen. Während ich diesen Edelstein mit einem Teil meiner magischen Essenz fülle, fühle ich, wie mein Traum sich zum Chaos hinbewegt. Ich bitte Euch... legt diesen Edelstein in den Essenzborn in meinem Unterschlupf und lasst unsere Magie dort den Makel von meiner Psyche nehmen, sodass ich endlich ruhen kann."]], },
    { [[Legt die Essenz von Eranikus in den Essenzborn, der sich in dem Versunkenen Tempel in seinem Unterschlupf befindet.]], },
  },
  [3374] = {
    [[Die Essenz des Eranikus]],
    { [[Ihr aktiviert den Schwurstein und Itharius spricht Euren Geist direkt durch den Stein an.]], [["Indem Ihr die Ladung dieses Schwursteins angenommen habt, erklärt Ihr Euch einverstanden, meiner Sache und der Sache von Yseras Drachenschwarm behilflich zu sein, und die verderbte Seele von Eranikus zu erlösen. Das erste Opfer, eines von vielen, die Ihr ohne Zweifel bringen müsst, ist, mir den Edelstein zu übergeben, der den Teil von Eranikus' Psyche gefesselt hat, der noch über einen freien Willen verfügt."]], [["Tut dies, <name>, und wir werden zusammen an der Erlösung einer Seele arbeiten, die es verdient hat, erlöst zu werden."]], },
    { [[Bringt den Schwurstein von Yseras Drachenschwarm sowie die gebundene Essenz des Eranikus zu Itharius in die Sümpfe des Elends. Dort werdet Ihr Eure Entscheidung treffen, ob Ihr Yseras Drachenschwarm helfen wollt oder nicht.]], },
  },
  [3375] = {
    [[Ersatzphiole]],
    { [[Ähm... Ihr braucht eine weitere Wahrsagephiole? Das soll wohl ein Witz sein! Was habt Ihr denn mit der Ersten angestellt?! Wir haben eine Menge Arbeit vor uns, <race>. Diese Phiolen wachsen nun mal nicht auf Bäumen! Eigentlich wünschte ich, es wäre so, dann könnte ich nämlich einfach eine pflücken gehen. Ich habe nämlich keine mehr!]], [[Um Euch eine zu machen, brauch ich Folgendes: Einen Manatrank, ein Stück Kohle und etwas süßen Nektar. Fragt mich ja nicht, warum, bringt mir einfach, was ich brauche, und ich werde Euch eine neue Phiole machen.]], },
    { [[Bringt einen Manatrank, Kohle und Süßen Nektar zu Talvash del Kissel in Eisenschmiede, um einen Ersatz für Talvashs Wahrsagephiole zu bekommen.]], },
  },
  [3376] = {
    [[Tötet Spitzhauer!]],
    { [[<name>... Ihr seid ein Neuling, ich habe von Euch gehört. Vielleicht könnt Ihr uns ja helfen, wo andere bereits versagt haben.]], [[Wir Tauren haben in diesem Land unsere Heimstatt geschaffen, aber nicht, ohne teuer dafür zu bezahlen. Die Stacheleber des Borstennackenstamms aus der Dornrankenklamm, mit Häuptling Spitzhauer Dornenmantel als Anführer, machen uns mit ihrem ständigen Krieg das Leben schwer. Ich beauftrage Euch, mir den Kopf des Häuptlings zu bringen!]], [[Ihr könnt ihn in der Klamm östlich von hier in dem heruntergekommenen Dorf finden.]], },
    { [[Bringt den Kopf von Häuptling Spitzhauer Dornenmantel zur Kriegerheldin Windfeder auf der Hochwolkenebene.]], },
  },
  [3377] = {
    [[Gebet an Elune]],
    { [[Werdet Ihr mir helfen, sterbliches Wesen? Hört meine Geschichte.]], },
    { [[Hört Zamaels Geschichte.]], },
  },
  [3378] = {
    [[Gebet an Elune]],
    { [[Werdet Ihr mir helfen, sterbliches Wesen? Werdet Ihr das Gebet an Elune finden?]], [[Wie ich bereits sagte, wurde das Gebet auf eine goldene Schrifttafel geätzt. Einer dieser Götzenanbeter muss sie bei sich tragen.]], [[Wenn Ihr mein Gebet wiederfindet, bringt es zu Astarii Sternsucher in Darnassus. Dass mein Glauben nach den zahllosen widerwärtigen Aktionen, die ich gegen diese Welt und seine Einwohner begangen habe, wiederbelebt wurde, ist vollkommen unwichtig, aber mein Gebet muss gehört werden. Absolution ist das Letzte, was ich erwarte.]], },
    { [[Findet das Gebet an Elune und bringt es zu Astarii Sternsucher in Darnassus.]], },
  },
  [3379] = {
    [[Schattenweber]],
    { [[Die Spinnen der Sengenden Schlucht sind eine unschätzbare Quelle für Schattenseide. Wie Ihr wisst, ist Schattenseide bei den Fachleuten unseres Gewerbes heiß begehrt.]], [[Seit Kurzem hat sich eine Gruppe von Wilderern in diesem Land eingenistet, die achtlos massenhaft Spinnen töten, ohne darüber nachzudenken, welche Auswirkungen ihre Taten auf Dauer haben können. Die erste Aufgabe, die ich von Euch verlange, ist recht einfach: Findet sie... und macht ihnen ein Ende.]], [[Beginnt Eure Suche im Norden.]], [[Wenn Ihr Hilfe benötigt, sprecht mit Schleifer.]], },
    { [[Tötet die Gruppe der Schattenseidewilderer, die in der Sengenden Schlucht umherstreifen. Nilith hat Grund zu der Annahme, dass es fünf von ihnen gibt, die ausgeschaltet werden müssen.]], },
  },
  [3380] = {
    [[Der Versunkene Tempel]],
    { [[Es gibt rastlose Geister in dieser Welt, <name>. Es sind Geister, die durch ihre Einsamkeit besudelt wurden und an ein altes Übel gebunden sind...]], [[Ich habe von einem Ort gehört, der tief unter der Erdoberfläche liegt... größere Mächte als ich jemals gesehen habe, haben ihn dorthin verbannt.]], [[Wenn Ihr mehr über diesen Ort, den Versunkenen Tempel, erfahren wollt, sucht einen Goblin namens Marvon Nietensucher auf. Er hat viele alte Gebiete der Welt erforscht. Zuletzt hieß es, dass er sein Heim in Ratschet verlassen habe, um die Trollruinen von Tanaris zu erforschen.]], },
    { [[Sucht Marvon Nietensucher in Tanaris.]], },
  },
  [3381] = {
    [[Ein Treffen mit dem Meister]],
    { [[Ah, <ein junger/eine junge><race> <race>, <der/die><race> mit Sanath sprechen möchte. Was für ein wundervoller Tag! Ich fühle mich so geehrt, dass Ihr mich mit Eurer Anwesenheit beehrt. Danke!]], [[Bitte... seid nicht so arrogant anzunehmen, dass mein Sarkasmus ein Kompliment war. Lasst uns eine Sache klarstellen: Ich mag Euch nicht. Aber diese kleine Unterhaltung hat nichts mit uns zu tun... es geht um meinen Meister.]], [[Bringt mir ein Paar Hippogryphenfedern für meine Pfeile und ich werde Euch zu ihm führen. Er bat darum, dass ich ihm Abenteurer schicke, sofern sie sich würdig erweisen.]], },
    { [[Bringt 2 unbeschädigte Hippogryphenfedern zu Sanath Lim-yo in Azshara, wenn Ihr mit seinem Meister sprechen wollt.]], },
  },
  [3382] = {
    [[Eine Mannschaft in Bedrängnis]],
    { [[Wir waren kurz vor der Küste und suchten nach einem sicheren Hafen, um den Anker zu werfen. Die Sonne war noch keine Stunde untergegangen, als sie angriffen... diese verdammten Naga!]], [[Ich weiß nicht genau, wonach sie gesucht haben, aber sie haben es offensichtlich nicht gefunden - sie sind bereits schon einmal wiedergekommen. Einige meiner Späher stehen am Eingang zum Tal Wache, um uns zu warnen, wenn der nächste Angriff kommt.]], [[Es tut mir leid, dass Ihr gerade jetzt vorbeikommt, <name>, aber das liegt nicht in meiner Macht.]], [[Seid Ihr bereit für den Kampf Eures Lebens?]], },
    { [[Verteidigt Kapitän Vanessa Beltis, ihre Mannschaft und die Passagiere der Herrin des Horizonts gegen den Angriff der Naga. Kapitän Vanessa Beltis muss überleben.]], },
  },
  [3385] = {
    [[Der Untermarkt]],
    { [[Sie bezeichnen sich selbst als den Untermarkt: Halsabschneider von Händlern, die verkaufen, verschieben, handeln und zu fast allem bereit sind, vorausgesetzt, der Preis stimmt.]], [[Es kommt noch schlimmer, <name>. Sie haben einen Stützpunkt für ihre Operationen hier in der Sengenden Schlucht errichtet. Handelsmeister Kovic führt diese Operationen an und hat enge Verbindungen zu den Dunkeleisenzwergen des Gebietes.]], [[Wir müssen hart zuschlagen und den Anführern ein Zeichen setzen. Erschlagt Handelsmeister Kovic und seinen Diener Clunk. Bringt seinen Händlerranzen zurück, sobald die Tat ausgeführt ist.]], [[Zum Kessel!]], },
    { [[Erschlagt Handelsmeister Kovic und seinen Diener Clunk. Nehmt den Händlerranzen von seinem Leichnam und bringt ihn zu Nilith.]], },
  },
  [3402] = {
    [[Der Untermarkt]],
    { [[Ich will, dass Ihr Vizzklick in Gadgetzan persönlich die Nachricht von Kovics Niederlage überbringt. Ich bin ziemlich sicher, Kovics Tod wird ihn so erfreuen, dass er Euch eine großartige Belohnung geben wird.]], },
    { [[Sprecht mit Vizzklick in Gadgetzan.]], },
  },
  [3421] = {
    [[Rückreise]],
  },
  [3441] = {
    [[Göttliche Vergeltung]],
    { [[Hört meine Geschichte, Abenteurer.]], },
    { [[Lasst Kalaran Windklinge seine Geschichte erzählen.]], },
  },
  [3442] = {
    [[Die fehlerlose Flamme]],
    { [[Ihr habt die richtige Entscheidung getroffen, <name>.]], [[Wir müssen dort zuschlagen, wo wir den größten Schaden anrichten: an den vier Wachpostentürmen.]], [[Wenn die vier Wachpostentürme in Flammen stehen, starten wir eine Offensive, bevor sie reagieren können.]], [[Ihr benötigt das passende Werkzeug, um die Türme in Brand zu setzen. Für die Herstellung des Werkzeugs wird einiges an Materialien benötigt.]], [[Bringt mir vier Herzen der Flamme und vier Kugeln Golemöl, und ich werde die Fackel der fehlerlosen Flamme herstellen können.]], [[Ihr solltet Euch also zuerst die Golems und Elementare dieses Landes vornehmen.]], },
    { [[Sammelt 4 Herzen der Flamme und 4 Kugeln Golemöl und bringt die Gegenstände zu Kalaran Windklinge.]], },
  },
  [3443] = {
    [[Das Schmieden des Schafts]],
    { [[Bringt 8 mit Thorium beschichtete Dolche zu Kalaran Windklinge.]], },
    { [[Bringt 8 mit Thorium beschichtete Dolche zu Kalaran Windklinge.]], },
  },
  [3444] = {
    [[Der runde Stein]],
    { [[Ich habe einst die furchteinflößenden Kammern des Versunkenen Tempels betreten. Das Böse an jenem Ort durchdringt alle, die eintreten. Mit Mühe und Not habe ich es geschafft, irgendwie meinen Weg wieder nach draußen zu finden, indem ich durch den Schlamm watete, der jetzt den Boden bedeckt. Ich kann mich nur daran erinnern, dass ich durch einen großen, kreisrunden Raum gestolpert bin, der angefüllt war mit Schlangenstatuen...]], [[Kürzlich habe ich einen runden Stein entdeckt, der anscheinend das gleiche Schlangensymbol trägt. Ich habe ihn verpackt und in meine Werkstatt nach Ratschet geschickt. Bringt ihn her und ich kann Euch vielleicht mehr erzählen.]], },
    { [[Holt den runden Stein aus Marvon Nietensuchers Werkstatt in Ratschet.]], },
  },
  [3445] = {
    [[Der Versunkene Tempel]],
    { [[Ich habe von einem Ort gehört, der tief unter der Oberfläche der Erde liegt - dorthin verbannt durch größere Mächte, als ich jemals gesehen habe.]], [[Wenn Ihr mehr über diesen Ort, den Versunkenen Tempel, erfahren wollt sucht einen Goblin namens Marvon Nietensucher auf. Genau wie Troyas und ich hat er viele alte Gebiete der Welt erforscht. Zuletzt hieß es, dass er seine Heimstatt in Ratschet verlassen habe, um die Trollruinen von Tanaris zu erforschen.]], [[Viel Glück bei Eurer Suche, man hat schon seit längerem nichts mehr von ihm gehört.]], },
    { [[Sucht Marvon Nietensucher in Tanaris.]], },
  },
  [3446] = {
    [[In die Tiefen]],
    { [[Im Tempel fand ich einen kreisrunden Raum mit sechs Altären, und am Rand eines jeden stand eine Schlangenstatue. Neugierig drückte ich gegen eine von ihnen, um zu sehen, was passieren würde. Es war eine Falle! Durch die Explosion wurde ich fast getötet. Als ich da auf einem Vorsprung lag, sah ich nach unten und bemerkte, dass eine weitere Statue unten im Raum stand; es war ein Altar der Art... Hakkar, ich glaube, so wurde er genannt.]], [[Ich denke, dass dieser Altar das Geheimnis ist... Ihr müsst dieses Zeichen zum Altar in den Versunkenen Tempel bringen.]], },
    { [[Sucht den Altar von Hakkar im Versunkenen Tempel in den Sümpfen des Elends.]], },
  },
  [3447] = {
    [[Das Geheimnis des Kreises]],
    { [[Meine Erinnerungen sind verworren, aber ich werde Euch alles sagen, was ich weiß. Der Kreis der Statuen im Versunkenen Tempel mag ja gefährlich sein, ich glaube dennoch, dass sie das Geheimnis eines noch größeren Schatzes in sich bergen.]], [[Vom Hauptaltar aus konnte ich eine Reihe von Lichtern aktivieren. Ich glaube, dass Ihr, wenn Ihr die Bedeutung dieser Lichter herausfindet, auch die weiteren Geheimnisse lüften könnt, die sich in diesem Raum verstecken.]], [[Aber ich warne Euch, <name>. In dem Tempel ist etwas unermesslich Böses. Es ist anzunehmen, dass alles Wertvolle äußerst streng bewacht wird.]], },
    { [[Reist zum Versunkenen Tempel und enthüllt das Geheimnis, das sich in dem Kreis der Statuen verbirgt.]], },
  },
  [3448] = {
    [[Die Bürde weitergeben]],
    { [[Kürzlich erhielt ich den Auftrag, Azshara zu untersuchen, einen einst wunderschönen Landstrich, den die Elfen ihr Zuhause nannten. Das war damals, als sie noch Magie praktizierten. Jetzt ist es dort eher gefährlich als schön, aber das hält uns nicht davon ab, jeden Stein umzudrehen, um Hinweise auf unsere Vergangenheit zu erhalten.]], [[Ein Gefährte von mir namens Tymor bot mir seine Hilfe bei der Untersuchung dieses Gebietes an, da er ein besonderes Interesse an solchen Dingen hat.]], [[Er besitzt ein kleines Anwesen rechts neben der Halle der Mysterien hier in der Stadt.]], },
    { [[Sprecht mit Magier Tymor in Eisenschmiede.]], },
  },
  [3449] = {
    [[Arkane Runen]],
    { [[Einstmals wurde Magie von einigen Elfen genauso verehrt wie Elune selbst. Einige ihrer mächtigsten Magier erstellten Runen und Schrifttafeln, die die Regeln des Arkanen zitierten und Einsichten in ihre Funde gaben. Sie ließen sie an öffentlichen Plätzen aufstellen, damit jeder darüber nachdenken konnte.]], [[Ich hätte sehr gern Abpausbilder dieser Runen aus den Ruinen von Eldarath. Ich habe ein Set für Euch, mit dem Ihr Abpausbilder herstellen könnt, aber Ihr müsst sie selbst finden. Seid aber vorsichtig, die Forscherliga ist nicht die Einzige, die sich dafür interessiert.]], },
    { [[Verwendet das Abschriftenset, um Abpausbilder von der Rune von Beth'Amara, der Rune von Jin'yael, der Rune von Markri und der Rune von Sael'hai herzustellen, bevor Ihr Euch zu der kleinen Insel vor der südlichen Halbinsel von Azshara aufmacht und dem Piloten Xiggs Luntenzünder signalisiert, dass er sie abholen kann.]], },
  },
  [3450] = {
    [[Eine einfache Ladung]],
    { [[Ich will diese Abpausbilder sofort in meinen Turm geliefert haben, aber Ihr müsst nicht den ganzen Weg selbst gehen. Ich werde sie einfach per Luftfracht aus Azshara bringen lassen.]], [[Xiggs Luntenzünder schuldet mir noch einen oder zwei Gefallen und wird sich bald in der Nähe aufhalten - er wurde zur Küstenpatrouille abkommandiert. Sprecht mit ihm, bevor Ihr weggeht. Er kann Euch eine Leuchtfeuerwaffe geben, damit Ihr ihm signalisieren könnt. Sobald Ihr die Abpausbilder zusammen habt, begebt Euch an den Ort, den er Euch nennt und übergebt sie ihm dann, damit er sie sicher zu meinem Turm bringen kann. Er sollte sich im Hangar im Militärviertel aufhalten.]], },
    { [[Sprecht mit Xiggs Luntenzünder in Eisenschmiede.]], },
  },
  [3451] = {
    [[Signal zum Abholen]],
    { [[Ah, Tymor hat Euch also geschickt, hm? Ja, dem schulde ich noch einen oder zwei Gefallen. Wie war das? Nein, der kleine Teufel. Ja, da kann ich ihm helfen. Ihr braucht also eine Leuchtfeuerwaffe und einen Ort... nichts leichter als das.]], [[Kurz vor der südlichen Halbinsel von Azshara liegt eine kleine Insel, auf der wir eine Landungsplattform gebaut haben. Ihr nehmt diese Leuchtfeuerwaffe und schießt sie dort ab. Ich sehe dann Euer Signal und werde vorbeikommen, um diese... was auch immer er da haben will, mitzunehmen.]], },
    { [[Sprecht mit Xiggs Luntenzünder in Eisenschmiede und erhaltet von ihm eine Standardleuchtpistole.]], },
  },
  [3452] = {
    [[Die Ummantelung der Flamme]],
    { [[Wir brauchen noch ein letztes Teil, um die Fackel zu formen.]], [[In den Bergen nordwestlich von hier könnt Ihr ein großes Lager des Schattenhammers finden. Es heißt, dass sie Symbole und Götzenbilder zur Verehrung ihres Gottes mit sich herumtragen. Diese Symbole und Götzenbilder besitzen vielleicht genügend Verzauberung, um daraus eine magische Ummantelung herzustellen. Bringt mir ein einziges Symbol von Ragnaros und ich werde versuchen, die Magie aus dem Gegenstand zu extrahieren, um die Ummantelung zu erschaffen.]], },
    { [[Bringt ein Symbol von Ragnaros zu Kalaran Windklinge.]], },
  },
  [3453] = {
    [[Die Fackel der Vergeltung]],
    { [[Wartet bis Kalaran Windklinge die Fackel der Vergeltung vollendet hat.]], },
    { [[Wartet bis Kalaran Windklinge die Fackel der Vergeltung vollendet hat.]], },
  },
  [3454] = {
    [[Die Fackel der Vergeltung]],
    { [[Die Fackel muss jetzt an ihren Besitzer gebunden werden. Nehmt die Fackel auf, <name>.]], },
    { [[Nehmt die Fackel der Vergeltung.]], },
  },
  [3461] = {
    [[Rückkehr zu Magier Tymor]],
    { [[Tymor hat mir nichts gegeben, um Euch zu bezahlen, also schlage ich vor, dass Ihr das mit ihm besprecht, wenn Ihr zurück in Eisenschmiede seid. Ich bin mir sicher, er wird dann immer noch an seiner Wohnung im Mystikerviertel sein. Klopft einfach bei ihm an, und er soll Euch ja gut bezahlen. Das hier ist nicht gerade ein Spaziergang... das ist schon mal sicher.]], [[Danke nochmals für Eure Hilfe und viel Glück.]], [[Oh, das Abschriftenset brauche ich nicht. Warum bringt Ihr es nicht selbst Tymor zurück, wenn Ihr ihn besucht?]], },
    { [[Bringt Magier Tymors Abschriftenset zu ihm zurück nach Eisenschmiede.]], },
  },
  [3462] = {
    [[Knappe Malzdarr]],
    { [[Sprecht mit meinem Gesandten, dem Knappen Malzdarr. Er wird Euch weiter unterweisen, wie die Fackel der Vergeltung zu benutzen ist.]], },
    { [[Sprecht mit dem Knappen Malzdarr.]], },
  },
  [3463] = {
    [[Setzt sie in Brand!]],
    { [[Hört gut zu, <name>. Obgleich die vier Türme niemals zerstört werden können, könnt Ihr dennoch die Fackel der Vergeltung benutzen, um sie für lange Zeit in Brand zu setzen. Ein Feuer, das kein Element und keine Macht löschen kann.]], [[Während sie in Flammen stehen, kann keine Wache oder Wachposteneinheit die Posten besetzen und so ist ihre erste Verteidigungslinie außer Kraft gesetzt.]], [[Ihr müsst in jeden Turm eindringen und die Fackel der Vergeltung innerhalb ihrer Befestigung verwenden. Nehmt Euch in Acht vor den Ausgucken der Dunkeleisenzwerge, <name>.]], },
    { [[Steckt die Wachpostentürme im Norden, Süden, Osten und Westen in Brand, indem Ihr die Fackel der Vergeltung innerhalb der einzelnen Gebäude benutzt.]], },
  },
  [3481] = {
    [[Plunder...]],
    { [[Die Truhe scheint verschlossen zu sein.]], },
    { [[Öffnet die Truhe.]], },
  },
  [3482] = {
    [[<NYI> <TXT> The Pocked Black Box]],
    { [[Das ist die Hordenversion des Flugschreiber-Quests.]], },
    { [[Bringt diesen Kasten zu Kravel.]], },
  },
  [3483] = {
    [[Signal zum Abholen]],
  },
  [3501] = {
    [[Viel hilft viel]],
    { [[Obgleich Ihr wohl niemals eine fehlerlose Draenethystsphäre finden werdet, könnt Ihr zweifelsohne auf Euren Reisen durch dieses Land einige unvollständige Draenethystfragmente entdecken.]], [[Es ist zwar unwahrscheinlich, dass Kum'isha die fehlerhaften Fragmente benutzen kann, um nach Hause zurückzukehren, dennoch können diese Stücke für andere nützliche Zwecke der Draenei dienen.]], [[Bringt mir alle fehlerhaften Draenethystfragmente, die Ihr finden könnt, und ich werde Euch belohnen.]], },
    { [[Bringt Kum'isha unvollständige Draenethystfragmente und Ihr werdet für jedes, das Ihr bringt, belohnt.]], },
  },
  [3502] = {
    [[Des einen Draeneis Plunder...]],
    { [[Bringt Kum'isha unvollständige Draenethystfragmente. Jede Art von Monster in den Verwüsteten Landen könnte ein Fragment bei sich tragen.]], },
    { [[Bringt Kum'isha unvollständige Draenethystfragmente. Jede Art von Monster in den Verwüsteten Landen könnte ein Fragment bei sich tragen.]], },
  },
  [3503] = {
    [[Treffen mit dem Meister]],
  },
  [3504] = {
    [[Verraten]],
    { [[Verräterisches, diebisches Weib! So nenne ich sie... und genau das wird sie immer sein!]], [[Ich habe wahrscheinlich einen großen Fehler gemacht, <name>. Ich habe einem Blutelfen vertraut. Was schlimmer ist, ich habe einer Frau vertraut!]], [[Sie behauptete, in Azshara lägen große Reichtümer... mächtige Gegenstände. Ich sollte ihr nur ein paar Krieger zu ihrem Schutz schicken und dafür würde ich belohnt. Und wer ist jetzt wohl der Dumme - sie hat sie alle umgebracht.]], [[Ich will Rache, <name>, aber ich kann es mir nicht leisten, noch jemanden zu schicken. Sucht Ag'tor bei Valormok auf, nördlich des Lagers der Haldarr. Er kann Euch mehr berichten.]], },
    { [[Bringt den versiegelten Brief zu Ag'tor Blutfaust nach Azshara.]], },
  },
  [3505] = {
    [[Verraten]],
    { [[Das hatte ich mir gedacht.]], [[Wir werden unseren Kreuzzug gegen ihre Tagelöhner entlang der westlichen Steilhänge an der nördlichen Halbinsel von Azshara beginnen. Tötet sie mit blutiger Wut - lasst sie wissen, dass sie die falschen Verbündeten verraten haben.]], [[Um sie selbst müsst Ihr Euch jetzt noch nicht kümmern - sie verbringt die meiste Zeit zusammen mit ihren Leibwachen auf der Suche nach Azshara. Es ist wahrscheinlich am klügsten, erst ihre Tagelöhner zu erschlagen und dann ihr persönliches Lager zu suchen. Es wäre weise auszukundschaften, wo wir sie angreifen können. Vielleicht gibt es ja etwas, womit wir sie provozieren können.]], },
    { [[Tötet 10 Blutelfeneintreiber und 10 Blutelfenfeldmesser, bevor Ihr Magus Rimtoris Lager sucht. Sobald Ihr es gefunden habt, sucht nach etwas, das für sie von Bedeutung ist und das wir nehmen können, um sie in einen Kampf zu locken.]], },
  },
  [3506] = {
    [[Verraten]],
    { [[Nach einigen Momenten des Nachdenkens kommt Ihr auf ein paar Ideen, von denen Euch eine zum Lächeln bringt: Vielleicht würde die Zerstörung der Kristalle die Blutelfe ärgern, die für das Abschlachten so vieler Orcs und den Verrat an Belgrom verantwortlich ist?]], [[Ihr fahrt fort, mehr vom Manuskript und den Bildern zu entschlüsseln und erkennt, dass einer der Kristalle an den Schöpfer des Kreises gebunden ist - die anderen sind an etwas weitaus Finstereres gebunden.]], [[Ihr schließt das Buch und beginnt Euch zu entscheiden, welchen Kristall Ihr zerstören wollt.]], },
    { [[Zerstört den arkanen Fokussierkristall, der an Magus Rimtori gebunden ist, und tötet sie danach. Bringt dann den Kopf von Magus Rimtori zu Ag'tor Blutfaust nach Azshara.]], },
  },
  [3507] = {
    [[Verraten]],
    { [[Kehrt zu Belgrom nach Orgrimmar zurück. Er wird ihren Kopf höchstpersönlich sehen wollen. Wir werden in Kürze dorthin zurückkehren.]], [[Ah, dies ist heute wahrlich ein Freudentag. Danke für Eure Hilfe, <name>.]], },
    { [[Sprecht mit Belgrom Felshammer in Orgrimmar.]], },
  },
  [3508] = {
    [[Den Zauberschutz brechen]],
    { [[Ihr seid tollkühn, <name>. Es ist eine bewundernswerte Eigenschaft Eurer Art, sich blind in die Schlacht zu stürzen, auch wenn nur wenig oder gar keine Aussicht auf Erfolg besteht. Ich werde Euch behilflich sein, sei es auch nur, um einen einzigen von Razelikhs Dienern fallen zu sehen.]], [[Den Zauberschutz, der auf Razelikhs Untergebenen liegt, zu brechen, kostet Zeit und macht es nötig, rare Artefakte zu erwerben.]], [[Seid still, ich muss erst das Erleuchtungsritual durchführen. Die Information, die ich dabei erhalte, wird mir helfen, Waffen von großer Macht zu schmieden.]], },
    { [[Wartet, bis Loramus den Erleuchtungszauber vollendet hat.]], },
  },
  [3509] = {
    [[Der Name der Bestie]],
    { [[Es gibt nur eine Möglichkeit, den Dämonenlord und seine Diener zu verletzen. Eine Waffe der Macht muss geschmiedet werden, die seinen wahren Namen trägt.]], [[Wird eine solche Waffe geschmiedet, sind der Dämon und alle, die unter seinem Kommando stehen, verwundbar.]], [[In Azshara residiert eine geringe Gottheit: Lord Arkkoroc. Ihn müsst Ihr aufsuchen. Aber seid gewarnt - wenn man Euch entdeckt, werden seine Diener alles tun, um Euch zu vernichten.]], [[Zweifelsohne müsst Ihr einen Preis für solche Informationen bezahlen.]], },
    { [[Sprecht mit Lord Arkkoroc in Azshara. Beginnt Eure Suche am Rand des Wassers, um den Tempel von Arkkoran zu finden.]], },
  },
  [3510] = {
    [[Der Name der Bestie]],
    { [[Erschlagt Hetaera und bringt den blutigen Kopf Hetaeras, den geschlagenen Kopf Hetaeras und den verwundeten Kopf Hetaeras zu Lord Arkkoroc.]], },
    { [[Erschlagt Hetaera und bringt den blutigen Kopf Hetaeras, den geschlagenen Kopf Hetaeras und den verwundeten Kopf Hetaeras zu Lord Arkkoroc.]], },
  },
  [3511] = {
    [[Der Name der Bestie]],
    { [[Wie versprochen, werde ich den Namen des Dämonenlords enthüllen: Es ist Razelikh der Entweiher. Zuerst muss ich jedoch etwas vorbereiten, das für Eure Reise von äußerster Wichtigkeit ist.]], [[Die Waffe, die Ihr herstellen wollt, benötigt einen Härter. Gebt mir einen Moment Zeit, um das Blut aus diesen Köpfen herauszupressen.]], [[<Lord Arkkoroc drückt die Köpfe mit seinen gigantischen Fäusten zusammen, sodass das Blut aus ihnen herausfließt.>]], [[Bringt diesen Härter zurück zu Loramus, zusammen mit dem Namen der Bestie: Rakh'likh.]], },
    { [[Bringt den Härter zurück zu Loramus Thalipedes.]], },
  },
  [3512] = {
    [[Mit Eranikus' eigenen Worten]],
    { [[<name>, als Erstes werden wir eine Methode ersinnen, um durch den Edelstein mit Eranikus zu sprechen. Zurzeit befindet er sich in einem gefährlichen Zustand zwischen Schlaf und Tod, verstrickt in ein Gewebe aus Alpträumen. Zwar ist die Essenz, die Ihr mir gegeben habt, stark besudelt, aber es gibt dennoch Hoffnung, dass wir zu ihm durchdringen.]], [[Reist weit hinaus in das eisige Land des östlichen Winterquell und sucht einen unserer sterblichen Verbündeten, Umbranse den Geistsprecher. Er ist ein menschliches Medium, der für die Toten spricht. Zwar ist Eranikus noch nicht wirklich tot, trotzdem könnte er uns eine Hilfe sein.]], },
    { [[Reist in den östlichen Teil von Winterquell und sprecht mit Umbranse, dem Geistsprecher.]], },
  },
  [3513] = {
    [[Die runenverzierte Rolle]],
    { [[Die offiziell aussehende Rolle wurde mit einem silbrigen Band eng verschnürt. Ihr seid Euch sicher, dass jemand das hier sehen möchte, aber wer?]], [[Es gibt einen Außenposten im Norden in der Nähe der Grenze des Eschentals, und wenn die Informationen auf dieser Rolle die Nachtelfen betreffen, dann sollte die Rolle dorthin gebracht werden.]], },
    { [[Bringt die runenverzierte Rolle zum nördlichen Wachturm des Brachlandes.]], },
  },
  [3514] = {
    [[Hordenpräsenz]],
    { [[Diese Rolle ist von der Ränke schmiedenden Dryade Rynthariel, Schlüsselmeisterin des Krallenbaus auf dem Steinkrallengipfel.]], [[Mit dem Boten, von dem Ihr diese Rolle erbeutet habt, hat sie jetzt ja wohl keinen Kontakt mehr, aber nachgeben dürfen wir trotzdem nicht. Die Horde wird ihre Kundschafter hier nicht tolerieren.]], [[<name>, reist zum Krallenbau. Tötet Rynthariel und stellt ein Symbol auf, das alle sehen können. Nehmt diese Hordenflagge mit Euch, <name>. Hisst sie im Krallenbau, damit sie es sich zweimal überlegen, bevor sie einen weiteren Boten durch unser Gebiet schicken.]], },
    { [[Tötet die Anführerin des Krallenbaus, Rynthariel die Schlüsselmeisterin.]], },
  },
  [3517] = {
    [[Wissen stehlen]],
    { [[Ich muss zugeben, <name>, ich habe das Beste aus meiner Situation in Azshara gemacht. Belgroms Bestrafung hatte schon seine Vorteile.]], [[Bevor ich wegging, habe ich einige Nachforschungen angestellt. Ihr wärt überrascht darüber, wie viele wertvolle Gegenstände hier einfach so herumliegen.]], [[Ich erzähle Euch noch mehr von meinen Geschäften und gebe Euch sogar einen Anteil an meinen Gewinnen, wenn Ihr mir helft, ein paar von den Sachen einzusammeln.]], [[In den östlichen Ruinen von Eldarath gibt es Schrifttafeln, die ich benötige. Alle vier wurden nach Hochgeborenenhexern benannt. Bringt sie mir und wir können weiterreden.]], },
    { [[Sucht die Schrifttafel von Beth'Amara, die Schrifttafel von Jin'yael, die Schrifttafel von Markri und die Schrifttafel von Sael'hai, bevor Ihr zu Jediga in Azshara zurückkehrt.]], },
  },
  [3518] = {
    [[Lieferung an Magatha]],
    { [[Die erste Schrifttafel geht an einen weiblichen Tauren... mit Namen Magatha. Sie gehört zu den Ältesten in Donnerfels und zu den Befürwortern der Allianz zwischen den Tauren und den Verlassenen. Sie ist mehr als jeder andere der Ältesten der Meinung, dass die Tauren und ihre Lebensart den Verlassenen helfen können, einen Weg zurück zum Menschsein zu finden.]], [[Bringt Ihr die Schrifttafel von Beth'Amara. Die Schrifttafel kann das fehlende Glied sein, das sie benötigt, um den Verlassenen zu helfen... das behauptet sie jedenfalls.]], },
    { [[Bringt die Schrifttafel von Beth'Amara zu Magatha in Donnerfels.]], },
  },
  [3519] = {
    [[Ein Freund in der Not]],
    { [[Aghhh... Ich wurde ziemlich schlimm von einer Spinne namens Githyiss die Üble gebissen, während ich die Spinnenhöhle ganz in ihrer Nähe erforschte. Ich bin sicher, dass ich ernsthaft vergiftet bin; Ihr müsst mir helfen.]], [[Sagt es bitte Dirania Silberschein. Sie wird mir helfen können.]], [[Beeilt Euch... Mir ist so schwindelig...]], },
    { [[Sprecht mit Dirania Silberschein im Laubschattental.]], },
  },
  [3520] = {
    [[Kreischergeister]],
    { [[Ich suche die Geister der Talkreischer von Feralas. Sie sind die Cousins eines alten, alten Gottes und ihre Geister sind mit ihm verbunden.]], [[Nehmt diesen Dornbuschzauberstab und reist nach Feralas. Sucht und besiegt die Talkreischer in Süd- und Mittel-Feralas, und schwingt meinen Dornbuschzauberstab dann über ihre gefallenen Körper. Dadurch wird ihr Geist dazu gebracht, sich zu zeigen. Berührt den Geist und sammelt seine Essenz.]], [[Kommt mit meinem Dornbuschzauberstab und der gesammelten Essenz zu mir zurück, und ich werde Euch mehr von dem alten Gott erzählen, von dem ich sprach.]], },
    { [[Fangt die Geister von 3 Kreischern in Feralas ein und kehrt dann zu Yeh'kinya beim Dampfdruckpier zurück.]], },
  },
  [3521] = {
    [[Iverrons Gegengift]],
    { [[Wir dürften in der Lage sein, Iverron zu helfen, denn ich kenne ein Gegengift, das gegen das Gift wirkt. Bevor ich es herstellen kann, benötige ich allerdings einige Zutaten.]], [[Ich brauche Hyazinthpilze. Sie wachsen unter Bäumen. Ihr könnt sie aber auch von den Grells südlich von hier sammeln; sie scheinen sie sehr zu mögen. Außerdem brauche ich noch Mondblütenlilien, die nur an Wasserteichen wachsen.]], [[Die letzte Zutat könnte die Schwierigste sein. Sammelt Waldwebersekret von genau den Spinnen, die Iverron vergiftet haben.]], },
    { [[Sammelt 7 Hyazinthpilze, 4 Mondblütenlilien und 1 Waldwebersekret.]], },
  },
  [3522] = {
    [[Iverrons Gegengift]],
    { [[Das Gegengift ist fertig, <name>. Bitte achtet darauf, dass Iverron es auch trinkt.]], [[Es gibt jedoch etwas, das Ihr wissen sollt: Das Gegengift ist nur 5 Minuten wirksam. Ihr müsst es ihm rechtzeitig bringen.]], [[Mögen Eure Füße beflügelt sein, <name>.]], },
    { [[Bringt Iverrons Gegengift zu Iverron, bevor die Zeit um ist. Ihr findet Iverron bei der Höhle im Norden.]], },
  },
  [3523] = {
    [[Geißel der Hügel]],
    { [[Phantastisch! Mein Name ist Belnistrasz und ich danke Euch für meine Rettung. Ich hatte mich schon tot gewähnt... oder schlimmer, als Mahlzeit für dieses von der Geißel befallene Gezücht. Ja, richtig gehört, ich sagte Geißel; sie haben anscheinend mit den Stachelebern hier eine Art Vereinbarung getroffen und dieser Alptraum ist das Ergebnis.]], [[Hört mir zu... anstatt einfach wegzulaufen, möchte ich doch uns allen zuliebe versuchen, etwas zu ändern. Könntet Ihr mir dabei helfen? Es ist wahrscheinlich gefährlich, aber Ihr scheint von dem Schlag zu sein, dem das nicht viel ausmacht.]], },
    { [[Wenn Ihr Belnistrasz helfen wollt, sprecht noch einmal mit ihm und übergebt ihm den Schwurstein, den er Euch gab.]], },
  },
  [3524] = {
    [[An Land gespült]],
    { [[Majestätische Meereskreaturen begeben sich häufig an die Ufer von Dunkelküste, und liegen dort gestrandet, bis sie sterben. Vor Kurzem wurden diese Kreaturen in immer größerer Zahl an Land gespült. Ich wurde vom Tempel des Mondes zur Untersuchung dorthin geschickt, aber die Anwesenheit der Murlocs entlang des Wassers hat meine Forschungen schwierig gemacht.]], [[Es gibt eine riesige Kreatur, die unmittelbar südlich von Auberdine an Land gespült wurde und die von den widerlichen Grauflossenmurlocs umringt ist. Könntet Ihr Euch dorthin begeben und Knochen von dieser Kreatur für unsere Studien sammeln?]], },
    { [[Sammelt Meereskreaturknochen von der gestrandeten Meereskreatur unmittelbar südlich von Auberdine und kehrt dann mit ihnen zu Gwennyth Bly'Leggonde nach Auberdine zurück.]], },
  },
  [3525] = {
    [[Ausschalten des Götzen]],
    { [[In der Nähe des Eingangs befindet sich ein Götze, der als makabrer Ofen für die Stacheleber fungiert; die Bestien... verzehren die aus dem Brachland Entführten. Ich kann den Götzen außer Funktion setzen und verhindern, dass sie ihn erneut nutzen, aber ich benötige dabei Eure Hilfe. Mein Plan: Begleitet mich zum Götzen. Wenn wir dort angekommen sind, werde ich das Ritual einleiten. Ich werde kaum in der Lage sein, mit Euch zu kommunizieren, geschweige denn, mich zu verteidigen. Schützt mich, während ich den Götzen außer Funktion setze. Wenn sie mich erschlagen, dann ist alles aus.]], [[Steht Ihr mir bei?]], },
    { [[Begleitet Belnistrasz zum Götzen der Stacheleber in den Hügeln der Klingenhauer.]], [[Beschützt Belnistrasz, während er das Ritual durchführt, um den Götzen auszuschalten.]], },
  },
  [3526] = {
    [[Gobliningenieurskunst]],
    { [[Bei der Experteningenieurskunst gibt es zwei unterschiedliche Disziplinen: Gnom und Goblin. Natürlich ist die Gobliningenieurskunst die Wahl aller brillanten Ingenieure. Durch den klugen Einsatz von hochempfindlichen Sprengstoffen und phantastischen Ausrüstungsgegenständen lernt Ihr, die Welt zu meistern!]], [[<name>, lest diese Anleitung. Wenn Ihr etwas über Gobliningenieurskunst wissen wollt, dann bringt sie zu Nixx Sprossfeder nach Gadgetzan. Denkt daran - das ist eine dauerhafte Mitgliedschaft. Es ist also nicht möglich, auch in die andere Disziplin einzutreten, daher solltet Ihr genau wissen, was Ihr wollt, bevor Ihr seine Aufgabe ausführt.]], },
    { [[Wenn Ihr mehr über die Gobliningenieurskunst wissen wollt, dann bringt das Handbuch der Ingenieurskunstdisziplinen zu Nixx Sprossfeder nach Gadgetzan.]], },
  },
  [3527] = {
    [[Die Prophezeiung von Mosh'aru]],
    { [[Die alte Prophezeiung von Mosh'aru berichtet von einer Methode, die Essenz des Gottes Hakkar zu bewahren. Sie wurde auf zwei Schrifttafeln niedergeschrieben und zu der Trollstadt Zul'Farrak gebracht, westlich von Gadgetzan.]], [[Bringt mir die Schrifttafeln von Mosh'aru!]], [[Die erste Schrifttafel hat der Troll Theka der Märtyrer, der schon lange tot ist. Man sagt, dass seine Verfolger zu Skarabäen verflucht wurden und nun an seinem Schrein herumhuschen.]], [[Die zweite Schrifttafel hat die Hydromantin Velratha, in der Nähe des hochheiligen Teiches von Gahz'rilla.]], [[Wenn Ihr die Schrifttafeln habt, bringt sie zu mir.]], },
    { [[Bringt die erste und die zweite Schrifttafel von Mosh'aru zu Yeh'kinya nach Tanaris.]], },
  },
  [3528] = {
    [[Der Gott Hakkar]],
    { [[Jetzt wird es Zeit, die Essenz des Avatars von Hakkar einzufangen.]], [[Bringt das Ei von Hakkar in das Sanktum des Gefallenen Gottes im Versunkenen Tempel in Azeroth. Ruft die Macht des Eies an, um den toten Gott aufzuwecken.]], [[Er wird seine Diener gegen Euch ausschicken. Besiegt seine Bluttrinker und nehmt ihr Hakkariblut an Euch; dann benutzt das Blut, um die ewigen Flammen auszulöschen, die Hakkars Geist halten. Erst wenn alle Flammen gelöscht sind, wird der Avatar von Hakkar unsere Welt betreten.]], [[Besiegt ihn und gebt seine Essenz in das Ei von Hakkar.]], },
    { [[Bringt das gefüllte Ei von Hakkar zu Yeh'kinya nach Tanaris.]], },
  },
  [3541] = {
    [[Lieferung an Jes'rimon]],
    { [[Ich kenne einen Hehler in Orgrimmar. Er wohnt in der Gasse. Er ist ziemlich schwer zu finden, wenn Ihr nicht wisst, wo Ihr suchen sollt. Wenn Ihr Euch dort in einem der höchsten Gebäude umschaut, solltet Ihr ihn jedoch ohne Probleme finden können.]], [[Tut mir aber bitte einen Gefallen und posaunt nicht in ganz Kalimdor herum, dass er dort ist und was er so tut, das wäre schlecht für uns beide.]], [[Er wird uns einen mehr als angemessenen Preis für die Schrifttafel von Jin'yael geben. Ich weiß zwar nicht genau warum, aber Ihr hättet mal sehen sollen, wie aufgeregt er war, als er von ihrem Aufenthaltsort erfahren hat.]], },
    { [[Bringt die Schrifttafel von Jin'yael zu Jes'rimon nach Orgrimmar.]], },
  },
  [3542] = {
    [[Lieferung an Andron Gant]],
    { [[Andron Gant ist mein nächster Kontakt. Normalerweise hält er sich in Unterstadt auf... ganz in der Nähe des Apothekariums.]], [[Er war ein bisschen redselig, als das Gespräch darauf kam, warum er die Schrifttafel haben wollte. Anscheinend wollen einige der Magier von Unterstadt die Arkanzauber studieren, die die Hochgeborenen benutzt haben. Sie glauben, dass ihnen diese Schrifttafel dabei helfen würde. Markri war sehr bewandert im Beschwören von Kreaturen, besonders von Elementaren.]], [[Nehmt die Schrifttafel von Markri und kommt zu mir zurück, um Euch Euren Teil der Belohnung abzuholen.]], },
    { [[Bringt die Schrifttafel von Markri zu Andron Gant nach Unterstadt.]], },
  },
  [3561] = {
    [[Lieferung für Erzmagier Xylem]],
    { [[Die letzte Schrifttafel geht an eine sehr gefährliche Person, <name>. Hört auf mich, wenn ich Euch sage, dass ich dieser Person viel weniger traue als irgendeiner der anderen, die Ihr in meinem Namen getroffen habt.]], [[Erzmagier Xylem hat einen Turm in den Hügeln mit Ausblick auf ganz Azshara. Er ist zwar ein Mensch, aber er benimmt sich wie kein Mensch, den ich jemals getroffen...oder getötet habe. Er strahlt Macht aus und ich wage nicht, ihn zu fragen, was er mit der Schrifttafel vorhat.]], [[Er kam zu mir, aber er teilte mir mit, dass seine Leibwache Sanath mir helfen würde, zum Turm zu kommen, wenn ich bereit sei, ihn zu treffen. Sucht nach Sanath im Norden.]], },
    { [[Bringt die Schrifttafel von Sael'hai zu Erzmagier Xylem in Azshara.]], },
  },
  [3562] = {
    [[Magathas Bezahlung für Jediga]],
    { [[Bringt diese Notiz zu Jediga. Sagt ihr, dass ihre Bezahlung hier auf sie wartet, wenn sie zurückkommt. Und sagt ihr, dass ich mit ihrer Arbeit zufrieden bin.]], [[Beeilt Euch, <name>. Sagt Eurer Meisterin, dass ich hier auf sie warten werde.]], },
    { [[Bringt Magathas Notiz zu Jediga in Azshara.]], },
  },
  [3563] = {
    [[Jes'rimons Bezahlung für Jediga]],
    { [[Bringt diesen Brief zu Jediga. Lasst sie wissen, dass unser Handel vorteilhaft ist und ich mit ihrer Arbeit zufrieden bin.]], [[Sagt ihr, sie soll schnell nach Orgrimmar zurückkommen und Jes'rimon würde sich gut um sie kümmern.]], [[Sagt ihr das und vielleicht benötige ich in Zukunft noch einmal Eure Dienste, <name>.]], },
    { [[Bringt Jes'rimons Notiz zu Jediga in Azshara.]], },
  },
  [3564] = {
    [[Androns Bezahlung für Jediga]],
    { [[Hier... bringt... <hick> das zu Jediga. Viel Glück!]], },
    { [[Bringt Androns Notiz zu Jediga in Azshara.]], },
  },
  [3565] = {
    [[Xylems Bezahlung für Jediga]],
    { [[Was ist? Oh, ja, Eure Bezahlung. Natürlich.]], [[Nehmt das und fort mit Euch. Jediga wartet bestimmt schon voller Geduld auf Eure Rückkehr.]], },
    { [[Bringt Xylems Notiz zu Jediga in Azshara.]], },
  },
  [3566] = {
    [[Erhebt Euch, Obsidion!]],
    { [[Tötet Lathoric den Schwarzen und Obsidion. Kehrt zurück zu Thorius nach Eisenschmiede mit dem Kopf von Lathoric dem Schwarzen und dem Herzen von Obsidion.]], },
    { [[Tötet Lathoric den Schwarzen und Obsidion. Kehrt zurück zu Thorius nach Eisenschmiede mit dem Kopf von Lathoric dem Schwarzen und dem Herzen von Obsidion.]], },
  },
  [3567] = {
    [[An die Spitze]],
  },
  [3568] = {
    [[Verbreitete Verderbnis]],
    { [[Seht Ihr die Tauren hinter mir - diejenige, die aussieht, als müsste sie sich jeden Moment übergeben? Sie war erst vor Kurzem in Azshara. Sie behauptet, dass sie von einigen Naga verwundet wurde, als sie sich den Weg durch die Ruinen von Eldarath bahnte.]], [[Nachdem sie einige von ihnen zurückschlagen konnte, machte sie eine Pause am Strand und trank aus einem der Gezeitenteiche. Jetzt ist ihr ein wenig übel... unter anderem.]], [[Ich würde gern herausfinden, was sie getrunken hat. Nehmt diesen Kasten, darin befinden sich einige leere Phiolen, die ich mit Etiketten versehen habe. Füllt jede an einem anderen Teich und kehrt dann zurück.]], },
    { [[Füllt alle 4 leeren Phiolen an den Gezeitenteichen entlang der Küste der Ruinen von Eldarath in Azshara und kehrt dann zu Chemiker Miesmut zurück.]], },
  },
  [3569] = {
    [[Verbreitete Verderbnis]],
    { [[Gebt das Thersa. Vielleicht wird das hier sie heilen... vielleicht aber auch nicht. Wir werden es nicht wissen, solange sie es nicht getrunken hat.]], },
    { [[Gebt Miesmuts Elixier Thersa Windweise in Unterstadt.]], },
  },
  [3570] = {
    [[Verbreitete Verderbnis]],
  },
  [3601] = {
    [[Kim'jael, fürwahr!]],
    { [[Kim'jael, fürwahr! Wisst Ihr, was Kim'jael für die Blutelfen bedeutet, <name>? Kleine Ratte... oder so etwas in der Art. Diese großen Angeber!]], [[Sie haben mich angeworben, damit ich ihnen helfe, das Gebiet auszukundschaften und nach einer Art verlorenen Schatz zu suchen. Natürlich habe ich sofort zugegriffen, als sie das Wort 'Schatz' erwähnten und die Summe, die sie mir bezahlen wollten. Aber dann haben sie sich gegen mich gewandt und versucht, mich zu töten... MICH!]], [[Das zeigt einmal mehr, dass einem Blutelfen nicht zu trauen ist.]], [[Mein Problem ist: Sie haben immer noch meine Ausrüstung und ich kann sie nicht wiederbekommen. Helft Ihr mir?]], },
    { [[Durchsucht den thalassischen Stützpunkt nach Kim'jaels Kompass, Zielfernrohr, ausgestopftem Huhn und Knobelwizz und bringt ihm dann alles nach Azshara zurück.]], },
  },
  [3602] = {
    [[Azsharit]],
    { [[Die uralten Klippenriesen aus dem südlichen Azshara ernähren sich von Felsen und Gebäuden des Landes. Ihre Ausscheidung ist, bedingt durch die Ernährung, ein höchst formbarer und extrem starker Kristall, der als Azsharit bekannt ist.]], [[Der südliche Bogen ist übersät mit diesen Gebilden, aber es konnten nur wenige geborgen werden, da die Riesen über ein extrem angriffslustiges Naturell verfügen.]], [[Ihr sollt nach Süden vorstoßen und genügend Azsharit einsammeln, um den Waffenkörper herzustellen. Meine Teufelshunde werden Euch beim Einsammeln der Kristalle behilflich sein.]], },
    { [[Sammelt 20 kristallisierte Azsharite im südlichen Azshara. Ihr könnt den Teufelshund benutzen, damit er Euch beim 'Erschnüffeln' der gut versteckten Ablagerungen behilflich ist.]], [[Kehrt zu Loramus zurück, sobald Ihr die Aufgabe erfüllt habt.]], },
  },
  [3621] = {
    [[Die Herstellung von Teufelsbann]],
    { [[Die Waffen können jetzt geschmiedet werden. Ihr müsst den ausgehärteten Azsharit zu jemandem bringen, der die Fähigkeit besitzt, Teufelsenergie zu bändigen.]], [[Im Schlingendorntal gibt es einen solchen Schmied. Galvan der Alte, Anführer des Mithrilordens, wird die Waffen nach Euren Angaben fertigen. Ihr könnt sein Lager zwischen Zul'Gurub und dem Ogerhügel der Mosh'Ogg finden.]], [[Mögen Eure Entschlossenheit und Euer Glaube Euch durch diese Tortur leiten. Solltet Ihr versagen, wisset, dass Euer Gott auf Euch herablächelt, sterbliches Wesen.]], },
    { [[Bringt die Lieferung zu Galvan dem Alten im Schlingendorntal.]], },
  },
  [3625] = {
    [[Verzauberte Azsharitteufelswaffe]],
    { [[Wartet, bis Galvan die Waffen fertig geschmiedet hat.]], },
    { [[Wartet, bis Galvan die Waffen fertig geschmiedet hat.]], },
  },
  [3626] = {
    [[Rückkehr in die Verwüsteten Lande]],
    { [[Kehrt zum Gefallenen Helden der Horde zurück. Zu finden ist er an der Splitterspeerkreuzung in den Sümpfen des Elends.]], },
    { [[Kehrt zum Gefallenen Helden der Horde zurück. Zu finden ist er an der Splitterspeerkreuzung in den Sümpfen des Elends.]], },
  },
  [3627] = {
    [[Das zerbrochene Amulett vereinen]],
    { [[Tötet Grol den Zerstörer, Lady Sevine und Erzmagier Allistarj. Nehmt das Amulett von Grol, das Amulett von Sevine und das Amulett von Allistarj von ihren Leichen.]], [[Denkt daran, Eure Verzauberte Azsharitteufelswaffe zu BENUTZEN, um die Triade zu schwächen. Auf eine andere Weise können sie nicht getötet werden - aber verwendet sie weise...]], },
    { [[Tötet Grol den Zerstörer, Lady Sevine und Erzmagier Allistarj. Nehmt das Amulett von Grol, das Amulett von Sevine und das Amulett von Allistarj von ihren Leichen.]], [[Denkt daran, Eure Verzauberte Azsharitteufelswaffe zu BENUTZEN, um die Triade zu schwächen. Auf eine andere Weise können sie nicht getötet werden - aber verwendet sie weise...]], },
  },
  [3628] = {
    [[Ihr seid Rakh'likh, Dämon]],
    { [[Vernichtet Razelikh den Entweiher und bringt das abgespaltene Horn des Entweihers zum Gefallenen Helden der Horde. Ihr müsst außerdem den Zauberschutz des Entweihers mit zurückbringen, damit der Gefallene Held ihn zerstören und somit verhindern kann, dass er jemals in die falschen Hände fällt.]], },
    { [[Vernichtet Razelikh den Entweiher und bringt das abgespaltene Horn des Entweihers zum Gefallenen Helden der Horde. Ihr müsst außerdem den Zauberschutz des Entweihers mit zurückbringen, damit der Gefallene Held ihn zerstören und somit verhindern kann, dass er jemals in die falschen Hände fällt.]], },
  },
  [3629] = {
    [[Gobliningenieurskunst]],
    { [[Bei der Experteningenieurskunst gibt es zwei unterschiedliche Disziplinen: Gnom und Goblin. Ich erwähne die Ingenieurskunst der Goblins nur, weil ich ein gerechter und unvoreingenommener Lehrer bin. Sie machen gern Dinge, die in die Luft gehen und es ist ihnen dabei meistens egal, ob dabei was im Weg herumsteht - auch wenn sie es selbst sind.]], [[Lest dieses Handbuch, <name>. Wenn Ihr lernen wollt, wie Ihr Euch selbst in die Luft jagt, dann bringt es zu Nixx Sprossfeder nach Gadgetzan. Denkt daran - das ist eine dauerhafte Mitgliedschaft. Es ist also nicht möglich, auch in die andere Disziplin einzutreten, daher solltet Ihr genau wissen, was Ihr wollt, bevor Ihr seine Aufgabe ausführt.]], },
    { [[Wenn Ihr mehr über die Gobliningenieurskunst wissen wollt, dann bringt das Handbuch der Ingenieurskunstdisziplinen zu Nixx Sprossfeder nach Gadgetzan.]], },
  },
  [3630] = {
    [[Gnomeningenieurskunst]],
    { [[Ingenieurskunst auf Expertenniveau gliedert sich in zwei Disziplinen auf: für Gnome und für Goblins. Gnomeningenieurskunst ist die weit Überlegenere der beiden! Es geht um die Konstruktion von wunderbaren Apparaten und Geräten, die das Leben schöner machen.]], [[<name>, lest diese Anleitung. Wenn Ihr mehr über Gnomeningenieurskunst lernen wollt, dann bringt sie zu Tüftlermeister Oberfunks in Eisenschmiede. Denkt daran - das ist eine dauerhafte Mitgliedschaft und es ist nicht möglich, in die andere Disziplin einzutreten, also solltet Ihr Euch absolut sicher sein, dass Ihr Euch sicher seid, bevor Ihr seine Aufgabe ausführt.]], },
    { [[Wenn Ihr etwas über die Gnomeningenieurskunst erlernen wollt, dann bringt die Anleitung der Ingenieurskunstdisziplinen zu Tüftlermeister Oberfunks nach Eisenschmiede.]], },
  },
  [3631] = {
    [[Teufelsross beschwören]],
    { [[Hmpf. Ich sage, Ihr seid nicht vorbereitet, aber das ist nicht meine Entscheidung. Strahad empfindet das anders und es ist seine Entscheidung... dieses Mal.]], [[Er wünscht mit Euch zu sprechen, und das schnell. Ihr werdet ihn immer noch über Ratschet im Brachland finden, in seinem kleinen Turm dort, mit seinen Akolythen.]], [[Trödelt nicht herum.]], },
    { [[Sprecht mit Strahad Farsan im Brachland.]], },
  },
  [3632] = {
    [[Gnomeningenieurskunst]],
    { [[Ingenieurskunst auf Expertenniveau gliedert sich in zwei Disziplinen auf: Gnom und Goblin. Gnomeningenieurskunst ist die weit Überlegenere der beiden! Es geht um die Konstruktion von wunderbaren Apparaten und Geräten, die das Leben schöner machen.]], [[<name>, lest diese Anleitung. Wenn Ihr mehr über Gnomeningenieurskunst lernen wollt, dann bringt sie zu Tüftlermeister Oberfunks hier in Eisenschmiede. Denkt daran - das ist eine dauerhafte Mitgliedschaft und es ist nicht möglich, in die andere Disziplin einzutreten, also solltet Ihr Euch absolut sicher sein, dass Ihr Euch sicher seid, bevor Ihr seine Aufgabe ausführt.]], },
    { [[Wenn Ihr etwas über die Gnomeningenieurskunst erlernen wollt, dann bringt die Anleitung der Ingenieurskunstdisziplinen zu Tüftlermeister Oberfunks nach Eisenschmiede.]], },
  },
  [3633] = {
    [[Gobliningenieurskunst]],
    { [[Bei der Experteningenieurskunst gibt es zwei unterschiedliche Disziplinen: Gnom und Goblin. Natürlich ist die Gobliningenieurskunst die Wahl aller brillanten Ingenieure. Durch den klugen Einsatz von hochempfindlichen Sprengstoffen und phantastischen Ausrüstungsgegenständen lernt Ihr, die Welt zu meistern!]], [[<name>, lest diese Anleitung. Wenn Ihr etwas über Gobliningenieurskunst wissen wollt, dann bringt sie zu Nixx Sprossfeder nach Gadgetzan. Denkt daran - das ist eine dauerhafte Mitgliedschaft. Es ist also nicht möglich, auch in die andere Disziplin einzutreten, daher solltet Ihr genau wissen, was Ihr wollt, bevor Ihr seine Aufgabe ausführt.]], },
    { [[Wenn Ihr mehr über die Gobliningenieurskunst wissen wollt, dann bringt das Handbuch der Ingenieurskunstdisziplinen zu Nixx Sprossfeder nach Gadgetzan.]], },
  },
  [3634] = {
    [[Gnomeningenieurskunst]],
    { [[Ingenieurskunst auf Expertenniveau gliedert sich in zwei Disziplinen auf: für Gnome und für Goblins. Ich bin zwar der Meinung, dass es Verschwendung ist, aber Ihr solltet doch etwas über die Gnomenspezialisierung wissen. Sie machen Geräte und Apparate, die angeblich die Welt verändern, aber meistens gar nicht funktionieren.]], [[<name>, lest diese Anleitung. Wenn Ihr lernen wollt, wie man Briefbeschwerer aus Metall herstellt, dann bringt sie zu Tüftlermeister Oberfunks in Eisenschmiede. Denkt daran - das ist eine dauerhafte Mitgliedschaft und es ist nicht möglich, in die andere Disziplin einzutreten, also solltet Ihr Euch absolut sicher sein, dass Ihr Euch sicher seid, bevor Ihr seine Aufgabe ausführt.]], },
    { [[Wenn Ihr etwas über die Gnomeningenieurskunst erlernen wollt, dann bringt die Anleitung der Ingenieurskunstdisziplinen zu Tüftlermeister Oberfunks nach Eisenschmiede.]], },
  },
  [3635] = {
    [[Gnomeningenieurskunst]],
    { [[Ingenieurskunst auf Expertenniveau gliedert sich in zwei Disziplinen: Gnom und Goblin. Ich bin zwar der Meinung, dass es reine Verschwendung ist, aber Ihr solltet doch etwas über die Spezialisierung der Gnome wissen. Sie machen Gegenstände und Apparate, die angeblich die Welt verändern, aber meistens gar nicht funktionieren.]], [[<name>, lest diese Anleitung. Wenn Ihr lernen wollt, wie man Briefbeschwerer aus Metall herstellt, dann bringt sie zu Glotz Widrikus in Beutebucht. Denkt daran - das ist eine dauerhafte Mitgliedschaft. Es ist also nicht möglich, auch in die andere Disziplin einzutreten, daher solltet Ihr genau wissen, was Ihr wollt, bevor Ihr seine Aufgabe ausführt.]], },
    { [[Wenn Ihr mehr über die Gnomeningenieurskunst erlernen wollt, dann bringt die Anleitung der Ingenieurskunstdisziplinen zu Glotz Widrikus nach Beutebucht.]], },
  },
  [3636] = {
    [[Das Licht bringen]],
    { [[Die Geißel ist eine dauerhafte Bedrohung für die Allianz. Ich bin sicher, Euch ist diese Tatsache vertraut. Kürzlich erst stießen wir auf einige sehr seltsame Funde, <name>.]], [[Im Brachland gibt es ein fürchterliches Dickicht von Dornsträuchern, das Hügel der Klingenhauer heißt. Früher war es die Heimat von einfachen Stachelebern, aber nun ist offensichtlich, dass ein Pakt mit der Geißel geschlossen wurde, die viel schlimmere Kreaturen hervorbrachte... Sie werden jetzt von dem Lich, Amnennar dem Kältebringer, beherrscht.]], [[Stellt Euch auf die Seite des Lichts, <name>, und macht diesem bösen Verbund ein Ende.]], },
    { [[Erzbischof Benedictus will, dass Ihr Amnennar den Kältebringer in den Hügeln der Klingenhauer tötet.]], },
  },
  [3637] = {
    [[Gnomeningenieurskunst]],
    { [[Ingenieurskunst auf Expertenniveau gliedert sich in zwei Disziplinen: Gnom und Goblin. Ich bin zwar der Meinung, dass es reine Verschwendung ist, aber Ihr solltet doch etwas über die Spezialisierung der Gnome wissen. Sie machen Gegenstände und Apparate, die angeblich die Welt verändern, aber meistens gar nicht funktionieren.]], [[<name>, lest diese Anleitung. Wenn Ihr lernen wollt, wie man Briefbeschwerer aus Metall herstellt, dann bringt sie zu Glotz Widrikus in Beutebucht. Denkt daran - das ist eine dauerhafte Mitgliedschaft. Es ist also nicht möglich, auch in die andere Disziplin einzutreten, daher solltet Ihr genau wissen, was Ihr wollt, bevor Ihr seine Aufgabe ausführt.]], },
    { [[Wenn Ihr mehr über die Gnomeningenieurskunst erlernen wollt, dann bringt die Anleitung der Ingenieurskunstdisziplinen zu Glotz Widrikus nach Beutebucht.]], },
  },
  [3638] = {
    [[Der Schwur der Geheimhaltung]],
    { [[Bei der Gobliningenieurskunst geht es um praktische Anwendungen mit hohem Profit und noch höherer Explosionskraft! Unsere Baupläne ermöglichen uns, mächtige Bomben und gewaltige Waffen hervorzubringen. Die Anleitung, die Ihr gelesen habt, berichtet darüber.]], [[Die Anleitung erwähnt auch unser uraltes Gelöbnis der Geheimhaltung unserer Baupläne. Sobald Ihr ein Gobliningenieur geworden seid, war's das. Es gibt keinerlei Zugang zur Gnomeningenieurskunst.]], [[Wenn Ihr immer noch ein Gobliningenieur werden wollt, dann unterzeichnet dieses Dokument, mit dem Ihr eine lebenslange Geheimhaltung gelobt, und sprecht dann wieder mit mir. Denkt gut darüber nach, <name>.]], },
    { [[Wenn Ihr ein Gobliningenieur werden wollt, dann müsst Ihr auf den Schwur der Geheimhaltung rechtsklicken und erneut mit Nixx Sprossfeder in Gadgetzan sprechen.]], },
  },
  [3639] = {
    [[Zeigt Eure Arbeit]],
    { [[Bringt 20 große Eisenbomben, 20 robuste Dynamitstangen und 5 explosive Schafe zu Nixx Sprossfeder nach Gadgetzan.]], },
    { [[Bringt 20 große Eisenbomben, 20 robuste Dynamitstangen und 5 explosive Schafe zu Nixx Sprossfeder nach Gadgetzan.]], },
  },
  [3640] = {
    [[Der Schwur der Geheimhaltung]],
    { [[Die Gnomeningenieurskunst beruht auf der Ingenieurskunsttheorie, die besagt, dass sie unseren Bedürfnissen zu dienen hat! Unsere Baupläne ermöglichen uns, Geräte hervorzubringen, die die Welt um uns kontrollieren. Die Anleitung, die Ihr gelesen habt, berichtet darüber.]], [[Die Anleitung erwähnt auch unser uraltes Gelöbnis der Geheimhaltung unserer Baupläne. Sobald Ihr ein Gnomeningenieur geworden seid, war's das. Es gibt keinerlei Zugang zur Gobliningenieurskunst.]], [[Wenn Ihr immer noch ein Gnomeningenieur werden wollt, dann unterzeichnet dieses Dokument, mit dem Ihr eine lebenslange Geheimhaltung gelobt und sprecht dann wieder mit mir. Denkt gut darüber nach, <name>.]], },
    { [[Wenn Ihr ein Gnomeningenieur werden wollt, dann müsst Ihr auf den Schwur der Geheimhaltung rechtsklicken und erneut mit Tüftlermeister Oberfunks in Eisenschmiede sprechen.]], },
  },
