  [3661] = {
    [[Elunes Lieblinge?]],
    { [[Wildekin sind unberechenbare Kreaturen, <name>. Sie sind zu einer außerordentlichen Zärtlichkeit fähig, aber diese Kreaturen können auch eine enorme Wildheit an den Tag legen, wenn etwas für sie sehr Wichtiges bedroht wird.]], [[Diese Kreaturen wurden angeblich von Elune geschaffen und ich bin daran interessiert herauszufinden, ob dies wirklich der Wahrheit entspricht.]], [[Vor Kurzem habe ich etwas von einer Population von Wildekin gehört, die im Hinterland leben - heimtückische, primitive und wilde Eulenbestien. Man findet sie unter den wild lebenden Tieren dort. Würdet Ihr einige Wildekinfedern für mich sammeln?]], },
    { [[Sammelt 15 Wildekinfedern aus dem Hinterland für Erelas Himmelsbern in Rut'theran.]], },
  },
  [3681] = {
    [[Foliant der Offenbarung]],
    { [[Verzeiht, dass wir Eure Reise unterbrechen, <race>, aber ich dachte, Ihr solltet wissen, dass uns vor Kurzem eine Meldung aus Sturmwind erreicht hat, dass sich Paladine zum Training dort melden sollen.]], [[Wenn ich es nicht besser wüsste, würde ich sagen, dass bald etwas Besonderes geschehen wird und es für Euch an der Zeit ist, die Folianten zu studieren.]], [[Sucht Duthorian Rall in Sturmwind auf. Er trainiert für gewöhnlich Paladine in der Kathedrale des Lichts.]], },
    { [[Sprecht mit Duthorian Rall in Sturmwind.]], },
  },
  [3701] = {
    [[Die glimmenden Ruinen von Thaurissan]],
    { [[Begebt Euch zu den glimmenden Ruinen von Thaurissan in der Brennenden Steppe und sammelt so viele Informationen wie möglich.]], [[Es heißt, dass geheimnisvolle Relikte in den Ruinen verstreut seien. Vielleicht solltet Ihr Eure Suche dort beginnen, <name>. Nehmt Euch vor den thaurissanischen Wächtern in Acht: Es sind Auftragsmörder, die die Ruinen bewachen und die dort möglicherweise noch immer begrabenen Geheimnisse beschützen, wie auch immer sie aussehen mögen.]], },
    { [[Begebt Euch zu den Ruinen von Thaurissan in der Brennenden Steppe und sammelt Informationen über die Relikte von Thaurissan. Kehrt zur königlichen Historikerin Archesonus zurück, wenn Ihr die Informationen besorgt habt.]], },
  },
  [3702] = {
    [[Die glimmenden Ruinen von Thaurissan]],
    { [[Würdet Ihr gern einen Überblick über die Geschichte der Dunkeleisenzwerge erhalten?]], },
    { [[Hört die Erzählung der königlichen Historikerin Archesonus über die Geschichte von Thaurissan.]], },
  },
  [3721] = {
    [[Ein eigener OOX]],
  },
  [3741] = {
    [[Hilarys Halskette]],
    { [[Meine Freundin Hilary hat ihre Halskette verloren und wir können sie einfach nicht finden. Den ganzen Tag lang fischen wir schon hier und haben auch schon rundum überall gesucht. Ich kann mir nur vorstellen, dass sie in den See gefallen ist. Unsere Eltern möchten nicht, dass wir im See schwimmen... meine Mama sagt, dass es unter Wasser ein paar garstige Dinge gibt, aber Ihr seht aus, als ob Ihr keine Angst vor garstigen Dingen hättet.]], [[Könnt Ihr die Kette bitte für uns suchen?]], },
    { [[Sucht Hilarys Halskette und bringt sie Hilary in Seenhain zurück.]], },
  },
  [3761] = {
    [[Erde von Un'Goro]],
    { [[Die Erde des Kraters von Un'Goro ist angeblich reich an starken magischen Eigenschaften. Der Zirkel des Cenarius möchte diese Erde in großen Mengen untersuchen; zieht daher in die Wildnis von Un'Goro und sammelt genug davon für unsere ständig laufenden Forschungen.]], [[Die angereicherte Erde ist manchmal zu Haufen aufgeworfen und einige der Wildtiere könnten vielleicht nützliche Proben bei sich haben. Bringt sie raus zu Ghede; sucht einfach nach den Erdhaufen und dem Tauren, der Befehle brüllt. Er ist kein schlechter Kerl, aber verärgert ihn lieber nicht!]], },
    { [[Bringt 20 Erdproben von Un'Goro zu Ghede in Donnerfels.]], },
  },
  [3762] = {
    [[Erzdruide Runentotem helfen]],
    { [[Entschuldigung, <class>, könnt Ihr einen Moment Eurer Zeit für mich erübrigen?]], [[Der Zirkel des Cenarius in Donnerfels ist sehr an erfahrenen Abenteurern wie Euch interessiert, die bei einem wichtigen Forschungsprojekt helfen können. Ich weiß zwar nichts Genaues, kann Euch aber sagen, dass kein Geringerer als Hamuul Runentotem an der Spitze dieses Projektes steht.]], [[Bitte... wenn Ihr interessiert seid, sprecht direkt mit ihm auf der Anhöhe der Ältesten in Donnerfels.]], },
    { [[Sprecht in Donnerfels mit Erzdruide Hamuul Runentotem.]], },
  },
  [3763] = {
    [[Erzdruide Hirschhaupt helfen]],
    { [[Entschuldigung, <class>, könnt Ihr einen Moment Eurer Zeit für mich erübrigen?]], [[Der Zirkel des Cenarius in Darnassus sucht nach erfahrenen Abenteurern wie Euch, die bei einem wichtigen Forschungsprojekt helfen können. Ich weiß zwar nichts Genaues, kann Euch aber sagen, dass kein geringerer als Erzdruide Fandral Hirschhaupt an der Spitze dieses Projektes steht.]], [[Bitte... wenn Ihr interessiert seid, sprecht direkt mit ihm in der Enklave des Cenarius in Darnassus.]], },
    { [[Sprecht mit Erzdruide Fandral Hirschhaupt in Darnassus.]], },
  },
  [3764] = {
    [[Erde von Un'Goro]],
    { [[Die Erde des Kraters von Un'Goro ist angeblich reich an starken magischen Eigenschaften. Ich möchte Wahrheit von Unwahrheit unterscheiden; begebt Euch daher in die Wildnis von Un'Goro und sammelt ausreichend hochwertige Erdproben für unsere ständig laufenden Studien.]], [[Ihr glaubt möglicherweise, dass die Aufgabe, etwas zu sammeln, unter Eurer Würde sei; doch mit solch einer Einstellung macht Ihr Euch weder bei mir noch beim Zirkel des Cenarius beliebt. Bringt die Proben zu Jenal, der zweifellos draußen hinter diesem Baum müßig herumlungert; sucht einfach nach den Erdhaufen. Er wird sich um die Logistik kümmern, nicht ich.]], },
    { [[Bringt 20 Erdproben von Un'Goro zu Jenal in Darnassus.]], },
  },
  [3765] = {
    [[Verderbnis in der Fremde]],
    { [[Sturmwind - geschäftiger Dreh- und Angelpunkt der Aktivität.]], [[Ich bin weit gereist, um Hilfe zu suchen. Man hört häufig, dass mutige Abenteurer durch die Straßen dieser Stadt ziehen.]], [[Mein Bruder Gershala benötigt Hilfe in Dunkelküste. Er lebt in der Küstenstadt Auberdine.]], [[Die Reise ist lang und abenteuerlich. Daher suche ich nach Hilfe. Es sollte <ein erfahrener/eine erfahrene><class> <class> sein, so wie Ihr.]], },
    { [[Reist zu Gershala Nachtraunen in Auberdine.]], },
  },
  [3781] = {
    [[Morgenkornforschung]],
    { [[Mit dem, was wir einen Immergrünbeutel nennen, haben wir Tharlendrissamenkörner in der Erde des Kraters von Un'Goro kultiviert. Diese Samenkörner sind zu einer Ansammlung wirksamer Kräuter erblüht. Ein Ergebnis ist das Morgenkorn, ein rätselhaftes Kraut, von dem wir wenig wissen; ich habe vor, dieses Geheimnis zu enthüllen.]], [[Bringt das nach unten zu Mathrengyl, und er wird Euch einige Samenkörner geben, mit denen Ihr beginnen könnt. Wenn Ihr diese verbraucht habt, müsst Ihr weitere von ihm kaufen.]], },
    { [[Bringt den Samenkorngutschein des Erzdruiden zu Mathrengyl Bärenfährte in der Enklave des Cenarius von Darnassus.]], },
  },
  [3782] = {
    [[Morgenkornforschung]],
    { [[Mit dem, was wir einen Immergrünbeutel nennen, haben wir Tharlendrissamenkörner in der Erde des Kraters von Un'Goro kultiviert. Diese Samenkörner sind zu einer Ansammlung wirksamer Kräuter erblüht. Ein Ergebnis ist das Morgenkorn, ein rätselhaftes Kraut, von dem wir wenig wissen; ich hoffe, dieses Geheimnis für den Zirkel zu enthüllen.]], [[Bringt diesen Gutschein zu meiner Tochter Bashana, und sie wird Euch einige Samenkörner geben, damit Ihr uns helfen könnt. Wenn Ihr diese verbraucht habt, müsst Ihr weitere von ihr kaufen.]], },
    { [[Bringt den Samenkorngutschein zu Bashana Runentotem in Donnerfels.]], },
  },
  [3783] = {
    [[Sind wir endlich da, Yeti?]],
    { [[Meine Freunde hatten immerzu tolle Ideen. Ich hatte nie das Gefühl mit ihnen mithalten zu können... bis heute! Gerade habe ich die Pläne für etwas Großes angefertigt! Wenn Ihr versprecht, es nicht zu erzählen, weihe ich Euch in das Geheimnis ein...]], [[Ich werde einen mechanischen Yeti herstellen! Meine Freunde werden ganz bestimmt durchdrehen, wenn sie ihn sehen, aber wenn sie darüber hinweg sind, bin ich mir sicher, dass sie beeindruckt sein werden!]], [[Für den Anfang benötige ich dickes Yetifell von Eisdistelyetis. Werdet Ihr helfen? Ihr könnt sie im Südosten finden.]], },
    { [[Sammelt 10 dicke Yetifelle für Umi Rumpelkicher in der Ewigen Warte.]], },
  },
  [3784] = {
    [[Erzdruide Runentotem helfen]],
    { [[Entschuldigung, <class>, könnt Ihr einen Moment Eurer Zeit für mich erübrigen?]], [[Der Zirkel des Cenarius in Donnerfels ist sehr an erfahrenen Abenteurern wie Euch interessiert, die bei einem wichtigen Forschungsprojekt helfen können. Ich weiß zwar nichts Genaues, kann Euch aber sagen, dass kein Geringerer als Hamuul Runentotem an der Spitze dieses Projektes steht.]], [[Bitte... wenn Ihr interessiert seid, sprecht direkt mit ihm auf der Anhöhe der Ältesten in Donnerfels.]], },
    { [[Sprecht in Donnerfels mit Erzdruide Hamuul Runentotem.]], },
  },
  [3785] = {
    [[Morgenkornforschung]],
    { [[Hier ist Euer Immergrünbeutel, den Ihr für die Nutzung der Tharlendrissamenkörner benötigen werdet. Ihr benötigt außerdem noch Erdproben von Un'Goro als Dünger für die Samenkörner. Verwendet einfach den Beutel, wenn Ihr alles habt; der Beutel wird den Rest erledigen.]], [[Wenn Ihr genug Einheiten Morgenkorn habt, bringt sie mir hierher in die Enklave. Und belästigt bitte nicht den Erzdruiden; er ist ein sehr beschäftigter Mann und muss nicht jedes Mal gestört werden, wenn jemand mit einer Ladung Morgenkorn zurückkehrt!]], },
    { [[Benutzt einen Immergrünbeutel mit einem Paket Tharlendrissamenkörner sowie zwei Erdproben von Un'Goro, um Morgenkorn zu kultivieren.]], [[Bringt 10 Einheiten Morgenkorn zu Mathrengyl Bärenfährte in Darnassus.]], },
  },
  [3786] = {
    [[Morgenkornforschung]],
    { [[Hier ist Euer Immergrünbeutel, den Ihr für die Nutzung der Tharlendrissamenkörner benötigen werdet. Ihr benötigt außerdem noch Erdproben von Un'Goro als Dünger für die Samenkörner. Verwendet einfach den Beutel, wenn Ihr alles habt; der Beutel wird den Rest erledigen.]], [[Wenn Ihr genug Einheiten Morgenkorn habt, bringt sie zu mir hierher auf die Anhöhe der Ältesten. Belästigt bitte nicht den Erzdruiden; er wird sehr mit den Forschungsarbeiten beschäftigt sein sowie mit den tausend anderen Sorgen, die er hat!]], },
    { [[Benutzt einen Immergrünbeutel mit einem Paket Tharlendrissamenkörner sowie zwei Erdproben von Un'Goro, um Morgenkorn zu kultivieren.]], [[Bringt 10 Einheiten Morgenkorn zu Bashana Runentotem in Donnerfels.]], },
  },
  [3787] = {
    [[Lebenshands Bitte]],
    { [[Entschuldigung...]], [[Ein Kollege von mir würde gern mit Euch über die Forschungsarbeiten sprechen, die Ihr zusammen mit dem Zirkel des Cenarius durchführt. Er ist ein Druide namens Quintis Lebenshand und lebt in der Mondfederfeste in Feralas. Er würde gern mit Euch über Morgenkorn reden... und er würde es vorziehen, wenn unser Gespräch dem Erzdruiden nicht zu Ohren kommt.]], [[Falls Ihr interessiert seid, dann sprecht mit ihm; zweifellos wird alles klargelegt, wenn Ihr es tut.]], },
    { [[Sprecht mit Quintis Lebenshand in der Mondfederfeste.]], },
  },
  [3788] = {
    [[Lebenshands Bitte]],
    { [[Entschuldigung...]], [[Ein Kollege von mir würde gern mit Euch über die Forschungsarbeiten sprechen, die Ihr zusammen mit dem Zirkel des Cenarius durchführt. Er ist ein Druide namens Quintis Lebenshand und lebt hier in der Mondfederfeste. Er würde gern mit Euch über Morgenkorn reden... und er würde es vorziehen, wenn unser Gespräch dem Erzdruiden nicht zu Ohren kommt.]], [[Falls Ihr interessiert seid, dann sprecht mit ihm; zweifellos wird alles klargelegt, wenn Ihr es tut.]], },
    { [[Sprecht mit Quintis Lebenshand in der Mondfederfeste.]], },
  },
  [3789] = {
    [[Erzdruide Hirschhaupt helfen]],
    { [[Entschuldigung, <class>, könnt Ihr einen Moment Eurer Zeit für mich erübrigen?]], [[Der Zirkel des Cenarius in Darnassus sucht nach erfahrenen Abenteurern wie Euch, die bei einem wichtigen Forschungsprojekt helfen können. Ich weiß zwar nichts Genaues, kann Euch aber sagen, dass kein geringerer als Erzdruide Fandral Hirschhaupt an der Spitze dieses Projektes steht.]], [[Bitte... wenn Ihr interessiert seid, sprecht direkt mit ihm in der Enklave des Cenarius in Darnassus.]], },
    { [[Sprecht mit Erzdruide Fandral Hirschhaupt in Darnassus.]], },
  },
  [3790] = {
    [[Erzdruide Hirschhaupt helfen]],
    { [[Entschuldigung, <class>, könnt Ihr einen Moment Eurer Zeit für mich erübrigen?]], [[Der Zirkel des Cenarius in Darnassus sucht nach erfahrenen Abenteurern wie Euch, die bei einem wichtigen Forschungsprojekt helfen können. Ich weiß zwar nichts Genaues, kann Euch aber sagen, dass kein geringerer als Erzdruide Fandral Hirschhaupt an der Spitze dieses Projektes steht.]], [[Bitte... wenn Ihr interessiert seid, sprecht direkt mit ihm in der Enklave des Cenarius in Darnassus.]], },
    { [[Sprecht mit Erzdruide Fandral Hirschhaupt in Darnassus.]], },
  },
  [3791] = {
    [[Das Geheimnis des Morgenkorns]],
    { [[Lasst mich offen mit Euch sein, <name>. Ich bin interessiert zu wissen - nein, ich muss es unbedingt wissen, warum der Erzdruide in Darnassus den Namen des Zirkels des Cenarius sowie dessen Mittel benutzt, um große Mengen Morgenkorn zu beschaffen. Ich bin ein Forscher - Flüche sind meine Spezialität. Ich habe gehört, dass Morgenkorn unter den richtigen Bedingungen Fähigkeiten verströmt wie andere Kräuter, die in primitiven Flüchen eingesetzt werden.]], [[Ich möchte mehr darüber wissen und bitte Euch um Eure Hilfe. Bringt mir 10 Einheiten Morgenkorn und ich werde mich um Euch kümmern.]], },
    { [[Bringt 10 Einheiten Morgenkorn zu Quintis Lebenshand in der Mondfederfeste.]], },
  },
  [3792] = {
    [[Morgenkorn für die Mondfederfeste]],
  },
  [3801] = {
    [[Dunkeleisenerbe]],
    { [[Seid gegrüßt, <name>. Ich bin Franclorn Schmiedevater. Ja, der Franclorn Schmiedevater, der für die Steinwerkmethode der Architektur verantwortlich ist. Ihr habt zweifellos während Eurer Abenteuer in unserer vom Krieg zerrissenen Welt eines meiner Werke durchquert oder habt auf einem gestanden.]], [[Ihr fragt Euch wahrscheinlich, warum ich hier bin und was ich von Euch möchte - beides berechtigte Fragen.]], [[Ich brauche natürlich Eure Hilfe. Im Austausch für Eure Hilfe gewähre ich Euch den Schlüssel zu den Tiefen meiner verfluchten Stadt.]], },
    { [[Sprecht mit Franclorn Schmiedevater bei Schmiedevaters Grabmal im Schwarzfels, wenn Ihr daran interessiert seid, einen Schlüssel für die Hauptstadt zu erhalten.]], },
  },
  [3802] = {
    [[Dunkeleisenerbe]],
    { [[Ihr findet Fineous Dunkelader jenseits des Rings des Gesetzes in der Halle des Handwerks. Tötet den elenden Wicht und nehmt Eisenhölle an Euch.]], [[Bringt Eisenhölle zum Schrein von Thaurissan und legt den Hammer an seinen rechtmäßigen Ort: in die Hände der Statue, die mir zu Ehren aufgestellt wurde.]], [[Sobald dieses getan ist, öffnet sich das Fach, in dem ich den Hauptschlüssel verborgen habe. Wichtiger jedoch ist: Eisenhölle wird für alle Zeiten in meinen Händen bleiben. Sollte jemand versuchen, den Hammer zu entfernen, werden Statue und Hammer bersten und für immer verloren sein.]], },
    { [[Erschlagt Fineous Dunkelader und bergt den großen Hammer Eisenhölle. Bringt Eisenhölle zum Schrein von Thaurissan und legt ihn auf die Statue von Franclorn Schmiedevater.]], },
  },
  [3803] = {
    [[Morgenkorn nach Darnassus]],
  },
  [3804] = {
    [[Morgenkorn nach Donnerfels]],
  },
  [3821] = {
    [[Schreckensfels]],
    { [[Sha'ni wurde ausgeschickt, um Berichte über merkwürdige rituelle Aktivitäten zu untersuchen, die vor mehr als drei Wochen von den Feuermagenogern der Brennenden Steppe kamen.]], [[Bluthauer sagt mir, dass er noch nichts wieder von dieser Gruppe gehört hätte und dass es am besten sei, wenn ich einfach weitermache und mich auf meine Aufgabe konzentriere. Könnt Ihr Euch das vorstellen? Das ist doch meine Frau!]], [[Ich kann meinen Posten nicht verlassen, <name>, aber ich muss wissen, was aus Sha'ni geworden ist. Reist in die Brennende Steppe und sucht nach ihr.]], },
    { [[Begebt Euch in die Brennende Steppe und findet heraus, was aus Sha'ni Ehrenhauer geworden ist.]], },
  },
  [3822] = {
    [[Krom'Grul]],
    { [[Meine ganze Gruppe wurde gefangen genommen, <name>. Alle wurden getötet - alle, außer mir.]], [[Ihr Anführer Krom'Grul wollte mich für irgendein Ritual aufsparen.]], [[Ich wurde von dem Biest an diesen Altar gekettet und brutal misshandelt. Mein körperliches Sein gab nach einer Stunde dieser Folter auf. Die Bestie riss mir dann meinen Ehenasenring aus dem Gesicht und steckte ihn an seinen Finger. Noch niemals habe ich solch eine Rohheit gesehen!]], [[Findet Krom'Grul und erledigt ihn! Bringt meinen Ring zurück zu Thal'trak.]], [[Und, <name>, sagt ihm, dass wir uns wiedersehen werden...in einem anderen Leben.]], },
    { [[Holt Sha'nis Nasenring von Krom'Grul zurück und bringt ihn zu Thal'trak in Kargath. Durchsucht die verschiedenen Höhlen und Minen im Schreckensfels.]], },
  },
  [3823] = {
    [[Die Feuermagenoger auslöschen]],
    { [[Wir müssen unsere Feinde schlagen, bevor sie uns schlagen!]], [[Ich werde es kurz machen, Soldat.]], [[Die Feuermagenoger der Brennenden Steppe sind Verbündete der Orcs des Schwarzfelsklans. Sie arbeiten eng mit diesen üblen Orcs zusammen, bewachen den mineralreichen Schreckensfels und halten die Nachschublinien ständig offen.]], [[Hinterlasst Euer Zeichen, <name>. Legt ihre Streitkräfte lahm und kehrt zu mir zurück, wenn der Auftrag erledigt ist.]], [[<Oralius greift in die Luft und macht eine erwürgende Bewegung.>]], [[ICH BRING DICH UM, WINKY!]], },
    { [[Tötet 15 Ogermagier der Feuermägen, 7 Oger der Feuermägen sowie 7 Schläger der Feuermägen.]], },
  },
  [3824] = {
    [[Gor'tesh der Schläger-Lord]],
    { [[Sie sind gelähmt, ich sag's Euch! Unser nächster Schlag muss entscheidend sein.]], [[Der Lord dieser Bestien, Gor'tesh, muss eliminiert werden. Wichtiger noch, sein Kopf muss zu mir zurückgebracht werden, damit ich die dritte Phase unseres Angriffs auf die Feuermagenoger vorbereiten kann.]], [[Seid gewarnt, Soldat, Gor'tesh regiert nicht vom Schreckensfelsen aus. Stattdessen befindet er sich in einem Gebäude der Schwarzfelsorcs westlich vom Schreckensfelsen.]], [[Eure Befehle sind daher einfach. Sucht Gor'tesh und hackt ihm seinen fetten Kopf ab. Kommt zurück, wenn Eure Aufgabe erledigt ist.]], [[KLAPPE HALTEN, WINKY!]], },
    { [[Beschafft Euch Gor'teshs abgehackten Kopf.]], },
  },
  [3825] = {
    [[Ogerkopf auf einem Pfahl = Party]],
    { [[Jetzt müsst Ihr nur noch eine Sache erledigen, Soldat.]], [[Ich habe mir die Freiheit genommen, Gor'teshs Kopf auf einen Pfahl zu stecken. Ihr sollt jetzt einfach diesen Kopf zur Spitze des Schreckensfelsens bringen und ihn in den Boden rammen. Wenn Ihr das getan habt, seht zu, wie diese feigen Oger um ihr Leben rennen! Nachdem Ihr Euren Spaß hattet, kommt zu mir zurück und ich werde Euch für all Eure harte Arbeit belohnen.]], [[<Oralius scheint seinen Bezug zur Realität zu verlieren.>]], },
    { [[Nehmt Gor'teshs abgehackten Kopf und platziert ihn auf dem Gipfel des Schreckensfelsens. Sucht nach einem weichen Erdhügel, um den Pfahl aufzustellen.]], },
  },
  [3841] = {
    [[Waise sucht Zuhause]],
    { [[Ach, hallo, <name>. Ich freue mich sehr, Euch wiederzusehen. Wie war Eure Reise nach Darnassus?]], [[Na, Ihr seid bestimmt froh darüber, dass wir während Eurer Abwesenheit ziemliche Fortschritte gemacht haben. Wir haben sogar ein ganzes Nest voller Eier gerettet. Leider ist die Mutter gestorben, ehe wir sie gefunden haben.]], [[Sagt mal, wollt Ihr nicht vielleicht so ein Tierchen haben? Es sieht aus, als würden sie bald schlüpfen, und dann brauchen sie <einen guten Vater/eine gute Mutter>. Was meint Ihr?]], [[Wenn Ihr wirklich Interesse habt, dann bringt eines der Eier zu Quentin. Ihr findet ihn an der Rennbahn in der Schimmernden Ebene. Er kann Euch mehr darüber sagen.]], },
    { [[Sprecht mit Quentin in Tausend Nadeln.]], },
  },
  [3842] = {
    [[Kurz vor dem Schlüpfen]],
    { [[Die Mondwebers, wie? Überaus interessant. Schade, dass die Grimmlingflitzer nicht schätzen können, was es bedeutet, wenn diese beiden sich um sie kümmern. Beide sind überaus fähig und gut.]], [[Soso, sie haben Euch das Ei also geschenkt. Tja, dann solltet Ihr auch so lange dafür verantwortlich sein, wie Ihr dies wünscht. Das Junge scheint wohl schon bald zu schlüpfen, aber wir sollten trotzdem sichergehen, dass es gesund ist und keinen Schaden genommen hat.]], [[Ich brauche lediglich zwei Elixiere der Seelenstärke für einen kleinen Zauber, der das Ei beschützen wird. Die übrigen Komponenten habe ich bereits hier.]], },
    { [[Bringt 2 Elixiere der Seelenstärke zu Quentin in Tausend Nadeln.]], },
  },
  [3843] = {
    [[Das neueste Mitglied der Familie]],
    { [[Ein prachtvolles Exemplar, <name>. Wenn dieses Geschöpf das Heranreifen übersteht, dann wird es ein wahrhaft gesundes Tier sein!]], [[Aber eines würde ich Euch noch vorschlagen, wenn Ihr es damit wirklich ernst meint - geht zum Nistgipfel im Hinterland. Sprecht dort mit einem Zwerg namens Agnar Tiermeister. Er ist ein Tierführermeister und kann Euch bestimmt bei den letzten Stadien der Brutzeit behilflich sein.]], [[Aber eilt Euch, <name>, das Ei ist empfindlich und wird sich ohne die richtige Pflege nicht lange halten.]], },
    { [[Bringt Euer empfindliches Grimmlingflitzerei ins Hinterland und sprecht mit Agnar Tiermeister, aber beeilt Euch. Ihr habt nur eine Stunde Zeit, ihn zu finden. Dann stirbt das Jungtier in dem Ei.]], },
  },
  [3844] = {
    [[Es ist für jeden ein Geheimnis]],
    { [[In Ufernähe liegt ein zerstörtes Floß. Das hier ist nur ein kleiner Tümpel, deswegen ist Euch nicht so ganz klar, wie das Floß hier landen konnte. Aber ein Blick auf die Bäume verrät Euch, dass der Wasserstand im Teich nicht unbedingt immer derselbe sein muss.]], [[Vielleicht findet Ihr ja mehr heraus, wenn Ihr Euch ein bisschen in der Gegend umschaut...]], },
    { [[Sucht in dem Tümpel nach einem Hinweis darauf, wem dieses havarierte Floß gehören könnte.]], },
  },
  [3845] = {
    [[Es ist für jeden ein Geheimnis]],
    { [[Ihr greift nach dem Bündel und zieht es an Land. Vielleicht findet Ihr heraus, wem es gehört, wenn Ihr die Gegenstände darin untersucht.]], [[In Marschalls Zuflucht halten sich ein paar vereinzelte Reisende auf. Vielleicht lässt sich dort ja mehr herausfinden.]], },
    { [[Untersucht die Gegenstände in dem Bündel und bringt sie zu ihrem Besitzer.]], },
  },
  [3861] = {
    [[GLUCK!]],
    { [[Gack... gack... gack... GAGGACK!]], [[Gack... gack... gack.]], [[Das Huhn sieht zu Euch hinauf und beginnt dann, mit seinen Krallen am Boden zu scharren. Ihr glaubt, dass es ein Wort schreibt, aber Ihr seid Euch nicht sicher, was es ist. Könnte es das sein... S-A-L-D-E-A-N? Bevor Ihr fragen könnt, dreht sich das Huhn weg und kümmert sich um seine Angelegenheiten.]], },
    { [[Beschafft Euch etwas Spezialhühnerfutter und kehrt zu dem Huhn zurück, das sich mit Euch angefreundet hat. Wenn Ihr es gefunden habt, dann benutzt erst /cheer, ehe Ihr dem Huhn das Spezialhühnerfutter gebt.]], },
  },
  [3881] = {
    [[Rettungsexpedition]],
    { [[Ich weiß zwar nicht, wie Ihr es bis hierher geschafft habt, aber Ihr seid Balsam für meine Augen! Ich heiße Williden Marschall und bin der Leiter eines neutralen Expeditionstrupps. Unsere jüngste und größte Unternehmung wurde von zahllosen Katastrophen begleitet. Die schlimmste davon war, dass wir die Wildheit des Kraters von Un'Goro unterschätzt hatten.]], [[Als wir aus unseren Stützpunkten im südwestlichen und nordöstlichen Teil des Kraters flohen, ließen wir lebenswichtige Vorräte zurück. Ihr seht aus, als wärt Ihr recht stark und geschickt... Könnt Ihr uns vielleicht dabei helfen, sie zurückzubekommen?]], },
    { [[Bringt eine Kiste mit Nahrungsmitteln und Forscherausrüstung zu Williden Marschall im Krater von Un'Goro.]], },
  },
  [3882] = {
    [[Knochenklappern]],
    { [[Man sollte meinen, dass ich gern grabe, nur weil ich der Oberbuddler von Marschalls Expeditionen bin. Es könnte schlimmere Dinge in der Welt geben... auch wenn mir gerade keines einfällt.]], [[Jedenfalls sollten wir eigentlich in der Terrorflucht - PHANTASTISCHER Name übrigens - nach toten Stegodonten und Dimetrodonten graben. Na ja, es treiben sich von der Sorte auch noch eine Menge lebende Viecher dort herum! Wil und Hol beschäftigen sich immer mit dieser Todesfalle, daher benötigen wir diese Dinosaurierknochen. Obwohl, unter uns gesagt, wenn Ihr mir welche von einem lebenden Exemplar bringt, dann ist das genauso gut.]], },
    { [[Bringt 8 Dinosaurierknochen zu Funks Nilminer im Krater von Un'Goro.]], },
  },
  [3883] = {
    [[Eine fremdartige Ökologie]],
    { [[Nur weil wir einen Rückschlag erlitten haben, heißt das noch lange nicht, dass unsere Forschungen ein Ende finden müssen! Bevor wir hier gelandet sind, untersuchten wir die Schwarmbauten der Gorishiinsekten im südlichen Teil des Kraters. Sie sind fremdartig und passen nicht zur ursprünglichen Ökologie des Un'Goro und wir wollen herausfinden, warum das so ist.]], [[In ihrem Schwarmbauverbund muss es Larvenbrutstätten geben. Ihr könnt diesen Ort an der hängenden Raupenbrut erkennen. Hier, nehmt mit dieser Phiole eine Probe von der Schwarmbauwand in der Nähe der Brutstätten. Bringt sie dann zu mir zurück, damit ich sie untersuchen kann, aber seid vorsichtig!]], },
    { [[Verwendet die Kratzphiole, um damit eine Schwarmbauwandprobe von einer der Schwarmbrutstätten der Gorishi im Krater von Un'Goro zu holen. Sucht nach den Kammern mit der hängenden Larvenbrut.]], [[Bringt die Schwarmbauwandprobe zu Hol'anyee Marschall im Krater von Un'Goro.]], },
  },
  [3884] = {
    [[Willidens Tagebuch]],
    { [[Ihr habt ein zerfleddertes Tagebuch zu Tage gefördert. Ein Teil davon ist noch lesbar; es scheint von einem gewissen Williden Marschall verfasst worden zu sein. Wenn das Tagebuch stimmt, dann muss Williden irgendwo im nördlichen Teil des Kraters zu finden sein. Dem Tagebuch zufolge haben er und seine Gefährten in den höheren Lagen des Kraters Schutz gesucht, nachdem ihre tiefer gelegenen Stützpunkte von den wilden Bestien überrannt wurden.]], },
    { [[Bringt das Tagebuch zu Williden Marschall zurück. Er befindet sich irgendwo im Krater von Un'Goro.]], },
  },
  [3901] = {
    [[Klappernde Skelette]],
    { [[Ihr habt den Verlassenen Euer Potenzial unter normalen Umständen gezeigt, <name>. Jetzt wollen wir mal sehen, wie es damit bestellt ist, wenn Ihr unter Druck steht.]], [[Die klappernden Skelette, weitere hirnlose Landplagen, sind zähere Feinde als die Zombies, mit denen Ihr es bisher zu tun hattet. Auch hier heißt es wieder: Dünnt ihre Reihen aus und beweist den Verlassenen, dass Ihr der Aufgabe gewachsen seid. Frisch ans Werk...und sprecht wieder mit mir, wenn Ihr fertig seid.]], },
    { [[Tötet 8 klappernde Skelette.]], },
  },
  [3902] = {
    [[Beutesuche in Todesend]],
    { [[Ihr da! Wenn Ihr Euch irgendwie nützlich machen wollt, dann hört zu!]], [[Wir benötigen frisch erweckte Rekruten, die sich nach Todesend begeben und dort nach allem suchen, was wir irgendwie gebrauchen können. Die Dinge werden sich höchstwahrscheinlich in gestapelten Kästen befinden. Wir rechnen damit, dass sich in Kürze weitere Rekruten erheben werden, und wenn wir nicht wollen, dass die nackt herumtappen, dann müssen wir wohl oder übel was für sie suchen.]], [[Also, macht Euch an die Arbeit, elendes Knochengestell! Und ohne ein bisschen Eile gibt es auch keine Belohnung.]], },
    { [[Durchsucht Todesend und Umgebung nach 6 Stücken aufgestöberter Ware und bringt sie zu Todeswache Saltain zurück.]], },
  },
  [3903] = {
    [[Milly Geltmark]],
    { [[Ihr habt Euch als verlässlich erwiesen, <name>. Verlässlich und ohne Angst, Euch auch mal die Hände schmutzig zu machen, wie?]], [[Tja, eine Freundin von mir, Milly Geltmark, hat ein paar Probleme. Sie ist mit ihrem Wagen auf der anderen Seite der Abtei, in der Nähe des Stalls. Ich bin mir sicher, sie könnte Eure Hilfe gut gebrauchen.]], },
    { [[Sprecht mit Milly Geltmark.]], },
  },
  [3904] = {
    [[Millys Ernte]],
    { [[Eine Bande von Briganten, die Defias, sind in das Weingut von Nordhain eingedrungen, als ich gerade bei der Weinlese war! Ich habe es den Wachen von Nordhain gemeldet und sie haben mir auch versprochen, dass sie sich darum kümmern würden, aber... Ich habe Angst um meine Traubenernte! Selbst wenn die Defias sie nicht stehlen, befürchte ich, dass unsere Wachen beim Vertreiben der Rohlinge alles zertrampeln.]], [[Bitte, Ihr müsst mir helfen! Die meisten Trauben sind schon in Eimern gesammelt, aber ich habe sie bei den Weinstöcken im Südosten zurückgelassen.]], [[Bringt mir diese Kübel! Rettet meine Ernte!]], },
    { [[Bringt 8 Kisten mit Millys Ernte zu Milly Geltmark in der Nähe der Abtei von Nordhain.]], },
  },
  [3905] = {
    [[Traubenliste]],
    { [[Jetzt, wo meine Ernte in Sicherheit ist, bringt diese Traubenliste zu Bruder Neals. Er ist verantwortlich für die Essens- und Getränkevorräte von Nordhain und ich bin mir sicher, er wird hoch erfreut sein zu hören, dass er frische Trauben bekommt.]], [[Ihr findet Bruder Neals im Glockenturm der Abtei... wo er gern seinen Wein probiert.]], },
    { [[Bringt die Traubenliste zu Bruder Neals in der Abtei von Nordhain.]], },
  },
  [3906] = {
    [[Disharmonie der Flamme]],
    { [[Die Ältesten haben eine Störung der Elemente gespürt. Die Dunkeleisenzwerge haben einen üblen Geist in unsere Welt beschworen, durch den unser Land von einer ewigen Dürre heimgesucht werden könnte. Die Disharmonie der Flamme muss untersucht werden.]], [[Begebt Euch zum Schwarzfels und dann durch die Flammenschlucht in den Steinbruch. Dort werdet Ihr diesen Herrn des Feuers finden: Übermeister Pyron.]], [[Macht der Störung ein Ende und kehrt dann zu mir zurück.]], },
    { [[Reist zum Steinbruch im Schwarzfels und tötet Übermeister Pyron. Kehrt zu Donnerherz zurück, sobald Ihr den Auftrag erledigt habt.]], },
  },
  [3907] = {
    [[Disharmonie des Feuers]],
    { [[Betretet die Schwarzfelstiefen und spürt Lord Incendius auf. Tötet ihn und bringt jegliche Informationsquelle, die Ihr finden könnt, zu Donnerherz.]], },
    { [[Betretet die Schwarzfelstiefen und spürt Lord Incendius auf. Tötet ihn und bringt jegliche Informationsquelle, die Ihr finden könnt, zu Donnerherz.]], },
  },
  [3908] = {
    [[Es ist für jeden ein Geheimnis]],
    { [[Seht, ich habe keine Ahnung, wie ich hierher kam. Niemand weiß das - niemand versteht es! Ich kann mich einfach an nichts erinnern...]], [[Aber diese Gegenstände, die Ihr mir gebracht habt... Ich erinnere mich an etwas.]], [[Mein Schwert. Ich soll es irgendwie stärken, aber ich weiß beim besten Willen nicht, warum. Es tut mir leid, dass ich Euch nicht mehr sagen kann, aber ich kann mich einfach nicht erinnern!]], [[Na, egal, ich weiß aber, wo Ihr es hinbringen könnt: zu Donova Schneeweh in Winterquell. Ihr findet sie bei den Thermalquellen. Sie kann das Schwert an den Teichen dort aushärten.]], },
    { [[Bringt Linkens Schwert zu Donova Schneeweh in Winterquell.]], },
  },
  [3909] = {
    [[Das Videre-Elixier]],
    { [[Was sagt Ihr da? Linken... HAT ES VERGESSEN?]], [[Ah, verstehe... Hat sein Gedächtnis verloren...]], [[Na gut, hier ist sein Schwert. Es ist aber noch nicht fertig. Ich habe von jemandem gehört, der es fertig stellen kann, aber... tja, es könnte etwas schwierig werden, mit ihm zu reden.]], [[Hört mal, ich weiß jemanden, der Euch bestimmt helfen kann. Ich kann Euch jetzt nicht alles erklären, das würde zu lange dauern, aber Ihr müsst Euch irgendwie ein Videre-Elixier beschaffen. Der Einzige, von dem ich weiß, dass er Euch dabei helfen kann, ist Gregan Hopfenspei. Er ist ziemlich schwer aufzuspüren, aber zuletzt hatte er ein Lager in der Wildnis von Feralas.]], },
    { [[Sucht nach Gregan Hopfenspei im nördlichen Feralas. Lasst Euch von ihm erklären, wie Ihr Euch das Videre-Elixier beschaffen könnt, und kehrt dann zu Donova Schneeweh nach Winterquell zurück.]], },
  },
  [3911] = {
    [[Das letzte Element]],
    { [[Begebt Euch in die Schwarzfelstiefen und beschafft 10 Essenzen der Elemente. Euer erster Gedanke ist, die Golems und die Schöpfer der Golems zu suchen. Doch Ihr erinnert Euch, dass Vivian Lagrave auch etwas von Elementaren vor sich hingemurmelt hat.]], },
    { [[Begebt Euch in die Schwarzfelstiefen und beschafft 10 Essenzen der Elemente. Euer erster Gedanke ist, die Golems und die Schöpfer der Golems zu suchen. Doch Ihr erinnert Euch, dass Vivian Lagrave auch etwas von Elementaren vor sich hingemurmelt hat.]], },
  },
  [3912] = {
    [[Treffen am Grab]],
    { [[Durch dieses Elixier könnt Ihr mit jemandem sprechen, den Ihr normalerweise nicht einmal sehen würdet...]], [[Bringt das Videre-Elixier und Linkens Schwert mit zum Friedhof kurz vor Gadgetzan in Tanaris. Wenn Ihr dort seid, nehmt den Trank zu Euch. Ihr werdet möglicherweise von der Wirkung sehr überrascht sein, aber erschreckt nicht.]], [[Geht in Eurer 'anderen Form' nach Norden, bis Ihr die Berge erreicht. Dort findet Ihr den, mit dem Ihr sprechen müsst, in einer der Felsspalten. Sein Name ist Gaeriyan.]], [[Aber vergesst nicht: Nur in dieser Form wird er mit Euch sprechen...]], },
    { [[Geht zum Friedhof außerhalb von Gadgetzan und benutzt dort das Videre-Elixier. Befolgt Donovas übrige Anweisungen, um Gaeriyan zu finden.]], },
  },
  [3913] = {
    [[Grabesstille]],
    { [[Wenn Ihr zum Friedhof zurückkehrt, <name>, findet Ihr dort einen Grabstein... Nicht einfach nur irgendeinen Grabstein... Dieser hebt sich von all den anderen ab. Geheimnisse liegen darin verborgen.]], [[Drückt gegen den Stein, dann erfahrt Ihr von diesen Geheimnissen...]], },
    { [[Wenn Ihr auferstanden seid, sucht nach dem Grabstein, von dem Gaeriyan Euch erzählt hat.]], },
  },
  [3914] = {
    [[Linkens Schwert]],
    { [[Ein greller Lichtblitz blendet Euch, dann könnt Ihr das Schwert wieder aufheben. Es hat sich verwandelt!]], },
    { [[Liefert Linkens überragendes Schwert bei Linken im Krater von Un'Goro ab.]], },
  },
  [3921] = {
    [[Wenikee Kübelbolz]],
    { [[Ich glaube, ich habe das Ding wirklich kaputt gemacht: sieht so aus, als hätte ich einen der tragenden Fluxoren in die Speichen des Deregulatorgetriebes gehämmert. Aber das können wir ganz einfach beheben. Ich kenne eine Expertin für 3-fach Expansionskolbenantriebe, die liebend gern ein Samophlang in die Finger bekommen würde. Bringt das zerbrochene Samophlang zu Wenikee Kübelbolz südlich des Schutzwalls von Mor'shan und sie wird es schon zusammenflicken.]], [[He, Ihr müsst noch nicht einmal zu Fuß gehen. Das ist die perfekte Gelegenheit, meinen persönlichen Personenporter zu testen!]], },
    { [[Bringt das zerbrochene und ramponierte Samophlang zu Wenikee Kübelbolz. Sprecht mit Brak, um direkt zu ihr teleportiert zu werden.]], },
  },
  [3922] = {
    [[Schraubnieten]],
    { [[Ich möchte das Samophlang reparieren, aber dazu brauche ich ein paar Dinge. Ich habe keine 17,5er Schraubnieten mehr, und davon werde ich für meine Arbeit hier eine ganze Menge brauchen.]], [[Der nächstgelegene Ort, an dem man sie bekommen kann, ist die in der Nähe befindliche Forschungsstation und das Schlickmoor im Osten. Die Leute von der Venture Company bewahren sie normalerweise in ihren Werkzeugkisten auf. Schaut in den Werkzeugkisten an beiden Orten nach und kommt zu mir zurück, sobald Ihr genug Schraubnieten gesammelt habt.]], },
    { [[Sammelt 10 Schraubnieten aus den Werkzeugkisten rund ums Schlickmoor.]], },
  },
  [3923] = {
    [[Rilli Schmierklump]],
    { [[Tja, das hat schon mal nicht funktioniert. Ich habe an dem Samophlang ein paar zusätzliche Messgeräte angebracht, um seine strukturelle Integrität zu messen, und laut der Anzeige war die Integrität am absoluten Tiefpunkt. Ich könnte natürlich noch ein paar Skalen und vielleicht auch den einen oder anderen Hebel anbringen, aber...ich glaube, das würde alles nur noch schlimmer machen.]], [[Ihr solltet das Samophlang zu Rilli Schmierklump bringen. Er hat mal für die Venture Company gearbeitet. Wenn irgendjemand weiß, wie man das Ding reparieren kann, dann Rilli.]], [[Ihr findet ihn bei 'Noggs Meistermaschinen' in Orgrimmar, im Tal der Ehre.]], },
    { [[Bringt das zerbrochene Samophlang nach Orgrimmar zu Rilli Schmierklump.]], },
  },
  [3924] = {
    [[Samophlanghandbuch]],
    { [[Ich glaube, ich kann das Samophlang reparieren, aber dazu brauche ich erst einmal das Handbuch. Als ich noch für die Venture Company arbeitete, hatte ich einen Konkurrenten, Boss Kupferdraht. Der hat das Geheimnis des Samophlangs gehütet wie seinen Augapfel und immer nur kleine Fitzelchen an Informationen an diejenigen verteilt, denen er traute. Zu dieser kleinen, ausgewählten Gruppe habe ich leider nicht gehört...]], [[Boss Kupferdraht war früher immer in der Felsadermine im Nordosten des Brachlandes. Beschafft Euch das Handbuch von ihm, und falls er irgendjemandem einzelne Seiten davon gegeben hat, dann müsst Ihr diese ebenfalls einsammeln.]], },
    { [[Bringt das Samophlanghandbuch nach Orgrimmar zu Rilli Schmierklump.]], },
  },
  [3941] = {
    [[Die Hilfe des Gnoms]],
    { [[Ich denke... Ich denke... Ich weiß, dass ich aus irgendeinem wichtigen Grund hergekommen bin...]], [[Ich hab da eine Idee, <name>. In der Höhle dort drüben wohnt eine Gnomin namens J.D. Sie war sehr freundlich zu mir, als ich hier ankam. Vielleicht habe ich in meiner Verwirrung ihr gegenüber irgendwas erwähnt.]], [[Warum fragt Ihr sie nicht, ob sie etwas weiß? Oh, aber seid vorsichtig: Sie spielt immer mit diesen verrückten, leuchtenden Kristallen herum, die ich im Krater gesehen habe. Ich habe keine Ahnung, was sie da drinnen treibt.]], },
    { [[Sucht J.D. Collie in der Höhle in Marschalls Zuflucht auf.]], },
  },
  [3942] = {
    [[Linkens Andenken]],
    { [[Oh... Linken? Jaaa, der ist echt witzig. Als er hier ankam, murmelte er ständig irgendwelche seltsamen Dinge. Hat wahrscheinlich einen ziemlich heftigen Schlag auf den Kopf bekommen, würde ich mal sagen.]], [[Aber etwas, das er gesagt hat, ist bei mir irgendwie hängen geblieben - dass er dabei war, eine Art Totem herzustellen; er sagte, er müsse mit jemandem namens Eridan Blauwind sprechen... an einem Ort namens Teufelswald, glaube ich.]], [[Aber ich bezweifle, dass Linken Euch jetzt noch viel darüber sagen kann. Wenn Ihr mehr wissen wollt, solltet Ihr diesen Eridan vielleicht selbst suchen gehen...]], },
    { [[Sucht Eridan Blauwind im südlichen Teil des Teufelswalds.]], },
  },
  [3961] = {
    [[Linkens Abenteuer]],
    { [[Wenn man sich überlegt, dass Linken nur hergekommen ist, um uns von Sturmflamme zu befreien... Er scheint so gar nichts Außergewöhnliches an sich zu haben, aber mir ist aufgefallen, dass er sich überhaupt nicht für meine Experimente oder Geräte interessiert hat, wie das bei den meisten Gnomen sonst der Fall ist. Er muss im Herzen ein echter Krieger sein.]], [[Bringt ihm das Totem. Vielleicht hilft ihm das, sich zu erinnern, warum er hergekommen ist.]], },
    { [[Bringt das Silbertotem von Aquementas zu Linken in Marschalls Zuflucht.]], },
  },
  [3962] = {
    [[Allein ist es gefährlich]],
    { [[<name>, ich fange endlich an, mich zu erinnern! Ich bin hergekommen, um gegen Sturmflamme zu kämpfen. Ich war auf einer Reise und das hätte der letzte Schritt sein sollen.]], [[Aber ich bin kein Held mehr... sondern Ihr. Alles, was Ihr getan habt... Ihr müsst dies jetzt zu Ende bringen, nicht ich.]], [[Findet Sturmflamme und besiegt ihn. Er bewacht ein Artefakt, das die Goldene Flamme genannt wird. Es verleiht mehr Macht als alles, was ich je zuvor gesehen habe, und in den Händen von jemandem, dessen Herz böse ist, ist es äußerst gefährlich.]], [[Ihr solltet aber unbedingt Verstärkung mitnehmen - es ist gefährlich, allein zu gehen.]], },
    { [[Begebt Euch zum Feuersäulengrat südlich von Marschalls Zuflucht.]], [[Sucht Sturmflamme und verwendet das Totem, um die schützende Aura, die ihn umgibt, zu entfernen. Sobald er besiegt ist, holt die Goldene Flamme aus der Höhle.]], },
  },
  [3981] = {
    [[Kommandant Gor'shak]],
    { [[Sucht Kommandant Gor'shak in den Schwarzfelstiefen.]], [[Ihr erinnert Euch, dass auf dem primitiv gezeichneten Bild des Orcs auch Gitter vor dem Gesicht zu sehen waren. Vielleicht solltet Ihr nach einer Art Gefängnis suchen.]], },
    { [[Sucht Kommandant Gor'shak in den Schwarzfelstiefen.]], [[Ihr erinnert Euch, dass auf dem primitiv gezeichneten Bild des Orcs auch Gitter vor dem Gesicht zu sehen waren. Vielleicht solltet Ihr nach einer Art Gefängnis suchen.]], },
  },
  [3982] = {
    [[Was ist los?]],
    { [[Ich wurde unter der Schirmherrschaft von Thrall hierher geschickt. Glaubt Ihr wirklich, diese Clowns könnten mich fangen? Ich habe mich fangen lassen, um so Informationen sammeln zu können.]], [[<Gor'shak flüstert.>]], [[Die Prinzessin von Eisenschmiede wurde von Imperator Thaurissan entführt. Prinzessin Bronzebart befindet sich in dieser Stadt! Ich wette, dem alten Magni ist fast das Herz stehen geblieben, als er diese Neuigkeiten gehört hat.]], [[<Gor'shak lacht.>]], [[Pssst! Hört Ihr das? Es kommen Wachen! VERTEIDIGT EUCH!]], },
    { [[Verteidigt Gor'shak.]], },
  },
  [4001] = {
    [[Was ist los?]],
    { [[Sprecht mit Kharan Hammermacht und sammelt Informationen über die Entführung von Prinzessin Moira Bronzebart. Bringt diese Informationen dann zu Thrall nach Orgrimmar.]], [[Ihr erinnert Euch, dass Gor'shak gesagt hat, Kharan werde in einer Zelle in seiner Nähe festgehalten.]], },
    { [[Sprecht mit Kharan Hammermacht und sammelt Informationen über die Entführung von Prinzessin Moira Bronzebart. Bringt diese Informationen dann zu Thrall nach Orgrimmar.]], [[Ihr erinnert Euch, dass Gor'shak gesagt hat, Kharan werde in einer Zelle in seiner Nähe festgehalten.]], },
  },
  [4002] = {
    [[Die Östlichen Königreiche]],
    { [[Ihr bringt ganz ausgezeichnete Nachrichten, <name>.]], [[Als Kriegshäuptling ist es meine Aufgabe, das zu tun, was das Beste für die Horde ist. Zu garantieren, dass die nachfolgenden Generationen nicht in einer hoffnungslosen Existenz gefangen sein werden.]], [[Jetzt ist uns die einzigartige Chance, die Allianzen der Zukunft zu formen, in den Schoß gefallen. Wenn wir jetzt den Zwergen helfen, könnte dies unsere Beziehungen zu den Östlichen Königreichen enorm verbessern. Und aus genau diesem Grund dürft Ihr nicht versagen!]], [[Seid Ihr bereit?]], },
    { [[Sprecht mit Thrall, wenn Ihr bereit seid, die von ihm geplante Mission zu übernehmen.]], },
  },
  [4003] = {
    [[Die königliche Rettung]],
    { [[Der Schlag gegen die Dunkeleisenzwerge wird, wenn er denn erfolgreich verläuft, eine Schockwelle durch ihr ganzes Königreich jagen.]], [[Prinzessin Bronzebart ist unter der Kontrolle von Imperator Thaurissan. Um sie zu befreien, müsst Ihr den Imperator töten. Doch seid gewarnt, denn Eure Aufgabe ist doppelt gefährlich. Zweifellos wird Euch Prinzessin Bronzebart angreifen, solange sie noch der Kontrolle des Imperators unterliegt.]], [[IHR DÜRFT IHR KEIN LEID ZUFÜGEN!]], },
    { [[Tötet Imperator Dagran Thaurissan und befreit Prinzessin Moira Bronzebart von seinem bösen Zauber.]], },
  },
  [4004] = {
    [[Ist die Prinzessin gerettet?]],
    { [[Kehrt zu Thrall in der Feste Grommash in Orgrimmar zurück.]], },
    { [[Kehrt zu Thrall in der Feste Grommash in Orgrimmar zurück.]], },
  },
  [4005] = {
    [[Aquementas]],
    { [[Nehmt diese Tasche mit Vorräten mit Euch nach Tanaris. Begebt Euch zur Mast- und Schotbucht und sucht dort einen Kreis aus Steinen.]], [[In der östlichsten Halbinsel liegt das Geheimnis...]], [[Lest aus dem Buch von Aquor vor - aber nur innerhalb dieses Kreises - und beschwört das einzige Wesen, das die Gegenstände, die Ihr gesammelt habt, in ein Totem verwandeln kann, mit dem Ihr Sturmflamme bekämpfen könnt.]], [[Es wird Aquementas nicht gefallen, dass Ihr ihn aufweckt, doch vergesst nicht: Ihr müsst unbedingt in dem Kreis bleiben, damit er die Gegenstände transformieren kann.]], },
    { [[Bringt das Buch von Aquor, die silbrigen Klauen und das Eisenstammherz nach Tanaris.]], [[Benutzt das Buch von Aquor, um Aquementas zu beschwören.]], [[Wenn Ihr das vollständige Totem habt, bringt es zu J.D. Collie in Marschalls Zuflucht zurück.]], },
  },
  [4021] = {
    [[Gegenangriff!]],
    { [[Die Horde lobt Euch für Eure Erfolge gegen die Kolkar, doch die Kolkar kochen vor Wut. Ich habe Berichte erhalten, denen zufolge ein Kontingent Kolkar ihr Zuhause in Desolace verlassen hat und ins Brachland gezogen ist, um dort Rache zu üben. Den letzten Berichten nach, rücken Sie in diesem Moment von Westen her gegen diese Bunker vor!]], [[Stellt Euch den Eindringlingen der Kolkar und bereitet der Bedrohung, die sie für das Brachland darstellen, ein Ende. Tötet sie, bis ihr Anführer, Kriegsherr Krom'zar, erscheint. Besiegt ihn und bringt mir ein Teil seiner Flagge als Beweis.]], [[Wir zählen auf Euch, <name>.]], },
    { [[Tötet Kolkarzentauren bis Krom'zar auftaucht, dann tötet ihn und nehmt einen Teil von Krom'zars Flagge an Euch.]], },
  },
  [4022] = {
    [[Eine Kostprobe der Flamme]],
    { [[Der Klan ist sich Eurer Arbeit in der Sengenden Schlucht bewusst.]], [[Zeigt sie mir, <race>. Die abgestreifte Drachenhaut, Dummkopf!]], },
    { [[Zeigt Cyrus Therepentous die abgestreifte Haut des schwarzen Drachenschwarms, die Ihr von Kalaran Windklinge erhalten habt.]], },
  },
  [4023] = {
    [[Eine Kostprobe der Flamme]],
    { [[Ihr sucht nach dem Klan? Zeigt mir einen Beweis für Eure Nützlichkeit, sterbliches Wesen.]], },
    { [[Zeigt Cyrus Therepentous einen Beweis für Eure Nützlichkeit.]], [[Ihr habt so ein Gefühl, dass Cyrus bereits weiß, dass Ihr unwürdig seid.]], },
  },
  [4024] = {
    [[Eine Kostprobe der Flamme]],
    { [[Ich möchte die sengenden Flammen des Feuerberges schmecken. Die flüssigen Feuer des Schwarzfels über mich strömen lassen. Diese Essenz, diese sengende Hitze ist es, nach der ich mich sehne.]], [[In den Tiefen des Schwarzfels haust Bael'Gar: ein Wesen aus reinstem Feuer, geboren aus den Flammen des Ragnaros. Tötet Bael'Gar und bringt mir aus seinen glimmenden Überresten einen Teil seines inneren Feuers.]], [[Verwendet die Drachenhaut für seine Überreste, um die innere Flamme sicher entnehmen zu können.]], [[Seid erfolgreich und Ihr werdet belohnt.]], },
    { [[Begebt Euch in die Schwarzfelstiefen und tötet Bael'Gar.]], [[Ihr wisst lediglich, dass der Riese irgendwo in den Schwarzfelstiefen haust. Vergesst nicht, die veränderte Haut des schwarzen Drachenschwarms mit Bael'Gars Überresten zu benutzen und damit die feurige Essenz einzufangen.]], [[Bringt die eingeschlossene feurige Essenz zu Cyrus Therepentous zurück.]], },
  },
  [4041] = {
    [[Das Videre-Elixier]],
  },
  [4061] = {
    [[Aufstieg der Maschinen]],
    { [[Begebt Euch in die Brennende Steppe und beschafft für Theodora Mulvadania 10 gerissene Elementarsplitter.]], [[Euch fällt ein, dass Theodora gesagt hat, die Golems und Elementare in der Gegend seien eine gute Quelle für solche Splitter.]], },
    { [[Begebt Euch in die Brennende Steppe und beschafft für Theodora Mulvadania 10 gerissene Elementarsplitter.]], [[Euch fällt ein, dass Theodora gesagt hat, die Golems und Elementare in der Gegend seien eine gute Quelle für solche Splitter.]], },
  },
  [4062] = {
    [[Aufstieg der Maschinen]],
    { [[G.L.A.]], [[Das sind die Buchstaben, die in die Elementarsplitter eingeprägt sind. Unter den Initialen findet sich eine Markierung für einen Rang oder etwas in der Art. Die Maschinen, die Ihr in der Brennenden Steppe zerstört habt, hatten den 'Rang IV'. Ich nehme an, das ist ein eher niedriger Rang.]], [[Bringt diese Splitterprobe in das Lager im Osten zu Lotwil Veriatus. Er ist ein Gelehrter und vor allem für seine Arbeit auf dem Gebiet der Elementarforschung bekannt. Vielleicht kann er uns bei unserer Suche helfen.]], },
    { [[Bringt die Elementarsplitterprobe zu Lotwil Veriatus.]], [[Ihr erinnert Euch, dass Theodora sagte, Lotwil sei in einem Lager im Osten stationiert.]], },
  },
  [4063] = {
    [[Aufstieg der Maschinen]],
    { [[Das kann doch nicht Argelmach sein... Argelmach wurde vor zehn Jahren getötet. Warum ich das so genau weiß? Weil ich derjenige war, der ihn getötet hat.]], [[Er war ein abscheulicher Zwerg und wild entschlossen, mein Lebenswerk zu verderben und es für seine teuflischen Zwecke zu benutzen. Wie es scheint, finden die Tückischen immer einen Weg, ihrer gerechten Strafe zu entgehen.]], [[Wenn dies Argelmachs Werk ist, dann muss er zerstört werden. Ich brauche Proben seiner jüngsten Schöpfungen. Mit den richtigen Proben kann ich vielleicht diesem Aufstand ein Ende machen. Die Schöpfungen bewachen Argelmach in den Schwarzfelstiefen.]], },
    { [[Sucht und tötet Golemlord Argelmach. Bringt Lotwil seinen Kopf. Außerdem müsst Ihr 10 intakte Elementarkerne von den Wuthäschergolems und Kriegshetzerkonstrukten, die Argelmach beschützen, beschaffen. Das wisst Ihr, weil Ihr übernatürliche Fähigkeiten habt.]], },
  },
  [4081] = {
    [[SOFORT TÖTEN: Dunkeleisenzwerge]],
    { [[Auf Befehl von Kriegsherr Bluthauer, Kommandant des Expeditionskorps von Kargath:]], [[Alle Streitkräfte, über die General Zornesschmied in den Schwarzfelstiefen verfügt, müssen ausgelöscht werden.]], [[Zornesschmieds Männer sind rücksichtslose Killer und verantwortlich für den Massenmord an der 109. Division des Expeditionskorps von Kargath. Alle Helden, die diesen gefühllosen Bestien gegenübertreten wollen, werden aufgefordert, extreme Vorsicht walten zu lassen.]], [[Jede Person, die die erste Kampfreihe von General Zornesschmieds Streitkräften in den Schwarzfelstiefen vernichtet, wird einen Tribut erhalten.]], },
    { [[Begebt Euch in die Schwarzfelstiefen und vernichtet die üblen Aggressoren!]], [[Kriegsherr Bluthauer möchte, dass Ihr 15 Gardisten der Zorneshämmer, 10 Aufseher der Zorneshämmer und 5 Fußsoldaten der Zorneshämmer tötet. Kehrt zu ihm zurück, sobald Ihr die Aufgabe erfüllt habt.]], },
  },
  [4082] = {
    [[SOFORT TÖTEN: Hochrangige Führungskräfte der Dunkeleisenzwerge]],
    { [[Begebt Euch in die Schwarzfelstiefen und vernichtet die üblen Aggressoren!]], [[Kriegsherr Bluthauer möchte, dass Ihr 10 Sanitäter der Zorneshämmer, 10 Soldaten der Zorneshämmer und 10 Offiziere der Zorneshämmer tötet. Kehrt zu ihm zurück, sobald Ihr die Aufgabe erfüllt habt.]], },
    { [[Begebt Euch in die Schwarzfelstiefen und vernichtet die üblen Aggressoren!]], [[Kriegsherr Bluthauer möchte, dass Ihr 10 Sanitäter der Zorneshämmer, 10 Soldaten der Zorneshämmer und 10 Offiziere der Zorneshämmer tötet. Kehrt zu ihm zurück, sobald Ihr die Aufgabe erfüllt habt.]], },
  },
  [4083] = {
    [[Der spektrale Kelch]],
  },
  [4084] = {
    [[Silberherz]],
    { [[Ich kenne denjenigen nicht, von dem Ihr sprecht, <name>.]], [[Doch keine Sorge, wenn Euer Freund auf der Suche nach mir war, dann weiß ich, was er von mir wollte.]], [[Im Herzen des Kraters von Un'Goro lebt das Böse in seiner reinsten Form. Dort manifestiert es sich in der Gestalt eines Elementars namens Sturmflamme. Er wird von einer Aura beschützt, die niemand durchdringen kann.]], [[Niemand... außer denen, die über genügend Macht verfügen. Diese Macht kann ich Euch lehren, <name>.]], [[Doch zunächst müsst Ihr mir eine Bitte erfüllen. Beschafft 11 silbrige Klauen und 1 Eisenstammherz von den Kreaturen im Teufelswald und kommt dann zu mir zurück.]], },
    { [[Beschafft 11 silbrige Klauen und 1 Eisenstammherz für Eridan Blauwind im Teufelswald.]], },
  },
  [4101] = {
    [[Den Teufelswald säubern]],
    { [[Die Pflanzen schenken dem Teufelswald Leben, doch sogar sie leiden unter der Besudelung der Verderbnis. Ich kenne Wege, um sie zu säubern, doch dieses Wissen kann ich an niemanden weitergeben, den ich nicht kenne oder dem ich nicht vertraue. Beweist mir, dass Ihr der Sache des Zirkels des Cenarius wirklich helfen wollt!]], [[Nördlich von hier liegen die Eisenstammhöhlen. Für die Wucherborkenelementare, die sich dort aufhalten, gibt es keine Hoffnung mehr auf Säuberung. Ich bitte Euch, bringt mir ihren Saft, er wird Blutharz genannt, damit ich ihn studieren kann. Vielleicht gelingt es mir so, die anderen Lebensformen hier zu retten.]], },
    { [[Bringt 15 Einheiten Blutharz zu Arathandris Silberwolke im Teufelswald.]], },
  },
  [4102] = {
    [[Den Teufelswald säubern]],
    { [[Die Pflanzen schenken dem Teufelswald Leben, doch sogar sie leiden unter der Besudelung der Verderbnis. Ich kenne Wege, um sie zu säubern, doch dieses Wissen kann ich an niemanden weitergeben, den ich nicht kenne oder dem ich nicht vertraue. Beweist mir, dass Ihr der Sache des Zirkels des Cenarius wirklich helfen wollt!]], [[Nördlich von hier liegen die Eisenstammhöhlen. Für die Wucherborkenelementare, die sich dort aufhalten, gibt es keine Hoffnung mehr auf Säuberung. Ich bitte Euch, bringt mir ihren Saft, er wird Blutharz genannt, damit ich ihn studieren kann. Vielleicht gelingt es mir so, die anderen Lebensformen hier zu retten.]], },
    { [[Bringt 15 Einheiten Blutharz zu Maybess Flussbrise im Teufelswald.]], },
  },
