  [4120] = {
    [[Die Kraft der Verderbnis]],
    { [[Als Jäger strebe ich danach, die Stärken nachzuahmen, die ich bei meinen Gegnern sehe. Man kann den Kampf zu seinen Gunsten wenden, indem man einfach seinen Feind gut kennt, <name>.]], [[An einem Ort namens Teufelswald, nördlich des Eschentals, wurden die Kreaturen durch dämonische Energien verderbt. Die Bären und Wölfe wurden von ihnen verrückt, allerdings glaube ich, dass sie dadurch auch noch stärker geworden sind.]], [[Geht, <name>. Reist zum Teufelswald und tretet den Bestien gegenüber. Erfahrt selbst, ob sie wirklich so stark sind, wie ich glaube.]], },
    { [[Talo Dornhuf im Camp Mojache in Feralas möchte, dass Ihr 12 Wutklauengrizzlys und 12 Teufelspfotenverheerer im Teufelswald erledigt.]], },
  },
  [4121] = {
    [[Gefährliche Zwickmühle]],
    { [[Eskortiert Euren Gefangenen, Grark Lorkrub, durch die Brennende Steppe und durch den Schwarzfels in die Sengende Schlucht.]], [[Euch fällt ein, dass Lexlort gesagt hat, seine Männer würden auf der anderen Seite warten, um Grark in Gewahrsam zu nehmen.]], [[Ihr werdet außerdem Eure Thoriumfesseln an Lexlort übergeben müssen.]], },
    { [[Eskortiert Euren Gefangenen, Grark Lorkrub, durch die Brennende Steppe und durch den Schwarzfels in die Sengende Schlucht.]], [[Euch fällt ein, dass Lexlort gesagt hat, seine Männer würden auf der anderen Seite warten, um Grark in Gewahrsam zu nehmen.]], [[Ihr werdet außerdem Eure Thoriumfesseln an Lexlort übergeben müssen.]], },
  },
  [4122] = {
    [[Grark Lorkrub]],
    { [[Begebt Euch in die Brennende Steppe und sucht Grark Lorkrub. Ihr erinnert Euch, dass Lexlort gesagt hat, er sei zuletzt in einer großen Festung des Schwarzfelsklans gesehen worden.]], [[Wenn Ihr Grark Lorkrub gefunden habt, verwendet die Thoriumfesseln, um ihn zu binden, und bringt ihn dann über den Schwarzfels in die Sengende Schlucht zurück. Lexlorts Männer werden auf der anderen Seite warten, um Grark in Gewahrsam zu nehmen.]], },
    { [[Begebt Euch in die Brennende Steppe und sucht Grark Lorkrub. Ihr erinnert Euch, dass Lexlort gesagt hat, er sei zuletzt in einer großen Festung des Schwarzfelsklans gesehen worden.]], [[Wenn Ihr Grark Lorkrub gefunden habt, verwendet die Thoriumfesseln, um ihn zu binden, und bringt ihn dann über den Schwarzfels in die Sengende Schlucht zurück. Lexlorts Männer werden auf der anderen Seite warten, um Grark in Gewahrsam zu nehmen.]], },
  },
  [4123] = {
    [[Das Herz des Berges]],
    { [[Schon seit Jahren suche ich einen ganz bestimmten Edelstein. Er wird das 'Herz des Berges' genannt und ist so groß wie Eure Faust! Die Dunkeleisenzwerge haben den Stein in ihrem Gewölbe weggeschlossen und ich kann machen, was ich will, sie wollen ihn mir einfach nicht verkaufen!]], [[Also muss ich Gewalt anwenden.]], [[Kämpft Euch zum unteren Gewölbe der Schwarzfelstiefen durch, brecht in den geheimen Safe dort ein und holt Euch das Herz. Dazu müsst Ihr den Wachmann Stahlgriff besiegen, aber der taucht erst auf, wenn Ihr jeden Reliktkasten im Gewölbe geöffnet habt!]], [[Viel Glück.]], },
    { [[Bringt das 'Herz des Berges' zu Maxwort Funkelglanz in der Brennenden Steppe.]], },
  },
  [4124] = {
    [[Der vermisste Kurier]],
    { [[Einer meiner Kollegen, Ginro Herdfeuer, braucht Hilfe und Informationen über den Aufenthaltsort eines Kuriers und der Pakete, die er bei sich hatte. Es ist etwa eine Woche her, dass der Kurier verschwunden ist; unsere eigene Suche hat nichts ergeben und wir wissen nicht, was wir noch tun sollen. Leider musste ich den Kurier für vermisst erklären.]], [[Wenn Ihr freie Zeit und Kapazitäten habt, dann sprecht doch bitte mit Ginro. Er sollte irgendwo hier in der Festung sein, vielleicht in seinem Quartier.]], },
    { [[Sprecht mit Ginro Herdfeuer in der Mondfederfeste.]], },
  },
  [4125] = {
    [[Der vermisste Kurier]],
    { [[Es ist etwa eine Woche her, seit der Kurier Raschal sich nach Thalanaar aufgemacht hat. Er kennt Feralas gut; er ist schon bei uns, seit General Mondfeder das Expeditionskorps hergeführt hat. Ich weiß, dass er vorhatte, mit einem Ruderboot die Küste entlangzufahren und Feralas im Süden zu durchqueren, um das Lager der Horde zu umgehen.]], [[Ich konnte sein Boot nicht finden, aber vielleicht findet Ihr es ja. Vielleicht liegt es irgendwo versteckt...oder vielleicht wurde es auch versenkt. Wie dem auch sei, möglicherweise enthält es einen Hinweis darauf, wo Raschal ist. Wenn Ihr irgendetwas entdeckt, lasst es mich bitte wissen.]], },
    { [[Sucht das Boot des Kuriers irgendwo entlang der Küste von Feralas.]], },
  },
  [4126] = {
    [[Hurley Pestatem]],
    { [[Die Dunkeleisenzwerge, angeführt von dem Bösewicht Hurley Pestatem, haben eines der besten Rezepte der Donnerbräus gestohlen, jenes für das Donnerbräu Lagerbier. Diese Schurken! Sie verdienen ein solch edles Gebräu nicht!]], [[<name>, ich habe eine schwierige Aufgabe für Euch. Geht zu den Schwarzfelstiefen in der Brennenden Steppe, zerstört alle Fässer mit Donnerbräu Lagerbier, die Ihr finden könnt, und bringt unser Donnerbräurezept zurück!]], [[Bitte, bringt das Rezept unbedingt zurück, und wenn Ihr dafür die ganzen Schwarzfelstiefen umkrempeln müsstet!]], },
    { [[Bringt Ragnar Donnerbräu in Kharanos das gestohlene Donnerbräurezept.]], },
  },
  [4127] = {
    [[Havarie]],
    { [[Als Ihr mit der Hand durch den Matsch fahrt, fühlt Ihr plötzlich den Griff eines Messers unter Euren Fingern. Es sieht gut gepflegt aus und dürfte mit Sicherheit jemandem gehört haben, der auf dem Boot gewesen ist. Vielleicht hat das große Stück des verschwundenen Bootes irgendetwas mit seinem Verschwinden zu tun.]], [[Ihr nehmt das Messer an Euch, denn Ginro Herdfeuer in der Mondfederfeste hatte Euch angewiesen, ihm jegliche Hinweise sofort zu bringen. Allerdings werdet Ihr das ungute Gefühl nicht los, dass Ihr in diesem Gewässer nicht allein seid...]], },
    { [[Kehrt zur Mondfederfeste zurück und zeigt Ginro Herdfeuer das gefundene Messer.]], },
  },
  [4128] = {
    [[Ragnar Donnerbräu]],
    { [[Hallo, <name>. Wie ich hörte, soll Ragnar Donnerbräu außer sich vor Wut sein, dass jemand sein Bier gestohlen hat. Er sucht nach wackeren Abenteurern und ist gewillt, sie reichlich zu entlohnen.]], [[Sprecht mit Ragnar, wenn Ihr Interesse habt. Ihr findet ihn in der Brauerei Donnerbräu in Kharanos in Dun Morogh.]], },
    { [[Sprecht mit Ragnar Donnerbräu.]], },
  },
  [4129] = {
    [[Das wieder aufgetauchte Messer]],
    { [[Der Hausdruide der Festung, Quintis Lebenshand, verfügt über ein paar... ungewöhnliche Fertigkeiten. Eine davon ist die Psychometrie - die Fähigkeit, durch Berührung eines Objekts etwas über seine Geschichte zu erfahren. Fragt mich nicht, wie er das bewerkstelligt. Es muss etwas mit Magie zu tun haben, wovon ich nichts verstehe und womit ich auch nichts zu tun haben will. Zwei Dinge weiß ich jedoch: Erstens, ich habe gesehen, dass es funktioniert, und zweitens, der Gauner schuldet mir etwas.]], [[Quint lebt in einem kleinen Baumhaus. Bringt ihm das Messer und richtet ihm von mir aus, dass seine Kalimdor-Poker-Schulden bei mir beglichen sind, wenn er seinen Hokuspokus bei dem Messer wirkt.]], },
    { [[Bringt Quintis Lebenshand in der Mondfederfeste das gefundene Messer.]], },
  },
  [4130] = {
    [[Psychometrie-Sitzung]],
    { [[Entschuldigt bitte, mir ist immer noch ein bisschen schwummrig. Wie dem auch sei, ich hatte ein paar nützliche Visionen.]], [[Raschal nimmt zwei lederne Rucksäcke aus seinem Boot und geht landeinwärts. Dann jedoch kehrt er zu seinem Boot zurück. Ich sehe Gnollskalps... er ist auf ein paar Gnolle gestoßen - Waldpfoten nennt man sie, glaube ich. Er ist sehr besorgt - fast schon panisch - wegen etwas. Und das so sehr, dass er nicht bemerkt, wie sich Wasserelementare hinter seinem Rücken manifestieren. Hier enden die Visionen.]], [[Ich muss mich ausruhen, tut mir leid, <name>. Erzählt Gin, was ich gesehen habe. Er muss das wissen.]], },
    { [[Sprecht mit Ginro Herdfeuer in der Mondfederfeste.]], },
  },
  [4131] = {
    [[Die Waldpfotengnolle]],
    { [[Unsere 'Freunde', die Waldpfoten, mögen Raschal am Ende vielleicht zwar erwischt haben, aber irgendwie habe ich das Gefühl, als hätte sich da etwas anderes ereignet. Wie dem auch sei, es dürfte allerdings der beste Anhaltspunkt sein, um mit der Suche zu beginnen.]], [[Ich wünschte nur, ich wüsste genauer, was zu tun ist, aber momentan kann ich lediglich sagen: 'Durchsucht die Lager der Waldpfoten. Macht Euch auf den Weg.' Ich weiß, dass Gnolle Gegenstände, die sie ergattern, behalten und um sie zanken. Vielleicht haben sie ja auch die Rucksäcke gefunden, die Quint erwähnte. Ohne Quints Visionen hätte ich bei früheren Suchaktionen niemals eine direkte Konfrontation mit ihnen gesucht, aber jetzt würde ich sagen: Auf sie mit Gebrüll!]], },
    { [[Durchsucht die Lager der Waldpfotengnolle im Süden von Feralas nach Hinweisen auf den Verbleib des Kuriers oder seiner Habseligkeiten.]], },
  },
  [4132] = {
    [[Operation: Tod dem Zornesschmied]],
    { [[Begebt Euch zu den Schwarzfelstiefen und eliminiert General Zornesschmied! Kehrt zum Kriegsherrn Bluthauer zurück, sobald Ihr diese Aufgabe erledigt habt.]], },
    { [[Begebt Euch zu den Schwarzfelstiefen und eliminiert General Zornesschmied! Kehrt zum Kriegsherrn Bluthauer zurück, sobald Ihr diese Aufgabe erledigt habt.]], },
  },
  [4133] = {
    [[Vivian Lagrave]],
    { [[Schattenmeisterin Vivian Lagrave benötigt Eure Dienste. Sie lebt im Ödland, im orcischen Außenposten Kargath, wo sie dem Gerücht nachgeht, nach dem die Dunkeleisenzwerge der Schwarzfelstiefen angeblich ein bestimmtes Gebräu herstellen.]], [[Sprecht mit Vivian. Sie erwartet Euch.]], },
    { [[Sprecht mit Schattenmeisterin Vivian Lagrave in Kargath.]], },
  },
  [4134] = {
    [[Verlorenes Donnerbräurezept]],
    { [[Es geht das Gerücht umher, dass der Dunkeleisenzwerg Hurley Pestatem ein Rezept zum Brauen von Bier gestohlen habe. Dieses Bier, das Donnerbräu Lagerbier, soll angeblich jeden, der es trinkt, mit großer Kraft und Mut erfüllen. Wir möchten dieses Getränk gern untersuchen. Vielleicht finden wir ja noch andere Anwendungsgebiete für seine Vorzüge... Anwendungsgebiete, die eher im Einklang mit den Zielen der Verlassenen sind.]], [[Durchforstet die Schwarzfelstiefen, findet Hurley, nehmt das Donnerbräurezept von ihm und bringt es mir.]], [[Und um ihn zu finden, müsst Ihr ihn möglicherweise locken, indem Ihr sein wertvolles Bier bedroht.]], },
    { [[Bringt Vivian Lagrave in Kargath das gestohlene Donnerbräurezept.]], },
  },
  [4135] = {
    [[Die Gewundene Tiefe]],
    { [[In den Rucksäcken befinden sich zwei Gegenstände: Der erste ist ein für Thalanaar bestimmtes Paket mit Waren, der zweite eine hastig hingekritzelte Notiz, die an einen unbekannten Empfänger gerichtet ist.]], [[Die Notiz spricht von der Entdeckung heimtückischer käferartiger Kreaturen südlich des Gnoll-Lagers an einem Ort, der Gewundene Tiefe genannt wird. Der Verfasser wollte die Sache persönlich untersuchen, da er dies als ernsthafte Bedrohung für ganz Feralas befand. Die Notiz ist mit 'Raschal von Mondfeder' unterzeichnet.]], [[Offenbar habt Ihr jetzt herausgefunden, wohin Raschal letztlich verschwunden ist.]], },
    { [[Da Ihr jetzt Raschals letzten bekannten Aufenthaltsort in Erfahrung gebracht habt, setzt Ihr die Suche nach ihm oder seinen sterblichen Überresten in der Gewundenen Tiefe fort. Laut Notiz soll dieser Ort südlich der Lager der Waldpfotengnolle liegen.]], },
  },
  [4136] = {
    [[Ribbly Schraubstutz]],
    { [[Mein Bruder Ribbly hat unserer Familie schon immer auf der Tasche gelegen, unser Geld genommen und es für eine Dummheit nach der anderen ausgegeben.]], [[Tja, seine letzte Dummheit muss wohl seine bislang schlimmste gewesen sein, denn Baron Revilgaz von Beutebucht hat ein Kopfgeld auf den armen Ribbly ausgesetzt. Ich kann Euch gar nicht sagen, wie glücklich das die Schraubstutzs macht! Endlich ist unser kleiner Ribbly mal etwas wert!]], [[Er versteckt sich jetzt allerdings ganz weit unten in den Schwarzfelstiefen. Sucht ihn doch bitte und bringt ihn zu mir!]], [[Und falls er nicht mitkommen will, ist das auch kein Problem...bringt mir einfach seinen Kopf.]], },
    { [[Bringt Yuka Schraubstutz in der Brennenden Steppe Ribblys Kopf.]], },
  },
  [4141] = {
    [[Muigin und Larion]],
    { [[Seht Ihr Larion da drüben? Es ist SEINE Schuld, dass wir hier sind!]], [[Ich hab ihm noch gesagt, dass wir in die falsche Richtung gehen, aber hat er auf mich gehört? Und stellt Euch vor, jetzt spricht er nicht mehr mit mir. Na ja, erlauben wir uns doch ein kleines Späßchen mit ihm, bis er es sich anders überlegt.]], [[Sind Euch die seltsamen pflanzenartigen Kreaturen in dieser Gegend hier aufgefallen? Die machen nichts als Ärger, wirklich, aber ich glaube, wenn man sie ordentlich abrichten würde, könnte man sie dazu bringen, Befehlen zu gehorchen.]], [[Als Erstes bräuchte ich allerdings ein paar davon, um sie zu studieren. Meint Ihr, Ihr könntet mir ein paar bringen?]], },
    { [[Sammelt 15 Blutblüten und kehrt zu Muigin zurück.]], },
  },
  [4142] = {
    [[Ein Besuch bei Gregan]],
    { [[Ich weiß nicht so recht, was ich davon halten soll, <name>. Die Pflanzen... sie scheinen ein gewisses Maß an Intelligenz zu besitzen.]], [[Allerdings bin ich auf diesem Gebiet kein Experte. Aber ich kenne einen!]], [[Geht nach Nord-Feralas und sucht einen Freund von mir auf, er heißt Gregan Hopfenspei. Er kennt sich mit solchen Dingen bestens aus.]], [[Gregan kann uns bestimmt weiterhelfen. Ihr findet ihn draußen in der Wildnis, denn für das Großstadtleben hat er nicht viel übrig.]], },
    { [[Bringt Gregan Hopfenspei in Feralas eine Blutblüte.]], },
  },
  [4143] = {
    [[Der Dunst des Bösen]],
    { [[Um ehrlich zu sein, <name>, solche Pflanzen habe ich auf meinen Reisen noch nie gesehen. Vielleicht sollte ich mal einen Ausflug zu diesem Krater machen...]], [[Egal, ich weiß vermutlich, was Muigin benötigt - Dunst der Atal'ai. Das ist ein abscheuliches Zeug, sehr schwer zu sammeln, aber es ist genau das, was er benötigt, um diese kleinen Monster unter Kontrolle zu bringen.]], [[Ihr müsst zum Tempel von Atal'Hakkar in den Sümpfen des Elends gehen. Den Dunst der Atal'ai bekommt Ihr von Tieflauerern, Düsterwürmern oder Schlammen, die man dort findet. Viel Glück, <name>!]], },
    { [[Sammelt 5 Proben Dunst der Atal'ai und bringt sie Muigin im Krater von Un'Goro.]], },
  },
  [4144] = {
    [[Blutblütensprösslinge]],
  },
  [4145] = {
    [[Larion und Muigin]],
    { [[Seht Ihr Muigin da drüben? Es ist SEINE Schuld, dass wir uns verirrt haben!]], [[Ich kannte den richtigen Weg, aber er bestand darauf, eine Abkürzung zu nehmen, und so sind wir hier gelandet!]], [[Und um die Lage noch zu verschlimmern, schickt er mir jetzt auch noch seine verrückten Pflanzen, um mich zu ärgern. Ich bin so wütend, dass ich bis auf weiteres kein Wort mehr mit ihm reden werde, aber diese Quälgeister sind wirklich unerträglich.]], [[Warum versuchen wir nicht als Erstes, seinen Bestand ein bisschen zu dezimieren? Geht hinaus und jagt einige Blutblüten und kehrt dann zu mir zurück.]], },
    { [[Jagt 5 Blutblütenpeitscher, 5 Blutblütendrescher, 5 Blutblütenschinder und 5 Blutblütenfallen für Larion in Marschalls Zuflucht.]], },
  },
  [4146] = {
    [[Treibstoff für die Unkrautvernichtung]],
    { [[Ihr habt Glück, <name>! Während Marvon fort war, habe ich an einigen meiner eigenen Experimente gearbeitet, eines davon ist ein Unkrautvernichter mit kurzer Reichweite. Nur noch ein paar kleine Änderungen, dann ist er perfekt, um damit diese Blutblütenplagen zu beseitigen.]], [[Ihr könnt gern einen meiner Prototypen haben, aber Ihr benötigt noch etwas Treibstoff dafür. Den für den Vernichter benötigten Dunst der Atal'ai findet Ihr im Tempel von Atal'Hakkar. Ihr bekommt ihn von Tieflauerern, Düsterwürmern oder Schlammen.]], },
    { [[Bringt Larion in Marschalls Zuflucht den ungeladenen Unkrautvernichter und 5 Proben Dunst der Atal'ai.]], },
  },
  [4147] = {
    [[Marvons Werkstatt]],
    { [[Ich habe herausgefunden, was Muigin so treibt. Er züchtet da drüben doch tatsächlich diese verdammten Dinger!]], [[Ich glaube, die einzige Lösung des Problems besteht darin, mit... nein, nicht mit ihm zu sprechen... Was für ein Blödsinn! Die Lösung ist zurückzuschlagen!]], [[Wir brauchen die Hilfe meines Freundes Marvon, der in Ratschet eine Werkstatt hat. Er kann bestimmt irgendein Gerät zusammenbasteln, das uns helfen wird.]], },
    { [[Reist zu Marvon Nietensuchers Werkstatt in Ratschet.]], },
  },
  [4148] = {
    [[Blutblütenvernichter]],
  },
  [4161] = {
    [[Rezept der Kaldorei]],
    { [[Vor langer Zeit nannte man die Nachtelfen 'Kaldorei', was übersetzt 'Kinder der Sterne' bedeutet. Für Euch als <class> ist es besonders wichtig, etwas über die Vergangenheit zu erfahren, also hört gut zu, was ich Euch zu sagen habe, <name>.]], [[Die Kaldorei waren schon immer stolz auf ihre harmonische Beziehung zur Natur. Das bedeutet, dass wir von der Natur nur nehmen, was notwendig ist, und es in gleichem Maße vergelten. Dieses Gleichgewicht bedeutet uns viel, <name>.]], [[Geht und sammelt sieben kleine Spinnenbeine - und nicht mehr, und kommt dann zu mir zurück.]], },
    { [[Sammelt 7 kleine Spinnenbeine.]], },
  },
  [4181] = {
    [[Gobliningenieurskunst]],
    { [[Bei der Experteningenieurskunst gibt es zwei unterschiedliche Disziplinen: Gnom und Goblin. Ich erwähne die Ingenieurskunst der Goblins nur, weil ich ein gerechter und unvoreingenommener Lehrer bin. Sie machen gern Dinge, die in die Luft gehen und es ist ihnen dabei meistens egal, ob dabei was im Weg herumsteht - auch wenn sie es selbst sind.]], [[Lest dieses Handbuch, <name>. Wenn Ihr lernen wollt, wie Ihr Euch selbst in die Luft jagt, dann bringt es zu Nixx Sprossfeder nach Gadgetzan. Denkt daran - das ist eine dauerhafte Mitgliedschaft. Es ist also nicht möglich, auch in die andere Disziplin einzutreten, daher solltet Ihr genau wissen, was Ihr wollt, bevor Ihr seine Aufgabe ausführt.]], },
    { [[Wenn Ihr mehr über die Gobliningenieurskunst wissen wollt, dann bringt das Handbuch der Ingenieurskunstdisziplinen zu Nixx Sprossfeder nach Gadgetzan.]], },
  },
  [4182] = {
    [[Drachkin-Bedrohung]],
    { [[Erschlagt 15 schwarze Brutlinge, 10 Exemplare der schwarzen Großdrachenbrut, 4 schwarze Wyrmkin und 1 Schwarzdrachen.]], },
    { [[Erschlagt 15 schwarze Brutlinge, 10 Exemplare der schwarzen Großdrachenbrut, 4 schwarze Wyrmkin und 1 Schwarzdrachen.]], },
  },
  [4183] = {
    [[Die wahren Meister]],
    { [[Bislang nahmen wir an, dass die Schwarzfelsorcs das Ergebnis ihrer primitiven Erziehung seien. Was wir jedoch hier, in ihrem Heimatland, entdeckt haben, erzählt uns eine ganz andere Geschichte.]], [[Angetrieben werden sie zwar tatsächlich von Kampfrausch und Streitlust, aber der schwarze Drachenschwarm ist es, der die Orcs des Schwarzfels beherrscht. Wir vermuten, dass es auch der schwarze Drachenschwarm ist, der ihre Wut und Feindseligkeit schürt.]], [[Bringt diesen Brief zu Magistrat Solomon. Er wird wissen, was zu tun ist.]], [[<Helendis gibt Euch einen versiegelten Brief.>]], },
    { [[Reist nach Seenhain im Rotkammgebirge und händigt Magistrat Solomon Helendis Flusshorns Brief aus.]], },
  },
  [4184] = {
    [[Die wahren Meister]],
    { [[Reist nach Sturmwind und tragt Solomons Hilfegesuch dem König Varian Wrynn vor.]], [[Der König residiert in Burg Sturmwind.]], },
    { [[Reist nach Sturmwind und tragt Solomons Hilfegesuch dem König Varian Wrynn vor.]], [[Der König residiert in Burg Sturmwind.]], },
  },
  [4185] = {
    [[Die wahren Meister]],
    { [[Sprecht zuerst mit Lady Katrana Prestor, dann mit Hochlord Bolvar Fordragon.]], },
    { [[Sprecht zuerst mit Lady Katrana Prestor, dann mit Hochlord Bolvar Fordragon.]], },
  },
  [4186] = {
    [[Die wahren Meister]],
    { [[Ich ernenne Euch hiermit zum kommissarischen Stellvertreter von Sturmwind, <name>.]], [[Kehrt zu Magistrat Solomon in Seenhain zurück und übergebt ihm diesen Erlass.]], },
    { [[Händigt Wrynns Erlass an Magistrat Solomon in Seenhain aus.]], },
  },
  [4201] = {
    [[Der Liebestrank]],
    { [[Oh, wie schmerzhaft doch die Liebe ist! Mein geliebter Gefreiter Rocknot ist immun gegen meinen Charme. Er verschmäht mich! Es ist zum Verrücktwerden!]], [[Ihr müsst mir helfen! Wir Sukkuben wissen viel über die Liebe und ich kann einen Trank herstellen, der den eisernen Panzer um Rocknots Herz erweichen könnte. Dazu benötige ich folgende Dinge: das Kraut Gromsblut, Riesensilbervenen von den Klippenriesen Azsharas sowie Wasser aus der Quelle des Golakkakraters in Un'Goro. Ihr könnt das Wasser in diese Phiole füllen.]], [[Bitte, <name>, verhelft einer armen Seele zum Glück!]], },
    { [[Bringt 4 Gromsblut-Kräuter, 10 Riesensilbervenen und Nagmaras gefüllte Phiole zu Herrin Nagmara in den Schwarzfelstiefen.]], },
  },
  [4221] = {
    [[Verderbte Windblüte]],
  },
  [4222] = {
    [[Verderbte Windblüte]],
  },
  [4223] = {
    [[Die wahren Meister]],
    { [[<Magistrat Solomon seufzt.>]], [[Ihr müsst in die Brennende Steppe zurückkehren, <name>. Als Stellvertreter von Sturmwind erwartet man von Euch, dass Ihr Euch bei Marschall Maxwell meldet. Viel Glück.]], },
    { [[Sprecht mit Marschall Maxwell in der Brennenden Steppe.]], },
  },
  [4224] = {
    [[Die wahren Meister]],
    { [[Was? Ihr dachtet, Ihr wärt der Erste? Wie viele Soldaten müssen wir noch verlieren, bevor Sturmwind handelt?]], [[Wir hatten riesige Mengen an Informationen über die Schwarzfelsbedrohung zusammengetragen, doch dann verloren wir unseren befehlshabenden Offizier, Marschall Windsor. Zusammen mit Marschall Windsor gingen auch all unsere wertvollen Informationen verloren.]], [[Was wir brauchen, ist diese Dokumentation. Sprecht mit dem struppigen John, <name>. Er war mit Windsor zusammen, als sie von einem Stoßtrupp der Schwarzfelsorcs überfallen wurden. Er wurde zuletzt in einer Höhle im Norden gesehen.]], },
    { [[Sprecht mit dem struppigen John.]], },
  },
  [4241] = {
    [[Marschall Windsor]],
    { [[Dieser nichtsnutzige Feigling behauptet also, Windsor sei noch am Leben, was?]], [[Macht Euch bereit, Stellvertreter, Ihr werdet ausziehen, um ihn zu suchen. Falls Windsor noch am Leben ist, müsst Ihr ihn zurückholen. Sollte er tot sein, brauche ich einen Beweis dafür.]], [[<Maxwell lacht.>]], [[Ich wette, das könnt Ihr langsam schon nicht mehr hören, was?]], },
    { [[Reist zum Schwarzfels im Nordwesten und dann weiter zu den Schwarzfelstiefen. Findet heraus, was aus Marschall Windsor geworden ist.]], [[Ihr erinnert Euch daran, dass der struppige John sagte, man hätte Windsor in ein Gefängnis verschleppt.]], },
  },
  [4242] = {
    [[Verlorene Hoffnung]],
    { [[Bolvar ist ein Dummkopf. Ich war gerade dabei, ihm seinen kostbaren 'Beweis' zu beschaffen, um ihm diesen in den Hals stopfen zu können.]], [[Er ist ein blinder Blödmann. Selbst wenn der Beweis ihn in die Nase beißt, würde er ihn nicht sehen.]], [[Was meine Informationen angeht, die sind verloren. Unwiderbringlich.]], [[Sollte ich Eisenfeind jemals finden und es aus diesem Labyrinth herausschaffen, wird mein erster Halt Sturmwind sein, damit ich diesem Reptil meinen Hammer über den Schädel ziehen kann.]], [[Geht jetzt, <name>.]], },
    { [[Überbringt Marschall Maxwell bei Morgans Wacht in der Brennenden Steppe die schlechten Neuigkeiten.]], },
  },
  [4243] = {
    [[Chasing A-Me 01]],
    { [[Irgendetwas muss meiner bislang besten Erfindung zugestoßen sein. Ihr habt doch schon mal mechanische Eichhörnchen gesehen, richtig? Tja, um näher an die Affen des Kraters von Un'Goro heranzukommen, habe ich eine mechanische Äffin geschaffen, die sich unbemerkt unter sie mischen kann - ich nenne sie A-Me 01.]], [[Sie hat Wahrsagesteine als Augen, so konnte ich alles sehen, was auch sie sah. Den Fungusfels hat sie wohlbehalten erreicht - das ist das Versteck der Affen, gleich nördlich von hier. Und dort habe ich dann den Kontakt zu ihr verloren. Das Letzte, was ich sah, war eines der Alphamännchen, das auf sie zugestürmt kam, um sie zu begrüßen!]], [[Könntet Ihr mir wohl helfen, sie zu finden?]], },
    { [[Sucht im Krater von Un'Goro nach A-Me 01.]], },
  },
  [4244] = {
    [[Chasing A-Me 01]],
    { [[Ihr setzt Eure Untersuchungen fort und findet eine Abdeckung, die sich aufschieben lässt. Oberhalb der Verkabelung steht in gut lesbarer Schrift geschrieben: 'Mithrilgehäuse ersetzen und Strom erst ausschalten, dann einschalten.']], [[Es dauert einen Moment, bis Ihr die Abdeckung wieder angebracht habt, und Ihr fragt Euch, ob es wirklich so einfach ist, A-Me 01 zu reparieren und zurück zu Karna Wirrbart in der Nähe von Marschalls Zuflucht zu bringen.]], [[Tja, probieren geht über studieren...]], },
    { [[Sucht ein Mithrilgehäuse und bringt es zu A-Me 01 im Krater von Un'Goro.]], },
  },
  [4245] = {
    [[Chasing A-Me 01]],
    { [[Nachdem Ihr Euch vergewissert habt, dass Ihr bereit seid, A-Me 01 zu Karna Wirrbart zurückzubringen, knipst Ihr den Schalter auf 'Aus'. Ihr seht Euch nochmals um, um sicherzugehen, dass sich keine Gorillas nähern, und fragt Euch, was wohl geschieht, wenn Ihr den Schalter wieder auf die 'Ein'-Position stellt.]], [[Das ist Eure letzte Chance...wollt Ihr das wirklich tun?]], },
    { [[Begleitet A-Me 01 zu Karna Wirrbart im Krater von Un'Goro.]], },
  },
  [4261] = {
    [[Alter Geist]],
    { [[Der Teufelswald war einst ein friedliches und ruhiges Land... bis die Legion hier eingefallen ist. Die Höllenbestien, Zerstörung, Chaos...]], [[Der Schädel des Gul'dan hat dafür gesorgt, dass hier niemand ungeschoren davonkam. Alles fiel der Verderbnis zum Opfer.]], [[Sogar jetzt, da ich ein Geist bin, bin ich immer noch gefangen und muss die Leiden dieses Landes ertragen. Helft mir, den Weg nach Hause zu finden. Bitte, <name>, sagt mir, dass Ihr einen alten Geist weg von hier führen werdet, dorthin, wo ich endlich ruhen kann.]], [[Sobald ich frei sein werde, solltet Ihr Kayneth Stillwind aufsuchen, einen Druiden, der in Waldeslied im Osten lebt.]], },
    { [[Bringt Arei in Sicherheit und sprecht dann mit Kayneth Stillwind in Waldeslied, im Osten des Eschentals.]], },
  },
  [4262] = {
    [[Übermeister Pyron]],
    { [[Die letzten fünf meiner Magier, die ausgezogen waren, um sich auf dem Schwarzfels umzusehen, kamen knusprig geröstet zurück, mit einem dauerhaften orangefarbenen Gnomenafro - oder noch schlimmer.]], [[<Jalinda nimmt etwas Asche vom Boden auf und lässt sie vom Wind davontragen.>]], [[Ein besonders mieses Individuum, Übermeister Pyron, scheint hier eine Menge Ärger zu verursachen. Dieser Elementar muss vernichtet werden, damit unser Expeditionsteam in die Schwarzfelstiefen reisen kann!]], [[Er bewacht den Eingang am Schwarzfelssteinbruch.]], },
    { [[Erschlagt Übermeister Pyron.]], [[Ihr erinnert Euch an Jalindas Worte, dass Pyron den Steinbruch bewachen würde. Vielleicht solltet Ihr dort nachsehen?]], },
  },
  [4263] = {
    [[Incendius!]],
    { [[Sucht Lord Incendius in den Schwarzfelstiefen und vernichtet ihn!]], },
    { [[Sucht Lord Incendius in den Schwarzfelstiefen und vernichtet ihn!]], },
  },
  [4264] = {
    [[Eine zusammengeknüllte Notiz]],
    { [[Soeben seid Ihr auf etwas gestoßen, das Marschall Windsor mit Sicherheit sehr interessiert. Vielleicht besteht ja doch noch Hoffnung.]], },
    { [[Soeben seid Ihr auf etwas gestoßen, das Marschall Windsor mit Sicherheit sehr interessiert. Vielleicht besteht ja doch noch Hoffnung.]], },
  },
  [4265] = {
    [[Flucht aus dem Schwarmbau]],
    { [[Die Kapsel platzt auf, als Ihr sie berührt, und zum Vorschein kommt ein Nachtelf, der überraschenderweise noch am Leben ist. Er scheint bei guter Gesundheit zu sein, ist jedoch sehr benommen. Als Ihr ihm aus der Kapsel heraushelft, regt sich Leben in ihm.]], [["Elune sei Dank! Ihr habt mich gerettet! Ich... Mein Name ist Raschal. Ich erwachte völlig gelähmt in dieser Kapsel, nachdem mich diese Käfer überwältigt hatten. Wurdet Ihr von der Festung geschickt?"]], [[Anscheinend habt Ihr den vermissten Kurier gefunden!]], },
    { [[Begleitet Raschal sicher aus dem Schwarmbau der Zukk'ash hinaus.]], [[Kehrt zu Ginro Herdfeuer in der Mondfederfeste zurück und lasst ihn wissen, dass Raschal am Leben ist und dass es ihm gut geht.]], },
  },
  [4266] = {
    [[Der Empfang eines Helden]],
    { [[<name>, General Shandris Mondfeder möchte unter vier Augen mit Euch sprechen. Sie möchte Euch bestimmt für Eure Dienste zum Wohle der Festung danken, aber ich weiß auch, dass sie außerdem mit Euch über diese Insektenwesen sprechen will, auf die Ihr und Raschal im Süden von Feralas gestoßen seid. Die Berichte, die diese Insekten der Zukk'ash betreffen, sind...mehr als beängstigend.]], [[General Mondfeder wartet im Haupttruppenlager auf Euch.]], },
    { [[Sprecht mit Shandris Mondfeder in der Mondfederfeste.]], },
  },
  [4267] = {
    [[Aufstieg der Silithiden]],
    { [[Ich vertraue Euch eine Kopie des Berichts an, den Raschal über die Plage im Südosten Feralas' angefertigt hat. Der eigentliche Bericht befindet sich in den Akten in Darnassus, aber ich möchte, dass Ihr diese Kopie an eine Vertraute des Protektorats überbringt. Ihr Name lautet Gracina Glaubensmacht. Sie ist eine Priesterin, die im Tempel des Mondes in Darnassus lebt. Sie verfügt über großes Wissen, solche Kreaturen betreffend, und wenn sie diesen Bericht erhalten würde, käme es uns allen sehr zugute.]], [[Macht Euch auf den Weg, und viel Glück, <name>. Möge Elune über uns alle wachen.]], },
    { [[Bringt Raschals Bericht zu Gracina Glaubensmacht in Darnassus.]], },
  },
  [4281] = {
    [[Thalanaar-Lieferung]],
    { [[Dieses Paket mit Waren, das Ihr in den Lagern der Waldpfotengnolle gefunden habt, war für den kleinen Streckenposten Thalanaar im Südosten Feralas' bestimmt. Das Paket, ungeachtet des Schicksals des vermissten Kuriers, harrt immer noch der Auslieferung.]], [[Ihr untersucht das Paket genauer und entdeckt den Namen 'Falfindel Wegeshut' handschriftlich auf der Verpackung vermerkt. Vielleicht ist er der eigentliche Empfänger des Pakets.]], },
    { [[Bringt das nicht ausgelieferte Paket zu Falfindel Wegeshut in Thalanaar.]], },
  },
  [4282] = {
    [[Ein Funken Hoffnung]],
    { [[<Marschall Windsor wedelt aufgeregt mit der Notiz herum.>]], [[Wisst Ihr, was das bedeutet, <name>?]], [[Vielleicht ist doch noch nicht alles verloren! Soweit ich das erkennen kann, wird die Information von zwei Dunkeleisenzwergen verwahrt: General Zornesschmied und dem Golemlord Argelmach. Ich habe das Gefühl, als würden sie die Information nicht einfach so herausrücken, wenn wir sie freundlich darum bitten.]], [[Also hoffe ich, dass Ihr auf einen Kampf vorbereitet seid.]], [[Zieht aus und findet die beiden Bösewichte! Vernichtet sie und bringt mir meine verloren gegangenen Informationen zurück.]], },
    { [[Holt Marschall Windsors verloren gegangene Informationen zurück.]], [[Marschall Windsor glaubt, dass sich die Informationen in den Händen des Golemlords Argelmach und des Generals Zornesschmied befinden.]], },
  },
  [4283] = {
    [[FÜNFZIG! Jawoll!]],
    { [[Ich werde Euch ein kleines Geheimnis verraten, Soldat: Kommt näher.]], [[ICH BIN HIER DER EINZIGE, DER NOCH BEI VERSTAND IST!]], [[Seht Euch nur all diese Leute an! Ist es zu fassen, dass sie alle irre sind!? Oh ja, das sind sie! DURCHGEDREHT! WAHNSINNIG!]], [[<Oralius senkt die Stimme.>]], [[Das bleibt aber unter uns: mir, Euch und meinem Kumpel Käpt'n Winky.]], [[<Oralius greift in die Luft und drückt zu.>]], [[Wie auch immer...Ich sammle Abzeichen, die gut zu meiner Sammlung Ogerohren passen. Orcs sind der letzte Schrei. Fünfzig. LOS!]], [[<Oralius stößt in ein Horn.>]], [[AB DAMIT!]], },
    { [[Beschafft Euch 50 Medaillons des Schwarzfelsklans.]], },
  },
  [4284] = {
    [[Machtkristalle]],
    { [[Ich bin sicher, Ihr habt sie bemerkt, <name>. Sie sind überall!]], [[Wovon ich rede? Na, von den Kristallen natürlich!]], [[Die Kristalle... Sie liegen überall im Krater herum. Die meisten Leute ignorieren sie, aber ich habe das seltsame Gefühl, als besäßen sie eine gewisse Macht. Ich habe eine Weile mit ihnen herumexperimentiert, aber ich habe noch nichts herausgefunden. Tja, bis jetzt jedenfalls.]], [[Aber hört mal, mir gehen langsam die Kristalle aus - meint Ihr, Ihr könntet noch ein paar für mich sammeln?]], },
    { [[Sammelt 7 Machtkristalle von jeder Farbe: rot, blau, gelb und grün. Bringt sie zu J. D. Collie in Marschalls Zuflucht.]], },
  },
  [4285] = {
    [[Der Nordpylon]],
    { [[Wofür die wohl gut sind? Ich muss ständig daran denken!]], [[Es macht mich verrückt, dass all die Gerüchte über die Kristallpylonen wahr sind. Nennt mich leichtgläubig, aber ich glaube gewöhnlich, was mir die Leute erzählen.]], [[Wie dem auch sei, ich habe gehört, dass sich einer dieser Kristallpylonen hier ganz in der Nähe befinden soll. Irgendwo im Norden... Sucht ihn doch bitte, <name>. Ich weiß genau, dass es die Dinger gibt.]], },
    { [[Findet den Standort des nördlichen Kristallpylonen im Krater von Un'Goro.]], },
  },
  [4286] = {
    [[Das gute Zeug]],
    { [[Beschafft Euch 20 Dunkeleisengürteltaschen.]], },
    { [[Beschafft Euch 20 Dunkeleisengürteltaschen.]], },
  },
  [4287] = {
    [[Der Ostpylon]],
    { [[Während meines Aufenthaltes hier sind mir seltsame Gerüchte über rätselhafte Pylonen zu Ohren gekommen, die es angeblich draußen in der Wildnis des Kraters geben soll, <name>. Ich nehme an, sie sind ziemlich groß, mit Kristallen an den Innenseiten.]], [[Ich habe zwar noch nie welche gesehen, aber ich komme nicht umhin zu glauben, dass die Gerüchte wahr sind - und dass die Pylonen irgendetwas mit all den Kristallen zu tun haben, die wir gefunden haben.]], [[Also, warum helft Ihr mir nicht, meine Neugier zu stillen? Angeblich soll es einen im östlichen Teil des Kraters geben. Meint Ihr, Ihr könntet ihn finden?]], },
    { [[Findet den Standort des östlichen Kristallpylonen im Krater von Un'Goro.]], },
  },
  [4288] = {
    [[Der Westpylon]],
    { [[Meine Experimente haben bis jetzt noch keine positiven Ergebnisse erzielt, <name>. Ich kann nur hoffen, dass Ihr eine Lösung findet, indem Ihr einen der Pylonen entdeckt, von denen ich gehört habe.]], [[Was, wenn wir bestimmte Farben mit ihnen kombinieren würden... ach, eines nach dem anderen. Zuerst müssen wir sie finden!]], [[<name>, sucht im westlichen Teil des Kraters. In diesem Gebiet soll sich irgendwo ein Pylon befinden.]], },
    { [[Findet den Standort des westlichen Kristallpylonen.]], },
  },
  [4289] = {
    [[Die Affen von Un'Goro]],
    { [[In diesem Krater gibt es viele seltsame Kreaturen - sie scheinen edler zu sein als ihre Verwandten, die man überall in meinem Heimatland und jenseits davon findet. Ich habe keine Erklärung dafür, aber dieser Ort trägt die Handschrift der Erdenmutter. Immer, wenn ich auf eines ihrer wundervollen Geschöpfe in diesem Krater stoße, erweise ich ihr meine Ehrerbietung.]], [[Wenn auch Ihr diese Erleuchtung spüren möchtet, die eine solche Tat begleitet, dann geht nach Norden. Dort findet Ihr große Affen, die die Kraft eines jeden Sterblichen auf eine harte Probe stellen. Bringt mir ihre Pelze...beweist mir Eure Kraft.]], },
    { [[Bringt Torwa Pfadfinder am Eingang zum Krater von Un'Goro 2 Gorillapelze von Un'Goro, 2 Stampferpelze von Un'Goro und 2 Donnererpelze von Un'Goro.]], },
  },
  [4290] = {
    [[Lar'korwis Mahl]],
    { [[Einst, vor sehr langer Zeit, stahl einer meiner Brüder ein Stück Fleisch aus Lar'korwis Jagdbeute. Damals wusste er es nicht, aber Lar'korwi hat ihn vom Wald aus beobachtet. Lar'korwi folgte meinem Bruder tagelang und vernichtete alles, was er berührte. Mein Bruder, von Angst erfüllt, entschloss sich schließlich, aus dem Krater zu fliehen, um Lar'korwi zu entkommen.]], [[Lar'korwi tötete ihn, nicht weit von dieser Stelle entfernt.]], [[Wenn Ihr Lar'korwi provozieren wollt, sucht nach seiner letzten Jagdbeute und stehlt ihm etwas Fleisch.]], [[Kommt dann zu mir zurück.]], },
    { [[Sucht das Gerippe von Lar'korwis letzter Jagdbeute und stehlt einen Teil eines Dreschadongerippes; kehrt dann zu Torwa Pfadfinder im Krater von Un'Goro zurück.]], },
  },
  [4291] = {
    [[Lar'korwis Fährte]],
    { [[Ich bin wirklich erstaunt, dass er noch nicht hinter Euch her ist, <name>. Alles läuft jedoch gut, denn ich habe einen letzten Plan, mit dem wir das Monster zu uns locken können.]], [[Als dominantem Männchen steht es Lar'korwi zu, sich mit jedem Weibchen zu paaren, das ihm gefällt - und ihm gefallen viele. Westlich von hier werdet Ihr eine Menge Ravasauruseier finden. Tretet dicht an sie heran, um damit einen Angriff seiner Artgenossen zu provozieren. Die Weibchen produzieren in einer Drüse an ihrem Kopf einen abscheulichen Gestank, der die Männchen wissen lässt, dass sie paarungsbereit sind. Bringt mir zwei dieser Drüsen.]], },
    { [[Bringt Torwa Pfadfinder in der Nähe des Eingangs zum Krater von Un'Goro 2 Pheromondrüsen eines Ravasaurus.]], },
  },
  [4292] = {
    [[Ein Köder für Lar'korwi]],
    { [[Das hier wird der schwierigste Teil werden, denn der Plan ist etwas kompliziert. Nördlich von hier findet Ihr einen kleinen Pfad, der in die Berge hinaufführt. Dort habe ich Lar'korwi zuletzt gesehen. Ihr werdet wissen, dass Ihr im richtigen Gebiet seid, wenn Ihr all die Knochen der großen Kreaturen findet, die es dort einst gab. Nehmt das Fleisch, das Ihr Euch besorgt habt, und legt es dort ab. Reibt das Fleisch dann mit der Drüse ein. Wenn es mit beiden Lockmitteln zusammen nicht gelingt, Lar'korwi zu Euch zu locken, dann weiß ich auch nicht weiter.]], },
    { [[Beschwört Lar'korwi mit dem Inhalt von Torwas Beutel und besiegt ihn. Bringt dann Lar'korwis Kopf zu Torwa Pfadfinder in der Nähe des Eingangs zum Krater von Un'Goro.]], },
  },
  [4293] = {
    [[Eine Schleimprobe...]],
    { [[Der Teufelswald ist ein Wald weit im Norden des Brachlandes, sogar noch weiter weg als das Eschental. Um die dortigen Teiche herum, die durch dämonische Energie gesegnet wurden, befinden sich überall Schlamme. Die Geißel hat mit den Angriffen von Höllenbestien und anderer Dämonenmagie die Gegend stark verwüstet - die Schleime sind angeblich während dieser Angriffe aus der Erde an die Oberfläche gekrochen.]], [[Sammelt einige Proben von Teufelswaldschleimen, dann verwenden wir die Testausrüstung hier neben mir, um eine Kontrollgruppe zusammenzustellen, mit der wir die anderen Proben, die Ihr eingesammelt habt, vergleichen können.]], },
    { [[Sammelt im Teufelswald Proben von verschiedenen Schleimen. Verwendet dann die Testausrüstung neben Chemiker Brenner in Unterstadt, um zu sehen, welche Eurer Proben am verderbtesten sind. Bringt 5 der verderbten Proben aus dem Teufelswald, die Ihr findet, zu Chemiker Brenner.]], },
  },
  [4294] = {
    [[...und eine Menge Schlamm]],
    { [[Habt Ihr schon einmal vom Krater von Un'Goro gehört, <name>? Er liegt in der Nähe der Tanariswüste und es ist ein großartiger Ort: üppig, von Leben sprühend, voller Kreaturen und Pflanzen.]], [[Dorthin müsst Ihr Euch begeben. Ich habe mir sagen lassen, dass der Krater noch völlig unbesudelt und frei von Einflüssen von außen sei. Erst jetzt beginnen Forscher, seine Geheimnisse zu entdecken.]], [[Ich würde die dortigen Schleime und Schlamme gern testen. Wenn es stimmt, dass sie nicht verderbt sind, dann würde ich gern wissen, wie sich solche Kreaturen entwickelt haben.]], },
    { [[Sammelt im Un'Goro Proben von verschiedenen Schleimen. Verwendet dann die Testausrüstung neben Chemiker Brenner in Unterstadt, um zu sehen, welche Eurer Proben am reinsten sind. Bringt 5 der reinen Proben aus dem Un'Goro, die Ihr findet, zu Chemiker Brenner.]], },
  },
  [4295] = {
    [[Rocknots Bier]],
  },
  [4296] = {
    [[Schrifttafel der Sieben]],
    { [[Die Legenden der Sieben führten mich in die Brennende Steppe. Sie waren hoch gebildete und mächtige Zwerge, und wenn man über Wissen und Macht verfügt, so ist das der Schlüssel zum Reichtum.]], [[Und genau das ist es, was ich will. Reichtum!]], [[Wenn wir mehr über die Sieben in Erfahrung bringen könnten, würde das uns beide vielleicht reich machen. In den Ruinen von Thaurissan soll sich angeblich die Schrifttafel der Sieben befinden, auf der das Wissen über die alten Zwerge geschrieben steht. Sucht diese Schrifttafel, kopiert den Text und bringt ihn mir. Lasst uns deren Geheimnisse gemeinsam entschlüsseln!]], },
    { [[Beschafft Euch die Abschrift der Schrifttafel.]], },
  },
  [4297] = {
    [[Essen für das Baby]],
    { [[Grimmlingflitzer sind Allesfresser - das bedeutet, dass sie sowohl Fleisch als auch Grünzeug fressen. Wenn dieses Junge hier schlüpft, sollten wir vorbereitet sein. Es ist kaum zu glauben, aber diese kleinen Kerlchen ernährt man am besten mit Fleischstücken, roh und saftig. Ich habe nie herausgefunden, warum das so ist, aber es muss wohl etwas mit Elunes Plan für die Elfen zu tun haben... vielleicht waren sie als Beschützer oder so etwas vorgesehen, ich weiß es nicht.]], [[Ich kümmere mich vorerst um das Ei. Warum besorgt Ihr nicht ein paar Silbermähnenpirscherflanken, mit denen wir das Junge füttern können, sobald es geschlüpft ist?]], },
    { [[Bringt Agnar Tiermeister im Hinterland 5 Silbermähnenpirscherflanken.]], },
  },
  [4298] = {
    [[Elternfreuden]],
    { [[He, es war wohl schon weiter, als ich dachte...]], },
    { [[Sprecht mit Agnar Tiermeister im Hinterland.]], },
  },
  [4300] = {
    [[Knochenklingenwaffen]],
    { [[Hmm, jetzt, wo ich darüber nachdenke, könnte es doch etwas geben, das Ihr für die Hand erledigen könntet. Ja, Jes'rimon könnte bestimmt Eure Hilfe brauchen.]], [[Im Krater von Un'Goro gibt es Raptoren mit tödlichen Klauen. Manche dieser Raptoren haben weiße Klauen statt schwarze wie der Tod. Die Mitglieder der Hand haben...Kunden..., denen aus diesen Klauen gefertigte Waffen gefallen. Bringt mir reichlich von diesen weißen Klauen, dann werde ich Euch gut entlohnen. Wenn Ihr das schnell genug tut, dann kann ich Euch vielleicht noch auf andere Weise helfen.]], },
    { [[Bringt Jes'rimon in Orgrimmar 8 weiße Ravasaurusklauen.]], },
  },
  [4301] = {
    [[Der mächtige U'cha]],
    { [[Während Ihr Un'Goro erkundet habt, sind mir ein paar andere Dinge aufgefallen, die einem mächtigen Jäger wie Euch sicher gefallen würden. Zum Beispiel gibt es da einen gewaltigen Affen unter den Gorillas im Norden. Ihr seid schon einmal in dieser Höhle gewesen, aber dieses Mal frage ich mich doch, ob Ihr die Begegnung mit ihm wohl überleben würdet, solltet Ihr meine Aufgabe annehmen.]], [[Ich nenne ihn U'cha und er ist einer der größten Söhne der Erdenmutter, da könnt Ihr sicher sein. Besiegt U'cha und bringt mir seinen Pelz. Wenn Euch das gelingt, habt Ihr das Recht darauf, ein großer Jäger genannt zu werden.]], },
    { [[Bringt U'chas Pelz zu Torwa Pfadfinder am Eingang des Kraters von Un'Goro.]], },
  },
  [4321] = {
    [[Die Sache verstehen]],
    { [[Die Information über die Pylonen, die Ihr mir gabt, ist sehr interessant...]], [[Gebt mir einen Moment Zeit, um ein paar Berechnungen anzustellen, <name>. Sprecht mich demnächst noch einmal an, dann sollte ich Euch die Antwort geben können, nach der wir gesucht haben.]], },
    { [[Sprecht demnächst mit J. D. Collie.]], },
  },
  [4322] = {
    [[Gefängnisausbruch!]],
    { [[Wir müssen hier weg, <name>. Helft mir, meine Ausrüstung aus dem Vorratsbereich zu holen und meine Freunde zu befreien. Falls wir hier rauskommen, dann müsst Ihr Euch weiter nach Morgans Wacht durchschlagen und mit Marschall Maxwell sprechen. Er wird Euch weitere Anweisungen geben.]], },
    { [[Helft Marschall Windsor, seine Ausrüstung zurückzuholen und seine Freunde zu befreien. Kehrt zu Marschall Maxwell zurück, wenn Ihr Erfolg hattet.]], },
  },
  [4324] = {
    [[Yuka Schraubstutz]],
    { [[Mein Bruder Ribbly war sein ganzes Leben lang zu nichts nütze; aber jetzt, glaube ich, hat er endlich einen gewissen Wert! Aber... wir werden Eure Hilfe brauchen, um aus Ribblys Wert Kapital zu schlagen.]], [[Meine Schwester Yuka hat ihn wochenlang gesucht und endlich in der Brennenden Steppe gefunden. Sie hat mich wissen lassen, dass sie ein paar Helfer brauchen könnte.]], [[Also, was meint Ihr? Wollt Ihr uns helfen? Falls ja, sprecht mit meiner Schwester. Ihr findet sie im Flammenkamm versteckt, in einer Höhle nordwestlich des Schreckensfelsens.]], },
    { [[Sprecht mit Yuka Schraubstutz in der Brennenden Steppe.]], },
  },
  [4341] = {
    [[Kharan Hammermacht]],
    { [[Seid Ihr in den rauchenden Ruinen von Thaurissan gewesen? Seid Ihr dort zufällig einem erbärmlichen Tölpel namens Kharan Hammermacht begegnet? Dem Zwerg, der eigentlich über mein kleines Mädchen wachen sollte?]], [[<Der König schluchzt.>]], [[Und jetzt ist sie verschwunden, <name>!]], [[Ich habe meine Leute die gesamte Steppe durchkämmen lassen. Doch der einzige Hinweis, den sie über den Verbleib meiner Tochter finden konnten, war, dass sie vielleicht in den Tiefen ist. Möglicherweise ist sie sogar schon tot!]], [[Angeblich wird Kharan dort als Gefangener festgehalten...findet ihn! Ich will Antworten!]], },
    { [[Begebt Euch in die Schwarzfelstiefen und findet Kharan Hammermacht.]], [[Der König erwähnte, dass Kharan dort gefangen sei - vielleicht solltet Ihr nach einem Gefängnis Ausschau halten.]], },
  },
  [4342] = {
    [[Kharans Geschichte]],
    { [[Lasst Kharan Hammermacht seine Geschichte erzählen.]], },
    { [[Lasst Kharan Hammermacht seine Geschichte erzählen.]], },
  },
