  [4361] = {
    [[Der Überbringer schlechter Botschaften...]],
    { [[Kehrt nach Eisenschmiede zurück und überbringt König Magni Bronzebart die schlechten Nachrichten.]], },
    { [[Kehrt nach Eisenschmiede zurück und überbringt König Magni Bronzebart die schlechten Nachrichten.]], },
  },
  [4362] = {
    [[Das Schicksal des Königreichs]],
    { [[Es scheint, als habe mein alter Widersacher, Dagran Thaurissan, mich und das ganze Königreich Eisenschmiede in seiner Gewalt.]], [[Vielleicht seid Ihr meine letzte Hoffnung, <name>. Ihr müsst meine Tochter Moira retten!]], [[Es gibt nur einen Weg, wie der Zauber, mit dem Thaurissan Moira belegt hat, wirklich zuverlässig gebrochen werden kann: Ihr müsst ihn töten.]], [[Aber, <name>, meiner Tochter dürft Ihr kein Leid zufügen! Vergesst nicht, dass sie Thaurissans Kontrolle unterliegt! Alles, was sie vielleicht sagt oder tut, geschieht aufgrund von Thaurissans bösem Zauber!]], },
    { [[Kehrt in die Schwarzfelstiefen zurück und rettet Prinzessin Moira Bronzebart aus den Fängen des bösen Imperators Dagran Thaurissan.]], },
  },
  [4363] = {
    [[Die Überraschung der Prinzessin]],
    { [[Thaurissan war ein großer und ehrenhafter Zwerg. Ihr habt mein Leben zerstört - meines und das meines ungeborenen Kindes!]], [[<Ihr habt den Eindruck, als leide Prinzessin Bronzebart noch immer unter den letzten Auswirkungen des vom Imperator gewirkten Zaubers.>]], [[Kehrt nach Eisenschmiede zurück und sagt meinem Vater, dass der Erbe des Königreichs Eisenschmiede ein Dunkeleisenzwerg sein wird.]], [[Ob er dies billigt oder nicht, so wird es sein.]], },
    { [[Kehrt nach Eisenschmiede zurück und sprecht mit König Magni Bronzebart.]], },
  },
