  [4402] = {
    [[Galgars Kaktusapfel Surprise]],
    { [[Hier im Tal der Prüfungen wird es ganz schön heiß.]], [[<Galgar wischt sich den Schweiß von der Stirn.>]], [[Wenn ich doch nur ein paar Kaktusäpfel hätte, dann könnte ich meinen berühmten Kaktusapfel Surprise machen! Nichts kühlt einen schneller ab als ein Stück dieser leckeren Spezialität.]], [[Ich sag Euch was, <name>. Wenn Ihr mir 10 Kaktusäpfel bringt, dann mache ich Euch ein paar Portionen Kaktusapfel Surprise, die Ihr mit auf Eure Abenteuer nehmen könnt. Falls Ihr daran interessiert seid, findet Ihr Kaktusäpfel bei den Kaktuspflanzen, die hier in der Gegend wachsen.]], },
    { [[Bringt Galgar 10 Kaktusäpfel. Ihr erinnert Euch doch, dass er gesagt hat, man fände sie in der Nähe von Kakteen?]], },
  },
  [4403] = {
