  [4485] = {
    [[Der Foliant des Adels]],
    { [[Hallo, <name>. Wie ich sehe, ist Eure Hingabe weiterhin stark, ebenso wie Euer Glaube.]], [[Ich denke, es ist an der Zeit, dass Ihr mit Duthorian Rall in Sturmwind sprecht. Es gibt noch einige Lektionen, die Ihr lernen müsst, und ich spüre, dass Ihr dazu bereit seid.]], [[Sucht ihn in der Kathedrale des Lichts auf... und, <name>, nehmt diese Lektionen ernst! Ihr habt Euren derzeitigen Rang durch harte Arbeit und Demut erreicht, enttäuscht uns jetzt nicht, indem Ihr überheblich werdet.]], [[Gehabt Euch wohl.]], },
    { [[Sprecht mit Duthorian Rall in Sturmwind.]], },
  },
  [4486] = {
    [[Der Foliant des Adels]],
    { [[Nee, nee, nee. Jetzt sieh sich nur mal einer den mächtigen Paladin in all seinem Glanz an.]], [[Tut mir leid, <name>, war nicht böse gemeint... Ich werde nur manchmal ein bisschen neidisch, wenn ich sehe, was Ihr für tolle Taten vollbracht habt. Tiza hat gesagt, das sei meine Schwäche, und ich habe ihr geschworen, dass ich daran arbeiten würde. Aber manchmal ist das einfach schwer, wisst Ihr.]], [[Aber der Grund, warum ich mit Euch sprechen wollte, war, dass Duthorian Rall in Sturmwind gesagt hat, Ihr solltet ihn in der Kathedrale des Lichts treffen, wenn Ihr bereit seid. Klang ziemlich wichtig. Klang, als sei irgendwas los.]], },
    { [[Sprecht mit Duthorian Rall in Sturmwind.]], },
  },
  [4487] = {
    [[Teufelsross beschwören]],
    { [[Ah, gut, da seid Ihr ja. Ich habe Euch <einen Sukkubus/eine Höllenbestie> hinterhergeschickt, um Eure Aufmerksamkeit zu erregen, aber es scheint, als sei es Euch gelungen herzukommen, ehe <er/sie> Euch gefunden hat. Einerlei.]], [[In letzter Zeit gab es einiges an Gerede über Eure wachsende Macht - das ist gut für Euch und möglicherweise schlecht für andere. Doch das sind alles Nebensächlichkeiten. Wichtig ist nur, dass ich denke, dass Ihr Euch bewiesen habt und deswegen... hihi... einen gewissen Segen verdient.]], [[Oberhalb von Ratschet im Brachland findet Ihr einen Mann namens Strahad. Ich glaube, Eure Wege haben sich bereits gekreuzt.]], },
    { [[Sprecht mit Strahad Farsan in Ratschet.]], },
  },
  [4488] = {
    [[Teufelsross beschwören]],
    { [[Ah, <name>, wie ich sehe, beehrt Ihr uns mit Eurer Anwesenheit. Wie wunderbar!]], [[Ich hoffe, Ihr erwartet nicht auch noch, dass wir Euch von Kopf bis Fuß bedienen. Ihr mögt ja vielleicht schnell an Macht gewinnen, doch Ihr seid noch immer ein Kleinkind, was das wahre Wissen und das wahre Verständnis angeht. Wenn Ihr nicht aufpasst, wird die Verderbnis Euch und Euren schwachen Willen überwältigen.]], [[Aber ich sollte zum Thema kommen: Strahad möchte mit Euch sprechen. Er wohnt noch immer im Brachland in seinem Turm, der Ratschet überblickt. Ich schlage vor, Ihr säumt nicht zu lange.]], },
    { [[Sprecht mit Strahad Farsan in Ratschet.]], },
  },
  [4489] = {
    [[Teufelsross beschwören]],
    { [[Ihr da, <race>! Kommt her!]], [[Ihr werdet <name> gerufen, nicht wahr?]], [[Das habe ich mir gedacht. Ich fasse mich kurz: Ihr gewinnt an Macht und das ist nicht unbeobachtet geblieben. Und nun ist die Zeit gekommen, dass Ihr für Eure Anstrengungen belohnt werdet.]], [[Begebt Euch ins Brachland. Oberhalb von Ratschet findet Ihr dort Strahad Farsan - vielleicht erinnert Ihr Euch an ihn? Er möchte mit Euch sprechen. Ich würde nicht säumen.]], },
    { [[Sprecht mit Strahad Farsan in Ratschet.]], },
  },
  [4490] = {
    [[Teufelsross beschwören]],
    { [[Ich möchte Euch eine Belohnung für Eure Geduld und Eure harte Arbeit geben. Doch wisset, dass sich die Fähigkeit, ein Teufelsross zu beschwören, in Zukunft wesentlich schwieriger gestalten wird. Ihr müsst sie Euch verdienen - und es wird nicht leicht sein.]], [[Doch geht nun fürs Erste, nehmt die Macht mit Euch, eine solche Kreatur zu beschwören, und verwendet diese Macht, wie Ihr es für richtig haltet.]], },
    { [[Sprecht in Ratschet mit Strahad Farsan, um zu lernen, wie man ein Teufelsross beschwört.]], },
  },
  [4491] = {
    [[Etwas Hilfe von guten Freunden]],
    { [[Argh... es ist so heiß...]], [[Ich war gerade dabei, die Gegend zu erkunden, als die Hitze plötzlich unerträglich wurde. Ich muss wohl einen Hitzschlag haben.]], [[Bitte, bringt mich zu meiner Freundin Spraggel in Marschalls Zuflucht zurück. Ich glaube, es geht mir noch gut genug, um Euch dorthin zu folgen.]], [[Aber ich fühle mich ziemlich schwach. Wenn ich also noch mal in Ohnmacht fallen sollte, dann bespritzt mich einfach mit etwas Wasser aus Spraggles Feldflasche. Das sollte helfen, hoffe ich...]], },
    { [[Führt Ringo zu Spraggel Frock in Marschalls Zuflucht.]], [[Falls Ringo ohnmächtig werden sollte, verwendet Spraggels Feldflasche, um ihn zu beleben.]], },
  },
  [4492] = {
    [[Verloren!]],
    { [[Ihr müsst mir helfen, <name>! Mein Freund Ringo ist vor einer Weile aufgebrochen, um den Vulkan südlich von hier zu erforschen, und er ist schon viel zu lange weg. Ich mache mir große Sorgen um ihn.]], [[Meint Ihr, Ihr könnt ihn suchen? Nehmt doch meine Feldflasche mit - wenn Ihr Ringo tatsächlich findet, dann hat er sie bestimmt nötig!]], },
    { [[Sucht Ringo am Feuersäulengrat.]], },
  },
  [4493] = {
    [[Der Marsch der Silithiden]],
    { [[Es scheint, als sei es die Absicht der Silithiden – nein, ihr ganzer Lebenszweck! –, alles zu verschlingen, was ihnen im Weg steht und was nicht zu ihrer eigenen Ökologie gehört. Nur darauf konzentrieren sie sich und darum stellen sie eine große Gefahr für alles Leben auf Azeroth dar.]], [[Bis jetzt haben wir nur die Anfänge gesehen. Ich vermute, dass sie ihre Kraft aus einem Ort tiefer drinnen in Kalimdor beziehen: aus dem Krater von Un'Goro. Geht nach Gadgetzan in der Tanariswüste und sprecht mit Alchemist Stößelbruch. Erzählt ihm von meiner Theorie; er wird uns bestimmt dabei helfen, die Silithiden zu bekämpfen.]], },
    { [[Sprecht in Gadgetzan mit Alchemist Stößelbruch.]], },
  },
  [4494] = {
    [[Der Marsch der Silithiden]],
    { [[Es scheint, als sei es die Absicht der Silithiden – nein, ihr ganzer Lebenszweck! –, alles zu verschlingen, was ihnen im Weg steht und was nicht zu ihrer eigenen Ökologie gehört. Nur darauf konzentrieren sie sich und darum stellen sie eine große Gefahr für alles Leben auf Azeroth dar.]], [[Bis jetzt haben wir nur die Anfänge gesehen. Ich vermute, dass sie ihre Kraft aus einem Ort tiefer drinnen in Kalimdor beziehen: aus dem Krater von Un'Goro. Geht nach Gadgetzan in der Tanariswüste und sprecht mit Alchemist Stößelbruch. Erzählt ihm von meiner Theorie; er wird uns bestimmt dabei helfen, die Silithiden zu bekämpfen.]], },
    { [[Sprecht in Gadgetzan mit Alchemist Stößelbruch.]], },
  },
  [4495] = {
    [[Ein guter Freund]],
    { [[Ich habe einen Freund, Iverron, der mich normalerweise jeden Tag um dieselbe Zeit besucht. Komischerweise ist er heute noch nicht da gewesen - genau genommen ist er sogar schon etliche Stunden über seiner sonstigen Zeit.]], [[Ich muss zugeben, ich bin etwas besorgt, <name>. Iverron verbringt viel Zeit bei der Spinnenhöhle oben im Norden und ich bin sicher, Ihr wisst, wie gefährlich es dort ist - überall Spinnen!]], [[Falls Ihr zufällig dort vorbeikommen solltet, könntet Ihr dann nach ihm Ausschau halten?]], },
    { [[Sucht Iverron an der Höhle im Norden.]], },
  },
  [4496] = {
    [[Dschungelei]],
    { [[Meine Forschungen haben ergeben, dass die Silithiden den Bienen ähneln: Wenn man die Königin des Schwarmbaus tötet, dann sollte das den Rest in völliges Chaos stürzen. Ich werde einen Köder brauen, mit dem wir die Königin herauslocken können, und sobald sie draußen ist, streckt Ihr sie nieder.]], [[Doch zuerst brauchen wir einige Reagenzien; wir wollen doch nicht, dass Ihr völlig unvorbereitet im Dschungel umherstolpert. Geht nach Westen, sucht den Schwarmbau der Silithiden im Krater von Un'Goro und beschafft Euch eine Duftdrüse von einem der Viecher. Außerdem brauche ich einige Erdproben aus Un'Goro.]], },
    { [[Bringt eine Gorishiduftdrüse und 5 Erdproben von Un'Goro zu Alchemist Stößelbruch in Gadgetzan.]], },
  },
  [4501] = {
    [[Vorsicht, Pterrordax!]],
    { [[Tötet 10 rasende Pterrordax und sprecht dann mit Spraggel Frock in Marschalls Zuflucht.]], },
    { [[Tötet 10 rasende Pterrordax und sprecht dann mit Spraggel Frock in Marschalls Zuflucht.]], },
  },
  [4502] = {
    [[Vulkanische Aktivität]],
    { [[Als Lehrling von Marvon Nietensucher habe ich unglaublich viel gelernt. Aber er ist jetzt schon sehr lange fort. Daher denke ich, werde ich an ein paar eigenen Experimenten arbeiten.]], [[Ich habe kürzlich etwas über den Krater von Un'Goro gelesen und über die eigentümliche Vulkanaktivität, die in der Mitte des Kraters zu erkennen ist. Ich glaube, ich könnte eine Menge lernen, wenn ich nur etwas Asche von den Elementaren dort haben könnte.]], [[Würde es Euch etwas ausmachen, etwas Asche für mich einzusammeln, falls Ihr dort entlangkommen solltet?]], },
    { [[Sammelt 9 Proben der Asche von Un'Goro von den Feuerelementaren am Vulkan im Krater von Un'Goro ein und bringt die Proben zu Liv Ritzelflick in Ratschet.]], },
  },
  [4503] = {
    [[Sappalots Flieger]],
    { [[Ich baue einen Flugapparat!]], [[Ich bin hier gestrandet und habe leider absolut überhaupt keinen Orientierungssinn. Ich würde ja einen Greifen oder Windreiter zurück nach Gadgetzan nehmen, aber diese Viecher jagen mir einfach eine höllische Angst ein.]], [[Also, werdet Ihr mir helfen? Ich muss nur noch die Flügel bauen und ich glaube, dass sich die eingesponnenen Schuppen von Pterrordax und Dimetrodonten perfekt dafür eignen würden. Ich werde mich zwar garantiert nicht mit denen anlegen, aber Ihr seht ziemlich zäh aus. Wollt Ihr es nicht versuchen?]], },
    { [[Sammelt 8 eingesponnene Dimetrodonschuppen und 8 eingesponnene Pterrordaxschuppen für Sappalot in Marschalls Zuflucht.]], },
  },
  [4504] = {
    [[Superkleber]],
    { [[Ich bin immer auf der Suche nach neuen Methoden, um schnelles Gold zu verdienen, <name>. Wenn Ihr mir bei meiner neuesten Idee helft, dann gebe ich Euch ein bisschen was vom Gewinn ab. Na, was sagt Ihr dazu?]], [[Das hab ich mir gedacht!]], [[Geht zu den Teergruben im Norden des Kraters von Un'Goro und beschafft mir etwas Teer. Aber nicht jeden x-beliebigen Teer, versteht Ihr? Ihr müsst den von den Kreaturen nehmen, die an den Teergruben leben. Normaler Teer ist einfach nicht klebrig genug.]], [[Die pflanzenartigen Kreaturen jedoch haben Chlorophyll in ihrer Haut, und wenn man das mit dem Teer mischt, dann wird das superklebrig!]], },
    { [[Sammelt 12 Proben superklebrigen Teer für Tran'rek in Gadgetzan.]], },
  },
  [4505] = {
    [[Brunnen der Verderbnis]],
    { [[Manche Leute sagen vielleicht, ich sei außergewöhnlich grausam... Aber in Wirklichkeit bin ich doch nur von dem Wunsch beseelt zu sehen, wie die Horde einen Vorteil gegenüber der Allianz erringt, und zwar auf jede nur mögliche Weise.]], [[Seit Kurzem interessiere ich mich für einen Mondbrunnen in den Ruinen von Constellas. Er soll südlich von hier liegen und von den Jadefeuersatyrn gepflegt werden. Der Mondbrunnen, der einst ein Symbol der Erneuerung für die unerträglichen Nachtelfen war, ist jetzt verderbt und wird dazu benutzt, neue Satyrn hervorzubringen.]], [[Ich habe da eine Idee, <name>, und ich hätte gern eine Probe von dem Wasser... für meinen kleinen Katzenfreund hier.]], },
    { [[Beschafft eine Probe verderbten Wassers vom Mondbrunnen der Jadefeuersatyrn und bringt sie zu Winna Hazzard am Blutgiftposten.]], },
  },
  [4506] = {
    [[Verderbte Säbler]],
    { [[Dieses verderbte Wasser können wir zu unserem Vorteil verwenden, <name>. Mit nur einer kleinen Menge Wasser wurde mein Kätzchen doppelt so groß und scheint jetzt irgendwie... stärker als vorher zu sein. Solche Katzen könnten sich noch als nützlich erweisen...]], [[Hier, <name>. Nehmt diesen Katzentransportkorb und lasst das Kätzchen darin neben dem Mondbrunnen in den Ruinen von Constellas frei. Hi, hi... Ich bin mir sicher, das Ergebnis wird Euch gefallen!]], [[Bringt mir danach die Katze zurück, dann werde ich mich für Eure Zeit und Mühe erkenntlich zeigen.]], },
    { [[Bringt Winnas Kätzchen zum verderbten Mondbrunnen, lasst es dort frei und bringt es dann wieder zu Winna zurück.]], [[Sobald Ihr wieder bei Winna seid, klickt auf die Katze, um sie Winna zu übergeben.]], },
  },
  [4507] = {
    [[Bauer schlägt Dame]],
    { [[Hier ist der Köder. Im Schwarmbau sollte sich ein Kristall befinden. Wir glauben, dass die Silithiden den Kristall als eine Art Einstellungsgerät verwenden, das den ganzen Schwarmbau verbindet. Wo sich dieser Kristall befinden könnte, wollt Ihr wissen? Ich würde vorschlagen, Ihr versucht es in der Brutstätte innerhalb des Schwarmbaus.]], [[Wenn Ihr den Köder bei dem Kristall benutzt, sollte das die Königin aufscheuchen, aber geht acht – sie ist wahrscheinlich von mehreren Wachen umgeben. Schaltet die Königin aus und entfernt ihr Gehirn. Ich weiß, das klingt eklig, aber wir müssen es unbedingt untersuchen. Viel Glück, <name>!]], },
    { [[Besiegt die Schwarmkönigin der Gorishi, sobald sie erscheint.]], [[Bringt das Gehirn der Schwarmkönigin zu Alchemist Stößelbruch nach Gadgetzan.]], },
  },
  [4508] = {
    [[Die Ruhe vor dem Sturm]],
    { [[Meine jüngsten Ergebnisse und das Wissen, das wir heute hier gesammelt haben, müssen in die Hände von Leuten gelangen, die etwas damit anfangen können. So einfallsreich wir hier in Gadgetzan auch sein mögen, das hier können wir nicht allein schaffen.]], [[Bringt meinen jüngsten Bericht zu Gracina Glaubensmacht nach Darnassus; sie hält sich doch immer noch im Tempel des Mondes auf, oder? Sie hat Freunde, und zwar ziemlich hohe Tiere innerhalb der Allianz; wenn wir die in diese Sache mit hineinziehen können, <name>, dann haben wir vielleicht eine Chance, dies alles zu überleben.]], },
    { [[Bringt Stößelbruchs Bericht von Un'Goro zu Gracina Glaubensmacht in Darnassus.]], },
  },
  [4509] = {
    [[Die Ruhe vor dem Sturm]],
    { [[Meine jüngsten Ergebnisse und das Wissen, das wir heute hier gesammelt haben, müssen in die Hände von Leuten gelangen, die etwas damit anfangen können. So einfallsreich wir hier in Gadgetzan auch sein mögen, das hier können wir nicht allein schaffen.]], [[Bringt meinen jüngsten Bericht zu Zilzibin Tomtom nach Orgrimmar; er lebt doch immer noch im oberen Teil der Gasse, oder? Er hat Freunde ziemlich weit oben in der Horde; wenn wir die mit in diese Sache hineinziehen können, <name>, dann haben wir vielleicht eine Chance, dies alles zu überleben.]], },
    { [[Bringt Stößelbruchs Bericht von Un'Goro zu Zilzibin Tomtom in Orgrimmar.]], },
  },
  [4510] = {
    [[Die Ruhe vor dem Sturm]],
    { [[<name>, Eure Hilfe war wirklich unschätzbar wertvoll. Es wird zweifellos der Moment kommen, an dem ich erneut um Euren Beistand im Kampf gegen die Silithiden bitten muss. Wir müssen erst verarbeiten, was wir im Krater von Un'Goro gelernt haben - ich fürchte, dass meine ursprüngliche Annahme, die Silithiden kämen aus dem Un'Goro, falsch war... dass an einem anderen Ort tatsächlich eine wesentlich stärkere Präsenz existiert.]], [[Bringt diese Nachricht zu Idriana in der Bank. Sie wird Euch etwas aus meinem Gewölbe geben, das Ihr als Zeichen meiner Dankbarkeit ansehen sollt. Ich danke Euch für Eure mutige Unterstützung, <class>.]], },
    { [[Bringt den Bankgutschein zu Idriana in der Bank von Darnassus.]], },
  },
  [4511] = {
    [[Die Ruhe vor dem Sturm]],
    { [[<name>, ohne Euch wären wir niemals so weit gekommen. Es wird zweifellos der Moment kommen, an dem ich erneut um Eure Hilfe im Kampf gegen die Silithiden bitten muss. Wir müssen erst verarbeiten, was wir im Krater von Un'Goro gelernt haben - ich fürchte, dass meine ursprüngliche Annahme, die Silithiden kämen aus dem Un'Goro, falsch war... dass an einem anderen Ort tatsächlich eine wesentlich stärkere Präsenz existiert.]], [[Bringt diese Nachricht zu Karus in der Bank. Er wird Euch etwas aus meinem Gewölbe geben, das Ihr als Zeichen meiner Dankbarkeit ansehen sollt. Danke für Eure tatkräftige Unterstützung, <class>.]], },
    { [[Bringt den Bankgutschein zu Karus in der Bank von Orgrimmar.]], },
  },
  [4512] = {
    [[Schlamm auf Abwegen]],
    { [[Aber womit ich mich am meisten beschäftigt habe, das sind Schlamme. Ich weiß, das klingt seltsam, aber die sind wirklich überall!]], [[Vor Kurzem habe ich einer Dame in Menethil dabei geholfen, ihre Tasche wiederzufinden - ein Schlamm hatte sie gefressen. Ich habe auch gesehen, wie Mitglieder der Königlichen Apothekervereinigung aus Unterstadt Proben von dem Zeug sammelten. Ich möchte wissen, wieso! Wo kommt es her?]], [[Ich habe auch gehört, dass es im Teufelswald noch mehr davon geben soll. Ich brauche einige Proben von den Schlammen dort, damit ich meine Forschungen weiter vorantreiben kann. Ich gebe Euch einige Behälter mit; verwendet diese, um eine Probe von den toten Kreaturen einzusammeln.]], },
    { [[Bringt 6 mit verfluchtem Schlamm gefüllte Gläser und 6 mit besudeltem Schlamm gefüllte Gläser zu Laris Bummelritzel in Eisenschmiede.]], },
  },
  [4513] = {
    [[Schlamm auf Abwegen]],
    { [[Ich brauche noch mehr Proben, <name>. Aber - und ich weiß nicht so recht, wie ich das ausdrücken soll - es müssen reine Proben sein. Ich weiß, ich weiß. "Was meint Ihr mit 'rein', Laris?", fragt Ihr. Ich kann es nicht genau sagen. Ich weiß, dass die hier verderbt sind... genau wie im Teufelswald. Und genau deswegen ist meine Erkenntnis so wichtig. Die Schlamme scheinen sich anzupassen... die Eigenschaften der Gegend anzunehmen, in der sie sich befinden. Ich muss Proben finden, die nicht verunreinigt sind. Aber wo? Wo?!]], [[Pah! <name>, nehmt diese Behälter einfach mit, nur für den Fall, dass Ihr irgendwo welche finden solltet.]], },
    { [[Bringt 10 gefüllte reine Probengläser zu Laris Bummelritzel in Eisenschmiede.]], [[Falls Ihr Probleme habt, fündig zu werden, dann kann Laris Euch vielleicht noch den einen oder anderen Hinweis geben.]], },
  },
  [4521] = {
    [[Wilde Wächter]],
    { [[Winterquell liegt gleich hinter der Holzschlundfeste, im Nordosten des Teufelswalds. Seid Ihr schon mal dort gewesen?]], [[Ich habe gehört, dass es in jener Region nur so von Kreaturen wimmeln soll, die Wildekin genannt werden. Diese Bestien sind dafür bekannt, dass sie Geheimnisse bewachen, und zwar Geheimnisse der Nachtelfen.]], [[Wir müssen herausfinden, was in Winterquell verborgen liegt.]], [[Ich habe gehört, die struppigen Eulenbestien findet man südlich von der Straße, und außerdem soll es noch so genannte tobende Eulenbestien geben, die im Zentrum von Winterquell zu finden sind. Räumt sie aus dem Weg und verschafft Euch so Zutritt zu dem Gebiet.]], },
    { [[Trull Scheiterbann im Teufelswald möchte, dass Ihr 15 tobende Eulenbestien und 15 struppige Eulenbestien tötet.]], },
  },
  [4542] = {
    [[Nachricht zum Freiwindposten]],
    { [[Bringt Klippenbehüter Langhorn am Freiwindposten die dringende Nachricht.]], },
    { [[Bringt Klippenbehüter Langhorn am Freiwindposten die dringende Nachricht.]], },
  },
  [4561] = {
    [[Test auf Unreinheiten - Krater von Un'Goro]],
  },
  [4581] = {
    [[Kayneth Stillwind]],
    { [[Der Druide Kayneth Stillwind hat sich bei mir gemeldet. Er hätte gern einen Bericht über das, was an unserer Küste und in den Wäldern um Astranaar vorgeht. Er befürchtet, dass eine Verderbnis durch das Eschental zieht... und vielleicht hat er Recht damit.]], [[Hier, bringt ihm meinen Bericht. Ihr werdet ihn im Schrein von Waldeslied finden, im Nachtweisenwald im Osten.]], },
    { [[Bringt Shindrells Notiz zu Kayneth Stillwind in Waldeslied.]], },
  },
  [4601] = {
    [[Der Funkelmat 5200!]],
    { [[Dieser Apparat ist etwas ganz Besonderes unter den Gnomen, er bietet dem Wahnsinn außerhalb dieses Raums tapfer die Stirn. Eine kleine Plakette auf dem Gerät besagt: "Der Funkelmat 5200", gefolgt von einem kurzen Text:]], [["Schmutz und Verkrustungen ruinieren Euer Funkelzeug? Der Funkelmat 5200 definiert das Wort Glanz neu! Gebt den Gegenstand hinein, den Ihr gesäubert haben wollt, und werft drei Silbermünzen in den Münzschlitz. Lasst den Funkelmat 5200 einen Moment arbeiten - und voilà! Eure Wertgegenstände werden sauber und glänzend herauskommen!"]], },
    { [[Gebt einen schmutzverkrusteten Gegenstand in den Funkelmat 5200 und stellt sicher, dass Ihr drei Silbermünzen habt, um die Maschine zu aktivieren.]], },
  },
  [4602] = {
    [[Der Funkelmat 5200!]],
    { [[Dieser Apparat ist etwas ganz Besonderes unter den Gnomen, er bietet dem Wahnsinn außerhalb dieses Raums tapfer die Stirn. Eine kleine Plakette auf dem Gerät besagt: "Der Funkelmat 5200", gefolgt von einem kurzen Text:]], [["Schmutz und Verkrustungen ruinieren Euer Funkelzeug? Der Funkelmat 5200 definiert das Wort Glanz neu! Gebt den Gegenstand hinein, den Ihr gesäubert haben wollt, und werft drei Silbermünzen in den Münzschlitz. Lasst den Funkelmat 5200 einen Moment arbeiten - und voilà! Eure Wertgegenstände werden sauber und glänzend herauskommen!"]], },
    { [[Gebt einen schmutzverkrusteten Gegenstand in den Funkelmat 5200 und stellt sicher, dass Ihr drei Silbermünzen habt, um die Maschine zu aktivieren.]], },
  },
