  [5441] = {
    [[Faule Peons]],
    { [[Verfluchte Peons! Sie arbeiten ja hart daran, Holz aus den Bäumen im Tal zu gewinnen, aber sie machen ständig Nickerchen! Ich brauche jemanden, der dabei hilft, die Peons auf Zack zu halten.]], [[Ihr seht wie geschaffen für meinen Auftrag aus, <race>. Hier, Ihr nehmt jetzt diesen Totschläger und lasst ihn auf allen faulen Peons tanzen, die Ihr bei der Arbeit schlafend vorfindet. Ein ordentlicher Schlag wird sie schnell wieder an die Arbeit bringen! Bringt mir den Totschläger wieder, wenn Ihr fertig seid.]], [[Verdammte faule Peons...]], },
    { [[Benutzt den Totschläger des Großknechts an faulen Peons, wenn sie schlafen. Weckt 5 Peons auf und bringt dann den Totschläger des Großknechts zu Großknecht Thazz'ril ins Tal der Prüfungen zurück.]], },
  },
  [5461] = {
    [[Der Mensch Ras Frostraunen]],
    { [[Die Untoten fürchten das Leben genauso sehr wie die Lebenden den Tod fürchten. Es ist nicht ungewöhnlich, dass ein denkender Untoter alle Überreste seines vorherigen Lebens vollständig zerstört, nachdem er zum Untoten wurde.]], [[Leider benötigen wir, um ein Artefakt zu erschaffen, das Ras Frostraunen wieder in einen Sterblichen zurückverwandeln kann, ein Andenken aus seinen Jahren unter den Lebenden.]], [[Ihr müsst Euch zur zerstörten Stadt von Stromgarde begeben, Frostraunens Heim, und nach solch einem Gegenstand suchen. Möge das Glück mit Euch sein, <name>.]], },
    { [[Reist in das Arathihochland zu den Ruinen von Stromgarde. Durchsucht Stromgarde nach einem Andenken der Erinnerung. Wenn Ihr einen solchen Gegenstand findet, kehrt mit ihm zu Magistrat Marduk zurück.]], },
  },
  [5462] = {
    [[Der Sterbende Ras Frostraunen]],
    { [[Das Andenken muss auf besudeltem Boden korrekt eingestellt werden.]], [[Man erzählt sich, dass Stratholme der Ort sei, an dem Frostraunen dem Lichkönig erlag. Auf diesem blutigen Boden gab er sein Leben - frei und mit einem Motiv -, um einer der Unsterbenden zu werden.]], [[Dieser Ort muss gefunden werden.]], [[Es gibt einen untoten Dissidenten der Geißel, der durch die Bansheekönigin befreit wurde, aber der Sache der Argentumdämmerung gegenüber loyal ist. Sucht ihn: Leonidas Bartholomäus der Geachtete. Er könnte mehr wissen. Sucht die Kapelle des Hoffnungsvollen Lichts auf, <name>.]], },
    { [[Reist zur Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern und sucht Leonidas Bartholomäus den Geachteten auf. Zeigt ihm das Andenken der Erinnerung und berichtet ihm alles, was Ihr entdeckt habt.]], },
  },
  [5463] = {
    [[Menethils Geschenk]],
    { [[Begebt Euch nach Stratholme und sucht Menethils Geschenk. Platziert das Andenken der Erinnerung auf dem unheiligen Boden.]], },
    { [[Begebt Euch nach Stratholme und sucht Menethils Geschenk. Platziert das Andenken der Erinnerung auf dem unheiligen Boden.]], },
  },
  [5464] = {
    [[Menethils Geschenk]],
    { [[Die Seele des Gefallenen hängt sich an das, was sein ehemaliges Leben repräsentiert, und verwandelt den Gegenstand in ein seelengebundenes Andenken. Ihr hebt den Gegenstand auf und verstaut ihn in Eurem Rucksack.]], },
    { [[Bringt das seelengebundene Andenken zu Leonidas Bartholomäus in den Östlichen Pestländern.]], },
  },
  [5465] = {
    [[Seelengebundenes Andenken]],
    { [[Kehrt mit dem Andenken zu Marduk zurück, <name>. Er wird Euch über die Anwendung instruieren.]], },
    { [[Kehrt zu Magistrat Marduk in Darrowehr zurück. Zeigt ihm das seelengebundene Andenken.]], },
  },
  [5466] = {
    [[Der Lich Ras Frostraunen]],
    { [[Es verbleibt noch eine Aufgabe, <name>. Tretet dem Lich Ras Frostraunen in seinem Unterschlupf gegenüber.]], [[Benutzt die Macht des Andenkens gegen die Bestie - seid unerbittlich in Euren Taten! Haltet den Schlägen stand, mit denen das um sich schlagende Biest Euch sicherlich eindecken wird. Gebt dem Schmerz nicht nach!]], [[Wenn das Ritual abgeschlossen ist, wird Ras wieder ein Sterblicher sein. An diesem Punkt muss er getötet werden. Schlagt ihn nieder und nehmt Euch seinen menschlichen Kopf.]], },
    { [[Sucht Ras Frostraunen in Scholomance. Wenn Ihr ihn gefunden habt, wendet das seelengebundene Andenken auf sein untotes Antlitz an. Solltet Ihr ihn erfolgreich in einen Sterblichen zurückverwandeln können, dann schlagt ihn nieder und nehmt den menschlichen Kopf von Ras Frostraunen an Euch. Bringt den Kopf zu Magistrat Marduk.]], },
  },
  [5481] = {
    [[Gordos Aufgabe]],
    { [[Sammelt 3 Düsterkräuter und bringt sie zu Jungapotheker Holland auf den Friedhof von Brill.]], },
    { [[Sammelt 3 Düsterkräuter und bringt sie zu Jungapotheker Holland auf den Friedhof von Brill.]], },
  },
  [5482] = {
    [[Verdammniskraut]],
    { [[Ihr seht also die Zwickmühle, in der ich mich befinde, <name>. Dieses Ding da draußen sammelt die falschen Kräuter! Ich werde Euch dafür belohnen, wenn Ihr sammelt, was ich wirklich benötige. Bringt mir genug Verdammniskraut und ich werde Euch eine angemessene Belohnung geben.]], [[Soweit mir bekannt ist, verseucht es gern die Flora in der Nähe des Massengrabs, nördlich des Friedhofes von Brill. Beeilt Euch und hütet Euch vor den Gnollen in dem Gebiet.]], },
    { [[Sammelt 10 Verdammniskräuter und bringt sie zu Jungapotheker Holland.]], },
  },
  [5501] = {
    [[Knochenjäger]],
    { [[Wir sitzen hier auf einer Goldmine, das sage ich Euch – einer Goldmine! Kodoknochen werden die Welt für immer verändern... Ich habe Kunden, die daraus Möbel, Musikinstrumente und sogar Sprengpulver herstellen.]], [[Eine Karawane kommt hier vorbei, die meine Kodoknochen mitnimmt und sie dann in Schattenflucht verkauft. Wenn Ihr zum Kodofriedhof südlich von hier gehen und mir zehn Kodoknochen bringen könntet, würde ich Euch dafür etwas anderes geben. Ist das ein Geschäft?]], },
    { [[Bringt Bibbly F'utzbuckel bei Kormeks Hütte 10 Kodoknochen vom Kodofriedhof.]], },
  },
  [5502] = {
    [[Tageseltern der Horde]],
    { [[Ihr wart die ganze Zeit so gut zu mir, <name>. Nie hätte ich erwartet, während der Kinderwoche so viel Spaß zu haben! Danke schön.]], [[Ich wünschte, all die anderen Waisenkinder hätten das Glück, die ganzen Sachen zu sehen, die wir gesehen haben. Meint Ihr, wir finden Blax Flaschenzünder wieder und bekommen einen Schaumstoffschwertständer von ihm? Ich glaube, er schiebt noch immer seinen Karren durch die Hauptstraße in der Gasse. Dann könnten alle im Waisenhaus auch ihren Spaß haben!]], [[Danach wird es aber Zeit, nach Hause zurückzukehren.]], },
    { [[Kauft einen Schaumstoffschwertständer bei Blax Flaschenzünder in Orgrimmars Gasse. Bringt anschließend Euren Schützling in das Waisenhaus von Orgrimmar zurück, indem Ihr die Pfeife des Orcwaisenkindes an Waisenmatrone Schlachterbe in der Gasse übergebt.]], },
  },
