  [5513] = {
    [[Mäntel der Dämmerung]],
    { [[Euer hervorragenden Dienste für die Argentumdämmerung haben sich herumgesprochen, <name>. Wir sind geehrt, Euch in unseren Reihen zu wissen. Ich wurde ermächtigt, Euch einige der wertvollsten Gegenstände der Dämmerung anzubieten - unsere Mäntel des Magiewiderstands.]], [[Wenn Ihr einen Mantel mit Eurer Schulterpanzerung verwendet, könnt Ihr euren Widerstand gegen die Kräfte der Magie auf eine von fünf mögliche Arten verbessern. Als Zeichen der Hingabe für unsere Sache, erbitte ich von Euch nicht weniger als zehn unserer Ehrenmarken, um Zugriff auf die Mäntel zu erhalten.]], },
    { [[Bringt 10 Ehrenmarken der Argentumdämmerung zu Rüstmeister Knackschloss an der Kapelle des Hoffnungsvollen Lichts, Östliche Pestländer.]], },
  },
  [5514] = {
    [[Sold reimt sich auf...]],
    { [[Der Goblin, mit dem Ihr sprechen müsst, ist Krinkle Goldstahl. Er geht mit seinen Fertigkeiten bei den höchsten Bietern in Gadgetzan, in der Tanariswüste, hausieren. Er dürfte der Einzige sein, der die Form herstellen kann, die wir benötigen, und seine Dienste werden nicht billig sein. Wir bitten Euch, tief in die Tasche zu greifen, indem Ihr die Gebühr zahlt, die er fordert.]], [[Ich bin Apotheker, kein Bankier. Wenn ich Gold herumliegen hätte, wäre ich sicher nicht hier.]], [[Ich schätze, ich hab meine Gedanken mal wieder laut ausgesprochen. Upps.]], [[Gadgetzan erwartet Euch! Viel Glück!]], },
    { [[Bringt die magieerfüllten Skelettfragmente sowie 15 Goldstücke zu Krinkle Goldstahl in Gadgetzan.]], },
  },
  [5515] = {
    [[Krastinovs Tasche der Schrecken]],
    { [[Ein großes Gewicht wurde von unseren Herzen genommen, <name>, aber Eure Aufgabe ist noch nicht vollendet. Krastinovs Meister, Kirtonos, lebt immer noch.]], [[Bevor Ihr aber Kirtonos gegenübertretet, müsst Ihr Euch zuerst eine Methode besorgen, mit der Ihr ihn beschwören könnt.]], [[Es heißt, dass Jandice Barov das Blut Unschuldiger von Krastinov zu Kirtonos bringt.]], [[Sucht sie in den versunkenen Katakomben der Scholomance und streckt sie nieder. Bringt alle Hinweise zurück, die Ihr findet.]], },
    { [[Sucht nach Jandice Barov in Scholomance und vernichtet sie. Entnehmt ihrer Leiche Krastinovs Tasche der Schrecken. Bringt die Tasche zu Eva Sarkhoff.]], },
  },
  [5517] = {
    [[Der Chromatische Mantel der Dämmerung]],
    { [[<Bruder / Schwester> <name> -, Eure Taten im Namen der Argentumdämmerung sind zu zahlreich um sie noch aufzuzählen. Als Anerkennung für Eure Taten, werde ich mich von einem unserer wertvollen chromatischen Mäntel der Dämmerung trennen - Er schützt den Träger gleichzeitig gegen alle Formen der Magie. Nur die Stärksten und Besten der Dämmerung haben ein Anrecht auf einen Chromatischen Mantel der Dämmerung!]], [[Bringt mir fünfundzwanzig Ehrenmarken als Zeichen Eurer Hingabe und Ihr erhaltet von mir den feinsten unserer Mäntel.]], },
    { [[Bringt 25 Ehrenmarken der Argentumdämmerung zu Rüstmeister Knackschloss an der Kapelle des Hoffnungsvollen Lichts, Östliche Pestländer.]], },
  },
  [5518] = {
    [[Der Ogeranzug der Gordok]],
    { [[Wenn Ihr versuchen wollt gegen den König dieser Bruchbude zu kämpfen - und ich denke, dass Ihr genau das vorhabt - dann müsst Ihr erstmal an Hauptmann Krombruch vorbei. Sicher könnt Ihr ihn einfach töten. Doch vielleicht wollt Ihr diesen Oger ja am Leben lassen.]], [[Es stellt sich nur die Frage, wie? Nun, ich bin hier schon eine ganze Weile und ich denke, dass ich Euch einen Ogeranzug anfertigen kann, der ausreicht, um diese Kerle zu täuschen. Das einzige Problem wird seine Stabilität sein - zehn Minuten sind das Maximum. Besorgt mir alles was ich brauche und Ihr bekommt einen Ogeranzug. Ich warte hier solange...]], },
    { [[Bringt 4 Runenstoffballen, 8 Stücke unverwüstliches Leder, 2 Runenfaden und etwas Ogergerbemittel zu Knot Zwingschraub. Momentan ist er im Gordokflügel von Düsterbruch angekettet.]], },
  },
  [5519] = {
    [[Der Ogeranzug der Gordok]],
  },
  [5521] = {
    [[Der Chromatische Mantel der Dämmerung]],
    { [[<Bruder / Schwester> <name> -, Eure Taten im Namen der Argentumdämmerung sind zu zahlreich um sie noch aufzuzählen. Als Anerkennung für Eure Taten, werde ich mich von einem unserer wertvollen chromatischen Mäntel der Dämmerung trennen - Er schützt den Träger gleichzeitig gegen alle Formen der Magie. Nur die Stärksten und Besten der Dämmerung haben ein Anrecht auf einen Chromatischen Mantel der Dämmerung!]], [[Bringt mir fünfundzwanzig Ehrenmarken als Zeichen Eurer Hingabe und Ihr erhaltet von mir den feinsten unserer Mäntel.]], },
    { [[Bringt 25 Ehrenmarken der Argentumdämmerung zu Rüstmeister Funkellicht im Zugwindlager in den Westlichen Pestländern.]], },
  },
  [5522] = {
    [[Leonidas Bartholomäus]],
    { [[Ich habe eine Botschaft von meinem Meister Vectus erhalten. Na ja, die Botschaft war nicht wirklich von Vectus... sie war von jemandem namens Leonidas Bartholomäus. Ich kenne Leonidas nicht, aber er kannte meinen Meister, und niemand kennt meinen Meister, außer er lässt es zu.]], [[Leonidas sagt, dass Vectus die Dracheneier will. Er möchte, dass Ihr sie ihm zur Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern bringt.]], [[Das tut weh! Warum Ihr? Vertraut Vectus mir nicht? Ach, egal... nehmt einfach die Eier und berichtet Leonidas, dass ich getan habe, was unser Meister befahl.]], },
    { [[Bringt die gefrorenen Eier zu Leonidas Bartholomäus in den Östlichen Pestländern.]], },
  },
  [5524] = {
    [[Der Chromatische Mantel der Dämmerung]],
    { [[<Bruder / Schwester> <name> -, Eure Taten im Namen der Argentumdämmerung sind zu zahlreich um sie noch aufzuzählen. Als Anerkennung für Eure Taten, werde ich mich von einem unserer wertvollen chromatischen Mäntel der Dämmerung trennen - Er schützt den Träger gleichzeitig gegen alle Formen der Magie. Nur die Stärksten und Besten der Dämmerung haben ein Anrecht auf einen Chromatischen Mantel der Dämmerung!]], [[Bringt mir fünfundzwanzig Ehrenmarken als Zeichen Eurer Hingabe und Ihr erhaltet von mir den feinsten unserer Mäntel.]], },
    { [[Bringt 25 Ehrenmarken der Argentumdämmerung zu Rüstmeister Hasana ins Bollwerk in den Westlichen Pestländern.]], },
  },
  [5525] = {
    [[Befreit Knot!]],
  },
  [5526] = {
    [[Die Splitter der Teufelsranke]],
    { [[Euer Wissen über Eldre'Thalas – Düsterbruch – in Feralas wird nun gebraucht, <name>. Im Ostteil der zerstörten Stadt erstreckt sich das Herrschaftsgebiet von Alzzin dem Wildformer. Begebt Euch mit der gebotenen Eile und Tapferkeit dorthin.]], [[Die verderbte Frucht des Gedeihens, die Teufelsranke, sollte sich in Alzzins Nähe befinden. Versucht so viel wie möglich von der Essenz der Teufelsranke zu erlangen, wenn er fällt. Versiegelt diese in dem Reliquiar und bringt es zu mir.]], [[Viel Glück und Cenarius' Kraft sei mit Euch!]], },
    { [[Sucht die Teufelsranke in Düsterbruch und nehmt einen Teufelsrankensplitter mit Euch. Aller Wahrscheinlichkeit nach werdet Ihr Alzzin den Wildformer töten müssen, um an die Teufelsranke zu gelangen. Benutzt das Reliquiar der Reinheit, um darin den Splitter sicher zu versiegeln, und bringt das versiegelte Reliquiar zu Rabine Saturna in Nachthafen auf der Mondlichtung.]], },
  },
  [5527] = {
    [[Ein Reliquiar der Reinheit]],
    { [[Zuerst müssen wir einen Behälter finden, in dem eine sehr stark verderbte Substanz transportiert werden kann, ohne das der Träger Nebenwirkungen davonträgt. Meines Wissens nach, gibt es nur einen Gegenstand der geeignet ist, darin etwas so abscheuliches wie eine Teufelsranke aufzubewahren: ein Reliquiar der Reinheit.]], [[Tief in der Einöde von Silithus befinden sich die Überreste des Nachtelfenpostens Südwind. Vor vielen, vielen Jahren gab es dort ein Reliquiar. Wenn wir Erfolg haben wollen, müsst Ihr die Ruinen durchsuchen, in der Hoffnung dort ein Reliquiar der Reinheit zu finden.]], },
    { [[Reist nach Silithus und sucht in den Ruinen des Südwindpostens nach einem Reliquiar der Reinheit. Sollte Eure Suche Erfolg haben, kehrt zu Rabine Saturna in Nachthafen auf der Mondlichtung zurück.]], },
  },
  [5528] = {
    [[Der Gordokgeschmackstest]],
  },
  [5529] = {
    [[Verseuchte Jungtiere]],
    { [[Die Argentumdämmerung hat kürzlich erfahren, dass die Geißel ihren eigenen verseuchten Drachenschwarm erschafft! Wir müssen sie stoppen - wenn die Geißel eine Großdrachenstreitmacht auf die Beine stellt, wird sie unbehelligt ganz Azeroth verwüsten!]], [[Ihre Totenbeschwörer züchten verseuchte Großdrachen in Scholomance, in der Mitte des Darromersees.]], [[Begebt Euch zur Scholomance und tötet so viele Jungtiere wie nur möglich...bevor sie größer werden können!]], },
    { [[Tötet 20 verseuchte Jungtiere und kehrt dann zu Betina Moppelzink bei der Kapelle des Hoffnungsvollen Lichts zurück.]], },
  },
  [5531] = {
    [[Betina Moppelzink]],
    { [[Die Eier, die Ihr gebracht habt, sollten in Experimenten der Geißel verwendet werden, um einen verseuchten Drachenschwarm zu erschaffen. Diese gefrorenen schwarzen Dracheneier wären für ihre Studien unersetzlich gewesen und hätten ihren Zeitplan sicherlich beschleunigt.]], [[Nun ist es an der Zeit, dass wir ihre Forschung beenden.]], [[Bringt die gefrorenen Eier zu Betina Moppelzink da draußen. Sie ist in Wissenschaft und Alchemie sehr bewandert und hat eine Waffe hergestellt, die wir gegen die Geißel einsetzen können. Mit Eurer Hilfe werden wir ihnen einen schweren Schlag versetzen.]], },
    { [[Bringt die gefrorenen Eier zu Betina Moppelzink.]], },
  },
  [5533] = {
    [[Scholomance]],
    { [[Auf der Insel Darrowehr liegt die Scholomance - ein elendes Zentrum der Totenbeschwörung und des Bösen der Geißel. Unser Feind lebt im Inneren und führt unaussprechliche Taten der Verderbtheit an nichts ahnenden Opfern aus... zumindest vermuten wir das. Eine Tür versperrt den Weg, und ganz gleich was wir versuchen, der Zutritt ist nur mit dem richtigen Schlüssel möglich.]], [[Alchemist Arbington weiß viel über die Scholomance und hat eine Idee, wie ein Schlüssel zu erlangen ist. Ihr habt mir Euren Wert bewiesen und nun vertraue ich Euch diese Aufgabe an. Sprecht mit ihm über diese Angelegenheit.]], },
    { [[Sprecht mit Alchemist Arbington an der Zugwindspitze in den Westlichen Pestländern.]], },
  },
  [5534] = {
    [[Kim'jaels 'fehlende' Ausrüstung]],
    { [[Was?! Was?!]], [[Das war nicht gut genug für Euch? Wisst Ihr, wie teuer diese Sprengstoffe sind? Die kosten... ach, egal. Ihr wollt also echtes Geld machen, ja? Na dann... so läuft das im Geschäftsleben: Ihr beweist, dass Ihr ein wertvoller Angestellter seid und dann werdet Ihr entsprechend belohnt, nachdem Ihr schön brav durchgehalten habt.]], [[Falls Ihr also ein "wertvoller" Angestellter seid, dann begebt Euch zu den Ruinen von Eldarath und bringt mir irgendeine Rune, nach der diese Blutelfen gesucht haben. Anscheinend befindet sie sich im Besitz der Naga.]], },
    { [[Sucht in den Ruinen von Eldarath eine beliebige Rune, die die Naga in ihrem Besitz haben, und bringt sie Kim'jael in Azshara.]], },
  },
  [5535] = {
    [[Spirituelle Unruhe]],
    { [[Vor langer Zeit gehörten die Hochgeborenen zu den mächtigsten Schülern des Arkanen, die die Nachtelfen je hervorgebracht hatten. Die Verderbnis, die mit solchen Studien einhergeht, brachte großes Unglück über die Gesellschaft der Nachtelfen und wirkte sich in großem Umfang auf Azeroth aus... wie Ihr sicherlich wisst.]], [[Ich habe den Schildwachen versprochen, dass ich die Geister aus ihren sterblichen Banden befreie, wenn ich sie finde. Wenn Ihr dasselbe tun würdet, kann ich Euch sicherlich belohnen.]], [[Die Geister spuken im Schattensangschrein, von hier aus im Nordosten.]], },
    { [[Vernichtet 6 Erscheinungen einer Hochgeborenen und 6 Lichlinge eines Hochgeborenen, bevor Ihr zu Loh'atu in Azshara zurückkehrt.]], },
  },
  [5536] = {
    [[Ein Land voller Hass]],
    { [[Die Satyrn, entstellte und böse Aspekte der Nachtelfen, infizieren jetzt die hochheiligen Länder, die einst von Cenarius bereist wurden. Ich kann nicht für die Nachtelfen sprechen, wenn es um die ganzen Gräueltaten geht, die diese dämonischen Kreaturen begangen haben, aber ich habe der Erdenmutter einen Eid geschworen, den Elfen zu helfen, das Land von solch einer Plage zu befreien.]], [[Ich bin sicher, dass Ihr Euch beweisen würdet, wenn Ihr mir bei der Reinigung helfen würdet.]], [[Nordöstlich von hier könnt Ihr den Haldarrklan finden. Kehrt zu mir zurück, nachdem Ihr Euch gegen ihre Stärke bewiesen habt.]], },
    { [[Tötet 6 Satyrn der Haldarr, 2 Schwindler der Haldarr sowie 2 Teufelsanbeter der Haldarr und kehrt dann zu Loh'atu an der Grenze zum Eschental zurück.]], },
  },
  [5537] = {
    [[Skelettfragmente]],
    { [[Um in die Scholomance zu gelangen, benötigt ihr einen Dietrich. Er muss aus den Überresten eines Skeletts geschmiedet werden- eigentlich aus mehreren - und er darf nur durch die stärksten Metalle in einer geeigneten Form gehärtet werden. Ein Signet der Macht von einem Wesen, das das Portal zur Scholomance natürlich öffnen kann, gibt dem Schlüssel seine endgültige Funktion.]], [[Aber eines nach dem anderen, <name>. Wir benötigen Skelettfragmente für das Schmieden des Schlüssels. Skelette innerhalb von Andorhal sollten den gewünschten Effekt bringen, aber die außerhalb der Mauern könnten vielleicht auch ausreichen.]], },
    { [[Bringt 15 Skelettfragmente zu Alchemist Arbington an der Zugwindspitze in den Westlichen Pestländern.]], },
  },
  [5538] = {
    [[Sold reimt sich auf...]],
    { [[Der Goblin, mit dem Ihr sprechen müsst, ist Krinkle Goldstahl. Er geht mit seinen Fertigkeiten bei den höchsten Bietern in Gadgetzan, in der Tanariswüste, hausieren. Er dürfte der Einzige sein, der die Form herstellen kann, die wir benötigen, und seine Dienste werden nicht billig sein. Wir bitten Euch, tief in die Tasche zu greifen, indem Ihr die Gebühr zahlt, die er fordert.]], [[Ich bin Alchemist, kein Bankier. Wenn ich Gold herumliegen hätte, wäre ich sicher nicht hier.]], [[Ich schätze, ich hab meine Gedanken mal wieder laut ausgesprochen. Upps.]], [[Gadgetzan erwartet Euch! Viel Glück!]], },
    { [[Bringt die magieerfüllten Skelettfragmente sowie 15 Goldstücke zu Krinkle Goldstahl in Gadgetzan.]], },
  },
  [5541] = {
    [[Munition für Schlingerschuss]],
    { [[Hegnar Schlingerschuss verkauft Gewehre an der Straße nach Ambossar. Scharfschützen und Mörsertrupps verbringen viel Zeit damit, in der Nähe seines Geschäfts zu üben, und er braucht ständig neue Munition.]], [[Aber... die letzte Munitionskiste, die ich ihm geschickt habe, ist unterwegs verloren gegangen. Mein Kurier sagte, er lagerte nahe des Graufelsbaus, als Wendigos ihn davonjagten... und der Dummkopf hat Schlingerschuss' Munition zurückgelassen!]], [[<name>, könnt Ihr diese Munition beschaffen und zu Schlingerschuss bringen? Er wartet darauf und ihm gehen bestimmt bald die Vorräte aus.]], },
    { [[Bringt Schlingerschuss' Munition zu Hegnar Schlingerschuss in Dun Morogh.]], },
  },
  [5542] = {
    [[Dämonenhunde]],
    { [[Wenn Ihr hier bleiben wollt, bitte ich Euch nur, Euren Unterhalt zu verdienen. Wir haben hier viele Plagegeister, denen etwas 'Disziplin' gut tun würde.]], [[Ihr könnt mit den Seuchenhunden und den kleinen Seuchenhunden beginnen.]], [[Viel kann ich Euch im Gegenzug nicht anbieten, aber ein warmes Mahl und ein wenig Konversation wäre Euch garantiert, wenn Ihr erfolgreich seid.]], },
    { [[Erschlagt 20 kleine Seuchenhunde, 5 Seuchenhunde und 5 rasende Seuchenhunde. Kehrt zu Tirion Fordring zurück, sobald Ihr die Aufgabe erfüllt habt.]], },
  },
  [5543] = {
    [[Blutgefärbter Himmel]],
    { [[Wehe jenen, die töricht genug sind, in die Pestländer zu wandern. Alle Arten der Verderbtheit bewohnen diese Wälder - von den fanatischen Anhängern des Scharlachroten Kreuzzugs, die jeden töten, der nicht das Zeichen der Kreuzzügler trägt, bis hin zur mordlustigen Geißel, die ihre Anzahl vergrößern will, indem sie ihre Ränge mit Untoten füllt.]], [[Sogar die Tierwelt wurde in wilde, menschenfressende Bestien verwandelt. Ich bitte Euch, die zu vernichten, die vom Firmament aus zuschlagen: die Seuchenfledermäuse.]], },
    { [[Erschlagt 30 Seuchenfledermäuse und kehrt zu Tirion Fordring zurück.]], },
  },
  [5544] = {
    [[Aassammlung]],
    { [[Meine Essensvorräte gehen zur Neige, <race>. Ich schäme mich zuzugeben, dass ich möglicherweise nicht genug Nahrung habe, um sie mit Euch zu teilen.]], [[Könnt Ihr einem alten Mann bei einer einfachen Aufgabe helfen? Hier in der Umgebung sind Aaswürmer die einzigen Tiere, die von den Verheerungen der Seuche unbeeinflusst blieben. Wenn sie auch ziemlich langweilig schmecken, kann das Fleisch der Würmer doch leicht monatelang aufbewahrt werden. Ich werde einige hundert Pfund benötigen, um meine Vorräte für den kommenden Winter aufzustocken!]], },
    { [[Sucht nach den Aaslarven und Aasverschlingern dieses Gebiets. Erschlagt sie und sammelt ihr Fleisch. Kehrt zu Tirion Fordring zurück, wenn Ihr 15 Scheiben Aaswurmfleisch gesammelt habt.]], },
  },
  [5545] = {
    [[Ein Bündel Probleme]],
    { [[Ich habe hier ein echtes Problem. Ich habe einen Liefertermin für eine Bestellung Holz und mir läuft die Zeit davon. Die Wölfe und Bären nördlich von hier haben meine Arbeiter von den Holzbündeln verjagt, die sie bereits gefällt hatten.]], [[Ich habe bereits mit Stellvertreter Rainer darüber gesprochen, dass die Tiere entfernt werden müssen, aber ich brauche jemanden, der das Holz für mich holt. Wenn Ihr acht Holzbündel für mich holen würdet, könnte ich den Termin vielleicht noch einhalten.]], },
    { [[Bringt 8 Holzbündel zu Raelen beim Holzfällerlager des Osttals.]], },
  },
  [5561] = {
    [[Kodozusammentrieb]],
    { [[Bibbly glaubt, dass er der einzige Goblin in Desolace sei, der weiß, wie man Geld macht. Tja, die einzige Karawane, die mutig genug ist, diese Gebiete zu bereisen, verlässt sich auf mich. Nachdem ich gelernt habe, wie man alternde Kodos mit meinem Kodokombobulator verjüngt und kontrolliert, sind die Karawanenbesitzer Cork und Rigger mit mir eine Partnerschaft eingegangen.]], [[Nehmt den Kombobulator und wendet ihn auf jeden beliebigen alten oder alternden Kodo an; er wird das Biest in einen gezähmten Kodo verwandeln und es zwingen, Euch zu folgen. Wenn Ihr einen Kodo gezähmt habt, bringt ihn zu mir her.]], },
    { [[Bringt 3 gezähmte Kodos sowie den Kodokombobulator zu Smeed Schraubenbuddel bei Schraubenbuddels Lager.]], },
  },
  [5581] = {
    [[Portale der Legion]],
    { [[Verbannt 6 Portale beim Zirkel der Mannoroc und kehrt dann zu Felgur Zweischneid zum Geistwandlerposten zurück.]], },
    { [[Verbannt 6 Portale beim Zirkel der Mannoroc und kehrt dann zu Felgur Zweischneid zum Geistwandlerposten zurück.]], },
  },
  [5582] = {
    [[Gesunde Großdrachenschuppe]],
    { [[Dies ist eine Schuppe von einem verseuchten Großdrachen-Jungtier. Aber im Gegensatz zu den anderen pockennarbigen, eiternden Schuppen an dem kleinen Biest ist diese rare Schuppe gesund und unbeschädigt.]], [[Betina Moppelzink wird eine Probe davon für Forschungen haben wollen.]], },
    { [[Bringt die gesunde Großdrachenschuppe zu Betina Moppelzink bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern.]], },
  },
  [5601] = {
    [[Schwester Pamela]],
    { [[Ich bin während des großen Krieges aus Lordaeron geflohen und hatte das Glück, entkommen zu können, bevor die Geißel durch meine Heimat fegte und sie zu einem verseuchten Alptraum machte! Ich hatte Glück, aber meine Familie nicht. Ich fürchte, dass sie während der Schlacht von Darroheim getötet wurden.]], [[Darroheim ist ein Dorf in den Östlichen Königreichen, eingebettet in die südlichen Hügel dessen, was heutzutage Östliche Pestländer genannt wird. Würdet Ihr Euch dorthin begeben und nach meiner Familie suchen? Ich vermisse sie so sehr, besonders meine kleine Schwester Pamela.]], [[Bitte, sucht sie für mich!]], },
    { [[Sucht Pamela Rotpfad in Darroheim.]], },
  },
  [5621] = {
    [[Gewänder des Mondes]],
    { [[Ich brauche Eure Hilfe, <name>. Ich kann hier nicht weg, denn ich muss hier die anderen Schüler unterweisen, doch Ihr könntet einer unserer verwundeten Schildwachen direkt südlich von Dolanaar helfen. Sie wurde beim Patrouillieren der Stadt verletzt und benötigt unsere Hilfe. Ihr Name ist Shaya.]], [[Wenn Ihr sie findet, heilt ihre Wunden, und stärkt dann ihren Körper mit Euren Zaubern. Das wird sie zumindest etwas länger vor Schaden bewahren, wenn es wieder hart auf hart kommt. Kehrt zu mir zurück, nachdem Ihr Eure Pflicht gegenüber Elune erfüllt habt.]], },
    { [[Sucht die Schildwache Shaya und heilt ihre Wunden mithilfe von 'Geringes Heilen' (Rang 2). Belegt sie danach mit 'Machtwort: Seelenstärke' und kehrt dann zu Laurna Morgenglanz in Dolanaar zurück.]], },
  },
  [5622] = {
    [[Elune geneigt]],
    { [[<name>, sobald Eure Aufgaben hier im Laubschattental abgeschlossen sind, müsst Ihr nach Dolanaar reisen. Dort werdet Ihr Laurna Morgenglanz finden. Sie wird Euch weiter in den Dingen Elunes unterweisen und bei ihr werdet Ihr Euch Eure ersten Gewänder verdienen, die Eure Position als <Priester/Priesterin> von Elune kennzeichnen.]], [[Seid vorsichtig und möget Ihr auf Euren Reisen beschützt werden.]], },
    { [[Sprecht mit Laurna Morgenglanz in Teldrassil.]], },
  },
  [5623] = {
    [[Dem Licht geneigt]],
    { [[Ich grüße Euch, <junger Priester/junge Priesterin>. Die Zeit ist gekommen, mehr über unsere Religion zu lernen. Nachdem Ihr Eure Aufgaben hier in Nordhain abgeschlossen habt, sucht bitte Priesterin Josetta in Goldhain auf. Sie kann Euch noch mehr beibringen und hat gehört, dass Ihr in den Wegen des Lichts gut voranschreitet. Sie freut sich darauf, Euch weiter unterrichten zu können.]], },
    { [[Sprecht mit der Priesterin Josetta im Wald von Elwynn.]], },
  },
  [5624] = {
    [[Gewänder des Lichts]],
    { [[Normalerweise stelle ich den Gefolgsleuten des Lichts eine kleine Herausforderung, um ihre Fertigkeiten zu prüfen. Ich möchte sehen, ob sie es wert sind, ihre ersten Roben zu tragen - sie sind ein Zeichen, dass die Kirche <den Priester/die Priesterin> nach außen hin trägt und unterstützt.]], [[Doch hier ist die Situation etwas anders. Einer der Bürger von Goldhain sah eine verletzte Wache nahe dem See östlich von hier. Er heißt Roberts. Sucht ihn, heilt ihn, stärkt seinen Körper und kehrt hierher zurück. Wenn Ihr das für mich tun könnt, dann werde ich Eure Prüfung als bestanden ansehen.]], },
    { [[Sucht die Wache Roberts und heilt seine Wunden mithilfe von 'Blitzheilung'.]], },
  },
  [5625] = {
    [[Gewänder des Lichts]],
    { [[Normalerweise wird etwa zu dieser Zeit die Macht in einem von Euch geprüft, um zu sehen, ob er würdig genug ist, jene Roben zu tragen, die seinen Platz in der Kirche anzeigen. Doch um ehrlich zu sein, gibt es wichtigere Dinge, um die ich mich kümmern muss. Bürger von Kharanos kamen gerade hierher und erzählten mir von einem verletzten Gebirgsjäger direkt außerhalb der Stadt im Süden - sein Name ist Dolf.]], [[Wenn Ihr Dolf heilt und ihm 'Seelenstärke' gebt, werde ich das als erfolgreiche Prüfung ansehen. Wie klingt das?]], },
    { [[Sucht den Gebirgsjäger Dolf und heilt seine Wunden mithilfe von 'Geringes Heilen' (Rang 2). Belegt ihn danach mit 'Machtwort: Seelenstärke' und kehrt dann zu Maxan Anvol in Kharanos zurück.]], },
  },
  [5626] = {
    [[Dem Licht geneigt]],
    { [[He! <name>! Habt Ihr einen Moment Zeit?]], [[Ihr seid dem Licht jetzt lange genug gefolgt, um den Weg zu Maxan Anvol in Kharanos zu finden. Er wird Euch mehr über den Weg beibringen, dem Ihr folgt, und wenn Ihr Euch als würdig erweist, wird er Euch Eure ersten Roben geben, die Eure Position als <Priester/Priesterin> kennzeichnen.]], [[Also dann mal los! Sobald Ihr hier fertig seid, geht durch den Tunnel - er wird auf Euch warten.]], },
    { [[Sprecht mit Maxan Anvol in Dun Morogh.]], },
  },
  [5627] = {
    [[Sterne von Elune]],
  },
  [5628] = {
    [[Heimkehr]],
    { [[Ah, ja, hallo, <name>? Ich freue mich, dass Ihr auf ein kurzes Gespräch hereinschaut. Ihr seid weit fort von zu Hause, und das in so jungen Jahren - ich bewundere einen solchen Ehrgeiz. Ich wünschte, ich hätte den Mut gehabt, durch die Welt zu reisen, als ich noch jünger war.]], [[Eine Priesterin Eures Ordens war vor nicht allzu langer Zeit hier und hat nach Euch gesucht. Sie sagt, Ihr müsstet dringend in den Tempel des Mondes in Darnassus zurückkehren und mit Priesterin Alathea sprechen - es hatte etwas mit Eurer Ausbildung zu tun. Ich würde nicht zu lange trödeln, wenn es irgendwie geht.]], },
    { [[Sprecht mit der Priesterin Alathea in Darnassus.]], },
  },
  [5629] = {
    [[Heimkehr]],
    { [[Hallo, <name>. Ich weiß, dass Ihr damit beschäftigt wart, unserem Volk zu helfen, aber Ihr müsst Euch auch Zeit für Euch selbst nehmen. Die Priesterin Alathea hat jemanden hergeschickt, um nach Euch zu suchen. Es scheint, dass Ihr die Aufmerksamkeit unserer Ältesten erregt habt, und sie sind der Meinung, dass Ihr für größere Dinge bereit seid. Begebt Euch zum Tempel des Mondes in Darnassus und sucht dort nach Alathea. Sie wird Euch erwarten. Viel Glück, und möge Elune mit Euch sein.]], },
    { [[Sprecht mit der Priesterin Alathea in Darnassus.]], },
  },
  [5630] = {
    [[Heimkehr]],
    { [[Hallo, <Bursche/Mädel>. Es ist gut zu sehen, dass Ihr entfernte Länder weit und breit bereist. Ich will Euch nicht beleidigen, aber viele Eurer Art vertragen das kalte Klima normalerweise nicht gut. Schön zu sehen, dass Ihr aus härterem Holz geschnitzt seid.]], [[Vor nicht allzu langer Zeit war eine Eurer Priesterinnen hier und hat nach Euch gesucht - sie sagte so etwas in dem Sinne, dass Ihr eine Priesterin Alathea im Tempel des Mondes in Darnassus aufsuchen solltet. Sagte, es wäre wichtig und Ihr solltet Euch bald dorthin begeben.]], },
    { [[Sprecht mit der Priesterin Alathea in Darnassus.]], },
  },
  [5631] = {
    [[Heimkehr]],
    { [[Eine Priesterin hat kürzlich unsere Hallen besucht und Euch gesucht, <race>. Sie sagte, sie hätte den Auftrag, Euch zu finden, und dass Ihr so bald wie möglich zum Tempel des Mondes in Darnassus zurückkehren solltet. Wenn Ihr ankommt, sucht Priesterin Alathea auf - sie wird Euch mehr berichten.]], [[Das Licht sei mit Euch, <name>.]], },
    { [[Sprecht mit der Priesterin Alathea in Darnassus.]], },
  },
  [5632] = {
    [[Heimkehr]],
    { [[Oh, aber ja, natürlich. Hallo, <name>. Eine Botin von Eurem Orden war vor nicht allzu langer Zeit hier und hat nach Euch gesucht. Sie erwähnte, dass in Darnassus etwas Wichtiges geschieht und dass Ihr so bald wie möglich zum Tempel des Mondes zurückkehren sollt. Wenn Ihr dort ankommt, sprecht mit Priesterin Alathea. Ich glaube, es hat etwas damit zu tun, dass Ihr so gute Fortschritte beim Verständnis unserer Bräuche macht.]], [[Wie gesagt, kehrt nach Darnassus zurück, sobald Ihr könnt.]], },
    { [[Sprecht mit der Priesterin Alathea in Darnassus.]], },
  },
  [5633] = {
    [[Heimkehr]],
    { [[Seid gegrüßt, <Bursche/Mädel>. Es ist schön, Euch hier in unseren großen Hallen zu sehen. Ein Segen sei mit Euch und Eurer Art.]], [[Während Ihr durch unsere Länder gereist seid, kam eine Priesterin Eures Tempels her, die nach Euch suchte. Sie sagte, dass Ihr so bald wie möglich zum Tempel des Mondes zurückkehren sollt. Es tut mir leid, dass ich Euch nicht mehr berichten kann, aber es klang wichtig. Wenn Ihr zum Tempel kommt, sprecht mit Priesterin Alathea.]], },
    { [[Sprecht mit der Priesterin Alathea in Darnassus.]], },
  },
  [5634] = {
    [[Verzweifeltes Gebet]],
    { [[You're needed in Stormwind, <name>. The High Priestess there, Laurena, says it's time for you to prove you're worthy of the next level of your training. Don't waste too much time finding your way there. You can find her in the Cathedral of Light in the center of the city. Give her my regards, and prove how worthy you are of such a calling. The Light be with you, <name>.]], },
    { [[Speak to High Priestess Laurena in Stormwind.]], },
  },
  [5635] = {
    [[Verzweifeltes Gebet]],
    { [[Ihr habt die Aufmerksamkeit unserer Vorgesetzten erregt, <name>. Sie bitten, dass Ihr so bald wie möglich zur Kathedrale des Lichts in Sturmwind reist. Anscheinend sind sie der Meinung, dass Ihr Euch bereits würdig erwiesen habt und bereit seid, Eure Ausbildung weiterzuführen. Wenn Ihr dort anlangt, sucht nach der Hohepriesterin Laurena. Sie kann Euch weitere Anweisungen geben. Viel Glück, und möge das Licht mit Euch sein.]], },
    { [[Sprecht mit der Hohepriesterin Laurena in Sturmwind.]], },
  },
  [5636] = {
    [[Verzweifeltes Gebet]],
    { [[Elune segne Euch, <race>. Ich hoffe, dass Euer Licht Euch findet und Euch heute gut schützt.]], [[Einer Eurer Priester war vor nicht allzu langer Zeit hier und suchte nach Euch. Er erwähnte, dass Ihr sobald wie möglich zur Kathedrale des Lichts zurückkehren und mit der Hohepriesterin Laurena sprechen sollt. Es scheint, als hättet Ihr Euch wacker geschlagen, dass Ihr eine solche Aufmerksamkeit erlangt. Ich würde hier nicht allzu viel Zeit verbringen - Eure Leute brauchen Euch.]], },
    { [[Sprecht mit der Hohepriesterin Laurena in Sturmwind.]], },
  },
  [5637] = {
    [[Verzweifeltes Gebet]],
    { [[Die Zeit ist für Euch gekommen, nach Sturmwind zu gehen, <name>. Die Hohepriesterin Laurena in der Kathedrale des Lichts möchte mit Euch sprechen. Es muss etwas sehr Gutes oder sehr Schlechtes sein, wenn die Hohepriesterin Euch persönlich dort sehen will, aber ich denke, dass Ihr selbst nur Gutes getan habt und dass es eine gute Sache ist, dass sie mit Euch sprechen möchte.]], [[Verschwendet hier aber nicht allzu viel Zeit. Ich an Eurer Stelle würde mich umgehend auf den Weg machen.]], },
    { [[Sprecht mit der Hohepriesterin Laurena in Sturmwind.]], },
  },
  [5638] = {
    [[Verzweifeltes Gebet]],
    { [[Vor kurzer Zeit kam einer Eurer Priester her und suchte nach Euch, <race>. Er sagte, dass ich Euch zur Kathedrale des Lichts schicken solle, wenn ich Euch sehen würde. Er sagte, es sei sehr wichtig, aber ich an Eurer Stelle würde mir trotzdem nicht allzu viele Sorgen machen. Seine Haltung erschien positiv...als ob es gute Neuigkeiten wären. Auf jeden Fall solltet Ihr Euch beeilen, wenn Ihr meine Meinung wissen wollt.]], [[Elune sei mit Euch, <race>.]], },
    { [[Sprecht mit der Hohepriesterin Laurena in Sturmwind.]], },
  },
  [5639] = {
    [[Verzweifeltes Gebet]],
    { [[Ihr werdet in Sturmwind gebraucht, <name>! Die Hohepriesterin dort, Laurena, sagt, es sei an der Zeit für Euch zu beweisen, dass Ihr der nächsten Stufe Eurer Ausbildung würdig seid. Verschwendet nicht zu viel Zeit auf dem Weg dorthin. Ihr könnt sie in der Kathedrale des Lichts im Zentrum der Stadt finden. Überbringt Ihr meine Wünsche und beweist, wie würdig Ihr eines solchen Rufes seid. Das Licht sei mit Euch, <name>.]], },
    { [[Sprecht mit der Hohepriesterin Laurena in Sturmwind.]], },
  },
  [5641] = {
    [[Furchtlos]],
  },
  [5642] = {
    [[Schattenwache]],
    { [[In Orgrimmar geht etwas Wichtiges vor, <name>.]], [[Normalerweise sind die Boten, die hier durchkommen und nach Trollpriestern suchen, recht umgänglich. Dieses Mal schienen sie jedoch in großer Eile zu sein. Trödelt nicht zu lange in Donnerfels herum. Ich würde so schnell wie möglich zum Tal der Geister zurückkehren.]], },
    { [[Sprecht mit Ur'kyo in Orgrimmar.]], },
  },
  [5643] = {
    [[Schattenwache]],
    { [[Wie viele unserer eigenen Priester werdet Ihr von Eurem Stamm durch die Initiationsriten geführt. Diese Riten lehren Priester häufig besondere Fähigkeiten, die wichtig für ihr Überleben sind. Ich spüre, dass die Zeit jetzt an Euch ist, jetzt, in diesem Augenblick. Ihr müsst zum Tal der Geister in Orgrimmar zurückkehren und mit Ur'kyo sprechen. Was er Euch lehrt, kann für die Zukunft sehr wichtig sein.]], },
    { [[Sprecht mit Ur'kyo in Orgrimmar.]], },
  },
  [5644] = {
    [[Verschlingende Seuche]],
    { [[Ah, ein wundervoller Tag für Euch, <name>. Aufgrund der Lektionen, die Ihr gelernt habt, kann ich sehen, dass die Zeit gekommen ist, Euch zu Aelthalyste zurückzuschicken. Es gibt Dinge, die nur sie Euch beibringen kann, und auch das nur, wenn Ihr Euch als würdig erweist.]], [[Ich werde die Überraschung für Euch nicht verderben, aber ich erinnere mich daran, wie es war, als ich ein junger Priester war und etwas über die wahre Macht lernte...die göttliche Macht. Ja, das waren noch Zeiten.]], [[Kehrt zu Aelthalyste nach Unterstadt zurück, <name>. Sie erwartet Euch im Kriegsviertel.]], },
    { [[Sprecht mit Aelthalyste in Unterstadt.]], },
  },
  [5645] = {
    [[Furchtlos]],
    { [[<name>, Euer Hohepriester Rohan erwartet Euch zu einer Audienz in Eisenschmiede. Beeilt Euch, Eure nächste Prüfung rückt schnell näher. Auch wenn ich wünschte, dass ich Zeuge der besonderen Riten Eures Volkes sein könnte, ist mein Platz doch hier. Aber möge das Licht während Eurer Reisen mit Euch sein. Rohan sollte sich noch im Mystikerviertel befinden, wenn Ihr die Stadt erreicht. Er wird Euch erwarten.]], },
    { [[Sprecht mit Hohepriester Rohan in Eisenschmiede.]], },
  },
  [5646] = {
    [[Verschlingende Seuche]],
    { [[Aelthalyste bezeugt unserem Volk großen Respekt, indem sie unsere Leute zur weiteren Ausbildung zu mir schickt, und nun tue ich dasselbe für ihren Stamm.]], [[Kehrt nach Unterstadt zurück, <name>. Dort, im Kriegsviertel, wartet sie auf Euch und ist bereit, Euch mehr über Euer Volk und seine Wege zu lehren. Begebt Euch dorthin und zögert nicht lange.]], },
    { [[Sprecht mit Aelthalyste in Unterstadt.]], },
  },
  [5647] = {
    [[Furchtlos]],
    { [[Euer Volk ist sehr stark, <name>. Wir sind stolz darauf, Euch Verbündete nennen zu können. Mit jedem Sonnenuntergang nähert Ihr und Eure Art Euch nicht nur Euren wahren Wurzeln, sondern seid auch immer sicherer, dass Ihr in sehr vielen Belangen auf dem richtigen Weg seid. Das muss Euch ein so wunderbares Gefühl geben.]], [[Erst vor Kurzem erhielten wir eine Nachricht aus Eisenschmiede. Sie besagte, dass Ihr dorthin zurückkehren und mit dem Hohepriester Rohan im Mystikerviertel sprechen sollt. Ich würde ihn nicht warten lassen. Möge Elune Eure Reisen leiten.]], },
    { [[Sprecht mit Hohepriester Rohan in Eisenschmiede.]], },
  },
  [5648] = {
    [[Gewänder der Spiritualität]],
    { [[Ihr seid bereit für Eure erste Prüfung, <name>. Südlich von Klingenhügel, direkt vor der Stadt, wurde eine Grunzerin verletzt, als sie einige von Prachtmeers alten Truppen bekämpfte. Es geht ihr nicht allzu gut und es wäre wirklich gut, wenn einer unserer Art ihr helfen könnte. Sucht sie - ihr Name ist Kor'ja -, heilt ihre Wunden und gebt ihr den Segen unserer Vorfahren.]], [[Wenn Ihr das tut, sehe ich das als ausreichenden Beweis dafür an, dass Ihr bereit seid, unseren Pfad weiter zu beschreiten.]], },
    { [[Findet die Grunzerin Kor'ja und heilt ihre Wunden mithilfe von 'Geringes Heilen' (Rang 2). Belegt sie danach mit 'Machtwort: Seelenstärke' und kehrt dann zu Tai'jin in Klingenhügel zurück.]], },
  },
  [5649] = {
    [[Der Spiritualität geneigt]],
    { [[Ja, Mann. Unser Volk hat sich der Horde angeschlossen, weil sie gut für uns ist. Die da sind loyal und lehren uns viel über Dinge wie Ehre. Aber unsere alten Gewohnheiten sterben nur langsam. Die Orcs mögen nicht alle unsere Traditionen billigen, aber das bedeutet nicht, dass wir einige davon nicht im Geheimen ausüben können.]], [[Ihr wisst das, und darum schicke ich Euch zu Tai'jin in Klingenhügel. Sie wird Euch mehr über unsere Geschichte lehren. Sie wird Euch in den Wegen unseres Stammes unterrichten. Geht zu ihr, wenn Ihr bereit seid.]], },
    { [[Sprecht mit Tai'jin in Durotar.]], },
  },
  [5650] = {
    [[Gewänder der Dunkelheit]],
    { [[Unsere Macht beziehen wir aus der Dunkelheit und dem Schatten, <name>, doch das ist nicht Eure einzige Rolle. Ihr habt erkannt, dass es von Vorteil ist, sich in der Heilkunst auszukennen. Das ist weise. Doch bevor Ihr die Dunkelheit kennen lernen könnt, müsst Ihr erst das Licht kennen. Und daher habe ich eine Prüfung für Euch.]], [[Nördlich des Friedhofs befindet sich eine verwundete Todeswache - Todeswache Kel. Er hat uns seit geraumer Zeit vor Gnollen beschützt und braucht Heilung. Doch Ihr müsst auch seinen Körper stärken, bevor er seinen Dienst wieder aufnehmen kann. Tut beides mit Euren Zaubern und kehrt dann zu mir zurück.]], },
    { [[Findet die Todeswache Kel und heilt seine Wunden mithilfe von 'Geringes Heilen' (Rang 2). Belegt ihn danach mit 'Machtwort: Seelenstärke' und kehrt zum dunklen Kleriker Beryll in Brill zurück.]], },
  },
  [5651] = {
    [[Der Dunkelheit geneigt]],
    { [[Es gibt viele Lektionen, die die Verlassenen durch Erfahrung gelernt haben... Dinge, die einige Völker allenfalls erahnen können. Und obwohl unser Weg in Dunkelheit liegt, werdet Ihr feststellen, dass sehr viele Dinge sowohl vom Licht als auch von der Dunkelheit abhängen. Eure Lektionen sollen Euch lehren, beides zu nutzen... mit Umsicht. Aber beachtet meine Worte: vergesst nie, wir wurden in die Verderbnis gezwungen und der Fäulnis überlassen wie ein Stück Aas. Die Dunkelheit ist unser Heim.]], [[Sucht den Dunklen Kleriker Beryll in Brill auf, wenn Ihr bereit seid, mehr zu erfahren.]], },
    { [[Sprecht mit dem Dunklen Kleriker Beryll in Tirisfal.]], },
  },
  [5652] = {
    [[Verhexung der Schwäche]],
    { [[I sent here to make sure that you not wander too far from home, <name>. They send me to make sure you return home because Ur'kyo, greatest of our priests, wish to speak to you. You go there. You got to the Valley of Spirits in the orc city and you speak to Ur'kyo. He wait for you now.]], },
    { [[Speak to Ur'kyo in Orgrimmar.]], },
  },
  [5654] = {
    [[Verhexung der Schwäche]],
    { [[Es ist an der Zeit, Euch mit Ur'kyo zu treffen, <name>. Er befindet sich im Tal der Geister in der Stadt der Orcs. Geht dorthin und sprecht mit ihm. Es ist an der Zeit, dass Ihr mehr über die alten Wege unserer Art lernt. Es wird Zeit, dass Ihr in den Kreis eingeführt und zu einem wahren Priester unseres Stammes gemacht werdet.]], },
    { [[Sprecht mit Ur'kyo in Orgrimmar.]], },
  },
  [5655] = {
    [[Verhexung der Schwäche]],
    { [[Ich wurde hergeschickt, um sicherzustellen, dass Ihr Euch nicht zu sehr von der Heimat entfernt, <name>. Sie haben mich geschickt, um sicherzustellen, dass Ihr nach Hause zurückkehrt, da Ur'kyo, der größte unserer Priester, mit Euch zu sprechen wünscht. Begebt Euch dorthin. Begebt Euch zum Tal der Geister in der Stadt der Orcs und sprecht mit Ur'kyo. Er wartet auf Euch.]], },
    { [[Sprecht mit Ur'kyo in Orgrimmar.]], },
  },
  [5656] = {
    [[Verhexung der Schwäche]],
    { [[Es erwärmt immer mein nicht sterbendes Herz, Priester aus allen Kulturen zu sehen, die durch die Welt ziehen. Zu wissen, dass Macht existiert, die nicht arkan und sehr wohl in unserer Reichweite ist. Daher ist es mir eine Ehre, Euch nach Hause zu schicken, <name>.]], [[Ur'kyo, Euer großer Priester in Orgrimmar, bittet um Eure Anwesenheit. Ihr findet ihn im Tal der Geister.]], },
    { [[Sprecht mit Ur'kyo in Orgrimmar.]], },
  },
  [5657] = {
    [[Verhexung der Schwäche]],
    { [[Nach Hause. Kehrt nach Hause zurück, <race>.]], [[Euer spiritueller Anführer Ur'kyo bittet Euch, zu ihm zurückzukehren und mit ihm über den Weg zu sprechen, den Ihr geht.]], [[Begebt Euch zum Tal der Geister in der großen Hordenstadt Orgrimmar. Dort werde Ihr ihn finden.]], },
    { [[Sprecht mit Ur'kyo in Orgrimmar.]], },
  },
  [5658] = {
    [[Berührung der Schwäche]],
    { [[You be far from home, <class>. You be needed there, back in your city under the ground. Your mistress, the one you be callin' Aelthalyste be needin' you back there. Like my tribe, there things you must learn only from her. Don't be stayin' here too long. You should be headin' to the War Quarter as fast as a zeppelin can carry you.]], },
    { [[Speak to Aelthalyste in the Undercity.]], },
  },
  [5660] = {
    [[Berührung der Schwäche]],
    { [[Ihr seid weit weg von zu Hause, <class>. Ihr werdet dort gebraucht, dort, in Eurer Stadt unter dem Boden. Eure Meisterin, diejenige, die Ihr Aelthalyste nennt, braucht Euch dort. Genau wie bei meinem Stamm gibt es dort Dinge, die Ihr nur von ihr lernen könnt. Bleibt nicht zu lange hier. Ihr solltet Euch zum Kriegsviertel begeben, so schnell ein Zeppelin Euch tragen kann.]], },
    { [[Sprecht mit Aelthalyste in Unterstadt.]], },
  },
  [5661] = {
    [[Berührung der Schwäche]],
    { [[Ihr seid weit weg von zu Hause, hier im Dorf der Bluthufe, <name>. Ich wurde hierher geschickt, um Priester meines eigenen Stammes zu finden und sie zurückzuschicken, um mit unseren Anführern zu sprechen, aber das bedeutet nicht, dass ich Euch nicht auch helfen kann.]], [[Ich habe gehört, dass der Geist Eurer führenden Priesterin möchte, dass alle Verlassenen zu ihr kommen - es gibt Dinge, die Ihr lernen müsst.]], [[Trefft Euch mit Aelthalyste im Kriegsviertel, in Eurer Stadt.]], },
    { [[Sprecht mit Aelthalyste in Unterstadt.]], },
  },
  [5662] = {
    [[Berührung der Schwäche]],
    { [[Mein Respekt gilt Euch und Eurer Art, <name>. Normalerweise lehre ich nur die meines eigenen Stammes. Eure Lektionen kommen von Aelthalyste in der Stadt, die einst Lordaeron genannt wurde.]], [[Ihr solltet zur ihr zurückkehren, wenn Ihr die Fähigkeiten erlernen wollt, die Euch von anderen Glaubensrichtungen abgrenzen.]], [[Begebt Euch ins Kriegsviertel in Unterstadt und sucht dort nach Eurer Meisterin.]], },
    { [[Sprecht mit Aelthalyste in Unterstadt.]], },
  },
  [5663] = {
    [[Berührung der Schwäche]],
    { [[Aelthalyste schickte eine Nachricht an alle ihre Priester. Wir sollen die Jüngeren unseres Ordens für die weitere Ausbildung nach Unterstadt schicken. Wenn Ihr Euch wahrlich zu unseren Gläubigen zählt, dann solltet Ihr auch dorthin zurückkehren.]], [[Ihr könnt Aelthalyste im Kriegsviertel in Unterstadt finden.]], },
    { [[Sprecht mit Aelthalyste in Unterstadt.]], },
  },
  [5672] = {
    [[Elunes Anmut]],
    { [[A wonderful day has come, <name>. You've reached the age in which our <priests or priestesses> are trained in abilities only Night elves know. But it is not for me to train you. You must return to the Temple of the Moon and speak to Priestess Alathea as soon as your business here is concluded. She will test you to ensure you're prepared, and only then will she teach you Elune's Grace.]], },
    { [[Speak to Priestess Alathea in Darnassus.]], },
  },
  [5673] = {
    [[Elunes Anmut]],
    { [[Ihr wisst, dass Ihr jederzeit zu jedem unserer Lehrer Zugang habt, <name>, aber es gibt einige Dinge, die wir nicht an Euch weitergeben können. Eines dieser Dinge sind die Lehren der Göttin Elune. Sie verleiht ihren Getreuen spezielle Fähigkeiten, und wenn ich mich nicht irre, ist es an der Zeit, dass Ihr nach Hause zu Eurem Tempel des Mondes zurückkehrt, um geprüft zu werden.]], [[Kehrt dorthin zurück, wenn Ihr könnt, und sprecht mit der Priesterin Alathea.]], },
    { [[Sprecht mit der Priesterin Alathea in Darnassus.]], },
  },
  [5674] = {
    [[Elunes Anmut]],
    { [[Ein wunderbarer Tag ist gekommen, <name>. Ihr habt das Alter erreicht, in dem unsere <Priester/Priesterinnen> in Fähigkeiten ausgebildet werden, die nur die Nachtelfen kennen. Aber es ist nicht an mir, Euch auszubilden. Ihr müsst zum Tempel des Mondes zurückkehren und mit der Priesterin Alathea sprechen, sobald Ihr Eure Angelegenheiten hier abgeschlossen habt. Sie wird Euch prüfen, um sicher zu sein, dass Ihr bereit seid, und erst dann wird sie Euch Elunes Anmut lehren.]], },
    { [[Sprecht mit der Priesterin Alathea in Darnassus.]], },
  },
  [5675] = {
    [[Elunes Anmut]],
    { [[Die Zeit verrinnt, <Bursche/Mädel>. Es ist an der Zeit, dass Ihr nach Darnassus heimkehrt. Die Priesterin Alathea ließ uns wissen, dass Ihr dorthin zur Ausbildung zurückkehren sollt. Es scheint, als wärt Ihr bereit, getestet zu werden, so wie jene, die dem Licht dienen. Ihr solltet Euch dorthin begeben, sobald Ihr könnt. Alatheas Nachricht besagte, dass sie im Tempel des Mondes auf Euch wartet. Viel Glück.]], },
    { [[Sprecht mit der Priesterin Alathea in Darnassus.]], },
  },
  [5676] = {
    [[Arkane Rückkopplung]],
    { [[It is time for you to return to Stormwind, young one. Your superiors there have called you back for more training. And although it disturbs me some, your race and mine are allies and I would not betray that.]], [[I do not mean to be so coy with you--my distrust of arcane magics, be they divine inspired or not, is still strong within me.]], [[You will know of what I speak after you return to your Cathedral of Light. Speak to High Priestess Laurena when you arrive.]], },
    { [[Speak to High Priestess Laurena in Stormwind.]], },
  },
  [5677] = {
    [[Arkane Rückkopplung]],
    { [[So gern ich Euch auch unsere eigenen speziellen Lehren des Lichts zeigen würde, so ist dies doch nicht an uns. Ihr müsst zurück nach Sturmwind, zu Eurer eigenen Art, <name>... zumindest vorerst. Glaubt mir, es war nicht böse gemeint. Wenn Ihr zur Hohepriesterin Laurena in die Kathedrale des Lichts zurückkehrt, werdet Ihr verstehen, worüber ich spreche... Die Zeit ist für Euer Volk gekommen, Euch auf die Zukunft vorzubereiten, Euch von den anderen abzuheben, die einem ähnlichen Weg folgen.]], },
    { [[Sprecht mit der Hohepriesterin Laurena in Sturmwind.]], },
  },
  [5679] = {
    [[Verschlingende Seuche]],
    { [[Aelthalyste show our people great respect by sending them back to me for more training, and now I do the same for her tribe.]], [[You return to the Undercity, <name>. There, in the War Quarter, she be ready to teach you more 'bout your people and their ways. Go there, and do not wait long.]], },
    { [[Speak to Aelthalyste in the Undercity.]], },
  },
  [5680] = {
    [[Schattenwache]],
    { [[Like many of our own priests, your own tribe puts you through rites of passage. These rites often teach priests special abilities that are vital to their survival. I sense that time is upon you even as we speak. You must return to the Valley of Spirits in Orgrimmar and speak to Ur'kyo. What he teaches you might be very important for the future.]], },
    { [[Speak to Ur'kyo in Ogrimmar.]], },
  },
  [5713] = {
    [[Ein Schuss, ein Treffer]],
    { [[Schützt die Schildwache Aynasha und sprecht dann mit Onaeya bei Maestras Posten im Eschental.]], },
    { [[Schützt die Schildwache Aynasha und sprecht dann mit Onaeya bei Maestras Posten im Eschental.]], },
  },
  [5721] = {
    [[Die Schlacht um Darroheim]],
    { [[Nun, <name>, Ihr werdet an der Schlacht von Darroheim teilnehmen und Joseph Rotpfad retten.]], [[Platziert dieses Reliktbündel auf dem Marktplatz von Darroheim, und die Geister von Darroheim werden sich erheben. Nehmt an der Schlacht teil und sorgt dafür, dass die folgenden zwei Dinge eintreten: Davil muss den Tod von Horgus überleben und Rotpfad muss überleben, um erst verdorben und dann besiegt zu werden.]], [[Nachdem Ihr den verderbten Rotpfad besiegt habt, wird seine Seele gerettet sein. Sprecht mit ihm und kehrt dann zu seiner Tochter Pamela zurück.]], },
    { [[Legt das Reliktbündel auf den Marktplatz von Darroheim.]], [[Verhindert Davil Lichterscheins Tod, bis Horgus stirbt.]], [[Verhindert Hauptmann Rotpfads Tod, bis Rotpfad der Verderbte erscheint.]], [[Besiegt Rotpfad den Verderbten.]], [[Sprecht mit dem Geist von Joseph Rotpfad, sobald er auf dem Marktplatz erscheint, und sprecht dann mit Pamela in ihrem Zuhause.]], },
  },
  [5722] = {
    [[Die Suche nach dem verloren gegangenen Ranzen]],
    { [[Die Troggs sind nicht das Einzige, das Magatha im Flammenschlund interessierte. Sie hatte gehört, dass einer ihrer Diener, ein gewisser Maur Grimmtotem, im Abgrund etwas Sonderbares gefunden haben soll, nur leider schaffte er es nicht mehr, lebend herauszukommen, um ihr davon zu berichten. Sie möchte, dass Maurs Leiche - falls sie noch existiert - geborgen wird...und auch jegliche Hinweise auf das, was er entdeckt haben könnte, als er versuchte, mit den Troggs zu sprechen.]], [[Bringt Magatha Gegenstände, die sie interessieren könnten, dann wird sie sich bestimmt mit einer Belohnung nicht kleinlich zeigen.]], [[Begebt Euch noch einmal in den Abgrund im dunkelsten Teil von Orgrimmar.]], },
    { [[Sucht im Flammenschlund nach Maur Grimmtotems Leiche und durchsucht sie nach interessanten Gegenständen.]], },
  },
  [5723] = {
    [[Die Kraft des Feindes wird auf die Probe gestellt]],
    { [[Unter der Stadt Orgrimmar haben stämmige Kreaturen, als Troggs bekannt, damit begonnen, sich aus den Tiefen unterhalb der lavagefüllten Tunnel an die Oberfläche zu wühlen. Wie es ihrer grundgütigen Art entspricht, hatte Magatha Frieden mit diesen Kreaturen schließen wollen, doch die Troggs stürzten sich auf ihre Diplomaten und töteten sie. Magatha lässt sich eine solche Behandlung des Taurenvolks nicht gefallen und betrachtet die Kreaturen jetzt als eine Gefahr für die gesamte Horde.]], [[Sie bittet Euch, der Gefahr durch die Troggs ein Ende zu machen, bevor sie die Horde von unten überwältigen. Geht zum Flammenschlund und vernichtet sie alle.]], },
    { [[Sucht in Orgrimmar nach dem Flammenschlund, tötet dann 8 Flammenschlundtroggs und 8 Schamanen der Flammenschlundtroggs und kehrt anschließend zu Rahauro in Donnerfels zurück.]], },
  },
  [5724] = {
    [[Wiederbeschaffung des verloren gegangenen Ranzens]],
    { [[Unter dem Körper des Tauren findet Ihr einen Ranzen. Er hält ihn immer noch fest an sich gedrückt, als hätte er ihn beim Angriff der Troggs schützen wollen.]], [[Ihr befreit den Ranzen aus seinem Klammergriff und verstaut ihn bei Eurer eigenen Ausrüstung, dann bereitet Ihr Euch darauf vor, die Tunnel zu verlassen und zu Rahauro in Donnerfels zurückzukehren.]], },
    { [[Bringt den Grimmtotemranzen zu Rahauro nach Donnerfels.]], },
  },
  [5725] = {
    [[Die Macht zu Zerstören...]],
    { [[Viele Angehörige der Legion bedenken ihre Anhänger mit Geschenken, wie Ihr bestimmt wisst. Manche dieser Geschenke wären nach Ansicht der Dunklen Fürstin besser in der Hand von Leuten aufgehoben, die... weniger Visionen haben.]], [[Eine Sekte des Schattenrates, die sich die Sengende Klinge nennt und sich in den Höhlen unterhalb Orgrimmars versteckt, ist ein Musterbeispiel dafür. Die Fürstin sagte mir, dass Mitglieder der Sengenden Klinge in den Besitz zweier sehr mächtiger Zauberbücher gelangt seien... und sie möchte sich diese Bücher von ihnen 'borgen'. Ihr werdet sie ihr beschaffen.]], },
    { [[Bringt die Bücher 'Schattenzauber' und 'Zauberformeln aus dem Nether' zu Varimathras nach Unterstadt.]], },
  },
  [5726] = {
    [[Verborgene Feinde]],
    { [[Eines werde ich in meinen Reihen auf keinen Fall dulden, <name>: Verräter. Ich wäre allerdings töricht, würde ich meine Trümpfe einfach so ausspielen. Die Geschwüre der Verderbnis in unserem Land würden nicht tief genug herausgeschnitten und die Infektion würde sich noch verschlimmern.]], [[Aber Ihr, ein junger Abenteurer, könntet dorthin gehen, wo es meinen Agenten nicht möglich ist... könntet die Wahrheit ans Licht bringen... herausfinden, wer wirklich hinter dieser Bestie steckt.]], [[Wenn Ihr mutig genug seid, geht zur Knochenhöhle östlich von Orgrimmar, besorgt mir das Insigne eines Offiziers der Brennenden Klinge dort und kehrt zu mir zurück.]], },
    { [[Bringt ein Insigne des Leutnants zu Thrall nach Orgrimmar.]], },
  },
  [5727] = {
    [[Verborgene Feinde]],
    { [[Und jetzt lasst uns sehen, ob das Insigne, das Ihr gefunden habt, der Mühe wert ist.]], [[In der Stadt gibt es einen Hexenmeister, der seiner Meinung nach mein Vertrauen genießt. Er weiß allerdings nicht, dass ich gewahr bin, wem er wirklich die Treue hält. Tatsächlich ist er der Anführer der Brennenden Klinge. Aber stürzt Euch jetzt nicht übereilt in einen Kampf mit ihm. Er könnte nützlich sein und wir werden ihn gegen unsere Feinde einsetzen.]], [[Bringt dieses Insigne zu ihm in die Kluft der Schatten hier in Orgrimmar. Sprecht mit ihm, seht, ob er Euch für einen der Ihren hält, und kommt dann zu mir zurück.]], },
    { [[Bringt Neeru Feuerklinge das Insigne des Offiziers und sprecht mit ihm. Seht, ob er Euch glaubt, dass Ihr ein Mitglied der Brennenden Klinge seid, und kehrt dann zu Thrall nach Orgrimmar zurück.]], },
  },
  [5728] = {
    [[Verborgene Feinde]],
    { [[Hmmm, Anführer der Sengenden Klinge... das beunruhigt mich zutiefst. Wenn sie es sind, die für Neeru zählen, dann sollten sie auch unser erstes Ziel sein. Dieser Satyr... Bazzalan, und der andere, den Neeru erwähnte... Was war er doch gleich, ein Hexenmeister? Sie müssen vernichtet werden.]], [[Kehrt zur Kluft der Schatten zurück und begebt Euch in den Flammenschlund, <name>. Findet diese beiden Anführer der Sengenden Klinge und tötet sie. Aber seid vorsichtig, damit Neeru nicht erfährt, wer das getan hat. Er muss Euch auch weiterhin für <seinen Kampfgenossen/seine Kampfgenossin> halten.]], },
    { [[Tötet Bazzalan und Jergosh den Herbeirufer, bevor Ihr zu Thrall nach Orgrimmar zurückkehrt.]], },
  },
  [5729] = {
    [[Verborgene Feinde]],
    { [[Ich glaube, unser nächster Schritt sollte sein, Euch in Neerus Nähe unterzubringen. Falls er wirklich so beunruhigt ist, wie die Berichte vermuten lassen, könnten wir zwei Fliegen mit einer Klappe schlagen. Erstens könntet Ihr mehr über den Rat oder zumindest die Sengende Klinge in Erfahrung bringen, und zweitens könnte er Euch möglicherweise genug vertrauen, um Euch zu bitten, ihm zu helfen. Vielleicht wird er Euch für jemanden halten, der die Lücken füllt, die der Tod der Anführer gerissen hat.]], [[Kehrt zur Kluft zurück und sprecht noch einmal mit ihm, aber haltet Euch bedeckt.]], },
    { [[Sprecht mit Neeru Feuerklinge in Orgrimmar.]], },
  },
  [5730] = {
    [[Verborgene Feinde]],
    { [[Ihr seid einer meiner Offiziere! Macht Euch bereit, <class>. Ich werde Euch bald benötigen.]], [[Die Zeit, die ich für die Sengende Klinge aufgebracht hatte, war jetzt vielleicht reine Zeitverschwendung, aber das bedeutet nicht, dass die restlichen Pläne des Schattenrates darunter leiden müssen. Ich werde tun, was ich kann, um den Schaden hier im Flammenschlund zu begrenzen. In der Zwischenzeit sollen meine Agenten im Brachland und im Eschental mit der Arbeit an unserem neuesten Geheimplan beginnen. Kommt bald zu mir zurück.]], },
    { [[Sprecht in Orgrimmar mit Thrall und berichtet ihm, was Ihr erfahren habt.]], },
  },
  [5741] = {
    [[Szepter des Lichts]],
    { [[Besorgt das Szepter des Lichts und bringt es zurück zu Azore Aldamort am Turm in Ethel Rethor.]], },
    { [[Besorgt das Szepter des Lichts und bringt es zurück zu Azore Aldamort am Turm in Ethel Rethor.]], },
  },
  [5742] = {
    [[Erlösung]],
    { [[Hört Euch an, was Tirion Fordring zu sagen hat.]], },
    { [[Hört Euch an, was Tirion Fordring zu sagen hat.]], },
  },
  [5761] = {
    [[Vernichtung der Bestie]],
    { [[Die vorrangige Aufgabe, die mir unser großer Kriegshäuptling übertragen hat, ist die Vernichtung der Kreaturen, die dafür verantwortlich sind, dass sich der dämonische Einfluss in der großen Stadt unseres Herrschers einnisten konnte. Die Kultisten der Brennenden Klinge sind eine Gefahr, aber es gibt auch noch andere. Die Anhänger der Sengenden Klinge zum Beispiel, die sich im Flammenschlund niedergelassen haben, versuchen insgeheim unschuldige Mitglieder der Horde auf ihre Seite zu ziehen.]], [[Um sie aufzuhalten, muss ihr Anführer beseitigt werden - eine Teufelswache namens Taragaman der Hungerleider.]], [[Tötet ihn, und sein Herz wird Thrall beschwichtigen, dessen bin ich mir sicher.]], },
    { [[Begebt Euch in den Flammenschlund und erschlagt Taragaman den Hungerleider. Bringt anschließend dessen Herz zu Neeru Feuerklinge nach Orgrimmar.]], },
  },
  [5762] = {
    [[Hemet Nesingwary Jr.]],
    { [[Ich habe hier eine Sendung für einen alten Kunden von mir, Hemet Nesingwary Jr., ein Zwerg. Die Sendung war wochenlang unterwegs und Hemet ist schon längst wieder weg. Er sagte, dass er ins Schlingendorntal gehen wolle, um die Bestien dort zu jagen, aber er ließ mir etwas Geld da und wollte, dass ich ihm seine Sendung schicke, sobald es mir möglich sei.]], [[Junior ist ein reicher Zwerg und man sollte sich mit den Reichen ruhig gut stellen, findet Ihr nicht auch? Also... könntet Ihr die Sendung für mich zustellen?]], [[Wie ich hörte, hat er im Schlingendorntal nördlich von Grom'gol ein Lager aufgeschlagen.]], },
    { [[Take Kravel's Crate to Hemet Nesingwary Jr. in Stranglethorn.]], },
  },
  [5763] = {
    [[Jagd im Schlingendorntal]],
    { [[Bringt Roons Kodohorn zu Hemet Nesingwary Jr. ins Schlingendorntal.]], },
    { [[Bringt Roons Kodohorn zu Hemet Nesingwary Jr. ins Schlingendorntal.]], },
  },
  [5781] = {
    [[Von vergessenen Erinnerungen]],
    { [[Wenn Ihr Taelan helfen wollt, zurückzubekommen, was er verloren hat, müsst Ihr Gegenstände aus seiner Vergangenheit zusammentragen.]], [[Der Erste dieser Gegenstände ist ein Spielzeug, das ich ihm zu seinem 7. Geburtstag schenkte. Es war sein wertvollster Besitz. Ein winziger Kriegshammer, die exakte Kopie meines eigenen.]], [[Als man mich wegen Verrats verbannte, erzählte ihm seine Mutter, dass ich gestorben sei. Man brachte ihn zu meinem angeblichen Grab beim Tiefgewölbe, wo er den Hammer zusammen mit seiner Erinnerung an mich begrub - für immer.]], [[Ihr müsst Euch zum Tiefgewölbe begeben und Taelans Hammer holen.]], },
    { [[Begebt Euch zum Tiefgewölbe im südlichsten Abschnitt der Östlichen Pestländer und sucht nach Taelans Hammer. Kehrt zu Tirion Fordring zurück, sobald Ihr Eure Mission abgeschlossen habt.]], },
  },
  [5801] = {
    [[Feuersäulen geschmiedet]],
    { [[Ihr braucht zwei Barren Thorium und eine kräftige und sehr heiße Wärmequelle, um den Schaft des Schlüssels zu schmieden. Das Thorium müsst Ihr Euch selbst besorgen, aber mit der Wärmequelle kann ich Euch helfen.]], [[Westlich von hier im Krater von Un'Goro liegt der Feuersäulengrat - ein brodelnder Vulkan. Der Lavasee an seiner Spitze ist heiß genug, um Thorium und Knochen miteinander zu verschmelzen. Ihr habt mit Eurem Gold eine Form erstanden, die Ihr einfach nur in den See tauchen müsst. Die Lava erledigt dann den Rest.]], [[Das war's von meiner Seite. Um Euren Schlüssel fertig zu stellen, müsst Ihr noch einmal mit Arbington sprechen.]], },
    { [[Bringt die Dietrichform und 2 Barren Thorium zur Spitze des Feuersäulengrats im Krater von Un'Goro. Benutzt die Dietrichform am Lavasee, um den unvollendeten Dietrich zu schmieden.]], [[Bringt den unvollendeten Dietrich dann zum Alchemisten Arbington an der Zugwindspitze in den Westlichen Pestländern.]], },
  },
  [5802] = {
    [[Feuersäulen geschmiedet]],
    { [[Ihr braucht zwei Barren Thorium und eine kräftige und sehr heiße Wärmequelle, um den Schaft des Schlüssels zu schmieden. Das Thorium müsst Ihr Euch selbst besorgen, aber mit der Wärmequelle kann ich Euch helfen.]], [[Westlich von hier im Krater von Un'Goro liegt der Feuersäulengrat - ein brodelnder Vulkan. Der Lavasee an seiner Spitze ist heiß genug, um Thorium und Knochen miteinander zu verschmelzen. Ihr habt mit Eurem Gold eine Form erstanden, die Ihr einfach nur in den See tauchen müsst. Die Lava erledigt dann den Rest.]], [[Das war's von meiner Seite. Um Euren Schlüssel fertig zu stellen, müsst Ihr noch einmal mit Zitters sprechen.]], },
    { [[Bringt die Dietrichform und 2 Barren Thorium zur Spitze des Feuersäulengrats im Krater von Un'Goro. Benutzt die Dietrichform am Lavasee, um den unvollendeten Dietrich zu schmieden.]], [[Bringt den unvollendeten Dietrich zu Apotheker Zitters im Bollwerk in den Westlichen Pestländern.]], },
  },
  [5803] = {
    [[Arajs Skarabäus]],
    { [[Araj der Beschwörer herrscht von dem ruinierten Herzen der Stadt aus über Andorhal. Wir wissen, dass er der ehemalige Kopf der Scholomance war, bevor er die Stadt an sich riss. Um den Schlüssel fertig zu stellen, benötigen wir sein Signet - Skarabäus genannt. Es wird den Kopf des Schlüssels bilden.]], [[Macht Euch auf den Weg ins Herz von Andorhal und brecht mit seiner Herrschaft über die Stadt, jetzt wo er für uns erreichbar ist. Nehmt den Skarabäus aus seinem Phylakterium, sobald er vernichtet ist!]], [[Der Schlüssel zu Scholomance erwartet Euch, <name>!]], },
    { [[Zerstört Araj den Beschwörer und bringt Arajs Skarabäus zum Alchemisten Arbington an der Zugwindspitze in den Westlichen Pestländern.]], },
  },
  [5804] = {
    [[Arajs Skarabäus]],
    { [[Araj der Beschwörer herrscht von dem ruinierten Herzen der Stadt aus über Andorhal. Wir wissen, dass er der ehemalige Kopf der Scholomance war, bevor er die Stadt an sich riss. Um den Schlüssel fertig zu stellen, benötigen wir sein Signet - Skarabäus genannt. Es wird den Kopf des Schlüssels bilden.]], [[Macht Euch auf den Weg ins Herz von Andorhal und brecht mit seiner Herrschaft über die Stadt, jetzt wo er für uns erreichbar ist. Nehmt den Skarabäus aus seinem Phylakterium, sobald er vernichtet ist!]], [[Der Schlüssel zu Scholomance erwartet Euch, <name>!]], },
    { [[Vernichtet Araj den Beschwörer und bringt Arajs Skarabäus zum Apotheker Zitters im Bollwerk, in den Westlichen Pestländern.]], },
  },
  [5805] = {
