  [5845] = {
    [[Von verlorener Ehre]],
    { [[Der Orden der Silbernen Hand wurde gar sehr geschwächt, als Uther sein gewaltsames Ende fand.]], [[Der Junge hat durchgehalten, solange er konnte. Er wurde bis zum vom Krieg zerrissenen Weiler Nordtal zurückgetrieben, und dort stellte er sich zu seinem letzten Kampf.]], [[War noch irgendjemand aus dem Orden am Leben, fragte er sich - und spielte das überhaupt noch eine Rolle?]], [[Mit diesem Gedanken im Kopf schmetterte Taelan die Standarte des Ordens zu Boden und entsagte allem, was er gelernt hatte. Auf dem blutgetränkten Boden des Nordtals ließ er seine Ehre zurück.]], [[Ihr müsst ins Nordtal reisen und das Symbol der verlorenen Ehre finden.]], },
    { [[Reist ins Nordtal im nordöstlichsten Gebiet der Östlichen Pestländer und sucht nach dem Symbol der verlorenen Ehre. Kehrt zu Tirion Fordring zurück, sobald Ihr Eure Mission abgeschlossen habt.]], },
  },
  [5846] = {
    [[Von Liebe und Familie]],
    { [[Als Taelan noch ein Kind war, unternahmen wir mit der Familie oft Ausflüge nach Darrowehr. Bei unserem letzten Besuch malte eine Künstlerin namens Renfray ein Porträt von uns, wie wir am Strand posierten. Das ist die schönste Erinnerung, die ich an Taelan und Karandra habe. Denn es war just dieser Moment, wo ich meine Frau und meinen Sohn in den Armen hielt, dass ich eine Liebe und Verbundenheit mit meiner Familie spürte, die ich seitdem nie wieder erlebt habe.]], [[Falls dieses Gemälde noch existiert, müsst Ihr es finden.]], [[Begebt Euch zur in Trümmern liegenden Insel Darrowehr und seht nach, ob das Gemälde der Künstlerin noch da ist.]], },
    { [[Begebt Euch zur Insel Darrowehr im südlich-zentralen Bereich der Pestländer und sucht nach Hinweisen, wo sich das Gemälde befinden könnte.]], },
  },
  [5847] = {
    [[Willkommen!]],
    { [[Bringt Claire Weidner in Brill den Geschenkgutschein von Brill.]], },
    { [[Bringt Claire Weidner in Brill den Geschenkgutschein von Brill.]], },
  },
  [5848] = {
    [[Von Liebe und Familie]],
    { [[Das Gemälde... Es hing jahrelang an der Wand meiner Werkstatt - in der Kaserne des Ordens.]], [[Nachdem Tirions Prüfung vorüber war, wusste ich, dass ich das Gemälde nicht mehr dort für alle sichtbar hängen lassen konnte. So versteckte ich es an einem Ort, an dem es niemand vermuten würde.]], [[Geht nach Stratholme und begebt Euch tief in die heutige Scharlachrote Bastion. Sucht dort nach einem Gemälde, auf dem unsere Zwillingsmonde abgebildet sind. Kratzt die Farbe weg, bis mein Meisterwerk zum Vorschein kommt, 'Von Liebe und Familie'.]], [[Möge das Licht Eure Taten leiten.]], },
    { [[Begebt Euch nach Stratholme im nördlichen Teil der Pestländer. In der Scharlachroten Bastion findet Ihr das Gemälde 'Von Liebe und Familie', das zwischen anderen Gemälden versteckt ist und auf dem die Zwillingsmonde unserer Welt abgebildet sind.]], [[Bringt das Gemälde zu Tirion Fordring.]], },
  },
  [5861] = {
    [[Myranda suchen]],
    { [[Ihr habt alles getan, um das ich Euch bislang bat, <name>. Jetzt fehlt nur noch ein Schritt auf Eurem Weg zur Erlösung.]], [[Ihr müsst die gesammelten Gegenstände Taelan aushändigen. Leider werden Euch Taelan und seine Scharlachroten Kreuzzügler angreifen, sobald sie Euer ansichtig werden.]], [[Es gibt nur eine Möglichkeit, meine Nachricht zu übermitteln, und zwar durch Täuschung.]], [[Im Süden findet Ihr Uthers Grabmal. Myranda, eine alte und getreue Vertraute von mir, lebt jetzt dort - geht zu ihr. Zeigt ihr die Gegenstände, dann wird sie Euch helfen.]], },
    { [[Begebt Euch zu Uthers Grabmal in den Südlichen Pestländern. Zeigt Myranda der Vettel Tirions Geschenk.]], },
  },
  [5862] = {
    [[Scharlachrote Täuschung]],
    { [[Ich bin eine Illusionistin, wie Ihr das nennen würdet. Ich könnte zwar eine Illusion erschaffen, die es Euch ermöglichen würde, Herdweiler zu betreten, aber seid gewarnt, meine Kräfte haben Grenzen.]], [[Solltet Ihr Euch zu weit aus diesem Land entfernen, lassen die Effekte dieser Illusion nach. Der Zauber selbst bedarf Unmengen an Konzentration und Kraft meinerseits, und deshalb kann ich den Effekt nur für kurze Zeit aufrechterhalten.]], [[Kommt wieder zu mir, wenn Ihr bereit seid.]], },
    { [[Sprecht mit Myranda, um die Scharlachrote Illusion zu erhalten. Begebt Euch nach Herdweiler, während Ihr unter dem Einfluss der Scharlachroten Illusion steht, und übergebt dem Hochlord Taelan Fordring Tirions Geschenk.]], [[Herdweiler liegt im Norden, Taelan dürfte sich in der Burg Mardenholde aufhalten.]], },
  },
  [5863] = {
    [[Das Truppenlager der Dünenbrecher]],
    { [[Erst kürzlich ist eine große Gruppe nomadischer Oger in Tanaris eingetroffen. Südlich von hier haben sie einen großen Bereich der Wüste in Besitz genommen und ein Truppenlager aufgeschlagen. Mehrere kleinere Gruppen durchstreifen momentan die Wüste, einige davon sind in die alten Trollruinen eingedrungen.]], [[Das Truppenlager der Dünenbrecher muss zerstört werden! Wir dürfen nicht zulassen, dass diese Oger zu einer Streitmacht heranwachsen.]], [[Der Anführer der Gruppe heißt Gor'marok der Verheerer. Ich bin mir sicher, dass es den Anfang vom Ende für die Oger bedeuten würde, wenn man ihnen den Anführer nimmt.]], },
    { [[Andi Lynn in Gadgetzan möchte, dass Ihr das Truppenlager der Dünenbrecher vernichtet, indem Ihr 10 Schläger der Dünenbrecher 10 Vollstrecker der Dünenbrecher sowie Gor'marok den Verheerer tötet.]], },
  },
  [5881] = {
    [[Hinzurufen der Reserve]],
    { [[Die Horde kümmert sich gut um ihre Truppen... seit dem großen Krieg ging es uns hier in Kalimdor sehr gut.]], [[Ihr müsst mir einen Gefallen tun. Ich lasse einen Jungen namens Grish Fernläufer den Großen Aufzug im Südlichen Brachland bewachen. Gebt ihm diesen Brief, er wird ihn wissen lassen, dass ich einen Ersatzmann angefordert habe, um ihn von seinem Dienst zu entbinden, damit er seine Familie besuchen kann. Er bat mich vor einer Weile darum und wird sich über diese Nachricht freuen.]], },
    { [[Maggran möchte, dass Ihr Grish Fernläufer am Großen Aufzug im Brachland einen Brief zustellt.]], },
  },
