  [5892] = {
    [[Vorräte der Eisenschachtmine]],
    { [[Bevor die Schlacht begann, lagerten wir Vorräte für Dun Baldar in der Eisenschachtmine. Die Troggs, die dort hausten, waren kein Problem solange wir Ersatztruppen hatten, die die Mine bewachen konnten, aber da wir nun gegen die Horde kämpfen, können wir sie nicht mehr kontrollieren.]], [[Wir brauchen die Vorräte in der Eisenschachtmine, <name>. Betretet die Mine und bring mir eine Ladung.]], },
    { [[Bringt 10 Vorräte der Eisenschachtmine zum Rüstmeister der Allianz in Dun Baldar.]], },
  },
  [5893] = {
    [[Vorräte der Eisbeißermine]],
    { [[Die Eisbeißermine im Norden war einst ein idealer Ort, um zusätzliche Vorräte zu lagern, aber da die Kämpfe mit den Sturmlanzen nun härter werden, haben wir nicht immer die notwendigen Soldaten über, um sie an der Mine zu positionieren und diese zu bewachen. Daher fällt die Kontrolle über diese Mine oft an die Allianz oder an Untergrundvölker...]], [[Aber wir benötigen dennoch unsere Vorräte, <name>! Geht zu der Eisbeißermine und bringt mir eine Ladung. Seid wachsam, was unsere Feinde anbelangt, die Übermacht in der Schlacht kann sich an jenem Ort schnell ändern...]], },
    { [[Bringt 10 Vorräte der Eisbeißermine zum Rüstmeister der Horde in die Burg Frostwolf.]], },
  },
  [5901] = {
    [[Eine Plage komme über Euch]],
    { [[Also, um Schmerz und Unfrieden über den Scharlachroten Kreuzzug zu bringen, möchte ich ihnen ihr wertvolles Sägewerk wegnehmen. Ich bin mir sicher, der Verlust einer solchen Ressource wird ihnen ziemlichen Schaden in Bezug auf Verteidigung und Wiederaufbau der Stadt für ihre Zwecke zufügen. Werdet Ihr mir helfen, <name>?]], [[Fangt an, indem Ihr diesen Krug zum Pestwald in den Östlichen Pestländern bringt. Füllt ihn mit den Termiten, die Ihr in ihren Hügeln überall im Wald findet. Kommt wieder, wenn Ihr eine ausreichende Menge habt.]], },
    { [[Wendet den leeren Termitenkrug auf die Termitenhügel in den Östlichen Pestländern an. Sobald Ihr 100 Pestlandtermiten gesammelt habt, kehrt zu Mickey Levine am Bollwerk in Tirisfal zurück.]], },
  },
  [5902] = {
    [[Eine Plage komme über Euch]],
    { [[Da wir jetzt jede Menge Termiten haben, bringt dieses stahlverstärkte Fass zum Sägewerk des Nordkamms südlich von Herdweiler. Ihr werdet es westlich der Straße finden, die Euch in die Stadt hinauf führt.]], [[Wenn Ihr dort seid, stellt Ihr das Fass in die Mitte des Sägewerkes, irgendwo weit oben, vielleicht auf eine Kiste oder so was.]], [[Nachdem Ihr es geöffnet habt, sollte sich der Rest von selbst erledigen. Kommt einfach hierher zurück und ich werde Euch dafür belohnen, dass Ihr mir geholfen habt.]], },
    { [[Sucht Euch einen geeigneten Ort in der Mitte des Sägewerk des Nordkamms in den Westlichen Pestländern, um das Fass mit den Pestlandtermiten abzustellen.]], },
  },
  [5903] = {
    [[Eine Plage komme über Euch]],
    { [[Ich habe bereits viele Male versucht, den Scharlachroten Kreuzzug allein mit Gewalt auszulöschen, doch ohne Erfolg. Daher habe ich mich entschlossen, ihnen das Leben schwer zu machen.]], [[Wenn Ihr bereit seid zu helfen, möchte ich, dass Ihr dieses Sägewerk mit der übelsten Termitenart verseucht, die je gefunden wurde.]], [[Im Pestwald, nahe dem einst großen Stratholme, gibt es eine Spezies, die die Seuche und auch die Geißel überlebt hat. Sucht ihre Hügel und füllt diesen Krug mit ihnen, damit wir sie darauf vorbereiten können, auf den Kreuzzug losgelassen zu werden.]], },
    { [[Wendet den leeren Termitenkrug auf die Termitenhügel in den Östlichen Pestländern an. Sobald Ihr 100 Pestlandtermiten gesammelt habt, kehrt zu Nathaniel Dumah im Zugwindlager in den Westlichen Pestländern zurück.]], },
  },
  [5904] = {
    [[Eine Plage komme über Euch]],
    { [[Da wir jetzt die Termiten haben, bringt dieses stahlverstärkte Fass zum Sägewerk des Nordkamms südlich von Herdweiler. Ihr werdet es westlich der Straße finden, die Euch in die Stadt hinauf führt.]], [[Wenn Ihr dort seid, stellt Ihr das Fass in die Mitte des Sägewerkes. Findet einen geeigneten Ort, vielleicht eine Kiste, und öffnet es dann. Die Termiten dürften dann ihre Arbeit tun und die Mühle für den Kreuzzug unbrauchbar machen.]], },
    { [[Sucht Euch einen geeigneten Ort in der Mitte des Sägewerk des Nordkamms in den Westlichen Pestländern, um das Fass mit den Pestlandtermiten abzustellen.]], },
  },
  [5921] = {
    [[Mondlichtung]],
    { [[Alle Druiden sind in den hochheiligen Landen der Mondlichtung willkommen, ungeachtet der derzeitigen Spannungen im Zirkel des Cenarius zwischen dem Erzdruiden Hirschhaupt und dem Bewahrer Remulos, dem Wächter der Mondlichtung. Jetzt, da Ihr den Weg der Klaue lernt, bringe ich Euch den Zauber bei, der es Euch ermöglicht, Euch selbst dorthin zu befördern. Wirkt ihn, wann immer Ihr das Wissen der Mondlichtung oder Zuflucht sucht.]], [[Als Eure erste Lektion müsst Ihr zur Mondlichtung reisen und mit Dendrite Sternenschauer in Nachthafen über den Pfad sprechen, auf dem Ihr jetzt wandelt. Viel Glück, <junger Druide/junge Druidin>.]], },
    { [[Benutzt den Zauber 'Teleportation: Mondlichtung', um zur Mondlichtung zu reisen. Wenn Ihr dort ankommt, sprecht mit Dendrite Sternenschauer in Nachthafen.]], },
  },
  [5922] = {
    [[Mondlichtung]],
    { [[Alle Druiden sind in den hochheiligen Landen der Mondlichtung willkommen, Tauren wie Nachtelfen. Bewahrer Remulos - der Wächter der Mondlichtung - wacht über alle Druiden, die hier ihren Winterschlaf halten. Jetzt, da Ihr den Weg der Klaue lernt, bringe ich Euch den Zauber bei, der es Euch ermöglicht, Euch selbst dorthin zu befördern. Wirkt ihn, wann immer Ihr das Wissen der Mondlichtung oder Zuflucht sucht.]], [[Als Eure erste Lektion müsst Ihr zur Mondlichtung reisen und mit Dendrite Sternenschauer in Nachthafen über den Pfad sprechen, auf dem Ihr jetzt wandelt. Viel Glück, <junger Druide/junge Druidin>.]], },
    { [[Benutzt den Zauber 'Teleportation: Mondlichtung', um zur Mondlichtung zu reisen. Wenn Ihr dort ankommt, sprecht mit Dendrite Sternenschauer in Nachthafen.]], },
  },
  [5923] = {
    [[Dem Ruf folgen]],
    { [[Entschuldigung...]], [[Es ist mir zu Ohren gekommen, dass Mathrengyl Bärenfährte bekannt gegeben hat, dass er mit Euch sprechen möchte. Es scheint, dass Eure Ausbildung als Druide ein neues Gesicht bekommen soll und dass er Euch persönlich ausbilden will. Mathrengyl ist der direkte Assistent des Erzdruiden selbst und einer der beflissensten und erhabensten Druiden, die Ihr in Teldrassil finden könnt.]], [[Er befindet sich in der Enklave des Cenarius von Darnassus, im zweiten Stock von Fandral Hirschhaupts Baum.]], },
    { [[Sprecht mit Mathrengyl Bärenfährte in der Enklave von Cenarius in Darnassus.]], },
  },
