  [6081] = {
    [[Die Ausbildung des Tiers]],
    { [[Ihr habt jetzt die Macht, ein Tier zu zähmen, aber Ihr benötigt auch die Fertigkeiten, es auszubilden.]], [[Reist nach Orgrimmar. Dort müsst Ihr mit Ormak Grimmschlag sprechen, einem unserer angesehensten Jäger. Er wird Euch die Fähigkeit verleihen, Euer neues Tier auszubilden. Ihr findet ihn im Tal der Ehre.]], [[Viel Glück, <name>.]], },
    { [[Sprecht mit Ormak Grimmschlag in Orgrimmar.]], },
  },
  [6082] = {
    [[Die Zähmung der Bestie]],
    { [[Benutzt die Zähmrute, um einen gepanzerten Skorpid zu zähmen. Übt Eure Fertigkeiten und kehrt zu Thotar in Klingenhügel zurück.]], },
    { [[Benutzt die Zähmrute, um einen gepanzerten Skorpid zu zähmen. Übt Eure Fertigkeiten und kehrt zu Thotar in Klingenhügel zurück.]], },
  },
  [6083] = {
    [[Die Zähmung der Bestie]],
    { [[Der Vorteil, den Euch ein Tier gegen Eure Rivalen bietet, wird zur Kenntnis genommen, <name>! Wenn Ihr Kontrolle über eine andere Kreatur erlangt, erhöht Ihr Eure eigene Kraft. Euer Tier muss sozusagen Euer verlängerter Arm werden.]], [[Die nächste Aufgabe führt Euch vom Land weg. Ihr findet die nächste Kreatur in den Gewässern vor der Küste. Sucht jenseits von Sen'jin nahe den Echoinseln. Sucht nach einem lebhaften Krebs, der Brandungskriecher genannt wird. Benutzt die Zähmrute, um ihn zu zähmen und übt dann Eure Fertigkeiten.]], },
    { [[Benutzt die Zähmrute, um einen Brandungskriecher zu zähmen. Übt Eure Fertigkeiten und bringt die Zähmrute dann zu Thotar in Klingenhügel.]], },
  },
  [6084] = {
    [[Die Zähmung der Bestie]],
    { [[Diese Klippeneber sind recht bullig. Vielleicht möchtet Ihr etwas mit ein wenig mehr Grazie? Na gut, da hab ich genau das Richtige. Südlich und östlich von hier findet Ihr große weiße Katzen, die man Schneeleoparden nennt. Genau wie bei einem elegant geschnitzten Gewehr verbirgt sich hinter dem wunderschönen Äußeren ein kräftiger Kämpfer, der extremen Schaden zufügen kann. Ihr werdet feststellen, dass sie wesentlich agiler und schneller als Euer gezähmter Eber sind.]], [[Nehmt diese Zähmrute und findet heraus, wie Euch der Schneeleopard gefällt.]], },
    { [[Benutzt die Zähmrute, um einen Schneeleoparden zu zähmen. Übt Eure Fertigkeiten und kehrt dann zu Grif Wildherz in Kharanos zurück.]], },
  },
  [6085] = {
    [[Die Zähmung der Bestie]],
    { [[Benutzt die Zähmrute, um einen Eisklauenbären zu zähmen. Übt Eure Fertigkeiten und bringt die Zähmrute dann zu Grif Wildherz in Kharanos.]], },
    { [[Benutzt die Zähmrute, um einen Eisklauenbären zu zähmen. Übt Eure Fertigkeiten und bringt die Zähmrute dann zu Grif Wildherz in Kharanos.]], },
  },
  [6086] = {
    [[Die Ausbildung des Tiers]],
    { [[Sprecht mit Belia Donnerstein in Eisenschmiede.]], },
    { [[Sprecht mit Belia Donnerstein in Eisenschmiede.]], },
  },
  [6087] = {
    [[Die Zähmung der Bestie]],
    { [[Ihr besitzt einen feurigen Geist, Jäger, und brennt schon auf Eure nächste Herausforderung.]], [[Als nächste Aufgabe müsst Ihr einen Wolf, der als Präriepirscher bekannt ist, zum Gefährten gewinnen. Diese Kreatur ist flink und furchtlos. Wenn Ihr diese Bestie als Euer Tier wollt, müsst Ihr diese Eigenschaften ebenfalls erlangen.]], [[Im Kampf werdet Ihr lernen, wie Ihr und Euer Tier ein Team werdet. Letztendlich müsst Ihr wie ein Wesen handeln, Muskel an Muskel.]], [[Hier habt Ihr die Zähmrute, <name>. Zähmt einen Präriepirscher und übt Eure Fertigkeiten.]], },
    { [[Benutzt die Zähmrute, um einen Präriepirscher zu zähmen. Übt Eure Fertigkeiten und bringt die Zähmrute dann zu Yaw Scharfmähne ins Dorf der Bluthufe.]], },
  },
  [6088] = {
    [[Die Zähmung der Bestie]],
    { [[Benutzt die Zähmrute, um einen Sturzflieger zu zähmen. Übt Eure Fertigkeiten und bringt die Zähmrute dann zu Yaw Scharfmähne ins Dorf der Bluthufe.]], },
    { [[Benutzt die Zähmrute, um einen Sturzflieger zu zähmen. Übt Eure Fertigkeiten und bringt die Zähmrute dann zu Yaw Scharfmähne ins Dorf der Bluthufe.]], },
  },
  [6089] = {
    [[Die Ausbildung des Tiers]],
    { [[Sprecht mit Holt Donnerhorn auf der Anhöhe der Jäger in Donnerfels.]], },
    { [[Sprecht mit Holt Donnerhorn auf der Anhöhe der Jäger in Donnerfels.]], },
  },
  [6101] = {
    [[Die Zähmung der Bestie]],
    { [[Auf die Vielfalt der Natur kann man sich verlassen, <name>. Sie ist insofern vorhersehbar, dass sie niemals gleich bleibt. Sie ist in konstantem Wandel.]], [[Die Aufgaben, die ich Euch gebe, werden Euch helfen, die enormen Unterschiede der Kreaturen zu erkennen, die Ihr als Eure Gefährten wählen könnt.]], [[Nehmt diese Zähmrute und zähmt damit einen der pirschenden Nachtsäbler, die im Süden zu finden sind. Übt Eure Fertigkeiten als Jäger. Dann werdet Ihr die Übereinstimmungen und Unterschiede der Natur begreifen.]], },
    { [[Benutzt die Zähmerrute, um einen pirschenden Nachtsäbler zu zähmen. Übt Eure Fertigkeiten und bringt die Zähmrute dann zu Dazalar in Dolanaar.]], },
  },
  [6102] = {
    [[Die Zähmung der Bestie]],
    { [[Ihr habt zwei Kreaturen gezähmt, die am Boden leben, jetzt müsst Ihr eine zähmen, die am Himmel dahinzieht. Benutzt die Zähmrute, um einen Strigidkreischer zu zähmen, die man westlich von hier findet. Übt Eure Fertigkeiten mit dieser mächtigen Eule an Eurer Seite.]], [[Wenn Ihr zurückkehrt gebe ich Euch die Fertigkeiten, die Ihr braucht, um ein Tier Eurer Wahl als Gefährten zu zähmen. Diese Kreatur wird denselben Herausforderungen trotzen wie Ihr und an Eurer Seite Erfahrung gewinnen. Ich zeige Euch auch, wie man es nach Belieben ruft und wegschickt.]], },
    { [[Benutzt die Zähmrute, um einen Strigidkreischer zu zähmen. Übt Eure Fertigkeiten und bringt die Zähmrute dann zu Dazalar in Dolanaar.]], },
  },
  [6103] = {
    [[Die Ausbildung des Tiers]],
    { [[Ihr habt jetzt die Macht, ein Tier zu zähmen, aber Ihr benötigt auch die Fertigkeiten, es zu trainieren.]], [[Reist nach Darnassus. Dort müsst Ihr mit Jocaste sprechen, einer unserer angesehensten Jägerinnen. Sie gibt Euch die Fähigkeit, Euer neues Tier auszubilden, also sputet Euch.]], [[Jocaste mag einen strengen Eindruck machen, freut sich aber stets, wenn sie den Aufstieg eines neuen Jägers verfolgen kann. Behandelt sie mit Respekt, dann wird sie Euch ebenso begegnen. Viel Glück, <name>.]], },
    { [[Sprecht mit Jocaste in der Enklave des Cenarius in Darnassus.]], },
  },
  [6121] = {
    [[Neue Lektionen]],
    { [[Ihr seid jetzt bereit, alles über Gifte und die Macht zu lernen, die wir Druiden darüber haben, <name>. Cenarius hat uns mit der Fähigkeit gesegnet, selbst die abscheulichsten Gifte mit einer Beschwörung auszutreiben. Mit dieser Gabe geht man nicht leichtfertig um...und damit Ihr diese Lektion lernt, müsst Ihr die Wirkung von Giften behandeln wie es ein Laie tun würde.]], [[Reist zur Mondlichtung und sprecht mit Dendrite Sternenschauer. Er erwartet Eure Ankunft, um die Lektion zu beginnen.]], },
    { [[Reist zur Mondlichtung und sprecht mit Dendrite Sternenschauer im Dorf Nachthafen.]], },
  },
  [6122] = {
    [[Die eigentliche Ursache]],
    { [[Die Klippenquellfälle in Dunkelküste dienen als wichtigste Trinkwasserquelle in der Region. Ich fürchte, jetzt ist es die primäre Ursache des Gifts, das der dortigen Fauna schadet, besonders dem Wild. Holt mit diesem Gefäß etwas Wasser vom Höhleneingang, aber seid vorsichtig, inzwischen hausen gefährliche Naga in einer Höhle bei den Wasserfällen.]], [[Wenn Ihr es gefüllt habt, bringt es zu Alanndarian Nachtweise in Auberdine. Sie bat um unsere Hilfe und Ihr werdet Euch gemeinsam mit ihr dieses Übels annehmen.]], },
    { [[Benutzt das leere Gefäß bei den Klippenquellfällen und nehmt eine Wasserprobe vom Höhleneingang bei den Wasserfällen.]], [[Bringt die gefüllte Probe der Klippenquellfälle zu Alanndarian Nachtweise nach Auberdine in Dunkelküste.]], },
  },
  [6123] = {
    [[Das Heilmittel sammeln]],
    { [[Das Wasser wird Grundlage einer Heilsalbe sein, die ich für das Wild anrühre. Für die Heilwirkung benötige ich etwas Erdwurzel. Kräuterkundige wissen, wo man sie finden kann. Wenn wir Pech haben und Ihr keiner seid, müsst Ihr einen suchen, der uns weiterhilft.]], [[Und der besseren Wirksamkeit wegen brauche ich etwas Lunarfungus. Es ist recht leicht, Lunarfungusblüten zu finden. In den Höhlen östlich von hier gedeihen sie reichlich. Mondkin, die neuerdings recht gereizt sind, hausen in diesen Höhlen, also seid vorsichtig.]], },
    { [[Bringt 5 Erdwurzeln und 12 Lunarfungi zu Alanndarian Nachtweise in Auberdine, damit sie eine Heilsalbe machen kann.]], },
  },
  [6124] = {
    [[Heilung der Kranken]],
    { [[Die Salbe, die ich aus den Reagenzien herstellen konnte, die Ihr mir gebracht habt, reicht für zehn Dosen, <name>. Ich arbeite weiter mit den Dorfbewohnern zusammen und versuche mehr Salbe herzustellen. Derweil möchte ich, dass Ihr uns einen Vorsprung verschafft. Rund um Dunkelküste wimmelt es von krankem Wild. Heilt es mit der Salbe von seinem Übel.]], [[Dendrite Sternenschauer bei der Mondlichtung wird ganz sicher mit Euch reden wollen, wenn Ihr fertig seid. Bitte übermittelt dem Zirkel des Cenarius Auberdines Dank... und lasst mich Euch persönlich für Eure Hilfe danken!]], },
    { [[Benutzt die Tier-Heilsalbe bei 10 kranken Wildtieren rund um Dunkelküste. Das müsste sie heilen. Es wurde berichtet, dass krankes Wild südlich des Klippenquells, im Norden von Auberdine und bis hinunter in die südlichen Regionen von Dunkelküste, wo das Eschental beginnt, aufgetaucht ist.]], },
  },
  [6125] = {
    [[Macht über Gift]],
    { [[Ihr habt gelernt, was es heißt, Vergiftungen ohne spirituelle Unterstützung zu heilen. Vergesst nicht, was andere auf sich nehmen müssen, wenn sie gegen Vergiftungen ankämpfen, wenn Ihr Eure neu erlangte Macht über Gift erhaltet.]], [[Ihr solltet jetzt zu Mathrengyl Bärenfährte in Darnassus zurückkehren, <name>. Zweifellos hat er Euch wichtige Zauber beizubringen, da Ihr nun die Lektionen begriffen habt, die Ihr erhieltet.]], [[Lebt wohl, junger Druide. Wir sprechen uns wieder.]], },
    { [[Kehrt zu Mathrengyl Bärenfährte in der Enklave des Cenarius in Darnassus zurück.]], },
  },
  [6126] = {
    [[Neue Lektionen]],
    { [[Ihr seid jetzt bereit, alles über Gifte und die Macht zu lernen, die wir Druiden darüber haben, <name>. Cenarius hat uns mit der Fähigkeit gesegnet, selbst die abscheulichsten Gifte mit einer Beschwörung auszutreiben. Mit dieser Gabe geht man nicht leichtfertig um...und damit Ihr diese Lektion lernt, müsst Ihr die Wirkung von Giften behandeln wie es ein Laie tun würde.]], [[Reist zur Mondlichtung und sprecht mit Dendrite Sternenschauer. Er erwartet Eure Ankunft, um die Lektion zu beginnen.]], },
    { [[Reist zur Mondlichtung und sprecht mit Dendrite Sternenschauer im Dorf Nachthafen.]], },
  },
  [6127] = {
    [[Die eigentliche Ursache]],
    { [[Der Glutnebelgipfel nordwestlich des Wegekreuzes ist höchstwahrscheinlich die Quelle, von wo aus sich das Gift verbreitet. Auf seinen luftigen Höhen gibt es Teiche, die das Wetter beeinflussen. Dieses Wasser wurde vermutlich durch Dämonen anbetende Kultisten der Brennenden Klinge verunreinigt.]], [[Entnehmt dem Teich auf der Spitze des Gipfels mit diesem Behältnis eine Probe. Wenn es gefüllt ist, bringt es zu Tonga Runentotem beim Wegekreuz. Er bat um unsere Hilfe und Ihr werdet Euch gemeinsam mit ihm dieses Übels annehmen.]], },
    { [[Benutzt die leere Glutnebelgipfelprobe beim Glutnebelgipfel und nehmt eine Wasserprobe vom Teich auf der Spitze des Gipfels.]], [[Bringt das gefüllte Behältnis vom Glutnebelgipfel zu Tonga Runentotem beim Wegekreuz im Brachland.]], },
  },
  [6128] = {
    [[Das Heilmittel sammeln]],
    { [[Das Wasser wird Grundlage einer Heilsalbe sein, die ich für die Gazellen herstelle. Für die Heilwirkung benötige ich etwas Erdwurzel. Kräuterkundige wissen, wo man sie finden kann. Wenn Ihr nicht mit dieser Kunst vertraut seid, müsst Ihr einen suchen, der uns weiterhilft.]], [[Der Wirksamkeit wegen brauche ich zuletzt Kodohörner – Splitter und Bruchstücke reichen aber nicht aus. Kodos, mit denen Ihr fertigwerdet, streifen durch den nördlichen Teil des Brachlands. Erlegt welche zum Wohle des Gleichgewichts, <name>, und bringt mir die Hörner.]], },
    { [[Bringt 5 Erdwurzeln und 5 Kodohörner zu Tonga Runentotem beim Wegekreuz, damit er eine Heilsalbe machen kann.]], },
  },
  [6129] = {
    [[Heilung der Kranken]],
    { [[Ich konnte genügend Salbe für zehn heilende Dosen machen, <name>. Ich arbeite weiter mit den hiesigen Behörden zusammen und versuche, mehr Salbe herzustellen. Derweil möchte ich, dass Ihr uns einen Vorsprung verschafft. Rund um das Nördliche Brachland wimmelt es von kranken Gazellen. Heilt sie mit der Salbe von ihrem Übel.]], [[Dendrite Sternenschauer bei der Mondlichtung wird ganz sicher mit Euch reden wollen, wenn Ihr fertig seid. Bitte übermittelt dem Zirkel des Cenarius unseren Dank... und lasst mich Euch persönlich für Eure Hilfe danken!]], },
    { [[Benutzt die heilende Tiersalbe bei 10 kränklichen Gazellen im nördlichen Teil des Brachlandes. Das müsste sie heilen. Kränkliche Gazellen wurden überall nördlich der Ost-West-Straße beim Wegekreuz gesehen.]], },
  },
  [6130] = {
    [[Macht über Gift]],
    { [[Ihr habt gelernt, was es heißt, Vergiftungen ohne spirituelle Unterstützung zu heilen. Vergesst nicht, was andere auf sich nehmen müssen, wenn sie gegen Vergiftungen ankämpfen, wenn Ihr Eure neu erlangte Macht über Gift erhaltet.]], [[Ihr solltet jetzt zu Turak Runentotem in Donnerfels zurückkehren, <name>. Zweifellos hat er Euch wichtige Zauber beizubringen, da Ihr nun die Lektionen begriffen habt, die Ihr erhieltet.]], [[Lebt wohl, junger Druide. Wir sprechen uns wieder.]], },
    { [[Kehrt zu Turak Runentotem auf der Anhöhe der Ältesten in Donnerfels zurück.]], },
  },
  [6131] = {
    [[Verbündeter der Holzschlundfeste]],
    { [[Die Furbolgs der Holzschlundfeste sind die einzigen Furbolgs, die von der Verderbnis verschont blieben. Aber viele andere Völker töten die Furbolgs jetzt blindwütig und scheren sich nicht darum, ob sie Freund oder Feind sind. Deswegen trauen die Einwohner der Holzschlundfeste auch kaum jemandem.]], [[Seid Ihr daran interessiert, Euch zu beweisen? Treibt den verderbten Stamm der Totenwaldfelle zurück, dann betrachten wir Euch eines Tages vielleicht als Verbündeten. Ihr findet die Krieger, Pfadfinder und Gärtner der Totenwaldfelle im Süden.]], },
    { [[Grazle möchte, dass Ihr Euch beweist, indem Ihr 5 Krieger der Totenwaldfelle, 5 Pfadfinder der Totenwaldfelle und 5 Gärtner der Totenwaldfelle besiegt.]], },
  },
  [6132] = {
    [[Holt mich hier raus!]],
    { [[Holt mich hier raus!]], [[Ich kam ins Tal der Speere, um mit den Maraudines zu sprechen. Ich dachte, ich könnte Geschäfte mit ihnen machen... aber sie reden einfach nicht mit mir! Ich glaube, sie möchten mich eher verspeisen!]], [[Holt mich hier raus, mein Bruder Hornizz wird es Euch reich vergelten! Er ist in unserem Lager neben dem Kodofriedhdof im Osten.]], [[Beeilen wir uns, <name>. Es ist keine zwei Minuten her, da hörte ich einige Zentauren sagen, dass sie Holz für ihren Kochtopf sammeln möchten...]], },
    { [[Eskortiert Melizza Brummsumms und sprecht anschließend mit Hornizz Brummsumms beim Kodofriedhdof Desolace.]], },
  },
  [6133] = {
    [[Das Ersuchen des Waldläuferlords]],
    { [[Die Hochelfen der Jagdhütte von Quel'Lithien haben etwas, das mir gehört, <class>: Ein Dokument, das mein Leben als Sterblicher schildert.]], [[Bevor Ihr fragt, Ihr seid wahrscheinlich nicht befugt, derlei Informationen zu bekommen. Macht einfach, was ich Euch sage, Wurm: Holt den Lebenslauf zurück.]], [[Und, Schwachkopf, richtet so viel Not und Elend an, wie Ihr könnt. Lasst sie leiden...]], },
    { [[Begebt Euch zur nördlichsten Grenze der Östlichen Pestländer und bringt die Registratur von Quel'Thalas zurück. Der Gegenstand befindet sich irgendwo in der Jagdhütte von Quel'Lithien.]], [[Außerdem müsst Ihr 8 Waldläufer, 8 Pfadschreiter und 8 Waldarbeiter töten.]], },
  },
  [6134] = {
    [[Geisterplasmasuche]],
    { [[Im Südosten liegt ein Tal, das Tal der Knochen, das von den Geistern der Magram heimgesucht wird. Klingt gruselig, nicht wahr? Stellt Euch mal vor, wie sich die Magram erst gruseln müssen! Wenn wir Geisterenergie an jenem Ort einfangen könnten, wäre das den Zentauren sicher einiges wert.]], [[Hier, nehmt diese Kiste mit Geistermagneten. Wenn Ihr einen zwischen den beiden großen Skeletten im Tal aufstellt, den toten Goliaths, dann werden sich Geister materialisieren und dorthin gehen. Nehmt Abstand, schaltet die Geister aus und holt Euch ihr Geisterplasma - das verkaufen wir den Magram.]], },
    { [[Bringt 8 Einheiten Geisterplasma und die Kiste mit den Geistermagneten zu Hornizz Brummsumms in Desolace.]], },
  },
  [6135] = {
    [[Dämmerschwinge, oh, wie ich Euch hasse...]],
    { [[Sucht Dämmerschwinge und tötet ihn. Nehmt ein Büschel vom Fell Dämmerschwinges und bringt es zu Nathanos Pestrufer.]], [[Man sagt, dass Dämmerschwinge nördlich von hier durch das Land streift.]], },
    { [[Sucht Dämmerschwinge und tötet ihn. Nehmt ein Büschel vom Fell Dämmerschwinges und bringt es zu Nathanos Pestrufer.]], [[Man sagt, dass Dämmerschwinge nördlich von hier durch das Land streift.]], },
  },
  [6136] = {
    [[Der Korpulente]],
    { [[Hinter der Kapelle des Hoffnungsvollen Lichts, dem Lager der Argentumdämmerung, und westlich vom Nordtal, findet Ihr die wohlbeleibte Madenbestie Blutschlinger.]], [[Ich habe ganze Regimenter an dieses Monster verloren. Sein Leibesumfang ist gewaltig! Und je mehr er frisst, um so größer wird er...]], [[Es würde mir keine schlaflosen Nächte bereiten, sollte die Bestie Euch verschlingen, dennoch sähe ich es lieber, wenn Ihr der Sieger wärt. Man weiß, dass Blutschlinger schon andere verschlungen hat, die töricht genug waren, ihn anzugreifen.]], },
    { [[Sucht in den nördlichen Wüsten der Östlichen Pestländer nach Blutschlinger und vernichtet ihn. Kehrt zu Nathanos Pestrufer zurück, sobald Ihr diese Mission erledigt habt.]], },
  },
  [6141] = {
    [[Bruder Anton]],
    { [[Bruder Anton, mein Waffenbruder, hörte Gerüchte über einen Angriff der Untoten im fernen Kalimdor, in einem Land, das Desolace genannt wird. Anton buchte sofort eine Überfahrt dorthin, um die Bedrohung durch die Untoten zu lokalisieren und zu beseitigen!]], [[Er sucht Leute mit viel Tapferkeit und Entschlossenheit, die ihm dabei helfen. Wenn Ihr aufrichtigen Herzens seid, dann sprecht mit Anton. Er befindet sich in Desolace in einer Stadt der Allianz namens Nijelspitze.]], },
    { [[Sprecht mit Bruder Anton in Desolace.]], },
  },
  [6142] = {
    [[Muschelköder]],
    { [[Ihr seht wahrlich aus wie ein Angler, <class>! Ich angle zu gern - schon mein Leben lang. Als Köder eignet sich das Fleisch der weichschaligen Muschel am besten. Die Fische können ihm einfach nicht widerstehen. Wisst Ihr, was ich meine?]], [[Oben im Norden findet Ihr ein Schiffswrack vor der Küste. In der Gegend wimmelt es von weichschaligen Muscheln. Wenn Ihr mir 10 Teile Fleisch der weichschaligen Muschel bringt, werde ich Euch etwas dafür geben.]], },
    { [[Sucht 10 Teile Fleisch der weichschaligen Muschel und bringt sie zu Mai'Lahii im Dorf Schattenflucht.]], },
  },
  [6143] = {
    [[Andere Fische zu grillen]],
    { [[Vielleicht habt Ihr es ja gehört, vielleicht auch nicht... meine Besatzung und ich waren vor der Insel Ranazjar fischen, nördlich des Schiffswracks vor der Küste im Norden. Wir haben immer dort gefischt, jetzt suche ich nur eine Möglichkeit, wie ich meine gefallenen Kameraden rächen kann.]], [[Die Naga haben uns eines Morgens angegriffen und alle außer mir getötet.]], [[Die Schuld lastet mir schwer auf der Seele, <class> ihr müsst losziehen und diese Naga für mich grillen. Ich werde Euch für die Mühe belohnen. Meint Ihr, Ihr könnt mir behilflich sein?]], },
    { [[Tötet 7 Myrmidonen der Zackenkämme, 7 Naga der Zackenkämme und 5 Zauberinnen der Zackenkämme, dann sprecht in Desolace mit Drulzegar in Schattenflucht.]], },
  },
  [6144] = {
    [[Der Befehl]],
    { [[Reist nach Unterstadt und sprecht mit Bragor Blutfaust.]], },
    { [[Reist nach Unterstadt und sprecht mit Bragor Blutfaust.]], },
  },
  [6145] = {
    [[Der Purpurrote Kurier]],
    { [[So wie niemand ohne den Kopf überleben kann, kann der Kopf nicht ohne den Körper überleben. Die Verteidigungsanlagen des Scharlachroten Kreuzzugs in Stratholme sind fast unüberwindbar. Stattdessen müssen wir den Körper unter dem Kopf abschneiden.]], [[Meine Todespirscher haben Daten über die Aktivitäten des Kreuzzugs außerhalb von Stratholme gesammelt. Jeden Tag wird ein Bericht aus ihrer Befehlszentrale nach Tyrs Hand geschickt. Dieser Bericht ist der Schlüssel, <race>! Sucht den Purpurroten Kurier und beschafft diesen Bericht. Bringt ihn zu Nathanos.]], },
    { [[Kehrt in die Östlichen Pestländer zurück und spürt den Purpurroten Kurier auf. Tötet den Kurier und schnappt Euch den Befehl des Obersten Kreuzfahrers.]], [[Bringt den Befehl zu Nathanos Pestrufer und wartet auf weitere Anweisungen.]], },
  },
  [6146] = {
    [[Nathanos' List]],
    { [[Ich habe diesen Befehl etwas modifiziert. Sollte unsere kleine List wirken, wird das Scharlachrote Orakel schutzlos sein und wir schlagen zu!]], [[Passt jetzt genau auf, <class>. Ihr werdet diesen Befehl dem Kreuzzüglerlord Valdelmar überbringen.]], [[Wie? Ihr drückt ihm das Ding in die Hand, Schwachkopf.]], [[Bringt den Befehl und diesen faulen Apfel nach Tyrs Hand. Wenn Ihr in der Stadt seid, dann esst den Apfel. Ihr werdet Euch in etwas verwandeln, das für Menschen...verträglicher ist. Nehmt den Befehl und übergebt ihn dem Kreuzzüglerlord Valdelmar.]], },
    { [[Reist nach Tyrs Hand, südöstlich der Siedlung Marris. Dort angekommen beißt in den faulen Apfel. In der Verkleidung des Scharlachroten Kreuzzugs überbringt Ihr Lord Valdelmar den Befehl des Obersten Kreuzfahrers.]], },
  },
  [6147] = {
    [[Kehrt zu Nathanos zurück.]],
    { [[Der Oberste Kreuzfahrer bringt eine ungewöhnliche Bitte vor. Hrm, wie dem auch sei... was er verlangt, soll geschehen. Kehrt zur Scharlachroten Bastion zurück und lasst ihn wissen, dass das Orakel sich bald auf den Weg machen wird.]], [[Wir werden alle Vorsichtsmaßnahmen treffen, damit sie es unbeschadet nach Stratholme schafft.]], },
    { [[Kehrt mit der neuen Information zu Nathanos Pestrufer zurück.]], },
  },
  [6148] = {
    [[Das scharlachrote Orakel Demetria]],
    { [[Der Scharlachrote Kreuzzug ist unterwegs. Irgendwo auf der Straße von Tyrs Hand nach Stratholme findet Ihr das Orakel Demetria und ihr Gefolge. Jagt sie und tötet sie. Kehrt zu Nathanos Pestrufer zurück, wenn Ihr erfolgreich wart.]], },
    { [[Der Scharlachrote Kreuzzug ist unterwegs. Irgendwo auf der Straße von Tyrs Hand nach Stratholme findet Ihr das Orakel Demetria und ihr Gefolge. Jagt sie und tötet sie. Kehrt zu Nathanos Pestrufer zurück, wenn Ihr erfolgreich wart.]], },
  },
  [6161] = {
    [[Nehmt Rackmores Schatz!]],
    { [[Aus Rackmores Logbuch geht hervor, dass sein Schiff zur Mondfederfeste segelte, als es von seefahrenden Kreaturen angegriffen wurde. Er versteckte seine Truhe auf der Insel Ranazjar, damit sein Schatz dem Feind nicht in die Hände fallen sollte.]], [[Zwei Schlüssel, ein silberner und ein goldener, sind erforderlich, um die Truhe zu öffnen. Diese Schlüssel gingen verloren, doch wenn Schlüssel und Truhe gefunden werden, winkt ein Schatz!]], },
    { [[Sucht Rackmores Silberschlüssel. Sucht Rackmores Goldschlüssel. Sucht und öffnet Rackmores Truhe.]], },
  },
  [6162] = {
    [[Eines Ehemanns letzter Kampf]],
    { [[Mein Gatte reiste unserem Klan zu Ehren durch den Teufelswald und kämpfte gegen den Schattenrat und andere verderbte Monstrositäten!]], [[Als er dem Tod viele Male entronnen war, erhielt ich einen Brief von ihm, in dem stand, dass ein Druide ihn um Beistand gegen einen großen beschmutzten Furbolg namens Oberanführer Ror gebeten hatte - man hörte nie wieder etwas von ihnen.]], [[Ich möchte die Klauenhand dieses Ror, <name>. Er soll dafür büßen, dass er mich zur Witwe gemacht hat! Ich glaube, die Kreatur ist im Süden... nahe der Grenze des Eschentals.]], },
    { [[Tötet Oberanführer Ror und bringt seine Klaue zu Dreka'Sur beim Blutgiftposten im Teufelswald.]], },
  },
  [6163] = {
    [[Ramstein]],
    { [[Ich war überwältigt. Ich stand in dieser Siedlung... und blieb standhaft bei der Verteidigung von Lordaeron. Der Hof und alles darauf wurde von einer gigantischen Welle der Geißel belagert.]], [[Der Dreck. Der Schmutz. Er stand über mir und die Eingeweide hingen ihm aus einem klaffenden Loch in der Brust.]], [[Ich erwachte als Geißel. Lange Zeit war ich hilflos. Meine Gedanken waren mit denen des Lichkönigs verschmolzen.]], [[Sie kam zu mir... irgendwo, irgendwie wusste ich es. Frei.]], [[Doch jetzt muss ich meine Rache haben - Rache an dem, der mir das Leben genommen hat. Vernichtet ihn.]], },
    { [[Reist nach Stratholme und tötet Ramstein den Würger. Bringt seinen Kopf als Souvenir zu Nathanos.]], },
  },
  [6164] = {
    [[Augustus' Quittungsbuch]],
    { [[Ich bin jetzt bereit fürs Geschäftliche! Was wollt Ihr?]], [[Oh, Moment mal... ohne mein Quittungsbuch kann ich kein Geschäft eröffnen. Wie soll ich ohne mein Quittungsbuch den Überblick behalten?]], [[Ich muss es in der Stadt gelassen haben. Ich hatte ein Zimmer im Gasthaus... dort muss das Buch sein.]], [[Bringt mir mein Buch. Bitte, bringt es mir! Bringt mir mein Buch und Ihr werdet sehen! Ich eröffne mein Geschäft und Ihr werdet sehen, dass meine Preise der WAHNSINN sind!]], },
    { [[Beschafft Euch Augustus' Quittungsbuch aus dem Gasthaus im Schreckenstal.]], },
  },
  [6181] = {
    [[Eine eilige Nachricht]],
    { [[Wir bekommen zwar nicht viel Unterstützung von Sturmwind direkt, aber ich habe einen Kontaktmann in der Stadt, der uns mit Rüstungen versorgt. Sein Name ist Osric Strang. Sein Geschäft, Begrenzte Immunität, liegt in der Altstadt von Sturmwind.]], [[Unser Vorrat an Rüstung schwindet, ich muss bei Osric mehr bestellen. Könnt Ihr ihm diese Notiz bringen?]], [[Der schnellste Weg nach Sturmwind führt über Thor, unseren Greifenmeister. Er ist unten am Hügel; bringt ihm meine Notiz und nehmt dann einen Greifen nach Sturmwind.]], },
    { [[Bringt Lewis' Notiz zu Thor dem Greifenmeister.]], },
  },
  [6182] = {
    [[Der Erste und Letzte]],
    { [[Nathanos Marris war der erste und letzte Waldläuferlord der Menschen. Ein Schüler von Sylvanas Windläufer, die heute Bansheekönigin der Verlassenen ist.]], [[Wir dachten, Nathanos sei fünf Jahre zuvor bei der Verteidigung von Lordaeron in der Schlacht gefallen. Sein Leichnam wurde zwar nie gefunden, aber man ging davon aus, dass er es nicht aus den Östlichen Pestländern hinaus geschafft hatte.]], [[Mathias Shaw hat sein Verschwinden untersucht und könnte neue Informationen haben. Meldet Euch sofort bei ihm. Er lebt in der Altstadt, in der Kaserne.]], },
    { [[Sprecht mit Meister Mathias Shaw in der Altstadt von Sturmwind. Er wohnt in der Kaserne.]], },
  },
  [6183] = {
    [[Ehret die Toten]],
    { [[Fünf meiner besten Agenten wurden dem Fall Marris zugeteilt. Einer kam zurück und starb drei Tage später im Schlaf.]], [[Die wenigen Informationen, die wir aus ihm herausbekamen, waren unverständliches Gestammel. Wir haben zwei Worte verstanden: 'Nathanos' und 'Pestrufer'.]], [[Ich habe nicht genügend Leute, um die Ermittlungen fortzusetzen, und Rabenholdt ist uns nicht behilflich. Wir müssen der Sache auf den Grund gehen, und wenn es nur ist, damit wir den Familien der Verstorbenen Trost spenden können. Werdet Ihr helfen?]], },
    { [[Sprecht wieder mit Mathias Shaw, wenn Ihr seine Aufgabe annehmen wollt.]], },
  },
  [6184] = {
    [[Flint Schattenmoor]],
    { [[Die einzige Information, die wir bislang haben, ist die, dass Nathanos Marris von diesem Pestrufer ermordet worden sein könnte. Wir gehen davon aus, dass Pestrufer derselbe Teufel ist, der meine Agenten beseitigt hat.]], [[Flint Schattenmoor, ein weiterer Agent des SI:7, ist im Lager der Allianz an der Zugwindspitze in den Westlichen Pestländern stationiert. Sucht ihn auf. Er wird Euch in die aktuelle Situation in den Pestländern einweihen und Euch einen Auftrag erteilen.]], [[Viel Glück, <name>.]], },
    { [[Reist zur Zugwindspitze in den Westlichen Pestländern und trefft Euren Kontaktmann Flint Schattenmoor.]], },
  },
  [6185] = {
    [[Die Östlichen Pestländer]],
    { [[Wie gesagt, Eure erste Mission ist eine Aufklärungsmission. Nichts Kompliziertes, <name>. Ihr müsst in die Östlichen Pestländer reisen und nach Hinweisen auf diesen Pestrufer suchen.]], [[Haltet nach Informationen über unsere vermissten Agenten Ausschau. Wir müssen davon ausgehen, dass sie tot sind, und wenn sie tot sind, muss es Leichen geben...irgendwo.]], [[<Flint schluckt schwer.>]], [[Alle SI:7-Agenten tragen diese Insignien.]], [[<Flint zeigt Euch seine SI:7-Insignien.>]], [[Bringt alle zu mir zurück, die Ihr finden könnt.]], },
    { [[Sucht in den Östlichen Pestländern nach Hinweisen auf den 'Pestrufer' und die vermissten SI:7-Agenten. Wenn Ihr SI:7-Insignien findet, bringt sie zu Flint Schattenmoor an der Zugwindspitze.]], },
  },
  [6186] = {
    [[Der Pestrufer kommt]],
    { [[Kehrt nach Sturmwind zurück und informiert König Varian Wrynn über das Schicksal von Nathanos Marris.]], },
    { [[Kehrt nach Sturmwind zurück und informiert König Varian Wrynn über das Schicksal von Nathanos Marris.]], },
  },
  [6187] = {
    [[Ordnung muss wiederhergestellt werden]],
    { [[Wisst Ihr, wie viele Waldläuferlords es auf dieser Welt gibt? Wie viele menschliche Waldläuferlords jemals existiert haben?]], [[Nathanos' Errungenschaften waren beispiellos. Er war ein taktisches Genie und führte die Allianz in einem Jahrzehnt der Konflikte zu vielen Siegen.]], [[Und jetzt... der Held der Verlassenen.]], [[Nein. Das darf nicht sein. Die Ordnung muss wiederhergestellt werden.]], [[Versammelt eine Armee, <name>. Kehrt mit Eurer Armee in die Pestländer zurück und vernichtet den Pestrufer.]], [[Ich wünsche Euch Glück, <name>. Das werdet Ihr für diesen Kampf wahrlich brauchen.]], },
    { [[Stellt eine Armee auf und reist zu den Östlichen Pestländern. Beginnt einen gnadenlosen Angriff auf Nathanos Pestrufer und jeden Abschaum der Horde, der ihn beschützen mag.]], [[Bewahrt einen klaren Kopf, <name>. Die Horde wird den Waldläuferlord mit dem Leben verteidigen.]], },
  },
  [6221] = {
    [[Totenwaldfelle des Nordens]],
    { [[Direkt südwestlich von hier liegt ein Furbolglager der Totenwaldfelle. Ich bin nicht sicher, was sie vorhaben, aber ich würde dem gern einen Riegel vorschieben, ehe es zum Problem wird.]], [[Der Stamm der Totenwaldfelle ist böse, aber die Verderbnis ist es, die ihre Hirne verseucht. Ihre Feindseligkeit ist keine angeborene Eigenschaft, doch da kann man nichts machen. Aus diesem Grund stimmt es mich zutiefst traurig, dass ich gegen meine Geschwister kämpfen muss.]], [[Bitte, <name>, tut, was getan werden muss, aber erspart mir die Einzelheiten. Sie beunruhigen mich zu sehr.]], },
    { [[Nafien möchte, dass Ihr 5 Höhlenbehüter der Totenwaldfelle, 5 Rächer der Totenwaldfelle und 5 Schamanen der Totenwaldfelle besiegt.]], },
  },
  [6241] = {
    [[Aktivität der Winterfelle]],
    { [[Die Furbolgs der Holzschlundfeste bleiben meist unter sich, <name>. Wir gewähren allen, die ihre Vertrauenswürdigkeit unter Beweis gestellt haben, freies Geleit, meiden aber wenn möglich andere Furbolgstämme.]], [[In letzter Zeit ist mir aufgefallen, dass der Winterfellstamm uns gegenüber immer größere Feindseligkeit zeigt. Sie scheinen in Rage zu sein, und ganz von ihrem Hass auf alles beseelt, das sie nicht verstehen.]], [[Wenn Ihr ihre Anzahl reduzieren könntet, würde uns das sehr helfen, <name>.]], },
    { [[Salfa möchte, dass Ihr 6 Totemiker der Winterfelle, 6 Höhlenbehüter der Winterfelle und 6 Pfadfinder der Winterfelle erledigt.]], },
  },
  [6261] = {
    [[Dungar Trinkfest]],
    { [[<name>, ich habe alles, worum Lewis gebeten hat, in diese Kiste gepackt. Würdet Ihr sie ihm bringen?]], [[Wenn Ihr bereits mit Thor in Westfall gesprochen habt, dann könnt Ihr einen Greif zurück zu ihm nehmen. Dungar Trinkfest ist unser Greifenmeister drüben im Handelsdistrikt.]], [[Sprecht mit Dungar, dann bringt diese Kiste auf dem schnellsten Weg zu Lewis. Wir möchten nicht, dass unsere kämpfenden Männer und Frauen in Westfall ohne neue Ausrüstung sind!]], },
    { [[Bringt Osrics Kiste zu Dungar Trinkfest dem Greifenmeister.]], },
  },
  [6281] = {
    [[Weiter nach Sturmwind]],
    { [[Gegen eine geringe Gebühr könnt Ihr einen Greifen nach Sturmwind nehmen und so Lewis' Notiz zu Osric bringen. Einen schnelleren Weg gibt es nicht.]], [[Wenn Euch das akzeptabel erscheint, sprecht mich wieder an, sobald Ihr bereit seid für den Flug. Das kostet eine Kleinigkeit, aber glaubt mir, es lohnt sich!]], },
    { [[Bucht einen Greifenritt bei Greifenmeister Thor, dann bringt Lewis' Notiz zu Osric Strang, im Laden Begrenzte Immunität, in der Altstadt von Sturmwind.]], },
  },
  [6282] = {
    [[Harpyien drohen]],
    { [[Tauren sind die stolzen Beschützer dieser Länder. Zweifellos könnt Ihr erraten, warum mich die Anzahl der Harpyien hier im Steinkrallengebirge so beunruhigt.]], [[Die Blutfurien sind die Ursache für die zunehmende Zahl von Harpyien in Kalimdor. Sie bedrängen jede Lebensform und hindern Unschuldige daran, ihre Grenzen zu verlassen.]], [[Ihr müsst die Harpyien für mich dezimieren, <name>!]], },
    { [[Maggran Erdbinder in Sonnenfels möchte, dass Ihr 7 Harpyien der Blutfurien, 7 Wegelagerinnen der Blutfurien, 7 Töterinnen der Blutfurien und 7 Wildfedern der Blutfurien erschlagt.]], },
  },
  [6283] = {
    [[Blutfuriengeschlecht]],
    { [[Es genügt nicht, die Anzahl der Blutfurienharpyien zu verringern. Die einzig wahre Kur für ihre Krankheit wäre es, ihre Anführerin zu beseitigen, die Zerfetzerin der Blutfurien!]], [[Für diese dreckigen Kreaturen ist sie so etwas wie eine Königin. Tötet sie und ihre Zahl wird ganz gewiss abnehmen.]], [[Geht zurück zum Verbrannten Tal, dort findet Ihr sie in den westlichen Hügeln. Sie wird nicht leicht zu bezwingen sein, aber die Belohnung ist groß.]], },
    { [[Maggran in Sonnenfels möchte, dass Ihr die Zerfetzerin der Blutfurien beseitigt und ihre sterblichen Überreste als Beweis für Eure Tat mitbringt.]], },
  },
  [6284] = {
    [[Arachnophobie]],
    { [[Warnung: Betretet den Sishircanyon westlich von hier auf eigene Gefahr.]], [[Besseleth und ihre achtbeinigen Kinder des Waldes haben diesen Canyon zu ihrem eingesponnenen Unterschlupf erkoren.]], [[Maggran Erdbinder in Sonnenfels hat ein Kopfgeld auf Besseleth ausgesetzt. Tötet sie und bringt einen Beweis Eurer Tat zu Maggran. Dort werdet Ihr eine Belohnung erhalten, wenn Ihr der achtbeinigen Gefahr ein Ende gemacht habt.]], },
    { [[Tötet Besseleth und bringt Besseleths Fangzahn zu Maggran in Sonnenfels.]], },
  },
  [6285] = {
    [[Zurück zu Lewis]],
    { [[Der Greifenmeister in Westfall heißt Thor. Falls Ihr schon mit ihm gesprochen habt, dann könnt Ihr einen meiner Greifen zu ihm bringen.]], [[Das hier sollte man unbedingt wissen: Greifen sind stets so ausgebildet, dass sie in ihre Hauptstadt fliegen, aber sie bringen Euch nur zu einem außerörtlichen Greifenmeister, wenn Ihr schon einmal dort gewesen seid.]], [[Ihr seid bereits bei Thor gewesen, sprecht daher einfach wieder mit mir, wenn Ihr bereit seid, einen Greifen nach Westfall zu nehmen. Dort könnt Ihr Osrics Kiste bei Rüstmeister Lewis abliefern.]], },
    { [[Bucht einen Greifenritt zur Späherkuppe bei Greifenmeister Dungar Trinkfest, dann bringt Osrics Kiste zu Lewis an der Späherkuppe.]], },
  },
  [6301] = {
    [[Zyklus der Wiedergeburt]],
    { [[Industrielle Gier und ständige Kriege haben die Länder des Steinkrallengebirges verwüstet. Nur der Segen der Erdenmutter kann solch eine Verletzung heilen.]], [[<name>, Ihr müsst für mich ein spezielles Samenkorn suchen, Gaeasamenkorn. Man findet diese Samenkörner nur hier im Steinkrallengebirge, nördlich von Sonnenfels am Mirkfallonsee, in der Nähe des Ufers.]], [[Wenn Ihr mir die Samenkörner bringt, werde ich sie auf magische Weise mit schamanistischer Macht erfüllen, um ihr Wachstum zu beschleunigen.]], },
    { [[Tammra Windfeld in Sonnenfels möchte, dass Ihr 10 Gaeasamenkörner sammelt.]], },
  },
  [6321] = {
    [[Nachschub für Brill]],
    { [[Exekutor Zygand bat mich, eine Inventur unserer Ausrüstung hier in Brill durchzuführen. Da die Geißel und Schlimmeres in den Wäldern lauert, möchte er nicht, dass den Todeswachen Ausrüstung fehlt, die sie brauchen.]], [[Ich habe festgestellt, dass wir im Großen und Ganzen gut bestückt sind, aber wir brauchen mehr Waffen als Ersatz für die, die wir auf dem Feld verloren haben.]], [[Hier eine Anforderungsliste der benötigten Dinge für den Waffenhändler Gordon Wendheim in Unterstadt. Bringt die Anforderungsliste zur Fledermausführerin Anette und sprecht mit ihr über den Transport nach Unterstadt.]], },
    { [[Bringt Morris' Anforderungsliste zu Fledermausführerin Anette.]], },
  },
  [6322] = {
    [[Michael Garrett]],
    { [[Ich habe den Nachschub für Brill in diese Kiste getan. Wenn Ihr Brill schon besucht und mit der Fledermausmeisterin gesprochen habt, könnt Ihr auf einer Fledermaus zu ihr reiten.]], [[Sprecht mit Michael Garrett, dem Fledermausmeister von Unterstadt. Er kann Euch eine Fledermaus nach Brill zur Verfügung stellen.]], },
    { [[Bringt Gordons Kiste zu Michael Garrett.]], },
  },
  [6323] = {
    [[Flug nach Unterstadt]],
    { [[Eine meiner Fledermäuse kann Euch nach Unterstadt bringen. Normalerweise kosten diese Flüge eine geringe Gebühr, aber der Flug nach Unterstadt ist für <einen tapferen Streiter/eine hübsche Dame> wie Euch kostenlos, <race>. Sprecht mich wieder an, wenn Ihr bereit seid, damit wir Euren Transport arrangieren können.]], [[Ihr findet Gordon Wendheim im Handwerksviertel von Unterstadt. In diesem Viertel landet auch meine Fledermaus.]], },
    { [[Bucht einen Fledermausritt zur Unterstadt bei Fledermausführerin Anette und bringt dann Morris' Anforderungsliste zu Gordon Wendheim in Unterstadt.]], },
  },
  [6324] = {
    [[Rückkehr zu Morris]],
    { [[Der Fledermausmeister in Brill ist Fledermausführerin Anette. Wenn Ihr schon bei ihr gewesen seid, dann gebe ich Euch eine Fledermaus, damit Ihr zu ihr fliegen könnt.]], [[Unsere Fledermäuse fliegen stets nach Unterstadt, aber um zu entlegeneren Orten zu reisen, muss ihr Reiter zuerst die Gegend besuchen und mit dem dortigen Fledermausmeister sprechen.]], [[Ihr habt Anette in Brill getroffen, daher könnt Ihr jetzt mit Fledermäusen dorthin fliegen. Sprecht wieder mit mir, wenn Ihr bereit seid.]], },
    { [[Bucht einen Fledermausritt nach Brill bei Fledermausmeister Michael Garrett und bringt dann Gordons Kiste zu Todeswache Morris in Brill.]], },
  },
  [6341] = {
    [[Auf nach Darnassus]],
    { [[Alle in Dolanaar sind so stolz auf Schwester Aquinne. Ich bin sicher, sie wird sich sehr freuen, dieses aufmerksame Geschenk von Nyoma zu erhalten.]], [[Wenn Ihr bereit seid, nach Darnassus zu reisen, sprecht einfach wieder mit mir und für ein paar Münzen könnt Ihr einen Hippogryphen mieten, der Euch dorthin bringt. Man sagte mir, Schwester Aquinne genießt die Abgeschiedenheit eines kleinen Platzes südlich vom Hippogryphenmeister in Darnassus. Sucht dort nach ihr, wenn Ihr angekommen seid.]], },
    { [[Sprecht mit Fidelio, um einen Flug nach Darnassus zu erstehen, und bringt dann die Rezeptsammlung von Dolanaar zu Schwester Aquinne in Darnassus.]], },
  },
  [6342] = {
    [[Ein unerwartetes Geschenk]],
    { [[Das ist wundervoll! Genau das Richtige, um mich während meiner Studien im Tempel des Mondes an mein Zuhause zu erinnern. Ich werde eins der Rezepte heute Abend ausprobieren. Ich kann es kaum abwarten!]], [[Würdet Ihr diese kurze Dankesnachricht zu Nyoma zurückbringen? Ihr könnt einen Hippogryphen zurück nach Dolanaar mieten. Besucht einfach unsere Hippogryphenmeisterin, Leora, wenn Ihr zum Abflug bereit seid. Sie wird Euch mit einem Rückflug nach Dolanaar helfen.]], },
    { [[Bringt Schwester Aquinnes Notiz zu Leora, der Hippogryphenmeisterin in Darnassus.]], },
  },
  [6343] = {
    [[Rückkehr zu Nyoma]],
    { [[Euch einen Flug zurück nach Dolanaar zu besorgen, wird einfach sein. Da Ihr Fidelio in Dolanaar bereits kennt, könnt Ihr ohne Probleme dorthin zurückfliegen.]], [[Immer wenn Ihr in einer neuen Stadt ankommt, solltet Ihr daran denken, mit dem Flugmeister zu sprechen, damit Ihr zu und von diesem neuen Ort fliegen könnt.]], [[Sprecht mit mir, wenn Ihr bereit seid, einen Hippogryphen für den Rückflug nach Dolanaar zu mieten.]], },
    { [[Sprecht mit Leora, um einen Hippogryphen zu mieten, der Euch nach Dolanaar zurückbringt. Sobald Ihr in Dolanaar angekommen seid, bringt Schwester Aquinnes Nachricht zu Nyoma.]], },
  },
  [6344] = {
    [[Andenken an Zuhause]],
    { [[Eine Freundin von mir, Schwester Aquinne, ist in der Ausbildung zur Mondpriesterin in Darnassus. Sie hat Dolanaar letztes Jahr verlassen, um im Tempel zu lernen. Laut ihren Briefen geht es ihr gut und sie lernt viel, aber sie vermisst ihr Zuhause und ihre alten Freunde. Ich habe ein Buch mit ihren Lieblingsrezepten aus Dolanaar zusammengestellt.]], [[Wenn Ihr die Zeit findet, könntet Ihr es ihr nach Darnassus bringen? Ihr könnt einen Hippogryphen von Fidelio mieten. Er befindet sich in der Mitte der Stadt, gegenüber vom Gasthaus.]], },
    { [[Bringt die Rezeptsammlung von Dolanaar zu Fidelio in Dolanaar.]], },
  },
  [6361] = {
    [[Ein Bündel Bälge]],
    { [[Ich habe ein Bündel Bälge von Tieren Mulgores und muss es nach Donnerfels bringen. Dort fertigt Ahanu, ein Kollege, Lederwaren aus diesen Häuten an.]], [[Bringt Ihr ihm die Bälge für mich?]], [[Der schnellste Weg nach Donnerfels ist auf dem Rücken eines Windreiters. Bringt das Bündel Bälge zu Tak, unserem Windreitermeister am Wegekreuz, und sprecht mit ihm über den Transport nach Donnerfels.]], },
    { [[Bringt das Bündel Bälge zu Windreitermeister Tak im Dorf der Bluthufe.]], },
  },
  [6362] = {
    [[Flug nach Donnerfels]],
    { [[Für ein kleines Entgelt gebe ich Euch einen Windreiter nach Donnerfels. Es gibt kein schnelleres Transportmittel in die Stadt!]], [[Sollte das akzeptabel sein, sprecht wieder mit mir, wenn Ihr abreisefertig seid.]], [[Wenn Ihr diese Bälge zu Ahanu bringen wollt, ich schätze, er ist in Hewas Waffenkammer am Fundament des Windreiterturms in Donnerfels. Und genau dort werdet Ihr landen!]], },
    { [[Bucht einen Windreiter nach Donnerfels bei Windreitermeister Tak und bringt dann das Bündel Bälge zu Ahanu in Donnerfels.]], },
  },
  [6363] = {
    [[Tal der Windreitermeister]],
    { [[Ich habe Lederwaren für Varg zum Verkauf im Dorf der Bluthufe vorbereitet. Würdet Ihr sie ihm bringen?]], [[Wenn Ihr schon im Dorf der Bluthufe gewesen seid und mit dem dortigen Windreitermeister gesprochen habt, dann könnt Ihr mit einem unserer Windreiter zu ihm zurück.]], [[Bringt die Lederwaren zu Tal, unserem Windreitermeister in Donnerfels, und sprecht mit ihm über den Transport zum Dorf der Bluthufe.]], },
    { [[Bringt Ahanus Lederwaren zu Tal in Donnerfels.]], },
  },
  [6364] = {
    [[Rückkehr zu Varg]],
    { [[Um zu einer Stadt zu fliegen, müsst Ihr schon einmal dort gewesen sein und mit dem Windreitermeister dieser Stadt gesprochen haben. Ihr seid beim Dorf der Bluthufe gewesen und habt mit dem Windreitermeister Tak gesprochen, daher könnt Ihr gegen ein geringes Entgelt von hier mit einem unserer Windreiter zu ihm fliegen.]], [[Sprecht wieder mit mir, wenn Ihr bereit seid. Im Dorf der Bluthufe könnt Ihr die Lederwaren zu Varg Windraunen bringen.]], },
    { [[Bucht einen Windreiter zum Dorf der Bluthufe bei Windreitermeister Tak und bringt dann Ahanus Lederwaren zu Varg Windraunen.]], },
  },
  [6365] = {
    [[Fleisch für Orgrimmar]],
    { [[Ich habe hier ausgewählte Fleischstücke, die ich einer Freundin schicken möchte. Ihr Name ist Gryshka, sie ist die Gastwirtin in Orgrimmar. Würdet Ihr das Fleisch für mich abliefern?]], [[Das schnellste Transportmittel nach Orgrimmar ist ein Windreiter. Bringt das Fleisch zu Windreitermeister Devrak beim Wegekreuz und sprecht mit ihm über den Transport nach Orgrimmar.]], },
    { [[Bringt Zarghs Fleisch zu Devrak beim Wegekreuz.]], },
  },
  [6381] = {
    [[Neues Leben]],
    { [[Wie Ihr sehen könnt, <class>, ist der Zyklus des Lebens eine mächtige Sache, auch wenn das Land Schmerzen leidet. Jetzt müssen wir unseren Teil leisten, um ihm zu helfen. Ihr müsst diese Samen im Zentrum der Zerstörung pflanzen, im Westen, im Verbrannten Tal. Aber seid wachsam, denn Harpyien und Feuerelementare verseuchen das vernarbte Land noch immer.]], [[Nehmt diese verzauberten Gaeasamenkörner und pflanzt sie in fruchtbare Erdhügel, <name>. Der Zyklus des Lebens muss weitergehen!]], },
    { [[Pflanzt 10 Gaeasamenkörner in Gaeaerdhügel und sprecht dann mit Tammra in Sonnenfels.]], },
  },
  [6382] = {
    [[Die Jagd im Eschental]],
    { [[Eure Kraft beeindruckt mich, <class>. Die Entschlossenheit, mit der Ihr Euch der Jagd zuwendet, erfüllt mich mit der Gewissheit, dass Ihr Euch größerem Wild... und größeren Herausforderungen zuwenden könnt.]], [[Der Wald des Eschentals ist eine große, ungezähmte Wildnis, der die Horde ihren Willen aufzuzwingen trachtet, sowohl politisch wie auch spirituell. Wenn Ihr Euch in einem unerforschten Land beweisen wollt, dann sucht die Führung von Senani Donnerherz im Splitterholzposten. Der Außenposten liegt nördlich des Pfads, der aus dem Brachland führt.]], },
    { [[Sprecht mit Senani Donnerherz im Splitterholzposten im Eschental.]], },
  },
  [6383] = {
    [[Die Jagd im Eschental]],
  },
  [6384] = {
    [[Flug nach Orgrimmar]],
    { [[Für eine Handvoll Münzen lasse ich Euch mit einem Windreiter nach Orgrimmar bringen. Wenn Ihr dort eintrefft, bringt Grimtaks Fleisch zu Gryshka. Ihr Gasthaus liegt im Tal der Stärke.]], [[Sprecht wieder mit mir, wenn Ihr bereit für die Reise seid.]], },
    { [[Bucht einen Windreiter nach Orgrimmar bei Windreitermeister Burok und bringt dann Grimtaks Fleisch zu Gryshka in Orgrimmar.]], },
  },
  [6385] = {
    [[Doras der Windreitermeister]],
    { [[Ich habe Grimtak einen Dankesbrief geschrieben. Könnt Ihr ihn für mich zustellen? Wenn Ihr schon beim Klingenhügel gewesen seid und mit dem dortigen Windreitermeister gesprochen habt, dann könnt Ihr mit einem Windreiter dorthin zurück fliegen.]], [[Bringt meinen Brief zu Doras, dem Windreitermeister von Orgrimmar und sprecht mit ihm über den Transport nach Klingenhügel.]], },
    { [[Bringt Gryshkas Brief zu Doras in Orgrimmar.]], },
  },
  [6386] = {
    [[Kehrt nach Klingenhügel zurück]],
    { [[Meine Windreiter wurden ausgebildet, an viele verschiedene Orte zu fliegen, wenn Ihr schon an diesem Ort gewesen seid und mit dem Windreitermeister dort gesprochen habt.]], [[Ihr seid in Klingenhügel gewesen und habt mit dem Windreitermeister Burok gesprochen, daher könnt Ihr jetzt direkt zu ihm fliegen. Wenn Ihr in Klingenhügel seid, könnt Ihr Gryshkas Brief zu Grimtak bringen.]], [[Sprecht wieder mit mir, wenn Ihr bereit zum Aufbruch seid.]], },
    { [[Bucht einen Windreiter zum Klingenhügel bei Windreitermeister Doras und bringt dann Gryshkas Brief zu Grimtak in Klingenhügel.]], },
  },
  [6387] = {
    [[Ehrenlehrlinge]],
    { [[Ich sehe viele junge Zwerge vor meiner Tür, die begierig sind, die Kochkunst zu erlernen. Und wenn ein Koch eines mehr als alles andere braucht, dann ist das ein Beil!]], [[Ich habe eine Liste von Kochlehrlingen mit vorzüglichen Abschlussprüfungen, die sich ein Ehrenbeil verdient haben. Bringt meine Liste zu Golnir Geröllzeh in Eisenschmiede. Er wird die Beile bereitstellen.]], [[Das schnellste Transportmittel nach Eisenschmiede ist ein Greif, also bringt die Liste Brolan Windbart, unserem Greifenmeister, und sprecht mit ihm über den Transport nach Eisenschmiede.]], },
    { [[Bringt Gremlocks Liste zu Brolan Windbart in Kharanos.]], },
  },
  [6388] = {
    [[Gryth Thurden]],
    { [[Hier sind die Ehrenbeile für Gremlocks Schüler. Wenn Ihr schon in Kharanos gewesen seid, dann solltet Ihr mit einem Greifen dorthin zurückfliegen!]], [[Bringt die Beile zu unserem Greifenmeister Gryth Thurden und sprecht mit ihm über die Rückreise nach Kharanos.]], },
    { [[Bringt die Ehrenbeile zu Gryth Thurden in Eisenschmiede.]], },
  },
  [6389] = {
    [[Eine Plage komme über Euch]],
    { [[Ihr seht Euch in dem Sägewerk nach etwas um, mit dem Ihr das Fass öffnen könnt. Ihr findet ein Stemmeisen, das aussieht, als würde es damit gehen.]], [[Ihr nehmt die Beine in die Hand und betet, dass Ihr aus dem Sägewerk entwischen könnt, bevor die Termiten Euch erwischen.]], },
    { [[Befreit die Pestlandtermiten im Sägewerk des Nordkamms, dann kehrt mit dem Fass Pestlandtermiten zu Nathaniel Dumah im Zugwindlager in den Westlichen Pestländern zurück.]], },
  },
  [6390] = {
    [[Eine Plage komme über Euch]],
    { [[Ihr seht Euch in dem Sägewerk nach etwas um, mit dem Ihr das Fass öffnen könnt. Ihr findet ein Stemmeisen, das aussieht, als würde es damit gehen.]], [[Ihr nehmt die Beine in die Hand und betet, dass Ihr aus dem Sägewerk entwischen könnt, bevor die Termiten Euch erwischen.]], },
    { [[Befreit die Pestlandtermiten im Sägewerk des Nordkamms, dann kehrt mit dem Fass Pestländer-Termiten zu Mickey Levine im Bollwerk in Tirisfal zurück.]], },
  },
  [6391] = {
    [[Flug nach Eisenschmiede]],
    { [[Für nur ein paar Münzen bringt Euch einer meiner Greifen nach Eisenschmiede. Bringt dann Gremlocks Liste zu Golnir Geröllzeh. Ihr findet ihn in der Tiefenbergbaugilde. Sie liegt im Distrikt der Großen Schmiede von Eisenschmiede, und genau dort setzt Euch mein Greif ab!]], [[Sprecht wieder mit mir, wenn Ihr bereit für die Reise seid.]], },
    { [[Bucht einen Greifen nach Eisenschmiede von Greifenmeister Brolan Windbart, dann bringt Gremlocks Liste zu Golnir Geröllzeh in Eisenschmiede.]], },
  },
  [6392] = {
    [[Rückkehr zu Gremlock]],
    { [[Für ein geringes Entgelt könnt Ihr einen Greifenritt nach Kharanos buchen... wenn Ihr schon einmal dort gewesen seid. Greifen fliegen nur zu Orten, an denen Ihr schon einmal gewesen seid, sprecht also mit jedem Greifenmeister, den Ihr seht, dann könnt Ihr später direkt zu ihm fliegen.]], [[Ihr seid schon bei Brolan, dem Greifenmeister von Kharanos, gewesen, daher könnt Ihr jetzt zu ihm zurückkehren. Wenn Ihr in Kharanos seid, könnt Ihr Gremlock Pilsnor die Beile bringen.]], [[Sprecht mit mir, wenn Ihr bereit zum Aufbruch seid.]], },
    { [[Bucht einen Greifen nach Kharanos bei Greifenmeister Gryth Thurden, dann bringt die Ehrenhacken zu Gremlock Pilsnor in Kharanos.]], },
  },
  [6393] = {
    [[Krieg der Elementare]],
    { [[Das Tribunal der Gezeiten hat mich geschickt, um den Feuerelementaren hier im Steinkrallengebirge den Krieg zu erklären. Neptulon der Gezeitenjäger, der Lord aller Wasserelementare hier, hat ein persönliches Interesse daran, dass unsere feurigen Vettern ausgelöscht werden.]], [[<name>, Ihr könnt mir helfen, die Gunst des Tribunals zu erringen, indem Ihr die Feuerelementare im Verbrannten Tal vernichtet. Wenn Feuerelementare zerstört werden, hinterlassen sie Pyrophore. Bringt sie mir, dann werde ich Euch belohnen.]], },
    { [[Bringt 10 Pyrophore zu Tsunaman in Sonnenfels.]], },
  },
  [6394] = {
    [[Thazz'rils Hacke]],
    { [[<name>, Ihr seid <ein/eine><race> <race>, auf <den/die><race> man sich verlassen kann. Würdet Ihr wohl eine weitere Aufgabe für mich erledigen?]], [[Vor einiger Zeit suchte ich in einer Höhle im Norden nach Mineralien und vergaß dort meine Lieblingshacke. Als ich später dorthin zurückkehrte, hausten tückische Bestien in dieser Höhle! Würdet Ihr diese Höhle aufsuchen, es ist der Koven der Brennenden Klinge, und meine Hacke holen?]], [[Ich habe sie in einer Kammer mit Wasserfällen gelassen. Meine Hacke ist verzaubert, daher könnt Ihr sie im Dunkeln sehen und müsst Euch keine Sorgen machen, wie Ihr sie findet... nur diejenigen, die sie bewachen, müsst Ihr fürchten!]], },
    { [[Bringt Thazz'rils Hacke zu Großknecht Thazz'ril.]], },
  },
  [6395] = {
    [[Marlas letzter Wunsch]],
    { [[Meine Freundin Marla Fipps lebte vor der Seuche mit ihrem Mann Samuel zusammen, doch als die Seuche kam, erlag Samuel ihr und lief zur Geißel über.]], [[Marla wurde der Untod erspart, doch sie starb durch die Hand ihres jetzt hirnlosen Mannes. Aber ihre Liebe war so stark, dass sie sterbend den letzten Wunsch äußerte, zusammen mit ihrem geliebten Samuel begraben zu werden.]], [[Samuel Fipps streift durch ein verwüstetes Lager nordöstlich von Todesend. Besiegt ihn und erfüllt Marlas Wunsch: Begrabt ihn in ihrem Grab in der ersten Reihe unseres Friedhofs.]], },
    { [[Bringt Samuel Fipps' sterbliche Überreste zu Marlas Grab und kehrt dann zu Novizin Elreth zurück.]], },
  },
  [6401] = {
    [[Kaya lebt]],
    { [[Teilt Tammra Windfeld, die in Sonnenfels lebt, die gute Nachricht mit.]], },
    { [[Teilt Tammra Windfeld, die in Sonnenfels lebt, die gute Nachricht mit.]], },
  },
  [6402] = {
    [[Treffen in Sturmwind]],
    { [[Reist nach Sturmwind und begebt Euch zu den Stadttoren. Sprecht mit Knappe Rowe, damit er Marschall Windsor über Eure Ankunft informiert.]], },
    { [[Reist nach Sturmwind und begebt Euch zu den Stadttoren. Sprecht mit Knappe Rowe, damit er Marschall Windsor über Eure Ankunft informiert.]], },
  },
  [6403] = {
    [[Die große Maskerade]],
    { [[Das war vielleicht ein Abenteuer. Ich schulde Euch großen Dank. Vielleicht, wenn alles gesagt und getan ist...]], [[Wie Ihr zweifellos schon bemerkt haben werdet, sind Lady Prestor und Onyxia ein und dieselbe Person. Sie ist die Brut von Todesschwinge, Zwillingsschwester des Tyrannen der Spitze, Nefarian.]], [[Viel zu lange schon hat sie ihren gierigen Blick auf unser Königreich gerichtet. Sogar der Hochlord steht schon unter dem Bann ihrer Zaubertricks - eine Marionette. Heute wird damit Schluss gemacht... Der Bann wird gebrochen.]], [[Sorgt Euch nicht, denn gemeinsam werden wir siegen!]], [[Seid Ihr bereit?]], },
    { [[Folgt Reginald Windsor durch Sturmwind. Schützt ihn vor Gefahren!]], },
  },
  [6421] = {
    [[Steinschlagklamm]],
    { [[Irgendetwas stimmt hier im Steinkrallengebirge nicht. Spürt Ihr die Spannung, die in der Luft liegt, <name>?]], [[Südlich von hier liegt eine tiefe Höhle in der Steinschlagklamm. Die Kobolde dort schürfen wie wild nach einem seltenen Kristall, dem so genannten Resonit. Ich möchte, dass Ihr mir ein paar Erzproben bringt, damit ich herausfinden kann, was in dieser Höhle vor sich geht. Ich hätte auch gern, dass Ihr die Tiefen dieser Höhle untersucht.]], [[Geht nun, <class>, es ist von größter Bedeutung, dass ich in Erfahrung bringe, welche Bösartigkeiten unter diesen Bergen lauern.]], },
    { [[Erkundet die Tiefen der Höhle in der Steinschlagklamm und bringt Mor'rogal in Sonnenfels 10 Resonitkristalle, damit er sie untersuchen kann.]], },
  },
  [6441] = {
    [[Satyrhörner]],
    { [[He! Ich bin auf der Durchreise nach Ratschet, habe das ganze Eschental durchquert und mich entschlossen, hier im Splitterholzposten Rast zu machen. Ich suche immer nach guten Gelegenheiten und es scheint mir, dass in Ratschet zurzeit Satyrhörner sehr gefragt sind.]], [[Nachdem ich mich hier ein bisschen umgehört habe, glaube ich, dass die Satyrn in der Nachtflucht, Satyrnaar und Xavian genau die Art Hörner besitzen, die ich suche. Ich kann mich mit diesen Satyrn nicht messen... aber Ihr!]], [[Ich sag Euch was: Wenn Ihr mir die Hörner bringt, soll es Euer Schaden nicht sein.]], },
    { [[Sammelt für Pixel im Splitterholzposten 16 Satyrhörner.]], },
  },
  [6442] = {
    [[Die Naga am Zoramstrand]],
    { [[Entlang der Küste hier lauert das Böse, <race>.]], [[Sie ist die Ruhestätte der untergegangenen Stadt Zoram, die vor langer Zeit zerstört wurde und im Meer versank. Sie war verloren und fast vergessen.]], [[Jetzt allerdings sind die Naga zurückgekehrt, aber warum, wissen wir nicht. Die Gründe sind jedoch auch nicht weiter wichtig. Wir müssen das Land verteidigen, um das wir so hart gekämpft haben.]], [[Bringt mir 20 ihrer Köpfe! Treibt die Naga in die Tiefen zurück!]], },
    { [[Bringt Marukai am Zoramstrand 20 Köpfe von Rächerflossen.]], },
  },
  [6461] = {
    [[Blutfresser]],
    { [[Wir Trolle hier am Malaka'jin haben von dem Land profitiert. Das Steinkrallengebirge bietet reiche Jagdgründe, von denen wir leben können.]], [[In letzter Zeit jedoch scheinen wir die falschen Gäste angezogen zu haben... Die Spinnen in diesen Bergen haben nachts unsere Lager überfallen, um uns unsere Jagdbeute zu stehlen.]], [[Wenn Ihr uns helfen könntet, diese schrecklichen Bestien zu töten, würden wir hier am Malaka'jin zutiefst in Eurer Schuld stehen. Die Spinnen sind überall im Steinkrallengebirge - geht einfach von hier aus nach Norden, dann werdet Ihr sehen, was ich meine.]], },
    { [[Xen'zilla am Malaka'jin möchte, dass Ihr 10 Kluftmooskrabbler und 7 Kluftmoosgiftspucker tötet.]], },
  },
  [6462] = {
    [[Trollglücksbringer]],
    { [[Meine Leute waren mit vielen hochheiligen Trollglücksbringern im Gepäck unterwegs, aber dann wurden sie von Distelfellfurbolgs angegriffen und getötet, bevor sie Zoram'gar erreichen konnten! Diese elenden Furbolgs!]], [[Ich vermisse meine getöteten Brüder und Schwestern, aber wir werden uns im Jenseits wiedersehen. Die Trollglücksbringer haben in dieser Welt jedoch hohen Wert. Es ist eine Schande, dass sie jetzt in den Händen der Furbolgs sind!]], [[<name>, Ihr müsst diese Glücksbringer zurückholen! Sie befinden sich in Kisten in der Höhle, einer Höhle der Distelfelle am Ende des Lagers der Distelfelle nördlich von Astranaar.]], },
    { [[Bringt Mitsuwa bei Höllschreis Wacht 8 Trollglücksbringer.]], },
  },
  [6481] = {
    [[Der Irdene erhebt sich]],
    { [[Ihr habt eine dunkle Bedrohung für das Land entdeckt, <class>. Der Resonitbehälter, den Ihr in der Höhle gefunden habt, birgt einen schlummernden Irdenen namens Goggeroc.]], [[Ich habe die Mixtur der Resonitkristalle, die Ihr gesammelt habt, mithilfe meiner Schamanenkräfte verändert. Nehmt den verzauberten Resonitkristall und zerschmettert den Resonitbehälter, in dem sich der Irdene befindet.]], [[Wenn Goggeroc erwacht, wird er noch geschwächt sein... erschlagt ihn, <name>!]], },
    { [[Öffnet den Resonitbehälter mit dem verzauberten Resonitkristall und tötet Goggeroc. Kehrt mit der Vollzugsmeldung und dem verzauberten Resonitkristall zu Mor'rogal zurück.]], },
  },
  [6482] = {
    [[Freiheit für Ruul]],
    { [[Huch? Wer seid Ihr?]], [[<name>? Ihr seid kein Furbolg. Sie sind verflucht! Sie haben mich für einen Bären gehalten und mich eingefangen. Jetzt lassen sie mich verhungern! Meine Kraft schwindet...]], [[Helft mir zu entkommen, dann sprecht mit meiner Schwester Yama im Splitterholzposten im Osten.]], [[<name>! Ich muss entkommen!]], },
    { [[Begleitet Ruul und sprecht dann mit Yama Schneehuf im Splitterholzposten.]], },
  },
  [6501] = {
    [[Das Großdrachenauge]],
    { [[Ihr müsst die Welt nach jemandem absuchen, der fähig ist, die Macht des Fragments des Großdrachenauges wieder herzustellen. Die einzige Information, die Ihr über ein solches Wesen besitzt ist, dass es existiert.]], },
    { [[Ihr müsst die Welt nach jemandem absuchen, der fähig ist, die Macht des Fragments des Großdrachenauges wieder herzustellen. Die einzige Information, die Ihr über ein solches Wesen besitzt ist, dass es existiert.]], },
  },
  [6502] = {
    [[Drachenfeueramulett]],
    { [[Das ist ein Stück des Großdrachen. Eine Schuppe - verzaubert und transformiert. Als Ganzes hätte sie Zugang zum Hort der Bestie verschafft. Zertrümmert ist sie nutzlos.]], [[Es ist möglich, den Gegenstand zu reparieren, aber dazu wäre das Blut eines schwarzen Großdrachenhelden erforderlich. Es gibt einen... Er ist bekannt als General Drakkisath, Herrscher über die Drachenbrutarmeen von Nefarian. Vielleicht habt Ihr schon von ihm gehört?]], [[Bringt mir sein Blut, <race>, dann verzaubere ich das Fragment in etwas, das Euch Zugang zu Onyxias Hort verschaffen wird.]], },
    { [[Ihr müsst das Blut des schwarzen Drachenhelden von General Drakkisath bekommen. Ihr findet Drakkisath in seinem Thronsaal hinter den Hallen des Aufstiegs auf der Schwarzfelsspitze.]], },
  },
  [6503] = {
    [[Die Kundschafter des Eschentals]],
    { [[Verdammte Elfen! Die wusst'n genau, wo sie zuschlag'n mussten!]], [[Das war'n diese Kundschafter... Ich hab' ja versucht, sie zu warnen, dass wir bespitzelt werd'n, aber es wollte ja keiner auf mich hör'n. Und jetzt steh'n wir da: eingepfercht wie die Hühner!]], [[Is aber nie zu spät für Rache, Mann! Bringt sie zur Strecke und lasst sie zahlen für das, was sie uns angetan haben!]], },
    { [[Tötet 9 Kundschafter des Eschentals.]], },
  },
  [6504] = {
    [[Die verlorenen Seiten]],
    { [[Ich habe gerade diesen Schredder in Ratschet abgeholt und war auf dem Heimweg ins Holzfällerlager des Kriegshymnenklans, in dem ich eigentlich Dienst verrichten sollte. Man hat mir eine Anleitung zur Bedienung des Schredders mitgegeben, aber auf dem Rückweg hat ein starker Windstoß sie mir aus den Händen geweht. Die Seiten sind überallhin geflogen und mir blieb nur noch der Einband.]], [[Die Seiten könnten mittlerweile überall sein, nehme ich an...aber ich weiß nicht, wie ich den Schredder ohne sie benutzen soll! Würdet Ihr sie für mich suchen?]], },
    { [[Findet die 12 fehlenden Seiten der Schredderanleitung und fügt sie zu den Kapiteln 1, 2 und 3 zusammen. Bringt die Seiten zu Gurda Zornnarbe in der Nähe des Splitterholzpostens zurück.]], },
  },
  [6521] = {
    [[Eine unheilige Allianz]],
    { [[Malcin behauptet, dass er sich abseits der Hügel der Klingenhauer aufhalte. Dieser Botschafter muss getötet werden - seine Mission für den Lichkönig muss fehlschlagen.]], [[Seine Anwesenheit dort erklärt vieles von dem, was wir über diese Gegend herausgefunden haben - einige der Stacheleber sind beängstigend stark geworden und zeigen erste Symptome der Seuche. Das muss in der Tat ein Anzeichen dafür sein, dass sie irgendeine Übereinkunft getroffen haben.]], [[Sucht Malcin und tötet ihn, <name>. Kommt zurück, wenn Ihr das erledigt habt.]], },
    { [[Bringt den Kopf von Botschafter Malcin zu Bragor Blutfaust nach Unterstadt.]], },
  },
  [6522] = {
    [[Eine unheilige Allianz]],
    { [[Mylady,]], [[Ihr wisst sehr genau, was wir Euch anbieten. Die Gelegenheit, das Brachland und das dahinter liegende Gebiet zu erobern, ist nicht zu verachten. Wir verlangen dafür nur Eure Hilfe. Mit Hilfe meinen wir nicht weniger, als dass Ihr uns die Freiheit zugesteht, in dem Gebiet aktiv zu werden.]], [[Erlaubt uns bitte, dies weiter auszuführen. Wir haben große Macht und sind gewillt, sie mit jenen zu teilen, die weise genug sind, sich uns anzuschließen. Ich werde mich bis auf Weiteres abseits der Hügel der Klingenhauer aufhalten.]], [[Hochachtungsvoll,]], [[Botschafter Malcin]], },
    { [[Bringt die kleine Rolle zu Bragor Blutfaust im Königsviertel von Unterstadt.]], },
  },
  [6523] = {
    [[Schützt Kaya]],
    { [[Habt Dank für meine Rettung! Wir müssen schnell fort von hier, ehe man bemerkt, dass ich frei bin. Bitte begleitet mich zum Camp Aparaje. Von dort aus kenne ich den Weg.]], [[Mein Vater Makaba Flachhuf wird unbedingt wissen wollen, dass ich in Sicherheit bin.]], },
    { [[Begleitet Kaya Flachhuf zum Camp Aparaje und kehrt dann zu Makaba Flachhuf am südöstlichen Rand des Steinkrallengebirges zurück.]], },
  },
  [6541] = {
    [[Meldung bei Kadrak]],
    { [[Seid Ihr jemals im Norden des Eschentals gewesen? Die Horde hat dort vor Kurzem einen Außenposten am Zoramstrand errichtet und wir können immer ein paar neue Rekruten gebrauchen, die uns helfen, unsere neuen Grenzen zu verteidigen.]], [[Wenn Ihr der Meinung seid, dass Ihr dafür in Frage kommt, sucht Kadrak im Wachturm im Norden des Brachlands auf. Er leitet die Eschentalkampagne und kann Euch weitere Befehle geben.]], },
    { [[Meldet Euch im Wachturm im Norden des Brachlands bei Kadrak.]], },
  },
  [6542] = {
    [[Meldung bei Kadrak]],
    { [[Seid Ihr jemals im Norden des Eschentals gewesen? Die Horde hat dort vor Kurzem einen Außenposten am Zoramstrand errichtet und wir können immer ein paar neue Rekruten gebrauchen, die uns helfen, unsere neuen Grenzen zu verteidigen.]], [[Wenn Ihr der Meinung seid, dass Ihr dafür in Frage kommt, sucht Kadrak im Wachturm im Norden des Brachlands auf. Er leitet die Eschentalkampagne und kann Euch weitere Befehle geben.]], },
    { [[Meldet Euch im Wachturm im Norden des Brachlands bei Kadrak.]], },
  },
  [6543] = {
    [[Die Berichte des Kriegshymnenklans]],
    { [[Ich habe kürzlich ein paar Späher ins Eschental entsandt und brauche jetzt einen Kurier, der ihnen neue Befehle übermittelt und mir einen Bericht über deren Entdeckungen bringt.]], [[Euer erster Halt sollte der Außenposten von Zoram'gar an der Küste Richtung Westen sein. Dort solltet Ihr den ersten Läufer finden. Ihr müsst weiterhin beim Splitterholzposten im Norden und an der Straße nach Osten, die nach Azshara führt, Halt machen. Dort findet Ihr einen Späher und eine Vorhut.]], [[Erstattet allen Bericht und lasst Euch von ihnen über ihre Entdeckungen informieren.]], },
    { [[Bringt die Berichte des Kriegshymnenklans dem Späher des Kriegshymnenklans, dem Läufer des Kriegshymnenklans und der Vorhut des Kriegshymnenklans. Meldet die Neuigkeiten, die Ihr von ihnen erfahrt, Kadrak im nördlichen Wachturm des Brachlands.]], },
  },
  [6544] = {
    [[Toreks Angriff]],
    { [[He! <class>! Kommt mal her!]], [[Meine Räuber und ich wollten gerade den Außenposten der Silberschwingen im Westen angreifen. Unser Ziel ist die dortige Anführerin, Duriel Mondfeuer. Späher berichten, dass der Außenposten durch Schildwachen und Krieger stark verteidigt wird. Das wird ein ruhmreicher Kampf!]], [[Wollt Ihr an dem Ruhm teilhaben und uns helfen, <name>? Falls ja, könnt Ihr meinem Vorgesetzten Ertog Zornhauer im Splitterholzposten später davon berichten.]], [[Was meint Ihr? Seid Ihr dabei?]], },
    { [[Eskortiert Torek bei seinem Angriff auf den Außenposten der Silberschwingen und Duriel Mondfeuer. Erstattet dann Ertog Zornhauer im Splitterholzposten Bericht.]], },
  },
  [6545] = {
    [[Aktualisierter Bericht des Läufers des Kriegshymnenklans]],
  },
  [6546] = {
    [[Aktualisierter Bericht der Vorhut des Kriegshymnenklans]],
  },
  [6547] = {
    [[Aktualisierter Bericht des Spähers des Kriegshymnenklans]],
  },
  [6548] = {
    [[Rächt mein Dorf]],
    { [[Tötet 8 Grobiane der Grimmtotem und 6 Söldner der Grimmtotem. Kehrt dann zu Makaba Flachhuf am südöstlichen Rand des Steinkrallengebirges zurück.]], },
    { [[Tötet 8 Grobiane der Grimmtotem und 6 Söldner der Grimmtotem. Kehrt dann zu Makaba Flachhuf am südöstlichen Rand des Steinkrallengebirges zurück.]], },
  },
  [6561] = {
    [[Schurkerei in der Tiefschwarzen Grotte]],
    { [[Die Kraft hat mich verlassen. Ich benötige Eure Hilfe!]], [[Diese Stätte war einst ein großer Tempel von Elune. Doch das Unglück führte zum Untergang, als die Verderbnis eines Alten Gottes unter der Erde emporwallte und den heiligen Mondbrunnen besudelte.]], [[Aku'mai, die Dienerin der Alten Götter, erhob sich aus den Wassern.]], [[Die Kultisten des Schattenhammers haben sich mit den Naga verschworen, um diese Ländereien zu besetzen. Die Kultisten opfern unter Führung von Kelris Unschuldige für Aku'mai, um Macht zu erlangen.]], [[Bitte, erschlagt Kelris und bringt Bashana in Donnerfels seinen Kopf, <name>.]], },
    { [[Bringt den Kopf von Zwielichtfürst Kelris zu Bashana Runentotem in Donnerfels.]], },
  },
  [6562] = {
    [[Ärger in der Tiefe]],
    { [[Je'neu Sancrea braucht Hilfe. Er wartet am Eingang zur Tiefschwarzen Grotte im Eschental. Der Nachtelfenwald liegt im Norden des Steinkrallengebirges. Ihr erreicht ihn, indem Ihr über die Scherwindklippe oder nördlich durchs Brachland reist. Der Zoramstrand liegt weit im Westen des Waldes. Seid vorsichtig, <name>. Dort gibt es Mächte, denen Eure Anwesenheit nicht gefallen wird.]], },
    { [[Sprecht im Eschental mit Je'neu Sancrea.]], },
  },
  [6563] = {
    [[Die Essenz von Aku'mai]],
    { [[Im Eingangsbereich des Tunnels der Tiefschwarzen Grotte findet man überall an den Höhlenwänden große Saphiransammlungen. Kürzlich ist aufgefallen, dass die Naga diese Kristalle abbauen. Bis wir herausgefunden haben, warum sie das tun, sollten wir die Naga einstweilen davon abhalten. Die Kristalle besitzen große Elementarkraft und wenn sie unangemessen eingesetzt werden, könnte man daraus eine mächtige arkane Komponente herstellen.]], [[Werdet Ihr mir helfen, <name>? Die Tiefschwarze Grotte liegt am Zoramstrand nördlich unseres Außenpostens.]], },
    { [[Bringt 20 Saphire von Aku'mai zu Je'neu Sancrea im Eschental.]], },
  },
  [6564] = {
    [[Treue zu den Alten Göttern]],
    { [[Die zerknitterte Notiz trägt eine kaum leserliche Handschrift:]], [["Ja, sagt allen Priesterinnen, dass sie mir die Kristalle bringen sollen. Die Wasserelementare sind stark in ihnen und sie werden unseren Aquamanten bei ihren Beschwörungen ungemein dienlich sein.]], [[Indem wir die Kraft dieser Kristalle beherrschen, beschwichtigen wir Aku'mai. Eines Tages werden wir genug Macht haben, um Aku'mai zu uns zurückzuholen. Dann wird die Welt wieder zur Ära ihrer einstigen Größe zurückkehren! Eine Ära, in der die Alten Götter herrschen!"]], },
    { [[Bringt die durchfeuchtete Notiz zu Je'neu Sancrea im Eschental.]], },
  },
  [6565] = {
    [[Treue zu den Alten Göttern]],
    { [[Dieser Anhänger des Schattenhammerklans darf seinen Plan nicht in die Tat umsetzen. Die Schattenhammerkultisten verstehen die Geister der Natur nicht. Sie glauben an die Alten Götter - Kreaturen des Chaos und der Zerstörung, die vor langer Zeit besiegt wurden. Dieser Lorgus darf keinen Erfolg haben. Ihr müsst ihn aufhalten, <name>. Ich kann mich leider nicht selbst darum kümmern... ich bin zu schwach.]], [[Geht zur Tiefschwarzen Grotte, findet ihn und tötet ihn. Kehrt hierher zu mir zurück.]], },
    { [[Tötet Lorgus Jett.]], },
  },
  [6566] = {
    [[Was der Wind erzählt]],
    { [[Hört Thrall zu.]], },
    { [[Hört Thrall zu.]], },
  },
  [6567] = {
    [[Der Champion der Horde]],
    { [[Sucht in Schattenflucht in Desolace nach Rokaro.]], },
    { [[Sucht in Schattenflucht in Desolace nach Rokaro.]], },
  },
  [6568] = {
    [[Meisterin der Illusionen]],
    { [[Illusion und Täuschung. So werdet Ihr es schaffen, den schwarzen Drachenschwarm zu überlisten und zu erlangen, was Ihr sucht.]], [[Auch wenn ich Euch in der Kunst der Täuschung unterrichten könnte, werdet Ihr die Hilfe eines Meistern benötigen.]], [[Ich kenne jemanden, der Euch dabei helfen kann. In den Westlichen Pestländern findet Ihr Myranda die Vettel, eine Meisterin der Illusionen aus Lordaeron. Sie hat uns in der Vergangenheit bereits geholfen. Wenn Ihr sie davon überzeugen könnt, dass Eure Sache es wert ist, wird sie Euch vielleicht unterstützen.]], [[Bringt diese Nachricht zu ihr.]], },
    { [[Überbringt Myranda der Vettel in den Westlichen Pestländern Rokaros Brief.]], },
  },
  [6569] = {
    [[Oculus-Illusionen]],
    { [[Ihr möchtet also einer vom schwarzen Drachenschwarm werden, was, <race>?]], [[Die Erschaffung einer Illusion, wie Ihr sie wünscht, ist eine Kleinigkeit, aber die für den Zauber erforderlichen Komponenten sind ziemlich schwierig zu beschaffen. Der Kriegshäuptling hat Euch mit dieser Mission betraut, also muss Myranda wohl annehmen, dass Ihr fähig dazu seid.]], [[Reist zur oberen Zitadelle der Schwarzfelsspitze und tötet genug Mitglieder der schwarzen Drachenbrut, um eine Eurer Taschen mit deren Augen zu füllen.]], [[Kehrt zu Myranda zurück, wenn Ihr genügend von dem Reagenz gesammelt habt.]], },
    { [[Reist zur Schwarzfelsspitze und sammelt 20 schwarze Drachenbrutaugen. Kehrt zu Myranda der Vettel zurück, sobald Ihr die Aufgabe erfüllt habt.]], },
  },
  [6570] = {
    [[Aschenschwinge]],
    { [[Reist zum Drachensumpf in den Düstermarschen und begebt Euch in Aschenschwinges Bau. Sobald Ihr darin seid, tragt das Amulett der drachischen Subversion und sprecht mit Aschenschwinge.]], },
    { [[Reist zum Drachensumpf in den Düstermarschen und begebt Euch in Aschenschwinges Bau. Sobald Ihr darin seid, tragt das Amulett der drachischen Subversion und sprecht mit Aschenschwinge.]], },
  },
  [6571] = {
    [[Nachschub des Kriegshymnenklans]],
    { [[Uns hier im Holzfällerlager des Kriegshymnenklans gehen langsam einige Dinge aus!]], [[Unsere Lieferung Sägeblätter liegt bei Pixel im Splitterholzposten. Jemand muss auch nach Beutebucht reisen, um eine Kiste mit Äxten bei Werftmeister Lozgil abzuholen.]], [[Bei unserer Öl- und Seillieferung sieht es jedoch schon schwieriger aus. Die Furbolgs haben die Seile geraubt und die Satyrn haben das Öl abgefangen und nutzen es jetzt für abscheuliche Rituale in der Nachtflucht und Satyrnaar!]], [[Ich komme in große Schwierigkeiten, wenn ich die Sachen nicht bald ausliefere... könnt Ihr mir helfen?]], },
    { [[Tragt den Nachschub des Kriegshymnenklans zusammen: Sägeblätter des Kriegshymnenklans, ein Holzfällerseil, Öl des Kriegshymnenklans und die Axtlieferung des Kriegshymnenklans für Locke Okarr im Splitterholzposten.]], },
  },
  [6581] = {
    [[Sägeblätter des Kriegshymnenklans]],
  },
  [6582] = {
    [[Die Prüfung der Schädel, Seher]],
    { [[Wir sind Kinder von Todesschwinge, Welpe. Wir müssen die Herrschaft über die niederen Aspekte durchsetzen.]], [[Wenn Ihr wirklich eines Aufstiegs würdig seid, werden die Geringeren des Drachenschwarms vor Euch niedersinken.]], [[Findet deren Champions und vernichtet sie im Namen unseres Vaters!]], [[Begebt Euch nach Winterquell und spürt den mächtigen Blaudrachen Seher auf. Zerschmettert ihn und alle anderen des blauen Drachenschwarms, die sich Euch in den Weg stellen. Entreißt ihm seinen Schädel und bringt ihn mir.]], },
    { [[Ihr müsst Seher, den Champion der blauen Großdrachen, finden und ihn töten. Schlagt seinem Kadaver den Schädel ab und bringt ihn zu Aschenschwinge.]], [[Ihr wisst, dass Seher in Winterquell zu finden ist.]], },
  },
  [6583] = {
    [[Die Prüfung der Schädel, Somnus]],
    { [[Genau wie unser Vater die Aspekte zerschmettert, müssen wir danach trachten, Chaos und Zerstörung unter ihren Kindern anzurichten.]], [[Ihr müsst Eure Leistungsfähigkeit im Kampf gegen die schlaflosen Träumer auf die Probe stellen: die Kinder des Gründrachenschwarms.]], [[Begebt Euch zu den Sümpfen des Elends und spürt Somnus auf, den Drachenchampion des Gründrachenschwarms. Lasst Eurem Hass freien Lauf und bezwingt das Insekt...Kommt mit seinem Schädel zu mir zurück...]], },
    { [[Vernichtet den Drachenchampion des grünen Drachenschwarms, Somnus. Nehmt seinen Schädel und bringt ihn zu Aschenschwinge zurück.]], },
  },
  [6584] = {
    [[Die Prüfung der Schädel, Chronalis]],
    { [[Die Zeitbehüter, Kinder Nozdormusss - Lord der Jahrhunderte...]], [[Es sind derer drei, die die Höhlen der Zeit bewachen, aber nur einer, an dem der schwarze Drachenschwarm interessiert ist: Chronalisss, der Zeitverschieber, bevorzugter Drachling Nozdormusss.]], [[Besssiegt diesssen Champion, denn das wäre ein Schlag, der sssogar den stoischen Sand der Zzzeit erschüttern würde. Kehrt mit seinem Schädel zu mir zzzurück.]], },
    { [[Chronalis, ein Kind Nozdormus, bewacht die Höhlen der Zeit in der Tanariswüste. Vernichtet ihn und bringt seinen Schädel zu Aschenschwinge.]], },
  },
  [6585] = {
    [[Die Prüfung der Schädel, Axtroz]],
    { [[Es war in den Bergen von Grim Batol, wo unser Vater gegen die vereinte Macht der Aspekte wütete. Die feige Tat der Verräterin Alexstrasza verschaffte den übrigen Aspekten genug Zeit, um dem Zorn des Vaters zu entkommen.]], [[Wasss für ein schändlicher Akt...]], [[Der rote Drachenschwarm bewacht heute Grim Batol und ihr Leutnant Axtroz muss vernichtet werden. Bringt mir Axtroz' Schädel. Für den Vater, Welpe!]], },
    { [[Begebt Euch nach Grim Batol und spürt Axtroz auf, den Drachenchampion des roten Drachenschwarms. Vernichtet ihn und nehmt seinen Schädel. Bringt den Schädel zu Aschenschwinge zurück.]], },
  },
  [6601] = {
    [[Aufstieg...]],
    { [[Ein Medaillon, aus den Schädeln Eurer Feinde gefertigt. Ihr kennt dieses Medaillon, nicht wahr? Zweifellos habt Ihr gesehen, wie Eure Ältesten es trugen.]], [[Nehmt es, Welpe. Begebt Euch zur Spitze zurück und zeigt es General Drakkisath. Der General wird das Medaillon mit der letzten Verzauberung versehen und das Schmuckstück auf Euren Geist abstimmen.]], [[Ihr werdet das fertige Medaillon als Ehrenabzeichen tragen, als Symbol für Euren Aufstieg in unsere am höchsten geschätzten Reihen: Wächter der Brutmutter.]], [[Geht!]], },
    { [[Mir scheint, als sei die Charade jetzt vorüber. Ihr wisst, dass das Amulett der drachischen Unterwanderung, das Myranda die Vettel für Euch erschuf, auf der Schwarzfelsspitze nicht funktioniert. Vielleicht solltet Ihr Rokaro aufsuchen und ihm Eure Notlage erklären. Zeigt ihm das glanzlose Drachenfeueramulett. Er wird hoffentlich wissen, was als Nächstes zu tun ist.]], },
  },
  [6602] = {
    [[Blut des schwarzen Großdrachenhelden]],
    { [[Ihr werdet dem General einen Besuch abstatten, jawohl, aber nicht als einer des schwarzen Drachenschwarms.]], [[Wisst Ihr, Zeremonie ist für den schwarzen Drachenschwarm nur ein anderer Begriff für Aderlass.]], [[Das ruhende Amulett benötigt nur das Blut des Generals, um wieder aktiv zu sein. Das ist einer ihrer primitiven, ausfallsicheren Mechanismen.]], [[Kehrt zur Schwarzfelsspitze zurück und vernichtet Drakkisath. Bringt sein Blut hierher zurück, dann aktiviere ich den Schlüssel zu Onyxias Hort.]], },
    { [[Begebt Euch zur Schwarzfelsspitze und tötet General Drakkisath. Sammelt sein Blut und bringt es zu Rokaro.]], },
  },
  [6603] = {
    [[Probleme in Winterquell!]],
    { [[Findet Donova Schneeweh bei den Thermalquellen in Winterquell.]], },
    { [[Findet Donova Schneeweh bei den Thermalquellen in Winterquell.]], },
  },
  [6604] = {
    [[Wütende Wildekin]],
    { [[Ist Euch das verlassene Lager südlich von hier aufgefallen? Es scheint, als wären die Wildekin verrückt geworden und haben so gut wie alles zerstört, was sie finden konnten!]], [[Ich hab Gerüchte gehört, wonach einer der Überlebenden des Angriffs sich in Sternfall aufhalten soll, das liegt westlich von hier. Ich bin neugierig, was wohl geschehen sein mag...Ihr nicht auch?]], },
    { [[Sprecht mit Jaron Steinschleifer in Sternfall.]], },
  },
  [6605] = {
    [[Eine seltsame Person]],
    { [[Ich bin gerade erst vom Teufelswald zurückgekehrt, <name>. Unterwegs habe ich im Blutgiftposten Halt gemacht und eine wirklich eigenartige Bekanntschaft gemacht... Sie schien ganz besessen von einer Sache gewesen zu sein, und ich könnte Euch wohl erzählen, um was es sich handelt, aber ich denke, das sollte sie Euch selbst erklären.]], [[Geht zum Blutgiftposten im Westen des Teufelswalds und fragt nach Winna Hazzard, falls Ihr Interesse habt.]], [[Wer weiß, vielleicht braucht sie ja Eure Hilfe...]], },
    { [[Sprecht im Teufelswald mit Winna Hazzard.]], },
  },
  [6606] = {
    [[Ein bisschen Glück]],
    { [[Ein bisschen Glück kann man immer gebrauchen, nicht wahr? Ein kleiner Vorteil ist alles, was man braucht, um eine günstige Wendung herbeizuführen...]], [[Ich habe vor einiger Zeit eine ziemlich exzentrische Hexendoktorin kennen gelernt. Sie fertigte einen Glücksbringer für mich... nicht gerade billig. Aber ich sage Euch, er war es auch wert!]], [[Möchtet Ihr vielleicht selbst gern einen haben? Sucht Hexendoktorin Mau'ari in der Ewigen Warte auf. Meinen bekommt Ihr nämlich nicht!]], },
    { [[Sprecht in der Ewigen Warte mit Hexendoktorin Mau'ari.]], },
  },
  [6607] = {
    [[Nat Pagle, Extremangler]],
    { [[Ich kann nicht behaupten, dass ich gern angele. Ich glaub, ich mach das jetzt seit gut 20 oder 30 Jahren.]], [[Es gibt wohl viele Gründe, die einen dazu treiben, etwas zu tun.]], [[Ich will damit jetzt nicht sagen, dass Ihr 30 Jahre lang angeln oder jeden einzelnen Fisch im Meer fangen müsst, um ein Meisterangler zu werden. Man muss einfach nur entschlossen sein - entschlossen genug, um stundenlang auf dem Hintern zu sitzen und nichts zu tun.]], [[Ich sag Euch was: Fangt mir ein paar seltene Fische, dann bring ich Euch das eine oder andere bei. Hier habt Ihr eine Liste:]], },
    { [[Nat Pagle möchte, dass Ihr folgende Fische fangt:]], [[Nebelschilf-Mahi Mahi vom Nebelschilfstrand in den Sümpfen des Elends.]], [[Einen Sar'therisbarsch vom Sar'therisstrand in Desolace.]], [[Feralas-Ahi aus dem Verdantis in Feralas.]], [[Blauwimpel von der Ungezähmten Küste im Schlingendorntal.]], [[Kehrt zu Nat Pagle zurück, wenn Ihr alle an Land gezogen habt!]], },
  },
  [6608] = {
    [[Ihr zu gut]],
    { [[Lumak können <name> nix mehr beibringen. Ihr zu gut! Finden Freund von Lumak, Nat Pagle. Er sein Mensch, aber nix Futter! Ist Freund von Lumak! Nat Euch zeigen, wie besserer angeln.]], [[Ihr finden Nat in den Düstermarschen, Süden von Thermoro-odersoähnlich. Nat bestimmt angeln. Ihr jetzt gehen!]], },
    { [[Ihr nehmt an, dass Lumak Euch zu erklären versucht, dass er nicht mehr tun kann, um Eure Angelfertigkeit zu verbessern. Ihr solltet seinen Freund aufsuchen, diesen Nat Pagle, einen Menschen, der auf einer der Inseln südlich von Theramore (jedenfalls glaubt Ihr, dass Lumak das gemeint hat) auf Fischfang geht.]], },
  },
  [6609] = {
    [[Ich hab nix mehr!]],
    { [[Ich bin ein Zwerg, zum Donnerwetter nochmal, <Jungchen/junge Dame>! Ich hab mit Angeln nichts am Hut! Ich meine, seht mich doch mal an! Ich bestehe praktisch aus Stein.]], [[Ich hab nichts mehr - Ihr habt mich ausgelaugt. Ich hab Euch alles beigebracht, was ich weiß, flipflop!]], [[Kapiert Ihr's nicht? Ah, gut. Ich hab in Kalimdor einen alten Freund, der Euch weiterhelfen kann. Heißt Nat Pagle, guter Junge. Findet ihn auf den Inseln südlich von Theramore. Jetzt verfatzt Euch!]], },
    { [[Reist nach Kalimdor zur Küstenstadt Theramore. Dort müsst Ihr nach einer Insel suchen, die südlich der Hauptstadt liegt. Nat Pagle erwartet Euch!]], },
  },
  [6610] = {
    [[Muschelette Surprise]],
    { [[Schon mal von einem Muschelette gehört? Natürlich nicht! Das ist mein neuestes kulinarisches Meisterwerk. Oh, <name>, mir läuft das Wasser im Mund zusammen, wenn ich nur an ein Muschelette denke.]], [[Leider habe ich nichts mehr hier, um eins zu machen! Wie wär's mit einem Handel? Ihr besorgt die Zutaten für ein Muschelette und ich bring Euch die Feinheiten der exotischen Küche bei und leg' sogar noch ein paar Muschelettes drauf! Abgemacht?]], [[Und das brauche ich dafür:]], },
    { [[Dirge Schnetzelhack möchte, dass Ihr folgende Dinge besorgt:]], [[10 Rieseneier. Ihr findet sie bei den Rocs, die Tanaris durchstreifen, oder anderen Großvogelarten.]], [[10 Stück scharfes Muschelfleisch. Das findet Ihr natürlich in Muscheln!]], [[20 Stück feiner alter Cheddar. Begebt Euch zu einer Käserei!]], [[Sammelt all diese Gegenstände und kehrt dann zu Dirge zurück.]], },
  },
  [6611] = {
    [[Ab mit Euch nach Gadgetzan!]],
    { [[Reist nach Gadgetzan in der Tanariswüste und sprecht mit Dirge Schnetzelhack über den Ausbau Eurer Kochkarriere.]], },
    { [[Reist nach Gadgetzan in der Tanariswüste und sprecht mit Dirge Schnetzelhack über den Ausbau Eurer Kochkarriere.]], },
  },
  [6612] = {
    [[Ich kenne einen Typen...]],
    { [[Reist nach Gadgetzan in der Tanariswüste und sprecht mit Dirge Schnetzelhack über den Ausbau Eurer Kochkarriere.]], },
    { [[Reist nach Gadgetzan in der Tanariswüste und sprecht mit Dirge Schnetzelhack über den Ausbau Eurer Kochkarriere.]], },
  },
  [6621] = {
    [[König der Fäulnisklauen]],
    { [[Meine Forschungen haben sich bezahlt gemacht.]], [[Ich glaube, dass eine Machtdemonstration die Angriffslust der Furbolgs verringern wird.]], [[Nehmt dieses Banner, geht ein paar Schritte in Richtung Nordosten und platziert es auf ihrem Totemhügel. Sie werden versuchen, das Banner zu zerstören, aber Ihr müsst Eure Stellung halten, bis Häuptling Murgut auftaucht, um das Gesicht seines Stammes zu wahren. Bringt mir das Totem, das er trägt.]], [[Versammelt Eure Verbündeten, <name>. Dies ist keine Aufgabe, die <ein einzelner/eine einzelne><class> <class> bewältigen kann.]], },
    { [[Platziert Senanis Banner auf dem Totemhügel der Fäulnisklauen. Die Furbolgs dürfen das Banner nicht zerstören. Besiegt Häuptling Murgut und bringt Murguts Totem zu Senani in der Silberwindzuflucht.]], },
  },
  [6622] = {
    [[Triage]],
    { [[Rettet Patienten, indem Ihr deren Wunden mit Triagenverbänden versorgt. Ihr müsst 15 Patienten retten, bevor 6 von ihnen ihren Verletzungen erliegen.]], [[Denkt an die Einhaltung der Reihenfolge! Schwerstverletzte Soldaten sterben am schnellsten, dann die schwer verletzten Soldaten. Bei verletzten Soldaten dauert es etwas länger, bis sie sterben. Ihr solltet sie retten, NACHDEM Ihr die schwerstverletzten und schwer verletzten Soldaten selektiert habt.]], },
    { [[Rettet Patienten, indem Ihr deren Wunden mit Triagenverbänden versorgt. Ihr müsst 15 Patienten retten, bevor 6 von ihnen ihren Verletzungen erliegen.]], [[Denkt an die Einhaltung der Reihenfolge! Schwerstverletzte Soldaten sterben am schnellsten, dann die schwer verletzten Soldaten. Bei verletzten Soldaten dauert es etwas länger, bis sie sterben. Ihr solltet sie retten, NACHDEM Ihr die schwerstverletzten und schwer verletzten Soldaten selektiert habt.]], },
  },
  [6623] = {
    [[Traumachirurg der Horde]],
    { [[Doktor <name>, nehme ich an. Es ist mir eine Freude, Euch wiederzusehen.]], [[Ich habe wunderbare Neuigkeiten für Euch! Doktor Gregory Victor, Chefarzt der Traumachirurgen der Horde, hat Euch persönlich angefordert, damit Ihr Euch ihm in Hammerfall anschließt. Das könnte eine einmalige Gelegenheit sein! Ich würde alles darum geben, bei dem großen Doktor Gregory Victor studieren zu dürfen! Diese Gelegenheit dürft Ihr Euch nicht entgehen lassen!]], },
    { [[Reist nach Hammerfall im Arathihochland und sprecht mit Doktor Gregory Victor darüber, dass Ihr auch ein Traumachirurg der Horde werden wollt.]], },
  },
  [6624] = {
    [[Triage]],
    { [[Rettet Patienten, indem Ihr deren Wunden mit Triagenverbänden versorgt. Ihr müsst 15 Patienten retten, bevor 6 von ihnen ihren Verletzungen erliegen.]], [[Denkt an die Einhaltung der Reihenfolge! Schwerstverletzte Soldaten sterben am schnellsten, dann die schwer verletzten Soldaten. Bei verletzten Soldaten dauert es etwas länger, bis sie sterben. Ihr solltet sie retten, NACHDEM Ihr die schwerstverletzten und schwer verletzten Soldaten selektiert habt.]], },
    { [[Rettet Patienten, indem Ihr deren Wunden mit Triagenverbänden versorgt. Ihr müsst 15 Patienten retten, bevor 6 von ihnen ihren Verletzungen erliegen.]], [[Denkt an die Einhaltung der Reihenfolge! Schwerstverletzte Soldaten sterben am schnellsten, dann die schwer verletzten Soldaten. Bei verletzten Soldaten dauert es etwas länger, bis sie sterben. Ihr solltet sie retten, NACHDEM Ihr die schwerstverletzten und schwer verletzten Soldaten selektiert habt.]], },
  },
  [6625] = {
    [[Traumachirurg der Allianz]],
    { [[Doktor <name>, nehme ich an. Es ist mir eine Freude, Euch zu sehen.]], [[Ich habe wunderbare Neuigkeiten für Euch! Gustav van Howzen, Chefarzt der Traumachirurgen der Allianz, hat Euch persönlich angefordert, damit Ihr Euch ihm in Theramore anschließt. Das könnte eine einmalige Gelegenheit sein! Ich würde alles darum geben, bei dem großen Doktor Gustav van Howzen studieren zu dürfen! Diese Gelegenheit dürft Ihr Euch nicht entgehen lassen, Doktor!]], },
    { [[Reist nach Theramore in die Düstermarschen und sprecht mit Doktor Gustav van Howzen darüber, dass Ihr auch ein Traumachirurg der Allianz werden wollt.]], },
  },
  [6626] = {
    [[Ein Hort des Bösen]],
    { [[Die Verlassenen geben ihre... Krankheit... nicht weiter, soweit ich das sehe. Dieser Diplomat scheint Untote als Möchtegernanhänger zu betrachten. Ich glaube langsam, dass er der Geißel angehört.]], [[Um ihn mache ich mir allerdings momentan keine Gedanken, sondern um die wachsende Schar um ihn herum. Tag für Tag kriechen sie aus den Schatten und betteln ihn nahezu um mehr Macht an.]], [[Ich bin nicht stark genug, um sie allein zu bekämpfen, <name>. Was meint Ihr? Hättet Ihr Lust, auf die Jagd zu gehen?]], },
    { [[Tötet 8 Schlachtwachen der Klingenhauer, 8 Dornenwirker der Klingenhauer und 8 Kultistinnen der Totenköpfe und kehrt dann zu Myriam Mondsang nahe dem Eingang zu den Hügeln der Klingenhauer zurück.]], },
  },
  [6627] = {
    [[Test der Lehre]],
    { [[Ihr habt mir das Buch gebracht, und jetzt ist es an der Zeit für Eure Befragung. Seid Ihr bereit?]], [[Zeit spielt keine Rolle, <class>, also fühlt Euch nicht unter Druck gesetzt. Ihr könnt Euch mit der Antwort so viel Zeit lassen, wie Ihr möchtet.]], },
    { [[Beantwortet Braug Dämmergeists Frage richtig und sprecht dann erneut mit ihm. Er wird immer noch im Steinkrallengebirge sein, wenn Ihr bereit seid.]], },
  },
  [6628] = {
    [[Test der Lehre]],
    { [[Die Zeit ist also wieder einmal gegen uns. Für einen Studenten der Lehre habt Ihr große Hartnäckigkeit bewiesen, indem Ihr meine Aufgabe erledigt habt.]], [[Und deshalb werde ich Euch eine Frage anbieten. Wenn Ihr bereit seid, sprecht mich wieder an, und lasst Euch so viel Zeit, wie Ihr braucht, <name> - es besteht keine Notwendigkeit, schnell zu antworten.]], },
    { [[Beantwortet Parqual Fintallas' Frage richtig und sprecht dann erneut mit ihm. Er wird immer noch in Unterstadt sein, wenn Ihr bereit seid.]], },
  },
  [6629] = {
    [[Tötet Grundig Finsterwolke]],
    { [[Tötet Grundig Finsterwolke und 6 Schläger der Grimmtotem und kehrt dann zu Makaba Flachhuf am südöstlichen Rand des Steinkrallengebirges zurück.]], },
    { [[Tötet Grundig Finsterwolke und 6 Schläger der Grimmtotem und kehrt dann zu Makaba Flachhuf am südöstlichen Rand des Steinkrallengebirges zurück.]], },
  },
  [6641] = {
    [[Vorsha die Peitscherin]],
    { [[Vorsha die Peitscherin... die satanische Hydra, die die Meere durchstreift...]], [[Sie hat unseren Außenposten angegriffen, völlig ohne Grund, und dabei alles verheert, was auf ihrem Weg lag.]], [[Aber ich bin bereit, <name>. Ich bin bereit, die Hydra zu besiegen und den Angriffen auf Zoram'gar ein Ende zu setzen.]], [[Die Naga verehren die tückische Natur dieser Bestie mit einer Kohlenpfanne, die auf einer Insel gleich vor der Küste immer am Brennen gehalten wird. Ich denke, dass Vorsha erzürnt auftauchen wird, wenn wir sie löschen.]], [[Wir werden jedoch Hilfe brauchen, also solltet Ihr ein paar starke Verbündete mitbringen.]], },
    { [[Bezwingt Vorsha die Peitscherin.]], },
  },
