  [6721] = {
    [[Der Weg des Jägers]],
    { [[Hallo, so jung und schon auf der Jagd. Ihr seht mir talentiert genug aus, um ein Tier zu führen und es zu Eurem Gefährten abzurichten.]], [[Ihr müsst Dazalar in Dolanaar aufsuchen. Er kann Euch auf den Weg zu einem eigenen Tier führen.]], },
    { [[Sprecht mit Dazalar in Dolanaar in Teldrassil.]], },
  },
  [6722] = {
    [[Der Weg des Jägers]],
    { [[Hallo, so jung und schon auf der Jagd. Ihr seht mir talentiert genug aus, um ein Tier zu führen und es zu Eurem Gefährten abzurichten.]], [[Ihr müsst Dazalar in Dolanaar aufsuchen. Er kann Euch auf den Weg zu einem eigenen Tier führen.]], },
    { [[Sprecht mit Dazalar in Dolanaar in Teldrassil.]], },
  },
  [6741] = {
    [[Mehr Beute!]],
  },
  [6761] = {
    [[Die neue Grenze]],
    { [[Ich habe meinen Stellvertreter Mathrengyl Bärenfährte beauftragt, sich um die Bitte um Unterstützung der Mondlichtung zu kümmern. Sprecht mit ihm über diese Angelegenheit - NICHT mit mir.]], [[Lasst mich Euch einen... gut gemeinten... Rat geben. ICH habe uns aus der Dunkelheit geführt - NICHT der Zirkel des Cenarius auf der Mondlichtung -, als für unser Volk alles verloren war. Teldrassil gibt uns allen neues Leben! Sie wollen Euch anderes glauben machen, und ihre Propaganda zu glauben, wäre reine Blauäugigkeit.]], [[Ich werde es Euch überlassen, ob Ihr ein Narr sein wollt oder nicht.]], },
    { [[Sprecht mit Mathrengyl Bärenfährte in der Enklave des Cenarius von Darnassus.]], },
  },
  [6762] = {
    [[Rabine Saturna]],
    { [[Für diese Aufgabe sucht Majordomus Rabine Saturna für Bewahrer Remulos bei der Mondlichtung Unterstützung bei der Erforschung des riesigen Ödlands weit im Westen von Tanaris... und noch weiter im Westen von Un'Goro. Ihr solltet ihn in Nachthafen finden, dem Hauptort der Mondlichtung.]], [[Eure Arbeit wird zwar zweifellos sehr vorteilhaft für uns sein, dennoch rate ich Euch, Vorsicht walten zu lassen. Es gibt Spannungen zwischen Darnassus und der Mondlichtung... Spannungen, die im Laufe der Zeit wachsen könnten.]], },
    { [[Sprecht mit Rabine Saturna im Dorf Nachthafen auf der Mondlichtung. Die Mondlichtung liegt zwischen dem Teufelswald und Winterquell, und Ihr erreicht es über den Pfad, der aus der Holzschlundfeste herausführt.]], },
  },
  [6781] = {
    [[Mehr Rüstungsfetzen]],
  },
  [6801] = {
    [[Lokholar der Eislord]],
  },
  [6804] = {
    [[Vergiftetes Wasser]],
    { [[Eine Truppe von Elementarspähern wurde in die Östlichen Pestländer von Lordaeron entsandt und die Krankheit dieses Orts verdrehte und infizierte sie. Die Wasserlords können diese Beleidigung unserer Reinheit nicht dulden!]], [[Nehmt diese Phiole. Darin befindet sich ein Quäntchen eines Aspekts von Neptulon. Wenn das auf die verseuchten Elementare angewendet wird, bekämpft es das Gift in ihren Körpern und heilt sie! Ihr innerer Konflikt wird sie allerdings in den lodernden Wahnsinn treiben.]], [[Besiegt die disharmonischen Wogen und bringt mir ihre Armschienen der Bindung.]], },
    { [[Wendet den Aspekt von Neptulon auf die vergifteten Elementare der Östlichen Pestländer an. Bringt 12 disharmonische Armschienen sowie den Aspekt von Neptulon zu Fürst Hydraxis in Azshara.]], },
  },
  [6805] = {
    [[Stürmer und Rumpler]],
    { [[Wir, die Elementare des Wassers, führen Krieg mit den Wesen der niederen Elemente. Wenn Ihr in diesem Konflikt wirklich unser Verbündeter seid, dann beweist mir Eure Überzeugung!]], [[Weit im Süden, im Wüstenland Silithus, treiben sich die Diener der Luft und Erde in einer Felsenecke herum, die Kristalltal genannt wird. Sie schützen Gegenstände der Macht vor kleineren Wesen und Sterblichen, und obwohl ich nicht weiß, warum sie das tun, will ich ihre Bemühungen zunichtemachen.]], [[Begebt Euch zum Kristalltal in Silithus und besiegt unsere Feinde. Kehrt danach zu mir zurück.]], },
    { [[Tötet 15 Staubstürmer und 15 Wüstenrumpler und kehrt dann zu Fürst Hydraxis in Azshara zurück.]], },
  },
  [6821] = {
    [[Auge des Glutsehers]],
    { [[Ihr habt mir gezeigt, dass Ihr unsere Feinde besiegen und Missionen ausführen könnt, um die Sache der Wasserlords weiter voranzutreiben. Aber bisher seid Ihr nur niederen Feinden gegenübergetreten... wenn Ihr gegen einen mächtigen Feind geschickt werdet, könnt Ihr dann siegen?]], [[Das ist Eure neue Aufgabe.]], [[Hoch droben auf der Schwarzfelsspitze haben die Orcs einen Diener des Feuers gefangen genommen, die Feuerwache Glutseher. Sucht den Ort seiner Gefangenschaft, erschlagt seine Häscher und erschlagt dann den Glutseher. Bringt mir sein Auge. Das wird mir Euren Wert zeigen.]], },
    { [[Bringt das Auge des Glutsehers zu Fürst Hydraxis in Azshara.]], },
  },
  [6822] = {
    [[Der Geschmolzene Kern]],
    { [[<name>, vielleicht ist es jetzt an der Zeit, Euch einem echten Test zu unterziehen. Seid Ihr bereit?]], [[Am Fuß der Schwarzfelstiefen befindet sich ein Durchgang zum Geschmolzenen Kern. Er ist eine Festung unseres Feindes, in der Hand des mächtigsten Dieners des Feuers in dieser Welt.]], [[Begebt Euch zum Geschmolzenen Kern, <name>. Zeigt mir, dass Ihr über die Kraft und den Willen verfügt, das zu besiegen, was Ihr dort finden werdet.]], [[Und wenn Ihr Verbündete in Eurem Volk habt, dann versammelt sie. Nichts Geringeres als eine Armee kann hoffen, den Geschmolzenen Kern zu betreten und zu überleben.]], },
    { [[Tötet 1 Feuerlord, 1 geschmolzenen Riesen, 1 uralten Kernhund sowie 1 Lavawoger und kehrt dann zu Fürst Hydraxis in Azshara zurück.]], },
  },
  [6823] = {
    [[Agent von Hydraxis]],
    { [[Wenn Ihr wirklich ein Bündnis mit uns wünscht, dann schlage ich vor, dass Ihr Krieg gegen unsere Feinde führt, die Diener von Luft und Erde und Feuer. Jede Niederlage, die Ihr ihnen beibringt, wird ihren Stand hier schwächen und Eure Verbindung zu unserer Sache weiter untermauern.]], [[Vor Euch liegen lange und harte Schlachten, doch wenn Ihr diese übersteht, so winken Euch Ruhm und Ehre.]], },
    { [[Verdient Euch eine geehrte Fraktion bei den hydraxianischen Wasserlords und sprecht dann mit Fürst Hydraxis in Azshara.]], },
  },
  [6824] = {
    [[Hände des Feindes]],
    { [[<name>, Euer Dienst ist für uns von großem Wert, und daher zögern wir, Euch auf diese letzte Mission zu schicken, denn wir wollen einen solch nützlichen Agenten nicht verlieren.]], [[Aber der Erfolg würde unsere Kriegsbemühungen ungeheuer voranbringen und... das wird als ausreichend erachtet, Euer Leben zu riskieren.]], [[Wir wollen, dass diese Hauptmänner im Geschmolzenen Kern vernichtet werden: Lucifron, Sulfuron, Gehennas und Shazzrah. Tötet sie und bringt mir ihre Hände!]], [[Diese Aufgabe wird Euer ganzes Können erfordern, <name>, aber wenn Ihr erfolgreich seid, dann wird den Feuerlords ein schrecklicher Schlag versetzt.]], },
    { [[Bringt die Hände von Lucifron, Sulfuron, Gehennas und Shazzrah zu Fürst Hydraxis in Azshara.]], },
  },
