  [6961] = {
    [[Altvater Winter ist hier!]],
    { [[Ich wünsche ein wunderbares Winterhauchfest! Vergesst nicht, Altvater Winter zu begrüßen! Wenn Ihr dieses Jahr brav wart, werdet Ihr am Tag des Winterhauchfestes wunderbare Leckereien erhalten. Denkt daran, ihm zu sagen, was Ihr Euch dieses Jahr wünscht!]], [[Achtet außerdem darauf, Euch mit Leckereien für die einzudecken, die in diesem Jahr brav waren. Wir haben ein großes Angebot zur Auswahl. Frisch von unserem Hof auf Euren Tisch: beste Kokelwälder Vollwertkost!]], },
    { [[Sprecht mit Altvater Winter. Er befindet sich in der Nähe des Verkaufsbereichs von Kokelwälder in Orgrimmar.]], },
  },
  [6962] = {
    [[Leckereien für Altvater Winter]],
    { [[Bringt 5 Lebkuchen und etwas eiskalte Milch zu Altvater Winter in Orgrimmar.]], },
    { [[Bringt 5 Lebkuchen und etwas eiskalte Milch zu Altvater Winter in Orgrimmar.]], },
  },
  [6963] = {
    [[Gestohlene Winterhauchleckereien]],
    { [[Hört zu... ich will Euch ja den Zauber der Feiertage nicht verderben, aber Kokelwälder könnte etwas Hilfe brauchen - von der Art, für die Ihr Abenteurer bekannt seid.]], [[Uns ist eine wichtige Lieferung mit Leckereien abhandengekommen, mit der wir die Vorräte für diese Feiertage aufstocken wollten, und jetzt ist auch noch der Agent von Kokelwälder verschwunden, den wir losgeschickt haben, um sie zu suchen! Also - die letzten Informationen über die Lieferung stammen aus dem Alteracgebirge, das an das Vorgebirge des Hügellands angrenzt und dort wollte unser Mann danach suchen.]], [[Seht nach, ob sowohl Mann als auch Material dort zu finden sind, und zwar gesund und munter.]], },
    { [[Sucht den Ermittler von Kokelwälder, der in den verschneiten Regionen des Vorgebirges des Hügellands vermisst wird.]], },
  },
  [6964] = {
    [[Der Anlass für die Feiertage]],
    { [[Gaben, Leckereien, Geschenke, pah!]], [[Diese Goblins beschmutzen die wahre Bedeutung dieser Feiertage - des Winterhauchfests. Dies ist eine Zeit der Erneuerung. Das Land schlummert unter einer Schneedecke, die Altvater Winter gebracht hat, und das ist NICHT der Wichtigtuer da drüben in dem roten Aufzug!]], [[Falls Ihr erfahren wollt, worum es bei dem Fest wirklich geht, dann fragt Sagorne Gratläufer. Es dürfte ihn bestimmt überraschen, dass sich jemand für Geschichte statt für Geschenke interessiert.]], },
    { [[Sprecht mit Sagorne Gratläufer im Tal der Weisheit von Orgrimmar über das Winterhauchfest.]], },
  },
  [6981] = {
    [[Der leuchtende Splitter]],
    { [[Der Splitter ist von einem glänzenden Grün, und obwohl er massiv ist, scheint etwas wie Rauch knapp unterhalb der Oberfläche herumzuwirbeln. Ihr fühlt den Zwang, in die Tiefen des Kristalls zu schauen, aber gleichzeitig fühlt Ihr auch, dass etwas sehr Beunruhigendes von ihm ausgeht.]], [[Vielleicht solltet Ihr in Ratschet nach jemandem suchen, der Euch mehr über diesen seltsamen Splitter erzählen kann.]], },
    { [[Begebt Euch nach Ratschet, um jemanden zu finden, der Euch mehr über den leuchtenden Splitter sagen kann.]], [[Liefert dann den Splitter ab, wie Ihr angewiesen wurdet.]], },
  },
  [6982] = {
    [[Vorräte der Eisbeißermine]],
    { [[Die Eisbeißermine ist ein Vorratslager für die Horde. Ich vertrage das Essen nicht, das sie ihren Truppen reichen und der Zustand ihrer Waffen und Rüstungen ist grauenhaft! Aber... in Zeiten wie diesen, in denen Vorräte so wichtig sind, können wir wirklich alles verwenden, was sie haben.]], [[Betretet die Eisbeißermine und bringt mir ihre Vorräte. Dies sollte der Allianz helfen sich weiterhin gut im Kampf zu bewähren und die Soldaten der Horde schwächen.]], [[Viel Erfolg, <name>. Die Mine ist im entfernten Süden, tief im Territorium der Horde.]], },
    { [[Bringt 10 Vorräte der Eisbeißermine zum Rüstmeister der Allianz in Dun Baldar.]], },
  },
  [6983] = {
    [[So was Gemeines...]],
    { [[Eine Kreatur namens "Der monströse Griesgram" hat unsere Lieferung mit Leckereien gestohlen, <name>. Seid aber vorsichtig! Er ist bösartig und befiehlt über ein paar dieser kleinen grünen Gnome!]], [[Ihr werdet Hilfe brauchen, wenn Ihr ihm gegenübertretet. Ich habe gehört, dass er unsere Leckereien und Metzen, unser geliebtes Rentier, in die Eisfellhöhle im Vorgebirge des Hügellandes gebracht hat. Findet die Leckereien, <name>, und bringt sie zurück nach Orgrimmar, bevor das Fest ruiniert ist und all die Kinder enttäuscht werden!]], },
    { [[Sucht die gestohlenen Leckereien und befreit Metzen das Rentier, nachdem Ihr den monströsen Griesgram besiegt habt. Er verbirgt sich nahe der Eisfellhöhle im Vorgebirge des Hügellandes.]], },
  },
  [6984] = {
    [[Ein Dankeschön von Kokelwälder!]],
    { [[Wir von Kokelwälder sind dankbar für die Wiederbeschaffung der gestohlenen Leckereien, <name>. Dafür möchten wir Euch ein besonderes Geschenk anbieten... präsentiert von niemand Geringerem als Altvater Winter persönlich!]], [[Bitte, sprecht mit Altvater Winter und er wird Euch Euer Geschenk zum Winterhauchfest von uns hier bei Kokelwälder überreichen. Frisch von unserem Hof auf Euren Tisch - beste Kokelwälder Vollwertkost... dank Euch natürlich!]], },
    { [[Sprecht mit Altvater Winter in Orgrimmar.]], },
  },
  [6985] = {
    [[Vorräte der Eisenschachtmine]],
    { [[Die Zwerge der Sturmlanzen benutzen die Eisenschachtmine als ein Vorratslager. Ich möchte, dass Ihr diese Mine überfallt und mir ihre Vorräte bringt. Dies sollte die Bemühungen der Sturmlanzen im Alteractal behindern und uns mit der nötigen Ausrüstung versorgen!]], [[Seid vorsichtig, <name>. Die Eisenschachtmine liegt tief im Territorium der Allianz, und die Kontrolle über die Mine ändert sich so oft wie neuer Schnee in Alterac fällt.]], },
    { [[Bringt 10 Vorräte der Eisenschachtmine zum Rüstmeister der Horde in die Burg Frostwolf.]], },
  },
  [7001] = {
    [[Verwaiste Ställe]],
    { [[Der Krieg hier im Tal hat viele Opfer gefordert. Unsere Streitkräfte müssen dringend wieder aufgebaut werden!]], [[Wenn wir es schaffen unsere Ställe wieder zu füllen, können unsere glorreichen Wolfsreiter wieder in die Schlacht reiten.]], [[Nehmt diesen Maulkorb und begebt Euch in die nahegelegenen Wälder. Sucht in der Umgebung nach herumstreifenden Frostwölfen und fangt sie mit dem Maulkorb ein. Wenn Ihr einen Wolf gezähmt habt, bringt ihn zu mir und ich werde ihn in unseren Ställen unterbringen.]], [[Sobald wir wieder über ausreichend Wölfe verfügen, werden die Wolfsreiter ihre Frontpatrouillen fortsetzen.]], },
    { [[Findet einen Frostwolf im Alteractal. Wenn Ihr Euch in der Nähe eines Frostwolfs befindet, benutzt den Frostwolfmaulkorb um das Tier einzufangen. Nach erfolgreicher Zähmung, folgt Euch der Frostwolf bis zum Stallmeister zurück. Sprecht mit dem Stallmeister um für Euren Fang belohnt zu werden.]], },
  },
  [7002] = {
    [[Widderledernes Zaumzeug]],
    { [[Ich bin ebenso begierig wie Ihr darauf, meine tragbare Schreddereinheit auszuprobieren, <race>, aber bevor ich das Set herstellen kann, benötige ich Material - viel Material!]], [[Um genau zu sein, benötige ich einen Berg aus Thorium, Mithril und Eisen. Ich benötige außerdem eine Dampfsäge pro Einheit. Diese müsst Ihr beim Sägewerk beschaffen.]], [[Als ich mich hier umgesehen habe (vor meiner Gefangennahme), ist mir ein Stapel Dampfsägen im Holzlager der Sturmlanzen aufgefallen.]], },
    { [[Ich bin ebenso begierig wie Ihr darauf, meine tragbare Schreddereinheit auszuprobieren, <race>, aber bevor ich das Set herstellen kann, benötige ich Material - viel Material!]], [[Um genau zu sein, benötige ich einen Berg aus Thorium, Mithril und Eisen. Ich benötige außerdem eine Dampfsäge pro Einheit. Diese müsst Ihr beim Sägewerk beschaffen.]], [[Als ich mich hier umgesehen habe (vor meiner Gefangennahme), ist mir ein Stapel Dampfsägen im Holzlager der Sturmlanzen aufgefallen.]], },
  },
  [7003] = {
    [[Geschrumpfte Riesen]],
    { [[Ich muss etwas gegen die Riesen unternehmen, wenn ich hier draußen bleibe! Sie sind sicherlich nicht gut fürs Geschäft, oder nicht?]], [[Ich habe mich an einer Variante von Gnomischen Schrumpfstrahlen versucht, benutze jetzt aber überlegene GOBLIN Technologien für die Herstellung! Im Zuge von Bemühungen, Unannehmlichkeiten wie meinen Tod beispielsweise zu vermeiden, möchte ich Euch bitten es einmal an diesen Riesen in Feralas zu testen. Ihr solltet nach dem Prozess Schrumpfüberreste auf ihnen finden, bringt mir bitte etwas davon!]], },
    { [[Benutzt Zorbins Ultraschrumpfer um jegliche Art von Riesen in Feralas in eine handlichere Form zu schrumpfen. Bringt Zorbin von Schiller 15 Schrumpfüberreste von diesen Riesen. Ihr findet ihn bei den Docks, an der Vergessenen Küste in Feralas.]], },
  },
  [7021] = {
    [[Altvater Winter ist hier!]],
    { [[Ich wünsche ein wunderbares Winterhauchfest! Ihr müsst unbedingt Altvater Winter begrüßen, der sich derzeit in Orgrimmar befindet! Wenn Ihr dieses Jahr brav wart, werdet Ihr am Tag des Winterhauchfestes wunderbare Leckereien erhalten. Denkt daran, ihm zu sagen, was Ihr Euch dieses Jahr wünscht!]], [[Achtet außerdem darauf, Euch mit Leckereien für die einzudecken, die in diesem Jahr brav waren. Wir haben ein großes Angebot zur Auswahl. Frisch von unserem Hof auf Euren Tisch: beste Kokelwälder Vollwertkost!]], },
    { [[Sprecht mit Altvater Winter. Er befindet sich in der Nähe des Verkaufsbereichs von Kokelwälder in Orgrimmar.]], },
  },
