  [7025] = {
    [[Leckereien für Altvater Winter]],
    { [[He, könnt Ihr Altvater Winter einen Gefallen tun, ähm, <Kleiner /Kleines>?]], [[Ich will ja nicht überflüssigerweise über mich selbst in der dritten Person sprechen, aber Altvater Winter arbeitet schon den ganzen Tag hier und könnte selbst eine kleine Leckerei brauchen. Ein paar Lebkuchen und etwas eiskalte Milch, um sie runterzuspülen, wären genau das Richtige. Glaubt Ihr, Ihr könntet dem Altvater eine Freunde machen und das für mich besorgen?]], },
    { [[Bringt 5 Lebkuchen und etwas eiskalte Milch zu Altvater Winter in Eisenschmiede.]], },
  },
  [7026] = {
    [[Widderzaumzeug]],
    { [[Ich bin ebenso begierig wie Ihr darauf, meine tragbare Schreddereinheit auszuprobieren, <race>, aber bevor ich das Set herstellen kann, benötige ich Material - viel Material!]], [[Um genau zu sein, benötige ich einen Berg aus Thorium, Mithril und Eisen. Ich benötige außerdem eine Dampfsäge pro Einheit. Diese müsst Ihr beim Sägewerk beschaffen.]], [[Als ich mich hier umgesehen habe (vor meiner Gefangennahme), ist mir ein Stapel Dampfsägen im Holzlager der Sturmlanzen aufgefallen.]], },
    { [[Ich bin ebenso begierig wie Ihr darauf, meine tragbare Schreddereinheit auszuprobieren, <race>, aber bevor ich das Set herstellen kann, benötige ich Material - viel Material!]], [[Um genau zu sein, benötige ich einen Berg aus Thorium, Mithril und Eisen. Ich benötige außerdem eine Dampfsäge pro Einheit. Diese müsst Ihr beim Sägewerk beschaffen.]], [[Als ich mich hier umgesehen habe (vor meiner Gefangennahme), ist mir ein Stapel Dampfsägen im Holzlager der Sturmlanzen aufgefallen.]], },
  },
  [7027] = {
    [[Verwaiste Ställe]],
    { [[Seht Euch nur unsere Ställe an, <name>! Völlig leer! Die Sturmlanzengarde hat keine Widder mehr, die sie reiten könnte, die Kavallerie ist praktisch nicht mehr existent.]], [[Ihr müsst uns helfen, Soldat. Nehmt dieses Zähmungshalsband und begebt Euch in die Wildnis dieses Tals. Sucht in der Umgebung nach herumstreifenden Alteracwiddern und fangt sie mit dem Zähmungshalsband ein. Wenn Ihr einen Widder eingefangen habt, bringt ihn zu mir und ich werde ihn in unseren Ställen unterbringen. Sobald wir wieder über ausreichend Widder verfügen, werde ich den Kommandanten der Widderreiter informieren.]], [[Mit Eurer Hilfe wird die Kavallerie neu entstehen!]], },
    { [[Findet einen Alteracwidder im Alteractal. Wenn Ihr Euch in der Nähe eines Alteracwidders befindet, benutzt das Zähmungshalsband der Sturmlanzen um das Tier einzufangen. Nach erfolgreicher Zähmung, folgt Euch der Alteracwidder bis zum Stallmeister zurück. Sprecht mit dem Stallmeister um für Euren Fang belohnt zu werden.]], },
  },
  [7028] = {
    [[Dunkles Böses]],
    { [[Die Kristallhöhlen von Terramok liegen im Westen, tief in Maraudon... Das ist ein uralter Ort mit einer langen Geschichte. Und ich will einiges davon in die Hände bekommen, <class>. Relikte der Alten Götter liegen hinter den riesigen Türen verborgen.]], [[Wenn Ihr bereit seid, dem dunklen Bösen zu trotzen, das sich in Maraudon findet, wird es Euer Schade bestimmt nicht sein. Sammelt die theradrischen Kristallschnitzereien ein, die Ihr aufstöbern könnt, und bringt sie mir.]], [[Sobald ich die Schnitzereien habe, kann ich sie... studieren. Diese Mächte haben schon zu lange geruht...]], },
    { [[Sammelt 15 theradrische Kristallschnitzereien für Trista in Desolace.]], },
  },
  [7029] = {
    [[Schlangenzunges Verderbnis]],
    { [[Ein Satyr, der unter dem Namen Schlangenzunge bekannt ist, hat die Höhlen von Maraudon verderbt und so das Wachstum vieler Pflanzen im Inneren behindert.]], [[Nehmt diese Phiole, <name>. Füllt sie am orangefarbenen Kristallteich, den Ihr in den Höhlen findet. Die Beschichtung in der Phiole wird den Toxinen entgegenwirken und sie in einen Heilstoff verwandeln.]], [[Sucht die Schlangenstrunkranken in den orangefarbenen Höhlen und wendet die geläuterte Flüssigkeit auf sie an. Die Pflanze wird damit von der Verderbnis gereinigt, doch Ihr müsst wissen, dass Schlangenzunge sie immer noch kontrolliert. Er kann sie gegen Euch einsetzen...]], },
    { [[Füllt die beschichtete himmelblaue Phiole am orangefarbenen Kristallteich in Maraudon.]], [[Benutzt die gefüllte himmelblaue Phiole mit den Schlangenstrunkranken, damit der verderbte Noxxiousspross herausgezwungen wird.]], [[Heilt 8 Pflanzen, indem Ihr diesen Noxxiousspross tötet und kehrt dann zu Vark Schlachtnarbe in Schattenflucht zurück.]], },
  },
  [7041] = {
    [[Schlangenzunges Verderbnis]],
    { [[Ein Satyr, der unter dem Namen Schlangenzunge bekannt ist, hat die Höhlen von Maraudon verderbt und so das Wachstum vieler Pflanzen im Inneren behindert.]], [[Nehmt diese Phiole, <name>. Füllt sie am verderbten orangefarbenen Kristallteich, den Ihr in den Höhlen findet. Die Beschichtung in der Phiole wird den Toxinen entgegenwirken und sie in einen Heilstoff verwandeln.]], [[Sucht die Schlangenstrunkranken in den orangefarbenen Höhlen und wendet die geläuterte Flüssigkeit auf sie an. Die Pflanze wird damit von der Verderbnis gereinigt, doch Ihr müsst wissen, dass Schlangenzunge sie immer noch kontrolliert. Er kann sie gegen Euch einsetzen...]], },
    { [[Füllt die beschichtete himmelblaue Phiole am orangefarbenen Kristallteich in Maraudon.]], [[Benutzt die gefüllte himmelblaue Phiole mit den Schlangenstrunkranken, damit der verderbte Noxxiousspross herausgezwungen wird.]], [[Heilt 8 Pflanzen, indem Ihr diesen Noxxiousspross tötet und kehrt dann zu Talendria an der Nijelspitze zurück.]], },
  },
  [7042] = {
    [[Gestohlene Winterhauchleckereien]],
    { [[Hört zu... ich will Euch ja den Zauber der Feiertage nicht verderben, aber Kokelwälder könnte etwas Hilfe brauchen - von der Art, für die Ihr Abenteurer bekannt seid.]], [[Uns ist eine wichtige Lieferung mit Leckereien abhandengekommen, mit der wir die Vorräte für diese Feiertage aufstocken wollten, und jetzt ist auch noch der Agent von Kokelwälder verschwunden, den wir losgeschickt haben, um sie zu suchen! Also - die letzten Informationen über die Lieferung stammen aus dem Alteracgebirge, das an das Vorgebirge des Hügellands angrenzt und dort wollte unser Mann danach suchen.]], [[Seht nach, ob sowohl Mann als auch Material dort zu finden sind, und zwar gesund und munter.]], },
    { [[Sucht den Ermittler von Kokelwälder, der in den verschneiten Regionen des Vorgebirges des Hügellands vermisst wird.]], },
  },
  [7043] = {
    [[So was Gemeines...]],
    { [[Eine Kreatur namens "Der monströse Griesgram" hat unsere Lieferung mit Leckereien gestohlen, <name>. Seid aber vorsichtig! Er ist bösartig und befiehlt über ein paar dieser kleinen grünen Gnome!]], [[Ihr werdet Hilfe brauchen, wenn Ihr ihm gegenübertretet. Ich habe gehört, dass er unsere Leckereien und Metzen, unser geliebtes Rentier, in die Eisfellhöhle im Vorgebirge des Hügellandes gebracht hat. Findet die Leckereien, <name>, und bringt sie zurück, bevor das Fest ruiniert ist und all die Kinder von Sturmwind enttäuscht werden!]], },
    { [[Sucht die gestohlenen Leckereien und befreit Metzen das Rentier, nachdem Ihr den monströsen Griesgram besiegt habt. Er verbirgt sich nahe der Eisfellhöhle im Vorgebirge des Hügellandes.]], },
  },
  [7044] = {
    [[Legenden von Maraudon]],
    { [[Ein dunkler Satyr namens Lord Schlangenzunge hat sein Böses in diesen verschlungenen Höhlen verbreitet und die Gedanken aller im Inneren vergiftet. Er residiert noch jenseits der lila Kristalle.]], [[Schlangenzunge hat des Weiteren ein lebendes Symbol seiner Verderbnis namens Noxxion geschaffen, das sich hinter den orangefarbenen Kristallen befindet. Zusammen haben sie die beiden Teile des Szepters meines Bruders gestohlen. Celebras, mein Bruder... wandert blind im Inneren umher, verflucht durch die Verderbnis.]], [[Ihr müsst ihm helfen! Findet die Stücke und sprecht mit meinem Bruder...irgendwie.]], },
    { [[Beschafft die beiden Teile des Szepters von Celebras: den Celebriangriff und den Celebriandiamanten.]], [[Findet einen Weg, um mit Celebras zu sprechen.]], },
  },
  [7045] = {
    [[Ein Dankeschön von Kokelwälder!]],
    { [[Wir von Kokelwälder sind dankbar für die Wiederbeschaffung der gestohlenen Leckereien, <name>. Dafür möchten wir Euch ein besonderes Geschenk anbieten... präsentiert von niemand Geringerem als Altvater Winter persönlich!]], [[Bitte, sprecht mit Altvater Winter und er wird Euch Euer Geschenk zum Winterhauchfest von uns hier bei Kokelwälder überreichen. Frisch von unserem Hof auf Euren Tisch: beste Kokelwälder Vollwertkost... dank Euch natürlich!]], },
    { [[Sprecht mit Altvater Winter in Eisenschmiede.]], },
  },
  [7046] = {
    [[Das Szepter von Celebras]],
    { [[Helft Celebras dem Erlösten, während er das Szepter von Celebras herstellt.]], [[Sprecht mit ihm, nachdem das Ritual vollendet ist.]], },
    { [[Helft Celebras dem Erlösten, während er das Szepter von Celebras herstellt.]], [[Sprecht mit ihm, nachdem das Ritual vollendet ist.]], },
  },
  [7061] = {
    [[Das Winterhauchfest]],
    { [[Hier - wenn Ihr daran interessiert seid, mehr über das Winterhauchfest zu erfahren, dann lest dieses Buch. Ich glaube zwar, dass unser Verständnis der Legende zutreffend ist, aber als jemand, der Legenden studiert, weiß ich durchaus eine Sammlung aller Informationen in einer einzelnen Quelle zu schätzen.]], [[Wenn Ihr fertig seid, bringt das Buch zu Baine Bluthuf in Donnerfels. Ich weiß mit Bestimmtheit, dass es ihm nicht gleichgültig ist, wenn auch andere sich für die Lehre unseres Volkes interessieren.]], },
    { [[Ihr könnt gern das Buch 'Das Winterhauchfest' lesen, um mehr über diesen Feiertag zu erfahren. Wenn Ihr das Buch gelesen habt, bringt es zu Baine Bluthuf in Donnerfels.]], },
  },
  [7062] = {
    [[Der Anlass für die Feiertage]],
    { [[Gaben, Leckereien, Geschenke, pah!]], [[Diese Goblins beschmutzen die wahre Bedeutung dieser Feiertage - des Winterhauchfests. Dies ist eine Zeit der Erneuerung. Das Land schlummert unter einer Schneedecke, die Altvater Winter gebracht hat, und das ist NICHT der Wichtigtuer da drüben in dem roten Aufzug!]], [[Falls Ihr erfahren wollt, worum es bei dem Fest wirklich geht, dann befragt den Historiker Karnik in der Forscherliga. Es dürfte ihn bestimmt überraschen, dass sich jemand für Geschichte statt für Geschenke interessiert.]], },
    { [[Sprecht mit dem Historiker Karnik in der Forscherliga von Eisenschmiede über das Winterhauchfest.]], },
  },
  [7063] = {
    [[Das Winterhauchfest]],
    { [[Hier - wenn Ihr daran interessiert seid, mehr über das Winterhauchfest zu erfahren, dann lest dieses Buch. Ich glaube zwar, dass unser Verständnis der Legende zutreffend ist, aber als Gelehrter, der Legenden studiert, weiß ich durchaus eine Sammlung aller Informationen in einer einzelnen Quelle zu schätzen.]], [[Wenn Ihr fertig seid, dann bringt das Buch zu Muradin Bronzebart. Ich weiß mit Bestimmtheit, dass es ihn sehr interessieren wird, wenn auch andere Interesse für die alten Legenden an den Tag legen.]], },
    { [[Ihr könnt gern das Buch 'Das Winterhauchfest' lesen, um mehr über diesen Feiertag zu erfahren. Wenn Ihr das Buch gelesen habt, dann bringt es zu Muradin Bronzebart nach Eisenschmiede.]], },
  },
  [7064] = {
    [[Verderbnis von Erde und Samenkorn]],
    { [[Tief in Maraudon lebt eine böse Kreatur des Chaos - Prinzessin Theradras ist eine Naturgewalt der Erde, die mit den Alten Göttern verwandt ist. Vor langer Zeit gingen sie und Zaetar, der erstgeborene Sohn von Cenarius, eine Beziehung ein. Aus ihrer gemeinsamen Zeit entsprangen Wesen, die unter den Menschen von Kalimdor als Zentauren bekannt wurden. Diese undankbaren Kreaturen töteten Zaetar und bewachen nun seine Überreste. Meine Aufgabe besteht darin, jemanden zu finden, der mächtig genug ist, um Theradras zu erschlagen, damit wir Zaetars Überreste holen können, bevor wir in das Steinkrallengebirge zurückkehren.]], },
    { [[Tötet Prinzessin Theradras und kehrt zu Selendra in der Nähe von Schattenflucht in Desolace zurück.]], },
  },
  [7065] = {
    [[Verderbnis von Erde und Samenkorn]],
    { [[In den Tiefen von Maraudon lebt eine Kreatur des Chaos und des Bösen, <name>. Prinzessin Theradras ist eine Naturgewalt der Erde, die mit den Alten Göttern verwandt ist. Vor langer Zeit stattete Zaetar, der erstgeborene Sohn von Cenarius, ihr einen Besuch ab. Die Nachkommen ihrer...Beziehung sind jene Kreaturen, die unter dem Namen Zentauren bekannt sind. Diese undankbaren Kreaturen töteten Zaetar und Theradras bewacht nun seine Überreste. Meine Aufgabe besteht darin, jemanden zu finden, der mächtig genug ist, um Theradras zu erschlagen, damit wir Zaetars Überreste holen können, bevor wir in das Steinkrallengebirge zurückkehren.]], },
    { [[Erschlagt Prinzessin Theradras und kehrt zum Bewahrer Marandis an der Nijelspitze in Desolace zurück.]], },
  },
  [7066] = {
    [[Samenkorn des Lebens]],
    { [[Diese missratenen Zentauren - meine Söhne und Töchter - brauchen ihren Vater.]], [[Seht Euch um - dieser Ort hallt wider von Hoffnung und all jenem, was mein Vater Cenarius predigt. All das entsprang aus mir! Und ich kann ihnen das nicht nehmen... nicht einmal ob ihrer Verbrechen gegen mich.]], [[Eines jedoch möchte ich von Euch erbitten, damit mein Bruder Ruhe findet. Nehmt dies... es ist das erste Samenkorn, das aus dem Leben entstand, das meine Überreste nährten. Sagt ihm, dass mein Geist hier bleibt und dass ich weiterlebe. Er wird es sicherlich verstehen.]], },
    { [[Sucht Remulos bei der Mondlichtung auf und gebt ihm das Samenkorn des Lebens.]], },
  },
  [7067] = {
    [[Die Anweisungen des Pariahs]],
    { [[Obwohl ich als Ketzer verbannt wurde, bleibe ich meinem Glauben treu - das allein treibt mich voran. Aber damit mein Schicksal sich erfüllt, benötige ich die Hilfe eines Außenstehenden... jemanden, der keinem Zentaurenstamm angehört.]], [[Für diese Aufgabe muss jemand die heiligen Hallen von Maraudon betreten - das ist ein Tempel, den mein Volk schützt, und nur die Gläubigsten dürfen ihn betreten.]], [[Wenn Ihr einwilligt, mir zu helfen, dann müsst Ihr nur nicken. Ich werde Euch ein Pergament mit meinen Anweisungen geben. Lest es durch und denkt über meine Worte nach.]], },
    { [[Lest die Anweisungen des Pariahs. Beschafft Euch danach das Amulett der Vereinigung von Maraudon und bringt es dem Zentaurenpariah im südlichen Desolace.]], },
  },
  [7068] = {
    [[Schattensplitter]],
    { [[Man findet starke Magie am heiligen Ort der Zentauren... dem heiligen Ort von Maraudon. Die Desolace-Stämme der Zentauren schützen diesen Ort mit ihrem Leben, aber sie können uns nicht davon abhalten, das zu nehmen, was wir brauchen.]], [[Begebt Euch zu diesem Maraudon-Ort und sucht nach den Felskreaturen, die aus Schattenstein bestehen. Bringt mir die lila Kristalle und Uthel'nay wird Euch Eure Mühe vergelten.]], [[Der Ort ist gefährlich, daher solltet Ihr einige Freunde mitnehmen.]], },
    { [[Sammelt 10 Schattensplitter aus Maraudon und bringt sie zu Uthel'nay nach Orgrimmar.]], },
  },
  [7070] = {
    [[Schattensplitter]],
    { [[Ich habe kürzlich begonnen, verschiedene Kristalle und ihre arkanen Eigenschaften zu testen. Lady Prachtmeer hat mir die Erlaubnis erteilt, Reisende, die nach Desolace ziehen, zu bitten, einen Ort zu suchen, der Maraudon genannt wird. In dem zerfallenen Tempel dort gibt es große Elementare, die aus lila Steinen bestehen. Ich habe sie Schattensplitter getauft.]], [[Falls Ihr beschließt, diese Richtung einzuschlagen, und dort einige dieser Kristalle findet, wäre ich sehr dankbar. Ich würde Euch sogar ein Schmuckstück daraus herstellen, wenn Ihr dies wünscht.]], },
    { [[Sammelt 10 Schattensplitter in Maraudon und bringt sie zu Erzmagier Tervosh in den Düstermarschen.]], },
  },
  [7081] = {
    [[Die Friedhöfe im Alteractal]],
    { [[Die Friedhöfe im Alteractal sind äußerst wertvolle Gebiete. Beide Seiten, die Horde und die Allianz, kämpfen verbissen um diese geheiligten Stätten. <name>, seid Ihr bereit die Kämpfe zu unterstützen?]], [[Ich möchte, dass Ihr einen gegnerischen Friedhof besetzt. Sucht einen Friedhof mit einem Banner der Horde in seiner Mitte und reißt es heraus! Wenn Ihr das schafft, ist meine Aufgabe an Euch erfüllt...]], [[Solltet Ihr es schaffen, den Friedhof für einige Minuten zu halten, werden unsere Verstärkungstruppen eintreffen, um Euch bei der Verteidigung zu unterstützen.]], },
    { [[Erobert einen Friedhof und kehrt zu Unteroffizier Durgen Sturmlanze im Alteracgebirge zurück.]], },
  },
  [7082] = {
    [[Die Friedhöfe von Alterac]],
    { [[Die Frontlinien in diesem Tal sind in ständiger Bewegung. Die Gebietsgrenzen bewegen sich hin und her, während wir unseren Feinden in tödlichen Schlachten gegenüber treten. Doch um die Friedhöfe, <name>, wird am grimmigsten gekämpft.]], [[Ich möchte, dass Ihr einen von der Allianz besetzten Friedhof angreift! Sucht einen Friedhof mit einem Banner der Allianz in seiner Mitte und reißt es aus dem Boden.]], [[Wenn Ihr das schafft, ist meine Aufgabe an Euch erfüllt...]], [[Solltet Ihr es außerdem schaffen, den Friedhof für einige Minuten zu halten, wird dieser uns gehören und unsere Truppen werden kommen, um Euch bei der Verteidigung dieser wichtigen Position zu unterstützen.]], },
    { [[Erobert einen Friedhof und kehrt zu Korporal Teeka Murrblut im Alteracgebirge zurück.]], },
  },
  [7101] = {
    [[Türme und Bunker]],
    { [[Die Türme und Bunker in diesem Tal sind wichtige Stellungen und daher müssen wir sie möglichst alle erobern und halten! Genug geredet... Erobert einen vom Feind kontrollierten Turm oder Bunker.]], [[Betretet dafür einen mit einem Banner der Allianz. Im Inneren befindet sich ein weiteres Banner. Zieht das Banner heraus und ein weißes Banner der Horde wird an seiner Stelle erscheinen.]], [[Damit habt Ihr meinen Auftrag erfüllt. Wenn Ihr das eroberte Gebäude lange genug haltet, ohne dass die Allianz es zurückgewinnt, gehört es uns!]], },
    { [[Erobert einen gegnerischen Turm und kehrt anschließend zu Korporal Teeka Murrblut im Alteracgebirge zurück.]], },
  },
  [7102] = {
    [[Türme und Bunker]],
    { [[Die Türme und Bunker in diesem Tal sind Schlüsselverteidigungspositionen und daher sollten wir so viele wie möglich davon unter Kontrolle halten! Geht und erobert einen vom Feind kontrollierten Turm oder Bunker.]], [[Betretet dafür einen mit einem Banner der Horde. Im Inneren befindet sich ein weiteres Banner. Zieht das Banner heraus und ein weißes Banner der Allianz wird an seiner Stelle erscheinen.]], [[Damit habt Ihr meinen Auftrag erfüllt. Wenn Ihr das eroberte Gebäude lange genug haltet, ohne dass die Horde es zurückgewinnt, gehört es uns!]], },
    { [[Zerstört das Banner im Inneren eines gegnerischen Bunkers oder Turms. Kehrt anschließend zu Unteroffizier Durgen Sturmlanze im Alteracgebirge zurück.]], },
  },
  [7121] = {
    [[Der Rüstmeister]],
    { [[<name>, unser Rüstmeister hat mich angewiesen, die neuen Rekruten direkt zu ihm zu schicken. Er benötigt dringend Vorräte aus Kisten, die in den Minen von Alterac eingelagert sind und aller Wahrscheinlichkeit nach wird er Euch entsenden, um sie zu beschaffen.]], [[Folgt einfach der Straße. Ihr findet den Rüstmeister im Westen.]], },
    { [[Sprecht mit dem Rüstmeister der Sturmlanzen.]], },
  },
  [7122] = {
    [[Eine Mine erobern]],
    { [[In den Minen des Alteractals ist noch weit mehr zu finden als bloßes Erz. Sowohl die Frostwölfe, als auch die Sturmlanzen, haben große Mengen ihrer Ausrüstung in der Eisenschacht- und der Eisbeißermine untergebracht... was eigentlich keine schlechte Idee ist, solange wir die Kontrolle über sie behalten!]], [[Besetzt eine Mine und kehrt zu mir zurück! Um eine Mine zu erobern, betretet eine vom Feind kontrollierte Mine und tötet den dortigen Anführer. Nachdem Ihr das getan habt, gehört die Mine uns und wir werden Truppen zu ihrem Schutz entsenden!]], },
    { [[Erobert eine Mine, die die Sturmlanzen noch nicht kontrollieren, und kehrt dann zu Unteroffizier Durgen Sturmlanze im Alteracgebirge zurück.]], },
  },
  [7123] = {
    [[Sprecht mit unserem Rüstmeister]],
    { [[<name>, der Rüstmeister der Frostwölfe sucht nach Lehrlingen für Aufgaben, die in die Minen des Tals von Alterac führen. Sprecht mit ihm; er wird Euch mehr berichten.]], [[Folgt der Straße westlich in den Bunker, dann nehmt den westlichen Ausgang und Ihr findet unseren Rüstmeister.]], },
    { [[Sprecht mit dem Rüstmeister der Frostwölfe.]], },
  },
  [7124] = {
    [[Eine Mine erobern]],
    { [[So wie Kämpfe im Gebiet des Alteractals wüten, so wüten sie auch durch die Eisbeißer- und Eisenschachtminen. Beide bedeuten enorme Ressourcen an Mineralien und beide werden als Lager genutzt; daher ist es kein Wunder, dass sie so heiß begehrt sind!]], [[Helft uns in der Schlacht, <name>! Erobert für die Horde eine Mine! Begebt Euch zu einer Mine, die noch nicht von uns kontrolliert wird und tötet den dortigen Anführer! Sobald Ihr das geschafft habt, schicken wir Truppen aus, um die Mine zu bewachen!]], },
    { [[Erobert eine Mine und kehrt zu Korporal Teeka Murrblut im Alteracgebirge zurück.]], },
  },
  [7141] = {
    [[Die Schlacht um Alterac]],
    { [[Die Forscherliga glaubt, dass es im Alteractal einige Artefakte von Interesse gibt. Allerdings werden uns die Orcs des Frostwolfklans wohl kaum danach forschen lassen. Wir können es der Horde jedoch nicht erlauben, unseren Forschungsbemühungen im Weg zu stehen! Vernichtet den General der Frostwölfe, Drek'Thar. Mit seinem Tod werden die Orcs ihren Vorteil verlieren.]], [[Drek'Thar hält sich in der Burg Frostwolf auf, im Süden des Alteractals. Bezwingt ihn und kehrt dann zu mir zurück.]], },
    { [[Betretet das Alteractal, bezwingt den Hordegeneral Drek'thar und kehrt dann zu Ausgrabungsleiter Steinhauer im Alteracgebirge zurück.]], },
  },
  [7142] = {
    [[Die Schlacht ums Alteractal]],
    { [[Die Sturmlanzenzwerge und ihre Verbündeten sind in das Alteractal eingefallen und wir müssen sie vertreiben! Um die Schlacht ums Alteractal gewinnen zu können, müssen wir ihren General töten, Vanndar Sturmlanze!]], [[Ihr werdet Vanndar in Dun Baldar, im Norden des Alteractals finden.]], [[Tötet ihn, <name>, und kehrt dann zu mir zurück.]], },
    { [[Betretet das Alteractal und bezwingt den Zwergengeneral, Vanndar Sturmlanze. Kehrt anschließend zu Voggah Todesgriff im Alteracgebirge zurück.]], },
  },
  [7161] = {
    [[Flaggenjagd]],
    { [[Seid gegrüßt, <class>. Es scheint eine Ewigkeit her, seit wir zuletzt dem Ruf des Krieges gefolgt sind. Die alten Gefühle erwachen nur langsam. Doch sind sie erst einmal erwacht, beginnt das Blut erneut zu kochen. Die Kampfeslust kehrt zurück...]], [[Alle Neuankömmlinge im Tal müssen sich zuerst dem Ritus des Kampfes unterziehen.]], [[Tief in der nahegelegenen Höhle der Wildpfoten findet Ihr ein Banner der Frostwölfe. Zieht los und holt es für uns zurück. Bringt es zu mir und ich gewähre Euch das Abzeichen eines Initianden der Frostwölfe.]], },
    { [[Begebt Euch in die Höhle der Wildpfoten südöstlich vom Hauptstützpunkt und findet das Banner der Frostwölfe. Bringt anschließend Kriegsmeister Laggrond das Banner.]], },
  },
  [7162] = {
    [[Flaggenjagd]],
    { [[Ihr seid genau zur rechten Zeit eingetroffen, <class>. Diese Wilden der Frostwölfe wollen einfach nicht aufgeben! Wir müssen ihnen die Macht der Sturmlanzen vor Augen führen! Am Ende müssen sie sich unserer Überlegenheit beugen.]], [[Doch bevor Ihr Euch auf das Schlachtfeld begebt, solltet Ihr den Reihen der Sturmlanzen beitreten.]], [[Südwestlich von Dun Baldar befinden sich die Eisschwingenhöhlen. Tief in ihrem Inneren werdet Ihr ein Banner der Sturmlanzen finden. Begebt Euch dorthin und holt das Banner zurück. Bringt es anschließend zu mir und ich werde Euch mit dem Abzeichen eines Initianden der Sturmlanzen auszeichnen.]], },
    { [[Begebt Euch in die Eisschwingenhöhle südwestlich von Dun Baldar und findet das Banner der Sturmlanzen. Bringt anschließend Leutnant Haggerdin im Alteracgebirge das Banner.]], },
  },
  [7163] = {
    [[Aufstieg und Anerkennung]],
    { [[<Warmaster Laggrond salutes you.>]], [[It is your time, <name>. You have done much for the Frostwolf Clan and in doing so, earned a place as not only a soldier of Frostwolf but also as a friend.]], [[Well done! Present your initiate's insignia.]], },
  },
  [7164] = {
    [[Ehre innerhalb des Klans]],
    { [[It is good to see you again, <class>. I had not expected you to return. Alas, you have proven yourself a brave and honorable soldier of the Frostwolf.]], [[Your time has come again, <name>. You have earned a new ranking and as such, a new insignia denoting this rise in stature.]], [[Present your insignia.]], },
  },
  [7165] = {
    [[Verdiente Ehrerbietung]],
    { [[I must know, <name>. When you look directly into the eyes of the enemy, do you see fear? Do they now cower in your presence? They must realize that they are defeated!]], [[You have earned reverence among the Guard.]], [[Present your insignia!]], },
  },
  [7166] = {
    [[Legendäre Helden]],
    { [[Your radiate command and power, shaman. Exalted in the eyes of Frostwolf - the enemy cowers at the mention of your name.]], [[Rise, Hero of Frostwolf. Rise and be honored!]], [[Present your insignia.]], },
  },
  [7167] = {
    [[Das Auge der Führung]],
    { [[Drek'Thar sings your praises. Kalimdor is abuzz with tales of your heroics. The Warchief glows - your stalwart defense of our clan has pleased him greatly.]], [[You have earned the Eye of Command.]], [[Present your insignia, Commander <name>.]], [[<Warmaster Laggrond salutes>]], },
  },
  [7168] = {
    [[Aufstieg und Anerkennung]],
    { [[Most do not live long enough to rise above their own mediocrity. You have proven yourself to be an exemplary soldier, <class>. The time has come.]], [[Present your insignia.]], },
  },
  [7169] = {
    [[Ehre innerhalb der Garde]],
    { [[The base buzzes with news of your exploits in the Field of Strife! You have struck mighty blows against our enemy - crushing their morale! For this, you have earned a rank of honor among the Stormpike.]], [[Present your insignia.]], },
  },
  [7170] = {
    [[Verdiente Ehrerbietung]],
    { [[I must know, <name>. When you look directly into the eyes of the enemy, do you see fear? Do they now cower in your presence? They must realize that they are defeated!]], [[You have earned reverence among the Guard.]], [[Present your insignia!]], },
  },
  [7171] = {
    [[Legendäre Helden]],
    { [[Your radiate command and power, shaman. Exalted in the eyes of Frostwolf - the enemy cowers at the mention of your name.]], [[Rise, Hero of Frostwolf. Rise and be honored!]], [[Present your insignia.]], },
  },
  [7172] = {
    [[Das Auge der Führung]],
    { [[It is good to see you again, <class>. I had not expected you to return. Alas, you have proven yourself a brave and honorable soldier of the Frostwolf.]], [[Your time has come again, <name>. You have earned a new ranking and as such, a new insignia denoting this rise in stature.]], [[Present your insignia.]], },
  },
  [7181] = {
    [[Die Legende von Korrak]],
    { [[Die angreifenden Sturmlanzen sind nicht die einzige Bedrohung in dieser Region, Soldat. Der Krieg in diesem Tal tobt an zwei Fronten. Den kannibalistischen Winteraxtrollen dürstet es ebenso nach Macht.]], [[Angeführt werden sie von Korrak dem Blutrünstigen - einem grausamen und verschlagenen Biest.]], [[Ein Streich gegen Korrak könnte sich als vernichtender Schlag für den Winteraxklan erweisen. Tötet diesen Bastard und Ihr werdet entlohnt.]], },
    { [[Der Legende zufolge erscheint der mächtige Anführer der Winteraxtrolle nach gutdünken, um Angst und Schrecken unter den Bewohnen des Alteractals zu verbreiten.]], [[Sollte Korrak auftauchen, tötet ihn und kehrt zu Kriegsmeister Laggrond zurück.]], },
  },
  [7201] = {
    [[Das letzte Element]],
    { [[Es gibt Arbeit für diejenigen, die sich in die Tiefen wagen, <name>.]], [[Den Dunkeleisenzwergen ist es gelungen, extrem mächtige Golems zu erschaffen.]], [[Ersten Berichten unserer Spione zufolge verwenden die Zwerge eine einzigartige Kraftquelle, die ihren Schöpfungen eine unvergleichliche Kraft zum Töten gibt.]], [[Stellt Euch nur einmal vor, was wir mit unseren Monstrositäten tun könnten, wenn wir diese Essenz der Elemente in die Hände bekommen könnten! Stellt die ganze Stadt auf den Kopf, wenn es sein muss, aber kommt nicht wieder, ehe Ihr nicht die Essenz gefunden habt! Die Bezahlung ist es wert, das Risiko einzugehen.]], },
    { [[Begebt Euch in die Schwarzfelstiefen und beschafft 10 Essenzen der Elemente. Euer erster Gedanke ist, die Golems und die Schöpfer der Golems zu suchen. Doch Ihr erinnert Euch, dass Vivian Lagrave auch etwas von Elementaren vor sich hingemurmelt hat.]], },
  },
  [7202] = {
    [[Korrak der Blutrünstige]],
    { [[Die eingeborenen Winteraxtrolle dieser Region sind unbarmherzige Wilde, die nichts lieber täten, als unsere Knochen in einer ihrer widerlichen Mahlzeiten zu sehen.]], [[Wir müssen ihnen unsere Macht vor Augen führen!]], [[Wir konnten einige Schriftstücke aus ihren Höhlen entwenden, die Aufzeichnungen über ihre Führungshierarchie enthalten. Diese Schriftstücke lassen vermuten, dass ihr Anführer, Korrak der Blutrünstige, sich solange versteckt hält, bis er einen Grund hat sich zu zeigen.]], [[Der Tod Korraks, würde den Tod des Winteraxklans bedeuten! Tötet ihn und kehrt zu mir zurück.]], },
    { [[Der Legende zufolge erscheint der mächtige Anführer der Winteraxtrolle nach gutdünken, um Angst und Schrecken unter den Bewohnen des Alteractals zu verbreiten.]], [[Sollte Korrak auftauchen, tötet ihn und kehrt zu Leutnant Haggerdin im Alteracgebirge zurück.]], },
  },
  [7221] = {
    [[Sprecht mit Ausgrabungsleiter Steinhauer]],
    { [[Ah, <name>, Ihr wollt Euch zum Dienst melden, oder? Großartig, die Sturmlanzen können jeden gebrauchen, der sich freiwillig mustern lässt!]], [[Sprecht mit Ausgrabungsleiter Steinhauer in Dun Baldar. Momentan sucht sie nach Freiwilligen für eine entscheidende Mission im Feindesland. Ihr findet sie unterhalb der Garnison von Dun Baldar.]], [[Und haltet ein Auge offen, während Ihr durch Dun Baldar geht. <Ein hart arbeitender/Eine hart arbeitende><class> <class> wie Ihr findet sicher viel zu tun, sofern Ihr nicht davor zurückscheut, Euch die Hände schmutzig zu machen.]], },
    { [[Sprecht mit Ausgrabungsleiter Steinhauer in der Garnison von Dun Baldar.]], },
  },
  [7222] = {
    [[Sprecht mit Voggah Todesgriff]],
    { [[Ah, <ein/eine><class> <class>! Wir sind alle hoch erfreut, Euch hier in Alterac anzutreffen, <name>. Zwergische Eindringlinge und ihre Verbündeten sind in unser Tal einmarschiert und wir sind froh über jedes Mitglied der Horde, das uns in dieser gerechten Schlacht zur Seite steht!]], [[Sprecht mit Voggah Todesgriff. Er sucht nach Freiwilligen für eine wichtige Mission. Es geht um die Allianzfestung hier im Alteractal!]], [[Ihr findet Voggah in Burg Frostwolf.]], },
    { [[Sprecht mit Voggah in Burg Frostwolf.]], },
  },
  [7223] = {
    [[Rüstungsfetzen]],
    { [[Im Alteractal wird ständig gekämpft und der Nachschub, den wir aus Eisenschmiede bekommen, reicht nicht, um unsere erfahrenen Truppen auszustatten.]], [[Wenn Ihr Rüstungsfetzen auf dem Schlachtfeld findet, dann unterstützt unsere Kriegsbemühungen und bringt mir diese Stücke!]], },
    { [[Bringt Murgot Tiefenschmied in Dun Baldar 20 Rüstungsfetzen.]], },
  },
  [7224] = {
    [[Beutezug im Feindesland]],
    { [[Wenn Ihr gegen den Feind kämpft, sammelt die Fetzen seiner Rüstungen ein und bringt sie zu mir. Aus den Fetzen werde ich neue Rüstungen für unsere eigenen Truppen herstellen!]], },
    { [[Bringt 20 Rüstungsfetzen zu Schmied Regzar im Dorf der Frostwölfe.]], },
  },
  [7241] = {
    [[Die Verteidigung der Frostwölfe]],
    { [[In der Ferne kann man den Klang der Kriegstrommeln vernehmen, <class>. Die ehrlose Sturmlanzengarde aus Eisenschmiede greift den Klan Eures Kriegshäuptlings an.]], [[Ihr seid kampfgestählt, das verrät mir Euer Auftreten. Es scheint, dass Ihr wahrlich bereit seid, der Sache des Frostwolfklans zu dienen.]], [[Es liegt nun an Euch, den nächsten Schritt in Eurer Entwicklung zu gehen.]], [[Reist in das Vorgebirge des Hügellands, dort befindet sich der Zugang zum Alteractal. Betretet das Tal und sucht Kriegsmeister Laggrond auf. Beeilung <class>, die Schlacht hat bereits begonnen!]], },
    { [[Reist ins Alteractal im Vorgebirge des Hügellands. Sucht dort Kriegsmeister Laggrond auf und beginnt Eure Karriere als Soldat der Frostwölfe.]], },
  },
  [7261] = {
    [[Die hoheitliche Anordnung]],
    { [[Reist ins Alteractal im Vorgebirge des Hügellands. Wenn Ihr dort angekommen seid, meldet Euch umgehend bei Leutnant Haggerdin.]], [[Für den Ruhm von Bronzebart!]], },
    { [[Reist ins Alteractal im Vorgebirge des Hügellands. Wenn Ihr dort angekommen seid, meldet Euch umgehend bei Leutnant Haggerdin.]], [[Für den Ruhm von Bronzebart!]], },
  },
  [7281] = {
    [[Bruderliebe]],
    { [[Welch tragische Fügung... Mein Bruder steht mir auf dem Schlachtfeld gegenüber: Er ist einer der Kommandanten der Sturmlanzen.]], [[Von allen vier Kommandanten stellt er zweifellos die größte Gefahr dar. Falls man ihn tötet, würde die Bansheekönigin ihn vielleicht wiedererwecken, so wie sie es mit mir getan hat - und ihm die Irrtümer seines bisherigen Handelns vor Augen führen...]], [[Tief im feindlichen Gebiet werdet Ihr meinen Bruder finden. Tötet ihn und bringt ihn zu mir zurück. Ich vertraue darauf, dass Ihr diese Mission erfolgreich zu Ende bringt, Soldat.]], [[Und Soldat... geht nicht alleine.]], },
    { [[Begebt Euch ins Gebiet der Sturmlanzen und tötet Kommandant Karl Philips. Kehrt zu Kommandant Louis Philips zurück, wenn Ihr Eure Aufgabe erfüllt habt.]], },
  },
  [7282] = {
    [[Bruderliebe]],
    { [[Er muss aus seinem Elend befreit werden, Soldat! Mein Bruder weiß nicht mehr, was er tut. Er ist nur noch eine willenlose Marionette der grausamen Bansheekönigin.]], [[Begebt Euch, über das Feld des Kampfes, in die Tiefen des Gebiets der Frostwölfe. Findet meinen Bruder und zerstört, was von ihm übrig ist. Seine Qualen sollen endlich ein Ende haben.]], },
    { [[Begebt euch ins Frostwolfgebiet und tötet Kommandant Louis Philips. Kehrt zu Kommandant Karl Philips zurück, wenn Ihr Eure Aufgabe erfüllt habt.]], },
  },
  [7301] = {
    [[Gefallene Himmelsstürmer]],
    { [[Wir konnten sie gefangennehmen, <race>. Ich rede von ihren Schwadronskommandanten. Es war eine wilde Himmelsschlacht, doch letztendlich haben wir sie geschlagen! Sie fielen wie Steine vom Himmel! Leider hat es auch viele unserer besten Piloten erwischt, als Sie die Burg Frostwolf angriffen. Ihr müsst herausfinden was aus ihnen geworden ist.]], [[Durchkämmt das Gebiet der Westturmstellung und Burg Frostwolf. Dort wurden Sie das letzte Mal gesehen. Und geht gründlich vor!]], },
    { [[Begebt Euch ins Frostwolfterritorium und sucht nach Schwadronskommandant Slidore, Schwadronskommandant Vipore und Schwadronskommandant Ichman. Meldet Euch bei Kommandant Duffy, sobald Ihr Eure Mission erfüllt habt.]], },
  },
  [7302] = {
    [[Gefallene Himmelsstürmer]],
    { [[Unsere Luftunterstützung musste einen schweren Schlag hinnehmen, <name>. Sämtliche Schwadronskommandanten wurden über feindlichem Gebiet abgeschossen. Ihr müsst herausfinden, was aus ihnen geworden ist, sodass wir einen Rettungsversuch unternehmen können!]], [[Guse wurde das letzte Mal gesehen, als sie über den Eisschwingenbunker hinwegflog. Jetzors Reittier wurde zuletzt am Sägewerk der Sturmlanzen gesichtet. Mulverick... dieser waghalsige Narr, stürzte in der Nähe des Nordbunkers von Dun Baldar ab.]], [[Das sind alle Informationen, die ich habe. Schwärmt aus und durchsucht das Gebiet gründlich!]], },
    { [[Findet heraus, was aus Schwadronskommandantin Guse, Schwadronskommandantin Jeztor und Schwadronskommandant Mulverick geworden ist. Wenn Eure Mission erfüllt ist, kehrt zu Kommandant Mulfort zurück.]], },
  },
  [7321] = {
    [[Milde Schildkrötensuppe]],
    { [[Bringt 10 Stücke Schildkrötenfleisch zu Christoph Jeffkott in Tarrens Mühle.]], },
    { [[Bringt 10 Stücke Schildkrötenfleisch zu Christoph Jeffkott in Tarrens Mühle.]], },
  },
  [7341] = {
    [[Ein gerechter Handel]],
  },
  [7342] = {
    [[Pfeile sind was für Memmen]],
  },
  [7361] = {
    [[Die Gunst der Dunkelspeere]],
    { [[<Najak knurrt.>]], [[Bevor die Nachtelfen waren, waren die Trolle. Erst von den Trollen sind Nachtelfen gekommen.]], [[Ihre aufrührerischen Angriffe gegen mein Volk können nicht toleriert werden. Hirschhaupt will uns auslöschen - sämtliche Spuren derer vernichten, die ihn und seines Gleichen ausgetragen haben. Das wird nie geschehen!]], [[Für jeden Nachtelfen, den Ihr tötet und enthauptet, wird Najak Euch die Gunst der Dunkelspeere sichern. Die Kunde Eurer Taten wird sich über ganz Kalimdor und den Östlichen Königreichen verbreiten. Geht nun und dient den Dunkelspeeren!]], },
    { [[Ihr wurdet beauftragt gegnerische Nachtelfenspieler im Alteractal zu töten.]], [[Tötet einen Nachtelfen und bringt einen abgeschlagenen Nachtelfenkopf zu Najak Hexxen in Burg Frostwolf.]], },
  },
  [7362] = {
    [[Verbündeter der Tauren]],
    { [[Die Älteste hat mich in dieses Kriegsgebiet geschickt um die anderen Völker zu erforschen. Genau genommen bin ich hier, um Proben von den Gnomen zu nehmen. Magatha interessiert sich insbesondere für ihre Überlebensfähigkeit. Die Gnome - obwohl erbärmlich schwach und winzig - scheinen sowohl im Kampf als auch körperlich äußerst widerstandsfähig zu sein.]], [[Zum Glück für Euch, suche ich nach Freiwilligen, die Magathas Anweisungen in die Tat umsetzen. Bringt mir Gnomhaarbüschel und die Tauren werden Euch als Verbündeten anerkennen.]], },
    { [[Ihr wurdet beauftragt gegnerische Gnomspieler im Alteractal zu töten.]], [[Tötet einen Gnom und bringt ein Büschel Gnomhaar zu Ravak Grimmtotem in Burg Frostwolf.]], },
  },
  [7363] = {
    [[Die menschliche Existenz]],
    { [[Menschen sind schlichtweg verabscheuenswert.]], [[<Kommandant Philips schneidet eine Grimasse.>]], [[Zwar entspricht es den Tatsachen, dass ich ein Soldat der Frostwölfe bin, dennoch bleibe ich ein Diener Sylvanas.]], [[Neben der Ausübung meiner Pflichten, arbeite ich auch für die Dunklen Apotheker von Unterstadt.]], [[Ich bin damit beauftragt worden menschliche Knochensplitter einzusammeln, die helfen sollen, uns von der menschlichen Existenz zu befreien.]], [[Man hat mich außerdem dazu berechtigt, diese Aufgabe an meine Untergebenen weiterzureichen.]], [[Findet sie. Tötet sie. Bringt mir ihre Knochensplitter.]], },
    { [[Ihr wurdet beauftragt gegnerische Menschenspieler im Alteractal zu töten.]], [[Tötet einen Menschen und bringt dessen Knochensplitter zu Kommandant Louis Philips (er bewegt sich zwischen den Frontlinien und Burg Frostwolf hin und her).]], [[Die Heilung für die menschliche Existenz ist zum Greifen nahe!]], },
  },
  [7364] = {
    [[Gnomeregan Kopfgeld]],
    { [[Der Hochtüftler persönlich hat mich auf dieses Schlachtfeld beordert, um meinen Brüdern und Schwestern beizustehen. Es ist uns zu Ohren gekommen, dass die Tauren sich einzelne Gnome herauspicken und töten. Noch viel schlimmer ist, dass sie den Leichen ihr prächtiges Gnomhaar büschelweise abschneiden. Diese Greultaten müssen aufgehalten werden. Wir müssen Vergeltung üben!]], [[Ich will sie alle enthuft sehen, <class>. Bringt mir ihre Hufe! Erweist euch als Verbündeter der Gnome.]], },
    { [[Ihr wurdet beauftragt gegnerische Taurenspieler im Alteractal zu töten.]], [[Tötet einen Tauren und bringt dessen Taurenhuf zu Dirk Schwindel in Dun Baldar.]], },
  },
  [7365] = {
    [[Hirschhaupts Requiem]],
    { [[Hirschhaupt hat uns Anweisung gegeben, alle Trolle auf dem Schlachtfeld auszulöschen. Ihrer lächerlichen Behauptung die Nachtelfen erschaffen zu haben, kann nur mit aller Macht und militärischen Mitteln entgegengewirkt werden.]], [[Tötet sie alle und bringt mir ihr Mojo. Ich wiederhole: Stehlt ihnen ihr Mojo.]], },
    { [[Ihr wurdet beauftragt gegnerische Trollspieler im Alteractal zu töten.]], [[Tötet einen Troll und bringt Athramanis in Dun Baldar etwas Trollmojo der Dunkelspeere.]], },
  },
  [7366] = {
    [[Die Gnade des Erzbischofs]],
    { [[Der Erzbischof ist so gütig wie er weise ist. Dass ein Mann wie er derartig gnädig zu einer solch elenden Bande sein kann...]], [[<Kommandant Philips schüttelt ungläubig den Kopf.>]], [[Zu gütig... einfach zu gütig.]], [[Der Erzbischof möchte, dass wir uns der Untoten erbarmen und ihnen einen schnellen und endgültigen Tod gewähren. Für diesen großen Akt der Gnade benötigen wir nur ihre schwarzen, von Krankheit besessenen Herzen.]], [[Bringt mir die Herzen der Verlassenen, sodass wir sie in der Kathedrale des Lichts zeremoniell verbrennen können. Eilt euch, <name>!]], },
    { [[Ihr wurdet beauftragt gegnerische Verlassenen-Spieler im Alteractal zu töten.]], [[Tötet einen Verlassenen und bringt dessen Herz zu Kommandant Karl Philips (er bewegt sich zwischen dem Sägewerk und der Mine hin und her).]], },
  },
  [7367] = {
    [[Die Gefahr bannen]],
    { [[Die Frostwölfe haben ihre Frontlinien und Basen allesamt vermint, <class>. Achtet darauf wohin Ihr tretet, wenn Ihr in feindliches Territorium eindringt.]], [[Wenn wir dem Feind einen entscheidenden Schlag versetzten wollen, müsst Ihr seine Quelle für Sprengwaffen zum Versiegen bringen - Ich rede vom feindlichen Sprengmeister! Tötet ihn und wir können alle Minen sicher entschärfen. Solange er ausfällt, fallen auch seine Minen aus.]], [[Vermutlich befindet er sich in einem Turm, nahe der Eisblutgarnison. Viel Glück, Soldat!]], },
    { [[Findet den Sprengmeister der Frostwölfe und tötet ihn. Kehrt nach Abschluss des Auftrags zum Sprengmeister der Sturmlanzen zurück.]], },
  },
  [7368] = {
    [[Die Gefahr bannen]],
    { [[Ich weiß alles über Landminen. Immerhin habe ich alle Minen an unseren Frontlinien selbst verlegt.]], [[Wenn Ihr das Minenfeld der Sturmlanzen unschädlich machen wollt, braucht Ihr nur eines zu tun - Tötet den feindlichen Sprengmeister! Zweifellos versteckt sich der feige Zwerg im ersten Turm, auf dem Feld des Kampfes. Schaltet ihn aus und die Landminen werden nicht zurückkommen, nachdem sie von Euch entschärft wurden.]], [[Falls Ihr es schaffen solltet den Sprengmeister zu töten, kommt zu mir zurück und lasst es mich wissen. Keine gute Tat soll unbelohnt bleiben.]], },
    { [[Findet den Sprengmeister der Sturmlanzen und tötet ihn. Kehrt nach Abschluss des Auftrags zum Sprengmeister der Frostwölfe zurück.]], },
  },
  [7381] = {
    [[Korraks Rückkehr]],
    { [[Es ist zwar eine Tatsache, dass Korrak gefallen ist, jedoch bleibt eine Frage offen: Wird er auch tot bleiben? Trolle sind berüchtigt für ihre außerweltliche Stammesmagie. Mich würde es nicht überraschen, wenn ihr toter Anführer wiedererweckt würde, um wieder einmal über seinen Klan zu herrschen.]], [[Ihr wisst ja was in einem solchen Fall zu tun ist.]], [[<Kriegsmeister Laggrond deutet mit seinem Finger eine Schneidebewegung an seinem Hals an.>]], [[Und nächstes mal möchte ich seinen Schädel. Haltet einfach die Augen offen, Soldat.]], [[Wegtreten!]], },
    { [[Sollte Korrak der Blutrünstige wieder in den Winteraxhöhlen auftauchen, sucht ihn auf und tötet ihn.]], [[Bringt Korraks Schädel zu Kriegsmeister Laggrond auf Burg Frostwolf.]], },
  },
  [7382] = {
    [[Korrak der Unsterbliche]],
    { [[Irgendetwas stimmt an dieser Sache nicht... Hat jemand daran gedacht Korraks Körper zu verbrennen? Diese Trolle werden vor nichts halt machen, um ihren gefallenen Anführer wiederzubeleben - so wie es die blauhäutigen Heiden des öfteren tun.]], [[Haltet immer ein Auge offen, <name>. Seid nicht beunruhigt, wenn Karrak überraschend wieder auftaucht. Und wenn er das tut, will ich ihn tot sehen - seinen Körper zu Asche verbrannt.]], [[Obendrein, schlagt ihm seinen Kopf ab und bringt mir seinen Schädel, bevor Ihr seinen leblosen Körper verbrennt.]], },
    { [[Sollte Korrak der Blutrünstige wieder in den Winteraxhöhlen auftauchen, sucht ihn auf und tötet ihn.]], [[Bringt Korraks Schädel zu Leutnant Haggerdin in Dun Baldar.]], },
  },
  [7383] = {
    [[Teldrassil: Die Bürde der Kaldorei]],
    { [[Ihr müsst nun den Mondbrunnen unter den Ästen des Orakelbaums im fernen Nordwesten aufsuchen. Er ist der erste und mächtigste unserer Brunnen und es heißt von seinem Wasser, es enthalte die Essenz von Elunes Macht.]], [[Entnehmt eine Phiole des Wassers und kehrt zu mir zurück. Ich werde euch an der Weggabelung vor Darnassus treffen, wenn Eure Aufgabe vollendet ist.]], },
    { [[Füllt die Amethystphiole.]], },
  },
  [7385] = {
    [[Eine Gallone Blut]],
    { [[Ich bin ebenso begierig wie Ihr darauf, meine tragbare Schreddereinheit auszuprobieren, <race>, aber bevor ich das Set herstellen kann, benötige ich Material - viel Material!]], [[Um genau zu sein, benötige ich einen Berg aus Thorium, Mithril und Eisen. Ich benötige außerdem eine Dampfsäge pro Einheit. Diese müsst Ihr beim Sägewerk beschaffen.]], [[Als ich mich hier umgesehen habe (vor meiner Gefangennahme), ist mir ein Stapel Dampfsägen im Holzlager der Sturmlanzen aufgefallen.]], },
    { [[Ich bin ebenso begierig wie Ihr darauf, meine tragbare Schreddereinheit auszuprobieren, <race>, aber bevor ich das Set herstellen kann, benötige ich Material - viel Material!]], [[Um genau zu sein, benötige ich einen Berg aus Thorium, Mithril und Eisen. Ich benötige außerdem eine Dampfsäge pro Einheit. Diese müsst Ihr beim Sägewerk beschaffen.]], [[Als ich mich hier umgesehen habe (vor meiner Gefangennahme), ist mir ein Stapel Dampfsägen im Holzlager der Sturmlanzen aufgefallen.]], },
  },
  [7386] = {
    [[Haufenweise Kristalle]],
    { [[Ich bin ebenso begierig wie Ihr darauf, meine tragbare Schreddereinheit auszuprobieren, <race>, aber bevor ich das Set herstellen kann, benötige ich Material - viel Material!]], [[Um genau zu sein, benötige ich einen Berg aus Thorium, Mithril und Eisen. Ich benötige außerdem eine Dampfsäge pro Einheit. Diese müsst Ihr beim Sägewerk beschaffen.]], [[Als ich mich hier umgesehen habe (vor meiner Gefangennahme), ist mir ein Stapel Dampfsägen im Holzlager der Sturmlanzen aufgefallen.]], },
    { [[Ich bin ebenso begierig wie Ihr darauf, meine tragbare Schreddereinheit auszuprobieren, <race>, aber bevor ich das Set herstellen kann, benötige ich Material - viel Material!]], [[Um genau zu sein, benötige ich einen Berg aus Thorium, Mithril und Eisen. Ich benötige außerdem eine Dampfsäge pro Einheit. Diese müsst Ihr beim Sägewerk beschaffen.]], [[Als ich mich hier umgesehen habe (vor meiner Gefangennahme), ist mir ein Stapel Dampfsägen im Holzlager der Sturmlanzen aufgefallen.]], },
  },
  [7401] = {
    [[GESUCHT: ZWERGE!]],
    { [[Auf Befehl von Unteroffizier Yazra Murrblut sind alle Zwerge auf dem Schlachtfeld umgehend zu töten.]], [[Solltet Ihr einen Zwerg im Kampf töten, schneidet ihm sein Rückgrat heraus und lasst den rückgratlosen Fleischhaufen als Mahnmal für alle zurück, die sich gegen die Frostwölfe stellen.]], [[Kehrt anschließend mit dem Beweis Eurer Heldentat zu Unteroffizier Yazra Murrblut zurück.]], },
    { [[Ihr wurdet damit beauftragt, alle feindlichen Zwergenspieler im Alteractal zu töten.]], [[Tötet einen Zwerg und schneidet ihm sein Rückgrat heraus. Bringt anschließend das Zwergenrückgrat zu Unteroffizier Yazra Murrblut in Burg Frostwolf.]], },
  },
  [7402] = {
    [[GESUCHT: ORCS!]],
    { [[Auf Befehl von Korporal Noreg Sturmlanze sind alle verfügbaren Soldaten dazu aufgefordert, jeden gegnerischen Orc auf dem Schlachtfeld umgehend zu töten.]], [[Die Soldaten sind weiterhin dazu aufgefordert, einen Hauer oder Zahn aus dem Maul des Feindes zu entfernen, um diese als Beweis für ihre Tat vorzeigen zu können.]], [[Bitte tragt dafür Schutzhandschuhe.]], },
    { [[Ihr wurdet damit beauftragt, alle feindlichen orcischen Spieler im Alteractal zu töten.]], [[Tötet einen Orc und bringt seinen Zahn zurück zu Korporal Noreg Sturmlanze in Dun Baldar.]], },
  },
