  [7441] = {
    [[Pusillin und der Älteste Azj'Tordin]],
    { [[Ich habe nur einen Moment nicht aufgepasst, Fremder. In meiner Eile, dem Griff des gefallenen Prinzen zu entkommen, wurde ich ausgeraubt. Ein besonders widerlicher Dämon, namens Pusillin, stahl mir mein Buch der Zauberformeln und den Schlüssel zu den einstmals großen Hallen von Eldre'Thalas.]], [[Mein Interesse am Schlüssel ist verloschen als ich meine Unsterblichkeit gegen die Freiheit eingetauscht habe. Doch ich benötige unbedingt mein Buch der Zauberformeln.]], [[Findet den Dämon Pusillin und bringt mir mein Buch.]], [[Sucht Pusillin im Wucherborkenviertel von Düsterbruch.]], },
    { [[Reist nach Düsterbruch und macht den Dämonen Pusillin ausfindig. Überzeugt ihn mit allen Mitteln davon, Euch Azj'Tordin's Buch der Zauberformeln zu geben.]], [[Kehrt mit dem Buch zu Az'Tordin, beim Larisspavillon in Feralas, zurück.]], },
  },
  [7461] = {
    [[Der innere Wahnsinn]],
    { [[Habt Ihr genug gehört? Seid Ihr <ein Mann/eine Frau> der Tat?]], [[Dieser Wahnsinn muss ein Ende haben. Das Zeitalter der Unsterblichen ist lange vergangen. Vernichtet die Wächter rings um die Pylonen herum, um das Kraftfeld zu senken. Habt Ihr das erst einmal geschafft, könnt Ihr Immol'thar vernichten. Die Macht des Prinzen wird stark geschwächt sein, greift ihn an und beendet seine Existenz. Gewährt den Geistern dieser Stadt ihre letzte Ruhe, Abenteurer.]], },
    { [[Zerstört alle Wächter, die um die 5 Pylonen herumstehen, welche Immol'thars Gefängnis mit Energie versorgen. Sobald die Pylone deaktiviert wurden, wird sich das Kraftfeld, das Immol'thar umgibt, auflösen.]], [[Betretet Immol'thars Gefängnis und vernichtet den verdorbenen Dämonen. Anschließend müsst Ihr Prinz Tortheldrin im Athenaeum entgegentreten.]], [[Kehrt nach Abschluss der Aufgabe zur uralten Shen'dralar im Hof zurück.]], },
  },
  [7462] = {
    [[Der Schatz der Shen'dralar]],
    { [[In der Bücherei, dem Athenaeum, werdet Ihr unter dem Treppenaufgang eine uralte Truhe finden. Nehmt Euch aus der Truhe was Ihr wollt.]], },
    { [[Kehrt in das Athenaeum zurück und sucht den Schatz der Shen'dralar. Nehmt Euch Eure Belohnung!]], },
  },
  [7463] = {
    [[Arkane Erfrischung]],
    { [[<Ein/Eine><race> <race> und dazu noch <Magier/Magierin>! Wie wunderbar. Ich habe jemanden wie Euch schon seit... wie lange ist es nun schon her?... 500 Jahren nicht mehr gesehen!]], [[Ich habe da etwas, das Euren Durst zu löschen vermag, doch zuerst müsst Ihr etwas für mich erledigen.]], [[<Lydros kichert.>]], [[Es gibt eine Anomalie in Eldre'Thalas. Ein Wasserelementar hat sich im östlichen Flügel niedergelassen. Seine Anwesenheit muss unbedingt katalogisiert werden!]], [[Reist nach Düsterbruch in das Wucherborkenviertel und sammelt etwas von der elementaren Essenz der Hydrobrut. Bringt Sie anschließend zu mir zurück und ich werde Euch einen äußerst nützlichen Zauberspruch beibringen.]], },
    { [[Reist nach Düsterbruch in das Wucherborkenviertel und besiegt den Wasserelementar Hydrobrut. Kehrt anschließend mit der Hydrobrutessenz zum Wissenshüter Lydros im Athenaeum zurück.]], },
  },
  [7481] = {
    [[Elfische Legenden]],
    { [[Vor vielen Jahren entkam ein einzelner Elfenmeister namens Kariel Winthalus der Vernichtung seines Heimatlandes. Bei sich trug er einige uralte Artefakte der Elfen.]], [[Unsere Suche nach dem verschwundenen Meister führte uns von den Ruinen von Quel'Thalas durch die Brennende Steppe über die großen Weiten der See hinüber nach Feralas.]], [[Wir nehmen an, dass der Elf in den Hallen von Eldre'Thalas, die nun als Düsterbruch bekannt sind, Schutz suchte.]], [[Ihr müsst ihn finden, Abenteurer. Ob er nun lebt oder schon lange tot ist, das Wissen, welches er bei sich trug, muss gerettet werden!]], },
    { [[Sucht in Düsterbruch nach Kariel Winthalus. Meldet Euch anschließend bei dem Weisen Korolusk in Camp Mojache.]], },
  },
  [7482] = {
    [[Elfische Legenden]],
    { [[Vor vielen Jahren entkam ein einzelner Elfenmeister namens Kariel Winthalus der Vernichtung seines Heimatlandes. Bei sich trug er einige uralte Artefakte der Elfen.]], [[Unsere Suche nach dem verschwundenen Meister führte uns von den Ruinen von Quel'Thalas durch die Brennende Steppe über die großen Weiten der See hinüber nach Feralas.]], [[Wir nehmen an, dass der Elf in den Hallen von Eldre'Thalas, die nun als Düsterbruch bekannt sind, Schutz suchte.]], [[Ihr müsst ihn finden, Abenteurer. Ob er nun lebt oder schon lange tot ist, das Wissen, welches er bei sich trug, muss gerettet werden!]], },
    { [[Sucht in Düsterbruch nach Kariel Winthalus. Meldet Euch anschließend bei der Gelehrten Runendorn in der Mondfederfeste.]], },
  },
  [7483] = {
    [[Buchband der Schnelligkeit]],
    { [[Meister Winthalus verlor den Buchband der Schnelligkeit im Ostflügel. Mit dem Buchband kann ein Zauber der geringen Hast auf Eure Ausrüstung gewirkt werden. Ein einfacher Trick...]], [[Wie auch immer... Falls Ihr Interesse habt, bringt mir den Buchband und einige grundlegende Reagenzien.]], [[Hrm... Ich glaube man benötigt das Blut von Helden, einen makellosen schwarzen Diamanten und große glänzende Splitter. Ich mag mich natürlich Irren...]], [[<Lydros zuckt mit den Achseln.>]], },
    { [[Bringt ein Buchband der Schnelligkeit, 1 makellosen schwarzen Diamanten, 2 große glänzende Splitter und 2-mal das Blut von Helden, zum Wissenshüter Lydros in Düsterbruch, um ein Arkanum der Schnelligkeit zu erhalten.]], },
  },
  [7484] = {
    [[Buchband des Fokus]],
    { [[Dieser Buchband erweckte die Neugierde eines jungen Familiars, welcher einem der Hüter gehörte. Das arme Ding wurde nur ein paar Wochen alt]], [[Diejenigen unter Euch, die sich mit Magie beschäftigen, dürften dieser Gegenstand interessieren.]], [[Das Buchband des Fokus ging in diesem Flügel verloren. Ich kann nur annehmen, dass einer der Geister der Hochgeborenen es in Besitz genommen hat.]], [[Bringt mir den Buchband, zusammen mit einen makellosen schwarzen Diamanten, einigen großen glänzenden Splittern und etwas Schattenhaut, und ich werde das Arkanum herbeizaubern.]], },
    { [[Bringt ein Buchband des Fokus, 1 makellosen schwarzen Diamanten, 4 große glänzende Splitter und 2-mal Schattenhaut, zum Wissenshüter Lydros in Düsterbruch, um ein Arkanum des Fokus zu erhalten.]], },
  },
  [7485] = {
    [[Buchband des Schutzes]],
    { [[Als er von dem Verlust dieses Buchbands hörte, weinte er wie ein kleines Kind. Wohlgemerkt, Ich selbst habe schon über derartige Verluste getrauert - und kein Verlust wiegt mehr, als der Verlust von Wissen und Geschichte- aber wegen diesem Haufen nutzloser Zauberformeln? Lächerlich! Mir scheint, der Prinz hat ihm wohl einen Gefallen getan, als er ihm das Fleisch von den Knochen riss.]], [[Bringt mir den Buchband, zusammen mit einen makellosen schwarzen Diamanten, einigen großen glänzenden Splittern und eine ausgefranste Monstrositätenstickerei, und ich werde das Arkanum wiederherstellen.]], [[Ach ja, sucht im Nordflügel.]], },
    { [[Bringt ein Buchband des Schutzes, 1 makellosen schwarzen Diamanten, 2 große glänzende Splitter und 1 ausgefranste Monstrositätenstickerei zum Wissenshüter Lydros in Düsterbruch, um ein Arkanum des Schutzes zu erhalten.]], },
  },
  [7486] = {
    [[Die Belohnung eines Helden]],
    { [[<name>, Euer Dienst für die Kriegsherrn hat alles übertroffen, was wir je von den Einwohnern dieser Welt erwartet haben.]], [[Wir kennen zwar nicht die Vorstellung Eures Volkes, von Dankbarkeit oder wie Ihr gute Arbeit bei Euch belohnt, aber in diesem Fall...ist eine Belohnung mehr als angebracht.]], [[Unter der Brandung an der südlichen Basis der Insel, findet Ihr einen Kasten. In diesem werdet Ihr Eure... Belohnung finden.]], [[Geht, <name>. Nehmt Euch Eure Belohnung.]], },
    { [[Nehmt Eure Belohnung aus Hydraxis' Kasten.]], },
  },
  [7487] = {
    [[Abstimmung mit dem Kern]],
    { [[Risse und Abgründe bilden sich um uns herum und kollabieren wieder, <race>. Keine zwei Schritte von mir entfernt, ist ein Riss, der hinab in die tiefsten Tiefen des Schwarzfels führt, bis in die Ruhestätte des Feuerfürsten.]], [[Überrascht? Wie bedauerlich... Ihr sterblichen Völker versteht einfach nichts, was Ihr nicht sehen, anfassen oder fühlen könnt.]], [[Ich versichere Euch, das Portal ist da und es ist passierbar.]], [[Habe ich Euer Interesse geweckt? Eine Anpassung ist simpel. Begebt Euch in die Schwarzfelstiefen und findet ein Kernfragment. Bringt es zu mir zurück und ich werde Eure Essenz mit dem Portal abstimmen.]], },
    { [[Begebt Euch zum Portal in den Schwarzfelstiefen, das in den Geschmolzenen Kern führt, und findet ein Kernfragment. Kehrt mit dem Fragment zu Lothos Felsspalter im Schwarzfels zurück.]], },
  },
  [7488] = {
    [[Lethtendris' Netz]],
    { [[Die Magierin Lethtendris, eine bösartige Blutelfe, deren Brutalität nur durch ihre pervertiere Hingabe zur Magie in den Schatten gestellt wird, ist nach Düsterbruch geflüchtet. Sie hat eine Apparatur entwickelt, ein Netz, um die magischen Energien dieses Ortes einzufangen und wir fürchten, dass wenn Sie niemand aufhält, sie der Welt einen unwiderruflichen Schaden zufügen wird!]], [[Stoppt Sie, <name>. Findet Sie und nehmt das Netz an Euch. Am ehesten werdet Ihr sie nahe den Satyrn, im Wucherborkenviertel von Düsterbruch, finden. Bringt das Netz anschließend zu mir und ich werde versuchen, dessen Kräfte wieder der Wildnis zurückzugeben...]], },
    { [[Bringt Lethtendris' Netz zu Latronicus Mondspeer in der Mondfederfeste in Feralas.]], },
  },
  [7489] = {
    [[Lethtendris' Netz]],
    { [[Die Blutelfe Lethtendris hat ihre Grenzen überschritten. So versessen war sie darauf magische Macht zu erlangen, das sie ihre Brüder verraten und eine teuflische Apparatur gebaut hat, ein Netz welches in der Lage ist, die verdorbenen magischen Energien von Düsterbruch einzufangen. Selbst jetzt sammelt sie diese Energien und plant, sie gegen ihre Feinde einzusetzen.]], [[Lethtendris muss aufgehalten werden. Sucht sie im Wucherborkenviertel von Düsterbruch, besiegt sie und bringt anschließend ihr Netz zu mir. So kann es vielleicht wieder in die Hände einiger Blutelfen übergeben werden, die dieses Artefakt mit klarem Verstand untersuchen können.]], },
    { [[Bringt Lethtendris' Netz zu Talo Dornhuf im Camp Mojache in Feralas.]], },
  },
  [7490] = {
    [[Sieg für die Horde]],
    { [[Ihr habt das Unmögliche vollbracht. Die Brutmutter des schwarzen Drachenschwarms liegt tot zu Euren Füßen. Nehmt ihren Kopf und präsentiert ihn Eurem Kriegshäuptling.]], },
    { [[Bringt Onyxias Kopf zu Garrosh in Orgrimmar.]], },
  },
  [7491] = {
    [[Für alle sichtbar]],
    { [[Oberanführer Runthak erwartet Eure Ankunft im Tal der Stärke.]], [[<Der Kriegshäuptling salutiert euch.>]], [[Lasst Euer Publikum nicht warten, großer Held.]], },
    { [[Sucht Oberanführer Runthak im Tal der Stärke auf.]], },
  },
  [7492] = {
    [[Camp Mojache]],
    { [[Talo Dornhuf, ein Taurenältester in Feralas, hat uns um Hilfe gebeten. Er hat nur wenige Einzelheiten verraten, doch wie es scheint sucht er nach Agenten für eine Angelegenheit, die sowohl heikel als auch dringend ist.]], [[Dient der Horde, <name>. Begebt Euch ins Camp Mojache in Feralas und sprecht mit Talo.]], },
    { [[Sprecht mit Talo Dornhuf in Camp Mojache, in Feralas.]], },
  },
  [7493] = {
    [[Die Reise beginnt erst]],
  },
  [7494] = {
    [[Die Mondfederfeste]],
    { [[<name>! Man braucht Euch in Feralas!]], [[Eine abtrünnige Magierin, Lethtendris, ist in die uralten Elfenruinen von Eldre'thalas, vielen bekannt als Düsterbruch, geflohen. Es wird angenommen, dass sie einen gefährlichen Gegenstand voller magischer Energien bei sich trägt, der dringend wiederbeschafft werden muss.]], [[Latronicus Mondspeer hat Agenten angefordert, die in Düsterbruch eindringen und den Gegenstand von Lethtendris zurückholen sollen.]], [[Dient der Allianz! Sucht Latronicus in der Mondfederfeste in Feralas auf.]], },
    { [[Sprecht mit Latronicus Mondspeer in der Mondfederfeste in Feralas.]], },
  },
  [7495] = {
