  [7507] = {
    [[Forors Kompendium]],
    { [[Ihr habt etwas von größter Wichtigkeit entdeckt. Dieses Buch ist anders, als alle anderen die Ihr bisher gesehen habt.]], [[Es scheint, als hätte jemand oder etwas es geschafft, das Siegel des Athenaeums aufzubrechen. Der Text in seinem Inneren ist beschädigt und schwer lesbar.]], },
    { [[Bringt Forors Kompendium des Drachentötens zurück in das Athenaeum.]], },
  },
  [7508] = {
    [[Das Schmieden von Quel'Serrar]],
    { [[Wenn Ihr diese Aufgabe annehmen wollt, zeigt Lydros die glanzlose Klinge.]], },
    { [[Bringt dem Wissenshüter Lydros die stumpfe und glanzlose Elfenklinge.]], },
  },
  [7509] = {
    [[Das Schmieden von Quel'Serrar]],
    { [[Bringt das uralte, noch unerhitzte Schwert in Onyxias Hort und tretet ihr im Kampf gegenüber.]], [[Nur mit einem Trick werdet Ihr es schaffen die Klinge zu erhitzen, also hört gut zu. Im Kampf wird Sie mit Sicherheit nichts unversucht lassen, um Euch mit ihrem Feueratem einzuäschern. Legt die Klinge irgendwo in der Nähe von Onyxia ab und hofft, dass ihr feuriger Atem diese Klinge erhitzt. Sollte es klappen und Ihr noch am Leben sein, hebt die dann erhitzte uralte Klinge wieder auf und erledigt den Drachen.]], [[Rammt anschließend die noch heiße Klinge in den schwelenden Drachenkörper, um dadurch eine behandelte uralte Klinge zu erhalten.]], },
    { [[Bringt Onyxia dazu, ihren Feueratem auf die noch unerhitzte Klinge zu benutzen. Sobald dies geschehen ist, hebt die erhitzte Klinge wieder auf. Doch seid gewarnt, die erhitzte Klinge wird nicht auf ewig erhitzt bleiben - behaltet also die Zeit im Auge.]], [[Zuletzt müsst Ihr das Biest töten und die uralte erhitzte Klinge in ihren Leib rammen. Kehrt anschließend zu mir zurück und Quel'Serrar wird Euer sein.]], },
  },
  [7521] = {
    [[Donneraan der Windsucher]],
    { [[Ich habe Euch alles gesagt was ich weiß, <name>. Es ist nun Eure Aufgabe Elementium zu finden. Ihr seid es, der die körperliche Form des Feuerfürsten zerstören muss.]], [[Gewinnt dadurch den Segen von Donneraan.]], },
    { [[Um Donneraan den Windsucher aus seiner Gefangenschaft zu befreien, müsst Ihr Hochlord Demitrian die rechten und linken Hälften der Fesseln des Windsuchers, 10 Elementiumbarren und die Essenz des Feuerfürsten bringen.]], },
  },
  [7522] = {
    [[Untersuchung des Gefäßes]],
    { [[Untersucht das Gefäß der Wiedergeburt, Sterblicher. Mit seiner Hilfe soll der Windsucher wieder auferstehen!]], },
    { [[Untersucht das Gefäß der Wiedergeburt und bringt es zu Hochlord Demitrian.]], },
  },
  [7541] = {
    [[Dienst an der Horde]],
  },
  [7562] = {
    [[Mor'zul Blutbringer]],
    { [[<name>, Eure Studien der dunklen Künste sind sehr eingehend. Euer Einfluss auf die Kräfte der jenseitigen Welt ist sehr stark geworden... ich denke, Ihr seid nun bereit eines der berüchtigten Schreckensrösser an Euch zu binden.]], [[Das Ritual zum Beschwören eines dieser Biester erfordert große Macht und Ressourcen, doch wenn Ihr solch ein episches Reittier beherrschen wollt, sprecht mit Meister Mor'zul Blutbringer. Er hat sein Lager nahe des Altars der Stürme in der Brennenden Steppe aufgeschlagen.]], [[Geht, <name>. Diese Aufgabe wird sehr gefährlich werden, aber ein Hexenmeister von Eurer Statur sollte sich nicht vor Gefahr fürchten.]], },
    { [[Sprecht mit Mor'zul Blutbringer beim Altar der Stürme in der Brennenden Steppe.]], },
  },
  [7563] = {
    [[Kochendes Blut]],
    { [[Die Schreckensrösser existieren auf einer uns fremden Welt, in der feurigen Ebene von Xoroth. Um eines dieser einzigartigen Tiere an Euch zu binden, werdet Ihr mächtige Glyphen beschwören müssen. Ich werde für Euch die erforderlichen Glyphen auf einem Pergament niederschreiben, doch dafür brauche ich eine ganz spezielle Tinte.]], [[Im weit entfernten Winterquell lebt eine besondere Eulenart. Einst waren es normale Tiere, doch die dämonische Macht der Flüsternden Schlucht trieb sie in den Wahnsinn. Übrig blieben nur Blutrünstige Bestien.]], [[Es ist ihr Blut, das ich zum Zeichnen der Glyphen benötige. Bringt mir etwas davon und ich kann Euch weiterhelfen.]], },
    { [[Mor'zul Blutbringer in der Brennenden Steppe möchte, dass Ihr ihm 30 Flaschen mit dem Blut der tobenden Bestien bringt.]], },
  },
  [7564] = {
    [[Wildaug]],
    { [[Mein Assistent Gorzeeki ist ein recht bewanderter Alchemist. Er sollte in der Lage sein die Tinte herzustellen, die ich für die Herstellung der Schriftrolle benötige. Ich habe das von Euch mitgebrachte Blut destilliert. Nehmt es und bringt es zu Gorzeeki, damit er mit seiner Arbeit beginnen kann.]], },
    { [[Bringt den Behälter mit Blut zu Gorzeeki Wildaug beim Altar der Stürme in der Brennenden Steppe.]], },
  },
  [7581] = {
    [[Das Fundament des Gefängnisses]],
    { [[Ihr müsst ein geeignetes Fundament für das Gefängnis der Verdammniswache finden.]], [[Das Blut der Wildhufsatyrn, die in Düsterbruch hausen, sollte dem Genüge tun. Das geronnene Blut wird die Splitter von Hederine zusammenhalten und das Kristallgefängnis mit Energie versorgen.]], },
    { [[Reist nach Düsterbruch in Feralas und holt Euch das Blut von 15 Satyrn der Wildhufe, die im Wucherborkenviertel ansässig sind. Kehrt anschließend zu Daio in der Faulenden Narbe zurück.]], },
  },
  [7582] = {
    [[Die Gefängnishülle]],
    { [[Die Dämonen von Hederine, die das Vorgebirge des Berges Hyjal bevölkern, sondern mächtige Gifte ab, die mit der Zeit hart und somit zu einem kompakten Anti-Magiekristall werden. Diese Kristalle sind auch als Tränen der Hederine bekannt.]], [[Nicht einmal die gewaltige Macht einer Verdammniswache könnte die Wände eines Gefängnisses durchbrechen, die aus diesem Material errichtet wurden.]], [[Reist in die Flüsternde Schlucht in Winterquell und holt Euch die Tränen.]], },
    { [[Reist in die Flüsternde Schlucht in Winterquell und besorgt Euch 5 Tränen bei den Dämonen von Hederine, die in der Schlucht leben. Kehrt zu Daio in der Faulenden Narbe zurück, sobald Ihr diese Aufgabe vollständig erledigt habt.]], },
  },
  [7583] = {
    [[Unterdrückung]],
    { [[Das Gefängnis ist bereit. Jetzt kommen wir zum schwierigsten Teil des Unterfangens.]], [[Ihr müsst mithilfe des Kristallgefängnisses einen Verdammniswachenkommandanten in der Faulenden Narbe finden und fangen.]], [[Seid gewarnt! Verdammniswachen sind gerissene und tödliche Gegner, die Euch sofort zu zerstören versuchen werden, sobald Ihr mit dem Ritual der Gefangenschaft beginnt. Euer Gegner wird bei seinem Versuch der Gefangenschaft zu entgehen nichts unversucht lassen.]], [[Er wird sich nur auf Euch konzentrieren, <name>. Ich muss Euch deshalb zu der Begleitung von einigen Gefährten raten, die fähig genug sind, um Euch am Leben zu erhalten!]], },
    { [[Wagt Euch in die Faulende Narbe heraus und macht einen Verdammniswachenkommandanten ausfindig.]], [[Wendet das leuchtende Kristallgefängnis auf den Verdammniswachenkommandanten an. Bereitet Euch auf eine heftige Serie von Attacken vor, da der Dämon alles gegen seine Gefangennahme unternehmen wird.]], [[Solltet Ihr erfolgreich sein, bringt die gefangene Verdammniswache zu Daio in der Faulenden Narbe.]], },
  },
  [7601] = {
    [[Nibys Anweisungen]],
    { [[Wenn ich Euch das Geheimnis der Beschwörung eines Höllenbestiendieners lehren soll, so müsst Ihr Kroshius zerstören und mir den Höllenbestienkern seiner brennenden Überreste bringen. Wie auch immer, es ist nicht so einfach wie es klingt. Kroshius hat zwar alles, ist aber vollständig ausgebrannt. Ihr müsst seinen Körper wieder entzünden und ihn wieder von dem Schutthaufen auferstehen lassen. Allerdings braucht Ihr dafür ein paar Komponenten von anderen Dämonenwesen.]], [[Impsy, mein Wichtel, soll Euch auf alles weitere vorbereiten.]], },
    { [[Sprecht mit Impsy im Teufelswald.]], },
  },
  [7602] = {
    [[Reine Teufelsessenz]],
    { [[Ihr werdet für die Wiederbelebung von Kroshius Teufelsfeuer benötigen. Ich kann zwar Teufelsfeuer machen, brauche dazu aber ein paar Komponenten.]], [[Ihr müsst mir reine Teufelsessenz aus drei bestimmten Gebieten der Welt bringen.]], [[Den ersten Essenztyp findet Ihr bei den Satyrn der Legashi von Azshara; den zweiten Typ bei den Legionären von Jaedenar in Jaedenar und den dritten bei den Wachposten der Teufelswache in den Verwüsteten Landen.]], [[Bringt mir jeweils eine Essenz pro Typ, dann werde ich das Teufelsfeuer entfachen, mit dem Ihr Kroshius wieder auferstehen lassen könnt.]], },
    { [[Impsy im Teufelswald möchte, dass Ihr ihm drei reine Teufelsessenzen aus drei bestimmten Gebieten besorgt.]], [[Die Satyrn der Legashi von Azshara bergen die Teufelsessenz ihrer Region. Die Legionäre von Jaedenar in Jaedenar bergen die Teufelsessenz ihrer Region. Die Wachposten der Teufelswache in den Verwüsteten Landen bergen die Teufelsessenz ihrer Region.]], [[Findet die Teufelsessenzen und bringt sie zu Impsy im Teufelswald.]], },
  },
  [7603] = {
    [[Kroshius Höllenbestienkern]],
    { [[Kroshius liegt zusammengefallen im Narbengrund, Richtung Nordosten. Verwendet dieses Teufelsfeuer auf seinen Überresten, es sollte die gefallene Höllenbestie wieder erwecken.]], [[Ich bin überzeugt, dass er Euch und Euren Begleiter genau dann umbringen wird.]], [[Solltet Ihr Kroshius dennoch auf wundersame Weise töten können, dann nehmt den Höllenbestienkern und bringt ihn zu Niby.]], },
    { [[Sucht im Narbengrund im Teufelswald nach den Überresten von Kroshius. Habt Ihr sie gefunden, so verwendet das Teufelsfeuer in deren Nähe und wartet dann auf Kroshius' Rückkehr. Tötet die Höllenbestie und entnehmt ihrem Körper den Höllenbestienkern.]], [[Bringt Kroshius' Höllenbestienkern zu Niby dem Allmächtigen im Teufelswald.]], },
  },
  [7604] = {
    [[Ein verbindlicher Vertrag]],
    { [[<Der Unterzeichner/Die Unterzeichnerin> erklärt hiermit, dass es sich bei dem Sulfuronblock, der an die Thoriumbruderschaft (TB Ltd.) geliefert wird, um ein Original und nicht um eine Nachbildung handelt. <Der Unterzeichner/Die Unterzeichnerin> erkennt ebenso an, dass jeglicher Schaden, der <ihm/ihr> in Folge des Versuchs Sulfuras herzustellen, zu schmieden, verkaufen oder zu essen widerfahren könnte, nicht in der Verantwortung oder Verschuldung von TB Ltd. liegt.]], [[Mit Unterzeichnung dieses Vertrags, wird allen darin aufgelisteten Bedingungen zugestimmt.]], [[_________________]], [[Unterschrift]], },
    { [[Bringt den Vertrag der Thoriumbruderschaft zu Lokhtos Düsterfeilsch, wenn Ihr die Pläne für das Sulfuron erhalten möchtet.]], },
  },
  [7621] = {
    [[Eine Warnung]],
  },
  [7622] = {
    [[Die Waage von Licht und Schatten]],
    { [[Ihr müsst tun was ich nicht konnte: Rettet die Arbeiter, die während ihrer Flucht von Stratholme niedergemäht wurden.]], [[Sie werden dem Licht entgegen laufen, sorgt für ihr Überleben. Sollten zu viele von ihnen fallen, wird unsere Existenz auch weiterhin verdammt sein - und Ihr werdet versagt haben.]], [[Alle von Euch gelernten Fähigkeiten, Gebete und Zauber werden auf die Probe gestellt werden. Möge das Licht Euch beschützen, <name>.]], },
    { [[Rettet 50 Arbeiter bevor 15 getötet wurden. Sprecht mit Eris Himmelsfeuer, falls Ihr diesen Auftrag erfolgreich zu Ende bringen solltet.]], [[Konsultiert den Todesposten, um zu sehen wie viele Arbeiter bereits getötet wurden.]], },
  },
  [7623] = {
    [[Lord Schattenfluch]],
    { [[Mor'zul sagte mir, dass Ihr ein Portal nach Xoroth öffnen möchtet. Für eine solche Aufgabe werdet Ihr xorothianischen Sternstaub benötigen und der ist nur schwer in Azeroth zu finden!]], [[Aber es gibt jemanden, der welchen besitzen sollte: Lord Schattenfluch in Jaedenar, dem Sitz des Schattenrats! Sprecht mit ihm und vielleicht wird er Euch etwas Sternenstaub überlassen.]], [[Doch wenn Ihr nach Jaedenar wollt, solltet Ihr Euch mit dem gleichen Aroma des Bösen, das den Schattenrat umgibt, tarnen. Dazu werdet Ihr einige meiner schattenhaften Tränke brauchen und die sind nicht umsonst...]], },
    { [[Erwerbt schattenhafte Tränke von Gorzeeki Wildaug beim Altar der Stürme in der Brennenden Steppe.]], [[Benutzt die Tränke um durch Jaedenar im Teufelswald zu reisen und sprecht mit Lord Schattenfluch in der Schattenfeste.]], },
  },
  [7624] = {
    [[Ulathek der Verräter]],
    { [[Ihr wollt meine Gunst erlangen? Dann tötet für mich.]], [[Mein Diener Ulathek treibt ein erbärmliches Spiel, während er wie ein elender Wurm vor mir kriecht, konspiriert er heimlich mit Lord Hel'nurath von Xoroth. Hel'nurath trachtet danach meine Macht an sich zu reißen und Ulathek ist sein Spion...]], [[Beweist mir, dass Ihr mir und nicht meinem Rivalen dient. Stellt Ulathek in seinem Quartier, der Schattenfeste im Osten. Macht ihm klar, dass ich sein kleines Geheimnis kenne und seht wie die Angst von ihm Besitz ergreift. Bringt mir anschließend das Herz des Verräters!]], },
    { [[Stellt Euch Ulathek und bringt das Herz des Verräters zu Lord Schattenfluch in Jaedenar.]], },
  },
  [7625] = {
    [[Xorothianischer Sternenstaub]],
    { [[Ihr kommt also zu mir, weil Ihr xorothianischen Sternenstaub sucht? Ihr möchtet ein Portal nach Xoroth, dem Herrschaftsgebiet meines Rivalen Hel'nurath öffnen? Vielleicht plant Ihr sogar eines seiner geliebten Schreckensrösser zu stehlen?]], [[Nun gut. Mein Diener Ur'dan hat einen Vorrat an Sternenstaub. Ihr könnt es von ihm bekommen... wenn ihr den Preis bezahlen könnt.]], },
    { [[Erwerbt xorothianischen Sternenstaub von Ur'dan tief im Inneren der Schattenfeste in Jaedenar im Teufelswald. Bringt es anschließend Gorzeeki Wildaug in der Brennenden Steppe.]], },
  },
  [7626] = {
    [[Die Glocke von Dethmoora]],
    { [[Um einen mächtigen Beschwörungskreis herzustellen, benötigt man eine Glocke von Dethmoora, benannt nach dem uralten Hexenmeister Dethmoora Finsteraugen. Richtig abgestimmt mit dem Rad und der Kerze, speichern diese Gegenstände riesige Mengen arkaner Macht.]], [[Mein Assistent Gorzeeki kann eine solche Glocke herstellen, allerdings benötigt er dafür einen großen Vorrat an Elixieren der Schattenmacht. Besorgt Euch die Elixiere von einem Alchemisten und bringt sie Gorzeeki.]], },
    { [[Bringt 2 Elixiere der Schattenmacht zu Gorzeeki Wildaug in die Brennende Steppe.]], },
  },
  [7627] = {
    [[Das Rad des Schwarzen Marsches]],
    { [[Ein Rad des Schwarzen Marsches enthält und kontrolliert die gefährlichen Energien, welche für das Ritual des mächtigen Beschwörungskreises benötigt werden. Um das Rad zu konstruieren, benötigt Gozeeki große glänzende Splitter von einem Verzauberer und Dunkeleisen von einem Meisterbergmann.]], [[Beschafft diese Gegenstände und bringt sie meinem Assistenten.]], },
    { [[Bringt 6 große glänzende Splitter und 25 Stücke Dunkeleisenerz zu Gorzeeki Wildaug in die Brennende Steppe.]], },
  },
  [7628] = {
    [[Die Altarkerze des Jüngsten Gerichts]],
    { [[Eine Altarkerze des Jüngsten Gerichts speichert Feuer in ihrem Inneren und schleudert es auf jeden, der versucht, Euer Ritual zu unterbrechen. Wenn Ihr das Ritual des mächtigen Beschwörungskreises aufbaut und die Kerze angezündet ist, wird sie jeden Dämon, der sich Euch in den Weg stellt, mit ihrer Flamme verbrennen.]], [[Dieses spezielle Feuer einzufangen ist nicht einfach und erfordert die Essenz aus der Schuppe eines schwarzen Drachen. Beschafft Euch die Drachenschuppen und bringt sie zu Gorzeeki Wildaug.]], },
    { [[Bringt 3 Schuppen eines schwarzen Drachen zu Gorzeeki Wildaug in der Brennenden Steppe.]], },
  },
  [7629] = {
    [[Wichtellieferung]],
    { [[Es gibt noch eine Sache die für Mor'zuls Pergament erledigt werden muss. Ich benötige Papier, welches mit xorothianischem Sternenstaub versetzt ist. Leider kann ich diese Prozedur hier nicht durchführen, da mir die Ausrüstung dafür fehlt!]], [[Ich kenne aber ein Alchemielabor mit der nötigen Ausstattung. Es befindet sich in Scholomance, auf der Insel Darrowehr in den Pestländern.]], [[Ich kann diese Reise nicht selber machen, aber nehmt dieses Gefäß. Im Inneren befindet sich ein äußerst intelligenter Wichtel. Lasst ihn frei, wenn Ihr im Alchemielabor angekommen seid und er wird die Prozedur mit dem Sternenstaub für Euch vornehmen.]], },
    { [[Bringt den Wichtel im Glas in das Alchemielabor in Scholomance. Bringt das Glas nach der Herstellung des Pergaments zurück zu Gorzeeki Wildaug.]], },
  },
  [7630] = {
    [[Arkanit]],
    { [[Nachdem die Glocke, das Rad und die Kerze jetzt fertiggestellt sind, muss sie jemand auf das Ritual vorbereiten. Ich besitze einen Wichtel, der die Vorbereitungen genau kennt... Ihr braucht nur noch ein Behältnis, um ihn zu transportieren.]], [[Ich kann Euch ein solches Behältnis verkaufen, aber um es herzustellen brauche ich Arkanit. Habt Ihr welches? Falls nicht, solltet Ihr einen Alchemisten finden, der es für Euch herstellen kann.]], [[Kommt zu mir zurück, wenn Ihr das Arkanit habt, dann verkaufe ich Euch den Wichtel im Glas!]], },
    { [[Bringt 1 Arkanitbarren zu Gorzeeki in der Brennenden Steppe.]], },
  },
  [7631] = {
    [[Schreckensross von Xoroth]],
    { [[Alle Vorbereitungen sind getroffen. Als erstes müsst Ihr das Ritual des mächtigen Beschwörungskreises vollziehen. Anschließend benutzt die xorothianischen Glyphen um ein Portal nach Xoroth zu öffnen.]], [[Ich habe Euch die Anweisungen für das Ritual auf eine Schriftrolle geschrieben. Studiert sie sorgfältig, denn wenn Ihr Erfolg haben wollt, müsst Ihr jeden Schritt exakt nach Anleitung durchführen.]], [[Ich wünsche Euch viel Erfolg, <name>. Und mögen die Seelen Eurer Feinde, den Weg in Euren Seelensteinbeutel finden.]], },
    { [[Lest Morzuls Anweisungen. Beschwört ein xorothianisches Schreckensross, besiegt es und bindet seinen Geist an Euch.]], },
  },
  [7632] = {
    [[Das uralte Blatt]],
    { [[Die Epidermis des Blatts leuchtet hellgrün. Die Oberfläche ist extrem runzelig und weist etliche Adern und Falten auf.]], [[Man könnte vermuten, dass dieses Blatt von etwas sehr Altem und Mächtigem stammt.]], [[Vielleicht solltet Ihr den ursprünglichen Besitzer dieses Blatts ausfindig machen?]], },
    { [[Findet den Besitzer des uralten, versteinerten Blatts. Viel Glück, <name> die Welt ist groß.]], },
  },
  [7633] = {
    [[Eine Einführung]],
  },
  [7634] = {
    [[Uraltes in Sehnen eingewickeltes Laminablatt]],
    { [[Seid gegrüßt, <class>. Ich bin Hastat das Urtum des Wissens. Ich verwahre seit Ewigkeiten verlorenes Wissen.]], [[Richtig gebunden kann eines meiner Blätter als Köcher dienen, dergleichen wurde seit 10.000 Jahren nicht mehr gesehen. Ein Köcher, der - wenn er von einem Bogen der Uralten begleitet wird - ungeahnte Kräfte verleihen könnte.]], [[Kehrt mit der Sehne eines ausgewachsenen blauen Drachen hierher zurück und der Köcher möge Euer sein.]], },
    { [[Hastat der Uralte hat Euch um die Beschaffung einer Sehne eines ausgewachsenen, blauen Drachen gebeten. Kehrt zu Hastat im Teufelswald zurück, solltet Ihr diese Sehne finden.]], },
  },
  [7635] = {
    [[Die passende Sehne]],
    { [[Vartrus wird Euch womöglich den Holzstab für die Erstellung eines Bogens der Uralten geben. Allerdings kann nur ich die benötigte Sehne verzaubern, die den Bogen vervollständigt.]], [[Es ist eine einfache Aufgabe, mickriges Geschöpf. Tötet einen ausgewachsenen, schwarzen Drachen und entfernt eine Sehne von seinen Knochen. Bringt mir diese Sehne und ich werde das Material in eine fast unzerstörbare Bogensehne verzaubern.]], },
    { [[Stoma der Uralte hat Euch um die Beschaffung einer Sehne eines ausgewachsenen, schwarzen Drachen gebeten. Solltet Ihr diese Sehne finden, kehrt zu Stoma im Teufelswald zurück.]], },
  },
  [7636] = {
    [[Holzstab der Uralten]],
    { [[Agenten der Brennenden Legion sind noch immer in unserer Welt aktiv, <class>. Diese Agenten verderben alles, was ihnen unterkommt. Die Natur welkt und siecht in ihrer Gegenwart dahin.]], [[Ihr müsst diese dämonischen Verderber aufspüren und zerstören, <name>. Seid gewarnt, Ihr müsst sie selbst angreifen, andernfalls werden sie fliehen. Solange Ihr alleine seid, werden sie versuchen Euch zu zerstören. Ihr müsst sie bekämpfen und ihren Hochmut zu ihrem Verderben werden lassen.]], [[Ich gewähre Euch ein Stück meiner Rinde, mögen die Inschriften Euch zu Eurem Ziel führen.]], },
    { [[Ihr müsst die vier dämonischen Verderber ausfindig machen und zerstören:]], [[Simone die Verführerin.]], [[Klinfran der Wahnsinnige.]], [[Solenor der Schlächter.]], [[Artorius der Verhängnisvolle.]], [[Vernichtet diese Kreaturen und kehrt zu Vartrus dem Uralten im Teufelswald zurück.]], [[Haltet Euch an die versteinerte Rinde in Eurem Inventar, um Hinweise zu ihren Aufenthaltsorten zu erhalten.]], [[Ihr MÜSST diese Aufgabe eigenständig erledigen.]], },
  },
  [7637] = {
    [[Ein Opfer darbieten]],
    { [[Um Euch auf diese Aufgabe vorzubereiten, müsst Ihr Eure Opferbereitschaft und Euer Urteilsvermögen beweisen... die Betonung liegt hier besonders auf 'opfern'.]], [[Ihr benötigt Hohepriester Rohans Weihrauchschwenker für die kommende Aufgabe. Und obwohl Ihr damit gutes bewirken werdet, müsst Ihr etwas zum Ausgleich hergeben.]], [[Begebt Euch nach Eisenschmiede und sprecht dort mit dem Hohepriester. Nehmt reichlich Münzen mit, um ihm den Verlust seines Weihrauchschwenker zu vergelten und um Euren Opferwillen zu zeigen.]], [[Geht nun, <name>, denn Zeit ist hier ein wichtiger Faktor.]], },
    { [[Reist nach Eisenschmiede und besorgt Euch Hohepriester Rohans Weihrauchschwenker. Eine Spende von 150 Gold wird erforderlich sein, um es zu bekommen.]], },
  },
  [7638] = {
    [[Lord Grayson Schattenbruch]],
    { [[<name>, der Zeitpunkt ist nun gekommen, an dem Ihr lernen werdet, was Ihr tun müsst, um etwas zu bekommen, auf das Ihr bestimmt schon lange gewartet habt... Euer eigenes Streitross. Sprecht mit Lord Grayson Schattenbruch, hier im Kathedralenviertel von Sturmwind. Er wird Euch nicht nur in die Geheimnisse des treuesten Begleiters eines Paladins einweihen, sondern er wird Euch auch durch die Prüfungen führen, die Ihr absolvieren werdet, um Euer eigenes Streitross zu erhalten.]], [[Ich wünsche Euch viel Glück mit dieser Prüfung. Für das Licht, <nobler Bruder/noble Schwester>!]], },
    { [[Sprecht mit Lord Grayson Schattenbruch im Kathedralenviertel von Sturmwind.]], },
  },
  [7639] = {
    [[Lass Gerechtigkeit walten]],
    { [[Nehmt diesen Weihrauchschwenker mit meinem Segen. Ich selbst habe ihn schon für dutzende Exzorzismen benutzt. Er wird Euch, bei den vor Euch liegenden Aufgaben, gut dienen.]], [[Kehrt nun zu Lord Grayson Schattenbruch in Sturmwind zurück, <name>. Ihr müsst Eure Suche mit seiner Hilfe fortsetzen.]], },
    { [[Bringt den Weihrauchschwenker zu Lord Grayson Schattenbruch in Sturmwind zurück.]], },
  },
  [7640] = {
    [[Der Exorzismus des Schreckenstals]],
    { [[Ich habe ein wenig von der verdorbenen Erde des Schreckenstals, aus den Östlichen Pestländern, in den Weihrauchschwenker getan. Benutzt den Schwenker, um Gebiete die von spiritueller Unruhe und Störungen befallen sind, ausfindig zu machen. Haltet nach grünen Auren Ausschau, die die Erde durchziehen, während sich die materielle Welt unter den Qualen der Spirituellen Ebene verzerrt. Benutzt dort den Weihrauchschwenker, um die Geister des Bösen zu vertreiben.]], [[Habt Ihr erst einmal genug Gerechtigkeit unter den Geistern walten lassen, seid Ihr bereit für Euren weiteren Weg.]], },
    { [[Vertreibt mithilfe des Weihrauchschwenkers die Geister im Schreckenstal. Vernichtet 25 Schreckenstalgeister und kehrt anschließend zu Lord Grayson Schattenbruch im Kathedralenviertel von Sturmwind zurück.]], },
  },
  [7641] = {
    [[Grimand Elmores Werk]],
    { [[Ohne ein angemessenes Pferdegeschirr, ist das Streitross eines Paladins nichts weiter als ein Packesel. Das Pferdegeschirr für Euer Streitross sollte aus den bestmöglichem Material gefertigt sein und in Eurem speziellen Fall muss es auch noch mit der spirituellen Ebene abgestimmt werden.]], [[Aber zuerst einmal muss Euer Pferdegeschirr gefertigt werden. Im Zwergendistrikt lebt einer der besten Schmiede unserer Zeit, Grimand Elmore. Seine Arbeit wird zwar nicht gerade billig sein, aber seine Qualität ist genau das, was wir suchen. Sprecht mit ihm.]], },
    { [[Sprecht mit Grimand Elmore im Zwergendistrikt von Sturmwind.]], },
  },
  [7642] = {
    [[Fünf Dinge]],
    { [[Ich benötige vier Dinge für das Pferdegeschirr - Runenstoff für das Innenfutter, Arkanitbarren für die Panzerung, Arthas' Tränen für den spirituellen Einklang und Weihwasser aus Stratholme zur spirituellen Stärkung. Nun, genau genommen braucht Ihr noch eine fünfte Sache... Gold, um mich zu bezahlen. Die Bezahlung umfasst alle anfallenden Arbeiten sowie einen handgemachten Sattel und Steigbügel.]], [[Wenn Ihr alles gebracht habt, kann ich Euer Pferdegeschirr fertigstellen. Bis Ihr alle Sachen beisammen habt, werde ich schon einmal einige der Arbeiten für Euch erledigen können.]], },
    { [[Bringt 40 Lagen Runenstoff, 6 Arkanitbarren, 10 Arthas' Tränen, 5 Phiolen Weihwasser aus Stratholme und 150 Gold zu Grimand Elmore im Zwergendistrikt von Sturmwind.]], },
  },
  [7643] = {
    [[Der uralte Pferdegeist]],
    { [[Euer Pferdegeschirr braucht eine ganz spezielle Form von Segnung durch einem geeigneten Geist. In Düsterbruch findet Ihr einen solchen Geist, den uralten Pferdegeist. Er ist im Besitz eines verdorbenen Treants namens Tendris Wucherborke. Richtet dieses verdorbene Biest, <name>.]], [[Doch wisset eines - Freiheit ist nicht alles. Ihr müsst den Geist erst beschwichtigen, wenn Ihr seinen Segen wollt. Dafür werdet Ihr ein spezielles Pferdefutter benötigen. Reist zuerst nach Süderstade und sprecht mit Merideth Carlson über diese Sache... und bereitet Euch auf weitere Opfer vor.]], },
    { [[Beschafft spezielles Pferdefutter, mit dem man ein Geisterpferd füttern kann. Merideth Carlson in Süderstade scheint dafür eine recht gute Quelle zu sein.]], [[Reist anschließend nach Düsterbruch in Feralas und tötet Tendris Wucherborke. Dies wird den uralten Pferdegeist befreien. Füttert den Geist zur Beruhigung mit dem speziellen Pferdefutter. Gebt dem Pferdegeist das Arkanitpferdegeschirr, um es segnen zu lassen.]], },
  },
  [7644] = {
    [[Gesegnetes Arkanitpferdegeschirr]],
    { [[Während Ihr dem uralten Pferdegeist das Pferdegeschirr anlegt, beginnt dieses langsam zu leuchten. Die Luft ist erfüllt von diesem heiligen Moment. Urplötzlich verschwindet das Pferdegeschirr und erscheint wieder in Eurem Rucksack.]], [[Es scheint so, als wäre das Pferdegeschirr wahrhaftig gesegnet. Entsprechend Lord Schattenbruchs Anweisungen müsst Ihr nun mit ihm nach Sturmwind zurückkehren.]], },
    { [[Kehrt mit dem gesegneten Arkanitpferdegeschirr zu Lord Grayson Schattenbruch in das Kathedralenviertel von Sturmwind zurück.]], },
  },
  [7645] = {
    [[Mit Manna angereichertes Pferdefutter]],
    { [[Die Silberne Hand hielt mich für VERRÜCKT, als ich erzählte, dass ich spezielles Pferdefutter für spirituelle Pferde herstellen könnte. ALLE dachten, ich sei verrückt! Nun, der Tag meiner Revanche ist gekommen!]], [[Ich werde herstellen, was Ihr braucht, PALADIN... sogenannter HELD... allerdings wird es Euch einiges kosten. Ich brauche für diese Mischung spezielle Kekse - angereicherte Mannakekse, die nur von der Argentumdämmerung verkauft werden. Außerdem fordere ich - ja, ich FORDERE - Gold als Entschädigung für mein erlittenes Elend!]], [[Bringt mir das Opfer, zu dem Ihr so oft aufruft, PALADIN.]], },
    { [[Treibt 20 angereicherte Mannakekse - die ausschlaggebende Zutat für die Herstellung von mit Manna angereichertem Pferdefutter - für Merideth Carlson aus Süderstade im Vorgebirge des Hügellands auf. Die Argentumdämmerung ist als alleiniger Händler dieser Kekse bekannt.]], [[Zusätzlich müsst Ihr ihre Gefühle noch mit 50 Goldstücken besänftigen.]], },
  },
  [7646] = {
    [[Das Orakel der Anrufung]],
    { [[Als Nächstes müssen wir ein Orakel der Anrufung erschaffen – damit wird Eure Prüfung beginnen, an deren Ende Ihr das gefallene Streitross erlösen werdet. Euer Orakel ist schon beinahe vollständig. Der von Euch im Schreckenstal eingesetzte Weihrauchschwenker wird als Katalysator für das Orakel dienen und ich werde Euch ein passendes Gehäuse für das Gerät anfertigen.]], [[Die letzten noch benötigten Komponenten sind zwei Diamanten. Sie müssen von reinster Qualität sein und gleichermaßen gut ihren Zweck erfüllen – ein azerothischer und ein schwarzer Diamant. Zusammen werden diese beiden das Leuchtfeuer sein, mit dessen Hilfe das Orakel die Schatten zerschneiden wird.]], },
    { [[Bringt Lord Grayson Schattenbruch im Kathedralenviertel von Sturmwind einen azerothischen Diamanten und einen makellosen schwarzen Diamanten.]], },
  },
  [7647] = {
    [[Gerechtigkeit und Erlösung]],
    { [[Nehmt diese Tasche – in ihr befinden sich alle Gegenstände, die Ihr für Eure Aufgabe brauchen werdet.]], [[Benutzt das Orakel der Anrufung im Herzen des Kellergewölbes des Großen Ossuariums, um die verfluchten Geister zu rufen, über deren Schicksal gerichtet werden muss. Benutzt Eure Richturteile während dieser Prüfung weise – sie werden weit wirksamer sein, als Ihr es Euch vorstellen könnt. Nach diesen Geistern wird der Todesritter erscheinen. Besiegt ihn und fordert die verlorene Seele des Streitrosses. Nur dann werdet Ihr in der Lage sein, es zu erlösen und Eurem neuen Reittier Euer Zaumzeug anlegen zu können!]], [[Viel Glück!]], },
    { [[Benutzt das Orakel der Anrufung im Herzen des Kellergewölbes des Großen Ossuariums in Scholomance. Damit ruft Ihr die verfluchten Geister, über deren Schicksal Ihr richten müsst. Durch das Besiegen der Geister wird der Todesritter Schattensichel beschworen. Besiegt ihn und holt Euch die verlorene Seele des gefallenen Streitrosses.]], [[Gebt die Seele des gefallenen Streitrosses und das gesegnete und verzauberte Pferdegeschirr Schattensichels gefallenem Streitross zurück.]], },
  },
  [7648] = {
    [[Grimands feinste Arbeit]],
    { [[<Grimand wischt sich Schweiß von seinen Brauen und strahlt stolz.>]], [[Da wären wir, <mein Freund/meine Freundin>. Dies ist eine meiner besten Arbeiten - so gut wie das Pferdegeschirr, das ich persönlich für Lord Schattenbruch gefertigt habe, als dieser sich für sein eigenes Streitross aufmachte. Ich danke Euch für Euer Vertrauen in mein Können.]], [[Grayson wird diese Arbeit zweifellos auch sehen wollen. Bringt Euer Pferdegeschirr zu ihm und richtet ihm meinen Dank für die ehrenvollen Geschäfte mit ihm aus.]], },
    { [[Bringt das Arkanitpferdegeschirr zur Begutachtung zu Lord Grayson Schattenbruch im Kathedralenviertel von Sturmwind.]], },
  },
  [7649] = {
