  [7667] = {
    [[Materielle Unterstützung]],
    { [[<name>, es gibt da eine dringliche Angelegenheit und ich hoffe, dass Ihr mir dabei helfen könnt... im materiellen und geistigen Sinne. Lasst mich bitte erklären.]], [[Tief in Scholomance in Lordaeron gibt es ein Gewölbe, das das Große Ossuarium genannt wird. Ich muss eine Vorrichtung fertigstellen, die die Schatten dieses entsetzlichen Raums durchbohren kann. Um zu beenden, womit ich begonnen habe, brauche ich zwei Diamanten: einen azerothischen und einen makellosen schwarzen Diamanten. Dies sind keine gewöhnlichen Objekte, ich weiß - aber ich verfüge nun einmal nicht selbst über die entsprechenden Mittel.]], [[Könntet Ihr mir jeweils einen von beiden beschaffen?]], },
    { [[Wenn Ihr Sagorne Gratläufer im Tal der Weisheit von Orgrimmar helfen wollt, so bringt ihm einen azerothischen und einen makellosen schwarzen Diamanten.]], },
  },
  [7668] = {
    [[Die Bedrohung durch Schattensichel]],
    { [[Der Todesritter Schattensichel herrscht über das Große Ossuarium in Scholomance. Er droht die spirituelle Ebene zu seinen eigenen Zwecken zu missbrauchen... Er muss gestoppt werden! Dabei kommt das Orakel der Anrufung, bei dessen Herstellung Ihr behilflich wart, zum Einsatz.]], [[Setzt das Orakel inmitten des Kellergewölbes des Großen Ossuariums ein, um Schattensichels Geistergefolge herbeizurufen. Besiegt die Geister und der Todesritter selbst wird erscheinen. <name>, bringt mir seinen Kopf! Lediglich seine Niederlage wird den Schaden, den er der spirituellen Ebene zugefügt hat, wieder umkehren können!]], },
    { [[Wendet das Orakel der Anrufung inmitten des Kellergewölbes des Großen Ossuariums in Scholomance an. Ihr werdet damit Geister herbeirufen, die es zu bekämpfen gilt. Nach Vernichtung dieser Geister wird der Todesritter Schattensichel auftauchen; besiegt ihn.]], [[Bringt Schattensichels Kopf zu Sagorne Gratläufer im Tal der Weisheit in Orgrimmar.]], },
  },
  [7669] = {
    [[Erneut im Großen Ossuarium]],
  },
  [7670] = {
    [[Lord Grayson Schattenbruch]],
    { [[<name>, der Zeitpunkt ist nun gekommen, an dem Ihr lernen werdet, was Ihr tun müsst, um etwas zu bekommen, auf das Ihr bestimmt schon lange gewartet habt... Euer eigenes Streitross. Sprecht mit Lord Grayson Schattenbruch, im Kathedralenviertel von Sturmwind. Er wird Euch nicht nur in die Geheimnisse des treuesten Begleiters eines Paladins einweihen, sondern er wird Euch auch durch die Prüfungen führen, die Ihr absolvieren werdet, um Euer eigenes Streitross zu erhalten.]], [[Ich wünsche Euch viel Glück mit dieser Prüfung. Für das Licht, <nobler Bruder/noble Schwester>!]], },
    { [[Sprecht mit Lord Grayson Schattenbruch im Kathedralenviertel von Sturmwind.]], },
  },
