  [7701] = {
    [[GESUCHT: Vorarbeiter Maltorius]],
    { [[Auf Anordnung seiner Eminenz, Aufseher Schlickfaust, des Agenten der Thoriumbruderschaft und gleichzeitig neu angeheuerten Kommandanten der Schlackengrubentruppe der Dunkeleisenzwerge des Kessels, soll Aufseher Maltorius getötet und sein Kopf als Beweis für die Tat erbracht werden.]], [[Trefft Euch mit dem Hauptmann des Ausgucks, Lolo Langhieb, wegen der Belohnung für diese schwierige Aufgabe.]], },
    { [[Tief in der Schlackengrube des Kessels in der Sengenden Schlucht werdet Ihr den verräterischen Dunkeleisenzwerg, Aufseher Maltorius, finden.]], [[Tötet den Aufseher Maltorius und nehmt seinen Kopf an Euch. Bringt diesen zum Hauptmann des Ausgucks Lolo Langhieb in der Sengenden Schlucht für eine Belohnung.]], },
  },
  [7703] = {
    [[Die offene Rechnung der Gordok]],
    { [[Nun Ihr de' König sein, vielleicht Ihr kriegen zurück wertvolles Artefakt der Gordok von gruseligen Elfen, der sich selbst nennen Prinz! König vor Euch hat versagt, so wie alle Könige seit Tortheldrin - de' gruselige Elf - es gestohlen hat, von damaligen Gordokkönig!]], [[Artefakt heißen Stulpen der Gordokmacht. Alte Zeit Geschichten sagen, nur de' wahre König de' Gordok kann sie zurückholen. Alte Zeit Geschichten sagen auch, de' gruselige Elf ist immer noch hier - Ihr finden es und es werden Eure mächtigen Stulpen, für immer! Yar, ich zeigen!]], },
    { [[Findet die Stulpen der Gordokmacht und bringt sie zu Hauptmann Krombruch in Düsterbruch.]], [[Krombruch zufolge sagen die "Alte Zeit Geschichten", dass Tortheldrin - ein "gruseliger" Elf, der sich selbst als Prinz bezeichnet- sie einem der Gordokkönige gestohlen hat.]], },
  },
  [7721] = {
    [[Treibstoff fürs Schrumpfen]],
    { [[Wenn ich gegen mein besseres Wissen und Gewissen handele und nun doch bleibe, um meinen Ingenieurladen an der Vergessenen Küste aufzumachen, muss ich mich dringend um diese nervigen Elementare kümmern. Verdammt sei der unkontrollierte Elementalismus, sage ich!]], [[Glücklicherweise stellen die Elementare eine ideale Quelle reiner Kraft für meinen Ultraschrumpfer dar – insofern, gebt ihnen Saures! Wenn Ihr dies allerdings tut, bringt mir doch bitte auch ein paar Elementarkerne.]], },
    { [[Sammelt 10 Wasserelementarkerne von den Meereselementaren und Meeresschauern entlang der Vergessenen Küste von Feralas und bringt sie zurück zu Zorbin von Schiller in der Nähe des dortigen Docks.]], },
  },
  [7722] = {
    [[Was zum Fluxus?!]],
    { [[Irgendwo in der Schlackengrube des Kessels findet Ihr die geheimen Pläne des Feurigen Fluxus, die der Aufseher Maltorius stahl. Findet diese Pläne und bringt sie zurück zu Meisterschmied Burninate in der Sengenden Schlucht.]], },
    { [[Irgendwo in der Schlackengrube des Kessels findet Ihr die geheimen Pläne des Feurigen Fluxus, die der Aufseher Maltorius stahl. Findet diese Pläne und bringt sie zurück zu Meisterschmied Burninate in der Sengenden Schlucht.]], },
  },
  [7723] = {
    [[Verdammt seien diese fetten Finger]],
    { [[Leider kann ich wegen dieses Fettfingerproblems meine Schläge nicht mehr so austeilen wie früher. Durch das Klima hier draußen und den fehlenden Luftdruck dehnen sich meine Finger aus. Die sehen aus wie riesige Thelsamarblutwürste, oder?]], [[Ihr werdet meine Befehle und all so'n Zeug ausführen müssen.]], [[Punkt Eins auf der Liste: Die schweren Kriegsgolems in der Schlucht windelweich prügeln. Legt los.]], },
    { [[Hansel Bleihand möchte, dass Ihr 20 Schwere Kriegsgolems tötet.]], [[Die Schweren Kriegsgolems wandern in der Gegend um den Kessel in der Sengenden Schlucht herum. Sobald Ihr genug getötet habt, kehrt zu Hansel zurück.]], },
  },
  [7724] = {
    [[Feurige Bedrohung!]],
    { [[Würd' ich's nicht besser wissen, würd' ich behaupten, dass jedes Mal, wenn der alte Hansel sich endlich 'mal ein wenig entspannen möchte, ihm die höheren Mächte einen weiteren Auftrag erteilen.]], [[Das macht mich echt nachdenklich, wisst Ihr? Ihr wisst doch was ich meine, oder?]], [[Die Lavaspinnen hier in der Gegend, haben in der letzten Zeit einen ganz schönen Krawall verursacht. Schlickfaust meint, dass sie Feuer spucken würden, und zwar aus ihren... Ähm, ja, jedenfalls beunruhigt so etwas die Kinder.]], [[Schätze wir werden sie erledigen müssen. Genau genommen wird das dein Job sein.]], },
    { [[Hansel Bleihand möchte, dass Ihr 20 große Lavaspinnen in der Sengenden Schlucht tötet.]], [[Die großen Lavaspinnen findet Ihr auf den Plateaus und in den Lavabecken der Sengenden Schlucht. Sobald Ihr genug getötet habt, kehrt zu Hansel zurück.]], },
  },
  [7725] = {
    [[Und wieder unter den geschrumpften Riesen]],
    { [[Ich weiß, dass Ihr schon Überreste für mich gesammelt habt... aber solltet Ihr auf der Suche nach einer zusätzlichen Aufgabe sein, könnte ich immer noch einige Überreste gebrauchen.]], [[Ich kann zwar nicht genauso viel als Belohnung bieten wie zuvor, aber dafür wäre ich mehr als glücklich ein gutes Wort über Euch unter meinen Goblinfreunden verlauten zu lassen. Unter denen zumindest, die über Ohren verfügen. .]], [[Glaubt mir, <Herr / Frau> wenn ich Euch sage, dass diese Goblins über wirklich ziemliche große Ohren verfügen.]], },
    { [[Benutzt Zorbins Ultraschrumpfer, um jegliche Art von Riesen in Feralas in eine handlichere Form zu schrumpfen. Bringt 10 Schrumpfüberreste, die auf den kleineren handlicheren Riesen liegen zu Zorbin von Schiller am Dock der Vergessenen Küste in Feralas.]], },
  },
  [7726] = {
    [[Neuer Treibstoff fürs Schrumpfen]],
    { [[Mehr Arbeit, sagt Ihr? Hat meine heroische Aufgabe des Schleimsammelns von den Elementaren nicht Euren Durst nach Abenteuern gelöscht? Vielleicht wird etwas mehr davon den unerschöpflichen Brunnen, der Ihr seid, füllen...]], [[Ok, Melodramatik beiseite, ich bräuchte noch ein paar Wasserelementarkerne. Ich versuche verzweifelt, einen Laden hier aufzumachen, also wird der Lohn leider nicht groß sein... aber ich verspreche, dass ich jedem in Hörweite erzählen werde, dass Ihr mir geholfen habt. Vertraut mir – Ich kenne eine Menge Leute im Kartell!]], },
    { [[Sammelt 6 Wasserelementarkerne von den Meereselementaren und Meeresschauern entlang der Vergessenen Küste von Feralas und bringt sie zurück zu Zorbin von Schiller in der Nähe des dortigen Docks.]], },
  },
  [7727] = {
    [[Incendosaurier? Was-auch-immer-Saurier?]],
    { [[Meine Zehen, <Kumpel/ Mädel>. Meine verdammten Zehen sind angeschwollen. Wie soll ich irgendetwas jagen können, wenn meine Füße mich schon umbringen? Darum brauche ich Eure Hilfe.]], [[Skrange und Burninate haben mich beauftragt, diese Dinger zur Strecke zu bringen, damit sie weitere Expeditionen in die Lavabecken der Schlackengrube schicken können. Anscheinend stehen einige Incendosaurier oder so was in der Art diesem Vorhaben im Weg... Wollt Ihr hier Wurzeln schlagen oder habt Ihr die Incendosaurier schon erledigt?]], },
    { [[Hansel Bleihand möchte, dass Ihr 20 Incendosaurier tötet.]], [[Die Incendosaurier haben sich in den Lavabecken der Schlackengrube eingenistet. Ihr findet die Schlackengrube im Kessel der Sengenden Schlucht.]], [[Sobald Ihr genug Incendosaurier getötet habt, kehrt zu Hansel Bleihand in der Sengenden Schlucht zurück.]], },
  },
  [7728] = {
    [[GESTOHLEN: Schmiedewindform und ein Ausguckhandfernrohr]],
    { [[Folgende Gegenstände wurden gestohlen:]], [[Eine Schmiedewindform.]], [[Ein Ausguckhandfernrohr.]], [[Es wurde eine Belohnung für das Zurückbringen der gestohlenen Gegenstände ausgesetzt.]], [[Die Thoriumbruderschaft ist sich sicher, dass diese Gegenstände von skrupellosen und neidischen Dunkeleisenzwergen aus der Sengenden Schlucht entwendet wurden.]], [[Das fragliche Fernrohr ist ein hervorragendes Werkzeug für Spähposten und die Schmiedeform ist schlicht unersetzlich.]], [[Solltet Ihr einen der vermissten Gegenstände finden, bringt sie zu Skrange.]], },
    { [[Sucht die Schmiedewindform und das Ausguckhandfernrohr. Bringt diese Gegenstände zu Zuchtmeister Skrange in der Sengenden Schlucht.]], [[Die einzigen Informationen die Ihr habt sind: Die Schmiedewindform ist ein Schmiedewerkzeug welches von Schmieden verwendet wird und das Ausguckhandfernrohr ist ein unverzichtbares Werkzeug für Spähposten.]], },
  },
  [7729] = {
    [[STELLE ZU VERGEBEN: Erledige die Konkurrenz]],
    { [[Zuchtmeister Skrange sucht einige fähige Männer und Frauen, um seiner Konkurrenz in der Sengenden Schlucht ein wenig einzuheizen.]], [[Interessierte Bewerber sollten zur Sengenden Schlucht reisen und jeden Zuchtmeister und Sklavenhändler der Dunkeleisenzwerge erschlagen, der ihnen über den Weg läuft.]], [[Meldet Euch bei Zuchtmeister Skrange, wenn Eure Kleidung ausreichend mit Blut getränkt wurde.]], [[Ihr werdet schon sehen, wann sie blutig genug ist, also spart Euch jegliche Rückfragen.]], },
    { [[Tötet 15 Zuchtmeister der Dunkeleisenzwerge und 15 Sklavenhändler der Dunkeleisenzwerge. Kehrt zu Zuchtmeister Skrange zurück, sobald Ihr die Aufgabe erfüllt habt.]], },
  },
  [7730] = {
    [[Zukk'ash-Plage]],
    { [[Schenken wir unseren angesammelten Erkundungen Glauben, so gehört der Gegner südlich der Gnolle einer insektenartigen Rasse an. Offen gesagt spielt es keine Rolle, solange sie lebendig sind. Wenn sie die Gnolle gegen uns aufhetzen, dann sind sie eine mindestens genauso große Bedrohung - wenn nicht sogar eine größere - als die Gnolle selbst.]], [[Dringt tief in das Gebiet vor, in dem die Insekten der Zukk'ash hausen; auch bekannt als Gewundene Tiefe. Dezimiert ihre Anzahl, berichtet mir von Euren Entdeckungen und bringt ihre Knochenpanzer als Beweis Eurer Taten zurück.]], },
    { [[Bringt Hadoken Flinkschreiter im Camp Mojache in Feralas 20 Zukk'ashknochenpanzer.]], },
  },
  [7731] = {
    [[Zornstachel]],
    { [[Einer unserer Späher berichtete, dass er während seiner Beobachtungen der Gewundenen Tiefe ein riesiges Insekt – viel größer als die anderen gesichteten Insekten – gesehen habe, das durch das verschlungene Gelände umherstreifte. Es ist nicht nur größer, es scheint auch eine Art Aura abzusondern, was die anderen Insekten nicht tun. Wir haben ihm einen Namen gegeben – Zornstachel.]], [[Eine Untersuchung der Innereien dieser Bestie wäre von unschätzbarem Wert, solange wir nach dem besten Weg suchen, um mit dieser Bedrohung fertigzuwerden. Schneidet seine Sekretionsdrüsen heraus und bringt sie sofort zu mir zurück!]], },
    { [[Besiegt Zornstachel in der Gewundenen Tiefe und bringt seine Drüsen zu Hadoken Flinkschreiter im Camp Mojache in Feralas zurück.]], },
  },
  [7732] = {
    [[Bericht über die Zukk'ash]],
    { [[Ihr habt mehr als richtig gehandelt, indem Ihr Euch der Bedrohung, die die Insekten der Zukk'ash für das Camp Mojache darstellen, zugewandt habt. Die Gefahr, die von ihnen ausgeht, ist noch lange nicht vorüber. Deshalb würde ich Euch im Namen der Horde gerne um die Erledigung eines letzten Auftrags bitten.]], [[Dies hier sind Aufzeichnungen, die alle Informationen, die wir über diese Zukk'ash erhalten haben, detailliert beinhalten. Ich möchte, dass Ihr sie zu Zilzibin Tomtom in der Gasse in Orgrimmar bringt. Er ist ein Experte, wenn es um fremde Kreaturen wie diese geht; ich hoffe, er kann etwas mit den von uns gewonnenen Erkenntnissen anfangen.]], },
    { [[Bringt den Bericht über die Zukk'ash aus Camp Mojache zu Zilzibin Tomtom. Er wohnt in der Gasse von Orgrimmar.]], },
  },
  [7733] = {
    [[Beste Qualität]],
    { [[Ob Ihr es glaubt oder nicht, ich habe eine noch viel bessere Quelle für Yetibälge entdeckt! Im nördlichen Teil von Feralas, tief in der Wutschrammfeste gibt es noch andere Yetis. Diese Wesen sind noch um einiges stärker, als jene mit denen Ihr schon zu tun hattet. Also, wenn Ihr eine größere Herausforderung sucht, sind diese Biester genau das Richtige für Euch.]], [[Ich werde Euch großzügig entlohnen, wenn Ihr mir 10 Wutschrammyetibälge bringt!]], },
    { [[Bringt Pratt McGrubben in der Mondfederfeste 10 Wutschrammyetibälge.]], },
  },
  [7734] = {
    [[Beste Qualität]],
    { [[Ob Ihr es glaubt oder nicht, ich habe eine noch viel bessere Quelle für Yetibälge entdeckt! Im nördlichen Teil von Feralas, tief in der Wutschrammfeste gibt es noch andere Yetis. Diese Wesen sind noch um einiges stärker, als jene mit denen Ihr schon zu tun hattet. Also, wenn Ihr eine größere Herausforderung sucht, sind diese Biester genau das Richtige für Euch.]], [[Ich werde Euch großzügig entlohnen, wenn Ihr mir 10 Wutschrammyetibälge bringt!]], },
    { [[Bringt Jangdor Flinkschreiter im Camp Mojache in Feralas 10 Wutschrammyetibälge.]], },
  },
  [7735] = {
    [[Makelloser Yetibalg]],
    { [[Ihr habt einen makellosen Yetibalg gefunden, der besonders dick und widerstandsfähig ist - sogar robuster als die Yetibälge aus der Wutschrammfeste.]], [[In Anbetracht der Tatsache, dass Pratt McGrubben in der Mondfederfeste mit Yetibälgen handelt, dürfte ihn dieser Fund besonders interessieren.]], },
    { [[Bringt Pratt McGrubben in der Mondfederfeste den makellosen Yetibalg.]], },
  },
