  },
  [7741] = {
    [[Lob des Smaragdkreises <NYI> <TXT>]],
  },
  [7761] = {
    [[Schwarzfausts Befehl]],
    { [[Schwachkopf,]], [[ich habe Euch immer für unfähig gehalten, Fremde daran zu hindern, den Hort unseres Herrn zu betreten. Für den sehr wahrscheinlichen Fall Eures Todes hat diese Kugel einen eingebauten Sicherheitsmechanismus, der verhindert, dass sich Außenstehende direkt in den Pechschwingenhort teleportieren können.]], [[Nur diejenigen, denen das Mal von Drakkisath in die Handfläche eingebrannt wurde, können die Kugel überhaupt benutzen. Glücklicherweise ist Drakkisath nicht einmal ansatzweise so inkompetent wie Ihr, Rüstmeister. Er selbst bewacht das Brandzeichen!]], [[Kriegshäuptling Rend Schwarzfaust]], [[P.S. Verbrennt diesen Brief, Idiot!]], },
    { [[Das war ja vielleicht mal ein dummer Orc. Es sieht so aus, als müsstet Ihr dieses Brandzeichen finden, um an das Mal von Drakkisath zu gelangen. Damit sollte sich die Befehlskugel aktivieren lassen.]], [[Dem Brief zufolge, wird das Brandzeichen von General Drakkisath bewacht. Vielleicht solltet Ihr diesem Hinweis nachgehen.]], },
  },
  [7781] = {
    [[Der Herrscher des Schwarzfels]],
    { [[Bringt König Varian Wrynn in Sturmwind den Kopf von Nefarian.]], },
    { [[Bringt König Varian Wrynn in Sturmwind den Kopf von Nefarian.]], },
  },
  [7782] = {
    [[Der Herrscher des Schwarzfels]],
    { [[Begebt Euch in das Tal der Helden und sprecht mit Feldmarschall Afrasiabi.]], },
    { [[Reist nach Sturmwind in das Tal der Helden und sprecht mit Feldmarschall Afrasiabi.]], },
  },
  [7783] = {
    [[Der Herrscher des Schwarzfels]],
    { [[Ihr spürt wie längst vergessene Gefühle in Euch aufflammen. Der Schmerz des Sieges hallt durch Euer Innerstes. Euch zu Füßen liegt der vom Kampf gezeichnete, gigantische Kopf von Nefarian. Bringt ihn Eurem Kriegshäuptling, großer Held!]], },
    { [[Bringt Garrosh in Orgrimmar den Kopf von Nefarian.]], },
  },
  [7784] = {
    [[Der Herrscher des Schwarzfels]],
    { [[Sprecht mit Oberanführer Runthak in Orgrimmar. Er wartet im Tal der Stärke auf Euch.]], },
    { [[Sprecht mit Oberanführer Runthak in Orgrimmar. Er wartet im Tal der Stärke auf Euch.]], },
  },
  [7785] = {
    [[Untersuchung des Gefäßes]],
    { [[Untersucht das Gefäß der Wiedergeburt, Sterblicher. Mit seiner Hilfe soll der Windsucher wieder auferstehen!]], },
    { [[Untersucht das Gefäß der Wiedergeburt und bringt es zu Hochlord Demitrian.]], },
  },
  [7786] = {
    [[Donneraan der Windsucher]],
    { [[Ich habe Euch alles gesagt was ich weiß, <name>. Es ist nun Eure Aufgabe Elementium zu finden. Ihr seid es, der die körperliche Form des Feuerfürsten zerstören muss.]], [[Gewinnt dadurch den Segen von Donneraan.]], },
    { [[Um Donneraan den Windsucher aus seiner Gefangenschaft zu befreien, müsst Ihr Hochlord Demitrian die rechten und linken Hälften der Fesseln des Windsuchers, 10 verzauberte Elementiumbarren und die Essenz des Feuerfürsten bringen.]], },
  },
  [7787] = {
    [[Donnerzorn erwache!]],
    { [[Ihr habt den Windsucher bezwungen. Nehmt die schlummernde Klinge und die Materialen, die Ihr Demitrian für seinen Verrat geliefert habt, und zwingt den verlogenen Schuft, Donnerzorn zu erwecken.]], },
    { [[Präsentiert Hochlord Demitrian die vom Wind berührte, schlummernde Klinge.]], },
  },
  [7788] = {
    [[Besiegt die Eindringlinge!]],
    { [[Jenseits dieses Tunnels werdet Ihr ein Feld des Kampfes und der Aufruhr finden, <junger/junge><class> <class>. Die Horde hört nicht auf, unseren heiligen Wald zu roden und benutzt unser immergrünes Blattwerk, um ihre Maschinen anzutreiben.]], [[Betretet die Kriegshymnenschlucht und verteidigt die Feste der Silberschwingen. Schlagt die eingefallenen Kräfte der Horde zurück.]], [[Ein Erfolg würde Euch ein Verdienstabzeichen einbringen. Bringt mir eines dieser Abzeichen, <name> und ich werde Euch belohnen.]], },
    { [[Betretet die Kriegshymnenschlucht und besiegt die Streitkräfte der Horde. Verdient Euch ein Ehrenabzeichen der Kriegshymnenschlucht und kehrt zur Schildwache Weltensang am Hain der Silberschwingen zurück.]], },
  },
  [7789] = {
    [[Bezwingt die Eindringlinge der Silberschwingen]],
    { [[Die Wildnis des Eschentals wird sich der Macht der Horde ergeben, <race>. Nichts, was die Silberschwingen tun oder sagen, wird uns daran hindern, unseren territorialen Herrschaftsanspruch durchzusetzen. Kalimdor gehört der Horde. Wie können sie es wagen, uns davon abzuhalten, das zu ernten, was rechtens unser ist!]], [[Lasst Eure Brust vor Stolz erbeben, während Ihr ihre schwachen Versuche, unseren Fortschritt zu behindern, zunichtemacht. Zerstört die Schildwachen der Silberschwingen und erlangt ein Ehrenabzeichen des Kriegshymnenklans. Kehrt zu mir zurück, <name>, wenn Ihr im Besitz eines Ehrenabzeichens seid, und ich werde Euch belohnen.]], },
    { [[Betretet die Kriegshymnenschlucht und besiegt die Streitkräfte der Allianz. Erwerbt ein Ehrenabzeichen der Kriegshymnenschlucht und kehrt anschließend zu Hauptmann Schädelbrecher im Stützpunkt Mor'shan zurück.]], },
  },
