  [7815] = {
    [[Schnappkiefer, Alter!]],
    { [[Lass dich ja nicht von ihren großen Augen täuschen, Alter! Die Schnappkiefer da sind ne Bedrohung. Ich kann nicht mal für fünf Minuten angeln, ohne dass einer von diesen überdimensionalen Fisch-Staubsaugern mir meinen Köder klaut.]], [[Tut mir und den Bruchhauern einen Gefallen und bringt sie um, Alter.]], [[Bringt sie alle um!]], },
    { [[Katoom, der Angler aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr 10 Salzwasserschnappkiefer tötet. Kehrt zu ihm zurück, sobald Ihr Eure Aufgabe erledigt habt.]], },
  },
  [7816] = {
    [[Gammerita, Alter!]],
    { [[Yo! Da is ne Schildkröte da draußen, die nicht wie die anderen is'. Das is' die Gammerita, Anführerin der Salzwasserschnappkiefer, 'ne richtig fiese Diebin!]], [[Alter, die hat mir meinen besten Köder geklaut. Einfach so abgebissen, hatse den! Seitdem beißen die Fisch nichmehr so an wie vorher.]], [[Finde Gammerita und hol' mir den Köder aus ihrem Bauch.]], },
    { [[Katoom, der Angler aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr Gammerita tötet und Katooms besten Köder aus ihrem Kadaver holt.]], [[Katoom meinte, dass Gammerita sich an der Küste bei den anderen Schildkröten aufhält.]], },
