  [7828] = {
    [[Die Pirsch auf die Pirscher]],
    { [[Viele mutige Trolle haben wir in der rauen Wildnis der Hinterlands verloren, <race>. Die Silbermähnenwölfe stellen eine der größten Bedrohungen für unsere Jäger dar. Sie lauern versteckt in den Büschen und schlagen zu, sobald sie eine Möglichkeit dazu sehen.]], [[Diese Art von Gewalt wird nicht länger geduldet!]], [[Ihr müsst wie die Silbermähnen denken. Lauert den Pirschern auf! Tötet sie alle!]], },
    { [[Jäger Markhor aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr 5 Silbermähnenpirscher und 5 Silbermähnenheuler tötet. Kehrt zu ihm zurück, sobald Ihr Eure Aufgabe erledigt habt.]], [[Markhor erwähnte, dass sich die Wölfe in der Wildnis des Hinterlands verstecken.]], },
  },
  [7829] = {
    [[Jagt die Wilden]],
    { [[Unter all den bösartigen Tieren der Wildnis des Hinterlandes, sind auch die Eulenbestien zu finden. Schon so manche gute Krieger der Bruchhauer verloren wir an die grausamen Krallen und die Schnäbel der Biester.]], [[Tötet jede wilde Eulenbestie, die Ihr finden könnt, <race>. Kehrt zu mir zurück, sobald Ihr von dem Blutbad genug habt. Euer Beitrag wird dann genau gewogen, abgemessen und bewertet.]], },
    { [[Jäger Markhor aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr 10 Wilde Eulenbestien tötet. Kehrt zu ihm zurück, sobald Ihr Eure Aufgabe erledigt habt.]], [[Markhor erwähnte, dass sich die Eulenbestien in der Wildnis des Hinterlands befinden.]], },
  },
  [7830] = {
    [[Die Gefallenen rächen]],
    { [[Dieser Abschaum namens Wildhammer trainiert Greifen, um Trolle abzuschlachten. Schrecklich, Ich weiß, aber sorgt Euch nicht denn ihnen wird bald Gerechtigkeit widerfahren.]], [[Für den Moment möchte ich, dass Ihr Euch auf die Ausrottung der Greifen in dieser Region konzentriert, die auch als Himmelsfürsten bekannt sind.]], [[Tötet einen und bringt mir seinen Federbusch!]], },
    { [[Jäger Markhor aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr einen Klingenschnabelhimmelsfürsten tötet und dessen Federbusch an Euch nehmt. Kehrt zu ihm zurück sobald Ihr diese Aufgabe erledigt habt.]], [[Die Klingenschnabelhimmelsfürsten sind eine recht seltene Spezies und nisten meist in der Wildnis des Hinterlands.]], },
  },
