  [7838] = {
    [[Großmeister der Arena]],
    { [[Avast Lubber!   Bring me 12 trinkets of the Arena Master and then ye shall be a Grandmaster of the Gurubashi Arena!   Arrrr!]], },
  },
  [7839] = {
    [[Schläger der Blutfratzen]],
    { [[Der Blutfratzenabschaum ist in unser Dorf eingedrungen und hat mein Werkzeug gestohlen! Ich habe zwar Ersatz, aber das Set, das sie mir geraubt haben, war das Geschenk eines gutherzigen Gnoms, der mir als junger Orc das Leben gerettet hat.]], [[Ich möchte dieses Werkzeug wieder haben, <name> - es bedeutet mir ungeheuer viel.]], [[Markhor berichtete, dass die Werkzeuge nahe der Tempel von Agol'watha und Shaol'watha gesichtet wurden.]], [[Diese Tempel liegen im Nordosten des Hinterlands.]], [[Findet meine Werkzeuge!]], },
    { [[Schmied Aschenbaum aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr seine verlorenen Werkzeuge ausfindig macht und dann zu ihm zurückkehrt.]], [[Ihr erinnert Euch an Schmied Aschenbaums Worte, dass die Werkzeuge möglicherweise bei einem der Tempel der Blutfratzen zu finden seien, im Nordosten des Hinterlands. Ihr solltet auch den Lauerfels absuchen.]], },
  },
  [7840] = {
    [[Lards vergessene Brotzeit]],
    { [[Lard Picknick gemacht hat, an geheimen Geheimplatz, als hässlicher, fieser Blutfratzentroll Lard mit großem Stock geschlagen hat. Lard zurück ins Dorf der Bruchhauer gerannt sein, aber Lard Brotzeit vergessen hat. Lard hungrig. Ihr vielleicht zurück gehen zu geheimen Geheimplatz und holen Brotzeit von Lard? Lard hat Angst. Gehen Norden! Insel dort im Norden seien geheimer Geheimplatz.]], },
    { [[Lard aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr Lards Brotzeit wieder findet. Kehrt nach Erfüllung dieser Aufgabe zu ihm zurück.]], [[Lard erwähnte, dass er es auf der Insel im Norden vergessen habe. Achtet auf die Trolle der Blutfratzen.]], },
  },
  [7841] = {
    [[Eine Botschaft an die Wildhämmer]],
    { [[Die Wildhämmer fangen Geschäfte mit unseren Feinden an, Mann, schmieden neue Bündnisse. Diesmal sind es die Hochelfen, Mann.]], [[<Otho spuckt.>]], [[Wir werden diesen Wildhammerzwergen wohl 'ne Botschaft überbringen müssen - 'ne Botschaft geschrieben mit dem Blut unserer Feinde. Tötet alle Hochtalelfen, alle die Euch über den Weg laufen, klar? Un' lasst ihre Leichen als Warnung zurück.]], [[Verscherzt es Euch niemals mit den Trollen!]], },
    { [[Otho Moji'ko aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr 5 Hochtalkundschafterinnen, 8 Hochtalspäher, 2 Hochtalschützen und 5 Hochtalwaldläuferinnen tötet. Kehrt nach vollendeten Taten zu ihm zurück.]], [[Ihr findet die Hochtalelfen bei der Jagdhütte von Quel'Danil im Nordwesten des Hinterlands.]], },
  },
  [7842] = {
    [[Eine weitere Botschaft an die Wildhämmer]],
    { [[Otho Moji'ko aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr ihm 1 lange elegante Feder von Greifen besorgt, die das Hinterland bewohnen. Kehrt mit der Feder zu ihm zurück.]], [[Es ist bekannt, dass Greifen überall im Hinterland zu finden sind.]], },
    { [[Otho Moji'ko aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr ihm 1 lange elegante Feder von Greifen besorgt, die das Hinterland bewohnen. Kehrt mit der Feder zu ihm zurück.]], [[Es ist bekannt, dass Greifen überall im Hinterland zu finden sind.]], },
  },
  [7843] = {
    [[Die letzte Botschaft an die Wildhämmer]],
    { [[<Otho überreicht Euch einen langen, blutverschmierten Speer, den mehrere Greifenfedern umhüllen.>]], [[Nehmt den hier, Mann. Geht damit zum Nistgipfel, zum toten Stadtzentrum beim Brunnen. Platziert ihn dort! Rammt ihn richtig in den Boden und zeigt diesen Zwergen, dass die Trolle es ernst meinen! Kommt danach zu mir zurück... hoffentlich noch in einem Stück.]], },
    { [[Otho Moji'ko aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr zum Nistgipfel im Westen des Hinterlands reist und die letzte Botschaft an die Wildhämmer beim Brunnen im Stadtzentrum platziert. Kehrt nach Vollendung der Aufgabe zu ihm zurück.]], [[Hütet Euch vor zornigen Wildhammerzwergen und niederträchtigen Mitgliedern der Allianz.]], },
  },
  [7844] = {
    [[Kannibalistische Vettern]],
    { [[Drei verschiedene Trollstämme bewohnen das Hinterland, <name>. So viel zu den Gemeinsamkeiten der Stämme: Wir sind alle Trolle und wir leben im Hinterland.]], [[Die Blutfratzen sind eine verkommene Bande, die sich an anderen Trollen und Humanoiden nährt.]], [[Wäre Zul'jin hier, hätte er sie alle mit einem einzigen Faustschlag niedergeschmettert - ach, Zul'jin ist noch immer nicht zurückgekehrt und wir sind uns selbst überlassen.]], [[Es liegt nun an Euch diese Gegend zu säubern!]], },
    { [[Mystikerin Yayo'jin aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr 6 Skalpierer der Blutfratzen und 2 Sterndeuter der Blutfratzen tötet. Kehrt nach Vollendung dieser Aufgabe zu ihr zurück.]], [[Yayo'jin deutete an, dass man diese Trolle nahe der Tempel von Shaol'watha und Agol'watha finden könnte. Sie liegen im Nordosten des Hinterlands.]], },
  },
  [7845] = {
    [[Die Entführung des Ältesten Stumpfhauer]],
    { [[Mein Gefährte wurde von den wilden Blutfratzen bei einem dreisten Überfall um Mitternacht verschleppt. Unsere Spione haben mir berichtet, dass er noch am Leben ist und hoch oben in Jintha'Alor gefangen gehalten wird. Man sagt, dass die üble Priesterin Hexx ihn als Opfer für den gesichtslosen Blutgott Hakkar vorbereiten lässt.]], [[Ihr müsst ihn retten, <name>! Unser Dorf könnte einen so großen Verlust nicht verschmerzen.]], },
    { [[Ura Stumpfhauer aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr ihren Gefährten, den Ältesten Stumpfhauer, aus Jintha'Alor befreit.]], [[Man sagte Euch, dass er hoch oben in Jintha'Alor gefangen gehalten wird - der Hauptstadt der Blutfratzen im Süden des Hinterlands.]], },
  },
  [7846] = {
    [[Erlangt den Schlüssel!]],
    { [[Der Schlüssel zu meinen Fesseln ist im Besitz von Hitah'ya der Bewahrerin. Sie hält sich in der nahegelegenen Höhle auf. Seid vorsichtig, <class>, sie wird äußerst gut bewacht.]], [[Tötet sie und bringt dann den Schlüssel zurück, um meine Fesseln zu öffnen. Erst dann werde ich frei und in der Lage sein, nach Hause zurückzukehren.]], },
    { [[Der Älteste Stumpfhauer in Jintha'Alor möchte, dass Ihr Hitah'ya die Bewahrerin tötet, um in den Besitz des Fesselnschlüssels zu kommen. Kehrt zum Ältesten Stumpfhauer zurück, wenn Ihr den Schlüssel habt.]], },
  },
  [7847] = {
    [[Rückkehr zu Ura Stumpfhauer]],
    { [[Kehrt zu meiner Gefährtin Ura Stumpfhauer ins Dorf der Bruchhauer zurück. Richtet ihr aus, dass ich heimkehren werde! Sie wird Euch sicherlich belohnen.]], },
    { [[Der Älteste Stumpfhauer in Jintha'Alor möchte, dass Ihr Ura Stumpfhauer im Dorf der Bruchhauer die Neuigkeit von seiner Befreiung überbringt.]], },
  },
  [7848] = {
    [[Abstimmung mit dem Kern]],
    { [[Risse und Abgründe bilden sich um uns herum und kollabieren wieder, <race>. Keine zwei Schritte von mir entfernt, ist ein Riss, der hinab in die tiefsten Tiefen des Schwarzfels führt, bis in die Ruhestätte des Feuerfürsten.]], [[Überrascht? Wie bedauerlich... Ihr sterblichen Völker versteht einfach nichts, was Ihr nicht sehen, anfassen oder fühlen könnt.]], [[Ich versichere Euch, das Portal ist da und es ist passierbar.]], [[Habe ich Euer Interesse geweckt? Eine Anpassung ist simpel. Begebt Euch in die Schwarzfelstiefen und findet ein Kernfragment. Bringt es zu mir zurück und ich werde Eure Essenz mit dem Portal abstimmen.]], },
    { [[Begebt Euch zum Portal in den Schwarzfelstiefen, das in den Geschmolzenen Kern führt, und findet ein Kernfragment. Kehrt mit dem Fragment zu Lothos Felsspalter im Schwarzfels zurück.]], },
  },
  [7849] = {
    [[Die getrennten Brüder]],
    { [[Wie viele in unserem Dorf, habe auch ich einen Nahestehenden verloren. Ach, es war mein Zwillingsbruder Malkhor - er wurde beim selben Überfall wie Stumpfhauers Gefährte von den Blutfratzen verschleppt.]], [[Das Schicksal sollte ihn traurigerweise nicht vom Tod verschonen und er wurde schnell und brutal hingerichtet. Seine Überreste wurden an die Schlamme und Wölfe der Blutfratzen verfüttert.]], [[Wir können zwar nicht mehr sein Leben retten, aber es ist noch nicht zu spät, um seine Seele zu befreien. Bergt seine Überreste, <name>.]], },
    { [[Jäger Markhor aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr die Überreste seines Zwillingsbruders bergt. Findet den Schädel und die Knochen des Jägers Malkhor und bringt sie zu Jäger Markhor zurück.]], [[Markhor erwähnte, dass die Überreste seines Bruders an die Schlamme und Wölfe der Blutfratzen verfüttert wurden. Er sagte weiterhin, dass es in Jintha'Alor eine Grube mit Schlammen gibt sowie einen Wolfsbau. Beide liegen in der Nähe der Tempelspitze.]], },
  },
  [7850] = {
    [[Dunkle Gefäße]],
    { [[Die Blutfratzen kämpfen mit übernatürlicher Wildheit. Dies liegt an der elenden Magie, die von der üblen Priesterin Hexx gewirkt wird.]], [[Ihr werdet in ganz Jintha'Alor dunkle Gefäße mit besudeltem Blut finden. Die Gefäße verstrahlen die elende Magie des gesichtslosen Blutgottes, verleihen den Blutfratzen Macht und treiben sie zusätzlich in den Wahnsinn.]], [[Stehlt diese Gefäße und bringt sie zu mir, damit ich sie von der Besudelung reinigen und letztendlich die Gewalt des Blutgottes zerschlagen kann.]], },
    { [[Ura Stumpfhauer aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr Euch 5 Gefäße mit besudeltem Blut aus Jintha'Alor beschafft. Kehrt dann damit zu Ura Stumpfhauer zurück.]], },
  },
  [7861] = {
    [[GESUCHT: Üble Priesterin Hexx und ihre Diener]],
    { [[Der üblen Priesterin Hexx und ihren Dienern soll im Namen von Ura Stumpfhauer Gerechtigkeit widerfahren!]], [[Wer auch immer die üble Priesterin Hexx und alle ihre Aman'zasiwachen vernichten möge, soll eine bedeutende Entlohnung erhalten.]], },
    { [[Ihr habt die Anweisung erhalten die üble Priesterin Hexx und 5 Aman'zasiwachen der Blutfratzen zu töten. Habt Ihr Eure Aufgabe erfüllt, so sprecht mit Ura Stumpfhauer aus dem Dorf der Bruchhauer im Hinterland.]], [[Die üble Priesterin Hexx und die Aman'zasiwachen halten sich hoch oben in Jintha'Alor im Hinterland auf.]], },
  },
  [7862] = {
    [[Offener Posten: Wachbefehlshaber der Bruchhauer]],
    { [[Mit dem Verlust von Malkhor ist auch wieder der Posten des Wachbefehlshabers der Bruchhauer unbesetzt. Sollte es Euch interessieren Befehlshaber der Bruchhauerwachen zu werden, so erfüllt die folgenden Aufgaben und reicht Eure Bewerbung bei Ura Stumpfhauer ein:]], [[Rottet die Berserker der Blutfratzen aus.]], [[Rottet die Schattenjäger der Blutfratzen aus.]], [[Rottet die Bluttrinker der Blutfratzen aus.]], [[Rottet die Seelenfresser der Blutfratzen aus.]], },
    { [[Ihr wurdet mit der Vernichtung von 5 Berserkern der Blutfratzen, 3 Schattenjägern der Blutfratzen, 3 Bluttrinkern der Blutfratzen und 2 Seelenfressern der Blutfratzen beauftragt.]], [[Kehrt nach Beendigung dieser Aufgabe zu Ura Stumpfhauer ins Dorf der Bruchhauer im Hinterland zurück.]], },
  },
