  [7905] = {
    [[Der Dunkelmond-Jahrmarkt]],
    { [[Der Dunkelmond-Jahrmarkt kommt! Nehmt an unseren aufregenden Spielen teil, gewinnt fabulöse Preise und erfahrt, wie es um Eure Zukunft steht!]], [[Zögert nicht und Ihr werdet diesen Gutschein gegen kostenfreie Gewinnlose und einen der weltberühmten Abenteuerführer des Dunkelmond-Jahrmarkts einlösen können! Mit ihm werdet Ihr die Schätze, die Ihr auf Euren Reisen findet, leicht identifizieren können.]], [[Bringt Euren Gutschein einfach zu Gelvas Rußgatter, der sich innerhalb des Jahrmarkts aufhält.]], [[Momentan befindet sich der Zugang zum Jahrmarkt außerhalb von Goldhain im Wald von Elwynn.]], },
    { [[Bringt den Gutschein zu Gelvas Rußgatter, der sich innerhalb des Dunkelmond-Jahrmarkts aufhält.]], },
  },
