  [8114] = {
    [[Kontrolliert vier Stützpunkte]],
    { [[<name>, Ihr habt unseren Feinden im Arathibecken den Krieg gebracht und auf dem Schlachtfeld große Ehre erlangt. Ihr habt bewiesen, wie wertvoll Ihr als <class> für uns seid, und unseren Feinden gezeigt, dass wir uns ihren Streitkräften gegenüber nicht beugen werden!]], [[Da Ihr Euren Wert unter Beweis gestellt habt, habe ich einen besonders schwierige Auftrag für Euch. Ich möchte, dass Ihr mindestens vier der Stützpunkte im Arathibecken besetzt und haltet. Wenn Euch das gelingt, sichern wir uns nicht nur kritische Ressourcen, sondern zeigen der Horde auch unsere Überlegenheit!]], [[Geht nun, <name>, und viel Glück!]], },
    { [[Betretet das Arathibecken, erobert und kontrolliert gleichzeitig vier Stützpunkte im Arathibecken und kehrt danach zu Feldmarschall Lichtmark in der Zuflucht zurück.]], },
  },
  [8115] = {
    [[Kontrolliert fünf Stützpunkte]],
    { [[<name>, es ist ein blutiges Unterfangen um die Ressourcen von Arathor zu kämpfen. Eure Dienste für den Bund von Arathor werden genauso dringend gebraucht, wie sie honoriert werden.]], [[Wir haben eine weitere Aufgabe für Euch.]], [[Der Bund möchte einen Schlag gegen die Okkupation der Entweihten im Arathibecken vollbringen. Wir möchten sie von jedem strategischen Punkt des Schlachtfelds vertreiben!]], [[Es wird nicht leicht werden, aber wenn es überhaupt jemand schaffen kann, dann Ihr.]], },
    { [[Kontrolliert gleichzeitig fünf Stützpunkte im Arathibecken und kehrt danach zu Feldmarschall Lichtmark in der Zuflucht zurück.]], },
  },
