  [8120] = {
    [[Die Schlacht um das Arathibecken!]],
    { [[Wie Ihr sicherlich schon gehört habt, entsendet der Bund von Arathor seine Truppen ins Arathibecken. Versessen auf Nahrung und Vorräte, können diese Idioten der Allianz es kaum erwarten, die riesigen Ressourcen des Beckens zu kontrollieren. Wir müssen ihnen zeigen, dass Arathi niemals wieder das Zuhause der Menschen sein wird!]], [[Geht ins Arathibecken und greift die Mine, das Sägewerk, die Schmiede und die Ställe an. Reißt die feindlichen Banner herunter und besetzt das Gebiet für die Horde.]], [[Geht, <name>. Kehrt zu mir zurück, sobald Ihr Eure Aufgabe erfüllt habt.]], },
    { [[Erobert die Mine, das Sägewerk, die Schmiede, die Farm und die Ställe und kehrt dann zu Todesmeister Dunkels in Hammerfall zurück.]], },
  },
  [8121] = {
    [[Erobert vier Stützpunkte]],
    { [[<name>, während die Schlachten über das Arathibecken wüten, haben sich die Neuigkeiten über Eure Taten wie ein Lauffeuer verbreitet. Wir schätzen Euch hier sehr und daher hoffe ich, dass Ihr die Mission erfüllen könnt, die ich Euch nun gebe.]], [[Es ist Zeit dem Bund von Arathor eine Nachricht zu senden. Wir müssen ihnen zeigen, dass ihre Stellungen im Arathibecken leicht angreifbar und schnell verloren sind. Das werden wir ihnen beweisen, indem wir gleichzeitig vier strategische Punkte im Arathibecken erobern und kontrollieren.]], [[Seid erfolgreich, <name> und erntet mehr Respekt unter den Entweihten.]], },
    { [[Haltet vier Stützpunkte zur selben Zeit im Arathibecken und kehrt danach zu Todesmeister Dunkels nach Hammerfall zurück.]], },
  },
  [8122] = {
    [[Erobert fünf Stützpunkte]],
    { [[Es ist schwer an Informationen zu gelangen, jetzt da wir so tief in diesem Konflikt stecken. Doch unsere Spione berichten, dass die Entschlossenheit des Bundes von Arathor schwindet. Wir sollten diesen Moment ausnutzen und eine große Offensive starten! Kehrt in das Arathibecken zurück und werft Euch unserem Feind entgegen. Verflucht und vernichtet sie, bis Ihr fünf strategische Punkte kontrolliert! Ihre Soldaten sollen blutend und schreiend in die Flucht geschlagen werden und ganz Azeroth mit zitternder Stimme von Eurer Grausamkeit berichten.]], [[Geht jetzt und erringt einen großen Sieg für die Entweihten.]], },
    { [[Haltet fünf Stützpunkte zur selben Zeit im Arathibecken und kehrt danach zu Todesmeister Dunkels nach Hammerfall zurück.]], },
  },
  [8123] = {
    [[Zerschlagt die Versorgungslinien Arathors]],
    { [[Der Bund von Arathor scheut keine Mühen, um Arathi sein Eigen zu nennen. Sie und all die anderen Völker der Allianz strömen nun in diese entfernte Region, versessen darauf, ihren Gebietsanspruch von der Zuflucht aus zu erweitern. Wir müssen sie aufhalten, und das beste Mittel dies zu tun, ist ihre Vorräte zu stehlen.]], [[Ihr könnt uns helfen, <name>. Betretet das Arathibecken, das für seine reichen Minen, sein fruchtbares Land und seine geschickten Handwerker bekannt ist, und erobert jeden Stützpunkt, den Ihr könnt. Entscheidet die Schlacht auf diese Weise für Euch und kehrt dann mit den Ressourcen, die Ihr erbeuten konntet, zu mir zurück.]], },
    { [[Entscheidet die Schlacht um das Arathibecken für Euch, erbeutet eine Arathivorratskiste und bringt sie Todespirscher Mortis in Hammerfall.]], },
  },
