  [8149] = {
    [[Die Ehrung eines Helden]],
    { [[Während des Erntedankfests in Azeroth danken wir unseren Helden für all die Opfer, die sie in unserem Namen dargebracht haben. Für einige war es gar das höchste aller Opfer, ihr Leben. Die Allianz kennt viele Helden, doch keine Heldensage und kein Lebensweg ist so tragisch, wie der von Uther Lichtbringer.]], [[Helft uns Uther zu ehren, indem Ihr diese Opfergabe zu seinem Grabmal in den Westlichen Pestländern bringt. Legt sie an seiner Statue nieder und kehrt zu mir zurück. Lasst Euch nicht zu viel Zeit, denn ich bin nur bis zum Ende des Erntedankfests hier.]], },
    { [[Reist zu Uthers Grabmal in den Westlichen Pestländern und benutzt Uthers Tribut an seiner Statue. Kehrt zu Wagner Hammerschlag außerhalb von Eisenschmiede zurück, bevor das Erntedankfest endet.]], },
  },
  [8150] = {
    [[Die Ehrung eines Helden]],
    { [[Während des Erntedankfests in Azeroth danken wir unseren Helden für all die Opfer, die sie in unserem Namen dargebracht haben. Für einige war es gar das höchste aller Opfer, ihr Leben. Die Horde kennt viele Helden, doch niemand verkörpert Stärke und Opferbereitschaft so sehr, wie Grom Höllschrei.]], [[Helft uns Grom zu ehren, indem Ihr diese Opfergabe zu seinem Monument im Dämonensturz im Eschental bringt. Legt sie an seinem Monument nieder und kehrt zu mir zurück. Lasst Euch nicht zu viel Zeit, denn ich bin nur bis zum Ende des Erntedankfests hier.]], },
    { [[Reist zu Groms Monument im Dämonensturz im Eschental und benutzt Groms Tribut am Fuße seines Monuments. Kehrt zu Javnir Nashak außerhalb von Orgrimmar zurück, bevor das Erntedankfest endet.]], },
  },
  [8151] = {
    [[Der Talisman des Jägers]],
    { [[Ogtinc von Azshara sucht nach erfahrenen Jägern, genau wie Ihr es seid. Er ist ein Furbolg-Jäger, der seinen Stamm verlassen hat, um dem Zirkel des Cenarius zu helfen. Seine Lehren sollen das Wissen anderer, die sein Geschick teilen, bereichern und ihnen dabei helfen, das Gleichgewicht zu bewahren. Ihr würdet gut daran tun, mit ihm zu sprechen.]], [[Ogtinc hält sich auf den Klippen von Azshara, nordöstlich der Ruinen von Eldarath, auf.]], },
    { [[Sprecht mit Ogtinc in Azshara.]], },
  },
  [8153] = {
    [[Gehörnte Renner]],
    { [[Die Renner tragen die gewaltigsten Geweihe die es in der bekannten Welt zu finden gibt. Solche Geweihe nutzt der Renner, um sich selbst zu verteidigen, indem er Feinde damit aufspießt. Meistens brechen sie dadurch die Spitzen der Geweihe ab. Perfekte, natürliche Geweihe sind das Zeichen für ein extrem selbstsicheres Tier.]], [[Ein solches Geweih zu erlangen, würde auch in Euch dasselbe Selbstvertrauen fließen lassen.]], [[Geht auf die Jagd nach Mooshufrennern, findet die perfekten Geweihe und bringt sie mir.]], },
    { [[Bringt ein Paar perfekte Rennergeweihe zu Ogtinc nach Azshara. Er befindet sich auf den Klippen nordöstlich der Ruinen von Eldarath.]], },
  },
  [8154] = {
    [[Die Ressourcen des Arathibeckens]],
    { [[Eine Schlacht muss mit Soldaten gewonnen werden, das wird Euch jeder Soldat gerne bestätigen. Man sagt auch, dass eine Schlacht auf leeren Magen und mit schlechter Ausrüstung bereits eine verlorene Schlacht ist!]], [[Deshalb ist das Arathibecken so wichtig. Es gibt dort Schlüsselregionen, die essentielle Ressourcen bergen. Metalle, Waffen, Holz, ja, alles wird gebraucht und kann dort gefunden werden.]], [[Daher brauche ich Euch, <name>. Begebt Euch ins Arathibecken und entscheidet die Schlacht für Euch, indem Ihr mehr Stützpunkte haltet als der Feind. Bringt mir als Beweis Eurer Taten eine Vorratskiste.]], },
    { [[Gewinnt die Schlacht um das Arathibecken, raubt eine Arathivorratskiste und bringt sie Unteroffizier Maclear in der Zuflucht.]], },
  },
