  [8170] = {
    [[Die Schlacht um das Arathibecken!]],
    { [[Wie Ihr sicherlich schon gehört habt, entsendet der Bund von Arathor seine Truppen ins Arathibecken. Versessen auf Nahrung und Vorräte, können diese Idioten der Allianz es kaum erwarten, die riesigen Ressourcen des Beckens zu kontrollieren. Wir müssen ihnen zeigen, dass Arathi niemals wieder das Zuhause der Menschen sein wird!]], [[Geht ins Arathibecken und greift die Mine, das Sägewerk, die Schmiede und die Ställe an. Reißt die feindlichen Banner herunter und besetzt das Gebiet für die Horde.]], [[Geht, <name>. Kehrt zu mir zurück, sobald Ihr Eure Aufgabe erfüllt habt.]], },
    { [[Erobert die Mine, das Sägewerk, die Schmiede, die Farm und die Ställe und kehrt dann zu Todesmeister Dunkels in Hammerfall zurück.]], },
  },
  [8171] = {
    [[Die Schlacht um das Arathibecken!]],
    { [[Wie Ihr sicherlich schon gehört habt, entsendet der Bund von Arathor seine Truppen ins Arathibecken. Versessen auf Nahrung und Vorräte, können diese Idioten der Allianz es kaum erwarten, die riesigen Ressourcen des Beckens zu kontrollieren. Wir müssen ihnen zeigen, dass Arathi niemals wieder das Zuhause der Menschen sein wird!]], [[Geht ins Arathibecken und greift die Mine, das Sägewerk, die Schmiede und die Ställe an. Reißt die feindlichen Banner herunter und besetzt das Gebiet für die Horde.]], [[Geht, <name>. Kehrt zu mir zurück, sobald Ihr Eure Aufgabe erfüllt habt.]], },
    { [[Erobert die Mine, das Sägewerk, die Schmiede, die Farm und die Ställe und kehrt dann zu Todesmeister Dunkels in Hammerfall zurück.]], },
  },
  [8181] = {
    [[Konfrontiert Yeh'kinya]],
    { [[Meine eingehende Beschäftigung mit den Schrifttafeln von Mosh'aru bestätigt den Verdacht auf schlechte Neuigkeiten, <name>. Hakkars wahre Gestalt, der Gott selbst, kann mit genau dem Ei wiedergeboren werden, in dem Ihr ihn gefangen habt. Yeh'kinya sagte Euch, dass es seiner Macht Einhalt gebiete, aber eigentlich dient es seiner Wiederherstellung! Der Troll hat Euch in die Irre geführt!]], [[Geht zu Yeh'kinya und verlangt das Ei von ihm. Betet, dass wir Hakkars Rückkehr noch rechtzeitig verhindern können!]], },
    { [[Sprecht mit Yeh'kinya.]], },
  },
  [8182] = {
    [[Die Hand von Rastakhan]],
    { [[<name>, unsere schlimmsten Befürchtungen haben sich erfüllt. Laut der Legende herrschte der Seelenschinder in der alten Welt der Trolle mit uneingeschränkter Macht und ließ das Böse und Terror walten. Wenn er jetzt nicht aufgehalten werden kann, dann könnte sich seine neue Schreckensherrschaft in ganz Azeroth verbreiten!]], [[Es gibt eine Gesandtschaft von Zandalaritrollen, die uns beim Eindämmen dieser finsteren Flut behilflich sein könnte. Sie kampieren auf der Insel Yojamba, fern der Küste des Schlingendorntals, westlich der Ruinen von Zul'Kunda. Sprecht mit ihrem Anführer Molthor und betet dafür, dass er weiß, wie man Hakkar bezwingen kann.]], },
    { [[Sprecht mit Molthor im Schlingendorntal.]], },
  },
  [8183] = {
    [[Das Herz von Hakkar]],
    { [[Das Herz von Hakkar brennt noch immer im Inneren. Obwohl der Seelenschinder besiegt ist, bleibt das Wesen seiner Macht doch noch in dem verfluchten Organ enthalten.]], },
    { [[Bringt das Herz von Hakkar zu Molthor auf die Insel Yojamba.]], },
  },
  [8184] = {
    [[Präsenz der Macht]],
    { [[Zanza can create the ancient troll enchantments with the right components.]], [[The first is a primal Hakkari idol that must be taken from the most powerful of the Hakkari lieutenants: Jin'do and the Bloodlord.]], [[The second is a punctured voodoo doll. These can be found in the piles of refuse that litter]], [[this foul place. Look inside the jinxed hoodoo. Bring me these things and I will create a powerful enchantment for you!]], },
