  },
  [8227] = {
    [[Nats Maßband]],
    { [[Inmitten von zerbrochenen Angelruten und anderen Geräten, liegt ein zusammengerolltes, seidenes Maßband. An dem Band hängt ein Schild mit der Aufschrift:]], [["Nat Pagle, die Düstermarschen."]], },
    { [[Bringt Nats Maßband zu Nat Pagle in den Düstermarschen zurück.]], },
  },
  [8228] = {
    [[Könnte ich einen Flugzettel für den Angelwettbewerb bekommen?]],
