  [8275] = {
    [[Die Rückeroberung von Silithus]],
    { [[Ihr Helden der Allianz, horcht auf! Die Bedrohung durch die Silithiden blieb zu lange unbeachtet. Ohne einen wirklichen Gegner, der sie bekämpft, haben sie sich die ganze Zeit über auf eine großangelegte Invasion vorbereitet.]], [[Der Zirkel des Cenarius sammelt seine Streitkräfte in Silithus, um diesen verachtungswürdigen Kreaturen den Krieg zu erklären. Alle kampftauglichen Abenteurer werden hiermit aufgerufen, sich uns anzuschließen!]], [[Wartet nicht, bis es zu spät ist! Helft uns bei der Verteidigung von Azeroth! Meldet Euch für genauere Informationen bei Windruferin Prachthorn auf Burg Cenarius.]], },
    { [[Sprecht mit Windruferin Prachthorn auf Burg Cenarius in Silithus.]], },
  },
  [8276] = {
    [[Die Rückeroberung von Silithus]],
    { [[Ihr Helden der Horde, horcht auf! Es gibt eine Bedrohung dort draußen, die wir zu lange ignoriert haben. Erderschütterungen in der Nähe von Silithidennestern, verursacht durch massive Wanderungen dieser Biester, haben unsere Aufmerksamkeit erregt. Es scheintn als bereiten sie ihre weitere Expansion vor.]], [[Der Zirkel des Cenarius sammelt seine Streitkräfte in Silithus, um diesen Silithiden den Krieg zu erklären, bevor es zu spät ist. Niemals wieder sollen diese Biester unsere Ländereien entweihen!]], [[Alle kampftauglichen Abenteurer sollten sich umgehend bei Windruferin Prachthorn auf Burg Cenarius melden. Mögen die Silithiden endgültig besiegt werden!]], },
    { [[Sprecht mit Windruferin Prachthorn auf Burg Cenarius in Silithus.]], },
  },
  [8277] = {
    [[Tödliches Wüstengift]],
    { [[Beetix Wankelhupf ist Euch zu Diensten! Mein Cousin Noggle hat es sich zum Lebensinhalt gemacht, seltene Kräuter für die Alchemisten der Burg Cenarius zu sammeln. Oder hatte es zumindest, egal. Er kam von seinem letzten Ausflug mit einer starken Vergiftung zurück.]], [[Ich habe es geschafft die Wirkung des Gifts zu verlangsamen, aber um es vollständig neutralisieren zu können, benötige ich Giftproben von was auch immer ihn gestochen haben mag.]], [[Noggle kam aus dem Norden, daher verwette ich mein letztes Hemd darauf, dass es Steinpanzerskorpide oder Sandhuscher waren. Bringt mir ein paar Stachel und Giftzähne und ich werde versuchen ein Gegengift für ihn zu erstellen.]], },
    { [[Beetix Wankelhupf von der Burg Cenarius in Silithus möchte, dass Ihr 8 Stachel von Steinpanzerskorpiden und 8 Giftzähne von Sandhuschern für sie sammelt. Kehrt zu ihr zurück, sobald Ihr diese Aufgabe erfüllt habt.]], },
  },
  [8278] = {
    [[Noggles letzte Hoffnung]],
    { [[Verflucht! Das Gegengift hat nicht gewirkt. Noggle muss von einer der stärkeren Kreaturen im fernen Süden gestochen worden sein.]], [[Ich werde Euch brauchen, um dorthin zu gehen und mir noch mehr Proben zu besorgen, <name>. Das Gute dabei - da ihr Gift stärker ist, werde ich nicht so viele brauchen. Der Haken ist allerdings, dass in der südlichen Gegend mehrere Arten giftiger Kreaturen herumfleuchen und Ihr daher verschiedene Proben besorgen müsst.]], },
    { [[Beetix Wankelhupf von der Burg Cenarius in Silithus möchte, dass Ihr je 3 Stachel von Steinpanzerzangen und Steinpanzerschindern sowie 3 Felspirschergiftzähne für sie sammelt.]], },
  },
  [8279] = {
    [[Das Zwielichtlexikon]],
    { [[Bringt Ortell dem Einsiedler in Silithus die drei Kapitel des Zwielichtlexikons.]], },
    { [[Bringt Ortell dem Einsiedler in Silithus die drei Kapitel des Zwielichtlexikons.]], },
  },
  [8280] = {
    [[Sicherung der Versorgungslinien]],
    { [[Burg Cenarius ist von außerordentlicher strategischer Bedeutung. Ich bin davon überzeugt, dass unser Sieg über die Silithiden feststeht, solange wir sie halten können. Natürlich unter der Bedingung, dass wir die Versorgung mit Nachschubgütern aufrechterhalten können.]], [[Die Sandwürmer in dieser Gegend stellen für unsere Nachschubkarawanen eine noch größere Gefahr dar als die Silithiden selbst. Erst heute Morgen verloren wir eine ganze Morgenkornlieferung an eine Gruppe Baggerschläger. Ich brauche Eure Hilfe, um die Bestände dieser Baggerschläger auszudünnen und um sicherzustellen, dass unsere Karawanen vor weiteren Überfällen sicher sind.]], },
    { [[Windruferin Prachthorn auf Burg Cenarius in Silithus möchte, dass Ihr 15 Baggerschläger tötet.]], },
  },
  [8281] = {
    [[Verstärkte Sicherheit]],
    { [[Eure bisherigen Leistungen sind makellos, <name>. Dennoch, sind unsere Versorgungslinien noch lange nicht sicher. In Abwesenheit der Baggerschläger, werden ihre südlichen Artgenossen nach Norden drängen, um ihren Platz einzunehmen.]], [[Ihr werdet eine ganze Reihe Baggerbrecher töten müssen, um diese Biester von unseren Versorgungslinien fernzuhalten.]], },
    { [[Windruferin Prachthorn auf Burg Cenarius in Silithus möchte, dass Ihr 20 Baggerbrecher tötet.]], },
  },
  [8282] = {
    [[Noggles verlorener Ranzen]],
    { [[Wo bin ich? Wo is' meine Tasche? *Keuch* Ich muss sie verloren haben, als ich vor Totenstachel, dem Riesenskorpid, geflohen bin. Bei meiner überstürzten Flucht bin ich geradewegs in ein Nest von Steinpanzerschindern gerannt.]], [[In dem Ranzen, den ich bei mir trug, befanden sich allerlei seltene Reagenzien. Beetix und ich werden ohne sie nicht die Tränke herstellen können, die von der Burg Cenarius benötigt werden. Ich bin noch zu schwach, um wieder aufzustehen, aber ich kann es für Euch lohnend machen, wenn Ihr mir meine Reagenzien zurückbringt.]], [[Ich bin Totenstachel nahe dem südlichen Gebirgszug über den Weg gelaufen, hinter Bronzebarts Lager.]], },
    { [[Spürt Noggles Ranzen im südlichen Gebirge von Silithus auf und bringt ihn zu ihm, auf Burg Cenarius, zurück.]], },
  },
  [8283] = {
    [[GESUCHT: Totenstachel, Terror der Wüste]],
    { [[Man sagt, dass sich der grausame Skorpid Totenstachel nahe des südlichen Gebirgszuges wegen erhöhter Silithidenaktivität zurückgezogen hat. Dies stellt eine große Bedrohung für Patrouillen und Expeditionen in der südlichen Gegend dar. Wer sich an dessen Vernichtung beteiligt, soll reichlich belohnt werden. Fragt bei Hauptmann Vish Kozus, ganz oben auf dem Wachturm der Burg Cenarius, wegen der Belohnung nach.]], },
    { [[Bringt die Zange Totenstachels zu Vish Kozus, Hauptmann der Wache.]], },
  },
  [8284] = {
    [[Das Geheimnis des Zwielichts]],
    { [[Einst existierte nordwestlich von hier ein florierender Außenposten des Schattenhammers. Wenn Ihr heutzutage in diese Gegend reist, werdet Ihr dort nur Schwärme von Elementaren antreffen. Verrottete Holzpfosten und alte Zelttuchfetzen sind das Einzige, was noch auf den Standort des Lagers hinweist.]], [[Vor einiger Zeit fand ich dort die Fragmente einer Tontafel, allerdings nicht genug Teile, um sie zu irgendetwas Brauchbarem zusammenzusetzen. Bringt mir die fehlenden Fragmente der Schrifttafel. Vielleicht können wir so etwas Licht in die Sache bringen und herausfinden, was die Anhänger des Schattenhammers in Silithus gesucht haben.]], },
    { [[Geologin Lerchenbann auf Burg Cenarius möchte, dass Ihr ihr 8 Zwielichtschrifttafelfragmente bringt.]], },
  },
  [8285] = {
    [[Der Deserteur]],
    { [[Es gibt Gerüchte über ein Mitglied des Schattenhammers, das aus seiner Sekte desertiert ist und nun als Einsiedler in den Bergen südöstlich vom Südwindposten lebt. Sein Name ist Ortell. Vielleicht könnt Ihr ihn davon überzeugen, Euch bei der Entschlüsselung der Schrifttafel behilflich zu sein.]], [[Nehmt die wiederhergestellte Schrifttafel mit Euch und sucht Ortell auf.]], },
    { [[Bringt die wiederhergestellte Schrifttafel zu Ortell dem Einsiedler in Silithus.]], },
  },
  [8286] = {
    [[Was uns morgen erwartet]],
    { [[Der Tod umarmte mich bereits als er erschien und seine Macht auf mich anwandte. Er ließ seinen Atem nur einmal über meinen versehrten Körper streichen und wartete daraufhin geduldig bis die Zeit meine Wunden geheilt hatte.]], [["Wenn es sein muss, wirst du für immer existieren", sagte er, und mit diesen Worten sollte ich auf ewige Zeiten als Wächter gebunden sein.]], [[Werft jetzt einen Blick auf die Wüste. Der zweite Krieg steht uns bevor. Ihr müsst nach der Ruhestätte des Bronzeschwarms suchen. Reist zu den Höhlen der Zeit und seht nach, ob der Meister zurückgekehrt ist. Ich muss mir dessen sicher sein, bevor ich weiter vorgehe.]], },
    { [[Reist zu den Höhlen der Zeit in Tanaris und findet Anachronos, Nachkomme Nozdormus.]], },
  },
  [8287] = {
    [[Ein teuflischer Plan]],
    { [[So, das sollte es gewesen sein… ziemlich wertvolle Informationen, muss ich sagen! Ich habe beobachtet, wie die Truppen des Zirkels des Cenarius in Silithus eingetroffen sind. Ich nehme an, sie planen den Schattenhammer aus Silithus zu vertreiben, nicht wahr? Diese Informationen sollten dabei hilfreich sein.]], [[Bringt die Entschlüsselte Abschrift der Schrifttafel zu ihrem Anführer, Kommandant Mar'alith. Schon bald wird der Schattenhammer vernichtet sein und ich bin endgültig vor ihrem Zugriff sicher.]], },
    { [[Bringt Kommandant Mar'alith die entschlüsselte Abschrift der Schrifttafel.]], },
  },
  [8288] = {
    [[Nur einer kann sich erheben]],
    { [[Wir dürfen keine Zeit mehr verschwenden! Es gilt einen Helden zu finden! Die Aufgabe, die ich Euch stellen werde, würde normalerweise eine Armee erfordern, aber nur einer aus diesem Haufen von Möchtegernhelden wird sich erheben und die Bürde und Verantwortung eines Verbündeten der Brut tragen.]], [[Im Hort des Herrschers des Schwarzfels befindet sich eine äußerst grausame Bestie. Der Brutwächter Dreschbringer bewacht die Hallen des Zwists und blockiert den Weg, der in Nefarians inneres Sanktum führt. Schlachtet dieses abartige Unwesen nieder und bringt mir seinen Kopf. Seid Ihr erfolgreich, erwartet Euch die nächste Aufgabe.]], },
    { [[Bringt Brutwächter Dreschbringers Kopf zu Baristolth der Sandstürme in die Burg Cenarius in Silithus.]], },
  },
  [8289] = {
    [[Verdienstabzeichen]],
  },
  [8290] = {
    [[Besiegt die Eindringlinge!]],
    { [[Jenseits dieses Tunnels werdet Ihr ein Feld des Kampfes und der Aufruhr finden, <junger/junge><class> <class>. Die Horde hört nicht auf, unseren heiligen Wald zu roden und benutzt unser immergrünes Blattwerk, um ihre Maschinen anzutreiben.]], [[Betretet die Kriegshymnenschlucht und verteidigt die Feste der Silberschwingen. Schlagt die eingefallenen Kräfte der Horde zurück.]], [[Ein Erfolg würde Euch ein Verdienstabzeichen einbringen. Bringt mir eines dieser Abzeichen, <name> und ich werde Euch belohnen.]], },
    { [[Betretet die Kriegshymnenschlucht und besiegt die Streitkräfte der Horde. Verdient Euch ein Ehrenabzeichen der Kriegshymnenschlucht und kehrt zur Schildwache Weltensang am Hain der Silberschwingen zurück.]], },
  },
  [8291] = {
    [[Besiegt die Eindringlinge!]],
    { [[Jenseits dieses Tunnels werdet Ihr ein Feld des Kampfes und der Aufruhr finden, <junger/junge><class> <class>. Die Horde hört nicht auf, unseren heiligen Wald zu roden und benutzt unser immergrünes Blattwerk, um ihre Maschinen anzutreiben.]], [[Betretet die Kriegshymnenschlucht und verteidigt die Feste der Silberschwingen. Schlagt die eingefallenen Kräfte der Horde zurück.]], [[Ein Erfolg würde Euch ein Verdienstabzeichen einbringen. Bringt mir eines dieser Abzeichen, <name> und ich werde Euch belohnen.]], },
    { [[Betretet die Kriegshymnenschlucht und besiegt die Streitkräfte der Horde. Verdient Euch ein Ehrenabzeichen der Kriegshymnenschlucht und kehrt zur Schildwache Weltensang am Hain der Silberschwingen zurück.]], },
  },
  [8292] = {
    [[Verdienstabzeichen]],
    { [[You have proven your value to our efforts in Warsong Gulch. Continue to aid the cause and bring me more talismans of merit. Do this, and you will earn even more of our trust.]], },
    { [[You obtained a Silverwing Talisman of Merit for your last task, talk to me again, and you gain your reward.]], },
  },
  [8293] = {
    [[Ehrenabzeichen]],
    { [[You have proven your value to our efforts in Warsong Gulch. Continue to rage against the Silverwing Sentinels! Bring me more marks of honor!]], },
    { [[You obtained a Warsong Mark of Honor for your last task, talk to me again, and you gain your reward.]], },
  },
  [8294] = {
    [[Bezwingt die Eindringlinge der Silberschwingen]],
    { [[Die Wildnis des Eschentals wird sich der Macht der Horde ergeben, <race>. Nichts, was die Silberschwingen tun oder sagen, wird uns daran hindern, unseren territorialen Herrschaftsanspruch durchzusetzen. Kalimdor gehört der Horde. Wie können sie es wagen, uns davon abzuhalten, das zu ernten, was rechtens unser ist!]], [[Lasst Eure Brust vor Stolz erbeben, während Ihr ihre schwachen Versuche, unseren Fortschritt zu behindern, zunichtemacht. Zerstört die Schildwachen der Silberschwingen und erlangt ein Ehrenabzeichen des Kriegshymnenklans. Kehrt zu mir zurück, <name>, wenn Ihr im Besitz eines Ehrenabzeichens seid, und ich werde Euch belohnen.]], },
    { [[Betretet die Kriegshymnenschlucht und besiegt die Streitkräfte der Allianz. Erwerbt ein Ehrenabzeichen der Kriegshymnenschlucht und kehrt anschließend zu Hauptmann Schädelbrecher im Stützpunkt Mor'shan zurück.]], },
  },
  [8295] = {
    [[Bezwingt die Eindringlinge der Silberschwingen]],
    { [[Die Wildnis des Eschentals wird sich der Macht der Horde ergeben, <race>. Nichts, was die Silberschwingen tun oder sagen, wird uns daran hindern, unseren territorialen Herrschaftsanspruch durchzusetzen. Kalimdor gehört der Horde. Wie können sie es wagen, uns davon abzuhalten, das zu ernten, was rechtens unser ist!]], [[Lasst Eure Brust vor Stolz erbeben, während Ihr ihre schwachen Versuche, unseren Fortschritt zu behindern, zunichtemacht. Zerstört die Schildwachen der Silberschwingen und erlangt ein Ehrenabzeichen des Kriegshymnenklans. Kehrt zu mir zurück, <name>, wenn Ihr im Besitz eines Ehrenabzeichens seid, und ich werde Euch belohnen.]], },
    { [[Betretet die Kriegshymnenschlucht und besiegt die Streitkräfte der Allianz. Erwerbt ein Ehrenabzeichen der Kriegshymnenschlucht und kehrt anschließend zu Hauptmann Schädelbrecher im Stützpunkt Mor'shan zurück.]], },
  },
  [8296] = {
    [[Ehrenabzeichen]],
  },
  [8297] = {
    [[Die Ressourcen des Arathibeckens]],
    { [[Eine Schlacht muss mit Soldaten gewonnen werden, das wird Euch jeder Soldat gerne bestätigen. Man sagt auch, dass eine Schlacht auf leeren Magen und mit schlechter Ausrüstung bereits eine verlorene Schlacht ist!]], [[Deshalb ist das Arathibecken so wichtig. Es gibt dort Schlüsselregionen, die essentielle Ressourcen bergen. Metalle, Waffen, Holz, ja, alles wird gebraucht und kann dort gefunden werden.]], [[Daher brauche ich Euch, <name>. Begebt Euch ins Arathibecken und entscheidet die Schlacht für Euch, indem Ihr mehr Stützpunkte haltet als der Feind. Bringt mir als Beweis Eurer Taten eine Vorratskiste.]], },
    { [[Gewinnt die Schlacht um das Arathibecken, raubt eine Arathivorratskiste und bringt sie Unteroffizier Maclear in der Zuflucht.]], },
  },
  [8298] = {
    [[Noch mehr Vorratskisten]],
    { [[Eine Schlacht muss mit Soldaten gewonnen werden, das wird Euch jeder Soldat gerne bestätigen. Man sagt auch, dass eine Schlacht auf leeren Magen und mit schlechter Ausrüstung bereits eine verlorene Schlacht ist!]], [[Deshalb ist das Arathibecken so wichtig. Es gibt dort Schlüsselregionen, die essentielle Ressourcen bergen. Metalle, Waffen, Holz, ja, alles wird gebraucht und kann dort gefunden werden.]], [[Daher brauche ich Euch, <name>. Begebt Euch ins Arathibecken und entscheidet die Schlacht für Euch, indem Ihr mehr Stützpunkte haltet als der Feind. Bringt mir als Beweis Eurer Taten eine Vorratskiste.]], },
    { [[Gewinnt die Schlacht um das Arathibecken, raubt eine Arathivorratskiste und bringt sie Unteroffizier Maclear in der Zuflucht.]], },
  },
  [8299] = {
    [[Zerschlagt die Versorgungslinien Arathors]],
    { [[Der Bund von Arathor scheut keine Mühen, um Arathi sein Eigen zu nennen. Sie und all die anderen Völker der Allianz strömen nun in diese entfernte Region, versessen darauf, ihren Gebietsanspruch von der Zuflucht aus zu erweitern. Wir müssen sie aufhalten, und das beste Mittel dies zu tun, ist ihre Vorräte zu stehlen.]], [[Ihr könnt uns helfen, <name>. Betretet das Arathibecken, das für seine reichen Minen, sein fruchtbares Land und seine geschickten Handwerker bekannt ist, und erobert jeden Stützpunkt, den Ihr könnt. Entscheidet die Schlacht auf diese Weise für Euch und kehrt dann mit den Ressourcen, die Ihr erbeuten konntet, zu mir zurück.]], },
    { [[Entscheidet die Schlacht um das Arathibecken für Euch, erbeutet eine Arathivorratskiste und bringt sie Todespirscher Mortis in Hammerfall.]], },
  },
  [8300] = {
    [[Noch mehr Vorratskisten]],
    { [[Der Bund von Arathor scheut keine Mühen, um Arathi sein Eigen zu nennen. Sie und all die anderen Völker der Allianz strömen nun in diese entfernte Region, versessen darauf, ihren Gebietsanspruch von der Zuflucht aus zu erweitern. Wir müssen sie aufhalten, und das beste Mittel dies zu tun, ist ihre Vorräte zu stehlen.]], [[Ihr könnt uns helfen, <name>. Betretet das Arathibecken, das für seine reichen Minen, sein fruchtbares Land und seine geschickten Handwerker bekannt ist, und erobert jeden Stützpunkt, den Ihr könnt. Entscheidet die Schlacht auf diese Weise für Euch und kehrt dann mit den Ressourcen, die Ihr erbeuten konntet, zu mir zurück.]], },
    { [[Entscheidet die Schlacht um das Arathibecken für Euch, erbeutet eine Arathivorratskiste und bringt sie Todespirscher Mortis in Hammerfall.]], },
  },
  [8301] = {
    [[Der Pfad des Gerechten]],
    { [[Die Brut Nozdormus misstraut den niederen Völkern für gewöhnlich. Ihr habt mir zwar bewiesen, dass Ihr das Zeug zu einem Helden habt, es ist jedoch an der Zeit, dies auch meinem Meister zu zeigen.]], [[Der Ashischwarm, Zoraschwarm, Regalschwarm: Sie alle schwärmen rege aus und sind eine Bedrohung für unsere Existenz. Vernichtet Silithiden dieser Schwärme und nehmt Euch Fragmente ihrer Knochenpanzer. Kommt zu mir zurück, wenn Ihr vom Tragen erschöpft sein solltet, auf dass ich Euch in Eurer Entschlossenheit stärke.]], },
    { [[Kehrt zu Baristolth zurück, sobald Ihr 200 Silithidenknochenpanzerfragmente gesammelt habt.]], },
  },
  [8302] = {
    [[Die Hand der Gerechtigkeit]],
    { [[Wählt Eure Abgesandten weise aus, Sterblicher. Jedes Mal, wenn Ihr nach Erfüllung Eurer Aufgabe zurückkehrt, dürft Ihr einen neuen Abgesandten auswählen.]], [[Das soll heißen, dass ich noch mehr der Knochenpanzerfragmente benötige. Verschwendet keine Zeit!]], },
    { [[Kehrt zu Baristolth zurück, sobald Ihr 200 Silithidenknochenpanzerfragmente gesammelt habt.]], },
  },
  [8303] = {
    [[Anachronos]],
    { [[Es ist an der Zeit, <name>.]], [[Ich muss mich dafür entschuldigen, so viel vor Euch geheim zu halten. Die Feinde sind überall, selbst unter uns. Bitte, lest aus diesem Folianten und lernt. Nutzt das Wissen, das ich Euch preisgebe, und sucht nach unserem Meister.]], },
    { [[Sucht nach Anachronos bei den Höhlen der Zeit in Tanaris.]], },
  },
  [8304] = {
    [[Liebste Natalia]],
    { [[Meine geliebte Natalia ist verschwunden.]], [[Um die Wahrheit zu sagen, sie hat sich schon Wochen vor ihrem Verschwinden äußerst seltsam benommen. Ich habe sie mehr als einmal dabei erwischt, wie sie zu sich selber sprach, obwohl niemand in der Nähe war. Doch sie behauptete felsenfest, dass mit ihr alles in Ordnung sei und sie ihre Forschungen unbedingt fortsetzen müsste.]], [[Das letzte Mal hatte man sie gesehen, wie sie nach Süden, in Richtung Bronzebarts Lager verschwand.]], [[Leider stehen mir zu wenige Soldaten für weitere Nachforschungen zur Verfügung, <name>. Ich brauche Eure Hilfe, um die Zwerge in Bronzebarts Lager nach ihrem Verbleib zu befragen.]], },
    { [[Befragt Frankal und Rutgar in Bronzebarts Lager über den Aufenthaltsort von Natalia Mar'alith.]], },
  },
  [8305] = {
    [[Lang Vergessenes]],
    { [[Sucht die Kristallträne und blickt in ihr Innerstes.]], },
    { [[Sucht die Kristallträne und blickt in ihr Innerstes.]], },
  },
  [8306] = {
    [[Der Schlund des Wahnsinns]],
    { [[Kommandant Mar'alith auf Burg Cenarius in Silithus möchte, dass Ihr seine geliebte Natalia wiederfindet. Die von Euch gesammelten Informationen deuten darauf hin, dass Ihr sie im Bau des Regalschwarms im Süden finden könntet.]], [[Sprecht noch einmal mit den Zwergen in Bronzebarts Lager, bevor Ihr Euch in den Schwarmbau begebt. Ihr Wissen könnte sich als hilfreich erweisen und sicherlich, haben sie noch einige Aufgaben für Euch.]], [[Und <name>, denkt an die Worte des Kommandanten: "Tut, was Ihr tun müsst..."]], },
    { [[Kommandant Mar'alith auf Burg Cenarius in Silithus möchte, dass Ihr seine geliebte Natalia wiederfindet. Die von Euch gesammelten Informationen deuten darauf hin, dass Ihr sie im Bau des Regalschwarms im Süden finden könntet.]], [[Sprecht noch einmal mit den Zwergen in Bronzebarts Lager, bevor Ihr Euch in den Schwarmbau begebt. Ihr Wissen könnte sich als hilfreich erweisen und sicherlich, haben sie noch einige Aufgaben für Euch.]], [[Und <name>, denkt an die Worte des Kommandanten: "Tut, was Ihr tun müsst..."]], },
  },
  [8307] = {
    [[Wüstenrezept]],
    { [[Sich an das Leben in der Wüste anzupassen ist alles andere als leicht. Ich vermisse nicht nur die Vögel und Bäume Auberdines, nein, diese ausgedörrte Gegend schränkt uns auch sehr in der Wahl unserer Nahrung ein. Ich versuche mehr über die Kräuter der Wüste zu lernen, aber es gibt nicht so viele.]], [[Ich habe gesehen, dass Gruppen aus dem Lager des Schattenhammers westlich von hier Sandwürmer jagten. Sandwurmfleisch ist aber so bitter, ich kann mir einfach nicht vorstellen, wie sie es schmackhaft machen. Haltet Eure Augen offen, wenn Ihr zu ihrem Lager geht und lasst mich wissen, falls Ihr etwas von ihnen erfahrt.]], },
    { [[Haltet beim Lager des Schattenhammers westlich von Burg Cenarius nach Informationen zum Zubereiten von Sandwurmfleisch Ausschau.]], },
  },
  [8308] = {
    [[Der verlorene Brief von Brann Bronzebart]],
    { [[An den Finder dieses Briefs,]], [[wenn Ihr dies lest, bin ich wahrscheinlich bereits tot oder werde es in Kürze sein. Die meisten meiner Männer sind schon tot.]], [[Ich habe Informationen über einen Titanen entdeckt, über eine große Schlacht zwischen ihm und einem Alten Gott. Ja, ein verdammter Alter Gott. Wir haben einen uralten Tunnel nach Ahn'Qiraj entdeckt, dem ich jetzt folge.]], [[Bringt diesen Brief in mein altes Lager. Kümmert Euch bitte um meinen Affen und gebt ihm ausreichend Wasser, Nahrung und die eine oder andere Umarmung.]], [[- Brann Bronzebart, Extremforscher]], [[P.S. Achtet auf die verrückten nachtelfischen Anbeter des alten Gottes.]], },
    { [[Liefert Brann Bronzebarts verlorenen Brief in Bronzebarts Lager im südlichen Silithus ab.]], },
  },
  [8309] = {
    [[Glyphenjagd]],
    { [[Nach unseren ersten Streifzügen in die Schwarmbauten und dem darauf folgenden Wahnsinn der Herrin Mar'alith, haben die drei Schwarmbauten damit begonnen hunderte von Insektenkriegern auszuspucken. Die erhöhte Aktivität könnte mit den Glyphen in Zusammenhang stehen, die diese merkwürdigen Kristalle in den drei Bauten der Schwärme bedecken.]], [[Wir müssen unbedingt Abpausbilder von diesen Glyphen anfertigen. Mithilfe der Abpausbilder gelingt es uns vielleicht, hinter das Geheimnis dieses Wahnsinns zu kommen.]], [[Hier <name>, nehmt dieses Abschriftenset und begebt Euch in den Bau des Regalschwarms im Osten. Das ist das letzte Abpausbild, das ich brauche.]], },
    { [[Benutzt das Abschriftenset für Geologen, um ein Abpausbild des glyphenverzierten Kristalls des Regalschwarms anzufertigen.]], },
  },
  [8310] = {
    [[Knackt den Kode]],
    { [[Ich denke, ich kann ein Elixier entwickeln, welches uns die Entschlüsselung der Glyphen und der Sprachen der Silithiden und Qiraji sehr erleichtern wird.]], [[Was ich dafür brauche? Gut, dass Ihr fragt! Ich benötige Gehirne!]], [[Normalerweise bräuchte ich Gehirne von jeder Silithidenart, die die Baue bewohnen. Ein mutiger Abenteurer hat mir bereits zwei Proben gebracht... Er ist aber nie aus dem Bau des Regalschwarms zurückgekehrt.]], [[Bringt mir ein Silithidengehirn aus dem Bau des Regalschwarms... und passt auf, dass Ihr nicht so endet wie er!]], },
    { [[Erhaltet ein intaktes Silithidengehirn des Regalschwarms von einem beliebigen Silithiden im Bau des Regalschwarms.]], },
  },
  [8311] = {
    [[Schlotternachtssüßigkeiten für Jesper!]],
    { [[Ich darf nicht zum Süßigkeitensammeln gehen. Nach Todesschwinges Angriff habe ich schon Glück, dass die Waisenmatrone Nachtigall mich so weit hinaus lässt.]], [[Wenn Ihr für mich loszieht, dann werde ich Euch Eure gesammelten Süßigkeiten gegen welche eintauschen, die ich bereits hier habe!]], [[Alle Gastwirte in den größeren Städten verteilen während der Schlotternächte Süßigkeiten - sprecht mit ihnen und führt ihnen vor, nach was sie Euch fragen! In Eisenschmiede verteilt auch ein kleiner Gnom namens Talvash Süßes. Sobald Ihr die Süßigkeiten habt, kommt hierher zu mir zurück!]], },
    { [[Sprecht mit den Gastwirten in Sturmwind, Eisenschmiede und Darnassus sowie mit Talvash del Kissel in Eisenschmiede. Führt ihnen vor, nach was sie im Gegenzug für die Süßigkeiten verlangen.]], [[Kehrt zu Jesper außerhalb von Sturmwind mit einem Marzipan aus Darnassus, einer Kaugummikugel aus Gnomeregan, Nugat aus Sturmwind und einem Pfefferminzbonbon aus Eisenschmiede zurück.]], },
  },
  [8312] = {
    [[Schlotternachtssüßigkeiten für Spuks!]],
    { [[Ich würde gerne während der Schlotternächte zum Süßigkeitensammeln gehen, aber ich kann nicht, weil die Matrone mich nicht lässt. Wenn Ihr für mich loszieht, dann werde ich Euch Eure gesammelten Süßigkeiten gegen welche eintauschen, die ich bereits hier habe! Ich glaube Ihr werdet sie mögen - sie verwandeln Euch entweder in etwas Lustiges oder Gruseliges!]], [[Alle Gastwirte in den größeren Städten verteilen während der Schlotternächte Süßigkeiten - sprecht mit ihnen und führt ihnen vor, nach was sie Euch fragen! In Orgrimmar verteilt auch ein Troll namens Kali Remik Süßes. Sobald Ihr die Süßigkeiten habt, kommt hierher zu mir zurück!]], },
    { [[Sprecht mit den Gastwirten in Orgrimmar, Unterstadt und Donnerfels sowie mit Kali Remik im Tal der Geister in Orgrimmar. Führt ihnen vor, nach was sie im Gegenzug für die Süßigkeiten verlangen.]], [[Kehrt zu Spuks in den Ruinen von Lordaeron mit einem Marzipan aus Donnerfels, einer Kaugummikugel der Dunkelspeere, Nugat aus Orgrimmar und einem Pfefferminzbonbon aus Unterstadt zurück.]], },
  },
  [8313] = {
    [[Geteiltes Wissen]],
    { [[Das Rezept für die Zubereitung von Sandwürmern ist zwar lesbar, enthält aber Hinweise auf Kräuter und Gewürze, von denen Ihr noch nie gehört habt. Calandrath weiß vielleicht mehr über die Pflanzen, die in der Wüste wachsen, damit das Rezept einen Sinn ergibt.]], },
    { [[Bringt die ausgerissene Rezeptseite zu Calandrath ins Gasthaus der Burg Cenarius.]], },
  },
  [8314] = {
    [[Des Rätsels Lösung]],
    { [[Wir stehen kurz vor einem Durchbruch, <name>. Dank der von Euch beschafften Abpausbilder ist es mir gelungen, die Glyphen auf diesem Kristallprisma nachzubilden. Es sieht so aus, als würden sie den Qiraji und anderen unbekannten Wesen dazu dienen, mit den Silithiden zu kommunizieren.]], [[Leider ist das alles, was ich aus diesen Glyphen herauskriegen konnte. Wir müssen eine Möglichkeit finden, wie wir die in den Kristallen versteckten Botschaften freilegen können.]], [[Nehmt dieses glyphenverzierte Kristallprisma und bringt es Geologin Lerchenbann auf Burg Cenarius. Sie müsste uns weiterhelfen können.]], },
    { [[Bringt das glyphenverzierte Kristallprisma zu Geologin Lerchenbann auf Burg Cenarius in Silithus.]], },
  },
  [8315] = {
    [[Die Anrufung]],
    { [[Die Glyphen zeigen einen Abgesandten der Qiraji, wie er sich mit drei Silithiden vor den Knochen eines großen Drachen trifft. Über den Glyphen steht, soweit ich dass beurteilen kann, eine Art Kode. Es könnte gut sein, dass dieser Kode der Schlüssel zur Beschwörung des Abgesandten ist.]], [[Ich werde eine Kopie des Glyphenkodes anfertigen, den Ihr vor den Knochen von Grakkarond in den Sand schreiben müsst. Wenn ich Recht habe, werden die Oberanführer in dem Glauben dort auftauchen, dass neue Informationen zur Übertragung bereit stehen. Streckt sie nieder und konfrontiert den Abgesandten. Er besitzt den Schlüssel für die Kristalle...]], },
    { [[Geologin Lerchenbann auf Burg Cenarius in Silithus möchte, dass Ihr ihr den Kristallentriegelungsmechanismus vom Abgesandten der Qiraji beschafft.]], [[Nehmt die Glyphen der Anrufung, begebt Euch zu den Knochen von Grakkarond südlich von Burg Cenarius und zeichnet sie dort in den Sand. Sollte der Abgesandte der Qiraji erscheinen, tötet ihn und nehmt den Kristallentriegelungsmechanismus an Euch. Kehrt anschließend zu Geologin Lerchenbann zurück.]], [[Für diese Aufgabe werdet Ihr eine kleine Armee benötigen, <name>!]], },
  },
  [8316] = {
    [[Kriegsausrüstung]],
  },
  [8317] = {
    [[Küchengehilfe]],
    { [[Das Rezept kommt gelegen. Ich mache mich besser an die Arbeit, damit ich ein paar geräucherte Wüstenknödel für heute bereit habe. Ich könnte dabei etwas Hilfe gebrauchen, Ihr werdet dafür auch belohnt werden.]], },
    { [[Calandrath von der Burg Cenarius braucht 10 geräucherte Wüstenknödel.]], },
  },
  [8318] = {
    [[Geheime Nachrichten]],
    { [[Einige Schattenhammerkultisten tragen mit Kauderwelsch beschriftete Papiere bei sich. Ich möchte, dass Ihr mir ein paar dieser Texte bringt, <class>.]], [[Ich habe zwar keine Möglichkeit, dieses Geschreibsel zu verstehen, doch das ist nicht der springende Punkt! Mithilfe dieser Papiere tauschen die verschiedenen Gruppierungen des Kults untereinander geheime Nachrichten aus. Mit jeder Nachricht, die wir aus dem Verkehr ziehen, stören wir ihre Kommunikation untereinander!]], },
    { [[Bringt Bor Wildmähne auf Burg Cenarius 10 verschlüsselte Schattenhammertexte.]], },
  },
  [8319] = {
    [[Verschlüsselte Schattenhammertexte]],
  },
  [8320] = {
    [[Geolords des Schattenhammers]],
    { [[Der Schattenhammer, ein dämonischer Kult, der sich den Kräften der Zerstörung verschrieben hat, errichtet seit längerem Lager in Silithus und hält dort Tag und Nacht bizarre Rituale ab. Bisher wissen wir noch nichts über ihre Pläne, allerdings vermuten wir, dass sie in irgendeiner Verbindung zu den pulsierenden roten Kristallen stehen, die man überall in der Wüste findet.]], [[Auch wenn wir ihrem Geheimnis früher oder später auf die Spur kommen werden, so müssen wir uns zunächst auf die unmittelbare Bedrohung konzentrieren. Sucht ihre Lager im Westen auf und haltet nach Zwielichtgeolords Ausschau. Sie sind die Köpfe dieser Schlangen; genau dort müssen wir zuschlagen!]], },
    { [[Tötet 10 Zwielichtgeolords und kehrt anschließend zu Huum Wildmähne auf Burg Cenarius zurück.]], },
  },
  [8321] = {
    [[Vyral der Üble]],
    { [[Wir konnten eine weitere Operationsbasis des Schattenhammers ausfindig machen. Es ist eine gut versteckte Höhle in der nordöstlichen Ecke von Silithus, bekannt als die Kavernen des Schattenhammers. Der örtliche Anführer des Kultes, Vyral der Üble, residiert dort. Er ist Euer nächstes Ziel. Besiegt ihn und bringt mir seinen Siegelring.]], [[Die Kavernen des Schattenhammers beherbergen furchterregende Schrecken, also passt auf Euch auf! Ich wünsche Euch alles gute, <name>.]], },
    { [[Tötet Vyral den Üblen und erhaltet Vyrals Siegelring.]], },
  },
  [8322] = {
    [[Faule Eier]],
    { [[Die Menschen von Süderstade lieben ihr Bier und deshalb schätzen sie das Bier, das im Gasthaus von Süderstade serviert wird, auch besonders. Wir können von dem Chaos während der Schlotternächte profitieren und ihre letzte Ladung Bier für Wochen - wenn nicht sogar Monate, verderben!]], [[Nehmt diese faulen Eier und werft sie in die großen Braufässchen im Gasthaus von Süderstade. Allein Eure Anwesenheit wird für Unruhe sorgen, Ihr solltet Euch also zu verteidigen wissen!]], [[Wenn Ihr Süderstade dieses unvergessliche Geschmackserlebnis überbracht habt, kommt hierher zu mir aufs Fest zurück!]], },
    { [[Werft die faulen Eier in die großen Braufässchen im Gasthaus von Süderstade.]], },
  },
  [8323] = {
    [[Wahrer Glauben]],
    { [[Jetzt, da sich das Zwielichtlexikon in meinem Besitz befindet, möchte ich Euch um einen kleinen Gefallen bitten.]], [[Meine ehemalige Organisation verschickt in regelmäßigen Abständen eine verschlüsselte Postille.]], [[Sollten sie bei ihrer Suche nach mir irgendwelche Fortschritte machen, wird dies mit Sicherheit im 'Wahren Glauben' bekannt gegeben werden.]], [[Abgesehen davon, dass diese Information von großer Bedeutung für mich ist, dürfte die Postille noch weitere, für Euch interessante Informationen enthalten, die ich mit Freuden teilen werde. Bringt mir einfach alle verschlüsselten Texte, die Ihr bei den Mitgliedern des Schattenhammers findet.]], },
    { [[Ortell der Einsiedler möchte, dass Ihr ihm 10 verschlüsselte Schattenhammertexte beschafft.]], },
  },
  [8324] = {
    [[Fester Glauben]],
  },
  [8325] = {
    [[Rückeroberung der Insel der Sonnenwanderer]],
    { [[Je früher Ihr mit Eurer Ausbildung anfangt, <name>, umso besser für uns alle. Wir können uns keine Fehler erlauben, also hört gut zu.]], [[Die brennenden Kristalle - die grünen, schwebenden Objekte westlich des Sonnenturms hier - wurden lange Zeit als Energiequelle für die Experimente der Insel verwendet. Die Manawyrms waren ihre Wächter, aber der Einmarsch der Geißel in Quel'Thalas hat sie auf Grund unserer mangelnden magischen Kontrolle über sie fehlgeleitet.]], [[Uns bleibt keine andere Wahl, als sie für die Wiedereroberung der Insel in ihrer Zahl zu reduzieren. Kehrt nach Beendigung Eurer Arbeit zu mir zurück.]], },
    { [[Kill 8 Mana Wyrms; they are located close to the Burning Crystals that are adjacent to the tower and gazebo.  Return to Magistrix Erona at the tower on Sunstrider Isle when you are done.]], },
  },
  [8326] = {
    [[Bedauerliche Maßnahmen]],
    { [[Eure Bemühungen haben uns etwas erkennen lassen, von dem ich hoffte, es wäre nicht wahr. Die unkontrollierte Macht der brennenden Kristalle hat einen viel größeren Teil des natürlichen Gleichgewichts der Insel durcheinander gebracht als ich vermutet habe. Wir müssen jetzt leider bedauerliche Maßnahmen ergreifen, um die Kontrolle wiederzuerlangen.]], [[Die Luchse aus der Umgebung sind dem Einfluss der Kristalle erlegen und müssen bekämpft werden. Bringt mir ihre Halsbänder, <name>. Noch kann ich vielleicht einen Magieschutz anfertigen, der manche wieder dem Einfluss entziehen kann.]], },
    { [[Sammelt 8 Luchshalsbänder von erlegten Bachtatzenluchsen und Bachtatzenjungen ein. Kehrt zu Magistrix Erona auf der Insel der Sonnenwanderer zurück, sobald Ihr Eure Aufgabe erfüllt habt.]], },
  },
  [8327] = {
    [[Berichterstattung an Lanthan Perilon]],
    { [[Erstattet meinem Assistenten Lanthan Perilon Bericht. Er wird Euch sagen, was es als Nächstes zu tun gilt. Während wir hier am Sonnenturm weiterhin über die brennenden Kristalle walten, werdet Ihr Euch auf eine dringendere Angelegenheit bezüglich der Akademie von Falthrien konzentrieren. Lanthan wird Euch alles erklären, wenn Ihr mit ihm sprecht.]], [[Geht einfach von hier aus gen Westen entlang des Pfads, Ihr solltet ihn in der Nähe antreffen.]], [[Viel Glück, <name> - jeglicher Erfolg soll belohnt werden.]], },
    { [[Sprecht mit Lanthan Perilon auf der Insel der Sonnenwanderer. Er steht in der Nähe des Pfads, der vom Sonnenturm Richtung Westen verläuft.]], },
  },
  [8328] = {
    [[Ausbildung zum Magier]],
    { [[Während Ihr in Eurer Klasse weiter aufsteigt, werdet Ihr sicher die Ausbildung, die Euch der Lehrer Eurer Klasse anbietet, nutzen wollen. Die Lehrerin für junge Magier hier auf der Insel der Sonnenwanderer ist Julia Sonnensturm. Sprecht mit Julia, um herauszufinden, welche Ausbildung sie Euch anbieten kann. Die Ausbildung hat ihren Preis, bringt also ein paar Münzen mit Euch.]], [[Julia - wie alle anderen Lehrer auf der Insel der Sonnenwanderer auch - ist innerhalb des Sonnenturms, auf der unteren Ebene, anzutreffen.]], },
    { [[Sprecht mit Julia Sonnensturm - der Magierlehrerin - innerhalb des Sonnenturms auf der Insel der Sonnenwanderer.]], },
  },
  [8329] = {
    [[Ausbildung zum Krieger]],
    { [[Während Ihr in Eurer Klasse weiter aufsteigt, werdet Ihr sicher die Ausbildung, die Euch der Lehrer Eurer Klasse anbietet, nutzen wollen. Der Lehrer für junge Krieger hier auf der Insel der Sonnenwanderer ist Delios. Sprecht mit ihm, um herauszufinden, welche Ausbildung er Euch anbieten kann. Die Ausbildung hat ihren Preis, habt also ein paar Münzen bei Euch.]], [[Delios - wie alle anderen Lehrer auf der Insel der Sonnenwanderer auch - ist innerhalb des Sonnenturms auf der unteren Ebene anzutreffen.]], },
    { [[Sprecht mit Delios - dem Kriegerlehrer - innerhalb des Sonnenturms auf der Insel der Sonnenwanderer.]], },
  },
  [8330] = {
    [[Solanians Habe]],
    { [[Bei dem Chaos hier beim Sonnenturm, habe ich es nicht geschafft, all mein Hab und Gut zusammenzusammeln, das ich an verschiedenen Plätzen auf der Insel gelassen habe. Ich muss hier beim Sonnenbrunnen weiterhin Wache halten, aber könntet Ihr vielleicht meine Sachen an meiner Stelle suchen gehen?]], [[Ich brauche meine Seherkugel, meine Rolle der Geißelmagie und mein Tagebuch. Nutzt diesen Ranzen für zusätzliches Gepäck, da die Dinge etwas sperrig sind. Bringt sie zu mir zurück und ich gebe Euch dafür etwas im Austausch - für Eure Mühe, versteht Ihr? Den Ranzen könnt Ihr dann auch behalten!]], },
    { [[Sammelt Brunnenbehüter Solanians Seherkugel, seine Rolle der Geißelmagie und sein Tagebuch ein. Ihr findet die Gegenstände auf der Insel der Sonnenwanderer bei dem Tümpel, dem Brunnen und bei einem der brennenden Kristalle. Bringt sie zu dem Brunnenbehüter oben im Sonnenturm auf der Insel der Sonnenwanderer, sobald Ihr alle gefunden habt.]], },
  },
  [8331] = {
    [[Aurel Goldblatt]],
    { [[<name>, wir hatten angenommen, dass wir die Operationen des Schattenhammers in Silithus verlangsamt haben… bedauerlicherweise lagen wir falsch. Durch verschiedene Unternehmungen gegen den Kult haben wir herausgefunden, dass eine noch viel dunklere Macht hinter dem Schattenhammer steht, der sogenannte Abyssische Rat. Er kontrolliert den Kult über die arkanen Windsteine, die in den Lagern der Schattenhammerkultisten postiert sind. Daher, <name>, werdet Ihr als Nächstes gegen den abyssischen Rat ins Feld ziehen.]], [[Sprecht mit Aurel Goldblatt im östlichen Teil von Burg Cenarius. Für unseren nächsten Schachzug werden wir ihre Hilfe benötigen.]], },
    { [[Sprecht mit Aurel Goldblatt auf Burg Cenarius.]], },
  },
  [8332] = {
    [[Fürsten des Rates]],
    { [[Wir glauben, es sollte möglich sein, mithilfe eines der Windsteine einen der Fürsten des Abyssischen Rates in eine Falle zu locken. Um das zu bewerkstelligen, werdet Ihr den Rat mit einem der Windsteine kontaktieren müssen. Dann gilt es, Euer Gegenüber davon zu überzeugen, dass Ihr ein Offizier mittleren Ranges des Schattenhammers seid.]], [[Für Euren Täuschungsversuch werden wir auf jeden Fall die Nachbildung eines Standesmedaillons des Schattenhammers benötigen. Ich kann ein solches Medaillon anfertigen, allerdings brauche ich dafür einen großen glänzenden Splitter und das Wappen eines abyssischen Templers.]], [[Bringt mir diese Komponenten und ich fertige Euch das Medaillon an.]], },
    { [[Bringt Aurel Goldblatt auf Burg Cenarius 1 großen glänzenden Splitter und 3 Abyssische Wappen.]], },
  },
  [8333] = {
    [[Das Standesmedaillon]],
  },
  [8334] = {
    [[Aggressivität]],
    { [[Bevor ich Euch zur Akademie aussende, möchte ich, dass Ihr Euch um eine andere Aufgabe kümmert, die es sofort zu erledigen gilt. Einst halfen uns die Borklinge, die Pflanzenwelt der Insel der Sonnenwanderer zu erhalten, aber jetzt sind uns diese Bestien außer Kontrolle geraten. Helft uns so schnell wie möglich bei ihrer Vernichtung, aber seid gewarnt - der Verlust ihrer Kontrolle hat sie äußerst aggressiv werden lassen. Es ist diese Angriffslust, die uns zwingt, diese einst friedlichen Untergebenen zu zerschlagen.]], [[Es bereitet mir keine Freude, Euch um diesen Gefallen zu bitten, aber es geht nun mal ums Überleben. Ich werde später trauern.]], },
    { [[Tötet 7 Borklinge und 7 wilde Borklinge. Kehrt dann zu Lanthan Perilon auf der Insel der Sonnenwanderer zurück.]], },
  },
  [8335] = {
    [[Felendren der Verbannte]],
    { [[Einer unserer Verräter haust oben in der Akademie von Falthrien.]], [[Felendren ist sein Name. Er wurde aus dem Reich der Blutelfen verbannt, da er die Warnungen unserer Lehrer und Ältesten missachtet hat. Er ist der Schlimmste unserer Art, da er nur nach der Befriedigung seines unstillbaren Magieverlangens strebt. Er hat sich der Kontrolle verweigert und ist nur noch ein Schatten seines einstigen Selbsts - ein Getriebener - und er stellt eine Bedrohung für den Sonnenturm dar.]], [[Vernichtet ihn und die Gespenster, die er sich als Diener zu Nutzen macht.]], },
    { [[Tötet 8 Arkangespenster, 2 besudelte Arkangespenster und Felendren den Verbannten. Ihr findet sie in der Akademie von Falthrien. Bringt Felendrens Kopf zu Lanthan Perilon auf der Insel der Sonnenwanderer.]], },
  },
  [8336] = {
    [[Für eine Handvoll Fragmente]],
    { [[Es ist eine Schande, dass wir die Kontrolle über so viele Kreaturen hier auf der Insel verloren haben. Es war einst ein ruhiger Ort der Studien und Forschungsarbeiten. Jetzt können wir uns nur noch hüten, damit wir von unseren eigenen Kreationen nicht angegriffen werden!]], [[Ich biete Euch einen magischen Segen an, wenn Ihr für mich etwas einsammelt. Bringt mir eine Handvoll Arkanspäne von den Mana benutzenden Kreaturen der Insel der Sonnenwanderer und ich wirke einen Zauber auf Euch, welcher Eure weiteren Abenteuer immens erleichtern wird.]], },
    { [[Sammelt 6 Arkanspäne und bringt sie zu Arkanist Ithanas auf der Insel der Sonnenwanderer. Derartige Fragmente findet man bei allen Mana benutzenden Kreaturen auf der Insel.]], },
  },
  [8338] = {
    [[Besudelter Arkanspan]],
    { [[Das Fragment kristallisierten Manas, das Ihr bei dem Körper des besudelten Arkangespensts gefunden habt, ist äußerst sonderbar. Es ist anders als die Fragmente, die Ihr vielleicht sonst gesehen habt. Es sieht so dunkel und unheimlich wie das Gespenst, von dem Ihr es habt, aus. Hält man es in der Hand, spürt man bereits etwas unnatürlich Bedrohliches.]], [[Arkanist Helion beim Vorbau des Sonnenturms auf der Insel der Sonnenwanderer könnte für das Fragment vielleicht Verwendung finden.]], },
    { [[Bringt den besudelten Arkanspan zu Arkanist Helion beim Vorbau des Sonnenturms auf der Insel der Sonnenwanderer.]], },
  },
  [8339] = {
    [[Belohnung des Rats]],
    { [[Um einen Siegelring eines Schattenhammerkultisten herzustellen, brauche ich ein paar Dinge.]], },
    { [[Bringt die Gegenstände zu Aurel Goldblatt in der Burg Cenarius.]], },
  },
  [8340] = {
    [[Siegelring des Schattenhammers]],
  },
  [8341] = {
    [[Lords des Rates]],
    { [[Ein Lord des Abyssischen Rates kann nur durch einen großen Windstein herbeigerufen werden und nur den höchstdekorierten Ratsmitgliedern des Schattenhammers ist es erlaubt mit den Lords in Kontakt zu treten. Doch... ich habe einen Weg gefunden, um Eure Verkleidung anzupassen und somit die abyssischen Fürsten zu täuschen!]], [[Dazu benötigen wir einen Herrschaftsring, welcher nur von den höchsten Mitgliedern des Kultus getragen wird. Ich kann Euch einen Ring anfertigen, wenn Ihr mir einige große glänzende Splitter und ein paar abyssische Siegel bringt.]], },
    { [[Bringt Aurel Goldblatt auf Burg Cenarius 5 große glänzende Splitter und 3 abyssische Siegel.]], },
  },
  [8342] = {
    [[Zwielichtherrschaftsring]],
  },
  [8343] = {
    [[Goldblatts Entdeckung]],
    { [[<name>, Aurel Goldblatt glaubt einen Weg gefunden zu haben, eines der führenden Mitglieder des Abyssischen Rates zu beschwören und zu besiegen. Diese Aufgabe wird äußerst gefährlich werden und Ihr genießt den größten Respekt innerhalb des Zirkels des Cenarius. Daher glauben wir, dass wenn es jemanden gibt, der diese schwere Prüfung meistern kann, dann seid Ihr es.]], [[Möge Euch das Schicksal gnädig sein, <name>! Sprecht mit Aurel, wenn Ihr bereit seid.]], },
    { [[Sprecht mit Aurel Goldblatt auf Burg Cenarius.]], },
  },
  [8344] = {
    [[Schlüssel zur Macht]],
    { [[Erhört meine Worte, Kind - diese Insel ist von einem Makel behaftet, der mächtiger als die Überbleibsel der Geißel ist.]], [[Ich glaube, dass auf der Insel eine wirklich dämonische Präsenz vorherrscht.]], [[Westlich von hier liegen die Türme der Akademie von Falthrien. Die Arkangespenster, die dort hausen, sind der Schlüssel zu der dämonischen Machtquelle. Bringt mir Essenzen von besudelten und unbesudelten Gespenstern.]], [[Im Austausch für Euren Gehorsam, werde ich Euch lehren, wie man einen Wichtel als Begleiter beschwören kann - Euren ersten von vielen als Hexenmeister.]], },
    { [[Bring 4 Essenzen eines Gespenstes und 1 Essenz eines besudelten Gespenstes zu Beschwörer Teli'Larien beim Sonnenturm auf der Insel der Sonnenwanderer.]], },
  },
  [8345] = {
    [[Der Schrein von Dath'Remar]],
    { [[Tag für Tag stehe ich hier, beobachte, warte. Man hat mich beschuldigt, zu tief in unserer Vergangenheit zu graben, während mein Blick zum Horizont gerichtet ist. Ich glaube jedoch fest daran, dass jeder Besucher dieser Insel diejenigen ehren sollte, die alles dafür geopfert haben, damit andere fortfahren können.]], [[Dath'Remar Sonnenwanderer war unser erster König. Er führte uns durch den Mahlstrom von Kalimdor hierher.]], [[Sucht seinen Schrein im Westen auf und kehrt erst dann zu mir zurück, wenn Ihr die dort angebrachte Tafel zu seiner Ehre gelesen habt.]], },
    { [[Lest die Tafel am Schrein von Dath'Remar und kehrt anschließend zu Brunnenbehüter Solanian beim Sonnenturm auf der Insel der Sonnenwanderer zurück.]], },
  },
  [8346] = {
    [[Unstillbarer Durst]],
    { [[If there is only one lesson you deign to remember from your time on Sunstrider Isle, let it be this - control your thirst for magic.  It is a thirst unending, <name> - you must absorb energy to survive via Mana Tap, and you must control how you release it via Arcane Torrent.  Failure is to become one of the Wretched... hopelessly addicted and insane.]], [[Seek out creatures on the isle that have mana and Mana Tap them.  Learn to master your cravings of power.  When you have sufficiently fed, return to me.]], },
    { [[Use your Mana Tap racial ability on creatures with mana six times, then return to Arcanist Helion.]], },
  },
  [8347] = {
    [[Unterstützung für die Kundschafter]],
    { [[Magistrix Erona möchte, dass Ihr Euch nach Silbermond begebt, um die dortigen Bestrebungen zur Wiedereroberung zu unterstützen... allerdings gilt es vorher noch, etwas bei der Akademie von Falthrien zu erledigen. Ihr habt uns nicht enttäuscht, <name>.]], [[Auf Eurem Weg zum Falkenplatz südlich von hier solltet Ihr mit Kundschafterin Alarion sprechen. Sie ist nicht weit der Brücke.]], [[Die Kundschafter kümmern sich um den Warenverkehr zwischen hier und dem Landesinneren der Insel, und da Ihr Eure Tatkräftigkeit eindrucksvoll unter Beweis gestellt habt, denke ich, dass sie Eure Hilfe sicherlich sehr begrüßen werden.]], },
    { [[Sprecht mit Kundschafterin Alarion nahe der Brücke, die in den Süden der Insel der Sonnenwanderer führt.]], },
  },
  [8348] = {
    [[Siegel der Fürsten]],
    { [[Huum hat mir erzählt, dass Ihr herausbekommen habt, wie man einen der abyssischen Fürsten herbeiruft? Eine beeindruckende Leistung, muss ich sagen.]], [[Wisst Ihr, was noch beeindruckender wäre? Wenn Ihr es fertig bringt, eines dieser Dinger zu erledigen. Das sollte einen bleibenden Eindruck bei ihren Gefolgsleuten hinterlassen, wenn Ihr wisst, was ich meine. Bringt mir das Siegel des Besiegten, als Beweis Eurer Tat.]], },
    { [[Bringt Bor Wildmähne auf Burg Cenarius das abyssische Siegel.]], },
  },
  [8349] = {
    [[Bor Wildmähne]],
    { [[Nun, da Ihr ein Medaillon habt und in der Lage seid, einen abyssischen Fürsten herbeizurufen, sprecht mit Bor Wildmähne. Er wird Euch mit einer Aufgabe, die sich gegen den Abyssischen Rat richtet, beauftragen.]], [[Viel Glück, <name>. Falls Ihr weitere Medaillons braucht, kehrt zu mir zurück.]], },
    { [[Sprecht mit Bor Wildmähne auf Burg Cenarius.]], },
  },
  [8350] = {
    [[Erfolgreiche Lieferung]],
    { [[Der Tod eines weiteren Kundschafters ist eine wahre Tragödie, aber es ist ein Risiko, das wir alle in Kauf nehmen, wenn wir einen Auftrag annehmen. Magistrix Erona beim Sonnenturm möchte, dass dieses Päckchen schnellstmöglich zum Gasthaus beim Falkenplatz geliefert wird.]], [[Wir haben nur noch wenige Leute und niemand könnte jetzt für uns einspringen.]], [[Ich möchte Euch nichts vormachen, Ihr habt selbst gesehen, wie gefährlich die Straße zum Falkenplatz sein kann, sobald man vom Dämmerweg abkommt. Fühlt Ihr Euch bereit für diese Aufgabe, <name>?]], },
    { [[Bringt Eronas Päckchen zu Gastwirtin Delaniel im Gasthaus beim Falkenplatz im Immersangwald.]], },
  },
  [8351] = {
    [[Bor möchte Euch sprechen]],
    { [[Nun, da Ihr Euch als ein führendes Mitglied des Schattenhammers ausgeben könnt, bin ich sicher, dass Bor Wildmähne gerne mit Euch sprechen möchte. Am besten sucht Ihr ihn gleich auf… und bereitet Euch auf eine sehr schwierige Aufgabe vor…]], },
    { [[Sprecht mit Bor Wildmähne auf Burg Cenarius.]], },
  },
  [8352] = {
    [[Szepter des Rates]],
    { [[Huum und ich haben gerade über Euch gesprochen. Er erzählte mir, dass Ihr schon beinahe bereit seid, einem der abyssischen Lords entgegenzutreten!]], [[Ich muss Euch warnen, das ist ein sehr gefährliches Unterfangen! Nehmt lieber Verstärkung mit, und ich meine nicht bloß ein bis zwei Eurer Freunde! Ich spreche von einer erfahrenen, kampfgestählten Truppe, die weiß, worauf sie sich einlässt. Sollten Eure Freunde aus dem gleichen Holz geschnitzt sein wie Ihr, besteht sogar eine reelle Chance, dass Ihr den Sieg davontragen werdet.]], [[Falls Ihr siegt, bringt mir wie immer einen Beweis und ich werde Euch eine wahrhaft mächtige Belohnung zuteilwerden lassen.]], },
    { [[Bringt Huum Wildmähne auf Burg Cenarius ein abyssisches Szepter.]], },
  },
  [8353] = {
    [[Hühnchengegacker für ein Pfefferminzbonbon]],
    { [[Ah, wir gehen also Süßigkeitensammeln für ein Kind? Ich dachte mir schon, dass Ihr etwas zu alt seid, um für Süßes loszuziehen...]], [[Na ja, für meine Leckerei - ein Pfefferminzbonbon aus Eisenschmiede - müsst Ihr... wie ein Hühnchen gackern! Ja, Hühnchen lieben Pfefferminzbonbons... nun ja, OK, vielleicht LIEBEN sie sie nicht... aber ich gebe Euch trotzdem kein Pfefferminzbonbon, wenn Ihr nicht wie ein Hühnchen für mich gackert!]], [[So <class>, seid Ihr dazu bereit oder nicht?! Verdient Euch Eure Süßigkeit!]], },
    { [[Spielt Gastwirt Feuerbräu ein Huhn vor, er wird Euch dafür ein Pfefferminzbonbon aus Eisenschmiede geben!]], },
  },
  [8354] = {
    [[Hühnchengegacker für ein Pfefferminzbonbon]],
    { [[Spielt Gastwirt Norman ein Huhn vor, er wird Euch dafür ein Pfefferminzbonbon aus Unterstadt geben!]], },
    { [[Spielt Gastwirt Norman ein Huhn vor, er wird Euch dafür ein Pfefferminzbonbon aus Unterstadt geben!]], },
  },
  [8355] = {
    [[Kaugummikugelexpress]],
    { [[Ah, Süßes oder Saures! Hurra!]], [[Nun, auch wenn Ihr etwas Ehrenhaftes tut und Süßigkeiten für ein Kind sammelt, so kann ich Euch doch nicht so einfach davonkommen lassen. Ich hätte da eine leckere Kaugummikugel aus Gnomeregan für das Kind... wenn Ihr mir einen Zug vorführt! Haha, ja, das wär's! Der Preis für diese Kaugummikugel ist das Imitieren eines Zuges!]], [[Tschugga-tschugga, <class>!]], },
    { [[Ahmt für Talvash del Kissel einen Zug nach und er wird Euch dafür eine Kaugummikugel aus Gnomeregan geben!]], },
  },
  [8356] = {
    [[Muskelspiel für Nugat]],
    { [[Das ist ein... interessantes Kostüm, welches Ihr da tragt. Auch wenn Ihr nett seid und einem Kind dieses Jahr beim Süßigkeitensammeln helft, so fürchte ich doch, dass ich Euch um eine kleine Vorstellung bitten muss!]], [[Mal sehen... Ihr seht in diesem... Kostüm stark und muskulös aus... jetzt gebt doch mal ein bisschen an! Ja, lasst Eure Muskeln für mich spielen, <starker Mann / starke Frau>!]], [[Wenn ich Euch angeben sehe, und auch nur dann, werde ich Euch etwas von dem leckeren Nugat aus Sturmwind geben. Es ist eine großartige Schlotternachtssüßigkeit.]], },
    { [[Ihr müsst vor Gastwirtin Allison angeben, damit sie Euch im Gegenzug etwas vom Nugat aus Sturmwind gibt!]], },
  },
  [8357] = {
    [[Tanzen für Marzipan]],
    { [[Auch wenn Ihr einem Kind beim Süßigkeitensammeln helft, so müsst Ihr mir noch immer etwas für Eure Belohnung vorführen! Man kann nicht so einfach mit der Tradition brechen, nicht wahr... ?]], [[Ich denke, dass ein Stück Marzipan heute eine ordentliche Tanzvorstellung wert ist! Ja genau, zeigt mal, was Ihr so drauf habt! Tanzt für mich und ich werde Euch etwas von dem leckeren Marzipan geben!]], },
    { [[Tanzt für Gastwirtin Saelienne und Ihr werdet im Gegenzug etwas von dem Marzipan aus Darnassus bekommen!]], },
  },
  [8358] = {
    [[Kaugummikugelexpress]],
    { [[Ahmt für Kali Remik einen Zug nach und sie wird Euch dafür eine Kaugummikugel der Dunkelspeere geben!]], },
    { [[Ahmt für Kali Remik einen Zug nach und sie wird Euch dafür eine Kaugummikugel der Dunkelspeere geben!]], },
  },
  [8359] = {
    [[Muskelspiel für Nugat]],
    { [[Ihr müsst vor Gastwirtin Gryshka angeben, damit sie Euch im Gegenzug etwas vom Nugat aus Orgrimmar gibt!]], },
    { [[Ihr müsst vor Gastwirtin Gryshka angeben, damit sie Euch im Gegenzug etwas vom Nugat aus Orgrimmar gibt!]], },
  },
  [8360] = {
    [[Tanzen für Marzipan]],
    { [[Auch wenn Ihr einem Kind beim Süßigkeitensammeln helft, so müsst Ihr mir noch immer etwas für Eure Belohnung vorführen! Man kann nicht so einfach mit der Tradition brechen, nicht wahr... ?]], [[Ich denke, dass ein Stück Marzipan heute eine ordentliche Tanzvorstellung wert ist! Ja genau, zeigt mal, was Ihr so drauf habt! Tanzt für mich und ich werde Euch etwas von dem leckeren Marzipan geben!]], },
    { [[Tanzt für Gastwirtin Pala und Ihr werdet im Gegenzug etwas von dem Marzipan aus Donnerfels bekommen!]], },
  },
  [8361] = {
    [[Abyssische Kontakte]],
    { [[Wir haben Kultisten des Schattenhammers dabei beobachtet, wie sie mithilfe bizarrer Rituale mächtige Elementarfürsten beschworen haben. Noch wissen wir nicht, ob das die Wesen sind, die die Kultisten verehren, oder ob diese Wesen nur Botschafter einer weit größeren Macht sind. Eines wissen wir jedoch mit Sicherheit: von diesen Elementaren erhält der Kult seine Befehle.]], [[Ich möchte, dass Ihr versucht, mit einem abyssischen Templer Kontakt aufzunehmen. Wenn Ihr das getan habt, verpasst ihm eine ordentliche Tracht Prügel! Nichts stürzt eine Organisation so sehr ins Chaos, wie eine Unterbrechung der Befehlskette. Oh, und vergesst nicht, mir einen Beweis für Euren Erfolg zu bringen!]], },
    { [[Bor Wildmähne auf Burg Cenarius in Silithus möchte, dass Ihr einen abyssischen Templer vernichtet und ihm dessen abyssisches Wappen als Beweis für Eure Tat bringt.]], },
  },
  [8362] = {
    [[Abyssische Wappen]],
  },
  [8363] = {
    [[Abyssische Siegel]],
  },
  [8364] = {
    [[Abyssische Szepter]],
  },
  [8365] = {
    [[Piratenhüte Ahoy!]],
    { [[Ihr seid die Hilfe, die das Kartell entsandt hat? Ach, ich denke, Ihr werdet reichen. Hört mir genau zu, <class>. Wie Ihr wisst, bin ich die Lieferantin für modische Kleidung, die in den höchsten Kreisen der Gesellschaft Azeroths gefragt ist. Könige und Königinnen, Kriegshäuptlinge und... naja wie auch immer die Horde ihre Königinnen bezeichnet, all jene tragen, was ich als modisch deklariere. Und ich verdiene mir an ihnen eine goldene Nase, was natürlich das Beste am Ganzen ist.]], [[Also hierfür benötige ich Eure Hilfe. Lauft hinunter zur Mast- und Schotbucht und beschafft mir einen Stapel Piratenhüte, koste es was es wolle. Glaubt Ihr, Ihr schafft das, hmm?]], },
    { [[Sammelt 20 Südmeerpiratenhüte und bringt sie zurück zur eingebildeten Modeschöpferin beim Dampfdruckpier in Tanaris.]], },
  },
  [8366] = {
    [[Gaunerei und Erpressung im Südmeer]],
    { [[Argh! Sie verlangen das Unmögliche von mir!]], [[Das Dampfdruckkartell möchte, dass ich den Südmeerpiraten eine Lektion erteile. Ich kann doch aber nicht an zwei Orten gleichzeitig sein; Ich muss hierbleiben, um zumindest hier alles ruhig zu halten.]], [[Seit neuestem hält nicht mal mehr das Schutzgeld, dass das Kartell regelmäßig auszahlt, die Piraten auf. Daher möchte ich Euch zeitweilig abordnen, mal dort runter zu gehen und denen Saures zu geben.]], [[Ihr findet die Piraten in der Mast- und Schotbucht, die hier gleich in der Nähe gen Süden ist. Besten Dank, <name>, ich schulde Euch was!]], },
    { [[Sicherheitschef Bilgenritzel beim Dampfdruckpier in Tanaris beauftragt Euch, 10 Piraten der Südmeerfreibeuter, 10 Seeräuber der Südmeerfreibeuter, 10 Dockarbeiter der Südmeerfreibeuter und 10 Schwadroneure der Südseefreibeuter zu töten.]], },
  },
  [8367] = {
    [[Große Ehre]],
    { [[Bringt ein Ehrenabzeichen jedes Schlachtfelds zu einem Kriegshetzer der Horde in einer der Hauptstädte oder in Shattrath.]], },
    { [[Bringt ein Ehrenabzeichen jedes Schlachtfelds zu einem Kriegshetzer der Horde in einer der Hauptstädte oder in Shattrath.]], },
  },
  [8368] = {
    [[Schlacht der Kriegshymnenschlucht]],
    { [[Bringt 3 Ehrenabzeichen des Kriegshymnenklans zu einem Kriegshetzer der Horde, außerhalb der Schlachtfelder.]], },
    { [[Bringt 3 Ehrenabzeichen des Kriegshymnenklans zu einem Kriegshetzer der Horde, außerhalb der Schlachtfelder.]], },
  },
  [8369] = {
    [[Die Invasoren des Alteractals]],
    { [[Hört mir genau zu, <name>. Egal, was auch immer Ihr über die noch immer tobende Schlacht im Alteractal gehört habt, etwas davon ist wahr: Es war die Expeditionsarmee der Sturmlanzen, die diesen Konflikt durch die Besetzung des Territoriums der Frostwölfe entfachte. Der Tag, an dem wir es der Allianz erlauben, Gebiete der Horde zu erobern, ohne militärisch zurückzuschlagen, wird der Tag sein, an dem ich meine Axt an den Nagel hänge, um den Rest meines Lebens in Schande zu verbringen. Geht jetzt und schlagt die Invasoren zurück!]], },
    { [[Bringt 3 Ehrenabzeichen des Alteractals zu einem der Kriegshetzer der Horde, außerhalb der Schlachtfelder.]], },
  },
  [8370] = {
    [[Eroberung des Arathibeckens]],
    { [[Unser Konflikt mit der Allianz hat sich zu einem richtigen Krieg entwickelt. Es ist nicht länger notwendig unter falschen Vorwänden in die Schlacht zu ziehen, keine Seite erwartet dies noch länger.]], [[In der Schlacht um das Arathibecken geht es nur um eines, Ressourcen. Nicht mehr und nicht weniger. Entweder die Allianz erobert diese Ressourcen und benutzt sie gegen uns, oder wir kommen ihnen zuvor und verwenden die dortigen Ressourcenquellen für unsere Kriegspläne. <name>, helft den Entweihten dabei, die von uns benötigten Ressourcenlieferungen aufrechtzuerhalten!]], },
    { [[Bringt 3 Ehrenabzeichen des Arathibeckens zu einem Kriegshetzer der Horde, außerhalb der Schlachtfelder.]], },
  },
  [8371] = {
    [[Gemeinsames Bestreben]],
    { [[Bringt ein Ehrenabzeichen jedes Schlachtfelds zu einem Kriegshetzer der Horde in einer der Hauptstädte oder in Shattrath.]], },
    { [[Bringt ein Ehrenabzeichen jedes Schlachtfelds zu einem Kriegshetzer der Horde in einer der Hauptstädte oder in Shattrath.]], },
  },
  [8372] = {
    [[Kampf um die Kriegshymnenschlucht]],
    { [[Die Schildwachen der Silberschwingen sind auf Grund der Zerstörung, die die Orcs im Wald anrichten, im Krieg mit der Vorhut des Kriegshymnenklans. Allerdings gibt es noch andere Gründe, als die bloße Liebe zu der Natur, für die Verteidigung von ausgerechnet diesem Wald.]], [[Der Wald bildet eine strategische Barriere, die es einfach macht, das Eschental gegen jegliche Art von Großangriffen zu verteidigen. Ohne den Wald, würde Astranaar lediglich ein oder zwei Tage standhalten, bevor es vom Brachland einverleibt werden würde.]], [[Seid tapfer und schlagt Euch gegen die Vorhut des Kriegshymnenklans, <name>! Für die Allianz!]], },
    { [[Bringt 3 Ehrenabzeichen der Kriegshymnenschlucht zu einem Brigadegeneral der Allianz, außerhalb der Schlachtfelder.]], },
  },
  [8373] = {
    [[Die Kraft der Pinie]],
    { [[Was ich während der Schlotternächte am meisten hasse, sind die Stinkbomben, die die Verlassenen hier inmitten von Süderstade loslassen. Wer hätte gedacht, dass eine Gedenkfeier zur Freilassung mit einer so stinkigen Sache einhergehen würde?]], [[Verwendet diesen Stinkbombenreiniger während der Schlotternächte, um die scheußlichen Stinkbomben, die die Horde womöglich hier in der Stadt loslässt, zu vernichten. Er bekämpft, dank der Kraft von Pinien, schreckliche Gerüche.]], [[Helft mit Süderstade sauber zu halten und ich werde Euch dafür ein paar Schlotternachtssüßigkeiten geben, die Ihr sicherlich mögen werdet. Abgemacht?]], },
    { [[Verwendet einen Stinkbombenreiniger, um alle Stinkbomben der Verlassenen zu vernichten, die hier in Süderstade losgelassen wurden. Kehrt zu Unteroffizier Hartman in Süderstade zurück, wenn Ihr fertig seid.]], },
  },
  [8374] = {
    [[Inbesitznahme des Arathibeckens]],
    { [[Es gehört viel mehr dazu, einen Krieg zu gewinnen, als das bloße Überlisten des Gegners oder siegreiche Schlachten. Vielmehr erringt die Seite den Sieg, welche dauerhaft mehr Kriegsgüter als der Gegner produziert.]], [[Das Arathibecken, könnte sich als eine potentielle und äußerst ergiebige Ressourcenquelle für die Seite erweisen, die das Becken zuerst für sich beansprucht. Falls die Allianz den Sieg erringt, könnte man aus den Ressourcen hunderte neuer Waffen herstellen. Sollte allerdings die Horde gewinnen, würden diese Waffen zwar hergestellt, doch würde man sie anschließend auf uns richten! Jetzt ist es an der Zeit, die Allianz im Kampf um das Arathibecken zu unterstützen!]], },
    { [[Bringt 3 Ehrenabzeichen des Arathibeckens zu einem der Brigadegeneräle der Allianz, die außerhalb der Schlachtfelder stehen.]], },
  },
  [8375] = {
    [[Vergesst das Alteractal nicht!]],
    { [[Ihr liegt vollkommen richtig, <name>. Die Horde verkündet die Wahrheit, mit ihrer Behauptung, das Alteractal gehöre zum Territorium der Frostwölfe. Die Sturmlanzen kamen, auf der Suche nach Erzvorkommen und Relikten, mit friedlichen Absichten in das Tal. Die Frostwölfe jedoch, reagierten auf die Anwesenheit der Sturmlanzen mit einer, in der Allianz, bis dato unbekannten Brutalität und Aggressivität.]], [[Niemals dürfen wir all die Zwerge vergessen, die während des folgenden feigen und unangekündigten Angriffs zu Tode kamen. Geht nun, <name>, und tragt Euren Teil zur Schlacht im Alteractal bei. Für die Allianz!]], },
    { [[Bringt 3 Ehrenabzeichen des Alteractals zu einem der Brigadegeneräle der Allianz, die außerhalb der Schlachtfelder stehen.]], },
  },
