  [8409] = {
    [[Verdorbenes Bier]],
    { [[Die faulen Eier wurden wie geplant in den Braukessel geworfen. Zukünftige Ladungen Bier des Gasthauses von Süderstade sind somit erfolgreich verdorben worden!]], [[Ihr solltet jetzt zurück zu Ruferin der Dunkelheit Yanka beim Fest des Weidenmanns in Tirisfal gehen und ihr von Eurem Erfolg berichten.]], },
    { [[Kehrt zur Ruferin der Dunkelheit Yanka beim Fest des Weidenmanns in Tirisfal zurück.]], },
  },
  [8410] = {
    [[Elementarbeherrschung]],
    { [[Bath'rah der Windbehüter sucht nach einem Schamanen von Eurer Weisheit. Als Zeichen Eurer Bereitschaft bittet er Euch, ihm Proben eines jeden Elements zu bringen.]], [[Um Bath'rah zu finden, begebt Euch zu dem Fluss östlich von Tarrens Mühle. Folgt seinem Verlauf Richtung Norden, irgendwann trefft Ihr auf die Ruinen, in denen sich Bath'rah niedergelassen hat.]], },
    { [[Bath'rah der Windbehüter möchte, dass Ihr jeweils eine Probe Luft, Feuer, Erde und Wasser für ihn sammelt.]], },
  },
  [8411] = {
    [[Die Beherrschung der Elemente]],
    { [[Willkommen, <class>. Bringt mir zuerst die Elemente und dann unterhalten wir uns.]], [[Ich brauche erst das Feuer, die Luft, das Wasser und die Erde, bevor ich Euch vertraue, Mann. Ich muss wissen, ob Ihr gut unterrichtet seid.]], },
    { [[Bringt Bath'rah dem Windbehüter die Elemente Erde, Luft, Feuer und Wasser.]], },
  },
  [8412] = {
    [[Geistertotem]],
    { [[Die Elementare an diesem Ort haben sich gegen mich gewendet.]], [[Ihr kennt Euch mit den Mächten der Totems besser als jeder andere aus und ich brauche jetzt ein Geistertotem zu meinem Schutz. Wir haben bereits die Elemente, jetzt brauchen wir etwas mit noch mehr Natur. Etwas noch Wilderes.]], [[In den Westlichen Pestländern könnt Ihr Bären für Klauen und Spinnen für Augen töten. Mit etwas Glück, könnt Ihr beides auch bei den dort lebenden Aasgeiern finden, sie fressen alles.]], },
    { [[Bringt Bath'rah dem Windbehüter die Komponenten für das Geistertotem.]], },
  },
  [8413] = {
    [[Die Macht des Voodoos]],
    { [[Das Geistertotem alleine ist nicht stark genug. Um erfolgreich zu sein, benötigen wir noch etwas Voodoomagie.]], [[Die Trolle im Versunkenen Tempel besitzen Federn, die von mächtiger Voodoomagie erfüllt sind. Begebt Euch dorthin und besiegt Schlitzer, Mijan, Zolo, Hukku, Zul'lor und Loro. Mit ihren Federn haben wir genug Macht, um ein starkes Geistertotem zu formen.]], },
    { [[Bringt Bath'rah dem Windbehüter die Voodoofedern.]], },
  },
  [8414] = {
    [[Austreibung des Bösen]],
    { [[Seid gegrüßt, <class>. Kreuzfahrer wie Ihr werden in solch abgeschiedenen Gegenden gerne gesehen.]], [[Eure heilige Stärke wird gebraucht, um uns bei der Vernichtung der unzähligen Untoten hier zu helfen. Genau genommen bräuchten wir Euch, um eine große Anzahl von Geißelsteinen zu sammeln und sie anschließend zu Hohepriester Thel'danis zu bringen. Der Hohepriester bewacht Uthers Grabmal in Trauerhügel, nicht weit von hier.]], [[Nur jemand mit einer Anstecknadel der Argentumdämmerung kann darauf hoffen Geißelsteine zu finden, sprecht also mit Offizier Reinherz, wenn Ihr noch keine besitzt.]], },
    { [[Sammelt 20 Geißelsteine des Dieners und bringt sie zu Hohepriester Thel'danis in den Westlichen Pestländern.]], },
  },
  [8415] = {
    [[Zugwindlager]],
    { [[Kommandant Ashlam Ehrenschlag sucht nach einem Paladin Eurer Reinheit und Euren Mutes, <name>. Ihr könnt ihn im Zugwindlager im Süden der Westlichen Pestländer finden. Sucht nach ihm und helft ihm im Namen des Lichts!]], },
    { [[Sprecht mit Kommandanten Ashlam Ehrenschlag im Zugwindlager.]], },
  },
  [8416] = {
    [[Gereinigte Geißelsteine]],
    { [[Das Böse wurde aus diesen Steinen getrieben, <name>.]], [[Ich weiß über Kommandant Ashlams Pläne Bescheid, er wird nur nach einem einzigen, gereinigten Geißelstein verlangen. Bringt ihn zu ihm, damit wir die Geißel ins Feuer der Gerechtigkeit tauchen können.]], },
    { [[Bringt den gereinigten Geißelstein zu Kommandant Ashlan Ehrenschlag an der Zugwindspitze.]], },
  },
  [8417] = {
    [[Ein geplagter Geist]],
    { [[In den Verwüsteten Landen gibt es einen geplagten Geist, der einst ein großer Held der Horde war. Die Ehre eines Kriegers stirbt nicht mit seinem Körper und es würde für Euch von Wert sein mit ihm zu sprechen.]], },
    { [[Sprecht mit dem gefallenen Helden der Horde am Eingang zu den Verwüsteten Landen.]], },
  },
  [8418] = {
    [[Erschaffung des Steins der Macht]],
    { [[Jetzt müssen wir die gereinigten Geißelsteine kombinieren und eine Waffe herstellen, die die Reihen der Untoten erschüttert. Das ist keine einfache Aufgabe, <name>. Nur die Trolle des Versunkenen Tempels besitzen diese Art seltener Magie, die fähig ist, das Böse von etwas gegen sich selbst zu richten.]], [[Im Tempel sind die Trolle bekannt als Schlitzer, Mijan, Zolo, Hukku, Zul'lor und Loro. Verspürt keine Reue für ihre verderbten Seelen... sie sind nicht frei von Bösem.]], },
    { [[Beschafft Euch gelbe, blaue und grüne Voodoofedern von den Trollen im Versunkenen Tempel.]], },
  },
  [8419] = {
    [[Die Bitte eines Wichtels]],
    { [[<name>, ein Wichtel im Teufelswald namens Impsy hat nach Euch verlangt. Seine Bitte ist etwas merkwürdig, da er möchte, dass Ihr ihm ein Stück Teufelsstoff als Zeichen des guten Willens bringt. Bedenkt man die Macht, die Ihr bereits als Hexenmeister besitzt, kann ich nur vermuten, dass Ihr wisst, dass die Satyrn des Teufelswalds diesen Teufelsstoff bei sich tragen.]], [[Ihr findet Impsy nördlich der Blutgiftfälle, von denen ich hörte, dass sie um diese Jahreszeit recht schön sein sollen.]], },
    { [[Bringt ein Stück Teufelsstoff zu Impsy im Teufelswald.]], },
  },
  [8420] = {
    [[Heiß und brennend]],
    { [[Vielleicht klingt es ja etwas komisch bei einem Wichtel, aber ich würde gerne meinen eigenen Begleiter haben. Das hätte ich wirklich gerne. Es ist nicht gerecht, dass Niby mich hat und ich niemanden!]], [[Es braucht ja gar nicht lebendig zu sein. Ich bräuchte etwas, um mit der Anfertigung beginnen zu können, zum Beispiel etwas Teufelsstoff. Die Satyrn hier im Teufelswald haben manchmal Teufelsstoff, Ihr müsstet also nur genug von ihnen töten, um welchen zu finden. Das hört sich doch gut an, oder?]], },
    { [[Bringt ein Stück Teufelsstoff zu Impsy im Teufelswald.]], },
  },
  [8421] = {
    [[Das richtige Zeug]],
    { [[Jetzt, da ich meinen Teufelsstoff habe, kann ich wirklich mit der Anfertigung *meines* Begleiters beginnen.]], [[Ich werde etwas für die Augen brauchen. Vielleicht könnt Ihr mir etwas faules Holz vom Stamm der Eisenstämme besorgen, damit ich daraus ein Paar wunderschöne Augen schnitzen kann. Ihr findet sie im Norden, im Eisenwald.]], [[Ich brauche auch etwas Blutgiftessenz, um meinem Begleiter diesen neuen dämonischen Duft zu verleihen. Die besudelten Schlamme haben diese Essenz und Ihr könnt sie gleich hier in der Nähe von Niby und mir finden.]], },
    { [[Bringt 10 Stück faules Holz und 4 Blutgiftessenzen zu Impsy im Teufelswald.]], },
  },
  [8422] = {
    [[Federn von Trollen]],
    { [[Mein Begleiter ist fast fertig. Jetzt brauche ich nur noch etwas als Füllmaterial.]], [[Nur die Voodoofedern von den Trollen aus dem Versunkenen Tempel sind dafür geeignet. Sie heißen Schlitzer, Mijan, Zolo, Hukku, Zul'lor und Loro. Tötet sie. Tötet sie alle.]], [[Dies ist keine gewöhnliche Aufgabe <name>, also werde ich Eure Bemühungen belohnen, indem ich Euch etwas von Niby gebe - er wird es sowieso nicht mehr brauchen.]], },
    { [[Bringt 6 Voodoofedern von den Trollen aus dem Versunkenen Tempel.]], },
  },
  [8423] = {
    [[Kriegerverwandtschaft]],
    { [[<Der Held betrachtet Euch.>]], [[Ihr seid ein <class>, ohne Zweifel. Ich würde Euch um eine ehrenhafte Aufgabe bitten, aber zuerst muss ich Eure Stärke testen.]], [[Man sagt, dass Rexxar die Schärfe seiner Klingen misst, indem er einen Hölleneber mit nur einem einzigen, schnellen Schlag erlegt. Jetzt werde ich Eure Stärke auf die gleiche Art messen.]], },
    { [[Tötet 7 Hölleneber in den Verwüsteten Landen und kehrt zu dem gefallenen Helden der Horde zurück.]], },
  },
  [8424] = {
    [[Krieg den Schattenanbetern]],
    { [[Die weiterhin bestehende Gegenwart der Schattenanbeter in den Verwüsteten Landen ist schmerzhaft für mich. Ihre dunklen Rituale und Zauber bereiten mir heute mehr Qualen als es zu meinen Lebzeiten je etwas hätte tun können.]], [[Ich kann ihnen mit Eurer Hilfe eine schmerzhafte Botschaft überbringen. Zieht hinfort und lasst meinen Zorn sprechen.]], },
    { [[Merzt die Schattenanbeter in den Verwüsteten Landen aus und kehrt zum gefallenen Helden der Horde zurück.]], },
  },
  [8425] = {
    [[Voodoofedern]],
    { [[Eure Ehre hat mir zu ein wenig Frieden verholfen. Es ist mehr als gerecht, dass ich Euch etwas im Gegenzug dafür gebe.]], [[Meine Waffen und meine Rüstung waren zu meinen Lebzeiten prächtig, aber jetzt habe ich keine Verwendung mehr dafür. In Euren Händen würden sie einem besseren Zweck dienen.]], [[Reist zu dem Versunkenen Tempel und tötet die Trollwächter Schlitzer, Mijan, Zolo, Hukku, Zul'lor und Loro. Sie tragen magische Federn, die meinen physikalischen Körper lange genug neu bilden können, damit ich Euch belohnen kann.]], },
    { [[Bringt die Voodoofedern der Trolle im Versunkenen Tempel zu dem gefallenen Helden der Horde.]], },
  },
  [8426] = {
    [[Schlacht der Kriegshymnenschlucht]],
    { [[Bringt 3 Ehrenabzeichen des Kriegshymnenklans zu einem Kriegshetzer der Horde, außerhalb der Schlachtfelder.]], },
    { [[Bringt 3 Ehrenabzeichen der Kriegshymnenschlucht zu einem Kriegshetzer der Horde, außerhalb der Schlachtfelder.]], },
