  [8631] = {
    [[Gamaschen des Mysteriums]],
    { [[Ihr möchtet Euch also uns gegenüber bewähren, Sterblicher? Nun gut, vielleicht können wir uns ja gegenseitig helfen.]], [[Der uralte Sandwurm, Ouro, ist ein Geschöpf von legendärer Macht. Man sagt, dass er von dem Alten Gott selbst als Gespött des Lebens erschaffen wurde.]], [[Seine Haut ist unnatürlich... dick, fast unbezwingbar und dennoch flexibel, was ihm ermöglicht, sich mit der Geschwindigkeit und Grazie eines kleineren Lebewesens zu bewegen.]], [[Bringt mir eine Probe seiner Haut sowie andere geringfügigere Bestandteile und ich werde mächtige Gamaschen für Euch herstellen.]], },
    { [[Bringt Ouros intakte Haut, 2 Götzen der Weisen, 5 Silberskarabäen und 5 Knochenskarabäen zu Kandrostrasz innerhalb von Ahn'Qiraj. Diese Aufgabe erfordert zum Abschluss auch einen freundlichen Ruf bei der Brut Nozdormus.]], },
  },
  [8632] = {
    [[Reif des Mysteriums]],
    { [[Die Zwillingsimperatoren... ihr unaufhörliches Flüstern martert langsam meinen Verstand. Sie verhöhnen mich, wissend, dass ich nicht tiefer in ihr Reich vordringen kann, um meinen Bruder Arygos zu retten.]], [[In meinen Visionen sehe ich ihre monströsen Fratzen, die mit mächtigen Kronen geschmückt sind. Ich werde meine Vergeltung noch bekommen... ich werde diese Symbole der Qirajimacht in solche verwandeln, die unserem Nutzen gerecht werden.]], [[Bringt mir die benötigten Bestandteile, <name>. Ich werde Euch dafür ein Kopfstück von unvergleichlicher Macht geben.]], },
    { [[Bringt Vek'nilashs Reif, 2 Götzen der Nacht, 5 Bronzeskarabäen und 5 Elfenbeinskarabäen zu Andorgos in Ahn'Qiraj. Diese Aufgabe erfordert zum Abschluss auch einen freundlichen Ruf bei der Brut Nozdormus.]], },
  },
  [8633] = {
    [[Roben des Mysteriums]],
    { [[Der unaussprechliche Alte Gott erwacht langsam aus seinem Schlummer unterhalb Ahn'Qirajs. Das schreckliche Schicksal, das Azeroth erwartet, könnte in Tagen oder auch Jahren Wirklichkeit werden, Zeit spielt für Wesen seiner Macht keine Rolle.]], [[Stehen die Sterne günstig, wird er furchtbaren Zorn gegen alle Lebewesen walten lassen. Ihr müsst dem zuvorkommen und einen Weg finden, um seiner Existenz ein Ende zu bereiten.]], [[Tut dies und bringt mir ein Stück seiner Hülle. Ich werde Euch eine Rüstung gewähren, die noch kein Sterblicher getragen hat... Rüstung für einen Helden, der einen Gott bezwungen hat!]], },
    { [[Bringt die Hülle des Alten Gottes, 2 Götzen der Sonne, 5 Goldskarabäen und 5 Tonskarabäen zu Vethsera innerhalb von Ahn'Qiraj. Diese Aufgabe erfordert zum Abschluss auch einen wohlwollenden Ruf bei der Brut Nozdormus.]], },
  },
  [8634] = {
    [[Stiefel des Mysteriums]],
    { [[Qirajimagie ist sehr stark und uralt. Die mächtigsten Qiraji handhaben Gegenstände, die so machtvoll sind, dass sie sogar einem Drachen Einhalt gebieten.]], [[Bringt mir die Dominanzbindungen der Qiraji sowie andere geringfügigere Bestandteile und ich werde ein Paar mächtige Stiefel für Euch herstellen.]], [[Ihr werdet sie im Kampf gegen den Feind als nützlich empfinden.]], },
    { [[Bringt die Dominanzbindungen der Qiraji, 2 Götzen der Sonne, 5 Silberskarabäen und 5 Kristallskarabäen zu Kandrostrasz in Ahn'Qiraj. Diese Aufgabe erfordert auch einen neutralen Ruf bei der Brut Nozdormus.]], },
