  [8728] = {
    [[Die gute und die schlechte Nachricht]],
    { [[Das hat ein wenig länger gedauert als erwartet. Nun lasst mich sehen, was dieses Buch zu berichten hat.]], [[<Leise vor sich hermurmelnd beginnt Narain das Buch zu lesen.>]], [[Die gute Nachricht ist, dass ich mir zu 99% sicher bin, eine Arkanitboje konstruieren zu können, die voll und ganz Euren Anforderungen entspricht. Kommen wir zur schlechten Nachricht: Ich brauche eine gehörige Menge Arkanit, Elementiumerz und Edelsteine.]], [[Sobald Ihr mir diese Materialien gebracht habt, kann ich mit der Konstruktion der Boje beginnen.]], },
    { [[Narain Pfauentraum in Tanaris möchte, dass Ihr ihm 20 Arkanitbarren, 10 Elementiumerz, 10 Azerothische Diamanten und 10 blaue Saphire bringt.]], },
  },
  [8729] = {
    [[Der Zorn von Neptulon]],
    { [[Hatte ich die schlechte Neuigkeit schon zur Sprache gebracht? Sein Name ist "Der weiße Riese": Dreißig Meter Tod und Zerstörung pur. Den kleinen Leckerbissen hatte ich glatt vergessen.]], [[Meinen gnomischen Superpsychokräften nach zu urteilen, befindet sich der beste Ort, um diese Boje zu platzieren, an der Küste Azsharas in der Bucht der Stürme.]], [[Haltet nach einem wirbelnden Mahlstrom Ausschau. Vermutlich ist die ganze Gegend mit Wrackteilen übersät.]], [[Macht es gut und nehmt ein paar Freunde mit.]], [[Falls Ihr die ganze Sache wie durch ein Wunder überleben solltet, bringt den Szeptersplitter zu Anachronos.]], },
    { [[Benutzt die Arkanitboje am wirbelnden Mahlstrom in der Bucht der Stürme in Azshara.]], },
  },
  [8730] = {
    [[Nefarius' Verderbnis]],
    { [[Held, seid Ihr es? Es ist tausend Jahre her, seitdem man mir den Splitter anvertraut hat. Jetzt, in meiner finstersten Stunde soll jemand erscheinen, um mich davon zu entlasten... aber was wäre eine Tragödie ohne Grausamkeit?]], [[<Vaelastrasz hustet geschwächt.>]], [[Ne... Nefarius besitzt nun den Szeptersplitter.]], [[Die Zeit ist entscheidend. Nefarius hat vor den Splitter zu zerstören. Ihr müsst Euch beeilen!]], },
    { [[Tötet Nefarian und bringt den roten Szeptersplitter wieder in Euren Besitz. Bringt den roten Szeptersplitter zu Anachronos in den Höhlen der Zeit in Tanaris. Euch bleiben 5 Stunden, um diese Aufgabe zu erfüllen.]], },
  },
  [8731] = {
    [[Felddienst]],
    { [[Seid gegrüßt, <name>. Bevor ich Euch mit umfassenderen Aufgaben in diesem Krieg beauftrage, müsst Ihr Euren Anteil am Felddienst leisten.]], [[Ihr könnt die Orgrimmarlegion von Hauptmann Schädelspalt außerhalb des Regalschwarmbaus finden.]], [[Erstattet ihm Bericht und kommt mit dem Beweis für Euren Felddienst zu mir zurück. Ich werde wichtigere Anweisungen für Euch bereithalten.]], [[Oh, bitte haltet Euch von der Eisenschmiedebrigade fern. Es herrschen große Spannungen zwischen unseren Freiwilligen der Allianz und der Horde.]], },
    { [[Erstattet Krug Schädelspalt am Posten der Orgrimmarlegion beim Regalschwarmbau Bericht. Bereitet Eure nicht unterzeichneten Felddienstpapiere vor, lasst sie unterzeichnen und bringt sie dann zu Windrufer Kaldon auf Burg Cenarius.]], [[Hinweis: das Heilen oder Wirken von begünstigenden Zaubern auf ein Mitglied der Orgrimmarlegion wird Euch für den PvP-Kampf markieren.]], },
  },
  [8732] = {
    [[Felddienstpapiere]],
  },
  [8733] = {
    [[Eranikus, der Tyrann des Traums]],
    { [[Dies ist der Weg der Begleichung, Held. Würde ich heute getötet werden, dann würde ich für den gerechten Zweck sterben. Ihr müsst den grünen Szeptersplitter wiedererlangen. Ihr müsst Eranikus von der Besudelung, die die Alten Götter über ihn verhängt haben, reinigen.]], [[Reist nach Darnassus. Ihr werdet einen meiner Agenten genau vor den Stadtmauern finden. Dieser Agent wird die Dinge für uns in Bewegung setzen und Tyrande über unsere Pläne informieren, ohne Hirschhaupt zu alarmieren.]], [[<Malfurion seufzt.>]], [[Ein Bruch mit der in tausend Jahren geschaffenen Gunst...]], },
    { [[Reist nach Teldrassil und sucht Malfurions Agenten irgendwo außerhalb der Stadtmauern von Darnassus auf.]], },
  },
  [8734] = {
    [[Tyrande und Remulos]],
    { [[Tyrande wird benachrichtigt, Sterblicher. Shan'do Sturmgrimm hat mich für diesen Fall vorbereitet.]], [[Ihr müsst jetzt zur Mondlichtung reisen und mit Remulos sprechen. Er wird weitere Anweisungen bezüglich der Herbeirufung von Eranikus haben.]], [[Wir alle wünschen Euch viel Glück.]], },
    { [[Reist zur Mondlichtung und sprecht mit Bewahrer Remulos.]], },
  },
  [8735] = {
    [[Die Verderbnis des Alptraums]],
    { [[Ihr müsst zu den vier Traumportalen von Azeroth reisen und Euch jeweils ein Verderbnisfragment des Alptraums beschaffen. Die Drachkin, die in dieser Gegend hausen, werden derartige Fragmente bei sich tragen. Kommt zu mir zurück, sobald Ihr diese Fragmente eingesammelt habt.]], },
    { [[Reist zu den vier Portalen des smaragdgrünen Traums in Azeroth und sammelt dort jeweils ein Verderbnisfragment des Alptraums ein. Kehrt zu Bewahrer Remulos bei der Mondlichtung zurück, sobald Ihr diese Aufgabe erfüllt habt.]], },
  },
  [8736] = {
    [[Der Alptraum offenbart sich]],
    { [[Verteidigt Nachthafen gegen Eranikus. Bewahrer Remulos darf dabei nicht ums Leben kommen und Eranikus darf nicht getötet werden. Verteidigt Euch. Erwartet Tyrande.]], },
    { [[Verteidigt Nachthafen gegen Eranikus. Bewahrer Remulos darf dabei nicht ums Leben kommen und Eranikus darf nicht getötet werden. Verteidigt Euch. Erwartet Tyrande.]], },
  },
  [8737] = {
    [[Azurblauer Templer]],
    { [[Die Schattenhammerkultisten und die von ihnen verehrten Elementarfürsten stellen eine dauerhafte Bedrohung für unsere Operationen in Silithus dar. Trotz der neuen Gegnermassen in Ahn'Qiraj können wir es uns nicht erlauben, unsere Wachposten und Einsätze gegen den äußeren Feind zu verringern.]], [[Findet einen Weg, um einen azurblauen Templer auf unsere Existenzebene zu beschwören und zerstört ihn.]], },
    { [[Beschwört und vernichtet einen azurblauen Templer. Erstattet anschließend Bericht bei Bor Wildmähne auf Burg Cenarius. Ihr benötigt auch den taktischen Einsatz I, um diese Aufgabe abschließen zu können.]], },
  },
  [8738] = {
    [[Späherbericht des Regalschwarms]],
    { [[Wir haben unsere besten Späher bis weit in die Schwarmbauten der Silithiden ausgesendet, um Informationen zu sammeln. Späher Landion hat die Aktivitäten der Silithiden im Regalschwarm nun schon seit Tagen beobachtet. Findet ihn und besorgt Euch einen schriftlichen Bericht. Wir müssen so schnell wie möglich auf diese Informationen reagieren können, <name>.]], },
    { [[Setzt Euch mit dem cenarischen Späher Landion innerhalb des Regalschwarmbaus in Verbindung und bringt den Späherbericht des Regalschwarms zu Windruferin Prachthorn auf Burg Cenarius. Ihr benötigt auch den taktischen Einsatz VII, um diese Aufgabe abschließen zu können.]], },
  },
  [8739] = {
    [[Späherbericht des Ashischwarms]],
    { [[Wir haben unsere besten Späher bis weit in die Schwarmbauten der Silithiden ausgesendet. Späherin Jalia hat die Aktivitäten der Silithiden im Ashischwarm nun schon seit Tagen beobachtet. Findet sie und besorgt Euch einen schriftlichen Bericht. Wir müssen so schnell wie möglich auf diese Informationen reagieren können, <name>.]], },
    { [[Setzt Euch mit der cenarischen Späherin Jalia innerhalb des Ashischwarmbaus in Verbindung und bringt den Späherbericht des Ashischwarms zu Windruferin Prachthorn auf Burg Cenarius. Ihr benötigt auch den taktischen Einsatz VIII, um diese Aufgabe abschließen zu können.]], },
  },
  [8740] = {
    [[Marodeure des Schattenhammers]],
    { [[Es wurden mehrfach berittene Gruppen des Schattenhammers gesichtet, die außerhalb der Reichweite unserer Truppen in Burg Cenarius kleinere Patrouillen und Karawanen angreifen.]], [[Findet und vernichtet die Zwielichtmarodeure, gemeinsam mit ihrer Anführerin, einer furchterregenden Kriegerin namens Morna. Meldet Euch anschließend bei Windruferin Prachthorn.]], },
    { [[Tötet Zwielichtmarodeurin Morna und 2 Zwielichtmarodeure. Erstattet anschließend Bericht bei Windruferin Prachthorn. Ihr benötigt auch den taktischen Einsatz IX, um diese Aufgabe abschließen zu können.]], },
  },
  [8741] = {
    [[Der Held kehrt zurück]],
    { [[Bringt den grünen Szeptersplitter zu Anachronos in den Höhlen der Zeit.]], },
    { [[Bringt den grünen Szeptersplitter zu Anachronos in den Höhlen der Zeit.]], },
  },
  [8742] = {
    [[Die Macht von Kalimdor]],
    { [[A thousand years has passed and just as it was fated, one stands before me. One who shall lead their people to a new age.]], [[The Old God trembles, <name>. Oh yes, it fears your faith. Shatter the prophecy of C'Thun.]], [[It knows you come, champion - and with you comes the might of Kalimdor. You have only to let me know when you are prepared and I shall grant you The Scepter of the Shifting Sands.]], },
  },
