  [8827] = {
    [[Winters Geschenke]],
    { [[Ich habe gehört, dass Altvater Winter mit großzügiger Unterstützung von Kokelwälder für jeden Geschenke unter den Weihnachtsbaum von Eisenschmiede gelegt hat. Wie gerne würde ich mich auf dem Weg machen, doch leider muss ich mich noch um den PX-238 Winterwundervolt kümmern.]], [[Ihr solltet dort auf jeden Fall mal vorbeischauen. Ich bin mir sicher, dass Altvater Winter auch einige Geschenke für Euch mitgebracht hat.]], },
    { [[Sprecht mit Altvater Winter. Er befindet sich in der Nähe des Verkaufsbereichs von Kokelwälder in Eisenschmiede.]], },
  },
  [8828] = {
    [[Winters Geschenke]],
    { [[Ich habe gehört, dass Altvater Winter mit großzügiger Unterstützung von Kokelwälder für jeden Geschenke unter den Weihnachtsbaum von Orgrimmar gelegt hat. Wie gerne würde ich mich auf dem Weg machen, doch leider muss ich mich noch um den PX-238 Winterwundervolt kümmern.]], [[Ihr solltet dort auf jeden Fall mal vorbeischauen. Ich bin mir sicher, dass Altvater Winter auch einige Geschenke für Euch mitgebracht hat.]], },
    { [[Sprecht mit Altvater Winter. Er befindet sich in der Nähe des Verkaufsbereichs von Kokelwälder in Orgrimmar.]], },
  },
  [8829] = {
    [[Die ultimative Täuschung]],
    { [[Die Anhänger des Schattenhammers operieren in zahllosen geheimen und gut getarnten Gruppen. Während der Kult unsere Welt durch zahllose Aktionen plagt, werden alle Operationen an einem uns immer noch unbekannten Ort geplant.]], [[Wir sind zu der Erkenntnis gelangt, dass die Steine, mit denen die Kultisten des Schattenhammers ihre Anführer beschwören, in beide Richtungen funktionieren. Um einen der Steine ohne Gefahr benutzen zu können, werden wir versuchen, die Robe eines Abgesandten des Schattenhammers bis ins kleinste Detail nachzubilden.]], [[Beschafft die dafür benötigten Materialien und bringt sie zu Aurel Goldblatt, <name>.]], },
    { [[Bringt 1 Schattenhaut, 3 ausgefranste Monstrositätenstickereien und 1 Robe eines Schattenhammerkultisten zu Aurel Goldblatt auf Burg Cenarius in Silithus. Ihr benötigt auch Logistikeinsatz XI, um diese Aufgabe abschließen zu können.]], },
