  [8856] = {
    [[Wüstenüberlebenspaket]],
    { [[Seitdem ständig neue Rekruten auf Burg Cenarius eintreffen, stehen wir vor dem Problem, sie in der Wüste am Leben zu erhalten. Der ständige Wasserentzug und die zahlreichen giftigen Kreaturen in dieser Gegend sind eine ernstzunehmende Gefahr für die unerfahren Soldaten. Sammelt alles, was notwendig ist, um ein Wüstenüberlebenspaket zusammenzustellen und bringt die erforderlichen Vorräte zu Calandrath auf Burg Cenarius.]], },
    { [[Bringt 4 Kugeln des Wassers, 4 mächtige Gegengifte und 4 gebackene Lachse zu Calandrath in der Taverne von Burg Cenarius in Silithus.]], },
  },
  [8857] = {
    [[Geheimnisse des Kolosses - Ashi]],
    { [[Ich suche nach schlachterprobten Abenteurern, die für mich eine Fragmentprobe vom Koloss des Ashischwarms zu Glotz in Beutebucht bringen. Nein, nein, Ihr braucht Euch nicht einmal in die Nähe dieses Giganten zu wagen. Nehmt einfach eine von meinen Proben und beeilt Euch. Verrottet nutzt sie Glotz nicht viel!]], },
    { [[Bringt das Fragment vom Koloss des Ashischwarms zu Glotz Widrikus in Beutebucht.]], },
  },
  [8858] = {
    [[Geheimnisse des Kolosses - Regal]],
    { [[Ich würde nichts lieber tun, als Schlickfaust dieses verdammte Fragment in sein fettes Maul zu stopfen. Stattdessen stecke ich hier fest und sammle Proben von dieser zu groß geratenen Kakerlake! In Anbetracht ihrer Größe, werde ich hier wohl noch eine Weile beschäftigt sein.]], [[Nehmt diese Probe und bringt sie für mich zu Schlickfaust... und beeilt Euch, <class>. Er zahlt auch sehr gut!]], [[Also, ab zur Thoriumspitze in die Sengende Schlucht.]], },
    { [[Bringt das Fragment vom Koloss des Regalschwarms zu Aufseher Schlickfaust in die Sengende Schlucht.]], },
  },
  [8859] = {
    [[Geheimnisse des Kolosses - Zora]],
    { [[Bringt das Fragment vom Koloss des Zoraschwarms zu Lord Maxwell Tyrosus an der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern.]], },
    { [[Bringt das Fragment vom Koloss des Zoraschwarms zu Lord Maxwell Tyrosus an der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern.]], },
  },
  [8860] = {
    [[Neujahrsfeier!]],
    { [[In Sturmwind wird das neue Jahr ganz groß gefeiert! Den ganzen Tag lang gibt es kostenlosen Hochprozentigen und nachts ein fantastisches Feuerwerk! Das wird sicher ein Riesenspaß... allerdings findet heute auch in Beutebucht eine Neujahrsfeier statt. Ich wette, dort ist sogar noch mehr los!]], [[Egal! Jedenfalls braucht Gastwirtin Allison in Sturmwind eine Ladung Kokelwälder Festtagsspeisen, um alle Partylöwen satt in das neue Jahr zu bringen. Würdet Ihr mir den Gefallen tun und die Vorräte für mich abliefern?]], [[Danke, <name>! Ihr findet Gastwirtin Allison in der Güldenen Rose, drüben im Handelsdistrikt von Sturmwind.]], },
    { [[Bringt die Vorräte von Kokelwälder zu Gastwirtin Allison in Sturmwind.]], },
  },
  [8861] = {
    [[Neujahrsfeier!]],
    { [[In Donnerfels wird das neue Jahr ganz groß gefeiert! Den ganzen Tag lang gibt es kostenlosen Hochprozentigen und nachts ein fantastisches Feuerwerk!]], [[Das wird sicher ein Riesenspaß... allerdings findet auch in Beutebucht eine Neujahrsfeier statt. Ich wette, dort ist sogar noch mehr los!]], [[Egal! Jedenfalls braucht Gastwirtin Pala eine Ladung Kokelwälder Festtagsspeisen, um alle Partylöwen satt in das neue Jahr zu bringen. Würdet Ihr mir den Gefallen tun und die Vorräte für mich abliefern?]], [[Danke, <name>! Ihr findet Gastwirtin Pala in der Taverne nahe der Brücke zur Anhöhe der Jäger.]], },
    { [[Bringt die Vorräte von Kokelwälder zu Gastwirtin Pala in Donnerfels.]], },
  },
  [8862] = {
    [[Elunes Kerze]],
  },
  [8863] = {
    [[Festtagsklöße]],
    { [[If you are hungry, then you have come to the right place my friend!]], [[Dumplings are the traditional food of the Lunar Festival; it has been served since the time of the first Lunar Festival, and I am pleased to be able to offer it at this one.  In exchange for just a single coin of ancestry, I will share with you a sampling of one of the tastiest dishes you will ever enjoy!]], },
  },
  [8864] = {
    [[Festtagskleider des Mondfests]],
    { [[I have a selection of dresses that may be of interest to you.  Such dresses have been worn in times past during the Lunar Festival, and the selection I have today is perhaps the finest I have ever seen.  Each is hand-crafted with only the finest and softest of components.]], [[In exchange for some coins of ancestry, I will let you choose one of the three styles I have to keep as your own.  Is this acceptable to you?]], },
  },
  [8865] = {
    [[Festliche Hosenanzüge des Mondfests]],
    { [[I am please to inform you that I have a fine selection of festive pant suits available for you to own... but only in trade for coins of ancestry.  If fashion is important to you, then these are what you're looking for!  I assure you that you will not find pant suits like these anywhere else.]], [[In exchange for some Coins of Ancestry, I will let you choose one of the three styles I have to keep as your own.  Is this acceptable to you?]], },
  },
  [8866] = {
    [[Urahne Bronzebart]],
  },
  [8867] = {
    [[Mondfeuerwerk]],
    { [[Die Druiden der Mondlichtung feiern jedes Jahr den großen Triumph unserer Stadt über ein uraltes Übel.]], [[Wir ehren die Weisheit unserer Urahnen, nehmen an herrlichen Festgelagen teil und... schießen natürlich Feuerwerk ab!]], [[Für eine kleine Spende könnt Ihr bei unserem Verkäufer farbenfrohe Raketen erwerben. Versucht doch mal einen der hier herumstehenden Zünder, <name>.]], },
    { [[Zündet 8 Raketen und 2 Raketenbündel und kehrt dann zu einer Botin des Mondfests in irgendeiner Hauptstadt zurück.]], },
  },
  [8868] = {
    [[Elunes Segen]],
    { [[Das Biest Omen war einstmals ein bekannter Held. Als ein Wolf von großer Stärke und Geschicklichkeit, kämpfte er im Krieg der Ahnen Seite an Seite mit den Helden dieser Zeit, bis er den dämonischen Mächten verfiel. Sein Hass richtete sich gegen seine einstigen Verbündeten und auf seinem Rachefeldzug durch Azeroth verbreitete er Tod und Verderben.]], [[Letztlich besiegte man ihn hier, in den Landen der Mondlichtung, wo er bis heute auf dem Grund des Sees von Elune'ara ruht. Doch jedes Jahr, während des Neujahrfests, erwacht er zu neuem Leben...]], [[Schart Eure Freunde um Euch! Zieht los, beschwört und besiegt das Biest Omen. Erlangt Elunes Segen!]], },
    { [[Beschwört Omen, besiegt ihn und erlangt Elunes Segen. Kehrt anschließend zu Valadar Sternensang in Nachthafen zurück.]], },
  },
  [8869] = {
    [[Süße Beschaulichkeit]],
    { [[Begebt Euch nach Stratholme und tötet den purpurroten Hammerschmied. Nehmt die Schürze des purpurroten Hammerschmiedes und kehrt zu Lilith zurück.]], },
    { [[Begebt Euch nach Stratholme und tötet den purpurroten Hammerschmied. Nehmt die Schürze des purpurroten Hammerschmiedes und kehrt zu Lilith zurück.]], },
  },
  [8870] = {
    [[Das Mondfest]],
    { [[Es ist mal wieder soweit, <name>! Der Zirkel des Cenarius lädt alle Bewohner Azeroths dazu ein, gemeinsam mit uns, das Mondfest zu feiern. Der Ort, an dem Ihr die örtliche Botin des Mondfests finden könnt, wird durch einen Kreis aus heiligem Mondlicht markiert. Sie vermag Euch ausführlicher über dieses Fest zu berichten.]], },
    { [[Sprecht mit der Botin des Mondfests im Mystikerviertel von Eisenschmiede. Die Boten in den anderen Hauptstädten können Euch ebenfalls weiterhelfen.]], },
  },
