  [8876] = {
    [[Kleine Raketen]],
    { [[I hold the secrets to creating Lunar Festival rockets, and I am happy to share my knowledge with the honored.]], [[In exchange for coins of ancestry, I can offer you the recipe to make a small rocket...]], },
  },
  [8877] = {
    [[Zünder für Feuerwerk]],
    { [[Although rockets are a wonderful way to celebrate, in order to see their glory... they must be fired from a launcher.]], [[Bring me coins of ancestry and I can offer you the knowledge to create rocket launchers like the ones you see in Moonglade.]], },
  },
  [8878] = {
    [[Festtagsrezepte]],
    { [[The dresses and pant suits you see are traditional clothes from many years past.  They are worn in honor of old ways and loved ones gone.]], [[If you bring me coins of ancestry, <name>, then I will give you the pattern to create traditional red dresses or suits.]], },
  },
  [8879] = {
    [[Große Raketen]],
    { [[Large Festival rockets light up the sky brilliantly - only the cluster rockets rival their magnificence!]], [[Bring me coins of ancestry, <name>, and I shall share the knowledge of their creation.]], },
  },
  [8880] = {
    [[Raketenbündel]],
    { [[Cluster rockets are a collection of individual rockets, and their beauty is awesome!  Bring me coins of ancestry and I will share the knowledge of their creation to you...]], },
  },
  [8881] = {
    [[Große Raketenbündel]],
  },
  [8882] = {
    [[Zünder für Raketenbündel]],
  },
  [8883] = {
    [[Valadar Sternensang]],
    { [[Jetzt, da Ihr in Besitz der Mondfesteinladung seid, könntet Ihr doch einmal zur Mondlichtung reisen - Ihr werdet dort viele Feiernde vorfinden! In Nachthafen erwarten Valadar Sternensang und seine Schwester Fariel bereits diejenigen, die die Feierlichkeiten kaum erwarten können.]], [[Reist zur Mondlichtung mithilfe des Mondlichts und sprecht mit Valadar Sternensang; schaut Euch um und erfahrt welche Preise Ihr während des Fests gewinnen könnt!]], },
    { [[Verwendet die Mondfesteinladung im Mondlicht und reist somit zur Mondlichtung. Sprecht nach Eurer Ankunft in Nachthafen mit Valadar Sternensang.]], },
  },
  [8884] = {
    [[Fischköpfe, Fischköpfe...]],
    { [[Seid gegrüßt, <class>. Was, frage ich Euch, könnt Ihr erkennen, wenn Ihr in Richtung Küste seht? Richtig, unser Strand... unsere Stille Küste wurde vom Übel, den Murlocs, befallen.]], [[Dieser Makel darf nicht bestehen bleiben!]], [[Ich habe eine Aufgabe für Euch. Solltet Ihr bereit dazu sein, so würde ich ihre Köpfe haben wollen! Mal sehen, ob wir ihre Zahl derart verringern können, dass sie nicht mehr anders können, als in die dunklen Tiefen zurückzukehren, aus denen sie gekommen sind!]], },
    { [[Sammelt 8 Köpfe von Grimmschuppenmurlocs und bringt sie zu Hathvelion Sonnenblick im Immersangwald auf der Klippe, die die Stille Küste überblickt.]], },
  },
  [8885] = {
    [[Mmmrrrgggllls Ring]],
    { [[Die Grimmschuppenmurlocs haben sich durch Eure Taten nicht vertreiben lassen. Ich glaube aber zu wissen, was sie endlich in die Flucht schlagen wird, und wie ich mich gleichzeitig persönlich an ihnen rächen kann.]], [[Sie haben einen Murlochäuptling, der sich selbst Mmmrrrggglll nennt. Er wandert entlang des Goldenen Strandes südlich der Flussmündung umher. Dies Biest hat einst einen meiner Kameraden getötet und trägt seitdem seinen Ring als offenes Zeichen der Verachtung und des Spotts.]], [[Vernichtet Mmmrrrggglll und bringt mir den Ring meines Freundes wieder. Das sollte diese Monster ein für alle Mal einschüchtern!]], },
    { [[Erlangt Mmmrrrgggllls Ring von dem Häuptling der Grimmschuppen und kehrt damit anschließend zu Hathvelion Sonnenblick im Immersangwald auf der Klippe, die die Stille Küste überblickt, zurück.]], },
  },
  [8886] = {
    [[Piraten der Grimmschuppen!]],
    { [[Wie gut, dass Ihr hier seid, <class>! Diese schleimigen Grimmschuppenmurlocs haben meine Fracht gestohlen! Als wenn es nicht schon schlimm genug wäre, dass die Getriebenen mir meine letzten magischen Güter geraubt haben - und jetzt das! Was ist nur aus diesem Land geworden?]], [[Könnt Ihr mir meine Fracht wiederbeschaffen? Ich erwarte zwar nicht, dass Ihr alles zurückerlangt, bin aber für jedes Stück dankbar. Es waren die Grimmschuppenmurlocs in den Hütten im Westen, südlich des Ankerplatzes am Goldenen Strand. Ich bin mir ganz sicher!]], },
    { [[Erlangt 6 Stücke von Kapitänin Kelisendras Fracht wieder und bringt sie zu Kapitänin Kelisendra beim befestigten Lager direkt östlich des Ankerplatzes der Sonnensegel im Immersangwald zurück.]], },
  },
  [8887] = {
    [[Kapitänin Kelisendras verlorene Seekarten]],
    { [[Interessant... was Ihr hier gefunden habt, ist eine wasserdichte Lederhülse, die ein paar Seiten Pergamentpapier enthält. Nach den Kennzeichnungen zu urteilen, handelt es sich eindeutig um Seekarten. Jede dieser Karten trägt das Siegel von Kapitänin Kelisendra.]], [[Ihr habt sicher schon von einem kleinen, befestigten Lager östlich des nahegelegenen Ankerplatzes der Sonnensegel gehört. Vielleicht findet Ihr die Kapitänin ja dort?]], },
    { [[Bringt Kapitänin Kelisendras verlorene Seekarten zu Kapitänin Kelisendra beim befestigten Lager direkt östlich des Ankerplatzes der Sonnensegel im Immersangwald zurück.]], },
  },
  [8888] = {
    [[Der Lehrling des Magisters]],
    { [[<class>, hört Euch mein Anliegen an. Auf Grund von Dingen, die ich jetzt nicht weiter erklären kann, habe ich beschlossen, meine nahegelegene Schule zu schließen. Vor ein paar Tagen habe ich eine meiner Schülerinnen, Lehrling Loralthalis, mit diesem Auftrag dorthin geschickt. Seitdem habe ich nichts mehr von ihr gehört.]], [[Werdet Ihr zum Nachtschimmerturm gehen und nachsehen, ob alles mit ihr in Ordnung ist? Ihr werdet den Turm finden, indem Ihr dem Pfad, der von der Zuflucht der Weltenwanderer aus nach Norden führt, folgt und dann bei der Weggabelung rechts abbiegt.]], },
    { [[Sprecht mit Lehrling Loralthalis nahe des Nachtschimmerturms im Immersangwald.]], },
  },
  [8889] = {
    [[Deaktivierung des Turms]],
    { [[Wie Ihr wisst, hat mich Magister Nachtschimmer hierher zurückgeschickt, damit ich die Kraftquellen, die den Turm versorgen, deaktiviere. Es gibt drei davon, sie befinden sich an unterschiedlichen Stellen im Innern des Turms.]], [[Unglücklicherweise scheine ich zu spät zu sein, denn der Nachtschimmerturm wurde von seltsamen, magischen Kreaturen überrannt. Ich habe nicht das benötigte Wissen, um diese Aufgabe zu erfüllen, aber <ein/eine><class> <class> wie Ihr könnte es doch sicherlich ohne größere Probleme?]], },
    { [[Deaktiviert 3 Kraftquellen des Nachtschimmerturms im Nachtschimmerturm. Bringt anschließend das Deaktivierungsjuwel zu Lehrling Loralthalis im Immersangwald.]], },
  },
  [8890] = {
    [[Nachricht vom Turm]],
    { [[<name>, ich hätte noch eine letzte Bitte an Euch. Würdet Ihr Magister Nachtschimmer bei der Zuflucht der Weltenwanderer über die Ereignisse berichten, die hier vorgefallen sind? Sagt ihm, dass es mir gut geht, aber betont bitte auch, dass einige Lehrlinge zu Getriebenen geworden sind.]], [[Vielen Dank für Eure Hilfe!]], },
    { [[Kehrt zu Magister Nachtschimmer bei der Zuflucht der Weltenwanderer im Immersangwald zurück.]], },
  },
  [8891] = {
    [[Aufgegebene Untersuchungen]],
    { [[Ihr habt ein Buch mit Untersuchungsberichten gefunden, das offenbar von Magister Nachtschimmer zurückgelassen wurde. Ihr überfliegt rasch den Inhalt und stellt mit Entsetzen fest, dass man versucht hat, verderbte magische Ströme zu filtern, was womöglich eine Erklärung für die Ereignisse hier beim Nachtschimmerturm sein könnte. Der Magister möchte dies sicherlich zurück.]], },
    { [[Bringt Magister Nachtschimmers Tagebuch zu Magister Nachtschimmer bei der Zuflucht der Weltenwanderer im Immersangwald zurück.]], },
  },
  [8892] = {
    [[Vorfall beim Ankerplatz der Sonnensegel]],
    { [[Der Ankerplatz der Sonnensegel wurde von einem Haufen Getriebener überrannt. Sobald wir Verstärkung erhalten, werden wir diesen Verbrechern schon zeigen, wer hier das Sagen hat, <name>.]], [[In der Zwischenzeit könntet Ihr uns helfen, indem Ihr ihnen etwas Disziplin beibringt. Geht zu ihnen und macht deutlich, dass man mit uns keine Spielchen treibt. Der Ankerplatz der Sonnensegel liegt genau westlich von Morgenluft.]], },
    { [[Tötet 5 getriebene Rohlinge und 5 getriebene Straßendiebe und erstattet Waldläufer Degolien im Immersangwald Bericht.]], },
  },
  [8893] = {
    [[Der Super-Ei-o-Mat]],
  },
  [8894] = {
    [[Sicherung des Grunds]],
    { [[Ich kann Euch sagen, es ist furchtbar! Seltsame Dinge gehen im Turm vor sich, der Magister und seine Lehrlinge sind weggelaufen und wie Ihr wisst, wurde der gesamte Grund von magischen Bestien überrannt! Und wer sagte Wyllithen Bescheid? Niemand, so sieht es aus!]], [[<class>, Ihr müsst mir helfen, hier wieder für Ordnung zu sorgen... ich kann nicht länger über den Grund wachen, solange diese 'Anomalien' hier in blinder Wut herumlaufen!]], },
    { [[Tötet 6 Manaschlangen und 6 Ätherbestien. Kehrt anschließend zu Grundbewahrer Wyllithen beim Nachtschimmergrund im Immersangwald zurück.]], },
  },
  [8895] = {
    [[Lieferung an das Sanktum des Nordens]],
    { [[Es freut mich, dass Ihr Euch zumindest in gewissen Hinsichten als fähig erweist, <name>. Ich gebe Euch eine weitere Aufgabe, da Ihr so begierig scheint, Euch beweisen zu wollen.]], [[Dies ist ein Brief für Leybewahrer Caidanis, der Arkanist, der für die Aufrechterhaltung des Sanktums des Nordens verantwortlich ist. Geht vom Falkenplatz Richtung Südwesten und Ihr werdet ihn finden. Er ist eine sehr wichtige Persönlichkeit, ich hoffe also, dass Ihr ihn nicht mit zu belanglosem Geschwätz belästigt.]], },
    { [[Überbringt den unbeschrifteten Brief an Leybewahrer Caidanis beim Sanktum des Nordens im Immersangwald.]], },
  },
  [8896] = {
    [[Der Zwergenspion]],
    { [[Seid Ihr sicher, dass Ihr diese Notizen bei einer Nachtelfe gefunden habt? Das meiste davon wurde in Zwergisch verfasst...]], [[Ausgrabungsleiter Ambossel! Er wurde von Eisenschmiede wegen eines diplomatischen Auftrags hierher geschickt. Ich hätte es wissen müssen, sein Interesse an unseren arkanen Sankten war etwas zu groß. <name>, findet den Zwerg im Sanktum des Nordens und kümmert Euch um ihn. Oh, bitte geht diskret dabei vor.]], },
    { [[Findet und tötet Ausgrabungsleiter Ambossel im Sanktum des Nordens und berichtet anschließend Aeldon Sonnenmal im Immersangwald.]], },
  },
  [8897] = {
    [[Liebste Colara]],
    { [[Wenn ich das Tor bewache muss ich ständig an meine geliebte Colara denken. Ich kann sie immer nur für ein paar kurze Stunden sehen, wenn ich Glück habe. Ach, seht mich doch an. Ich erhalte nur einen niederen Offizierssold und jeden Tag könnte ich an die Front abberufen werden.]], [[Würde sie mich dann einfach vergessen? Ich meine, es gibt so viele wohlhabende Männer in der Stadt.]], [[Hier, ich habe einige Dinge aufgeschrieben... Nicht viel, aber vielleicht könntet Ihr diese Nachricht meiner Liebsten überbringen. Mein Dank wäre Euch gewiss.]], },
    { [[Bringt Leutnant Heldrics sorgfältig verfasste Notiz zu Colara Dean außerhalb der Bank von Sturmwind.]], },
  },
  [8898] = {
    [[Liebste Colara]],
    { [[Als ich geschäftlich mit der Bank in Sturmwind zu tun hatte, traf ich ein Mädchen. Sie hat mir auf Anhieb mein Herz gestohlen. Ich kann einfach nicht mehr aufhören, an sie zu denken! Ich würde Ihr gerne diese Nachricht schicken. Ganze Zwölfmal musste ich sie neu schreiben!]], [[Jetzt möchte ich sicherstellen, dass sie meinen Brief auch erhält.]], [[Vielleicht reist Ihr ja nach Sturmwind und könnt mir weiterhelfen?]], },
    { [[Bringt den zerknitterten Brief zu Colara Dean außerhalb der Bank in Sturmwind.]], },
  },
  [8899] = {
    [[Liebste Colara]],
    { [[Wie soll ich ihre Schönheit nur beschreiben? Tausende Jahre voll der Poesie und mir fehlen die Worte.]], [[Diese Sterblichen verwässern den Geist und am Ende bleiben wir als plappernde Narren zurück.]], [[Zu lange habe ich diese Worte immer und immer wieder in mich aufgenommen. Es ist mir unmöglich, sie weiter zu verfeinern. Nein, sie muss diese Nachricht noch heute erhalten, oder ein anderer Verehrer wird sie für sich einnehmen.]], },
    { [[Bringt den makellosen Brief zu Colara Dean außerhalb der Bank von Sturmwind.]], },
  },
  [8900] = {
    [[Liebste Elenia]],
    { [[Sie war so traurig. Ich habe es genau gespürt, trotz Ihrer Versuche es zu verbergen. Ich meine, sie wirkte so traurig, versteht Ihr?]], [[Da war diese Verbindung zwischen uns beiden, als ich mit ihr sprach. Niemand hatte mich bisher verstanden, doch sie tat es auf Anhieb.]], [[Als ich es den anderen erzählte, lachten sie nur über mich und so habe ich nie wieder mit ihr gesprochen. Doch ich vermisse sie. Würdet Ihr mir einen Gefallen tun und ihr diesen Brief von mir geben? Ich will sicher sein, dass sie ihn auch erhält.]], },
    { [[Bringt die leicht zerknitterte Notiz zu Elenia Haydon nahe der Bank in Unterstadt.]], },
  },
  [8901] = {
    [[Liebste Elenia]],
    { [[Habt Ihr jemals wahre Liebe erfahren, <junger Herr/junge Dame>? Noch vor Kurzem war dies ein Fremdwort für mich, doch dann traf ich dieses junge Mädchen aus Unterstadt, Elenia. Es ist selten für einen Angehörigen meiner Art, sich mit den anderen Völker einzulassen, ganz besonders mit den Verlassenen.]], [[Doch jetzt sind meine Gedanken Tag und Nacht von ihr erfüllt.]], [[Wäre es Euch vielleicht möglich, ihr diesen Brief zu überbringen? Ich möchte, dass sie hierher nach Mulgore kommt. Hoffentlich sagt sie ja.]], },
    { [[Bringt den sorgfältig verfassten Brief zu Elenia Haydon nahe der Bank in Unterstadt.]], },
  },
  [8902] = {
    [[Liebste Elenia]],
    { [[Zu Lebzeiten hatte ich geschworen, Elenia, meine Jugendliebe, zu meiner Frau zu nehmen. Es ist schon Jahre her, seit ich das letzte Mal an sie denken musste. Doch seit einiger Zeit beherrschen mehr und mehr Gedanken an sie meinen Verstand.]], [[Obwohl es eine Ewigkeit her ist, sollte es in unser beider Leben noch etwas Raum für unsere Liebe geben.]], [[Ich bin sicher, auch sie wird sich verändert haben. Sei es drum! Wäre es Euch vielleicht möglich, ihr diesen Brief zu überbringen? Ich selbst bin zu nervös, um ihr selbst gegenüberzutreten.]], },
    { [[Bringt den liebevoll verfassten Brief zu Elenia Haydon nahe der Bank in Unterstadt.]], },
  },
  [8903] = {
    [[Gefährliche Liebschaft]],
    { [[Besorgt die Karte einer Wache von Sturmwind und bringt sie zu Aristan Mottar in Sturmwind.]], },
    { [[Besorgt die Karte einer Wache von Sturmwind und bringt sie zu Aristan Mottar in Sturmwind.]], },
  },
  [8904] = {
    [[Gefährliche Liebschaft]],
    { [[Diese Liebesepidemie hat etwas Unnatürliches an sich. Sie ist abscheulich und hat sie erst einmal unsere Verteidigung geschwächt, könnte dies zu einer ernsten Bedrohung für uns alle werden.]], [[Schlimm genug, dass bereits so viele unserer Leute von diesem lächerlichen Verhalten befallen sind. Ich denke, selbst unsere Wächter hat es bereits erwischt, dabei sollten sie gegen derartige Einflüsse immun sein.]], [[Geht zu einem der Wächter und seht, ob er von dieser Dummheit befallen wurde.]], },
    { [[Besorgt eine modrige Karte des Wächters und bringt sie zu Fenstad Argyle in Unterstadt.]], },
  },
  [8905] = {
