  [8989] = {
    [[Das rechte Stück von Lord Valthalaks Amulett]],
    { [[Ein Freund von mir, Mor Grauhuf, war der Erste, der Lord Valthalaks Amulett und dem darin enthaltenen, korrumpierenden Seelenfragment zum Opfer fiel. Nachdem wir Valthalaks Zauberbuch und Amulett gestohlen hatten, flohen wir um unser Leben aus dem oberen Teil der Schwarzfelsspitze. Während unserer überstürzten Flucht fiel Mor in den unteren Teil der Schwarzfelsspitze hinab.]], [[Wir erfuhren später, dass er den Fall überlebt hatte und von Kriegsmeister Voone gefangen genommen und gefoltert worden war. Später schlichen sich spektrale Assassine in die Schwarzfelsspitze ein und erschlugen Mor vor Voones Augen.]], },
    { [[Benutzt das Räuchergefäß der Beschwörung, um den Geist von Mor Grauhuf zu beschwören und zu vernichten. Kehrt dann mit Lord Valthalaks zusammengesetzten Amulett und dem Räuchergefäß der Beschwörung zu Bodley im Schwarzfels zurück.]], },
  },
  [8990] = {
    [[Das rechte Stück von Lord Valthalaks Amulett]],
    { [[Wir erfuhren später, während wir alle unser Bestes taten, um zu überleben, dass Isalien, eine Nachtelfenpriesterin, die unserer Gruppe angehörte, in die Heimatstadt der Urahnen der Nachtelfen zurückgekehrt war - jetzt als Düsterbruch bekannt. Demzufolge, was uns berichtet wurde, zog es sie sowohl aus persönlichen Gründen dorthin zurück, als auch weil sie die Geheimnisse ihres Stücks des Amuletts ergründen wollte.]], [[Allerdings gelang es ihr nicht, dieses Wissen zu erlangen. Wie uns zugetragen wurde, wurde sie am Schrein von Eldretharr von Alzzin dem Wildformer und seinen Schergen in einen Hinterhalt gelockt.]], },
    { [[Benutzt das Räuchergefäß der Beschwörung, um den Geist von Isalien zu beschwören und zu vernichten. Kehrt dann mit Lord Valthalaks zusammengesetzten Amulett und dem Räuchergefäß der Beschwörung zu Bodley im Schwarzfels zurück.]], },
  },
  [8991] = {
    [[Das rechte Stück von Lord Valthalaks Amulett]],
    { [[Die Geschwister, Jarien und Sothos, beschlossen, sich dem Scharlachroten Kreuzzug anzuschließen, nachdem sich unser Söldnerbund aufgelöst hatte. Das rechte Stück von Valthalaks Amulett nahmen sie mit sich. Ich war froh, sie los zu sein, aber jetzt brauchen wir dieses Stück zurück.]], [[Unseren Informationen zufolge, wurden sie vom Obersten Kreuzfahrer Dathrohan höchstpersönlich hingerichtet, da sie die Initiationsriten in seiner Kammer in der Scharlachroten Bastion nicht bestanden hatten.]], [[Wie dem auch sei, wir... und damit meine ich Ihr, müssen dieses Stück des Amuletts zurückbekommen.]], },
    { [[Benutzt das Räuchergefäß der Beschwörung, um die Geister von Jarien und Sothos zu beschwören und zu vernichten. Kehrt dann mit Lord Valthalaks zusammengesetzten Amulett und dem Räuchergefäß der Beschwörung zu Bodley im Schwarzfels zurück.]], },
  },
  [8992] = {
    [[Das rechte Stück von Lord Valthalaks Amulett]],
    { [[Jetzt, wo wir alles beisammen haben, was wir benötigen, wird es Zeit, uns darüber zu unterhalten, wen Ihr als Nächstes beschwören werdet.]], [[Kormok war für einen Oger ganz okay, bis er sich der Nekromantie zuwandte. Von da ab wurde es für jeden im Bund ganz schön gruselig. Es überraschte also niemanden, dass er mit seinem Stück des Amuletts in sein neues Zuhause verschwand, Scholomance.]], [[Aber selbst dort, oder vielleicht gerade dort, war er nicht vor den spektralen Assassinen sicher. Sie erschlugen ihn vor den Augen seines Meisters, Ras Frostraunen.]], },
    { [[Benutzt das Räuchergefäß der Beschwörung, um den Geist von Kormok zu beschwören und zu vernichten. Kehrt dann mit Lord Valthalaks zusammengesetzten Amulett und dem Räuchergefäß der Beschwörung zu Bodley im Schwarzfels zurück.]], },
  },
  [8993] = {
    [[Geschenkübergabe]],
  },
  [8994] = {
    [[Letzte Vorbereitungen]],
    { [[Wir sind nun beinah bereit, <class>. Ich brauche nur noch einige weitere Komponenten, um das Räuchergefäß darauf abzustimmen, Lord Valthalak herbeizurufen.]], [[Für den Anfang würde ich Euch darum bitten, Euch in die Schwarzfelsspitze zu begeben und die dort heimischen Orcs zu töten. Ihre Armschienen enthalten Metallspuren, die ich benötigen werde. Da diese Armschienen nur Spuren des Metalls enthalten, werde ich eine Menge von ihnen benötigen. Um den Abstimmungsprozess abzuschließen, brauche ich weiterhin noch ein Fläschchen der obersten Macht.]], [[Viel Glück, <name>!]], },
    { [[Bringt Bodley im Schwarzfels 40 Schwarzfelsarmschienen und ein Fläschchen der obersten Macht.]], },
  },
  [8995] = {
    [[Mea Culpa, Lord Valthalak]],
    { [[Die nötigen Vorbereitungen sind nun getroffen, <name>.]], [[Zu seinen Lebzeiten bewohnte Lord Valthalak die Kammer, in der sich nun die Bestie aufhält. Geht dorthin und benutzt dann das Räuchergefäß der Beschwörung, um Lord Valthalak herbeizurufen. Es wird ihn wiederbeleben, also werdet Ihr ihn erneut töten müssen. Es wäre sinnvoll, zuerst die Bestie zu erledigen. Bringt außerdem genug Kameraden mit, um Euch durch den oberen Teil der Schwarzfelsspitze zu kämpfen.]], [[Benutzt, sobald Ihr ihn ausgeschaltet habt, das Amulett bei seiner Leiche. Nachdem seine Seele wieder vollständig ist, wird er es vermutlich zurückverlangen.]], [[Viel Glück!]], },
    { [[Benutzt das Räuchergefäß der Beschwörung, um Lord Valthalak zu beschwören. Macht ihn unschädlich und benutzt dann Lord Valthalaks Amulett bei seiner Leiche. Danach werdet Ihr dem Geist von Lord Valthalak sein Amulett zurückgeben müssen.]], },
  },
  [8996] = {
    [[Rückkehr zu Bodley]],
    { [[Ich werde Euch Eure Unverschämtheit dieses eine Mal vergeben, <class>, da Ihr heute eine noble Tat vollbracht habt. Ihr habt mich wieder mit dem Rest meiner gestohlenen Seele vereint. Obgleich Eurer offensichtlich schwachen Natur, <race>, habt Ihr den Mut bewiesen, über den jene, die es wagten mich zu bestehlen, nicht verfügten und vermutlich niemals werden.]], [[Kehret schnell zu ihnen zurück, bevor ich es mir anders überlege und Euch dort niederstrecke, wo Ihr steht. Kehret zurück und richtet ihnen aus, dass sie vor mir sicher sind... Momentan zumindest.]], },
    { [[Bringt das Räuchergefäß der Beschwörung zurück zu Bodley im Schwarzfels.]], },
  },
  [8997] = {
    [[Zurück zum Anfang]],
    { [[Ich denke, nun wird es Zeit Lebewohl zu sagen, mein Freund. Ich werde immer hier sein, falls Ihr von Zeit zu Zeit mal mit mir reden wollt. Vielleicht ja, während Ihr auf Eure Kameraden wartet um der Schwarzfelsspitze einen Besuch abzustatten.]], [[Geht, bevor ich noch anfange zu heulen. Geht zu Deliana in Eisenschmiede zurück und erzählt ihr, was vorgefallen ist.]], },
    { [[Sprecht mit Deliana am Hohen Sitz von Eisenschmiede.]], },
  },
  [8998] = {
    [[Zurück zum Anfang]],
    { [[Ich denke, nun wird es Zeit Lebewohl zu sagen, mein Freund. Ich werde immer hier sein, falls Ihr von Zeit zu Zeit mal mit mir reden wollt. Vielleicht ja, während Ihr auf Eure Kameraden wartet um der Schwarzfelsspitze einen Besuch abzustatten.]], [[Geht, bevor ich noch anfange zu heulen. Geht zu Mokvar in Orgrimmar zurück und erzählt ihm, was vorgefallen ist.]], },
    { [[Sprecht mit Mokvar in der Feste Grommash im Tal der Weisheit von Orgrimmar.]], },
  },
  [8999] = {
    [[Das Beste gibt's zum Schluss]],
    { [[Das ist ja eine unglaubliche Geschichte, <name>! Seid Euch meines Dankes gewiss, und der aufrichtigen Dankbarkeit all derer, die in dieses Abenteuer verwickelt waren. Vielleicht kann ich nun endlich wieder mit geschlossenen Augen schlafen.]], [[Nun zu Eurer Belohnung. Ich habe hier einige Dinge, an denen Ihr gewiss Gefallen finden werdet.]], },
    { [[Gebt Deliana Eure Gugel des Wildherzens und Eure Weste des Wildherzens.]], },
  },
