  [9015] = {
    [[Die Herausforderung]],
    { [[Hört mir gut zu, <name>. Damit das Banner die gewünschte Wirkung erzielen kann, werdet Ihr Euch in den Ring des Gesetzes der Schwarzfelstiefen begeben müssen. Lasst Euch von Oberrichter Grimmstein verurteilen und errichtet das Banner als Zeichen der Herausforderung.]], [[Euer Opfer wird sich nicht zurückhalten können und sich, von dem Wunsch Euch zu bekämpfen beseelt, in den Kampf stürzen. Kehrt zu Anthion zurück, sobald Ihr das habt, wonach es Euch begehrt.]], },
    { [[Reist zum Ring des Gesetzes der Schwarzfelstiefen und errichtet das Banner der Provokation in dessen Mitte, während Ihr von Oberrichter Grimmstein verurteilt werdet. Tötet Theldren und seine Gladiatoren und kehrt dann mit dem ersten Stück von Lord Valthalaks Amulett zu Anthion Harmon in den Östlichen Pestländern zurück.]], },
  },
  [9016] = {
    [[Anthions Abschiedsworte]],
    { [[Valthalaks Seele wurde in diesem Amulett verwahrt. In unserer Gier haben wir es wie Narren in drei Teile aufgeteilt ohne um den Fluch zu wissen, der uns heimsuchen würde.]], [[Die einzige Möglichkeit, den Fluch aufzuheben, besteht darin das Amulett wieder zusammenzusetzen. Kehrt zu Mokvar zurück und sagt ihm, dass Bodley unsere einzige Hoffnung ist, die einzelnen Teile wiederzufinden. Mit ihm könnt Ihr auch über eine eventuelle Belohnung sprechen.]], [[Ich selbst kann nun endlich in Frieden ruhen. Ich wünsche Euch viel Glück, Ihr werdet es dringend brauchen.]], },
    { [[Kehrt mit einem Paar Stiefel des Wildherzens, einem Kilt des Wildherzens und einer Schiftung des Wildherzens zu Mokvar in Orgrimmar zurück.]], },
  },
