  [9023] = {
    [[Das perfekte Gift]],
    { [[Mein Bruder und ich sind im Rabenholdtanwesen für den Giftbedarf zuständig.]], [[Lord Rabenholdt hat mich damit beauftragt, eine neue und extrem effektive Giftmischung zusammenzustellen, die aus den Giftbeuteln der Bewohner von Zul'Gurub und den Ruinen von Ahn'Qiraj gewonnen werden kann.]], [[Es gibt in diesen verfluchten Ruinen zwei ganz besonders giftige Bestien, die vielleicht genau das haben, wonach wir suchen, <class>. Bringt mir einen Giftbeutel der Bestien Kurinnaxx und Venoxis und wir wären Euch ewig dankbar.]], },
    { [[Dirk Donnerholz in der Burg Cenarius will, dass Ihr ihm Venoxis' Giftbeutel und Kurinnaxx' Giftbeutel bringt.]], },
  },
  [9024] = {
    [[Aristans Verdacht]],
    { [[Ich hab's! Habt Ihr den neuen Duft in den Straßen aufgeschnappt? Das neue Duftwasser und Parfüm, das gerade so angesagt ist? Unsere Wachen würden ihre Pflichten nicht so einfach vernachlässigen.]], [[Zumindest nicht, solange ihre Sinne nicht von fauler Alchemie vernebelt wurden.]], [[Es gibt nur einen Weg, um ganz sicher zu sein - bringt Morgan Pestle einen Flakon von dem Parfüm und dem Duftwasser, und klärt ihn über meinen Verdacht auf. Ich werde Euch für Eure Ausgaben entschädigen, sobald Ihr zurückkommt.]], },
    { [[Bringt 1 Duftwasserflakon und 1 Parfümflakon zu Morgan Pestle im Handelsdistrikt von Sturmwind.]], },
  },
  [9025] = {
    [[Morgans Entdeckung]],
    { [[Nun... es sieht ganz so aus, als ob Aristan doch noch auf etwas gestoßen sein könnte. Ich habe Spuren von Substanzen gefunden, die in Liebestränken und Gedanken verwirrenden Gebräuen verwendet werden.]], [[Es ist nicht ungewöhnlich, dass man in Parfüm derartige Spuren auffindet, allerdings sind einige der Zutaten, die ich in den Düften finden konnte, doch recht... merkwürdig.]], },
    { [[Kehrt zu Aristan Mottar zurück, er befindet sich vor der Bank im Handelsdistrikt von Sturmwind.]], },
  },
  [9026] = {
    [[Verfolgung der Spur]],
    { [[Wir müssen der Ursache auf den Grund gehen. Ich habe viel mehr Leute als sonst - einschließlich Wachen - im Gasthaus 'Die güldene Rose' ein- und ausgehen sehen. Sprecht mit Allison, sollte sie verkaufen, so kann sie Euch zu der Herkunft der Ware Auskunft geben.]], },
    { [[Sprecht mit Gastwirtin Allison im Gasthaus 'Die güldene Rose' im Handelsdistrikt von Sturmwind.]], },
  },
  [9027] = {
    [[Verfolgung der Spur]],
    { [[Irgendwas mit dem Duftwasser? Das ist möglich. Ich habe noch nie gesehen, dass eine Ware so begehrt war wie diese.]], [[Hört mal, sollte es etwas damit auf sich haben, dann möchte ich es auch wissen. Ich habe meine Lieferung von einem Händler namens Evert Sorisam. Er ist neu in der Stadt.]], [[Er erwähnte, dass er einige Waren hätte, die er bei 'Das feinste Band' abliefern müsse.]], },
    { [[Sprecht mit Evert Sorisam bei 'Das feinste Band' entlang der Kanäle Sturmwinds.]], },
  },
  [9028] = {
    [[Lüftung des Geheimnisses]],
    { [[Was? Verrat? Nein, nein. Ich versichere Euch, dass meine Absichten in jeder Hinsicht harmlos sind.]], [[Ist es denn so falsch, denen helfen zu wollen, die dieses leidvolle Ritual des Umwerbens zu erleichtern wünschen? Kommt schon, das ist doch kein Verbrechen.]], [[Ich werde Euch beweisen, dass ich keine schlechten Absichten hege, indem ich Euch meine Quelle verrate.]], [[Die Lieferung wurde mir von einem Staffron Lerent zugestellt, so eine Art Apotheker. Ich habe nie mit ihm gesprochen.]], [[Unser Goblinmittelsmann meinte, dass er im Vorgebirge des Hügellands gearbeitet hätte, irgendwo hinter dem unheimlichen Rabenholdtanwesen. Ich wünsche Euch viel Glück bei der Suche nach ihm.]], },
    { [[Findet Apotheker Staffron Lerent hinter dem Rabenholdtanwesen im Vorgebirge des Hügellands.]], },
  },
  [9029] = {
    [[Ein blubbernder Kessel]],
  },
  [9030] = {
    [[Anthions Abschiedsworte]],
    { [[Valthalaks Seele wurde in diesem Amulett verwahrt. In unserer Gier haben wir es wie Narren in drei Teile aufgeteilt ohne um den Fluch zu wissen, der uns heimsuchen würde.]], [[Die einzige Möglichkeit, den Fluch aufzuheben, besteht darin das Amulett wieder zusammenzusetzen. Kehrt zu Deliana zurück und sagt ihr, dass Bodley unsere einzige Hoffnung ist, die einzelnen Teile wiederzufinden.]], [[Ich selbst kann nun endlich in Frieden ruhen. Ich wünsche Euch viel Glück, Ihr werdet es brauchen.]], },
    { [[Sprecht mit Deliana in Eisenschmiede.]], },
  },
  [9032] = {
    [[Bodleys trauriges Schicksal]],
    { [[Das Amulett enthielt Lord Valthalaks Seele? Dann ist es nicht weiter verwunderlich, dass es mit einem solch machtvollen Fluch belegt war.]], [[Bodley aufzufinden, könnte sich allerdings schwierig gestalten. Als ich das letzte Mal von ihm hörte, wollte er in die Schwarzfelsspitze zurückkehren. Seitdem hat niemand jemals wieder etwas von ihm gehört.]], [[Sucht ihn in der Schwarzfelsspitze, aber ich befürchte, dass er bereits tot ist. Ich würde Euch empfehlen, den Apparat des Goblins mitzunehmen.]], },
    { [[Reist zum Schwarzfels und benutzt den extradimensionalen Geisterdetektor, um Bodley nahe der Schwarzfelsspitze zu finden.]], },
  },
  [9033] = {
    [[Echo des Krieges]],
    { [[Fünf Jahre sind seit dem letzten Krieg vergangen...]], [[Zehntausende mussten dabei ihr Leben lassen - und für was? Die Bedrohung wurde niemals gänzlich ausgelöscht.]], [[Jetzt verbreitet der Lichkönig erneut Angst und Schrecken... sein getreuer Jünger, Kel'Thuzad, zieht seine Truppen für einen weiteren Angriff auf unser Land zusammen.]], [[Wir werden es nicht zulassen! Beweist Euer Engagement. Zeigt, dass Ihr gewillt seid, Leib und Leben zu riskieren, um diesen Wahnsinn aufzuhalten! Die vereinigte Macht der Argentumdämmerung und des Kreuzzugs wird Euch mit allen Mitteln zur Seite stehen.]], [[Geht nach Naxxramas und zerstört die Geißel...]], },
    { [[Kommandant Eligor Morgenbringer bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern möchte, dass Ihr 5 lebende Monstrositäten, 5 Steinhautgargoyles, 8 Hauptmänner der Todesritter und 3 Giftpirscher tötet.]], },
  },
  [9034] = {
    [[Brustplatte des Schreckenspanzers]],
    { [[Solltet Ihr während Euren Kämpfen gegen die Geißel in Naxxramas auf entweihte Rüstungsteile und kampfzerschlissene Plattenstücke stoßen, bringt sie zu mir, und ich werde Euch ein Rüstungsteil anfertigen, das eines Heldens würdig ist.]], },
    { [[Korfax bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern wird für Euch eine Brustplatte des Schreckenspanzers anfertigen, wenn Ihr ihm folgende Gegenstände bringt: 1 entweihte Brustplatte, 25 kampfzerschlissene Plattenstücke, 4 Arkanitbarren und 2 Nexuskristalle.]], },
  },
  [9035] = {
    [[Hinterhalt am Straßenrand]],
    { [[He, Ihr da! Ihr müsst mir helfen, bitte!]], [[Ich und ein anderer Lehrling wurden von den Getriebenen angegriffen! In ihrem wahnsinnigen Hunger nach Magie haben sie alles mitgenommen, was sie in ihre Hände bekamen: Manakristalle, Zauberstäbe, Zauberbücher... alles!]], [[Meledor ist ihnen nachgejagt, ich musste zurückbleiben und auf die wenigen Dinge aufpassen, die sie nicht gestohlen haben. Geht weiter die Straße entlang und seht nach, ob Ihr ihm behilflich sein könnt.]], },
    { [[Sprecht mit Lehrling Meledor im Immersangwald entlang der Straße, die südlich von Silbermond wegführt.]], },
  },
