  [9059] = {
    [[Handschützer des Gruftpirschers]],
    { [[Ich habe eine Verwendung für den Panzer der Gruftbestie gefunden. Ich bin in der Lage, ein extrem leichtes und äußerst tödliches Rüstungsset anzufertigen, indem ich die Fragmente von Gruftbestien mit einigen entweihten Rüstungsteilen aus Naxxramas kombiniere.]], [[Bringt mir die benötigten Gegenstände und ich werde eine Rüstung für Euch anfertigen, wie man sie auf dieser Welt noch nicht gesehen hat!]], },
    { [[Jäger Leopold bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern wird für Euch Handschützer des Gruftpirschers anfertigen, wenn Ihr ihm folgende Gegenstände bringt: 1 Paar entweihte Handschützer, 8 kampfzerschlissene Kettenstücke, 1 Arkanitbarren und 5 geschmeidige unverwüstliche Bälge.]], },
  },
