  [9076] = {
    [[Getriebener Rädelsführer]],
    { [[Die Getriebenen sind so auf den Verzehr von fauler Magie konzentriert, sie haben nicht einmal gemerkt, dass wir den Großteil unserer Waffen wieder an uns gebracht haben. Wir können uns allerdings nicht auf ihren Zustand der Benommenheit verlassen. Es ist an der Zeit für einen ausschlaggebenden Angriff. Lasst uns anhand ihres Anführers Aldaron ein Exempel statuieren. Er wurde im obersten Teil des Gebäudes beim Ankerplatz gesichtet. Bringt mir seinen Kopf, ich werde Euch entsprechend entlohnen.]], },
    { [[Bringt Aldarons Kopf zu Velendris Weißflor nahe des Ankerplatzes der Sonnensegel im Immersangwald.]], },
  },
  [9077] = {
    [[Brustplatte der Knochensense]],
    { [[Wenn Ihr etwas von der Knochensensenrüstung wollt, dann werdet Ihr die Materialien dafür auftreiben müssen.]], [[Geht nach Naxxramas, der schwebenden Metropole des Todes, hier oben im Himmel, und sucht nach entweihter Rüstung sowie kampfzerschlissenen Rüstungsstücken. Bringt alles mitsamt der Materialien zum Zusammenfügen zurück und Ihr bekommt Eure Knochensensenrüstung. Oh, Ihr werdet mich natürlich auch für meine Mühen entschädigen müssen... Ich stelle die Knochen zur Verfügung.]], },
    { [[Rohan der Assassine bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern wird für Euch eine Brustplatte der Knochensense anfertigen, wenn Ihr ihm folgende Gegenstände bringt: 1 entweihte Brustplatte, 25 kampfzerschlissene Lederstücke, 2 Arkanitbarren und 6 geschmeidige unverwüstliche Bälge.]], },
