  [9116] = {
    [[Handschuhe des Glaubens]],
    { [[<Vater Montoy vollführt vor Euch ein paar merkwürdige Gesten in der Luft.>]], [[<Gesegneter Junge/Gesegnetes Mädchen>, habt Ihr die Gewänder der Gefallenen gefunden? Die armen Seelen, die ihre letzten Atemzüge auf dem Schlachtfeld ausgehaucht haben - um alles beraubt, einschließlich ihrer Würde.]], [[Bringt mir die entweihten Überbleibsel der Verstorbenen sowie die Reagenzien zur Läuterung... auf dass Ihr mit Glauben erfüllt werdet.]], },
    { [[Vater Inigo Montoy bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern wird für Euch Handschuhe des Glaubens anfertigen, wenn Ihr ihm folgende Gegenstände bringt: 1 Paar entweihte Handschuhe, 8 kampfzerschlissene Stoffstücke und 4 Einheiten Mondstoff.]], },
  },
  [9117] = {
    [[Gürtel des Glaubens]],
    { [[<Vater Montoy vollführt vor Euch ein paar merkwürdige Gesten in der Luft.>]], [[<Gesegneter Junge/Gesegnetes Mädchen>, habt Ihr die Gewänder der Gefallenen gefunden? Die armen Seelen, die ihre letzten Atemzüge auf dem Schlachtfeld ausgehaucht haben - um alles beraubt, einschließlich ihrer Würde.]], [[Bringt mir die entweihten Überbleibsel der Verstorbenen sowie die Reagenzien zur Läuterung... auf dass Ihr mit Glauben erfüllt werdet.]], },
    { [[Vater Inigo Montoy bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern wird für Euch einen Gürtel des Glaubens anfertigen, wenn Ihr ihm folgende Gegenstände bringt: 1 entweihten Gürtel, 8 kampfzerschlissene Stoffstücke, 2 Arkankristalle und 2 Einheiten Mondstoff.]], },
  },
  [9118] = {
    [[Bindungen des Glaubens]],
    { [[<Vater Montoy vollführt vor Euch ein paar merkwürdige Gesten in der Luft.>]], [[<Gesegneter Junge/Gesegnetes Mädchen>, habt Ihr die Gewänder der Gefallenen gefunden? Die armen Seelen, die ihre letzten Atemzüge auf dem Schlachtfeld ausgehaucht haben - um alles beraubt, einschließlich ihrer Würde.]], [[Bringt mir die entweihten Überbleibsel der Verstorbenen sowie die Reagenzien zur Läuterung... auf dass Ihr mit Glauben erfüllt werdet.]], },
    { [[Vater Inigo Montoy bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern wird für Euch Bindungen des Glaubens anfertigen, wenn Ihr ihm folgende Gegenstände bringt: 1 Paar entweihte Bindungen, 6 kampfzerschlissene Stoffstücke, 1 Arkankristall und 1 Nexuskristall.]], },
  },
  [9119] = {
    [[Zwischenfall beim Sanktum des Westens]],
    { [[Da Ihr schon mal hier seid, <name>, habe ich eine Bitte an Euch. Leybewahrerin Velanias Lehrling kam vorhin vorbei und bat um Unterstützung. Beim Sanktum des Westens kam es offenbar zu einem Zwischenfall. Ich bin gerade zu sehr mit der... Unterhaltung... unserer Zwergengäste beschäftigt. Vielleicht könnt Ihr ja aushelfen. Ihr findet das Sanktum des Westens südwestlich von hier.]], },
    { [[Sprecht mit Leybewahrerin Velania beim Sanktum des Westens im Immersangwald.]], },
  },
  [9120] = {
    [[Der Niedergang Kel'Thuzads]],
    { [[Das Phylakterium ist alles, was vom Herrscher Naxxramas' übrig bleibt. Nach Eurem pflichtgemäßen Ermessen, müsstet Ihr das Phylakterium zerstören, um somit einem erneuten Aufbäumen des Lichs vorzubeugen. Glücklicherweise hört Ihr nur selten auf Eure innere Stimme der Vernunft.]], [[Bei der Kapelle des Hoffnungsvollen Lichts wird Euch irgendjemand mit Sicherheit ein Vermögen für dieses Artefakt bezahlen. Wen kümmert es schon, ob Kel'Thuzad eines Tages wieder von Neuem erstarkt?]], },
    { [[Bringt Kel'Thuzads Phylakterium zu der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern.]], },
  },
  [9121] = {
    [[Die Zitadelle des Schreckens - Naxxramas]],
    { [[Inmitten des Pestwalds liegt der Eingang zu der Zitadelle des Schreckens von Naxxramas. Bisher wurde jeglicher Versuch, dort hineinzugelangen, von den magischen Schutzzaubern, die über das Runenportal verhängt wurden, zunichtegemacht. Zumindest war es bis heute so.]], [[Wir haben einen Weg gefunden, wie wir uns mithilfe von arkaner Tarnung Zugang verschaffen können – einem von mir leicht abgewandelten alten Zauber der Kirin Tor. Da es an Eurer Hingabe für die Ziele der Argentumdämmerung noch etwas mangelt, <name>, wird der Preis hoch sein. Es steht Euch frei, abzulehnen und noch einmal für einen günstigeren Preis wiederzukehren, sobald Euer Ruf respektvoll oder ehrfürchtig ist.]], },
    { [[Erzmagierin Angela Dosantos bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern möchte 5 Arkankristalle, 2 Nexuskristalle, 1 rechtschaffene Kugel und 60 Goldstücke. Euer Ruf bei der Argentumdämmerung muss außerdem wohlwollend sein.]], },
  },
  [9122] = {
    [[Die Zitadelle des Schreckens - Naxxramas]],
    { [[Inmitten des Pestwalds liegt der Eingang zu der Zitadelle des Schreckens von Naxxramas. Bisher wurde jeglicher Versuch, dort hineinzugelangen, von den magischen Schutzzaubern, die über das Runenportal verhängt wurden, zunichtegemacht. Zumindest war es bis heute so.]], [[Wir haben einen Weg gefunden, wie wir uns mithilfe von einer Art dauerhaften arkanen Tarnung Zugang verschaffen können – einem von mir leicht abgewandelten alten Zauber der Kirin Tor. Diese Tarnung ist allerdings sehr kostspielig. Da Eure Hingabe für die Ziele der Argentumdämmerung bewundernswert ist, sind wir gewillt, einige dieser Kosten für Euch zu übernehmen.]], },
    { [[Erzmagierin Angela Dosantos bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern möchte 2 Arkankristalle, 1 Nexuskristall und 30 Goldstücke. Euer Ruf bei der Argentumdämmerung muss außerdem respektvoll sein.]], },
  },
  [9123] = {
    [[Die Zitadelle des Schreckens - Naxxramas]],
    { [[Inmitten des Pestwalds liegt der Eingang zu der Zitadelle des Schreckens von Naxxramas. Bisher wurde jeglicher Versuch, dort hineinzugelangen, von den magischen Schutzzaubern, die über das Runenportal verhängt wurden, zunichtegemacht. Zumindest war es bis heute so.]], [[Wir haben einen Weg gefunden, wie wir uns mithilfe von einer Art dauerhaften arkanen Tarnung Zugang verschaffen können – einem von mir leicht abgewandelten alten Zauber der Kirin Tor. Diese Tarnung ist allerdings sehr kostspielig. Da Eure Hingabe für die Ziele der Argentumdämmerung jedoch unerschütterlich ist, werden wir Euch sämtliche Kosten erlassen.]], },
    { [[Erzmagierin Angela Dosantos bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern wird Euch die arkane Tarnung kostenfrei gewähren. Euer Ruf bei der Argentumdämmerung muss ehrfürchtig sein.]], },
  },
  [9124] = {
    [[Was man für Gruftpirscherrüstung braucht...]],
    { [[Würdet Ihr Euch gerne eine dieser glänzenden Münzen verdienen?]], [[<Leopold hält ein Insigne des Kreuzzugs in die Höhe.>]], [[Ich habe ein kleines Problem, bei dem Ihr mir vielleicht behilflich sein könntet, <name>. Seht Ihr, ich fertige Gruftpirscherrüstung für diejenigen an, die dämlich genug sind, um sich nach Naxxramas zu wagen. Zwar versorgen sie mich mit den wichtigsten Bestandteilen für die Rüstung versorgen, mir gehen aber ständig die Teile von Gruftbestien aus. Bringt mir einen Schwung dieser Teile und ich werde Euch dafür mit einem Insigne bezahlen - einlösbar bei dem Rüstmeister.]], },
    { [[Jäger Leopold bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern möchte 30 Teile einer Gruftbestie.]], },
  },
  [9125] = {
    [[Teile einer Gruftbestie]],
  },
  [9126] = {
    [[Knochensensenjagd]],
    { [[Don Julios Muster für die Rüstung der Knochensense benötigt Knochenfragmente - ein Menge Knochenfragmente. Diejenigen, die verrückt genug sind, um sich nach Naxxramas zu wagen, versorgen mich bereits mit den wichtigsten Bestandteilen für die Rüstung. Allerdings halte ich stets nach weiteren Knochenfragmenten Ausschau.]], [[Was denkt Ihr? Würdet Ihr Euch gerne die Hände schmutzig machen und dabei ein paar Anhänger der Geißel vernichten? Hierbei kommt jeder gut davon.]], },
    { [[Rohan der Assassine bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern möchte 30 Knochenfragmente.]], },
  },
  [9127] = {
    [[Knochenfragmente]],
  },
  [9128] = {
    [[Die Elementargleichung]],
    { [[Seid gegrüßt, Abenteurer! Hättet Ihr Interesse an Arbeit? Wer für mich arbeitet, wird mit Insignien bezahlt. Diese Insignien können beim Rüstmeister für mächtige Gegenstände eingetauscht werden.]], [[Eure Aufgabe besteht darin, mir so viele Kerne der Elemente zu bringen, wie Ihr tragen könnt. Je mehr, umso besser!]], [[Ihr findet sie bei Elementaren verschiedenster Form und Größe, wie es sie überall in unserer Welt gibt.]], },
    { [[Erzmagierin Angela Dosantos bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern möchte 30 Kerne der Elemente.]], },
  },
  [9129] = {
    [[Kern der Elemente]],
  },
  [9130] = {
    [[Waren von Silbermond]],
    { [[Ah, <ein Freiwilliger/eine Freiwillige>! Uns gehen hier langsam die Vorräte aus, aber Silbermond hat uns Unterstützung in Form von Hilfsgütern zugesichert. Hier kommt Ihr ins Spiel.]], [[Bringt diesen Frachtbrief zu Himmelsmeister Morgenhell. Er wird Euch weiter Auskunft darüber geben, wie Ihr am besten nach Silbermond gelangt.]], },
    { [[Bringt Sathiels Frachtbrief zu Himmelsmeister Morgenhell in Morgenluft.]], },
  },
  [9131] = {
    [[Bindung des Schreckenspanzers]],
    { [[Habt Ihr jemals Schreckenspanzerrüstung zu Gesicht bekommen, <class>? Nein, natürlich nicht. Ihr seid immer noch ein <Junge/Mädchen> – ein Dreikäsehoch eben. Ich muss Euch sagen, es ist herrlich.]], [[Ihr fragt Euch wahrscheinlich, was all das mit Euch zu tun haben soll, richtig? Hört gut zu, <Anfänger/Anfängerin>: Ich brauche Dunkeleisenfragmente und bin bereit zu zahlen, um an so viele Stücke wie möglich zu kommen.]], [[Ich benötige die Fragmente für das Zusammensetzen der Schreckenspanzer für unsere Helden. Bringt mir Fragmente, dann werdet Ihr bezahlt.]], },
    { [[Korfax bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern möchte 30 Dunkeleisenfragmente.]], },
  },
  [9132] = {
    [[Dunkeleisenfragmente]],
  },
  [9133] = {
    [[Fliegt nach Silbermond]],
    { [[<class>, ich könnte Euch für eine ganz geringe Summe auf dem Rücken einer dieser herrlichen Kreaturen rasch durch die Lüfte fliegend an Euer Ziel bringen lassen. Es ist auch viel sicherer, als wenn Ihr zu Fuß gehen würdet, das könnt Ihr mir glauben!]], [[Sucht Sathren Morgenblau auf, sobald Ihr in Silbermond angekommen seid.]], [[Wendet Euch wieder an mich, wenn Ihr bereit sein solltet, durch die Lüfte zu reisen.]], },
    { [[Erwerbt einen Flug nach Silbermond und bringt Sathiels Frachtbrief zu Sathren Morgenblau in Silbermond.]], },
  },
  [9134] = {
    [[Himmelsmeisterin Abendlicht]],
    { [[Naja, sieht nicht so aus, als hättet Ihr einen Karren oder ein Lasttier dabei, also gebe ich Euch besser nur das, was Ihr auch wirklich tragen könnt. Wenn Ihr bereits mit Himmelsmeister Morgenhell in Morgenluft gesprochen habt, dann solltet Ihr das hier so schnell wie möglich zu Sathiel bringen, indem Ihr dorthin zurückfliegt.]], [[Bringt Himmelsmeisterin Abendlicht diese Waren zur Endkontrolle. Ihr findet sie am Hirtentor direkt außerhalb von Silbermond.]], },
    { [[Bringt Sathiels Waren zu Himmelsmeisterin Abendlicht am Hirtentor direkt außerhalb von Silbermond.]], },
  },
  [9135] = {
    [[Zurück zu Sathiel]],
    { [[Sieht aus, als ob alles klar geht.]], [[So, die Frage ist, habt Ihr schon mit meinem Kollegen, Himmelsmeister Morgenglanz, gesprochen? Wenn ja, dann zahlt Ihr ganz einfach die kleine Gebühr und fliegt rasch zurück.]], [[Lasst mich wissen, wenn Ihr bereit seid.]], },
    { [[Erwerbt einen Flug zurück nach Morgenluft im Immersangwald und liefert Sathiels Waren bei Sathiel ab.]], },
  },
  [9136] = {
    [[Wildpflanzen]],
    { [[Rayne bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern möchte 30 Wildwedel.]], },
    { [[Rayne bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern möchte 30 Wildwedel.]], },
  },
  [9137] = {
    [[Wildwedel]],
  },
  [9138] = {
    [[Sonnenkuppe]],
    { [[Wir sind hier eingekesselt, <class>! Die anderen Dörfer wurden alle von der Geißel überrannt!]], [[Ihr müsst umgehend nach Nordosten, in das Dorf Sonnenkuppe reisen und Euch dort den nerubischen Truppen der Geißel stellen. Es ist unmöglich sie zu verfehlen - sie sehen aus wie riesige Spinnen.]], [[Wir dürfen keine Zeit verlieren! Quel'Thalas muss zurückerobert werden!]], },
    { [[Tötet 10 nerubische Wachen und kehrt anschließend zu Arkanist Vandril in Tristessa in den Geisterlanden zurück.]], },
  },
  [9139] = {
    [[Goldnebel]],
    { [[Wir haben unseren ersten Sieg errungen, doch es warten noch zwei weitere Dörfer auf ihre Befreiung!]], [[Das Dorf Goldnebel ist unser nächstes Ziel. Es liegt im Nordwesten und wurde von den Geistern der Hochelfen besetzt, die dort einst lebten. Grauenerregend, ich weiß, allerdings scheint dies eine der bevorzugten Taktiken zu sein, derer sich die Geißel bedient.]], [[Zieht los und besiegt diese Geister und Gespenster, <class>. Folgt der westlichen Straße, die aus Tristessa hinaus führt und Ihr werdet das Dorf finden.]], },
    { [[Besiegt 6 Geister der Quel'dorei und 4 Gespenster der Quel'dorei. Kehrt anschließend zu Arkanist Vandril in Tristessa in den Geisterlanden zurück.]], },
  },
  [9140] = {
    [[Windläuferdorf]],
    { [[<name>, der entscheidende Kampf um die Dörfer der Geisterlande steht kurz bevor. Windläuferdorf im Südwesten wurde von einer großen Armee aus den besten Sturmtruppen der Geißel, Schemengeister und Gargoyles überrannt. Ihr werdet als Speerspitze unserer Offensive fungieren und die Zahl ihrer Kämpfer dezimieren.]], [[Die Magister vom Sanktum der Sonne haben uns einen besonderen Auftrag erteilt; sie würden gerne die Überreste der Geißel studieren, um neue Wege zur Bekämpfung dieser Plage zu finden.]], [[Gute Jagd!]], },
    { [[Sammelt 6 geisterhafte Substanzen und 4 Gargoylefragmente und bringt sie zu Arkanist Vandril in Tristessa in den Geisterlanden.]], },
  },
  [9141] = {
    [[Sie nennen mich "den Schleifer"]],
    { [[Ihr wollt eine Verfügung? Ihr erwartet von mir, Euch eine heißbegehrte Arbeitsanweisung einfach so auszuhändigen? Ich glaube nicht, Knirps. Handwerker aus der ganzen Welt bewerben sich um Aufträge hier an der Front.]], [[Wenn Ihr eine Arbeitsanweisung wollt, zeigt mir, dass Ihr gewillt seit, für die Sache zu bluten! Bringt mir eine Ehrenmarke, dann können wir darüber reden.]], [[Und es gibt nur einen Weg, so eine Marke in Eure schmutzigen kleinen Pfoten zu bekommen - geht kämpfen.]], },
    { [[Einsatzkommandant Metz bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern möchte eine Ehrenmarke der Argentumdämmerung.]], },
  },
  [9142] = {
    [[Handwerksverfügung]],
    { [[You know the drill, kid. One valor token gets you a craftsman's writ.]], },
  },
  [9143] = {
    [[Maßnahmen gegen Zeb'Sora]],
    { [[Unser Leutnant hier hat uns einfach angreifen lassen, ohne sich vorher ein Bild über die taktische Lage zu machen. Die Trolle lauerten bereits jenseits des Sees auf uns. Durch seine Unfähigkeit wurden wir einfach dahingemetzelt! Hoffentlich hörst du auch zu, Tomathren? Dämlicher Idiot!]], [[Werdet Ihr uns helfen, <class>? Diese Trolle sind immer noch dort draußen. Ich werde hier bei unserem Leutnant bleiben - ich glaube, er hat seinen Verstand verloren.]], [[Geht nach Zeb'Sora im Osten auf der andere Seite des Sees. Ich will Rache! Bringt mir ihre Ohren, damit ich mir eine nette Halskette anfertigen kann!]], },
    { [[Sammelt 6 Trollohren von Zeb'Sora und bringt sie zu Waldläuferin Valanna in den Geisterlanden.]], },
  },
  [9144] = {
    [[Verschollen in den Geisterlanden]],
    { [[Mein Neffe wurde vor einigen Tagen in die Geisterlande entsandt. Er hat nicht von seinem Auftrag gesprochen, nur, dass es sich um etwas extrem Geheimes handelt.]], [[Er kehrte nie zurück, und ich befürchte das Schlimmste, <class>. Ein vermisster Bote mag den hartherzigen Magistern von Silbermond nicht viel bedeuten, aber er ist wie ein Sohn für mich. Ich bitte Euch, <name>, sucht nach ihm.]], },
    { [[Folgt der Straße, die von Morgenluft aus nach Süden führt, und haltet Ausschau nach Kurier Morgenwandler, wenn Ihr die Geisterlande erreicht.]], },
  },
  [9145] = {
    [[Hilfe für Waldläuferin Valanna!]],
    { [[<class>, ich kann hier nicht einfach rumsitzen und auf eine Audienz mit dem Hochexekutor oder der Dame Auriferous warten! Diese Angelegenheit duldet keinen Aufschub!]], [[Wir hatten den Auftrag, uns um die Waldschattentrolle von Zeb'Sora im Nordosten jenseits des Elrendarsees zu kümmern. Doch unser Leutnant hat den Angriff falsch geplant und wir haben hohe Verluste erlitten. Kurz nach der Attacke ist er dann zusammengebrochen. Waldläuferin Valanna hat mich hierher und Waldläuferin Salissa in die Enklave der Weltenwanderer entsandt, um Verstärkung anzufordern.]], [[Ihr müsst unsere Einheit finden, <class>! Valanna ist schwer verletzt und der Leutnant ist nur noch ein plapperndes Mündel.]], },
    { [[Findet Waldläuferin Valanna und Leutnant Tomathren am Ufer des Elrendarsees nahe Sonnenkuppe in den Geisterlanden.]], },
  },
  [9146] = {
    [[Meldung bei Hauptmann Helios]],
    { [[Ihr müsst mir einen letzten Gefallen tun, <class>. Für den Fall, dass Waldläuferin Salissa es nicht bis zur Enklave der Weltenwanderer am südlichen Ausläufer des Sees geschafft hat, möchte ich, dass Ihr dorthin reist und Hilfe holt.]], [[Meldet Euch einfach bei Hauptmann Helios und lasst ihn wissen, was hier geschehen ist. Sicherlich wird er uns schnellstmöglich Hilfe schicken.]], },
    { [[Sprecht mit Hauptmann Helios bei der Enklave der Weltenwanderer am südlichen Ufer des Elrendarseese in den Geisterlanden.]], },
  },
  [9147] = {
    [[Der sterbende Kurier]],
    { [[Was steht Ihr hier herum? Seht Ihr nicht, dass Ihr Euch um diesen Jungen sofort kümmert müsst? Wie es aussieht, hat die Seuche seinen Körper jetzt gänzlich befallen. In wenigen Minuten kann nicht einmal eine Apothekermeisterin wie ich ihn noch retten.]], [[Das Blut der Tiere südlich des Flusses ist bereits verseucht. Geht über die Brücke und jagt dort die befallenen Biester. Bringt mir genügend verseuchte Blutproben und ich werde daraus ein Serum gewinnen, das diesen Blutelfen retten kann. Beeilt Euch!]], },
    { [[Apothekerin Thedra im Immersangwald möchte, dass Ihr ihr 4 verseuchte Blutproben von Tieren aus den Geisterlanden bringt.]], },
  },
  [9148] = {
    [[Zustellung nach Tristessa]],
    { [[Ich... ich bin zu schwach... ich kann nicht mehr weiter. Ich trage eine finstere Warnung vom Lordregenten Theron bei mir, einen Brief, um genau zu sein. Ihr müsst ihn für mich überbringen.]], [[Bringt ihn zu Arkanist Vandril in Tristessa. Die Stadt liegt weiter im Süden entlang dieser Straße. Geht dorthin... und findet Vandril...]], },
    { [[Bringt den Brief aus Silbermond zu Arkanist Vandril in Tristessa in den Geisterlanden.]], },
  },
  [9149] = {
    [[Die verseuchte Küste]],
    { [[Um ein Heilmittel gegen die Seuche finden zu können, müssen wir verstehen, wie sie sich auf verschiedene Kreaturen auswirkt: von den komplexesten Lebensformen bis hin zu niedrigen Untergattungen.]], [[Ich kam hierher, um die Murlocs zu untersuchen, da sie, wie wir, ihren freien Willen auch im Untotendasein erhalten haben. Was ist an diesen einfachen Biestern nur so besonders? Ich muss es herausfinden, <name>!]], [[Reist nach Nordwesten und bringt mir ihre Rückgrate, damit ich die Ablagerungen der Seuche in ihrem Knochenmark studieren kann. Wer weiß was für Geheimnisse diese kleinen Knochen in sich bergen?]], },
    { [[Bringt 6 verseuchte Murlocrückgrate zu Apotheker Renzithen in Tristessa in den Geisterlanden.]], },
  },
  [9150] = {
    [[Reise zum vergessenen Sanktum]],
    { [[Glaubt Ihr das, <name>? Momentan gibt es ein funktionstüchtiges arkanes Sanktum, das südöstlich von hier im Einsatz ist, das Sanktum der Sonne. Alle Magister, die sich für wichtig halten, möchten an dessen Unterhaltung beteiligt sein.]], [[Das Sanktum des Mondes im Westen haben natürlich alle vergessen. Es ist nicht mehr viel davon übrig und seine Energien haben merkwürdige Kreaturen erweckt.]], [[Ich bin niemand, der gute Ressourcen einfach so verkommen lässt! Geht zum Sanktum des Mondes und versucht kristallisierte Essenzen von den Kreaturen dort zu erlangen.]], },
    { [[Magister Darenis möchte, dass Ihr zum Sanktum des Mondes westlich von Tristessa reist und dort 8 kristallisierte Manaessenzen von den dort hausenden Arkanverschlingern und Manaschleichern erlangt.]], },
  },
  [9151] = {
    [[Das Sanktum der Sonne]],
    { [[Ihr habt Eure Loyalität gegenüber Tristessa bewiesen und uns geholfen, gegen die Geißel zu kämpfen. Einige unserer mächtigsten Magister sind aus Silbermond gekommen, um uns dabei zu helfen, den Feind vor seiner eigenen Schwelle zu bekämpfen.]], [[Sie haben das arkane Sanktum im Süden wiedererrichtet und die Geißel aus der umliegenden Gegend vertrieben. Sie planen einen Angriff auf die Todesfestung, Dar'kahns Stützpunkt. Ihr solltet sie unterstützen, <name>. Ich bin mir sicher, dass <ein erfahrener/eine erfahrene><class> <class> wie Ihr hier eine echte Hilfe wäre.]], },
    { [[Sprecht mit Magister Idonis im Sanktum der Sonne in den Geisterlanden.]], },
  },
  [9152] = {
    [[Tombers Vorräte]],
    { [[Wie soll ich die Kriegsvorbereitungen mit Vorräten unterstützen, wenn ich sie nicht mal sicher hierher bringen kann? Man hat mir Geleitschutz versprochen, der niemals kam. Ich musste dann auch noch einen meiner Karren zurücklassen, als ich von einer Bande Ghule im Westen angegriffen wurde!]], [[Wollt Ihr mir vielleicht helfen, damit ich mein Geschäft auch eröffnen kann? Geht die westliche Straße, die aus der Ortschaft führt, runter und am Friedhof vorbei. Auf der anderen Seite der Todesschneise liegt eine Verzweigung. Der Hügel, auf dem ich meinen Karren lassen musste, liegt kurz dahinter.]], },
    { [[Erlangt Rathis Tombers Vorräte und kehrt zu ihm in Tristessa in den Geisterlanden zurück.]], },
  },
  [9153] = {
    [[Bedrohliche Schatten]],
    { [[Benutzt Eure Karte, um von der Geißel angegriffene Gebiete ausfindig zu machen. Geht dorthin und beschädigt eine Nekropole, indem Ihr die Truppen der Geißel, die sie umgeben, besiegt. Bringt 10 ihrer nekrotischen Runen zu Kommandant Thomas Helleran bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern.]], },
    { [[Benutzt Eure Karte, um von der Geißel angegriffene Gebiete ausfindig zu machen. Geht dorthin und beschädigt eine Nekropole, indem Ihr die Truppen der Geißel, die sie umgeben, besiegt. Bringt 10 ihrer nekrotischen Runen zu Kommandant Thomas Helleran bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern.]], },
  },
  [9154] = {
    [[Kapelle des Hoffnungsvollen Lichts]],
    { [[Die Geißel ist in größerer Anzahl zurückgekehrt, als wir es jemals zuvor gesehen haben. Dies müssen die Machenschaften des Lichkönigs sein! Deshalb rekrutiert die Argentumdämmerung alle Männer und Frauen, die fähig und bereit sind, unsere Länder zu verteidigen.]], [[Falls Ihr unserer Sache dienen wollt, dann bringt dieses Dokument zum Bewahrer der Schriften bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern.]], },
    { [[Bringt den Ruf zu den Waffen zum Bewahrer der Schriften in der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern.]], },
  },
  [9155] = {
    [[Entlang der Todesschneise]],
    { [[Da Ihr es bis hierher geschafft habt, habt Ihr bestimmt schon mal von der Todesschneise gehört und dass selbst jetzt, Jahre nachdem Arthas sie während seines Feldzuges nach Silbermond erschaffen hat, die geistlosen Kreaturen der Geißel noch immer auf ihrem Pfad wandeln.]], [[Macht Euch nützlich und dünnt die Anzahl der Geißel innerhalb der Schneise etwas aus. Falls Ihr Euch als fähig erweist, lege ich ein gutes Wort für Euch ein.]], },
    { [[Tötet 10 hungrige Auferstandene und 10 gegeißelte Kannibalen in der Todesschneise. Kehrt anschließend zu Todespirscher Rathiel in Tristessa zurück, um Eure Belohnung zu erhalten.]], },
  },
  [9156] = {
    [[Gesucht: Faulbein und Luzran]],
    { [[Hiermit ist ein Kopfgeld auf die Monstrositäten Faulbein und Luzran ausgesetzt. Diese abscheulichen Kreaturen von Dar'Khan sind sehr gefährlich, und wurden in der Nähe der Heulenden und Blutenden Ziggurats auf Patrouille gesehen. Personen, welche die Belohnung einfordern wollen, müssen die Köpfe der Monstrositäten als Beweis zu Todespirscher Rathiel bringen.]], },
    { [[Sucht in den Geisterlanden nach Faulbein und Luzran. Bringt ihre Köpfe zu Todespirscher Rathiel in Tristessa, um eine Belohnung zu erhalten.]], },
  },
  [9157] = {
    [[Vergessene Rituale]],
    { [[Jedes Jahr haben wir, die Einwohner von Sonnenkuppe, an einem Ritual teilgenommen, um die verschiedenen Aspekte der Natur unter unserer Kontrolle zu halten. Die Herrschaft über das Wasser wurde symbolisiert durch die Beschwörung und Versklavung eines Wasserelementars namens Aquantion. Aber es gelang ihm, unsere Magie gegen uns zu wenden, seine Fesseln abzuwerfen und stattdessen uns zu versklaven!]], [[Während des Rituals trugen wir wellenförmige Medaillons. Sucht sie auf dem Grund des Sees, wir benötigen sie, um die Geister meiner Kameraden zu befreien!]], },
    { [[Bringt 8 Wellenfrontmedaillons zu Geranis Weißflor in den Geisterlanden.]], },
  },
  [9158] = {
    [[Überträger der Seuche]],
    { [[Eure Anwesenheit in den Geisterlanden zeugt von großem Mut, <name>. Hier gibt es so viel zu erledigen und nur wenige mutige Seelen, die uns zur Seite stehen.]], [[Ich habe eine undankbare Aufgabe für Euch, sofern Ihr geneigt seid, unserer Sache zu dienen. Die gesamte Fauna wurde von der Seuche befallen und wir müssen ihre weitere Ausbreitung stoppen. Jagt die Geisterklauenluchse nördlich und südlich von hier und kehrt zu mir zurück, nachdem Ihr eine ausreichende Zahl erlegt habt.]], },
    { [[Weltenwanderin Sedina in der Enklave der Weltenwanderer in den Geisterlanden möchte, dass Ihr 10 Geisterklauenluchse erschlagt.]], },
  },
  [9159] = {
    [[Gebietet der Seuche Einhalt]],
    { [[Ihr habt Euch als würdiger Verbündeter erwiesen, <name>. Ihr müsst mit Eurer Aufgabe fortfahren und weitere verseuchte Wildtiere erlegen. Dieses Mal werdet Ihr Euch die etwas gefährlicheren Exemplare vornehmen. Jagt Spindelweberlauerer westlich der Todesschneise und Vampirnebelfledermäuse direkt südlich von hier. Kehrt anschließend zu mir zurück.]], },
    { [[Weltenwanderin Sedina in der Enklave der Weltenwanderer in den Geisterlanden möchte, dass Ihr 8 Spindelweberlauerer und 10 Vampirnebelfledermäuse erschlagt.]], },
  },
  [9160] = {
    [[Untersucht An'daroth]],
    { [[<class>, wir haben einige Schwierigkeiten mit einer Gruppe von Nachtelfen, die erst kürzlich eine der naheliegenden Inseln besetzt hat. Sie haben ein kleines Lager an dem alten hochelfischen Leyliniennexus, An'daroth, im Nordwesten aufgeschlagen. Es gibt einen kleinen schlammigen Pfad, der hinter der Todesschneise dorthin führt.]], [[Ich würde vorschlagen, dass Ihr uns helft, indem Ihr Euch dorthin begebt und herausfindet, was sie vorhaben. Tötet bei der Gelegenheit so viele dieser eingebildeten Nachtelfen, wie Ihr könnt.]], },
    { [[Untersucht das Gebiet um An'daroth, tötet 12 Spione der Schildwache und kehrt anschließend zu Dame Auriferous in Tristessa in den Geisterlanden zurück.]], },
  },
  [9161] = {
    [[Schatten des Verräters]],
    { [[Man sagt, Dar'Khan Drathir sei von den Toten wiederauferstanden, um über die Armeen der Geißel in den Geisterlanden zu herrschen. Schon vor langer Zeit hatte sich Dar'Khan mit der Geißel eingelassen... damals war er noch ein lebender, atmender Elf. Er verbrachte viel Zeit in den Gemäuern des Morgensternturms, nordöstlich von hier. Dort verbarg er sich viele Nächte lang, ersann finstere Machenschaften gegen sein eigenes Volk.]], [[<name>, durchsucht den Turm nach weiteren Informationen. Vielleicht hatte er seine Pläne niedergeschrieben, oder er führte ein Tagebuch. Wer weiß schon, wo nützliche Hinweise auf Ihre Entdeckung warten.]], },
    { [[Begebt Euch in die Geisterlande zum Morgensternturm und sucht nach möglichen Hinweisen auf Dar'Khans Vergangenheit.]], },
  },
  [9162] = {
    [[Die Vergangenheit erwacht]],
    { [[Als Ihr durch die Einträge des Tagebuchs blättert, bekommt Ihr das unbestimmte Gefühl, beobachtet zu werden.]], [[Das Geschriebene beunruhigt Euch. Dar'Khans Seele scheint erfüllt von Hass und Verachtung für sein eigenes Volk. Das ganze Ausmaß seiner Machenschaften und Intrigen wird auf diesen Seiten ausführlich beschrieben.]], [[Einiges davon wird Vynna sicherlich sehr nützlich finden.]], },
    { [[Kehrt mit Dar'Khans Tagebuch zu Waldläuferin Vynna bei der Enklave der Weltenwanderer in den Geisterlanden zurück.]], },
  },
  [9163] = {
    [[In besetztem Gebiet]],
    { [[Nun, die direkten Maßnahmen, für die wir uns letztlich entschieden haben, um herauszubekommen, was diese verdammten Nachtelfen vorhaben, sehen grob zusammengefasst folgenden Ablauf vor: Landet auf ihrer Insel und nehmt alles mit, das für uns von Interesse sein könnte.]], [[Ich möchte, dass Ihr die Straße nach Westen in Richtung der Insel Shalandis nehmt. Sie liegt recht nah an der Küste. Nehmt dort sämtliche Pläne mit, die sie möglicherweise auf der Insel oder auf ihrem Schiff verstaut haben.]], },
    { [[Stehlt die Nachtelfenpläne: An'daroth, Nachtelfenpläne: An'owyn und Nachtelfenpläne: Auskundschaften der Sin'dorei und kehrt anschließend in die Geisterlande zu Dame Auriferous in Tristessa zurück.]], },
  },
  [9164] = {
    [[Gefangene der Todesfestung]],
    { [[Ah, ja. Eine meiner gelungensten Kreationen. Dieses Gebräu wirkt jeglichen Betäubungseffekten entgegen, die durch alchemistische Mittel verursacht werden.]], [[Es kann zu Nebeneffekten wie Verwirrung und Übelkeit kommen, in wenigen Fällen führt es zu zeitweiser Blindheit. Doch wenn Ihr mich fragt, ist dies alles immer noch besser, als das, was die Geißel den Gefangenen in der Todesfeste antun wird.]], [[Viel Glück, <mutiger/mutige><class> <class>. Ihr werdet es brauchen.]], },
    { [[Nehmt Renzithens Regenerationsgebräu und begebt Euch zur Todesfestung. Befreit dort Lehrling Varnis, Apothekerin Enith und Waldläufer Vedoran. Kehrt danach zu Arkanistin Janeda beim Sanktum der Sonne zurück, um Eure Belohnung zu erhalten.]], },
  },
  [9165] = {
    [[Verfügung des freien Geleits]],
    { [[Ohne die Hilfe von Euch Abenteurern, hätte meine Karawane es niemals geschafft! Die Geißelsoldaten, die uns im Unterholz aufgelauert haben, wollten wohl nicht, dass wir hier in einem Stück eintreffen.]], [[<Wache Didier wischt sich den Schweiß von der Stirn.>]], [[Jetzt werden wir erst einmal hier warten, bis Feldmarschall Kammerer eintrifft. Er wird Eure Verfügung für freies Geleit unterzeichnen, die Ihr dann zu Einsatzkommandant Metz an der Kapelle des Hoffnungsvollen Lichts bringen könnt.]], [[Und <name>, achtet in der Gegenwart von Kammerer auf Eure Worte. Er ist ein Mensch der Sorte 'Ich reiße Euch den Kopf ab und breche Euch sämtliche Knochen'... mit dem Unterschied, dass er es auch wirklich tut...]], },
    { [[Bringt die unterschriebene Verfügung des freien Geleits zu Einsatzkommandant Metz an der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern.]], },
  },
  [9166] = {
    [[Bringt die Pläne nach An'telas]],
    { [[Bringt die Nachtelfenpläne für mich nach An'telas. Es liegt östlich von hier, auf dem südlichen Hang des Sonnenhügels, nordöstlich vom Sanktum der Sonne.]], [[Magister Sylastor führt dort diverse Experimente mit den Energien des Leyliniennexus durch, um potentielle Verwendungsmöglichkeiten im Kampf gegen die Geißel zu finden. Zeigt ihm die Pläne, möglicherweise kann er etwas Licht in die Sache bringen und herausfinden, was die Nachtelfen vorhaben.]], },
    { [[Bringt die Nachtelfenpläne zu Magister Sylastor bei An'telas in den Geisterlanden.]], },
  },
  [9167] = {
    [[Der Tod des Verräters]],
    { [[Alles, was wir in den Geisterlanden getan haben, hatte nur ein Ziel, die Rückeroberung all dessen, was rechtmäßig unser ist! Schon zu lange hielt die Geißel diese Region in ihrem Würgegriff.]], [[Die Zeit der Abrechnung ist gekommen, <name>. Dank Eurer Unterstützung und der Hilfe unserer Verbündeten, den Verlassenen, wird die Rache unser sein!]], [[Derjenige, welcher danach trachtete, die Macht des Sonnenbrunnens an sich zu reißen, muss sterben. Begebt Euch zur Todesfestung, der Festung der Geißel südlich der Todesschneise. Bringt mir Dar'Khans Kopf und Eure Taten werden in die Geschichte eingehen!]], },
    { [[Begebt Euch in die Todesfestung und tötet Dar'Khan Drathir. Bringt anschließend Dar'Khans Kopf zu Magister Kaendris beim Sanktum der Sonne in den Geisterlanden.]], },
  },
  [9168] = {
    [[Im Herzen der Todesfestung]],
    { [[Tief in Inneren der Todesfestung führt der Feind magische Experimente zur Erschaffung stärkerer, tödlicherer spektraler Soldaten durch. Diese verabscheuungswürdigen Wesen sind perfekte Tötungsmaschinen, erschaffen von Dar'Khan selbst und verstärkt durch die dunklen Magien der Todesfestung.]], [[Begebt Euch zum Turm der Verdammten im Inneren der Todesfestung und zerstört sie alle, <name>. Wir dürfen es nicht zulassen, dass die Armee des Feindes immer stärker wird, während wir immer noch um die Aufrechterhaltung eines Brückenkopfes in den Geisterlanden kämpfen.]], },
    { [[Zerstört 8 Augen von Dar'Khan und meldet Euch anschließend bei Magister Idonis am Sanktum der Sonne in den Geisterlanden.]], },
  },
  [9169] = {
    [[Die Abschaltung von An'owyn]],
    { [[Die Mondkristalle sind nichts anderes als Seherartefakte - sie wollen uns ausspionieren! Ich weiß, dass An'daroth dafür nicht genügend Energie liefert, doch An'owyn, direkt im Süden, verfügt über genügend Reserven!]], [[Durch die Pläne habe ich einen ungefähren Eindruck darüber gewonnen, wie diese Mondkristalle funktionieren und wir sollten in der Lage sein, sie zu deaktivieren. Allerdings sind sie gesichert, um eine ungewollte Abschaltung zu verhindern. Begebt Euch daher nach An'owyn, beschafft Euch die Kontrollkugel von einem der Nachtelfen und benutzt sie mit den Mondkristallen, um die Durchführung ihrer Pläne zu verhindern!]], [[Beeilt Euch, <class>!]], },
    { [[Beschafft die Kristallkontrollkugel und benutzt sie, um die nachtelfischen Mondkristalle bei An'owyn zu deaktivieren. Kehrt anschließend zu Magister Sylastor in den Geisterlanden zurück.]], },
  },
  [9170] = {
    [[Dar'Khans Gefolgschaft]],
    { [[Der verhasste Verräter, Dar'Khan, hat innerhalb der Geißel große Macht erlangt. Seine Gefolgschaft steht an der Spitze gigantischer Heere, die gegen unsere schwindenden Streitkräfte zu Felde ziehen.]], [[Masophet der Schwarze, Jurion der Betrüger, Borgoth der Blutvergießer, Mirdoran der Gefallene. Merkt Euch diese Namen, denn in den Geisterlanden sind diese Männer einzig Dar'Khan gegenüber verantwortlich. Tötet sie und ihre seelenlosen Armeen werden im Chaos versinken.]], },
    { [[Magister Idonis möchte, dass Ihr Euch zur Todesfestung begebt und Masophet den Schwarzen, Jurion den Betrüger, Borgoth den Blutvergießer und Mirdoran den Gefallenen tötet. Kehrt nach Abschluss Eurer Aufgabe zum Sanktum der Sonne in den Geisterlanden zurück und berichtet ihm.]], },
  },
  [9171] = {
    [[Knusperkost]],
    { [[Das ist unerhört! Ich habe hier keinerlei Zutaten! Mylady hat mich von Silbermond hierher bestellt. Ihr steht schließlich eine Küche zu, wie es sich für ihren Stand geziemt. Nicht? Ich komme der Bitte also nach, aber finde mich hier ohne Vorräte wieder. Ihr müsst für mich losziehen, <class>, und mir frische Beute bringen, damit ich etwas zubereiten kann.]], [[Dame Auriferous liebt besonders knusprige Gerichte. Südwestlich, auf der anderen Seite der Todesschneise, gibt es zahlreiche Spindelweberlauerer. Ekelhaft, ich weiß, aber wir müssen nun mal damit zurechtkommen.]], },
    { [[Sammelt 5 knusprige Spinnenbeine ein und kehrt zu Meisterkoch Mouldier in Tristessa in den Geisterlanden zurück.]], },
  },
  [9172] = {
    [[Bericht an Magister Kaendris]],
    { [[Die Magister beim Sanktum der Sonne machen große Fortschritte bei ihrer Suche nach den Schwächen der Geißel, nicht zuletzt durch die Hilfe der Verlassenen. Magister Kaendris wird wissen, was mit diesen Informationen zu tun ist.]], [[Das Sanktum der Sonne liegt südwestlich von hier. Ihr müsst Euch beeilen, <name>. Dar'Khan ist ein geschickter Gegner und er beobachtet jeden einzelnen unserer Schritte.]], },
    { [[Bringt Dar'Khans Tagebuch zu Magister Kaendris beim Sanktum der Sonne in den Geisterlanden.]], },
  },
  [9173] = {
    [[Rückeroberung des Windläuferturms]],
    { [[<class>, Ich möchte, dass Ihr den Windläuferturm zurückerobert!]], [[Dabei handelt es sich um eine Sache der Ehre. Der Windläuferturm ist die ursprüngliche Residenz von Fürstin Sylvanas Windläufer, Königin der Verlassenen und ehemalige Waldläufergenerälin der Elfen. Aber jetzt wurde der Turm von der Geißel überrannt.]], [[Folgt der Straße nach Süden, bis Ihr zu einer Abzweigung kommt. Wendet Euch dort nach rechts und geht weiter nach Südwesten, wenn Ihr die Todesschneise überquert habt. Dann könnt ihr den Ort nicht verfehlen.]], },
    { [[Besiegt 8 Akolythen der Todesfestung und 10 gefallene Waldläufer, dann kehrt zu Hochexekutor Mavren nach Tristessa in den Geisterlanden zurück.]], },
  },
  [9174] = {
    [[Aquantion bezwingen]],
    { [[Dieser eigenwillige Elementar hat viele Leben auf dem Gewissen. Indem er die unwürdigen Murlocs dazu brachte, unsere Rituale zu sabotieren, wendete er unsere Magie gegen uns.]], [[Zeigt ihm, dass unser Durst nach Rache noch nicht gestillt ist! Nehmt diese Medaillons und legt sie auf den Altar der Gezeitenbeherrschung am Grund des Sees. Aquantion wird Eurem Ruf sofort folgen!]], [[Seid auf der Hut, <race>! Ohne die Kontrolle durch unsere Magie wird sein Zorn ungezügelt sein. Zerstört Aquantion, damit wir ruhen können!]], },
    { [[Geranis Weißflor möchte, dass Ihr auf den Grund des Sees östlich von Sonnenkuppe taucht und das Bündel mit Medaillons auf den Altar der Gezeitenbeherrschung legt. Beschwört und besiegt den Elementar Aquantion und kehrt zu Geranis zurück.]], },
  },
  [9175] = {
    [[Die Halskette der Fürstin]],
    { [[Diese Halskette funkelt, ein bemerkenswertes Schmuckstück, das nicht der Schwermut der Geisterlande zu unterliegen scheint. Auf Grund der Inschrift und des Ortes, an dem Ihr sie gefunden habt, muss es sich dabei um Eigentum von Fürstin Sylvanas Windläufer selbst handeln!]], [[Vielleicht wäre es am besten, sie zuerst zu Hochexekutor Mavren nach Tristessa zu bringen? Er scheint der höchstrangige Verlassene in der Gegend zu sein und weiß sicher, was zu tun ist.]], },
    { [[Bringt die Halskette der Fürstin zu Hochexekutor Mavren nach Tristessa in den Geisterlanden.]], },
  },
  [9176] = {
    [[Die Zwillingsziggurats]],
    { [[Glaubt man den Einträgen in seinem Tagebuch, versprach Dar'Khan der Geißel zwei mächtige elfische Artefakte: Den Stein des Lichts und den Stein der Flamme. Beide waren durchdrungen von den magischen Energien des Sonnenbrunnens und verfügten über mächtige Schutzkräfte gegen die Geißel.]], [[Man plante, die Kristalle zu den heutigen Standorten der Heulenden und der Blutenden Ziggurat, inmitten der Todesschneise, zu transportieren. Falls sie dort noch immer aufbewahrt werden, müssen wir sie unbedingt an uns bringen. Ihre Kräfte wären im Kampf gegen die Geißel von großem Nutzen.]], },
    { [[Reist zur Blutenden und zur Heulenden Ziggurat und bergt den Stein des Lichts und den Stein der Flamme. Kehrt anschließend zu Magister Kaendris beim Sanktum der Sonne in den Geisterlanden zurück.]], },
  },
  [9177] = {
    [[Reise nach Unterstadt]],
    { [[Wie außergewöhnlich! Es scheint sich um ein Geschenk für Fürstin Sylvanas Windläufer zu handeln, von ihrer Schwester, Alleria!]], [[Ihr müsst es sofort zu ihr nach Unterstadt bringen! Die Fürstin residiert im Königsviertel.]], [[Um dorthin zu kommen, benutzt die Translokationskugel im Inneren Sanktum des Sonnenzornturms in Silbermond für den Übergang nach Unterstadt, dann nehmt den Fahrstuhl, um die eigentliche Stadt zu betreten. Schaut auf Eure Karten, wenn Ihr Probleme habt, das Königsviertel zu finden.]], },
    { [[Bringt die Halskette der Fürstin zu Fürstin Sylvanas Windläufer im Königsviertel von Unterstadt.]], },
  },
  [9178] = {
    [[Handwerksverfügung - Verdichteter Gewichtsstein]],
    { [[Der Inhaber dieser Handwerksverfügung hat nach Erfüllung des Vertrags Anspruch auf eine Bezahlung in Form von frei wählbaren Insignien.]], [[Vertragsnummer BS-091:]], [[120 verdichtete Gewichtssteine.]], [[Was im Vertrag angefordert wurde, muss zu Rottenkommandant Steinberster bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern gebracht werden.]], [[Im Falle von Verzug oder Beschädigung der Ware ist der Vertrag als null und nichtig anzusehen.]], [[-Einsatzkommandant Metz, die Argentumdämmerung]], },
    { [[Bringt 120 verdichtete Gewichtssteine und die Handwerksverfügung für die verdichteten Gewichtssteine zu Rottenkommandant Steinberster bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern.]], },
  },
  [9179] = {
    [[Handwerksverfügung - Imperiale Plattenrüstung]],
    { [[Der Inhaber dieser Handwerksverfügung hat nach Erfüllung des Vertrags Anspruch auf eine Bezahlung in Form von frei wählbaren Insignien.]], [[Vertragsnummer BS-428:]], [[3 imperiale Plattenrüstungen.]], [[Was im Vertrag angefordert wurde, muss zu Rottenkommandant Steinberster bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern gebracht werden.]], [[Im Falle von Verzug oder Beschädigung der Ware ist der Vertrag als null und nichtig anzusehen.]], [[-Einsatzkommandant Metz, die Argentumdämmerung]], },
    { [[Bringt 3 imperiale Plattenrüstungen und die Handwerksverfügung für die imperialen Plattenrüstungen zu Rottenkommandant Steinberster bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern.]], },
  },
  [9180] = {
    [[Reise nach Unterstadt]],
    { [[Wie außergewöhnlich! Es scheint sich um ein Geschenk für Fürstin Sylvanas Windläufer zu handeln, von ihrer Schwester, Alleria!]], [[Ihr müsst es sofort zu ihr nach Unterstadt bringen! Die Fürstin residiert im Königsviertel.]], [[Um dorthin zu kommen, benutzt die Translokationskugel im Inneren Sanktum des Sonnenzornturms in Silbermond für den Übergang nach Unterstadt, dann nehmt den Fahrstuhl, um die eigentliche Stadt zu betreten. Schaut auf Eure Karten, wenn Ihr Probleme habt, das Königsviertel zu finden.]], },
    { [[Bringt die Halskette der Fürstin zu Fürstin Sylvanas Windläufer im Königsviertel von Unterstadt.]], },
