  [9211] = {
    [[Der Eiswächter]],
    { [[Ah, der Eiswächter. Es ist ein leichter Hauch schützender Magie, der bestimmte Teile der Ausrüstung bedeckt. Der Eiswächter verleiht jenen, die gegen Kreaturen von Frost und Eis kämpfen, erhöhten Schutz vor deren Angriffen.]], [[Wie bei allem anderen auch, das vom Zornwirker hergestellt wird, so hat auch dieses Stück seinen Preis - einen Preis, der nicht diskutiert werden kann, wie viel Ihr Euch auch beklagen mögt. Interesse?]], },
    { [[Mataus der Zornwirker bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern möchte 10 Insignien des Kreuzzugs und 30 Gold.]], },
  },
  [9212] = {
    [[Flucht aus den Katakomben]],
    { [[Sehe ich so verängstigt aus, wie ich mich fühle? Diese Trolle haben mich aus dem Hinterhalt überfallen und in diesem Käfig gefangen gehalten. Ich glaube, sie wollen mich opfern! Helft mir von hier zu fliehen!]], [[Das Schloss des Käfigs ist nicht allzu kompliziert... Ich kann es von dieser Seite nur nicht erreichen. Befreit mich und geleitet mich zurück zur Enklave der Weltenwanderer. Ich bin mir sicher, dass Hauptmann Helios Euch dafür entlohnen wird, außerdem wäre ich Euch für immer dankbar!]], },
    { [[Begleitet Waldläuferin Lilatha zu Hauptmann Helios bei der Enklave der Weltenwanderer in den Geisterlanden.]], },
  },
  [9213] = {
    [[Der Schattenwächter]],
    { [[Das Machwerk eines Genies! Wie beim Eiswächter, ist es auch beim Schattenwächter ein leichter Hauch schützender Magie, der bestimmte Teile der Ausrüstung bedeckt. Der Schattenwächter wird Euch genauso wie der Eiswächter einen guten Batzen Geld kosten.]], [[<Mataus streckt seine Hand aus und reibt seine Finger aneinander.>]], [[Interesse?]], },
    { [[Mataus der Zornwirker bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern möchte 10 Insignien des Kreuzzugs und 30 Gold.]], },
  },
  [9214] = {
    [[Das Arsenal der Waldschattentrolle]],
    { [[<class>, ich möchte, dass Ihr so viele Waldschattentrolle wie möglich erledigt. Wir müssen ihre Reihen so weit wie möglich ausdünnen, bevor wir uns der Todesfestung zuwenden können. Und wenn Ihr schon mal dabei seid, sollten wir gleich zwei Fliegen mit einer Klappe schlagen! Bringt einfach sämtliche Waffen mit, die Ihr bei den Trollen findet. Es wäre eine Verschwendung sie zusammen mit den Trollen verrotten zu lassen und wir könnten sie im Kampf gegen die Geißel einsetzen.]], [[Erledigt ihre Lager im Osten und Süden bei Zeb'Tela und Zeb'Nowa.]], },
    { [[Sammelt 3 Kopfjägeräxte, 3 Schattenzauberstreitkolben, 3 Klauen eines Pantherlords und 3 Verhexerstäbe. Bringt sie zu Hauptmann Helios bei der Enklave der Weltenwanderer in den Geisterlanden.]], },
  },
  [9215] = {
    [[Bringt mir Kel'gashs Kopf!]],
    { [[GESUCHT: Bringt den Kopf von Kel'gash dem Boshaften, Häuptling der Waldschattentrolle, zu Hauptmann Helios. Die Wahl der Mittel steht frei. Kel'gash soll sich irgendwo im Süden, in Zeb'Nowa, herumtreiben.]], [[BELOHNUNG: Ja, aber nicht im Voraus!]], },
    { [[Bringt den Kopf von Kel'gash dem Boshaften zu Hauptmann Helios bei der Enklave der Weltenwanderer in den Geisterlanden.]], },
  },
  [9216] = {
    [[Faulende Herzen]],
    { [[Ich habe gehört, dass die Amanitrolle die Herzen ihrer Feinde essen, um deren Stärke zu stehlen. Sie könnten etwas im Schilde führen, <name>. Bereitet man sie richtig auf, kann man aus den Herzen der Untoten mächtige Elixiere gewinnen.]], [[Jagt die Zombies in der Todesschneise und bringt mir ihre Herzen. Ich werde Euch die Art von Verstärkung zeigen, die wir Euren körperlichen Fähigkeiten im Kampf gegen die Untoten verleihen können.]], },
    { [[Bringt 10 faulende Herzen zu Magistrix Aminel in Tristessa.]], },
  },
  [9217] = {
    [[Mehr faulende Herzen]],
  },
  [9218] = {
    [[Wirbelknochenstaub]],
    { [[Die dunklen Energien, welche durch die Körper reanimierter Skelette fließen, reichern sich hauptsächlich in ihrer Wirbelsäule an. Ich habe eine Methode entdeckt, dank der man diese Energie in einen Trank mischen kann, der unsere magischen Fähigkeiten im Kampf gegen die Untoten verstärkt.]], [[Bringt mir ein paar Einheiten Wirbelknochenstaub und ich werde Euch gerne eine Probe von dem Trank abgeben. Ihr könnt zahlreiche Skelette in der Todesschneise finden, westlich von hier.]], },
    { [[Bringt 10 Einheiten Wirbelknochenstaub zu Magistrix Aminel in Tristessa.]], },
  },
  [9219] = {
    [[Mehr Wirbelknochenstaub]],
  },
  [9220] = {
    [[Krieg gegen die Todesfestung]],
    { [[Wenn Ihr unserer Sache dienen wollt, gibt es keinen besseren Ort und keinen idealeren Zeitpunkt, als das Hier und Jetzt.]], [[Mit dem Wiederaufbau des Sanktums der Sonne haben wir das Banner des Widerstandes im Angesicht der Thronfeste unseres Feindes gehisst, kurz vor den Toren der Todesfestung.]], [[Geht und tötet die Horden der Untoten, die diesen finsteren Hort der Geißel bewohnen.]], },
    { [[Tötet in der Todesfestung 5 Augen von Dar'khan, 6 nerubische Zenturio und 6 Wehklagende und kehrt anschließend zu Magister Idonis beim Sanktum der Sonne in den Geisterlanden zurück.]], },
  },
  [9221] = {
    [[Überragende Kampfausrüstung - Freund der Dämmerung]],
    { [[Seid gegrüßt, <mein Freund/meine Freundin>. Besitzt Ihr Insignien der Dämmerung oder des Kreuzzugs, die Ihr gerne für mächtige Gegenstände einlösen würdet?]], [[Ich muss Euch warnen, wer bei der Argentumdämmerung einen freundlichen Ruf genießt, muss sich bei Käufen mit einem hohen Aufpreis abfinden. Ich würde Euch raten, Euren Ruf bei der Argentumdämmerung erst einmal zu verbessern, bevor Ihr Eure Pläne weiterverfolgt.]], },
    { [[Für 30 Insignien der Dämmerung und 30 Insignien des Kreuzzugs dürft Ihr Euch einen Gegenstand aus den Schatzkammern der Argentumdämmerung aussuchen.]], },
  },
  [9222] = {
    [[Epische Kampfausrüstung - Freund der Dämmerung]],
    { [[Für 110 Insignien der Dämmerung und 110 Insignien des Kreuzzugs dürft Ihr Euch einen Gegenstand aus den Schatzkammern der Argentumdämmerung aussuchen.]], },
    { [[Für 110 Insignien der Dämmerung und 110 Insignien des Kreuzzugs dürft Ihr Euch einen Gegenstand aus den Schatzkammern der Argentumdämmerung aussuchen.]], },
  },
  [9223] = {
    [[Überragende Kampfausrüstung - Wohlwollen der Dämmerung]],
