  [9225] = {
    [[Epische Kampfausrüstung - Respekt der Dämmerung]],
    { [[Seid gegrüßt, <mein Freund/meine Freundin>. Besitzt Ihr Insignien der Dämmerung oder des Kreuzzugs, die Ihr gerne für sehr mächtige Gegenstände einlösen würdet?]], [[Ich muss Euch warnen, wer bei der Argentumdämmerung einen respektvollen Ruf genießt, muss für epische Gegenstände noch immer einen sehr hohen Preis bezahlen. Ich würde Euch raten, Euren Ruf bei der Argentumdämmerung erst einmal zu verbessern, bevor Ihr Eure Pläne weiterverfolgt.]], },
    { [[Für 45 Insignien der Dämmerung und 45 Insignien des Kreuzzugs dürft Ihr Euch einen Gegenstand aus den Schatzkammern der Argentumdämmerung aussuchen.]], },
  },
  [9226] = {
    [[Überragende Kampfausrüstung - Respekt der Dämmerung]],
    { [[Seid gegrüßt, <mein Freund/meine Freundin>. Besitzt Ihr Insignien der Dämmerung oder des Kreuzzugs, die Ihr gerne für mächtige Gegenstände einlösen würdet?]], [[Wer bei der Argentumdämmerung einen respektvollen Ruf genießt, kann Gegenstände zu einem recht angemessenem Preis erwerben. Ich würde Euch zu einem Geschäft raten!]], },
    { [[Für 7 Insignien der Dämmerung und 7 Insignien des Kreuzzugs dürft Ihr Euch einen Gegenstand aus den Schatzkammern der Argentumdämmerung aussuchen.]], },
