  [9228] = {
    [[Epische Kampfausrüstung - Ehrfurcht der Dämmerung]],
    { [[Seid gegrüßt, <mein Freund/meine Freundin>. Besitzt Ihr Insignien der Dämmerung oder des Kreuzzugs, die Ihr gerne für sehr mächtige Gegenstände einlösen würdet?]], [[Wer bei der Argentumdämmerung einen ehrfürchtigen Ruf genießt, erhält bei Käufen garantiert den besten Preisnachlass. Ich würde Euch zu einem Geschäft raten.]], },
    { [[Für 27 Insignien der Dämmerung und 27 Insignien des Kreuzzugs dürft Ihr Euch einen Gegenstand aus den Schatzkammern der Argentumdämmerung aussuchen.]], },
  },
  [9229] = {
    [[Ramaladnis Schicksal]],
    { [[Ramaladni war ein Krieger der Bruderschaft; er war ein Schüler von Hochlord Mograine bevor die Dämmerung überhaupt existierte! Als ihn die Nachricht von Mograines Tod erreichte, wurde er von Wut und Hass überwältigt.]], [[Versteht bitte, <name>, wenn Wut und der Drang nach Vergeltung von jemandem Besitz ergreifen, hört er auf, ein Mensch zu sein, und wird stattdessen zur Verkörperung der Emotionen, die ihn beherrschen. Und so geschah es, dass Ramaladni, beseelt vom Geist der Vergeltung, Naxxramas betrat um Rache zu üben... und niemals zurückkehrte.]], [[Betretet Naxxramas und findet heraus, was mit Ramaladni geschah.]], },
    { [[Betretet Naxxramas und bringt Ramaladnis Schicksal in Erfahrung.]], },
  },
  [9230] = {
    [[Ramaladnis eisiger Griff]],
    { [[Den Ring wiederherzustellen, scheint eine ziemliche Herausforderung zu sein, <name>. Seht ihr, er wurde hergestellt aus Runen, die sich in Bauwerken der Geißel finden - in Bauwerken wie Naxxramas. Es könnten noch weitere Materialien gebraucht werden, aber Eure Hauptsorge sollte es sein, diese gefrorenen Runen zu beschaffen. Ich kann dem Ring seinen alten Glanz zurückgeben, wenn ihr mir bringt, was ich dazu benötige.]], },
    { [[Korfax in der Kapelle des Hoffnungsvollen Lichtes möchte, dass Ihr ihm 1 gefrorene Rune, 1 blauen Saphir und 1 Arkanitbarren bringt.]], },
  },
  [9232] = {
    [[Das Einzige, das ich kann...]],
    { [[Ihr haltet Euch für einen Helden der Dämmerung, ja? Ich habe gesehen, wie unsere besten Soldaten von Kel'Thuzads Gefolgsleuten überrannt wurden. Wie könnte jemand, der so ahnungslos ist wie Ihr, in Naxxramas bestehen?]], [[<Wilhelm seufzt.>]], [[Pah, was für eine Wahl habe ich denn? Ihr seid das Beste, das ich zur Verfügung habe; warum sollte ich Euch nicht helfen?]], [[Omarion hat mir einen Plan hinterlassen. Es ist das Einzige, das ich kann... Ich kann ein paar Varianten dieser Hosen herstellen, Ihr müsst mich nur mit den Materialien versorgen.]], },
    { [[Handwerker Wilhelm in der Kapelle des Hoffnungsvollen Lichts möchte, dass Ihr ihm 2 gefrorene Runen, 2 Essenzen des Wassers, 2 blaue Saphire und 30 Goldstücke bringt.]], },
  },
  [9233] = {
