  [9272] = {
    [[Kleider machen Seebären]],
  },
  [9274] = {
    [[Geister der Ertrunkenen]],
    { [[Der See im Norden wird seit langer Zeit von den Geistern meiner Ahnen heimgesucht, <name>. Die armen Seelen waren Opfer eines tragischen Unfalls, bei dem etliche von ihnen am Grund des Sees ertranken.]], [[Die Ursache dieser Geschehnisse ist längst in Vergessenheit geraten, aber ihre quälenden Schreie erinnern noch heute an ihren Fluch.]], [[Falls Ihr dazu bereit seid, möchte ich, dass Ihr zum See geht und ihrem Leiden ein Ende bereitet, <name>. Ich wäre Euch dankbar dafür, und sie auch, da bin ich sicher.]], },
    { [[Waldläufer Krenn'an aus der Enklave der Weltenwanderer möchte, dass Ihr beim nahegelegenen See 8 rachsüchtige Erscheinungen und 8 ruhelose Erscheinungen tötet.]], },
  },
  [9275] = {
    [[In der Würze liegt der Tod]],
    { [[Ich wurde von den Weltenwanderern angeheuert, um ihnen bei der Lösung ihres Trollproblems behilflich zu sein. Ich denke, dass ich die Antwort auf ihr Problem parat habe.]], [[Ich verfüge über eine Reihe hochwirksamer Gifte. Trolle sind lebende Wesen wie Ihr und ich, und müssen daher regelmäßig Nahrung zu sich nehmen. Wenn wir also etwas Gift unter ihr Essen mischen... nun, das Resultat dürfte sehr unterhaltsam sein!]], [[Sicherlich möchtet Ihr uns zur Hand gehen, um uns die Waldschattentrolle südlich von hier, in Zeb'Nowa, vom Hals zu schaffen? Das Lager erstreckt sich über die ganze nördliche und südliche Seite der Straße nach Zul'Aman.]], },
    { [[Vergiftet die Trollnahrungsvorräte in den Lagern von Zeb'Nowa. Kehrt anschließend zu Apotheker Venustus bei der Enklave der Weltenwanderer in den Geisterlanden zurück.]], },
  },
  [9276] = {
    [[Angriff auf Zeb'Tela]],
    { [[Unsere Ressourcen hier sind recht begrenzt, <class>, daher ist Eure Hilfe für uns unverzichtbar. Ich wurde vom Hauptmann damit beauftragt, die regelmäßigen Störangriffe auf die Lager der Waldschattentrolle zu überwachen. Wir versuchen die Trolle so lange zu beschäftigen, bis wir genug Kräfte für eine vernichtende Offensive zusammenziehen können, um sie endgültig auszulöschen.]], [[Bis dahin benötigen wir jeden Freiwilligen, der uns dabei helfen kann, die Anzahl der Waldschattentrolle zu reduzieren.]], [[Denkt Ihr, Ihr könntet nach Osten in Richtung Zeb'Tela reisen und ihnen ein wenig Schaden zufügen?]], },
    { [[Tötet 8 Schattenzauberer der Waldschattentrolle und 8 Kopfjäger der Waldschattentrolle. Kehrt anschließend zu Weltenwanderin Solanna bei der Enklave der Weltenwanderer in den Geisterlanden zurück.]], },
  },
  [9277] = {
    [[Angriff auf Zeb'Nowa]],
    { [[Es ist Zeit für eine größere Herausforderung. Zeb'Nowa, südlich von hier, beherbergt die mächtigsten Waldschattentrolle. In unserem Bestreben, die Anzahl der Trolle so klein wie möglich zu halten, erteile ich Euch den Auftrag, dorthin zu gehen und alle Euch zur Verfügung stehenden Mittel einzusetzen, um den Trollen so viel wie möglich zuzusetzen.]], [[Kann ich auf Euch zählen, <name>? Seid Ihr bereit für die Trolljagd?]], },
    { [[Tötet 10 Pantherlords der Waldschattentrolle und 10 Verhexer der Waldschattentrolle. Kehrt anschließend zu Weltenwanderin Solanna bei der Enklave der Weltenwanderer in den Geisterlanden zurück.]], },
  },
  [9278] = {
    [[Willkommen!]],
    { [[Welcome to the World of Warcraft!]], [[As special thanks for purchasing the World of Warcraft Collector's Edition, turn in this gift voucher to Jaeleil at the Crash Site in Ammen Vale.  You'll then be given a gift: a little companion to join you on your quest for adventure and glory.]], [[Thanks again, and enjoy your stay in the World of Warcraft!]], },
    { [[Bring the Ammen Vale Gift Voucher to Jaeleil inside the ship at the Crash Site in Ammen Vale.]], },
  },
  [9279] = {
    [[Ihr habt überlebt!]],
    { [[Kümmert Euch nicht um meine Wunden, <Bruder / Schwester>. Es ist ein Wunder, dass überhaupt jemand von uns überlebt hat!]], [[Ich dachte schon, Ihr wärt nicht durchgekommen; Ihr wurdet seit Wochen vermisst!]], [[Es ist etwas passiert und die Exodar ist abgestürzt. Viele ließen ihr Leben.]], [[Es ist ein Segen, dass Ihr noch unter uns weilt, <name>! Mit Euch an unserer Seite werden wir bald wieder in Sicherheit sein! Proenitus hat mich beauftragt, Euch zu ihm zu schicken, sobald Ihr erwacht seid. Er wartet außerhalb des Schiffes, südwestlich am Fuße des Hügels auf Euch.]], },
    { [[Sprecht mit Proenitus bei der Absturzstelle im Am'mental.]], },
  },
  [9280] = {
    [[Wiederaufladen der Heilkristalle]],
    { [[Unsere dringlichste Sorge ist die Erschöpfung unserer Heilkristalle. Wir müssen sie so schnell wie möglich wieder aufladen!]], [[Unglücklicherweise können wir das derzeit nur, indem wir das Blut der einheimischen Talmotten abzapfen. Ihr Blut eignet sich als Ersatzstoff.]], [[Man findet die Motten entlang des gesamten Tals, jedoch verstärkt im Nordwesten.]], },
    { [[Beschafft Euch 6 Phiolen mit Mottenblut von den in der Nähe hausenden Talmotten und bringt sie zu Proenitus bei der Absturzstelle im Am'mental.]], },
  },
  [9281] = {
    [[Den Weg frei machen]],
    { [[Magister Kaendris hat mich entsandt, um die Gegend von aggressiven Wildtieren zu säubern. Sobald die Gegend sicher ist, wollen die Magister hier einen Außenposten gegen die Geißel errichten.]], [[Wenn Ich als Lehrling eines gelernt habe, dann ist das, Verantwortung zu delegieren, besonders bei solch lebensgefährlichen Unternehmungen!]], [[Wenn Ihr Interesse habt, werde ich Euch gut dafür bezahlen, die Großen Spindelweber und Geisterklauenverheerer westlich und südlich von hier aus dem Weg zu räumen.]], },
    { [[Lehrling Vor'el von Andiliens Grund in den Geisterlanden möchte, dass Ihr 10 Große Spindelweber und 10 Geisterklauenverheerer tötet.]], },
  },
  [9282] = {
    [[Die Enklave der Weltenwanderer]],
    { [[Die Magister und die Weltenwanderer mögen nicht immer einer Meinung sein, <name>, doch wenn es um Dar'Khan geht, stimmen alle überein, dass wir gemeinsam einen Weg finden müssen, um ihn und seine Legionen zu besiegen.]], [[Waldläuferin Vynna, in der Enklave der Weltenwanderer im Nordosten, ist auf einige wichtige Informationen über unseren Feind gestoßen. Sucht sie auf und seht, ob sie Eure Hilfe benötigt.]], },
    { [[Magister Kaendris beim Sanktum der Sonne möchte, dass Ihr mit Waldläuferin Vynna bei der Enklave der Weltenwanderer in den Geisterlanden sprecht.]], },
  },
  [9283] = {
    [[Rettet die Überlebenden!]],
    { [[Jetzt haben wir wieder genügend Heilkristalle, aber sie sind nicht der einzige Weg, wie wir den Verletzten helfen können. Alle Draenei können die Gabe der Naaru einsetzen, um zu heilen. Ihr müsst den Überlebenden des Absturzes mit dieser Gabe helfen.]], [[Die meisten Überlebenden findet Ihr im Westen und Nordwesten, aber der Absturz hat uns alle im gesamten Tal verstreut.]], [[Die Überlebenden haben sehr unter der Strahlung der Energiekristalle gelitten, daher solltet Ihr sie an dem roten Schein, der sie umgibt, erkennen können.]], [[Ihr müsst uns helfen, sie zu retten, <name>!]], },
    { [[Wendet Eure Gabe der Naaru an, um einen der Überlebenden der Draenei zu heilen, die über das ganze Am'mental verstreut sind. Kehrt dann zu Zalduun bei der Absturzstelle im Am'mental zurück.]], },
  },
