  [9292] = {
    [[Gesprungener nekrotischer Kristall]],
    { [[Ihr entdeckt beim Körper der toten Monstrosität einen gesprungenen Kristall, der langsam mit nekrotischer Energie pulsiert.]], },
    { [[Bringt den gesprungenen nekrotischen Kristall zu Leutnant Orrin außerhalb der Tore von Sturmwind.]], },
  },
  [9293] = {
    [[Was getan werden muss...]],
    { [[Der nahegelegene See wurde von einem Energiekristall des Schiffs verseucht. Zahlreiche Pflanzen und Tiere des Am'mentals sind deswegen mutiert.]], [[Ich habe bereits eine Theorie aufgestellt, wie man den See heilen kann, allerdings brauche ich Proben von mutierten Wurzelpeitschern für eine Auswertung. Man findet sie im Westen der Am'menfelder.]], [[Werdet Ihr mir helfen, <class>?]], },
    { [[Sammelt 10 Peitscherproben ein und bringt sie zu Botanikerin Taerix bei der Absturzstelle im Am'mental.]], },
  },
  [9294] = {
    [[Die Heilung des Sees]],
    { [[Dem Licht sei Dank, ich glaube es wird funktionieren! Ich habe einen Wirkstoff, den Ihr beim Silberwellensee im Süden einsetzen müsst. Er wird den Effekt des strahlenden Energiekristalls des Schiffs, der hier heruntergestürzt ist, neutralisieren.]], [[Schwimmt raus auf den See und verwendet dort den Neutralisierungswirkstoff. Keine Sorge, Ihr werdet der Strahlung nicht lange genug ausgesetzt sein, um verseucht zu werden.]], },
    { [[Verwendet den Neutralisierungswirkstoff bei dem strahlenden Energiekristall und kehrt anschließend zu Botanikerin Taerix bei der Absturzstelle im Am'mental zurück.]], },
  },
  [9295] = {
    [[Brief von der Front]],
    { [[Ihr habt dieses Schreiben bei einem der zahlreichen Diener der Geißel gefunden, die Ihr getötet habt. Sein Äußeres und der Geruch lassen darauf schließen, dass es sich schon seit längerem im Besitz des Untoten befunden haben muss. Vielleicht wäre ja jemand bei der Kapelle des Hoffnungsvollen Lichts an einer weiteren Untersuchung interessiert...]], },
    { [[Bringt den zerfledderten Brief zum Bewahrer der Schriften bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern.]], },
  },
  [9299] = {
    [[Notiz von der Front]],
