  },
  [9301] = {
    [[Umschlag von der Front]],
    { [[Ihr habt dieses Schreiben bei einem der zahlreichen Diener der Geißel gefunden, die Ihr getötet habt. Sein Äußeres und der Geruch lassen darauf schließen, dass es sich schon seit längerem im Besitz des Untoten befunden haben muss. Vielleicht wäre ja jemand bei der Kapelle des Hoffnungsvollen Lichts an einer weiteren Untersuchung interessiert...]], },
    { [[Bringt den blutbefleckten Umschlag zum Bewahrer der Schriften bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern.]], },
  },
  [9302] = {
    [[Schreiben von der Front]],
    { [[Ihr habt dieses Schreiben bei einem der zahlreichen Diener der Geißel gefunden, die Ihr getötet habt. Sein Äußeres und der Geruch lassen darauf schließen, dass es sich schon seit längerem im Besitz des Untoten befunden haben muss. Vielleicht wäre ja jemand bei der Kapelle des Hoffnungsvollen Lichts an einer weiteren Untersuchung interessiert...]], },
    { [[Bringt das zerknitterte Schreiben zum Bewahrer der Schriften bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern.]], },
  },
  [9303] = {
    [[Immunisierung]],
    { [[Zhanaa braucht unsere Hilfe, um Teile aus der Exodar zu bergen. Taerix hat eine Immunisierungsmethode vorbereitet, mit der die Nisteleulkins behandelt werden können. Sie bewohnen die Gegend, in der die meisten Teile gefunden werden können.]], [[Nähert Euch den noch nicht verseuchten Biestern und wendet den Kristall auf sie an. Die anderen müsst Ihr leider umbringen. Ihr findet sie im Nisteldickicht und bei den Nistelhügeln im Südosten.]], },
    { [[Wendet den Immunisierungskristall auf 6 Nisteleulkin an. Kehrt anschließend zu Verteidiger Aldar bei der Absturzstelle im Am'mental zurück.]], },
  },
  [9304] = {
    [[Dokument von der Front]],
    { [[Ihr habt dieses Schreiben bei einem der zahlreichen Diener der Geißel gefunden, die Ihr getötet habt. Sein Äußeres und der Geruch lassen darauf schließen, dass es sich schon seit längerem im Besitz des Untoten befunden haben muss. Vielleicht wäre ja jemand bei der Kapelle des Hoffnungsvollen Lichts an einer weiteren Untersuchung interessiert...]], },
    { [[Bringt das verschmierte Dokument zum Bewahrer der Schriften bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern.]], },
  },
  [9305] = {
    [[Ersatzteile]],
    { [[Wir konnten einen beschädigten holografischen Emitter nach dem Absturz bergen. Sollten wir ihn reparieren können, besteht die Möglichkeit, dass wir noch andere Überlebende außerhalb des Am'mentals finden.]], [[Von dem Schiff hätte man einige Apparaturen sichern können, die geeignete Ersatzteile geliefert hätten, aber die heimischen Eulkin haben sie verschleppt, bevor wir sie überhaupt erreichen konnten.]], [[<name>, Ihr müsst ins Nisteldickicht und in die Nistelhügel im Süden gehen und die Ersatzteile zurückholen!]], },
    { [[Beschafft 4 Emitterersatzteile und kehrt anschließend zu Technikerin Zhanaa bei der Absturzstelle im Am'mental zurück.]], },
  },
  [9309] = {
    [[Der vermisste Späher]],
    { [[Verschiedene Quellen konnten uns nun bestätigen, dass beim Schattenkamm ungewöhnliche Dinge vor sich gehen.]], [[Das ist äußerst beunruhigend, denn wir dachten, dass die bisherigen Aufklärungsmissionen gründlich genug gewesen wären. Einer meiner Späher hat sich noch nicht zurückgemeldet.]], [[<class>, Ihr müsst für mich Richtung Südwesten gehen, zur anderen Uferseite des Silberwellensees, und Tolaan finden. Findet heraus, was ihn aufgehalten hat.]], },
    { [[Sprecht mit Tolaan im Schattenkamm im Am'mental.]], },
  },
  [9310] = {
    [[Schwacher nekrotischer Kristall]],
    { [[Bringt den schwachen nekrotischen Kristall zu Leutnant Rukag im Hof von Unterstadt.]], },
    { [[Bringt den schwachen nekrotischen Kristall zu Leutnant Rukag im Hof von Unterstadt.]], },
  },
  [9311] = {
    [[Blutelfenspionage]],
    { [[Ich habe einen Plan. Wir müssen ihren Anführer umbringen. Vielleicht ziehen sie sich ohne ihren Meister zurück, um sich neu zu formieren. Wir müssen Zeit schinden, damit wir die Reparaturen abschließen können.]], [[Verteidiger Aldar muss davon erfahren. Tötet die Gutachterin und kehrt dann zu ihm zurück, um ihn zu erzählen, was wir herausgefunden haben.]], },
    { [[Tötet Gutachterin Kandress und informiert anschließend Verteidiger Aldar bei der Absturzstelle im Am'mental über die Ereignisse.]], },
  },
  [9312] = {
    [[Der Emitter]],
    { [[<name>, während Ihr den Schattenkamm erkundet habt, hat Zhanaa weiter am holografischen Emitter gearbeitet. Vielleicht solltet Ihr mit ihr sprechen.]], },
    { [[Sprecht mit Technikerin Zhanaa bei der Absturzstelle im Am'mental.]], },
  },
  [9313] = {
    [[Reise zur Azurwacht]],
    { [[Soweit so gut, <name>, Ihr müsst allerdings sofort zur Azurwacht reisen und mit Dyvuun sprechen!]], [[Es gibt einen Pfad, der westlich von dieser winzigen Insel führt. Folgt ihm und überquert den Kristallfluss, um auf den größeren Teil der Insel zu gelangen. Wenn Ihr dem Pfad noch weiter folgt, solltet Ihr die Azurwacht finden.]], [[Vielleicht könnt Ihr sie ja überzeugen, uns etwas Hilfe und Vorräte zukommen zu lassen. Wir kommen nach, sobald wir können.]], [[Ich danke Euch, ohne Euch hätten wir das nicht geschafft!]], },
    { [[Sprecht mit Techniker Dyvuun bei der Azurwacht auf der Azurmythosinsel.]], },
  },
  [9314] = {
    [[Nachricht von der Azurwacht]],
    { [[<class>! Ein paar dieser mutierten Biester haben mich angesprungen und jetzt ist mein Bein verletzt.]], [[Nachdem wir von Zhanaa gehört hatten, wurde ich von der Azurwacht auf eine Spähmission geschickt. Könntet Ihr vielleicht an meiner statt mit Versorgerin Chellan sprechen? Sie muss wissen, wie viele Leute sie in der Azurwacht erwarten soll. Ihr müsst lediglich den Kristallfluss hier überqueren und dem Pfad bis zur Azurwacht folgen.]], [[Vergesst nicht, nach einer Unterkunft zu fragen, wenn Ihr dort seid.]], },
    { [[Sprecht mit Versorgerin Chellan bei der Azurwacht auf der Azurmythosinsel.]], },
  },
  [9315] = {
    [[Anok'suten]],
    { [[Kommt näher, ich... ich sterbe.]], [[Man entsandte mich hierher, um... um die Neruber zu besiegen, doch Vandril konnte nicht ahnen, dass sie... sie eine Anführerin haben.]], [[<Der sterbende Blutelf spuckt etwas Blut.>]], [[Ihr müsst Anok'suten finden und... und töten. Sie wandert in der Umgebung von... Sonnenkuppe umher.]], },
    { [[Tötet Anok'suten und kehrt anschließend zu Arkanist Vandril in Tristessa in den Geisterlanden zurück.]], },
  },
  [9317] = {
    [[Geweihte Wetzsteine]],
  },
  [9318] = {
    [[Gesegnetes Zauberöl]],
  },
  [9319] = {
    [[Licht im Dunkeln]],
    { [[Wir sind nicht die Einzigen, die dieses Fest feiern, <name>. Die Flamme ist ein wichtiger Bestandteil aller Lebewesen, aller Kulturen. Selbst an den dunkelsten Orten beginnen die Feuer zu leuchten.]], [[Wenn Ihr dergleichen sehen möchtet, so wagt Euch in die Tiefe. Sucht nach den Freudenfeuern in Stratholme, Scholomance, bei den Ogern des Düsterbruchs und in den Hallen der Schwarzfelsspitze selbst. Ich erwarte Eure Rückkehr.]], },
    { [[Findet und berührt die Freudenfeuer innerhalb der Schwarzfelsspitze, dem Düsterbruch, Scholomance und Stratholme. Kehrt dann zum Flammenbewahrer zurück.]], },
  },
  [9320] = {
    [[Erheblicher Manatrank]],
  },
  [9321] = {
    [[Erheblicher Heiltrank]],
  },
  [9322] = {
    [[Glühende Feuer in Kalimdor]],
    { [[Die Freudenfeuer des Sonnenwendfests werden überall in Azeroth gefeiert, die mannigfaltigen Länder Kalimdors sind dabei keine Ausnahme. Ihr scheint mir tauglich für eine Reise - wie wäre es mit einem Ausflug?]], [[Das Sonnenwendfest ist erst dann ein Ereignis, wenn man die blau glühenden Feuer in der Welt gesehen hat. Die Feiernden haben Feuer im eisigen Westen von Winterquell, im Nordosten von Azshara, in der Nähe der Heldenwacht in Silithus und beim Eingang zum üppigen Krater von Un'Goro entzündet. Kehrt zu mir zurück, sobald Ihr die Reise beendet habt.]], },
    { [[Sucht die Lager des Sonnenwendfests in Azshara, Silithus, dem Krater von Un'Goro und Winterquell auf. Kehrt dann zum Flammenbewahrer zurück.]], },
  },
  [9323] = {
    [[Glühende Feuer in den Östlichen Königreichen]],
    { [[Das Fest der Flamme ist im ganzen Land verbreitet, <class>. Kein Ort bleibt von der Wärme dieses Ereignisses unberührt, und die prächtigsten Feuer lodern in einem strahlenden Blau. Sucht nach ihnen.]], [[Eines befindet sich genau nördlich von der Schwarzfelsspitze. Die anderen liegen tief im Innern der Östlichen Pestländer, in der Nähe eines verfallenen Dorfes; entlang der Straße, die zu den Klippen im bergigen Hinterland führt, und auf dem Weg zu dem Dunklen Portal in den Verwüsteten Landen.]], [[Kehrt anschließend zu mir zurück.]], },
    { [[Reist zu den Lagern des Sonnenwendfests in den Verwüsteten Landen, den Östlichen Pestländern, dem Hinterland und der Sengenden Schlucht. Kehrt dann zum Flammenbewahrer zurück.]], },
  },
  [9324] = {
    [[Raubt die Flamme von Orgrimmar]],
    { [[Die Flamme, die aus dem Zentrum von Orgrimmar entwendet wurde, durchflutet Euch mit Wärme. Diese durchdringende Macht ist Euch fremd, aber der Meister der Lehren könnte vielleicht mehr darüber wissen...]], },
    { [[Bringt die Flamme von Orgrimmar zum Meister der Lehren.]], },
  },
  [9325] = {
    [[Raubt die Flamme von Donnerfels]],
    { [[Die Flamme, die dem Schamanen von Donnerfels entwendet wurde, durchflutet Euch mit Wärme. Diese durchdringende Macht ist Euch fremd, aber der Meister der Lehren könnte vielleicht mehr darüber wissen...]], },
    { [[Bringt die Flamme von Donnerfels zum Meister der Lehren.]], },
  },
  [9326] = {
    [[Raubt die Flamme von Unterstadt]],
    { [[Die Flamme, die aus den Ruinen über Unterstadt entwendet wurde, durchflutet Euch mit Wärme. Diese durchdringende Macht ist Euch fremd, aber der Meister der Lehren könnte vielleicht mehr darüber wissen...]], },
    { [[Bringt die Flamme von Unterstadt zum Meister der Lehren.]], },
  },
  [9327] = {
    [[Die Verlassenen]],
    { [[Ich weiß, es mag seltsam erscheinen, aber die Verlassenen sind hier, um uns zu helfen. Ihr solltet mit Hochexekutor Mavren sprechen. Er befindet sich in dem Turm im Westen der Stadt.]], [[Wenn Ihr mit ihm gesprochen habt, kehrt bitte zu mir zurück, es gibt da eine schlimme Angelegenheit, bei der ich dringend Eure Unterstützung benötige.]], },
    { [[Sprecht mit Hochexekutor Mavren in Tristessa in den Geisterlanden.]], },
  },
  [9328] = {
    [[Held der Sin'dorei]],
    { [[Dies ist der Beginn eines neuen Zeitalters für unser Volk, <name>. Wir werden unser Land zurückerobern und unseren Bund mit Sylvanas und ihren Verbündeten festigen.]], [[Bringt den Kopf des Verräters nach Silbermond, zu Lor'themar Theron, dem Lordregenten Silbermond. Er wird begierig sein, die Neuigkeit zu erfahren.]], },
    { [[Bringt Dar'Khans Kopf zu Lor'themar Theron in Silbermond.]], },
  },
  [9329] = {
    [[Die Verlassenen]],
    { [[Ich weiß, es mag seltsam erscheinen, aber die Verlassenen sind hier, um uns zu helfen. Ihr solltet mit Hochexekutor Mavren sprechen. Er befindet sich in dem Turm im Westen der Stadt.]], [[Wenn Ihr mit ihm gesprochen habt, kehrt bitte zu mir zurück, es gibt da eine schlimme Angelegenheit, bei der ich dringend Eure Unterstützung benötige.]], },
    { [[Sprecht mit Hochexekutor Mavren in Tristessa in den Geisterlanden.]], },
  },
