  [9338] = {
    [[Treue zum Zirkel des Cenarius]],
  },
  [9339] = {
    [[Der Lohn des Diebes]],
    { [[This flame, stolen from the heart of Stormwind itself, floods you with warmth. This raw power is foreign to you, but the (NAME) might know more...]], },
    { [[Return the Flame of Stormwind to (NAME).]], },
  },
  [9340] = {
    [[Die Große Kluft]],
    { [[Wir stehen kurz davor, vollständig von den Streitkräften der Allianz eingekesselt zu werden. Mit dem ständigen Zustrom von Sin'doreiflüchtlingen aus Azeroth, müssen wir einen Weg finden, der vor den neugierigen Blicken des Feindes sicher ist.]], [[Ich habe darüber etwas nachgedacht und bin kürzlich zu einer Lösung gekommen. Wir werden eine sichere Passage durch die Große Kluft etablieren.]], [[Ja, auch wenn es dort nur so von tödlichen Felsklauen wimmelt, sind sie doch nichts weiter als bloße Tiere, denen wir beibringen können, uns zu fürchten. Ich möchte, dass Ihr loszieht und eine größere Anzahl dieser Biester erlegt. Zeigen wir ihnen, was Angst ist!]], },
    { [[Waldläuferhauptmann Venn'ren in der Falkenwacht möchte, dass Ihr 8 Steinsichelwelpen und 3 Steinsichelalphas bei der Großen Kluft östlich der Falkenwacht erlegt.]], },
  },
  [9341] = {
    [[Wappenrock der Argentumdämmerung]],
  },
  [9342] = {
    [[Plündernde Krustenbohrer]],
    { [[Als ob es nicht schon schlimm genug wäre, dass die Gan'arg in den Minen eingefallen sind, rücken uns nun diese Riesenwürmer von der anderen Seite auf die Pelle!]], [[Da Thrallmars Garnison mit der Verteidigung gegen die Legion und dem Pflegen der Verwundeten beschäftigt ist, bedeutet das, dass ich und die Minenarbeiter uns um dieses Problem kümmern müssen.]], [[<Der Großknecht zeigt auf die schlafenden Peons.>]], [[Und Ihr sehr ja selbst wie hilfreich sie sind...]], [[Ich bezahle Euch gerne ihr Gehalt, wenn Ihr ein paar dieser Würmer vernichtet.]], },
    { [[Großknecht Razelcraz bei den Minen in der Nähe von Thrallmar möchte, dass Ihr 15 plündernde Krustenbohrer tötet. Die Würmer haben die Angewohnheit, unplötzlich aus ihren unterirdischen Tunneln anzugreifen.]], },
  },
  [9343] = {
    [[Wappenrock der Argentumdämmerung]],
  },
  [9344] = {
    [[Der überstürzte Aufbruch]],
    { [[Seid gegrüßt, <name>. Die Scherbenwelt ist ein Ort voller Gefahren, beinahe jedes Mitglied unserer Expedition musste diese leidvolle Erfahrung machen. Ich befürchte, der junge Grelag hat diese Lektion aus erster Hand erfahren.]], [[Er ist ein viel versprechender Schüler, doch es mangelt ihm an Geduld! Er studiert mit Begeisterung die Geschichte seines Volkes und vergisst dabei manchmal die Gefahren des heutigen Lebens.]], [[Er ist zum Rückenbrecherposten aufgebrochen, ohne an die Legion und die Höllenorcs zu denken. Ich habe Angst, dass ihm etwas zugestoßen ist.]], },
    { [[Scharfseherin Regulkut möchte, dass Ihr ihren Schüler Grelag findet.]], },
  },
  [9345] = {
    [[Herstellung der Salbe]],
    { [[Als ich gerade Zeth'Gor erkennen konnte, wurde ich von einer Patrouille der Legion entdeckt. Ich rannte los in der Hoffnung, dass ich mich in einem der Türme in der Ferne verstecken kann, aber einer ihrer Teufelshunde hat mich eingeholt und mich fast zerfleischt!]], [[Ich konnte entkommen, aber mein Bein ist in einem sehr schlechten Zustand. Steingardist Ambelan hat mich gefunden und hierher gebracht.]], [[Jetzt muss ich einen Weg finden, um mein Bein zu versorgen. Wirbelkraut wächst in und um das Tal der Knochen im Norden herum. Eine Salbe daraus wird den Schmerz lange genug betäuben, dass ich weiterreisen kann.]], },
    { [[Grelag im Rückenbrecherposten möchte, dass Ihr ihm 12 Höllenwirbelkräuter für seine Salbe bringt.]], },
  },
  [9346] = {
    [[Wenn Hölleneber fliegen]],
    { [[Habt Ihr schon mal einen fliegenden Hölleneber gesehen? Die brauchen nicht mal Flügel!]], [[<Wachtkommandant Krunk lacht schallend.>]], [[Der Kampf gegen die Lager der Legion im Norden hat unsere Vorräte restlos erschöpft, einschließlich der Katapultmunition. Also der perfekte Augenblick, mal etwas völlig Neues auszuprobieren.]], [[Statt Steine zu verschießen, dachte ich daran, die Kadaver verhungernder Hölleneber in Brand zu stecken, um damit die Lager der Legion zu bombardieren.]], [[Meldet Ihr Euch freiwillig oder muss ich Euch erst dazu zwingen?]], },
    { [[Wachtkommandant Krunk in Thrallmar möchte, dass Ihr ihm 10 klobige Hölleneberkadaver von verhungernden Höllenebern bringt.]], },
  },
  [9349] = {
    [[Felshetzereier sammeln]],
    { [[Ich bin am verhungern!]], [[Ist das nicht ironisch? Ein verhungernder Koch?]], [[<Legassi lacht halbherzig und tippt auf ein gefaltetes Pergament in seiner Tasche.>]], [[Laut dieser Touristenkarte, der wir gefolgt waren, bevor wir so hinterhältig niedergeschossen wurden, gibt es im Südwesten eine Gegend, nahe bei den Wäldern von Terokkar. Sie wird der Messerdornpfad genannt und angeblich soll es dort von leckeren Felshetzereiern nur so wimmeln!]], [[Ich könnte ein tolles Festmahl daraus zaubern, wenn nur jemand mutig genug wäre, dorthin zu gehen und ein paar zu sammeln!]], },
    { [[Bringt 12 Felshetzereier zu Legassi bei der Zeppelinabsturzstelle auf der Höllenfeuerhalbinsel.]], },
  },
  [9351] = {
    [[Wilde Leerwandler]],
    { [[Auch wenn ich den Rahmen des Zeppelins repariert bekomme, muss ich einen Ersatztreibstoff finden! Ich habe kein Benzin mehr, und es gibt weit und breit keine Tankstelle!]], [[Südlich von hier liegt eine Gegend namens Sphärenfelder. Dort wimmelt es nur so von wilden und unkontrollierten Leerwandlern, aber für mich sind sie eher wandelnde Batterien.]], [[Wenn Ihr mir genug von Ihrer Essenz bringt, kann ich damit den Motor des Zeppelins zum Laufen bekommen. Ich wette fünf Gold, dass ich dann sogar einen niedrigeren Verbrauch habe!]], },
    { [[Bringt 10 kondensierte Leerwandleressenzen zu "Schreihals" Quack Lufthans bei der Zeppelinabsturzstelle.]], },
  },
  [9352] = {
    [[Darnassische Eindringlinge]],
    { [[Abgesehen von der konstanten Bedrohung durch die Getriebenen, befasse ich mich auch mit den zahlreichen Berichten über angebliche Nachtelfensichtungen.]], [[Obwohl viele dieser Sichtungen frei erfunden sind, gibt es doch einen Vorfall, der weiterer Untersuchungen bedarf. Das Sanktum des Westens, eine unserer primären Energiequellen, hatte eine massive Fehlfunktion und Gerüchte, eine darnassische Sabotageaktion sei der Grund dafür, machen die Runde.]], [[Begebt Euch dorthin und kümmert Euch um jeden, der verdächtig aussieht. Meldet Euch bei Leybewahrerin Velania, sie überwacht das Sanktum.]], },
    { [[Reist zum Sanktum des Westens südwestlich vom Falkenplatz und vernichtet jegliche Eindringlinge, die Ihr dort findet. Berichtet anschließend Leybewahrerin Velania.]], },
  },
  [9355] = {
    [[Eine Aufgabe für einen intelligenten Helden]],
    { [[Gut, <class>. Nun, da die Verstärkung anrückt, können wir endlich wieder ein mehr oder weniger normales Leben führen.]], [[Eines unserer ersten Ziele ist die Wiederaufnahme des Transports von Männern und Waren auf der Straße nach Südwesten, aber dazu müssen wir sie erst von den Krustenbohrern zurückfordern, die diese Gegend in Beschlag genommen haben.]], [[Wenn Ihr uns bei dieser Aufgabe helfen würdet, wären die Söhne Euch sehr dankbar. Gebt auf die aufgewühlte Erde Acht, die sie hinterlassen!]], },
    { [[Tötet 15 plündernde Krustenbohrer und kehrt dann zu Großknecht Biggums in der Ehrenfeste zurück.]], },
  },
  [9356] = {
    [[So weich wie Butter]],
    { [[Quack sagt immer, er fliegt "so weich wie Butter". Ich glaube eher, sein Kopf ist so "weich wie Butter"!]], [[Schaut Euch nur die Trümmer da drüben an. Er nennt das eine "harte" Landung.]], [[Klar, wir wurden beschossen, und ich würde es ihm ja auch nachsehen, wenn er nicht immer so mit seinen Flugkünsten angeben würde!]], [[Ich glaube, ich koche ihm ein paar Flügel, um ihm einmal einen Wink mit dem Zaunpfahl zu geben. Im Osten, im Tal der Knochen, findet Ihr Knochenhäuterbussarde. Ihre Flügel sind fleischig, dick und perfekt für meine Zwecke.]], },
    { [[Bringt 12 fleischige Bussardflügel zu Legassi bei der Zeppelinabsturzstelle.]], },
  },
  [9357] = {
    [[Meldet Euch bei Aeldon Sonnenmal]],
    { [[Wenn Ihr nach Arbeit sucht, dann sprecht mit Hauptmann Sonnenmal beim inneren Elfentor, geht einfach durch die Türen hinter mir.]], [[Lasst mich jetzt in Ruhe, ich muss diesen Halunken verhören.]], },
    { [[Meldet Euch bei Aeldon Sonnenmal beim inneren Elfentor im Immersangwald.]], },
  },
  [9358] = {
    [[Waldläuferin Sareyn]],
    { [[Betrachtet die Sicherheit dieses Orts nicht als selbstverständlich. Wir werden von der Geißel im Osten und von den Getriebenen im Westen belagert.]], [[Waldläuferin Sareyn sichert unsere Grenzen vor den Untoten, die uns von der Todesschneise her bedrängen. Folgt der Straße in Richtung Osten und erkundigt Euch, ob sie Unterstützung braucht.]], },
    { [[Meldet Euch bei Waldläuferin Sareyn außerhalb von Morgenluft im Immersangwald.]], },
  },
  [9359] = {
    [[Zuflucht der Weltenwanderer]],
    { [[Ihr habt uns geholfen die Getriebenen wieder unter Kontrolle zu bekommen und dies war von unschätzbarem Wert. Die Weltenwanderer könnten jemanden wie Euch in ihrem Kampf gegen die Amanitrolle gebrauchen.]], [[Haltet nach der Zuflucht der Weltenwanderer nordöstlich von hier Ausschau, sprecht mit Leutnant Dämmerflucht und erkundigt Euch, ob sie Unterstützung braucht.]], },
    { [[Meldet Euch bei Leutnant Dämmerflucht bei der Zuflucht der Weltenwanderer im Immersangwald.]], },
  },
  [9360] = {
    [[Angriff der Amani]],
    { [[Ihr versteht zwar die Sprache dieser Schlachtpläne nicht, aber Ihr könnt klar eine Karte mit den eingetragenen Positionen der Zuflucht der Weltenwanderer und von Morgenluft ausmachen.]], [[Am besten informiert Ihr Leutnant Dämmerflucht bei der Zuflucht der Weltenwanderer darüber.]], },
    { [[Bringt die Angriffspläne der Amani zu Leutnant Dämmerflucht bei der Zuflucht der Weltenwanderer.]], },
  },
  [9361] = {
    [[Hölleneber, die etwas andere Delikatesse]],
    { [[Diese Eier sind wundervoll, aber was haltet Ihr davon, wenn wir noch einen Zahn zulegen?]], [[Eine Scheibe Speck würde unser Mahl erst richtig abrunden, und ich weiß ganz genau, wo man sie herbekommen könnte.]], [[Dieser Ort wimmelt nur so von Höllenebern. Natürlich kann hier niemand das Fleisch überprüfen und weichklopfen, aber wir haben Glück!]], [[<Legassi kramt in seinem Beutel und holt eine Flasche hervor.>]], [[Dies wurde zwar nicht gemacht, um es mit Schweinefleisch zu benutzen, aber Improvisation ist alles!]], },
    { [[Legassi bei der Zeppelinabsturzstelle möchte, dass Ihr 8 Stück geläutertes Hölleneberfleisch sammelt. Um das Fleisch zu reinigen, schüttet die Läuterungsmischung über das besudelte Hölleneberfleisch, das Ihr von den Höllenebern gesammelt habt.]], },
  },
  [9362] = {
    [[Kriegsherr Krellian]],
    { [[Die Zeit drängt, <name>! Meine Orakelsprüche haben eine mächtige Quelle der Magie offenbart. Ich kann den Turm nicht verlassen und bitte Euch deshalb, diese bösartige Kreatur, die im Besitz dieses Artefakts ist, zu töten.]], [[Der erbärmliche Schwachkopf ist bekannt als Kriegsherr Krellian und versteckt sich hinter seinen Wachen im Tempel von Zin-Malor. Verbrennt ihn zu Asche und nehmt die prismatische Muschel, die er so begehrt, an Euch.]], },
    { [[Beschafft die prismatische Muschel für Erzmagier Xylem. Der Erzmagier befindet sich in einem Turm auf den Klippen von Azshara.]], },
  },
  [9363] = {
    [[Warnt Morgenluft]],
    { [[Geht nach Morgenluft und klärt Waldläufer Degolien über die Pläne der Amani auf. Morgenluft muss sich auch nach dem Tod von Zul'marosh noch auf einen Angriff der Trolle vorbereiten. Sagt Degolien, dass ich ihm so schnell wie möglich Verstärkung schicken werde.]], },
    { [[Reist nach Morgenluft im Immersangwald und sprecht mit Waldläufer Degolien.]], },
  },
  [9364] = {
    [[Gespaltene Magie]],
    { [[Meine Untersuchungen haben den einzigartigen Zweck für die prismatische Muschel enthüllt.]], [[Greift die Grollflossen von Azshara mit Verwandlungszaubern an und tragt dabei diese Muschel bei Euch. Wenn Ihr einen Moment wartet, wird die chaotische Aura der Muschel dafür sorgen, dass sie sich für eine kurze Zeit in mehrere kleinere Versionen aufspalten. Tötet diese Kopien so schnell wie möglich, und deren gespaltene Essenzen werden in der Muschel gesammelt.]], [[Kehrt zu mir zurück, sobald die Muschel gefüllt ist.]], },
    { [[Verwandelt die Grollflossen von Azshara und tötet die Kopien, die einige Sekunden später auftauchen. Wenn Ihr 50 verwandelte Kopien vernichtet habt, kehrt zu Erzmagier Xylem in Azshara zurück.]], },
  },
  [9365] = {
    [[Der Lohn des Diebes]],
    { [[This flame, stolen from the heart of Stormwind itself, floods you with warmth. This raw power is foreign to you, but the (NAME) might know more...]], },
    { [[Return the Flame of Stormwind to (NAME).]], },
  },
  [9366] = {
    [[Die Suche nach Teufelsblut]],
    { [[Nordöstlich von hier liegen die Teiche von Aggonar. Vor langer Zeit waren sie ein Heiligtum der Draenei, doch fielen sie der Verderbnis anheim, als das Blut des Dämonen Aggonar in die heiligen Wasser vergossen wurde.]], [[Obwohl es alt und verwässert ist, ist das Blut noch immer von einer enormen Macht erfüllt. Angetrieben durch die puren dämonischen Energien des ursprünglichen Blutes, durchstreifen Terrorbestien dieses Gebiet. Vernichtet sie und sammelt so viel von dem Teufelsblut ein, wie Ihr tragen könnt. Ich werde Euch fürstlich belohnen.]], },
    { [[Ryathen der Düstere an der Falkenwacht auf der Höllenfeuerhalbinsel möchte, dass Ihr ihm 6 Teufelsblutproben von den Terrorbestien bringt, die bei den Teichen von Aggonar umherstreifen.]], },
  },
  [9367] = {
    [[Das Fest des Feuers]],
    { [[Die Zeit des Sonnenwendfests ist wieder angebrochen!]], [[Jedes Jahr werden, wie die Tradition es verlangt, Flammenbewahrer auserkoren, die die Freudenfeuer in unseren Hauptstädten hüten. Auch wenn Feuer in ganz Azeroth brennen, so müssen unsere stets die heißesten und hellsten von allen sein, um dieser Zeit im Jahr ausreichend Ehrerbietung zu erweisen.]], [[Ich hätte da übrigens eine Aufgabe für Euch. Mir ist noch nicht zu Ohren gekommen, ob die Feuer in allen Hauptstädten auch wirklich ordentlich brennen. Reist selbst dorthin und wärmt Euch im Schein der Flammen - überzeugt Euch, dass sie ausreichend heiß sind!]], },
    { [[Wärmt Euch an den Freudenfeuern in Sturmwind, Eisenschmiede und Darnassus. Sprecht anschließend mit einem Meister der Lehren innerhalb der Hauptstädte.]], },
  },
  [9368] = {
    [[Das Fest des Feuers]],
    { [[Die Zeit des Sonnenwendfests ist wieder angebrochen!]], [[Jedes Jahr werden, wie die Tradition es verlangt, Flammenbewahrer auserkoren, die die Freudenfeuer in unseren Hauptstädten hüten. Auch wenn Feuer in ganz Azeroth brennen, so müssen unsere stets die heißesten und hellsten von allen sein, um dieser Zeit im Jahr ausreichend Ehrerbietung zu erweisen.]], [[Ich hätte da übrigens eine Aufgabe für Euch. Mir ist noch nicht zu Ohren gekommen, ob die Feuer in allen Hauptstädten auch wirklich ordentlich brennen. Reist selbst dorthin und wärmt Euch im Schein der Flammen - überzeugt Euch, dass sie ausreichend heiß sind!]], },
    { [[Wärmt Euch an den Freudenfeuern in Orgrimmar, Donnerfels und Unterstadt. Sprecht anschließend mit einem Geschichtenerzähler innerhalb der Hauptstädte.]], },
  },
  [9369] = {
    [[Wiederaufladen der Heilkristalle]],
    { [[Wir sind die Draenei. Unser Schiff ist hier abgestürzt und es gibt etliche Verwundete. Unsere dringlichste Sorge ist die Erschöpfung unserer Heilkristalle. Wir müssen sie so schnell wie möglich wieder aufladen!]], [[Unglücklicherweise können wir das derzeit nur, indem wir das Blut der einheimischen Talmotten abzapfen. Ihr Blut eignet sich als Ersatzstoff.]], [[Man findet die Motten entlang des gesamten Tals, jedoch verstärkt im Nordwesten.]], },
    { [[Beschafft Euch 6 Phiolen mit Mottenblut von den in der Nähe hausenden Talmotten und bringt sie zu Proenitus bei der Absturzstelle im Am'mental.]], },
  },
  [9370] = {
    [[Stoppt das Säuberungsritual]],
    { [[Unsere Späher haben kürzlich einen Paladin der Draenei in der Nähe der Teiche von Aggonar aufgegriffen. Wir nehmen an, dass er vorhatte, an einem Säuberungsritual zur Reinigung der Teiche teilzunehmen. Natürlich müssen wir das Vorhaben der Draenei stoppen, oder unsere Teufelsblutquelle wird für immer versiegen.]], [[Er hatte diesen Signalstein bei sich. Offensichtlich hatte er den Auftrag, einen sicheren Ort zu markieren, an dem ein Priester - sie selbst nennen sich 'Anachoret' - in aller Sicherheit ein Säuberungsritual durchführen kann. Nehmt den Stein und platziert ihn am Altar von Aggonar und bereitet einen Hinterhalt für die eintreffenden Draenei vor.]], [[Ich schlage vor, dass Ihr besser ein oder zwei Freunde mit Euch bringt.]], },
    { [[Platziert den Signalstein bei den Teichen von Aggonar am Altar von Aggonar und besiegt alle Anachoreten der Draenei, die auf Eure Signal reagieren. Kehrt anschließend mit dem Signalstein zu Ryathen dem Düsteren bei der Falkenwacht zurück.]], },
  },
  [9371] = {
    [[Botanikerin Taerix]],
    { [[<name>, Botanikerin Taerix braucht Eure Hilfe. Bitte, sucht sie bei ihren Lehrlingen außerhalb der Westseite des Schiffes auf.]], },
    { [[Sprecht mit Botanikerin Taerix bei der Absturzstelle im Am'mental.]], },
  },
  [9372] = {
    [[Dämonische Verderbnis]],
    { [[Willkommen im Posten des Cenarius, <class>. Wir bekommen hier draußen nicht oft Besuch, daher freue ich mich ganz besonders über Eure Anwesenheit. Meine Arbeit mit den Lebewesen dieser trostlosen Umgebung macht es oft erforderlich, alleine in die abgelegensten Winkel zu reisen.]], [[Die Expedition des Cenarius hat mich mit dem Studium einiger der gefährlichsten Tiere der Scherbenwelt beauftragt, und dabei habe ich eine verblüffende Entdeckung gemacht. Obwohl viele Kreaturen bereits massive Veränderungen durchgemacht haben, scheint die Verderbnis noch immer neue Mutationen hervorzurufen. Ich habe mit dem Sammeln von Blutproben begonnen, um diese Veränderungen zu dokumentieren, und dabei ist mir jede Hilfe recht.]], },
    { [[Thiah Rotmähne im Posten des Cenarius auf der Höllenfeuerhalbinsel möchte, dass Ihr 6 Blutproben eines Höllenebers von bulligen Höllenebern sammelt.]], },
  },
  [9373] = {
    [[Fehlender Schriftverkehr]],
    { [[Bei der Untersuchung des ramponierten Lederkoffers stellt Ihr fest, dass die Beschädigung des Behälters von der ätzenden Flüssigkeit im Verdauungstrakt des Wurms herrühren muss. Das Leder scheint einst kunstvoll verarbeitet gewesen zu sein, aber die Verzierungen sind nicht mehr deutlich sichtbar.]], [[Das Einzige, was noch zu erkennen ist, ist ein abgenutztes Symbol des Zirkels des Cenarius. Da Ihr nichts über den Inhalt wisst, haltet Ihr es für das Beste, den Koffer zu einem Mitglied des Zirkels zu bringen.]], },
    { [[Bringt den zerfressenen Lederkoffer zu Thiah Rotmähne in der Nähe der Ruinen von Sha'naar im westlichen Teil der Höllenfeuerhalbinsel.]], },
  },
  [9374] = {
    [[Arelions Tagebuch]],
    { [[Mein Mann Arelion war seit unserer Ankunft in der Scherbenwelt von den Teichen von Aggonar fasziniert. Er verbrachte fast seine gesamte Zeit mit dem Studium der heimischen Schleimarten!]], [[Vor einigen Tagen verschwand er spurlos. Man sagte mir, dass er tot sei und dass ich nichts mehr für ihn tun könnte.]], [[Doch was ich noch zu tun vermag, ist sicherzustellen, dass seine Forschungen nicht mit ihm gestorben sind. Begebt Euch zu den Teichen im Nordosten und haltet nach den Seiten seines Tagebuchs Ausschau. Er bewahrte sie immer in einem braunen Wanderknappsack auf.]], },
    { [[Magistrix Carinda bei der Falkenwacht auf der Höllenfeuerhalbinsel möchte, dass Ihr zu den Teichen von Aggonar reist und Arelions Tagebuch sucht.]], },
  },
  [9375] = {
    [[Der Weg zur Falkenwacht]],
    { [[<Auf dem geschundenen Gesicht der Blutelfe zeichnet sich bei Eurem Anblick Erleichterung ab.>]], [[Bitte, <class>, holt mich aus diesem Käfig heraus! Ich bin der Gruppe aus Thrallmar, trotz Martik Tor'seldoris Warnungen, vorausgereist.]], [[Schreckliche Kreaturen lauerten mir auf. Ich glaube, es waren diese Felshetzer, von denen er immer sprach. Zunächst gelang es mir ihnen zu entkommen, doch das war erst der Anfang meiner Schwierigkeiten.]], [[Ich verband gerade meine Wunden, als mich die Vogelmenschen fanden und zurück in ihre Höhle schleiften, wo sie mich in diesen Käfig sperrten! Ich muss unbedingt zur Falkenwacht!]], },
    { [[Beschützt die verwundete Blutelfenpilgerin auf Ihrem Weg zur Falkenwacht und sprecht dann mit Taleris Morgenblick.]], },
  },
  [9376] = {
    [[Die Pflicht eines Pilgers]],
    { [[Diese verletzte Pilgerin schreit irgendwas von einem Beutel.]], [[Habt Ihr einen Beutel in der Nähe gesehen, als Ihr sie gefunden habt? Hmm...]], [[Ihre Angreifer müssen sich damit aus dem Staub gemacht haben, als sie sie vertrieben hat. Die Stachelklauenfelshetzer aus der Schlucht im Süden sind bekannt dafür, dass sie einsame Reisende angreifen.]], [[Wenn sie sich nicht beruhigt, kann ich ihre Wunden nicht behandeln. Ihre Sturheit könnte sie das Leben kosten.]], [[Geht zur Staubstachelschlucht und haltet nach ihrem Lager Ausschau. Ich möchte wetten, dass sie ihn da verloren hat.]], },
    { [[Die verletzte Blutelfpilgerin wurde von giftigen Stachelklauenfelshetzern angegriffen, als sie die Staubstachelschlucht erkunden wollte. Findet ihr Lager und haltet nach dem vermissten Beutel Ausschau.]], },
  },
  [9378] = {
    [[Die Zitadelle des Schreckens - Naxxramas]],
    { [[Inmitten des Pestwalds liegt der Eingang zu der Zitadelle des Schreckens von Naxxramas. Bisher wurde jeglicher Versuch, dort hineinzugelangen, von den magischen Schutzzaubern, die über das Runenportal verhängt wurden, zunichtegemacht. Zumindest war es bis heute so.]], [[Wir haben einen Weg gefunden, wie wir uns mithilfe von einer Art dauerhaften arkanen Tarnung Zugang verschaffen können – einem von mir leicht abgewandelten alten Zauber der Kirin Tor. Diese Tarnung ist allerdings sehr kostspielig. Da Eure Hingabe für die Ziele der Argentumdämmerung jedoch unerschütterlich ist, werden wir Euch sämtliche Kosten erlassen.]], },
    { [[Erzmagierin Angela Dosantos bei der Kapelle des Hoffnungsvollen Lichts in den Östlichen Pestländern wird Euch die arkane Tarnung kostenfrei gewähren. Euer Ruf bei der Argentumdämmerung muss ehrfürchtig sein.]], },
  },
  [9380] = {
    [[Die Hatz nach mehr]],
    { [[Ich sage Euch, sollte ich noch einen Brief von Eisenschmiede bekommen...]], [[Habt Ihr jemals versucht, jemandem etwas zu erklären, der nicht zuhört, <class>? Ich habe den Jungs in Eisenschmiede das von ihnen angeforderte Sphärenhundexemplar geschickt. Jetzt waren sie so frech und haben mir zurückgeschrieben, dass sie ein ganzes "Rudel" dieser Viecher möchten! Ein Rudel!]], [[Ich habe ihnen also zu erklären versucht, dass Sphärenhunde Einzelgänger sind, aber es schert sie nicht. Ich bin es leid zu diskutieren. In der Gegend östlich der Festung findet Ihr sie. Beschafft sechs der besten und bringt sie hierher zurück?]], },
    { [[Beschafft 6 unversehrte Sphärenhundkadaver und bringt sie zu Ausgrabungsleiter Murantus in der Ehrenfeste.]], },
  },
  [9381] = {
    [[Treffsichere Pfeile]],
    { [[Mit dem Zustrom von Pilgern und dem Vordringen der Allianz, war die Falkenwacht in letzter Zeit ein sehr geschäftiger Ort. In meiner freien Zeit stelle ich normalerweise Pfeile für die Waldläufer her. Leider habe ich schon lange keine Zeit mehr dazu gehabt, die für die Pfeilherstellung notwendigen Federn zu beschaffen.]], [[Es wäre mir ein paar Münzen wert, wenn Ihr die Schwanzfedern von Knochenhäutern für mich einsammeln würdet. In der Vergangenheit habe ich diese Geier oft über den Knochen verendeter Kreaturen, nördlich der Falkenwacht, ausfindig gemacht.]], },
    { [[Sammelt 8 Schwanzfedern eines Knochenhäuters von den Knochenhäutergeiern und bringt sie Falknerin Drenna Flusswind in der Falkenwacht.]], },
  },
  [9382] = {
    [[Das Schicksal der Grollhufe]],
    { [[Grüße, <class>.]], [[Ich bin sicher, dass Ihr auf Euren Reisen etwas von der Verderbtheit dieser Welt mitbekommen habt. Sie ist so allgegenwärtig, dass sie sogar die einheimischen Tiere verändert hat. Natürlich nur die, die sie überlebt haben.]], [[Die Grollhufe im Süden und Westen waren einst friedliche Weidentiere, aber die Verderbtheit hat sie mit einer unstillbaren Wut erfüllt. Der einzige Ausweg aus dieser Qual ist der Tod, auch wenn wir nur ungern ihr Blut vergießen. Helft Ihr mir dabei, das Leiden der borstigen Grollhufe zu beenden?]], },
    { [[Tötet 15 borstige Grollhufe und kehrt dann zu Amaan dem Weisen im Tempel von Telhamat zurück.]], },
  },
  [9383] = {
    [[Ein gewagter Plan]],
    { [[Die Blutelfen verkörpern den völlig unverantwortlichen Umgang mit den Mächten der Magie, <class>. Sie kontrollieren die Magie nicht, nein, vielmehr sind sie ihre Sklaven und sie werden niemanden zwischen ihre Abhängigkeit und die magischen Energien kommen lassen, nach denen es ihnen dürstet. Die Blutelfen nutzen die Energien der Scherbenweltleerwandler, um ihre dämonische Magie zu wirken.]], [[Ich habe einen Kristall mit einem Zauber aufgeladen, der es uns ermöglichen wird, einen Leerwandler zu neutralisieren und das magische Band zwischen ihm und dieser Welt zu zerschneiden. Ich habe die Leerwandler hauptsächlich in den Sphärenfeldern und südöstlich der Höllenfeuerzitadelle gesehen.]], },
    { [[Wendet den geweihten Kristall auf einen verwundeten unkontrollierten Leerwandler an. Kehrt anschließend zu Elsaana beim Tempel von Telhamat zurück.]], },
  },
  [9385] = {
    [[Tobende Felshetzer]],
    { [[Also schickt uns die Ehrenfeste hierher, um mit den Arakkoa fertigzuwerden, und wir bauen unser Lager auf und denken uns hey, schön hier. Wir hatten ja keine Ahnung, dass wir so nahe bei den Stachelklauenfelshetzern sind.]], [[Sie sind erbarmungslose Raubtiere und haben jegliche Arbeit unsererseits zunichtegemacht. Wenn Ihr Euch darum kümmert, würde ich Euch bezahlen. Habt Ihr Lust?]], },
    { [[Tötet 10 Stachelklauenfelshetzer und kehrt anschließend zu Gremni Langbart auf der Höllenfeuerhalbinsel zurück.]], },
  },
  [9386] = {
    [[Licht im Dunkeln]],
    { [[Ihr seid ziemlich eifrig, oder? Solltet Ihr Euch noch einmal in die Tiefen wagen, so bin ich gerne bereit, Euch dafür zu belohnen.]], [[Sucht die Freudenfeuer in Stratholme, Scholomance, dem Düsterbruch und den Hallen der Schwarzfelsspitze auf. Ich erwarte Eure Rückkehr, <name>.]], },
    { [[Findet und berührt die Freudenfeuer innerhalb der Schwarzfelsspitze, dem Düsterbruch, Scholomance und Stratholme. Sprecht dann mit dem Flammenbewahrer.]], },
  },
  [9387] = {
    [[Die Quelle der Verderbtheit]],
    { [[Unverderbte Draenei, wie dieser unglückliche Geselle hier, sind begabte Helden des Lichts. Welche Verderbtheit hat wohl ihr großartiges Volk dazu gebracht, sich zu Zerschlagenen und Verirrten zurückzuentwickeln?]], [[Mir ist eine Theorie zu Ohren gekommen, die besagt, dass die Mutation von teuflischen Energien ausgelöst wurde. Ich würde diese Theorie gerne überprüfen.]], [[Nordwestlich von hier findet Ihr die Ruinen von Sha'naar. Dort befinden sich einige einfache Zerschlagene unter dem Befehl der Zuchtmeister der Illidari. Bringt mir die dämonischen Essenzen der Zuchtmeister, damit ich sie an diesem Draenei ausprobieren kann.]], },
    { [[Reist zu den Ruinen von Sha'naar auf der Höllenfeuerhalbinsel und besorgt 5 dämonische Essenzen von den Zuchtmeistern der Illidari. Kehrt zu Apotheker Azethen in der Falkenwacht zurück, wenn Ihr die Aufgabe erfüllt habt.]], },
  },
  [9388] = {
    [[Lodernde Flammen in Kalimdor]],
    { [[Heute lodern zahlreiche Flammen in der Welt, <name>. Die Feuer, die am schwierigsten zu unterhalten sind, leuchten grün; werden sie nicht ständig beaufsichtigt, brennen sie nicht lange. Versichert Euch für unseren Flammenbewahrer, dass die Smaragdfeuer in Kalimdor auch weiterhin lodern, eh?]], [[Ihr findet sie in der Nähe von Ratschet im Brachland, unweit der Meistergleve in Dunkelküste, im Wald des Eschentals bei der Brücke nahe des Außenpostens der Silberschwingen und entlang der Hauptstraße im Steinkrallengebirge, in der Nähe der Straße, die zur Scherwindklippe führt.]], },
    { [[Sucht die Freudenfeuer im Eschental, dem Brachland, der Dunkelküste und dem Steinkrallengebirge auf. Sprecht anschließend mit dem Flammenbewahrer.]], },
  },
  [9389] = {
    [[Lodernde Flammen in den Östlichen Königreichen]],
    { [[Die Feuer des Sonnenwendfests lassen das Land traditionsgemäß im Licht unzähliger Flammen erstrahlen. Manche behaupten, dass die grünen Flammen die besten seien, da sie so schwierig zu unterhalten sind und eine solch ungewöhnliche Farbgebung haben.]], [[Versichert Euch für unseren Flammenbewahrer, dass die Feuer in den Östlichen Königreichen auch weiterhin lodern. Ihr findet sie in der Nähe von Dun Modr im Sumpfland, unweit von Lohenscheit im Silberwald, bei den Klippen zum Leuchtturm in Westfall und nahe dem zerstörten Turm im Vorgebirge des Hügellands.]], },
    { [[Sucht die Freudenfeuer im Vorgebirge des Hügellands, im Silberwald, in Westfall und dem Sumpfland auf. Sprecht anschließend mit dem Flammenbewahrer.]], },
  },
  [9390] = {
    [[Die Suche nach Sedai]],
    { [[Hört mein Leid, <class>. Mein Bruder Sedai ist schon vor längerer Zeit losgezogen, um den Orcs beim Posten der Mag'har eine Botschaft zu übermitteln. Als ein unverbesserlicher Idealist war er der Meinung, dass der Zeitpunkt nun gekommen sei, unsere Differenzen beizulegen und einen Friedensvertrag mit ihnen auszuhandeln.]], [[Er wird jetzt schon seit Tagen vermisst und ich befürchte das Schlimmste.]], [[Würdet Ihr für mich nach ihm Ausschau halten, damit ich endlich wieder Ruhe finde? Sedai müsste sich irgendwo in der östlichen Umgebung aufhalten.]], },
    { [[Anachoret Obadei möchte, dass Ihr östlich von hier auf der Höllenfeuerhalbinsel nach seinem Bruder Sedai Ausschau haltet.]], },
  },
  [9391] = {
    [[Den Weg kennzeichnen]],
    { [[Nachdem die Felsklauenpopulation ausgedünnt ist, müssen wir die sichere Passage nur noch für unsere Pilger kenntlich machen. Nehmt diese Fackel und begebt Euch erneut in die Große Kluft. Sucht dort die drei Leuchtsignale, die den Verlauf des Pfads im Osten kennzeichnen.]], [[Entzündet die drei Signale und kehrt anschließend zu mir zurück. Doch seid gewarnt, das Entzünden eines Leuchtsignals zieht unweigerlich die Felsklauen aus der Umgebung an.]], },
    { [[Waldläuferhauptmann Venn'ren an der Falkenwacht möchte, dass Ihr auf der Höllenfeuerhalbinsel die Große Kluft aufsucht und dort das südliche Leuchtsignal, westliche Leuchtsignal und das zentrale Leuchtsignal entzündet. Kehrt anschließend mit der brennenden Fackel zu ihm zurück.]], },
  },
  [9392] = {
    [[Ausbildung zum Schurken]],
    { [[Während Ihr in Eurer Klasse weiter aufsteigt, werdet Ihr sicher die Ausbildung, die Euch der Lehrer Eurer Klasse anbietet, nutzen wollen. Der Lehrer für junge Schurken hier auf der Insel der Sonnenwanderer ist Pfadpirscher Kariel. Sprecht mit Kariel, um herauszufinden, welche Ausbildung er Euch anbieten kann. Die Ausbildung hat ihren Preis, bringt also ein paar Münzen mit Euch.]], [[Kariel - wie alle anderen Lehrer auf der Insel der Sonnenwanderer auch - ist innerhalb des Sonnenturms, auf der unteren Ebene, anzutreffen.]], },
    { [[Sprecht mit Pfadpirscher Kariel - dem Schurkenlehrer - innerhalb des Sonnenturms auf der Insel der Sonnenwanderer.]], },
  },
  [9393] = {
    [[Ausbildung zum Jäger]],
    { [[Während Ihr in Eurer Klasse weiter aufsteigt, werdet Ihr sicher die Ausbildung, die Euch der Lehrer Eurer Klasse anbietet, nutzen wollen. Die Lehrerin für junge Jäger hier auf der Insel der Sonnenwanderer ist Waldläuferin Sallina. Sprecht mit Sallina, um herauszufinden, welche Ausbildung sie Euch anbieten kann. Die Ausbildung hat ihren Preis, bringt also ein paar Münzen mit Euch.]], [[Sallina - wie alle anderen Lehrer auf der Insel der Sonnenwanderer auch - ist innerhalb des Sonnenturms, auf der unteren Ebene, anzutreffen.]], },
    { [[Sprecht mit Waldläuferin Sallina - der Jägerlehrerin - innerhalb des Sonnenturms auf der Insel der Sonnenwanderer.]], },
  },
  [9394] = {
    [[Wo ist Wyllithen?]],
    { [[Der Nachtschimmergrundbewahrer ist verschwunden! Jemand muss vergessen haben, ihm zu sagen, dass er den Ort verlassen sollte, als der Magister und alle anderen es taten. Ich weiß, dass er sich noch irgendwo hier auf dem Grund aufhält, aber ich getraue mich nicht, selbst nach ihm zu sehen, da sich hier so viele magische Kreaturen herumtreiben.]], [[Könnt Ihr ihn für mich finden, <class>? Bitte seht nach, ob er in Ordnung ist.]], },
    { [[Findet Grundbewahrer Wyllithen beim Nachtschimmergrund im Immersangwald.]], },
  },
  [9395] = {
    [[Saltherils Hafen]],
    { [[Ich schwöre, dass ich jemandem einen Feuerball verpasse, wenn Lord Saltheril mir auch nur noch eine Lieferanfrage für seine endlosen Feste schickt. Sehe ich verdammt nochmal wie ein Festtagsplaner aus?! Unter uns gesagt, dieser Verrückte und seine Bande von Speichelleckern scheinen bis heute zu verdrängen, dass wir unter ständiger Belagerung stehen!]], [[Einige von uns sind doch tatsächlich mit, äh, ich weiß nicht... der verdammten Verteidigung unseres Königreichs, Quel'Thalas, beschäftigt?! Würdet Ihr mir einen Gefallen tun, <class>? Seid doch so gut und reist für mich nach Saltherils Hafen und seht zu, dass er endlich Ruhe gibt! Es ist gleich die Straße runter, Richtung Westen.]], },
    { [[Sprecht mit Lord Saltheril in Saltherils Hafen im Immersangwald.]], },
  },
  [9396] = {
    [[Die Magie der Arakkoa]],
    { [[Die Scherbenwelt birgt viele Geheimnisse, <name>. Es ist nur noch eine Frage der Zeit, bis wir sie alle enträtselt haben.]], [[Das Volk der halb Vogel, halb menschlichen Arakkoa verfügt über uraltes arkanes Wissen, welches sich als sehr nützlich für unsere Erforschung dieser Welt erweisen würde. Reist nach Süden zum Haal'eshbau. Nehmt alle magischen Rollen an Euch, die Ihr bei den Haal'eshi finden könnt und bringt sie mir.]], },
    { [[Bringt 4 Rollen der Haal'eshi zu Arkanist Calesthris Morgenstern bei der Falkenwacht auf der Höllenfeuerhalbinsel.]], },
  },
  [9397] = {
    [[Kommt ein Vogel geflogen]],
    { [[Die Arakkoa der Haal'eshi haben eine wunderschöne Vogelart, den Kaliri, domestiziert.]], [[Mir ist es gelungen, ein junges Kalirimännchen einzufangen, und sie scheinen zähmbar zu sein. Die weiblichen Exemplare sind deutlich seltener, aber wenn es mir gelänge, eines davon zu ergattern, sollte ich in der Lage sein, sie selbst zu züchten. Es muss jedoch ein junges Weibchen sein, fast noch ein Küken. Ein alter Wildvogel würde die Gefangenschaft nicht lange überleben.]], [[Nehmt diesen Käfig und reist nach Süden zum Haal'eshbau. Sucht nach Eiern, die kurz vor dem Schlüpfen stehen. Wenn Ihr mir ein junges Weibchen zurückbringt, werde ich Euch fürstlich belohnen.]], },
    { [[Reist zum Haal'eshbau auf der Höllenfeuerhalbinsel und durchsucht die Kalirinester nach einem jungen Kaliriweibchen. Benutzt den leeren Vogelkäfig, um es zu fangen, und kehrt anschließend zu Falknerin Drenna Flusswind in der Falkenwacht zurück.]], },
  },
  [9398] = {
    [[Tödliche Raubtiere]],
    { [[Die Zerschlagenen in der Scherbenwelt haben unendlich viel Grausamkeit ertragen müssen. Kein Wunder, dass sie sofort nach der Öffnung des Dunklen Portals scharenweise in der Hoffnung, diesem Land entfliehen zu können, dorthin gepilgert sind.]], [[Zum Pech für unsere verwirrten Brüder und Schwestern sind sie für die Felsklauen, die in der Großen Kluft leben, eine leichte Beute. Geht dort hin und vernichtet einige der Felsklauen, damit unseresgleichen eine Chance hat.]], },
    { [[Späher Vanura im Tempel von Telhamat auf der Höllenfeuerhalbinsel möchte, dass Ihr 8 Steinsichelwelpen und 4 Steinsichelalpha tötet.]], },
  },
  [9399] = {
    [[Die Grausamkeit der Zuchtmeister]],
    { [[Sicherlich glaubt auch Ihr, dass die meisten Zerschlagenen bösartig sind, nicht wahr? In Wahrheit mangelt es vielen nur an geistiger Führung.]], [[Ich war ebenfalls verloren, bevor ich die Omenai getroffen habe.]], [[Unser Verstand litt sehr unter der dämonischen Magie der Orcs. Man kann uns leicht in die Irre führen und ausnutzen.]], [[Dämonen, die mit Illidan verbündet sind, überfallen täglich die Höllenfeuerhalbinsel und verschleppen unseresgleichen als Sklaven. Doch mit den Omenai kam die Hoffnung. Reist zu den Ruinen von Sha'naar, südwestlich von hier, und tötet die Zuchtmeister der Illidari, damit mein Volk endlich wieder frei ist!]], },
    { [[Reist zu den Ruinen von Sha'naar und erschlagt 4 Zuchtmeister der Illidari. Kehrt anschließend zu Ikan zum Tempel von Telhamat zurück.]], },
  },
  [9400] = {
    [[Der Assassine]],
    { [[<name>, wir haben erst kürzlich einen Plan zur Ermordung unseres Gesandten in der Falkenwacht aufgedeckt. Spione der Höllenfeuerzitadelle haben von unseren Plänen, eine Expedition zur Wacht auszusenden, erfahren und einen Hinterhalt vorbereitet.]], [[Unsere Späher berichteten mir, dass ein Assassine namens Krun Rückenbrecher auf der Straße nördlich der Zitadelle in Stellung gegangen ist. Begebt Euch dorthin und tötet diesen Assassinen. Macht den Höllenorcs klar, dass man mit uns keine Spielchen treibt.]], },
    { [[Nazgrel möchte, dass Ihr zu der Straße nördlich der Höllenfeuerzitadelle reist und nach einem Assassinen namens Krun Rückenbrecher Ausschau haltet.]], },
  },
  [9401] = {
    [[Eine seltsame Waffe]],
    { [[Ihr zieht die Steinaxt aus dem toten Körper, um sie eingehender zu untersuchen. Irgendetwas an der Verarbeitung der Axt ist ungewöhnlich.]], [[Sie scheint orcischen Ursprungs zu sein und dennoch besteht sie aus einem Material, das Ihr noch nie zuvor gesehen habt. Nazgrel wäre bestimmt daran interessiert, die Axt weiter zu untersuchen.]], },
    { [[Bringt die schwere Steinaxt zu Nazgrel in Thrallmar auf der Höllenfeuerhalbinsel.]], },
  },
  [9402] = {
    [[Fangt!]],
    { [[So... Ihr seid <name>, oder? Fühlt Euch nicht geschmeichelt, ich kenne Euren Namen, einer meiner Schüler ist bei Euch herumscharwenzelt und plappert ununterbrochen.]], [[Hmm... wenn Ihr <ihn/sie> beeindrucken möchtet, warum helft Ihr mir dann nicht aus? Auf dem Grund des Stillwispertümpels hinter mir liegt eine Azurphiole. Taucht in die Tiefe, schnappt sie Euch und bringt sie zu mir.]], },
    { [[Taucht auf den Grund des Stillwispertümpels und holt die Azurphiole für Instrukteur Antheol.]], },
  },
  [9403] = {
    [[Das reinste Wasser]],
    { [[Jetzt, da Ihr eine kleine Lektion gelernt habt, bitte ich Euch etwas Handfesteres an. Es betrifft Eure Fähigkeiten der Selbstverteidigung. Ihr solltet natürlich nur im äußersten Notfall von Nahkampftechniken Gebrauch machen, aber deshalb sollte man trotzdem nicht unvorbereitet sein.]], [[Ich kann Euch bei der Herstellung eines Stabs helfen, der einem Magier Eures Ranges gerecht wird. Nehmt die Phiole, die Ihr für mich besorgt habt, und füllt sie bei den Elrendarfällen auf. Folgt einfach dem Fluss südöstlich von hier.]], },
    { [[Instrukteur Antheol möchte, dass Ihr die Azurphiole bei den Elrendarfällen auffüllt.]], },
  },
  [9404] = {
    [[Noch lebendig]],
    { [[Ihr habt vielleicht die wandelnden Bäume nahe der Elrendarfälle bemerkt. Als Behüter der Natur sind sie mit urzeitlicher Magie erfüllt, die wir mithilfe des reinem Wassers, das Ihr mir gebracht habt, umformen und für unsere Zwecke verwenden können.]], [[Unglücklicherweise sind die Waldbehüter knorrige und verdrehte Kreaturen, so dass Ihr womöglich erst einige töten müsst, bevor Ihr einen brauchbaren Zweig findet.]], },
    { [[Tötet die Immersangwaldbehüter bis Ihr einen lebendigen Zweig für Instrukteur Antheol findet.]], },
  },
  [9405] = {
    [[Das Anliegen des Kriegshäuptlings]],
    { [[Wie Ihr wisst, steht der Kriegshäuptling aus ganzer Kraft hinter dieser Expedition und das aus gutem Grund. Dies ist unsere Heimatwelt. Hier liegen unsere Wurzeln.]], [[Diese Reise hat für Orcs und Blutelfen gänzlich unterschiedliche Bedeutung. Für sie ist es die Suche nach dem gelobten Land. Für uns ist es die Rückkehr in unsere lange verlorene Heimat.]], [[Wer auch immer diese Axt gefertigt hat, ist mit den alten Traditionen vertraut. Wir müssen Kontakt zu ihnen aufnehmen. Diese Angelegenheit ist zu wichtig, als dass wir reguläre Methoden des Aufspürens verwenden. Zeigt Scharfseherin Regulkut die Waffe; sie wird wissen, was zu tun ist.]], },
    { [[Bringt die schwere Steinaxt zu Scharfseherin Regulkut in Thrallmar auf der Höllenfeuerhalbinsel.]], },
  },
  [9406] = {
    [[Die Mag'har]],
    { [[Diese Neuigkeiten, die Ihr von jenseits des Dunklen Portals bringt... Ihr wollt mir ernsthaft Glauben machen, dass der Sohn Durotans über die jenseitigen Länder geherrscht hat? Frei von dämonischem Einfluss? Und nun sind seine Leute in diese Welt zurückgekehrt?]], [[Um Euretwillen hoffe ich, dass das nicht irgendein perfider Witz sein soll, <class>. Es ist schwer zu beschreiben, was dies für mein Volk bedeutet. Ich werde umgehend eine Botschaft nach Nagrand schicken! Höllschrei muss davon erfahren.]], [[Bringt diesen Brief zu Eurem Anführer in Thrallmar. Sagt ihm, die Mag'har heißen Euch willkommen!]], },
    { [[Bringt den Brief von den Mag'har zu Nazgrel in Thrallmar auf der Höllenfeuerhalbinsel.]], },
  },
  [9407] = {
    [[Durch das Dunkle Portal]],
    { [[Viele Jahre stand das Dunkle Portal still... vergessen. Aber nun ist es nicht mehr still.]], [[Nachdem der Angriff der Dämonen aufgehalten wurde, haben unsere Truppen das Portal durchschritten und sich den Dämonen auf ihrem eigenen Gebiet gestellt!]], [[Nun sind wir in einem wilden Krieg gefangen, und einige sind der Meinung, dass wir ihn nicht gewinnen können. Es ist also unsere Pflicht und unser grausames Vergnügen, unsere Feinde in der Scherbenwelt zu bekämpfen und unseren Sieg zu sichern, <name>.]], [[Wenn Ihr uns helfen wollt, meldet Euch unverzüglich bei Generalleutnant Orion. Er erwartet Euch in der Scherbenwelt...]], },
    { [[Reist durch das Dunkle Portal und meldet Euch bei Generalleutnant Orion. Er erwartet Euch auf der anderen Seite - in der Scherbenwelt.]], },
  },
  [9408] = {
    [[Vergessene Helden]],
    { [[Auch wenn es mich traurig macht, dass wir so viele Soldaten bei dem Versuch, das Portal zu durchbrechen, verloren haben, erfüllt es mich doch mit Stolz, dass das ursprüngliche Expeditionskorps noch am Leben ist.]], [[Ich kann es kaum glauben, aber dort stehen sie - ein Zeichen für die Entschlossenheit der Allianz.]], [[In dieser Festung steht einer unserer großartigsten Helden: Danath Trollbann! Geht zu ihm, <class>. Er hat schon all unsere Soldaten getroffen und mit ihnen gesprochen. Er möchte Euch sicher kennenlernen.]], },
    { [[Sprecht mit Truppenkommandant Danath Trollbann in der Ehrenfeste.]], },
  },
  [9409] = {
    [[Eilige Lieferung!]],
    { [[Da Ihr jetzt alles besorgt habt, was zum Wiederaufladen der Heilkristalle benötigt wird, müsst Ihr die Phiolen zu unserem Priester Zalduun bringen. Er kümmert sich um die Verwundeten innerhalb der zentralen Kammer des Schiffes.]], },
    { [[Bringt die Phiolensammlung zu Zalduun bei der Absturzstelle im Am'mental.]], },
  },
  [9410] = {
    [[Spirituelle Führung]],
    { [[Selbst die Sinne unserer besten Spürwölfe können sich irren, <name>. Die Geister jedoch, haben eine andere Sicht auf diese Welt. Das Wesen, welches Ihr beschwören werdet, wird die von Euch gesuchte Person ausfindig machen, denn es spürt nicht dem Geruch des Gesuchten hinterher sondern seiner Essenz.]], [[Geht jetzt, <class>, nehmt dieses Totem mit Euch und benutzt es an dem Ort, wo Ihr die Steinaxt gefunden habt.]], },
    { [[Verwendet das Wolfstotem an dem Ort, wo Ihr Krun Rückenbrechers Leichnam gefunden habt und folgt dem Ahnengeistwolf.]], },
  },
  [9415] = {
    [[Meldung bei Marschall Himmelswand]],
    { [[Gehört Ihr zu Marschall Himmelswands neuen Rekruten? Natürlich.]], [[Auf was wartet Ihr noch? Gute Soldaten verlieren ihr Leben in der Wüste. Freut es Euch nicht, bald dazuzugehören?]], [[Los jetzt, macht Euch auf den Weg... und trocknet dabei nicht aus.]], },
    { [[Sprecht mit Marschall Himmelswand beim Lager der Allianz in der Nähe des Zoraschwarmbaus.]], },
  },
  [9416] = {
    [[Meldung bei General Kirika]],
    { [[Ihr müsst zu General Kirikas neuen Rekruten gehören. Es wurde Zeit, dass Ihr kommt. Der Truppenverschleiß in der Wüste ist hoch.]], [[Die Zwerge haben tagtäglich Verstärkung bekommen, wir können es uns nicht leisten in unserem Kampf um Ressourcen in Silithus an Boden zu verlieren.]], [[Lasst Euch vom General weitere Anweisungen geben. Ich muss Euch Rekruten lediglich finden, nicht ausbilden.]], },
    { [[Sprecht mit General Kirika beim Lager der Horde in der Nähe des Regalschwarmbaus.]], },
  },
  [9417] = {
    [[Die Arakkoabedrohung]],
    { [[Die Arakkoa sind hochintelligente Wesen... genauso gerissen wie Gnome. Nur, dass sie über ein aggressives Gemüt und ein Händchen für Magie verfügen.]], [[In letzter Zeit haben sie häufiger Patrouillen der Ehrenfeste überfallen, was auch der Grund für unsere Anwesenheit hier ist. Wisst Ihr, Mirren und ich sind weltbekannte Scharfschützen, ja, das sind wir! Allerdings sind wir eher auf Federvieh und Wildtiere spezialisiert... nicht auf diese Blitze schleudernden Vogelmenschen.]], [[Was sagt Ihr, greift Ihr uns in dieser Sache etwas unter die Arme? Ihr findet den Haal'eshbau im Südosten.]], },
    { [[Gremni Langbart auf der Höllenfeuerinsel möchte, dass Ihr 4 Windspäher der Haal'eshi und 6 Krallenwachen der Haal'eshi tötet.]], },
  },
  [9418] = {
    [[Avruus Kugel]],
    { [[<Als Ihr die Kugel näher an Euer Ohr haltet, hört Ihr, wie Euch eine Stimme anspricht.>]], [[Ihr seid kein Arakkoa, wie ich spüre. Deswegen werde ich versuchen, Euch einen Handel für meine Freiheit vorzuschlagen. Mein Name ist Aeranas. Ich regierte einst über die östlichen Winde. Der Magier der Haal'eshi, Avruu, hat mich in dieser Kugel eingefangen.]], [[Sucht den Altar der Arakkoa, mit dessen Hilfe sie mich versklavt haben. Er ist südöstlich vom Bau der Arakkoa versteckt. Nähert Euch ihm mit der Kugel und gebt sie mir, sobald ich frei bin. Als Ausgleich für meine Freiheit werde ich Euch einen mächtigen Schatz geben!]], },
    { [[Bringt Avruus Kugel zum Altar der Haal'eshi südöstlich des Haal'eshbaus auf der Höllenfeuerhalbinsel und gebt sie Aeranas, sobald er frei ist.]], },
  },
  [9419] = {
    [[Durchkämmen der Wüste]],
    { [[Wir hatten vor Kurzem herausgefunden, dass die Horde die Wüste von Silithus nach Ressourcen abgesucht hat, schenkten dem Ganzen aber keine besondere Beachtung. Wir dachten, es wäre die Sache nicht wert, eine größere Anzahl von Soldaten in dieser Bruthitze zu stationieren.]], [[Wie dem auch sei, wir haben die Wichtigkeit dieser Ressourcen erkannt und versuchen nun, um jeden Preis aufzuholen.]], [[Wenn Ihr uns unterstützen wollt, müsst Ihr in die Wüste gehen, dort nach Silithyst suchen und diesen anschließend hierher bringen.]], },
    { [[Findet Silithyst, bringt es zum Silithystextraktor und sprecht anschließend mit Marschall Himmelswand beim Lager der Allianz in der Nähe des Zoraschwarmbaus.]], },
  },
  [9420] = {
    [[Feinste Daunen]],
    { [[Die Arakkoa das Haaleshbaus südlich von hier züchten eine besonders prächtige Art von Vögeln, namens Kaliri. Ihre Federn würden mir in den Handelsposten Azeroths einen ganz schönen Batzen Gold einbringen. Denkt nur an all das viele Bier, das ich mir leisten könnte!]], [[Ich sag Euch was, bringt mir eine Ladung Kalirifedern und ich gebe Euch meinen Hut. Was sagt Ihr dazu? So einen Hut wie diesen hier, werdet Ihr nirgendwo sonst finden! Sind wir im Geschäft?]], },
    { [[Mirren Langbart auf der Höllenfeuerhalbinsel möchte, dass Ihr ihm 8 Kalirifedern von den Kaliris im Haal'eshbau bringt.]], },
  },
  [9421] = {
    [[Ausbildung zum Schamanen]],
  },
  [9422] = {
    [[Durchkämmen der Wüste]],
    { [[Wir haben die Wüste von Silithus bereits seit einiger Zeit nach Kristallressourcen durchkämmt. Die einzigen Gefahren, die sich uns dabei in den Weg gestellt haben, waren die Kreaturen, die dort heimisch sind, sowie Sand, Staub und Hitze.]], [[Die Allianz scheint nun aber Wind von unserem Unterfangen bekommen zu haben. Ein Rennen hat begonnen. Ein Rennen um die natürlichen Ressourcen und um deren Verteidigung.]], [[Wir brauchen jede helfende Hand, die wir bekommen können. Ich hoffe, Ihr werdet uns unterstützen.]], },
    { [[Findet Silithyst, bringt es zum Silithystextraktor und sprecht anschließend mit General Kirika beim Lager der Horde in der Nähe des Regalschwarmbaus.]], },
  },
  [9423] = {
    [[Rückkehr zu Obadei]],
    { [[Jetzt, da sich Sedais Tod bestätigt hat, liegt es an Euch, Obadei die traurige Nachricht zu überbringen.]], },
    { [[Kehrt zu Anachoret Obadei beim Tempel von Telhamat auf der Höllenfeuerhalbinsel zurück und berichtet ihm von Sedais Tod.]], },
  },
  [9424] = {
    [[Makurus Rache]],
    { [[Sedai war für Makuru wie ein Bruder. Ich schulde ihm mein Leben und noch weit mehr! Sie sagen, Makuru sei verrückt, doch wenn die Omenai nach seinem Tod nichts tun wollen, dann sind sie die wahren Verrückten!]], [[Makuru kann in dieser Welt nicht viel sein Eigen nennen, aber ich werde Euch mit Freuden meinen einzigen Besitz überlassen, wenn Ihr Sedai rächt.]], [[Ich habe die braunen Orcgrunzer schon mal gesehen, oben in den Bergen, im Nordosten. Sie hassen uns! Sie haben unser Mitleid nicht verdient! Geht und rächt Sedais Tod. Bringt mir ihre Ahnenperlen als Beweis!]], },
    { [[Makuru im Tempel von Telhamat auf der Höllenfeuerhalbinsel möchte, dass Ihr ihm 10 Ahnenperlen der Mag'har bringt.]], },
  },
  [9425] = {
    [[Bericht an Tarrens Mühle]],
    { [[Ah, gut Euch hier zu sehen, <class>! Ich habe eine Aufgabe, die wie für Euch gemacht ist.]], [[Als neue Mitglieder der Horde müssen wir dafür sorgen, dass wir sie bei ihren Unternehmungen unterstützen und bei ihren Problemen helfen. Dann werden sie uns auch bereitwilliger bei unseren helfen.]], [[Reist zum Dorf der Verlassenen, Tarrens Mühle, im Vorgebirge des Hügellands im Südwesten. Meldet Euch bei Beraterin Morgenabend, wenn Ihr da seid.]], },
    { [[Reist nach Tarrens Mühle im Vorgebirge des Hügellands und sprecht mit Beraterin Morgenabend.]], },
  },
  [9426] = {
    [[Die Teiche von Aggonar]],
    { [[Seit den Tagen, in denen wir dieses Land Draenor nannten, hat sich viel geändert. Teile des Landes wurden durch die Verderbtheit der Dämonen für immer verwandelt.]], [[Dazu gehören die Teiche von Aggonar. Einst befanden sich an diesem Ort die heiligen Quellen unseres Volkes. Das Blut des Dämonen Aggonar hat die Essenz des geheiligten Wassers verschmutzt. Dieses teuflische Blut hat verderbte Kreaturen vorgebracht, die ausgelöscht werden müssen, wenn wir die Teiche jemals zurückfordern möchten. Reist von hier aus nach Osten und tötet die Teufelsblutelementare und den Schleim, der sich in den Teichen herumtreibt.]], },
    { [[Amaan der Weise möchte, dass Ihr zu den Teichen von Aggonar geht und 6 Teufelsblutschrecken und 6 mörderische Faulschleime tötet.]], },
  },
  [9427] = {
    [[Reinigung des Wassers]],
    { [[Ich muss euch bitten, das, was wir angefangen haben, zu Ende zu bringen. Jetzt, da der teuflische Einfluss nachgelassen hat, müsst Ihr diese Reinigungsphiole nehmen und sie auf den Ursprung der Verderbnis schütten, Aggonars Leiche.]], [[Ihr könnt die Knochen des Dämonen am nördlichen Ende der Teiche finden. Aber seid vorsichtig, seine Essenz ist immer noch nicht ganz aus dieser Welt verschwunden.]], },
    { [[Amaan der Weise möchte, dass Ihr zu den Teichen von Aggonar reist und die Reinigungsphiole bei Aggonars Leiche benutzt. Kehrt zu ihm zurück, wenn Ihr das Wasser von Aggonars Essenz befreit habt.]], },
  },
  [9428] = {
    [[Bericht an den Splitterholzposten]],
    { [[<Die Botschafterin senkt die Stimme und beugt sich zu Euch.>]], [[Euer Ruf eilt Euch voraus, <name>. Ich möchte, dass Ihr zum Splitterholzposten im Eschental reist. Zu Fuß gelangt man am einfachsten durch Durotar, das Brachland und dann an der Weggabelung nach Norden dorthin.]], [[Wenn Ihr dort angekommen seid, sprecht mit Berater Sonnenschwur. Reist vorsichtig, das Eschental kann sehr gefährlich sein!]], },
    { [[Reist zum Splitterholzposten im Eschental und sprecht mit Berater Sonnenschwur.]], },
  },
  [9429] = {
    [[Reist nach Dunkelhain]],
    { [[Mir wurde gesagt, dass es einen Dorn in der Seite des Königreichs von Sturmwind gibt, Dämmerwald genannt. Dieser Ort ist erfüllt vom bösen und niederträchtigen Gestank des Untodes.]], [[Da wir versuchen, so gut wie möglich zu helfen, möchte ich, dass Ihr nach Südosten zu der größeren Menschensiedlung Dunkelhain reist. Anachoret Delan wird dort auf Eure Ankunft warten.]], },
    { [[Reist nach Dunkelhain im Dämmerwald und sprecht mit Anachoret Delan.]], },
  },
  [9430] = {
    [[Relikte von Sha'naar]],
    { [[Wie ich es Euch bereits sagte, ich brauche mein Augenlicht nicht, um zu sehen, was andere nicht zu erkennen vermögen. Ich werde nicht verurteilen, was Ihr in Makurus Namen getan habt, dennoch denke ich, Ihr solltet über das, was Euch Eure Augen zeigen, hinausblicken.]], [[Reist zu den Ruinen von Sha'naar im Südwesten und sammelt so viele Relikte ein, wie Ihr finden könnt. Wenn es das Schicksal so will, werdet Ihr finden, was wir brauchen.]], [[Die Bedeutung meiner Bitte mag Euch im Moment unklar sein, doch habt vertrauen, <name>. Der Geduldige wird erleuchtet werden.]], },
    { [[Amaan der Weise beim Tempel von Telhamat auf der Höllenfeuerhalbinsel möchte, dass Ihr zu den Ruinen von Sha'naar reist und 10 Relikte von Sha'naar einsammelt.]], },
  },
  [9431] = {
    [[Eine andere Lösung]],
    { [[Was hat es zu heißen, wenn ein Sin'dorei auf dem Gipfel einer vom Wind gepeitschten Plattform steht anstatt in den Hallen von Silbermond?]], [[Ganz einfach. Meine Brüder wollen mich nicht mehr haben. Jede Abschrift meiner Abhandlung über die natürliche Heilung unserer Abhängigkeit wurde verbrannt.]], [[Ich kann meine Forschungen hier sicher fortsetzen, aber ich habe keine Reagenzien. Wenn Ihr bereit wärt, etwas reinigende Erde von den donnernden Felskloppern am westlichen Ende der Nadeln zu besorgen und zu meinem Verbündeten beim Weißgipfelposten zu bringen, wäre ich Euch sehr dankbar.]], },
    { [[Sammelt 2 Handvoll reinigende Erde von den donnernden Felskloppern und bringt sie zu Wizlo Lagerputzer beim Weißgipfelposten.]], },
  },
  [9432] = {
    [[Reist nach Astranaar]],
    { [[Ich bin froh, dass Ihr helfen wollt. Es gibt in der Tat etwas, was Ihr tun könnt.]], [[Als Teil unseres Bündnisses mit der Allianz möchte ich, dass Ihr zur Elfenstadt Astranaar reist. Sie befindet sich im Eschental, einem großen Wald südlich von Dunkelküste auf dem Kontinent Kalimdor.]], [[Bitte sprecht mit Verteidigerin Palanaar, wenn Ihr angekommen seid, und fragt nach, ob es etwas gibt, bei dem Ihr den Bewohnern behilflich sein könnt.]], },
    { [[Reist nach Astranaar im Eschental und sprecht mit Verteidigerin Palanaar.]], },
  },
  [9433] = {
    [[Ein Bad im Mondbrunnen]],
    { [[Schaut mich nicht so an! Ich habe nicht behauptet, dass ich Euch mit einer Kelle und einer Flasche zum Mondbrunnen von Thalanaar schicke!]], [[Ich habe ein wenig an einem Produkt, das ich für einen Kunden transportiert habe, äh... herumexperimentiert. Ehrlich gesagt habe ich bereits eine tragbare Steuerungskonsole für den Robotron in der Nähe von Thalanaar aufgestellt. Ihr müsst diese Steuerungseinheit mitnehmen und in der Nähe meiner versteckten Konsole benutzen.]], [[Wenn mein Kunde je erfährt, dass ich den Robotron auch nur aus der Verpackung genommen habe, hängt er mich an den Ohren auf!]], },
    { [[Benutzt die Bedienungseinheit für Robotron in der Nähe der versteckten Steuerungskonsole in einer kleinen Gruppe von Büschen am Rand der Nadel, die sich am dichtesten an Thalanaar befindet, und übernehmt die Steuerung des Robotron 3000. Steuert den Roboter zum westlichen Ende von Tausend Nadeln zum Allianzaußenposten von Thalanaar. Benutzt den Roboter, um eine Probe Mondbrunnenwasser zu besorgen und bringt sie zu Wizlo Lagerputzer beim Weißgipfelposten.]], },
  },
  [9434] = {
    [[Das Tonikum testen]],
    { [[<Wizlo nimmt dem Roboter die Wasserprobe ab und legt sie zusammen mit der Felskloppererde und verschiedenen anderen Dingen in eine Kiste.>]], [[So, das ist alles auf Magistrix Elosais Liste. Sagt ihr, dass es mir eine Ehre war, mit ihr zusammen zu arbeiten, und dass ich ihr viel Glück bei ihren Forschungen wünsche.]], },
    { [[Bringt die Kiste mit Reagenzien zu Magistrix Elosai im Freiwindposten.]], },
  },
  [9435] = {
    [[Vermisste Kristalle]],
    { [[Wenn man etwas mit Sicherheit über das Hügelland sagen kann, dann, dass es auserkoren wurde, um unter dem Schatten des Krieges zu leben. Sogar etwas so Harmloses wie eine Wagenladung Steine kann als Ziel dienen.]], [[Normalerweise ist der Verlust von Baumaterial kein allzu großes Problem, aber bei der letzten Lieferung aus dem Azurschacht war eine Kiste mit seltenen Kristallen dabei, die ich sehr gerne untersucht hätte. Plünderer der Verlassenen haben den Wagen abgefangen und ihn nach Nordosten zu einem Turm gebracht, den sie wiederaufbauen wollen. Wir müssen diese Kristalle zurückholen, <class>!]], },
    { [[Besorgt die Lieferung seltener Kristalle von den Verlassenen und bringt sie zu Huraan in Süderstade zurück.]], },
  },
  [9436] = {
    [[Erkenntnisse über die Blutskalpe]],
    { [[Das trifft sich gut, <class>. Auf meinen Reisen habe ich die Kampftechniken der verschiedenen Trollstämme untersucht und eine Entwicklung erkannt. Die Blutskalpe in den Ruinen von Tkashi und Bal'lal im Norden benutzen die gleichen Taktiken wie die Amani in den nördlichen Wäldern.]], [[Ihre Magie scheint sich in eine interessante Richtung entwickelt zu haben, und sie haben sie benutzt, um der mächtigste Stamm in der Gegend zu werden. Sie könnte der Kern einer Waffe gegen die Amani sein, aber ich muss noch mehr darüber herausfinden.]], },
    { [[Nemeth Falkenauge im Basislager von Grom'gol möchte, dass Ihr ihm ein Totem der Blutskalpe von einem Schamanen der Blutskalpe bringt.]], },
  },
  [9437] = {
    [[Der Untergang der Morgenbö]],
    { [[Die Oger hier sind für nichts außer dreckige Witze, bitteres Bier und Wettkämpfe im Köpfe-aneinander-Schlagen zu gebrauchen. Als mein Schiff, die Morgenbö, untergegangen ist, haben sich diese Hunde von Theramore mit meiner Ladung und dem letzten überlebenden Crewmitglied aus dem Staub gemacht. Ich habe mich auf der Suche nach Hilfe hierher begeben, aber diese nichtsnutzigen Oger haben mich nur dumm angeschaut, als ich ihnen erzählt habe, was passiert ist.]], [[Ich könnte die Hilfe eines zuverlässigen Helden brauchen, um die Überreste meiner Ladung und Ithania aus der Nordwacht zu retten. Der Turm befindet sich im Nordosten.]], },
    { [[Balandar Sternenlicht in Brackenwall möchte, dass Ihr die Fracht der Morgenbö wiederholt und den Ersten Maat Ithania von den Streitkräften von Theramore in der Nordwacht befreit.]], },
  },
  [9438] = {
    [[Bote für Thrall]],
    { [[Seitdem wir das erste Mal die Scherbenwelt betreten haben, hat der Kriegshäuptling auf diese Nachricht gehofft. Nun seid Ihr es, der die Ehre hat, ihm persönlich die Botschaft der Mag'har zu übermitteln.]], [[Verliert keine Zeit, <name>. Nehmt diesen Brief, er enthält mein persönliches Siegel. Es gibt nichts, was Thrall mehr erfreuen wird, als die Botschaft, die Ihr bei Euch tragt.]], },
    { [[Reist nach Orgrimmar und übergebt Thrall Nazgrels Brief.]], },
  },
  [9439] = {
    [[Unabgeholtes Gepäck]],
    { [[Das ist Kargath? Oh je. Ich habe eine... nun, eine Art Siedlung erwartet, ein Zeichen von Zivilisation inmitten der Wildnis.]], [[Das muss ein Fehler sein, diese Hütte voller ungehobelter, ungewaschener, dümmlich grinsender Wilder kann nicht das Hauptquartier des Expeditionskorps sein. Diese flegelhaften Barbaren haben es gewagt, mich auszulachen, als ich ihnen erzählt habe, wie die Dunkeleisenzwerge mich überfallen haben und dann in der Festung Angor verschwunden sind. Ich kann darauf zählen, dass Ihr mir meinen Besitz und meine Würde zurückbringt, oder?]], },
    { [[Holt den Beutel des Beraters und das Rapier des Beraters aus der Festung Angor und kehrt dann zu Berater Sarophas in Kargath zurück.]], },
  },
  [9440] = {
    [[Kleine Häppchen]],
    { [[Ich baue gerade eine Waffe gegen die Draenei und Ihr werdet mir dabei helfen!]], [[Die Verirrten sind die zurückgebliebenen Vettern der Draenei und ich habe ein paar Experimente mit ihnen durchgeführt. Ihr müsst nur ein paar teuflische Häppchen zu ihrem Vieh bringen. Wenn sie die Tiere dann schlachten und essen werden sie verflucht sein! Das wird es dem spießigen Anachoreten Avuun im Sicheren Hafen zeigen!]], [[Geht nun und beeilt Euch! Ihr findet die Verirrten und ihre eingesperrten Tiere im Nordosten in der Zuflucht der Verirrten.]], },
    { [[Verfüttert die teuflischen Häppchen an die gefangenen Tiere der Verirrten. Bringt die Überbleibsel zu Cersei Abendsänger in Steinard in den Sümpfen des Elends.]], },
  },
  [9441] = {
    [[Entsandt zu den Mag'har]],
    { [[Nachdem Ihr mir diese Botschaft überbracht habt, würde ich umgehend in die Scherbenwelt aufbrechen. Leider machen wichtige Angelegenheiten meine Anwesenheit in Durotar erforderlich.]], [[Ich werde den Mag'har umgehend einen würdigen Tribut als Zeichen unserer Freundschaft zukommen lassen. Des Weiteren entsende ich meine fähigsten Diplomaten. All das wird Vorbereitungen und Zeit kosten... wir benötigen jemanden, der uns schon heute vor den Mag'har würdig vertreten kann.]], [[Ich setzte mein gesamtes Vertrauen in Euch, <name>. Reist zum Posten der Mag'har auf der Höllenfeuerhalbinsel und bietet ihnen Eure Dienste im Namen unseres gesamten Volkes an. Enttäuscht mich nicht, <name>. Zu viel hängt davon ab.]], },
    { [[Kehrt zu Gorkan Blutfaust beim Posten der Mag'har auf der Höllenfeuerhalbinsel zurück und bietet ihm im Namen Thralls Eure Dienste an.]], },
  },
  [9442] = {
    [[Eine erschöpfende Krankheit]],
    { [[Es ist schön, dass Ihr uns helfen wollt, <name>. Unsere Grunzer haben verdorbenes Wasser getrunken und sind krank geworden. Leider fehlt mir eine Zutat für eine Heilsalbe. Ich benötige einen seltenen Pilz namens Trockenkappe.]], [[Unser wichtigster Befehl ist, unseren Aufenthaltsort geheim zu halten, weswegen ich nicht riskieren kann, bei der Suche danach entdeckt zu werden. Der einzige Ort, an dem man auf der Höllenfeuerhalbinsel Trockenkappen finden kann, ist eine Höhle in der Nähe der Großen Kluft, südlich von hier.]], [[Seid äußerst vorsichtig, wenn Ihr Euch entschließen solltet, danach zu suchen.]], },
    { [[Erdenruferin Ryga beim Posten der Mag'har auf der Höllenfeuerhalbinsel möchte, dass Ihr 1 Trockenkappe aus einer Höhle in der Großen Kluft besorgt.]], },
  },
  [9443] = {
    [[Das sogenannte Mal des Lichtbringers]],
    { [[Einst war ich ein Schüler von Uther Lichtbringer, dem Gründervater der Ritter der Silbernen Hand. Ich sah sein Versagen bei der Ausbildung von Prinz Arthas. Es führte zu der Zerstörung meines Heimatlandes, Quel'Thalas, und ich gebe Uther die alleinige Schuld daran! Und nun ruht er in einem Grabmal, das zu seinen Ehren erbaut wurde - wie liebenswert.]], [[Meine Quellen berichteten mir, dass diese Scharlachroten Narren eines seiner 'heiligen' Relikte in der Spitze ihres Wachturms, nahe den Toren Herdweilers aufbewahren.]], [[Begebt Euch in die Westlichen Pestländer und bringt es mir.]], },
    { [[Nehmt das Mal des Lichtbringers an Euch und kehrt anschließend zu Mehlar Dämmerklinge beim Bollwerk in den Westlichen Pestländern zurück.]], },
  },
  [9444] = {
    [[Entweiht Uthers Grabmal]],
    { [[So, dass ist viel besser. Ich habe ihm etwas dämonische Magie hinzugefügt.]], [[Nun müsst Ihr es zu seiner Ruhestätte in den Westlichen Pestländern bringen. Benutzt das verderbte heilige Symbol in der Gegenwart von Uthers Statue, um sein Grabmal zu entweihen! Geht nahe heran, sonst kann die Magie ihre Wirkung nicht entfalten.]], [[Das Grabmal befindet sich südöstlich von den Ruinen Andorhals. Kehrt zu mir zurück, wenn Euer Werk vollbracht ist.]], [[Beeilt Euch, <class>!]], },
    { [[Entweiht Uthers Grabmal und kehrt anschließend zu Mehlar Dämmerklinge beim Bollwerk in den Westlichen Pestländern zurück.]], },
  },
  [9446] = {
    [[Das Grab des Lichtbringers]],
    { [[Jetzt, da Ihr das Mal des Lichtbringers zurückgeholt habt, müssen wir es nur noch zu Uthers Grabmal bringen.]], [[Wollt Ihr mich dorthin begleiten? Es ist nicht weit und ich würde Eure Gesellschaft genießen. Danach könnt Ihr hierher zurückkehren und der Hohepriesterin MacDonnell von Eurer Erfahrung berichten.]], [[Was haltet Ihr davon, <name>?]], },
    { [[Begleitet Anachoret Truun zu Uthers Grabmal in den Westlichen Pestländern. Sprecht danach mit Hohepriesterin MacDonnell im Zugwindlager in den Westlichen Pestländern.]], },
  },
  [9447] = {
    [[Salbe verabreichen]],
    { [[Ich habe mit den Kräutern, die Ihr mir gebracht habt, eine Salbe zubereitet, die die Stärke der Grunzer wiederherstellen sollte. Nehmt sie und verabreicht sie allen entkräfteten Grunzern an, die Ihr hier in der Nähe seht.]], },
    { [[Erdenruferin Ryga möchte, dass Ihr die Heilsalbe 10 entkräfteten Grunzern beim Posten der Mag'har auf der Höllenfeuerhalbinsel verabreicht.]], },
  },
  [9448] = {
    [[Mitleid mit den Verfluchten]],
    { [[Diese Blutelfenhexe, Cersei Abendsänger, hat die Verirrten verflucht! Ich weiß es einfach! Ich weiß nicht wie und warum, aber die Folgen sind verheerend. Viele der Verirrten wurden noch weiter verderbt und mit noch teuflischeren Energien verflucht!]], [[<class>, könnt Ihr tief in Eurem Inneren etwas Mitleid für diese armen Kreaturen aufbringen? Sie sollten ihr Leben nicht in einem so furchtbaren Zustand verbringen.]], [[Ihr findet die verfluchten Verirrten in der Zuflucht der Verirrten, den Pfad hinunter nach Osten.]], },
    { [[Tötet 3 Verfluchte der Verirrten und kehrt dann zu Anachoret Avuun im Sicheren Hafen in den Sümpfen des Elends zurück.]], },
  },
  [9449] = {
    [[Ruf der Erde]],
    { [[Die Zeit ist gekommen, <name>. Euer Körper und Euer Geist sind an einem Punkt angelangt, an dem Ihr direkt mit den elementaren Geistern Azeroths kommunizieren müsst.]], [[Lasst Euch jedoch gewarnt sein. Ich spüre, dass unser ungewöhnliches Betreten dieser Welt und dessen unglückliche Nebenwirkungen die Erdgeister erzürnt und zu einem Ungleichgewicht der Elemente geführt haben.]], [[Wollt Ihr Euer Erdtotem erschaffen, so müsst Ihr zuerst mit dem Geist des Tals sprechen. Ihr findet ihn im Heiligen Hain, der sich an die Hügel im Nordwesten schmiegt.]], },
    { [[Sprecht mit dem Geist des Tals beim Heiligen Hain im Am'mental.]], },
  },
  [9450] = {
    [[Ruf der Erde]],
    { [[Das Gleichgewicht der Elemente wurde durch das gewaltsame Eindringen der Draenei ins Tal gestört. Euer Absturz hat die Erdgeister erzürnt und gegen die anderen Elemente gewendet. Ihr werdet sehen, dass man die Elemente nur dann verstehen kann, wenn diese in Einklang sind.]], [[Folglich müsst Ihr das Gleichgewicht wieder herstellen. Im Hain hinter mir findet Ihr ruhelose Erdgeister, die gegen die anderen drei Elemente aufbegehren. Helft uns, stellt den Einklang wieder her.]], },
    { [[Tötet 4 ruhelose Erdgeister und kehrt zum Geist des Tals beim Heiligen Hain im Am'mental zurück.]], },
  },
  [9451] = {
    [[Ruf der Erde]],
    { [[Ich werde Euch jetzt einen Teil meines physischen Wesens geben, damit Ihr Euer Erdtotem erschaffen könnt. Bringt es zu dem, der Euch hierher gesandt hat. Ihr und ich werden von nun an auf Lebzeiten miteinander verbunden sein.]], [[Die Kräfte der Erde werden Euch in Eurem Leben fortwährend zur Verfügung stehen. Wenn Eure Knochen am Ende Eurer Tage wieder zu uns zurückkehren, schließt sich der Kreislauf wieder.]], [[Setzt Eure Macht weise ein.]], },
    { [[Bringt den Erdkristall zu Firmanvaar bei der Absturzstelle im Am'mental.]], },
  },
  [9452] = {
    [[Schnapper - sehr, sehr lecker!]],
    { [[Ich habe festgestellt, dass es in diesen Gewässern von Schnappern nur so wimmelt. Das ist ein sehr, sehr leckerer Fisch.]], [[Mit einem Fischernetz kann ich genug fangen, um dutzende von Draenei satt zu bekommen! Unglücklicherweise wurde ich erst kürzlich von einer Kreatur angegriffen, die man Murloc nennt, als ich im Fluss fischte. Ich wurde dabei verletzt und kann deshalb meine Arbeit nicht mehr verrichten. Ich brauche Hilfe!]], [[Ihr müsst dieses Fischernetz lediglich in der Nähe von Schnapperschwärmen, die man im Kristalfluss östlich von hier findet, auswerfen.]], [[Was sagt Ihr? Vergesst aber nicht, das Netz nach getaner Arbeit wieder zurückzubringen.]], },
    { [[Verwendet das Fischernetz der Draenei, um 10 Schnapper zu fangen. Bringt die Fische und das Netz anschließend zu Diktynna auf der Azurmythosinsel zurück.]], },
  },
  [9453] = {
    [[Findet Acteon!]],
    { [[Ihr habt das wunderbar gemacht, <name>. Unsere Leute werden Euch für diesen Fang sehr dankbar sein. Ach ja, könntet Ihr diese Ladung Schnapper vielleicht zu Acteon bei der Azurwacht bringen?]], [[Er dürfte leicht zu finden sein, da er diese Lieferung bereits erwartet.]], },
    { [[Bringt die Ladung Schnapper zu Acteon bei der Azurwacht.]], },
  },
  [9454] = {
    [[Die große Mondweidenjagd]],
    { [[Ihr macht Euch hier einen Namen, <name>. Warum versucht Ihr Euch nicht mal im Jagen?]], [[Auf Draenor würden unsere besten Jäger Tiere jagen, die den Mondweidenhirschen dieser Gegend sehr ähneln und den Namen Talbuk tragen.]], [[Wenn Ihr denkt, Ihr seid 'Draenei' genug, dann zieht durch die Wildnis rund um die Azurwacht und tötet ausreichend Mondweidenhirsche und -böcke, um Eure Taschen mit ihrem Fleisch zu füllen.]], [[Die Hirsche grasen südlich der Azurwacht, die Böcke im Norden. Gebt auf ihre Hörner Acht!]], },
    { [[Acteon bei der Azurwacht möchte, dass Ihr ihm 6 Mondweidenhirschlenden bringt.]], },
  },
  [9455] = {
    [[Seltsame Funde]],
    { [[Der Kristall, den Ihr beim Körper des Nachtpirscherjungen gefunden habt, scheint ein Teil der Exodar zu sein. Vielleicht ist dies eine Erklärung für das ungewöhnliche Aufkommen und Verhalten einiger Tiere dieser Gegend.]], [[Bringt den Kristall zurück zur Azurwacht. Der Exarch wird sicherlich von dieser Entdeckung erfahren wollen.]], },
    { [[Bringt den schwach leuchtenden Kristall zu Exarch Menelaous bei der Azurwacht.]], },
  },
  [9456] = {
    [[Nachtpirscherjagd]],
    { [[Ich werde nicht versuchen, irgendwelche Ausreden für das, was Ihr gefunden habt, zu finden. Es ist ganz klar unsere Schuld. Uns wurde von zertrümmerten Kristallfragmenten im östlich gelegenen Fluss berichtet, aber wir hatten beschlossen, diese Implikationen zu ignorieren. Wir können jetzt entweder auf dieser Insel herumsitzen und diese Tragödie beweinen, oder etwas tun, um eine Ausbreitung zu vermeiden.]], [[Wenn Ihr es ertragen könnt, möchte ich, dass Ihr zurück in die Wildnis geht und so viele der infizierten Nachtpirscherjungen tötet, wie Ihr finden könnt.]], },
    { [[Exarch Menelaous bei der Azurwacht möchte, dass Ihr 8 infizierte Nachtpirscherjungen tötet.]], },
  },
  [9457] = {
    [[Ein ungewöhnlicher Patron]],
    { [[Naias ist also das Genie hinter den Blutskalpen und ihrer fremdartigen Magie? Ich muss zugeben, dass ich nie gedacht hätte, dass meine Nachforschungen dort enden würden, aber unsere Erkenntnisse könnten sich vielleicht trotzdem als hilfreich herausstellen.]], [[Vor der Küste im Nordwesten befindet sich eine kleine Insel, die von geringen Wasserelementaren bewohnt wird. Es gibt dort eine grobe Steinplatte, die den Trollen vielleicht als Altar dienen könnte. Bringt das Totem, das Ihr gefunden habt, zu diesem Altar. Vielleicht können wir diesen Naias ja hervorlocken und seine Macht in eine angenehmere Form bringen.]], },
    { [[Benutzt Naias' Gabe in der Nähe des Altars von Naias, um den Wasserelementar Naias zu rufen. Besiegt ihn und kehrt dann zu Nemeth Falkenauge im Basislanger von Grom'gol mit dem Herz von Naias zurück.]], },
  },
  [9460] = {
    [[Mit vereinten Kräften]],
    { [[Keltus Dunkellaub meldet sich zum Dienst!]], [[Oh... wer seid Ihr eigentlich? Wenn Ihr wegen meinem Bericht hier seid, dann müsst Ihr mir zuerst helfen.]], [[Ich habe vor, die Spione der Schildwache anzugreifen, möchte meine treue Tuschel aber als Vorhut vorausschicken. Sie braucht lediglich eine Fährte, die sie auf sie hetzt.]], [[Jemand von Eurem Können kann sich doch sicherlich hineinschleichen und etwas von der Anführerin der Schildwachen stehlen?]], },
    { [[Keltus Dunkellaub möchte, dass Ihr das spitzenverzierte Taschentuch von der Anführerin der Schildwachen stehlt und zu ihm bringt.]], },
  },
  [9461] = {
    [[Ruf des Feuers]],
    { [[Ihr sollt wissen, dass der Prophet nach Euch gefragt hat, da er von Euren Erfolgen in Euren Prüfungen erfahren hat. Die Erschaffung Eures Feuertotems muss warten, bis Ihr mit ihm gesprochen habt.]], [[Dies ist eine seltene Ehre; geht sofort zur Exodar, um so schnell wie möglich mit ihm zu sprechen!]], },
    { [[Geht zu Prophet Velen in der Halle des Lichts in der Exodar.]], },
  },
  [9462] = {
    [[Ruf des Feuers]],
    { [[<name>! Es ist an der Zeit, dass Ihr weitere Ausbildung im Weg der Geister erhaltet. Ihr solltet mit Tuluun, dem Schamanen in der Azurwacht, sprechen. Er kann Euch in dem Verständnis und der Beherrschung der Macht des Feuers unterweisen.]], },
    { [[Geht zu Tuluun in der Azurwacht auf der Azurmythosinsel.]], },
  },
  [9463] = {
    [[Medizinische Notwendigkeit]],
    { [[Ich habe herausgefunden, dass die Ranken der Pflanzenkreaturen, den sogenannten Peitschern, auf dieser Insel eine regenerierende Eigenschaft besitzen. Wenn man die Ranken richtig verarbeitet, kann man aus ihnen eine hochwirksame Heilsalbe herstellen. Durch die vielen verletzten Draenei, die ständig in der Azurwacht eintreffen, sind unsere Vorräte an Heilmitteln beinahe erschöpft.]], [[Unser besonderer Gast wurde erst gestern schwer verletzt und bewusstlos am Kristallfluss gefunden. Bringt mir die Wurzeln und wir können sie möglicherweise retten.]], [[Haltet südlich und östlich von hier nach den Lauerwurzkreaturen Ausschau, <class>. Ihr dürft keine Zeit verlieren!]], },
    { [[Anchoret Fateema bei der Azurwacht möchte, dass Ihr für sie 8 Lauerwurzranken sammelt.]], },
  },
  [9464] = {
    [[Ruf des Feuers]],
    { [[Ihr sollt noch einmal auf die Probe gestellt werden. Diesmal ist es das chaotische Element des Feuers, das Ihr verstehen müsst, wenn Ihr ein Totem erstellen wollt, um dessen Energien zu nutzen.]], [[Im Nordosten entlang der Küste gibt es einen heiligen Ort des Feuers, den Glutgrund. Inmitten der verbrannten Bäume wacht Glutos, ein besonders mächtiger und weiser Feuerelementar. Mit ihm müsst Ihr sprechen.]], [[Geht jetzt zu ihm, mögen die Elementargeister von Azeroth mit Euch sein.]], },
    { [[Sprecht mit Glutos beim Glutgrund auf der Azurmythosinsel.]], },
  },
  [9465] = {
    [[Ruf des Feuers]],
    { [[Einer meiner Brüder, Hauteur, ist verrückt geworden! Zweifellos ist der Absturz Eures Schiffs daran schuld. Er verlangt jetzt, dass ihn die heimischen Eulenbestien verehren.]], [[Das kann nicht so weitergehen! Keiner von uns würde jemals einen Gott verkörpern! Ihr müsst das Gleichgewicht wieder herstellen.]], [[Als Erstes müsst Ihr ihre Ritualfackel finden, welche in Besitz der wahnsinnigen Wildekin innerhalb der Tannenruhfeste im Westen ist.]], [[Bringt mir diese Fackel.]], },
    { [[Erlangt die Ritualfackel wieder und bringt sie zu Glutos beim Glutgrund auf der Azurmythosinsel.]], },
  },
  [9466] = {
    [[Gesucht: Schwarzkralle die Wilde]],
    { [[Auf Anweisung des Waldläuferhauptmanns wird hiermit eine Belohnung auf die Erlegung der teuflischen Felsklauenmatriarchin, bekannt als Schwarzkralle die Wilde, ausgesetzt.]], [[Schwarzkralle wurde zuletzt in einer Höhle in der Großen Kluft gesichtet. Zum Erhalt der Belohnung müssen die Klauen der Kreatur vorgezeigt werden.]], },
    { [[Bringt Schwarzkralles Klauen zu Waldläuferhauptmann Venn'ren bei der Falkenwacht auf der Höllenfeuerhalbinsel.]], },
  },
  [9467] = {
    [[Ruf des Feuers]],
    { [[Den Eulenbestien vom Mondschwingenbau auf der Silbermythosinsel, weit im Südwesten, wurde weisgemacht, dass Hauteur ein Gott sei, den es zu huldigen gilt. In Wirklichkeit ist er ein abtrünniger Elementar, den man von seinem Leid erlösen muss. Diese Fackel ist der Schlüssel zu seinem Niedergang.]], [[Hauteur verlangt von den Eulenbestien die regelmäßige Entzündung eines Feuers zu seinen Ehren. Sobald Ihr es entfacht, wird er kommen. Tötet ihn und bringt mir seine Asche. Verwendet die Kugel in Eurer Tasche und ich werde Euch hierher zu mir zurückbringen.]], },
    { [[Besorgt Euch Hauteurs Asche und bringt sie zusammen mit der Ritualfackel zu Glutos beim Glutgrund auf der Azurmythosinsel. Vergesst nicht, dass Ihr die Kugel der Rückkehr verwenden könnt, um zu Glutos zurückzukehren, sobald Ihr die Asche habt.]], },
  },
  [9468] = {
    [[Ruf des Feuers]],
    { [[Ihr habt heute bei dem Versuch, Eure Bindung zum Feuer zu verstehen, etwas Großartiges geleistet. Vergesst nicht, dass Ihr ihm stets den gebührenden Respekt erweisen sollt, um Euch nicht selbst daran zu verbrennen.]], [[Ich habe einen Teil von Hauteurs Asche für Euch aufgehoben. Verwendet ihn, wenn Ihr Euer Feuertotem erstellt und vergesst nichts von dem, was Ihr auf Eurem Weg hierher gelernt habt.]], [[Kehrt jetzt zu Tuluun zurück.]], },
    { [[Bringt Hauteurs Asche zu Tuluun in der Azurwacht auf der Azurmythosinsel.]], },
  },
  [9469] = {
    [[Federbarts Unterstützung]],
    { [[Sollte ich endlich eine Audienz bei Falstad Wildhammer gewährt bekommen, hoffe ich einen der besten Fürsprecher der Wildhammerzwerge an meiner Seite zu haben, <name>.]], [[Federbart genießt bei seinen Leuten hohes Ansehen, sei es wegen seiner legendären Ausbildungsmethoden für Greifen oder wegen seiner Weisheit. Wenn jemand Falstad Wildhammer überzeugen kann, dann er.]], [[Ich würde ihn ja gerne selbst aufsuchen, allerdings darf ich den Nistgipfel nicht verlassen, solange ich meine Audienz erwarte. Sucht Federbart bei seiner Wohnstätte im Süden auf und fragt ihn, ob er an meiner Seite aussagen möchte.]], },
    { [[Geht zu Federbarts Hütte und stattet dem Zwerg dort einen Besuch ab. Solltet Ihr ihn nicht dort antreffen, so sucht nach Hinweisen auf seinen Verbleib.]], },
  },
  [9470] = {
    [[Eine Geste des guten Willens]],
    { [[Das zerklüftete Gebiet des Hinterlands ist nicht nur das Zuhause der Zwerge des Wildhammerklans und ihrer Greifen, sondern auch einiger Waldtrollstämme.]], [[Die Trolle betrachten die Wildhämmer als Besatzer ihres Heimatlands und lassen keine Gelegenheit aus, um die Zwerge zu belästigen, indem sie die Greifennester angreifen. Ein Weg uns in den Augen der Wildhämmer zu beweisen ist, diese Angriffe zum Ende zu bringen, indem wir einen Anführer der Bleichborken, Hexendoktor Mai'jin, töten. Er befindet sich in einer Höhle in Bogens Kante, westlich von Shadra'Alor.]], },
    { [[Botschafter Rualeth auf dem Nistgipfel möchte, dass Ihr Hexendoktor Mai'jin und seine Spinne Tcha'kaz tötet.]], },
  },
  [9471] = {
    [[Die Raubtiere zur Beute machen]],
    { [[Die Wildhammerzwerge sind die Begleiter, Hüter und Ausbilder der Greifen. Die Greifen haben genügend Feinde, vor allem unter den Stämmen der Waldtrolle.]], [[Manchmal stammt die Gefahr aber auch von natürlichen Feinden, wie zum Beispiel den Wölfen. Ein einzelnes Tier ist kein erstzunehmender Gegner für einen Greifen, aber in letzter Zeit werden die Greifen immer häufiger von Rudeln angegriffen, da ihre natürliche Beute knapp wird. Die Wölfe auszulöschen könnte wesentlich zur Sicherheit der Greifen beitragen und uns bei den Wildhämmern sehr beliebt machen.]], },
    { [[Botschafter Rualeth auf dem Nistgipfel möchte, dass Ihr 10 räudige Silbermähnen und 5 Silbermähnenwölfe tötet.]], },
  },
  [9472] = {
    [[Arelions Geliebte]],
    { [[Bei all den wichtigen Dingen, die er zu tun hatte, fand Arelion immer noch genug Zeit seine Geliebte zu besuchen!]], [[Jetzt wird mir alles klar. Viera Sonnenwisper, dieses widerwärtige Frauenzimmer... wir werden Ihr beibringen, sich niemals in die Angelegenheiten einer Magistrix einzumischen.]], [[Nehmt diese Schriftrolle und findet Viera. Sucht einen Weg, sie vom Dorf wegzulocken und benutzt die Schriftrolle.]], [[Arelion erwähnte, dass sie einem bestimmten Wein, der von den Druiden des Cenarius angebaut wird, nicht widerstehen kann. Vielleicht findet Ihr ein wenig davon bei der Zuflucht des Cenarius in den Zangarmarschen westlich von hier.]], },
    { [[Magistrix Carinda bei der Falkenwacht auf der Höllenfeuerhalbinsel möchte, dass Ihr Viera Sonnenwisper aus der Siedlung herauslockt und Carindas Rolle der Vergeltung auf sie anwendet.]], },
  },
  [9473] = {
    [[Eine alternative Alternative]],
    { [[Der Zustand des Mädchens ist bereits zu kritisch, als dass einfache Salben sie noch retten könnten. Es gibt möglicherweise noch einen anderen Weg...]], [[Der Wald von Azurmythos birgt viele Geheimnisse, eines davon ist eine seltene Pflanze, das Azurlöwenmäulchen, die viele als Allheilmittel ansehen.]], [[Sie ist leicht zu entdecken, da sich ihr Aussehen sehr stark von allen anderen Pflanzen unterscheidet und sie ausschließlich im Schatten der Azurkiefer wächst. Beeilt Euch!]], [[Und <name>, nehmt Euch vor den Drescherwurzkreaturen in Acht! Sie ernähren sich von den Azurlöwenmäulchen.]], },
    { [[Daedal bei der Azurwacht möchte, dass Ihr für ihn 5 Azurlöwenmäulchen sammelt.]], },
  },
  [9474] = {
    [[Das Mal des Lichtbringers]],
    { [[In der kurzen Zeit, die ich in Azeroth verbracht habe, habe ich viele seiner Helden studiert. Einer von ihnen, Uther Lichtbringer, hebt sich von allen anderen ab. Nachdem er viele Jahre ein Held war, wurde er am Ende von seinem viel versprechendsten Schüler, Arthas, verraten.]], [[Zu seiner Ehre würde ich gerne etwas, das er verloren hat, zu seinem Grabmal bringen. Man sagt, dass der Scharlachrote Kreuzzug eines seiner heiligen Relikte oben in dem Wachturm in der Nähe von Herdweiler aufbewahrt.]], [[Bitte, <class>, geht und holt es zurück.]], },
    { [[Besorgt das Mal des Lichtbringers und kehrt dann zu Anachoret Truuen im Zugwindlager in den Westlichen Pestländern zurück.]], },
  },
  [9475] = {
    [[Wiedererlangen der Eier]],
    { [[Die Begutachtung der Überreste lässt vermuten, dass es sich bei dem zerschmetterten Skelett um Federbart handeln muss. Eine der skelettierten Hände umklammert fest ein zerfetztes und schmutziges Stück Papier. Ihr zieht es vorsichtig heraus und rollt es auf. Zum Vorschein kommt eine handgezeichnete Karte und ein paar eilig hingekritzelte Notizen.]], [[Die Karte scheint eine Skizze von Shadra'Alor und seinen drei Tempeln zu sein. Mehrere 'X' auf der Zeichnung markieren vermutlich die Lage gestohlener Eier. Ihr packt die Karte ein und plant, sie an einem sicheren Ort genauer zu untersuchen.]], },
    { [[Erlangt 5 Greifeneier und bringt sie zu Botschafter Rualeth beim Nistgipfel.]], },
  },
  [9476] = {
    [[Suche nach Federbart]],
    { [[Die jüngsten Einträge in Federbarts Tagebuch beschreiben seine Sorge bezüglich der immer häufigeren Überfälle von Bleichborkentrollen auf die Greifen im Wildhammergebiet und die sich mehrenden Diebstähle von Eiern. Die Untersuchung der kleineren Bleichborkensiedlungen brachte keine Informationen zu Tage.]], [[Der letzte Eintrag handelt von Federbarts Verdacht, dass ein Hexendoktor der Bleichborken, namens Mai'jin, hinter all den Raubzügen steckt. Am Ende des Eintrags, steht mit hastig geschriebenen Worten, dass der Zwerg die Absicht hat, nach Shadra'Alor zu gehen und nach den Eiern zu suchen.]], },
    { [[Reist nach Shadra'Alor und sucht nach Spuren von Federbart.]], },
  },
  [9483] = {
    [[Die feinen Freuden im Leben]],
  },
  [9484] = {
    [[Die Zähmung der Bestie]],
    { [[Ihr habt zweifellos schon Tiere für Euren Schutz und für Euren Lebensunterhalt getötet, jedoch seid Ihr erst jetzt bereit, Euch mit ihnen anzufreunden und sie zu zähmen, um von ihrer Stärke Gebrauch zu machen. Ich kann Euch darin ausbilden.]], [[Nehmt diese magische Zähmerrute, sie wird ein wildes Tier vorübergehend an Euch binden und an Eurer Seite kämpfen lassen. Beginnt mit einem wahnsinnigen Drachenfalken, ein recht gefügiger Vogel, den man außerhalb der Zuflucht der Weltenwanderer findet. Kehrt anschließend zu mir zurück.]], },
    { [[Verwendet die Zähmerrute, um einen wahnsinnigen Drachenfalken zu zähmen. Kehrt anschließend zu Leutnant Dämmerflucht zurück.]], },
  },
  [9485] = {
    [[Die Zähmung der Bestie]],
    { [[Ihr habt Euch gut geschlagen, <name>. Euer Verständnis für die Wildnis wird rasch größer.]], [[Die Nebelfledermaus ist ein listiger Jäger, sie vereint die Schnelligkeit des Drachenfalken und die Wildheit einer Bachtatze. Man findet die Nebelfledermäuse direkt über dem Elrendar im Süden, wo sie unglücklichen Reisenden auflauern, die in den Geisterlanden umherwandern.]], [[Verwendet diese letzte Zähmerrute, um eine Nebelfledermaus zu bändigen. Kehrt anschließend zu mir zurück.]], },
    { [[Verwendet die Zähmerrute, um eine Nebelfledermaus zu zähmen. Kehrt anschließend zu Leutnant Dämmerflucht zurück.]], },
  },
  [9486] = {
    [[Die Zähmung der Bestie]],
    { [[Die alte Bachtatze ist ein gründlicher und geschickter Jäger. Wenn man sie zähmen möchte, sieht sie einen sofort als Beute. Lasst Euch nicht von Eurem Vorhaben abbringen.]], [[Verwendet die Zähmerrute, um diese große Katze gefügig zu machen und an ihrer Seite zu jagen. Man findet sie überall im Immersangwald, manche sogar ganz nahe bei der Jagdhütte. Kehrt zu mir zurück, wenn Ihr genug gelernt habt.]], },
    { [[Verwendet die Zähmerrute, um eine alte Bachtatze zu zähmen. Kehrt anschließend zu Leutnant Dämmerflucht zurück.]], },
  },
  [9487] = {
    [[Arkanhäscher]],
    { [[Arkanhäscher sind magische Kunstwesen mit roher Kraft. Sie sind dick gepanzert und können schwere Schläge austeilen. Eure Zauberkraft gewährt Euch jedoch den Vorteil, ihre Abwehr umgehen und Treffer von ihnen vermeiden zu können, <name>.]], [[Reist zum Morgensternturm und sprengt die Arkanhäscher in Stücke! Bei ihren Überresten findet Ihr kristallisierte Fragmente magischer Energie. Sammelt diese und bringt sie zu mir. Den Morgensternturm findet Ihr im Osten, jenseits des Sees.]], },
    { [[Arkanist Vandril möchte, dass Ihr 10 Arkantrümmer für ihn sammelt.]], },
  },
  [9488] = {
    [[Eine einfache Robe]],
    { [[Die Arkantrümmer, die Ihr gesammelt habt, sind gut geeignet.]], [[Geeignet wofür, möchtet Ihr wissen? Um eine prächtige <class>robe für Euch herzustellen!]], [[Alles, was wir jetzt noch benötigen, ist etwas Leinenstoff und eine Seidendrüse der Spindelweber. Die Seidendrüse bekommt Ihr von einer Spindelweberspinne, vorzugsweise nachdem Ihr diese getötet habt.]], },
    { [[Arkanist Vandril möchte, dass Ihr ihm 15 Leinenstoff und eine Seidendrüse der Spindelweber bringt.]], },
  },
  [9489] = {
    [[Ordnung in der Schneise]],
    { [[<name>, gut, dass Ihr hier seid. Die Geißel überfällt unsere Stadt mit so unendlichem Hass, dass ich befürchten muss, dass die Moral unserer Wache langsam dahinschwindet.]], [[Eilt zu der Todesschneise südlich der Stadt, wo Jaela und ihre Waldläufer bereitstehen. Erfüllt sie mit Eurem Segen mit der Macht des Lichts.]], },
    { [[Wirkt das Machtwort: Seelenstärke auf 6 Waldläufer von Immersang in der Todesschneise.]], },
  },
  [9490] = {
    [[Die Felsklauenmatriarchin]],
    { [[Felsklauen sind bösartige, hinterhältige und grausame Kreaturen. Aber die normalen sind noch harmlose Schoßtierchen im Vergleich zu ihrer Matriarchin, Schwarzkralle der Wilden.]], [[Mein Cousin wurde von ihr gefressen. Das war kein schöner Anblick. Ich habe an diesem Tag geschworen, dass Schwarzkralle dafür bezahlen wird. Bringt mir einen Beweis für den Tod dieses Monsters, und ich werde Euch meine Ersparnisse dafür geben. Einverstanden?]], [[Ihr findet das Biest in einer Höhle in der Großen Kluft. Viel Erfolg!]], },
    { [[Besorgt Schwarzkralles Klauen von Schwarzkralle der Wilden in der Großen Kluft und bringt sie zu Ikan beim Tempel von Telhamat auf der Höllenfeuerhalbinsel.]], },
  },
  [9491] = {
    [[Gier]],
    { [[Ein reicher, und wenn ich sage 'reich', meine ich 'stinkreicher' Händler kam gestern hier vorbei und erzählte etwas über einen Ring von 'immenser archäologischer Bedeutung', der in einer der zahllosen Grabtruhen in den Amanikatakomben eingeschlossen ist. Wenn er nicht über den Ring sprach, dann darüber, wie viel er dafür bezahlen würde.]], [[Ich könnte Euch Diebeswerkzeug vorstrecken, und wenn es Euch gelingt, mir den Ring zu bringen, zahle ich Euch so viel, dass ihr zehn Ringe für Euch selbst kaufen könnt!]], },
    { [[Eralan in Tristessa möchte, dass Ihr ihr einen gekörnten Goldring aus den Amanikatakomben bringt.]], },
  },
  [9492] = {
    [[Das Schicksal wenden]],
    { [[Die weiterhin bestehende orcische Bedrohung ist eine der traurigen Wahrheiten der Scherbenwelt. Es gab eine Zeit, in der wir Söhne des Lothars glaubten, dass wir die Sicherheit der Ehrenfeste mit unserem Blut erkauft haben, aber es sollte keinen Frieden geben.]], [[Die Söhne werden sich keinem Orc fügen - ob er nun grün, braun oder eben rot sein mag - wir werden sie besiegen oder bei dem Versuch sterben. Wenn wir eine Chance auf den Sieg haben wollen, müssen wir sie in der Höllenfeuerzitadelle bekämpfen. Bringt mir die Waffe ihres selbsternannten Kriegshäuptlings Kargath Messerfaust, und ich werde Euch reich belohnen.]], },
    { [[Bringt Kriegshäuptling Kargaths Faust zu Truppenkommandant Danath Trollbann in der Ehrenfeste.]], },
  },
  [9493] = {
    [[Der Stolz der Höllenhorde]],
    { [[Wir wissen herzlich wenig über die Höllenhorde in der Höllenfeuerzitadelle, und die Informationen, die wir haben, wurden teuer mit Blut bezahlt.]], [[Die Truppen der Höllenhorde unter dem Kommando von Kargath Messerfaust sind ein blutrünstiger, schlachterprobter Haufen, der von bösartigen Magiern unterstützt wird. Die Elite dieser Streitkräfte, die Legionäre, Champions und Zenturios der Zerschmetterten Hand, werden von den gewöhnlichen Soldaten gefürchtet und dienen als Offiziere.]], [[Auf sie loszugehen und sie zu vernichten ist der erste wichtige Schritt für einen erfolgreichen Angriff auf die Führung der Höllenhorde.]], },
    { [[Feldkommandant Romus in der Ehrenfeste möchte, dass Ihr 8 Legionäre der Zerschmetterten Hand, 4 Zenturios der Zerschmetterten Hand und 4 Champions der Zerschmetterten Hand tötet.]], },
  },
  [9494] = {
    [[Teufelsglut]],
    { [[Die altmodischen Akademiker Azeroths haben sich schon viel zu lange in ihren Türmen versteckt. Jetzt ist es an mir, ihnen zu zeigen, was hochmoderne Forschung bedeutet! Sie haben Angst, mit dem Feuer zu spielen, und genau das werde ich tun! Das heißt, sobald ich dieses Feuer in die Finger bekomme.]], [[In den Zerschmetterten Hallen der Höllenfeuerzitadelle vollbringt der Großhexenmeister Nethekurse unerreichte magische Meisterleistungen, die von der Macht einer ungewöhnlichen Kohlenpfanne noch verstärkt werden. Benutzt dieses Gefäß, um ein Stück Teufelsglut zu fangen, und bringt es zu mir, <name>.]], },
    { [[Magus Zabraxis in der Ehrenfeste möchte, dass Ihr ihr ein Stück Teufelsglut bringt.]], },
  },
  [9495] = {
    [[Der Wille des Kriegshäuptlings]],
    { [[Es ist nicht einfach, das orcische Volk von seinen verderbten Blutsverwandten zu befreien, <name>. Kriegshäuptling Thrall hat schon zu viele orcische Helden durch die Hand des Schicksals fallen sehen. Und nun scheint es, als ob auch Kargath Messerfaust ihm zum Opfer gefallen ist.]], [[In den Zerschmetterten Hallen der Höllenfeuerzitadelle hat Kargath sich selbst den Titel Kriegshäuptling der Höllenhorde gegeben. Er und seine Armee von irren und blutdurstigen Höllenorcs werden nicht ruhen, bis Thrallmar in Trümmern liegt. Kriegshäuptling Thrall hat befohlen, Kargath und seine Streitkräfte koste es was es wolle zu vernichten.]], },
    { [[Bringt Kriegshäuptling Kargaths Faust zu Nazgrel in Thrallmar.]], },
  },
  [9496] = {
    [[Der Stolz der Höllenhorde]],
    { [[Von Oberanführer Stümmelfaust mal abgesehen, <name>, ist mit der Höllenhorde nicht zu spaßen.]], [[Soweit wir wissen, sind die Truppen der Höllenhorde unter dem Kommando von Kargath Messerfaust ein blutrünstiger, schlachterprobter Haufen, der von bösartigen Magiern unterstützt wird. Die Elite dieser Streitkräfte, die Legionäre, Champions und Zenturios der Zerschmetterten Hand, werden von den gewöhnlichen Soldaten gefürchtet und dienen als Offiziere.]], [[Auf sie loszugehen und sie zu vernichten ist der erste wichtige Schritt für einen erfolgreichen Angriff auf die Führung der Höllenhorde.]], },
    { [[Schattenjäger Ty'jin in Thrallmar möchte, dass Ihr 8 Legionäre der Zerschmetterten Hand, 4 Zenturios der Zerschmetterten Hand und 4 Champions der Zerschmetterten Hand tötet.]], },
  },
  [9498] = {
    [[Falkenwacht]],
    { [[Mein Volk hat die Falkenwacht erst kürzlich als Operationsbasis im Südwesten der Höllenfeuerhalbinsel, hinter der Höllenfeuerzitadelle, fertiggestellt. Man wird Euch dort als Verbündeten willkommen heißen, <name>.]], [[Wenn Ihr also auf der Suche nach Arbeit seid, reist dorthin und sagt Waldläuferhauptmann Venn'ren, dass ich Euch geschickt habe. Er wird Euch sicherlich eine Aufgabe zuweisen können, die Eurer Fähigkeiten würdig ist.]], },
    { [[Meldet Euch bei Waldläuferhauptmann Venn'ren bei der Falkenwacht auf der Höllenfeuerhalbinsel.]], },
  },
  [9499] = {
    [[Falkenwacht]],
    { [[Was macht Ihr denn noch hier, <name>? Habt Ihr nicht gehört, dass wir einen anständigen Außenposten der Sin'dorei auf der Höllenfeuerhalbinsel errichtet haben?]], [[Ihr wärt doch sicher viel lieber bei den Blutelfen als bei all diesem... Gestank hier. Ihr findet die Falkenwacht im Südosten, hinter der Höllenfeuerzitadelle. Meldet Euch bei Waldläuferhauptmann Venn'ren, er wird sich um Euch kümmern.]], [[Die Straße dorthin ist sehr gefährlich und wird von den Spähern der Allianz überwacht. Reist lieber in Begleitung. Am besten mit Blutelfen.]], },
    { [[Reist zur Falkenwacht im Südwesten der Höllenfeuerhalbinsel und meldet Euch bei Waldläuferhauptmann Venn'ren.]], },
  },
  [9500] = {
    [[Ruf des Wassers]],
    { [[Es ist schon eine Weile her, seitdem Ihr das letzte Mal auf die Probe gestellt wurdet. Scharfseher Nobundo hat nach Euch gefragt. Ihr müsst sehr viel an Erfahrung gewonnen haben, da man Euch nun die Geheimnisse des Elements des Wassers lehren möchte.]], [[Ihr wisst noch, dass Ihr ihn in der Kristallhalle der Exodar findet, ja?]], },
    { [[Sprecht mit Scharfseher Nobundo in der Kristallhalle der Exodar auf der Azurmythosinsel.]], },
  },
  [9501] = {
    [[Ruf des Wassers]],
    { [[Ein mächtiger Wasserelementar namens Aqueous hat nach Euch verlangt. Anscheinend ist das Element des Wassers auf der Blutmythosinsel aus dem Gleichgewicht geraten, was bei der unnatürlichen Vorliebe der Blutelfen nicht wirklich verwunderlich ist.]], [[Ihr findet Aqueous in einem versteckten Riff unter den Wogen vor der Küste im Norden der Blutmythosinsel.]], [[Nehmt diesen Trank, er wird Euch auf Eurem Weg zu Aqueous behilflich sein, sobald Ihr in der Nähe des Riffs im Wasser seid.]], },
    { [[Sprecht mit Aqueous im Versteckten Riff bei der Blutmythosinsel.]], },
  },
  [9502] = {
    [[Ruf des Wassers]],
    { [[Es ist schon eine Weile her, seitdem Ihr das letzte Mal auf die Probe gestellt wurdet. Scharfseher Nobundo hat nach Euch gefragt. Ihr müsst sehr viel an Erfahrung gewonnen haben, da man Euch nun die Geheimnisse des Elements des Wassers lehren möchte.]], [[Ihr wisst noch, dass Ihr ihn in der Kristallhalle der Exodar findet, ja?]], },
    { [[Sprecht mit Scharfseher Nobundo in der Kristallhalle der Exodar auf der Azurmythosinsel.]], },
  },
  [9503] = {
    [[Ruf des Wassers]],
    { [[In Verbindung mit den anderen Elementen ist Wasser der Quell und die Grundlage allen Lebens. Alle Kreaturen sind darauf angewiesen. Allerdings bringt das Wasser durch die Verunreinigung jetzt auch den Tod.]], [[Wir müssen das Ausmaß des Schadens erfassen. Im Südwesten liegt ein derart verderbtes Gewässer, welches mittlerweile als besudelter Teich bekannt ist. Geht dorthin und besorgt Euch besudelte Essenzen von den dort lebenden Wassergeistern.]], },
    { [[Sammelt 6 besudelte Essenzen ein und bringt sie zu Aqueous im Versteckten Riff bei der Blutmythosinsel.]], },
  },
  [9504] = {
    [[Ruf des Wassers]],
    { [[It is worse than I feared. The corruption is very potent and if unchecked it could spread to the ocean currents and be carried throughout all of Azeroth!]], [[We are going to need a sample of the purest water to counter this malignancy. There is a forest on the nearby continent of Kalimdor named Ashenvale. Near its southwestern border is a place called the Ruins of Stardust.]], [[Take this bota bag and fill it with the water from the fountain on the small island at the center of the ruins. Hurry, <name>!]], },
    { [[Füllt den leeren Botabeutel und kehrt anschließend zu Aqueous im Versteckten Riff bei der Blutmythosinsel zurück.]], },
  },
  [9505] = {
    [[Die Prophezeiung von Velen]],
    { [[Der Prophet sprach von diesem Tag, da auf dieser Insel, in dieser Welt, sich uns unsere Verbündeten offenbaren würden.]], [["Man wird uns auf die Probe stellen. Wir werden für die Sünden unserer Vorväter bezahlen..."]], [[Damals blieb mir der Sinn seiner Worte verborgen; doch in dem Moment, als ich dieses Wesen erblickte, wurde mir die Bedeutung seiner Prophezeiung gewahr. Selbst an diesem Ort verspüre ich noch die schleichende Verderbnis unseres Volkes.]], [[Begebt Euch nach Süden, dort wo wir sie gefunden haben. Findet Ihresgleichen und berichtet ihnen von unserem Volk.]], },
    { [[Daedal möchte, dass Ihr nach Süden reist und die Personen aufsucht, von denen das verletzte Wesen gesprochen hat.]], },
  },
  [9506] = {
    [[Ein kleiner Anfang]],
    { [[Ich bin Admiral Odesyus vom Marinekommando der Allianz. Eine Plage der besonderen Art zwang uns dazu, an der Küste Eurer Insel vor Anker zu gehen. Es beschämt mich das sagen zu müssen, doch ein paar Goblins haben es irgendwie geschafft, sich als blinde Passagiere in den Frachträumen unserer beiden Schiffe zu verstecken.]], [[Letzte Nacht, als der Großteil der Schiffsmannschaften in den Kojen lag, schlugen sie zu!]], [[Sie stahlen unsere Navigationsinstrumente und sprangen von Bord!]], [[Anschließend verschwanden sie im Dickicht, östlich von hier. Werdet Ihr uns bei der Suche nach den Instrumenten behilflich sein? Es wäre eine großzügige Geste von Euch.]], },
    { [[Admiral Odesyus bei Odesyus' Ankerplatz möchte, dass Ihr den von der Venture Co. gestohlenen nautischen Kompass und die nautische Karte wiederbeschafft.]], },
  },
  [9508] = {
    [[Ruf des Wassers]],
    { [[Ich habe den Ursprung der Verunreinigung gefunden. Es ist das Nebenprodukt eines wahnsinnigen Experiments und Ihr werdet dem Ganzen ein Ende bereiten!]], [[Südwestlich des Besudelten Teichs liegt ein kleines Lager, wo Ihr auf das 'Experiment' von Tel'athion dem Unreinen stoßen werdet. An diesem Ort erzeugt er den Unrat und lässt ihn in die Gewässer der Insel sickern.]], [[Verwendet das reine Wasser aus dem Eschental, um seine Fässer zu zerstören. Das wird ihn sicherlich aus seinem Versteck locken.]], [[Wir werden das alles ein für alle Mal beenden!]], },
    { [[Bringt Tel'athions Kopf zu Aqueous im Versteckten Riff bei der Blutmythosinsel zurück.]], },
  },
  [9509] = {
    [[Ruf des Wassers]],
    { [[Ich habe etwas von dem Wasser aus dem Eschental beiseite gelegt, jedoch nicht, weil ich an Euren Fähigkeiten, die Situation in den Griff zu bekommen, gezweifelt habe. Im Gegenteil, ich wusste, dass Ihr Erfolg haben würdet. Ich wollte lediglich sicherstellen, dass Ihr etwas Besonderes habt, das Ihr zurück zu Nobundo bringen könnt.]], [[Hier, <name>, nehmt dieses Fläschchen mit reinstem Wasser. Die Erstellung eines mächtigen Wassertotems ist Euch dadurch gewiss.]], [[Scharfseher Nobundo erwartet Eure Rückkehr bei der Exodar.]], },
    { [[Nehmt das Fläschchen mit reinstem Wasser zu Scharfseher Nobundo innerhalb der Exodar auf der Azurmythosinsel.]], },
  },
  [9510] = {
    [[Bälge eines borstigen Grollhufs]],
    { [[Ihr wollt Euch also nützlich machen? Unsere Vorräte an Grollhufbälgen sind sehr knapp, <name>.]], [[Wenn Ihr von hier aus nach Südwesten geht, werdet Ihr in der Nähe der Falkenwacht haufenweise borstige Grollhufe finden. Ihre Bälge sind ideal für die Lederverarbeitung. Bringt mir einen Stapel davon und ich werde Euch angemessen belohnen.]], },
    { [[Versorger Braknar beim Mag'harposten auf der Höllenfeuerhalbinsel möchte, dass Ihr ihm 5 Bälge eines borstigen Grollhufs bringt.]], },
  },
  [9511] = {
    [[Kargaths Schlachtpläne]],
    { [[Die Dokumente, die Ihr bei Kargath gefunden habt, enthalten genaue Pläne für einen großen Angriff auf die Ehrenfeste.]], },
  },
  [9512] = {
    [[Kräckas Jumboeintopf]],
    { [[Der Admiral hat wohl Recht, Ihr seid ziemlich clever. Könnt Ihr Kräcka vielleicht aushelfen?]], [[Wenn ich der Mannschaft noch einmal Hühnchen vorsetze, werden'se mich sicher kielholen.]], [[Hab' gesehen, dass es an der Küste nur so von Kriechern wimmelt! Bringt mir Kriecherfleisch und ich steh' für immer in Eurer Schuld.]], [[Helft Ihr 'nem ehemaligen Piraten aus der Patsche?]], },
    { [["Kräcka" Aschebäscha bei Odesyus' Ankerplatz möchte, dass Ihr ihm 6 Stücke Raubkriecherfleisch bringt.]], },
  },
  [9513] = {
    [[Die Rückeroberung der Ruinen]],
    { [[Während einige glauben, dass unsere momentane Lage das Resultat einer langen Kette von unglücklichen Ereignissen ist, glaube ich vielmehr, dass wir aus einem bestimmten Grund hier sind. Die unsichtbare Hand des Schicksals hat uns an diesen Ort verschlagen. Die Gründe dafür werden sich uns im Laufe der Zeit offenbaren.]], [[Im Westen liegt eine uralte Stadt der Nachtelfen, heute sind es nur noch von Naga besetzte Ruinen. Ich wäre nachlässig, würde ich tatenlos zusehen, wie die Naga weiterhin das Land meiner Vorfahren mit ihrer Anwesenheit beflecken.]], [[Ihr habt Eure Fähigkeiten als <class> unter Beweis gestellt. Nun ehrt mein Volk und reinigt diese Ruinen von seinen unrechtmäßigen Einwohnern.]], },
    { [[Priesterin Kyleen Il'dinare bei Odesyus' Ankerplatz auf der Azurmythosinsel möchte, dass Ihr 5 Myrmidonen der Hasspranken, 5 Naga der Hasspranken und 5 Sirenen der Hasspranken tötet.]], },
  },
  [9514] = {
    [[Runenbesetzte Schrifttafel]],
    { [[Es gelingt Euch nicht, die Symbole auf der Schrifttafel zu entziffern. Vielleicht ist die Priesterin in der Lage, die mysteriösen Runen zu entschlüsseln.]], },
    { [[Bringt die runenbesetzte Schrifttafel zu Priesterin Kyleen Il'dinare bei Odesyus' Ankerplatz auf der Azurmythosinsel.]], },
  },
  [9515] = {
    [[Kriegsherr Sriss'tiz]],
    { [[Eine Nagainvasion wäre ein Desaster, <name>. Ich habe gesehen, was diese Naga anrichten können und ich werde es nicht zulassen, dass sie weiter über unschuldige Wesen herfallen. Ich nehme an, dass dieser Kriegsherr Sriss'tiz sich in einer Höhle versteckt - möglicherweise irgendwo an der Küste im Westen.]], [[Macht Kriegsherr Sriss'tiz ausfindig und beendet seine Herrschaft für immer!]], },
    { [[Priesterin Kyleen Il'dinare bei Odesyus' Ankerplatz auf der Azurmythosinsel möchte, dass Ihr Kriegsherr Sriss'tiz tötet.]], },
  },
  [9516] = {
    [[Zerstört die Legion]],
    { [[Ich werde Ihre Existenz in dieser Welt nicht tolerieren! Die Brennende Legion hat unser Volk fast ausgerottet, und wir mussten ein neues Zuhause in einer neuen Welt suchen. Dann kamen sie erneut. Und wir haben uns zurückgezogen, immer und immer wieder.]], [[Nie wieder!]], [[Im Süden gibt es noch ein Überbleibsel der Legion, in den Regionen, die als Dämonenhügel, Dämonensturz und Dämonenstieg bekannt sind. Sie sind von dieser Macht, die einst in Eure Welt eingedrungen ist, übriggeblieben.]], [[Geht und zerstört so viele von ihnen, wie Ihr könnt, bevor es zu spät ist!]], },
    { [[Tötet 15 Dämonen auf dem Dämonenhügel, dem Dämonensturz und dem Dämonenstieg.]], },
  },
  [9517] = {
    [[Eine schändliche Verschwendung]],
    { [[Willkommen in Waldeslied, <class>. Wo Ihr jetzt noch Zelte seht, werden einst Gebäude stehen, aber dieses Ziel erfordert einen ständigen Nachschub an Baumaterial. Ich bringe es nicht übers Herz, unseren Arbeitern zu befehlen, die uralten Bäume dieses Hains zu fällen, aber unsere Nachbarn im Süden und Westen haben keine so große Ehrfurcht vor dem Wald.]], [[Wir werden das verdorbene Holz aus der Festung der Satyrn im Westen zurückfordern. Bringt das Holz zu Knorre im Holzfällerlager des Kriegshymnenklans. Er ist ein Urtum, der es für unsere Zwecke reinigen wird.]], },
    { [[Sammelt 5 Stück verdorbenes Satyrnaarholz.]], },
  },
  [9518] = {
    [[Agenten der Zerstörung]],
    { [[Das Holzfällerlager des Kriegshymnenklans ist ein Schandfleck in unserer Landschaft. Wann immer wir Fortschritte dabei machen, ihre Arbeiten zu stoppen, befiehlt Vorarbeiter Gorthak seinen Holzarbeitern, ihre Anstrengungen noch zu verdoppeln!]], [[Jetzt fällen die Orcs mehr Holz, als sie je verbrauchen können und lassen es verrotten, während ihre Klingen schon wieder in frisches Holz schlagen! Dies ist eine Beleidigung gegenüber allem, für das wir einstehen, <name>, und wir werden ihnen zeigen, dass wir uns eine weitere Zerstörung unseres Landes nicht gefallen lassen!]], [[Geht, und ruht nicht, bevor Vorarbeiter Gorthak und seine Lakaien tot sind!]], },
    { [[Tötet Vorarbeiter Gorthak und 12 weitere, beliebige Mitglieder der Horde im Holzfällerlager des Kriegshymnenklans.]], },
  },
  [9519] = {
    [[Elunes Kelch]],
    { [[Das Gebiet westlich von hier, das nun Satyrnaar heißt, war einst ein bedeutender Schrein meines Volkes. Dort befand sich ein Relikt von unschätzbarem Wert, Elunes Kelch, der den Hütern des Schreins von den Priestern Elunes anvertraut wurde. Einst reisten Pilger zu dem Schrein, um die heilenden Kräfte, die dem Kelch nachgesagt wurden, am eigenen Leib zu erfahren.]], [[Vielleicht sind diese Geschichten nur Ammenmärchen, aber wenn nur ein Körnchen Wahrheit in ihnen steckt, könnte der Kelch vielleicht der Schlüssel zur Wiederherstellung unseres leidenden Waldes sein. Ich muss es wissen, <name>.]], },
    { [[Bringt Elunes Kelch in Euren Besitz.]], },
  },
  [9520] = {
    [[Teuflische Pläne]],
    { [[Der Dämon hat dieses Pergament fallenlassen, welches überraschenderweise in der Allgemeinsprache verfasst wurde! Darin werden Diathorus und Gorgannon erwähnt, zwei berüchtigte Schreckenslords, die die Dämonen in dieser Gegend befehligen.]], [[Die Pläne enthalten genaue Vorbereitungen für einen dämonischen Angriff auf Waldeslied im Norden, wo gerade eine neue Draeneisiedlung errichtet wird.]], [[Vielleicht ist Verteidiger Vedaar im Holzfällerlager des Kriegshymnenklans daran interessiert?]], },
    { [[Bringt die teuflischen Pläne zu Verteidiger Vedaar im Holzfällerlager des Kriegshymnenklans im Eschental.]], },
  },
  [9521] = {
    [[Bericht von der Nordfront]],
    { [[Die Schlacht gegen das Holzfällerlager des Kriegshymnenklans wird nicht nur vom Rajenbaumturm aus geführt, <name>. Eine beträchtliche Anzahl von Schildwachen der Silberschwingen hat sich im Hain der Silberschwingen, in der Nähe der Grenze zum Brachland, postiert. Schildwache Weltensang ist für unsere Bemühungen gegen die, die unser Land zerstören wollen, verantwortlich.]], [[Ich bin mit meinem Bericht an Schildwache Weltensang etwas spät dran, möchte aber den Turm nur ungern unbewacht lassen. Wenn Eure Reisen Euch in die Nähe des Silberschwingenhains bringen, könntet Ihr dann meinen Bericht abgeben?]], },
    { [[Bringt Schildwache Melyrias Bericht zu Schildwache Weltensang im Hain der Silberschwingen im Eschental.]], },
  },
  [9522] = {
    [[Nie wieder!]],
    { [[<name>, es ist Eure Aufgabe dafür zu sorgen, dass dieser Angriff der Brennenden Legion niemals stattfindet. Ihr müsst losziehen und ihre Anführer erschlagen. Ohne sie ist der Rest der Dämonen nur ein Haufen geistloser Untiere.]], [[Ihr findet Gorgannon im Dämonensturz südlich hinter dem Dämonenhügel in der Nähe des schwebenden Speers von Mannoroth.]], [[Diathorus der Sucher lebt irgendwo im Grabhügel auf dem Dämonensturz. Um dort hinaufzugelangen, sucht nach einem Stieg in der Nähe des Denkmals der Horde für Grom Höllschrei.]], [[Bringt mir ihre Waffen.]], },
    { [[Erhaltet Gorgannons flammende Klinge und den Teufelsspeer des Suchers.]], },
  },
  [9523] = {
    [[Marmor, Stein und Eisen bricht, aber uralte Nachtelfenrelikte nicht...]],
    { [[Die Priesterin sagte mir, dass die Ruinen im Westen die Überreste einer Nachtelfenkultur sind. Das erinnerte mich wiederum an etwas, was mein Paps immer über Nachtelfenruinen zu sagen pflegte: "Kleiner, wenn du Nachtelfenruinen siehst, dann sind bestimmt auch Nachtelfenrelikte in der Nähe."]], [[Was für ein Zwerg wäre ich wohl, wenn ich nicht die erste Person anstellen würde, die mir über den Weg läuft, um für mich nach Nachtelfenrelikten zu graben? Los jetzt!]], },
    { [[Archäologe Adamant Eisenherz bei Odesyus' Ankerplatz möchte, dass Ihr ihm 8 uralte Relikte bringt.]], },
  },
  [9524] = {
    [[Gefangene der Zitadelle]],
    { [[Allianztruppen, hier?! Hört mir genau zu, <class>.]], [[Kargath hat viele unserer Kämpfer gefangen genommen. Unter ihnen ist auch Hauptmann Alina, einer unserer wichtigsten Offiziere. Rettet sie, bevor sie hingerichtet wird.]], [[Macht Euch um mich keine Sorgen, ich komme hier schon heraus, lasst mich nur etwas Luft schnappen.]], },
    { [[Rettet Hauptmann Alina aus der Höllenfeuerzitadelle, bevor sie hingerichtet wird.]], [[Um diese Aufgabe zu beenden, müsst Ihr den Dungeon auf dem Schwierigkeitsgrad 'Heroisch' betreten.]], },
  },
  [9525] = {
    [[Gefangene der Zitadelle]],
    { [[Ich hatte Glück und konnte Kargaths Verlies entkommen, <name>. Die Hinrichtung der meisten Gefangenen wurde bereits angesetzt, unter ihnen befindet sich einer unserer wichtigsten Offiziere.]], [[Hauptmann Knochenbrechers Tod wäre für uns ein schwerer Schlag. Tut alles in Eurer Macht stehende, um ihn vor seinem Schicksal zu bewahren. Ich bin zu schwach, um weiter zu machen...]], },
    { [[Rettet Hauptmann Knochenbrecher aus der Höllenfeuerzitadelle, bevor er hingerichtet wird.]], [[Um diese Aufgabe zu beenden, müsst Ihr den Dungeon auf dem Schwierigkeitsgrad 'Heroisch' betreten.]], },
  },
  [9526] = {
    [[Die Rückeroberung des Dämonenhügels]],
    { [[Kleines Wesen. Im Süden gibt es ein Gebiet im Nachtweisenwald, das seit kurzer Zeit Dämonenhügel genannt wird. Es ist sehr traurig zu sehen, wie die Dämonen auf ihm herumkrabbeln und ihre Verderbnis verbreiten.]], [[Würdet Ihr etwas tun, um dabei zu helfen, das Land zurückzuerobern?]], [[Ich habe ein paar Samen, die ich für eine solche Gelegenheit aufgehoben habe. Bringt sie zum Dämonenhügel und pflanzt sie in fruchtbare Erdhaufen.]], },
    { [[Pflanzt 8 Baumsetzlinge in die fruchtbaren Erdhaufen.]], },
  },
  [9527] = {
    [[Was noch bleibt]],
    { [[Wir lebten viele Jahre in Frieden mit den Kreaturen dieser Insel. Ihr könnt Euch vorstellen, wie überrascht ich war, als sie den Strand stürmten und uns angriffen.]], [[Ich habe mich mit Händen und Füßen gewehrt, wurde aber sofort überwältigt. Als ich auf Grund der zahlreichen Schläge in Ohnmacht fiel, betete ich, dass meine Familie nicht leiden muss.]], [[Ich erwachte und alles war ruhig. Der Sand war rot, vor Blut getränkt - mit ihrem Blut. Man hatte sie vollständig verschlungen.]], [[Ich sehne mich nicht nach Rache, <name>. Ich wünsche mir nur, dass man mir ihre Überreste bringt, damit ich sie ordnungsgemäß bestatten kann.]], },
    { [[Cowlen auf der Azurmythosinsel möchte, dass Ihr ihm die Überreste von Cowlens Familie bringt.]], },
  },
  [9528] = {
    [[Ein Hilferuf]],
    { [[Meine Familie wurde vor einigen Wochen von den Murlocs und Eulkin dieser Insel überfallen. Ich schaffte es zu fliehen, aber ich glaube, dass mein Vater und meine Mutter getötet wurden. Ich weiß nicht, was ich jetzt tun soll, aber ich muss versuchen, zurück zu meinem Zuhause zu gehen - oder was davon übrig ist... Könnt Ihr mich durch den Wald begleiten?]], },
    { [[Begleitet Magwin über die Insel zu ihrem Zuhause.]], },
  },
  [9529] = {
    [[Der Stein]],
    { [[Ich bin mir sicher, dass Euer Wichtel sich als nützlicher und manchmal auch unterhaltsamer Begleiter bewiesen hat. Es ist jetzt an der Zeit, dass Ihr einen mächtigeren Dämonen zu beherrschen lernt: den Leerwandler.]], [[Reist bis zu dem Punkt der Todesschneise, an dem sie vom Elrendar gekreuzt wird. Macht direkt südlich vom Fluss einen der blauen glühenden Steine ausfindig und nehmt ihn. Euer Weg wird sich Euch dort offenbaren.]], },
    { [[Beschafft Euch einen Leerstein von der Todesschneise.]], },
  },
  [9530] = {
    [[Einen Plan aus Hecken]],
    { [[Ich habe eine ganze Zeit nun darüber gegrübelt, aber es stellt sich als wahr heraus.]], [[Es gibt einen Verräter unter uns.]], [[Die Pläne, die von den Goblins zurückgelassen wurden, bestätigen diesen Komplott. Da ich nicht weiß, wer es sein könnte, benötige ich Eure Hilfe, um den Verräter auszumachen. Keinem aus meiner Mannschaft kann vertraut werden.]], [[Für ein gelungenes Unterfangen benötige ich einen ausgehöhlten Stamm und Laub. Sucht in der Nähe der Holzarbeiter nördlich von uns nach dem Baumstamm. Das Laub müsst Ihr selbst auftreiben. Durchforstet den Wald.]], },
    { [[Admiral Odesyus bei Odesyus' Ankerplatz auf der Azurmythosinsel möchte, dass Ihr ihm einen ausgehöhlten Stamm und 5 Laubhaufen bringt.]], },
  },
  [9531] = {
    [[Laubangriff]],
    { [[Der Verräter wird sich den Plänen zufolge mit dem Anführer der Goblins, Geezle, bei einer Bucht auf der westlichen Insel treffen. Ihr müsst zu diesem Treffen gehen, <name>.]], [[Erinnert Ihr Euch an den Stamm und das Laub? Ihr müsst Euch damit verkleiden! Es ist die perfekte Tarnung.]], [[Geht zur Bucht und haltet nach einer Nagaflagge Ausschau. Seht Ihr die Flagge, so wurde ein Treffen arrangiert. Legt die Verkleidung an, wenn Ihr die Flagge seht, und wartet. Der Verräter wird sich früher oder später zeigen. Lasst das Treffen enden und kehrt anschließend mit der Identität des Schufts zu mir zurück.]], },
    { [[Admiral Odesyus bei Odesyus' Ankerplatz auf der Azurmythosinsel möchte, dass Ihr das Treffen beobachtet, das Geezle und der Verräter gemeinsam ausgemacht haben.]], [[Kehrt zu ihm zurück, wenn Ihr ihm die Identität des Verräters mitteilen könnt.]], },
  },
  [9532] = {
    [[Findet Keltus Dunkellaub]],
    { [[<name>, einer unserer Späher nimmt seine Pflichten ein wenig zu ernst. Er bewacht unsere Feinde ohne Unterlass, erstattet uns aber keine Berichte darüber.]], [[Ihr müsst diesen Spion, Keltus Dunkellaub, finden und überzeugen, damit er seinen Bericht aushändigt und so schnell wie möglich zu mir zurückkommt. Ihr findet ihn weit im Südwesten, wo der Elrendar sich teilt und um Goldnebel herum fließt. Er beobachtet die Nachtelfen von An'daroth und hält sich sicher in ihrer Nähe auf.]], },
    { [[Macht Keltus Dunkellaub ausfindig.]], },
  },
  [9533] = {
    [[Eine helfende Hand]],
    { [[Meine Leute haben im Osten des Eschentals mithilfe der Kaldorei eine wichtige Entdeckung gemacht.]], [[Unter der Aufsicht von Architekt Nemos und Verteidiger Vedaar erneuern sie einen alten elfischen Außenposten. Der Wiederaufbau wird nicht leicht werden, <name>, denn sie stehen vielen Herausforderungen und Feinden gegenüber. Ich weiß, dass Architekt Nemos und seine Landsleute in Waldeslied jede Hilfe, die sie bekommen können, zu schätzen wissen, und er bat mich, <jeden Mann, der/jede Frau, die> Interesse hat, zu ihm zu schicken.]], },
    { [[Geht nach Waldeslied im Eschental und sprecht mit Architekt Nemos.]], [[Verteidigerin Palanaar kann Euch ein Reittier anbieten, um schneller dorthin zu gelangen.]], },
  },
  [9534] = {
    [[Zerstört die Legion]],
    { [[Die Überreste der Brennenden Legion sind uns schon viel zu lang ein Dorn im Auge!]], [[Das hat jetzt ein Ende!]], [[Die Dämonen greifen von den Gebieten im Osten, die als Dämonenhügel, Dämonensturz und Dämonenstieg bekannt sind, an.]], [[Geht und zerstört so viele von ihnen, wie Ihr könnt, und erlangt Ruhm für Euch selbst und für die Horde!]], },
    { [[Tötet 15 Dämonen auf dem Dämonenhügel, dem Dämonensturz und dem Dämonenstieg und kehrt dann zu Valusha am Splitterholzposten im Eschental zurück.]], },
  },
  [9535] = {
    [[Teuflische Pläne]],
    { [[Der Dämon hat dieses Pergament fallenlassen, welches überraschenderweise auf Orcisch geschrieben wurde! Darin werden Diathorus und Gorgannon erwähnt, zwei berüchtigte Schreckenslords, die die Dämonen in dieser Gegend anführen.]], [[Die Pläne enthalten genaue Vorbereitungen für einen dämonischen Angriff auf den Splitterholzposten im Nordwesten.]], [[Ihr seid Euch sicher, dass Valusha im Splitterholzposten daran interessiert ist.]], },
    { [[Bringt die teuflischen Pläne zu Valusha im Splitterholzposten im Eschental.]], },
  },
  [9536] = {
    [[Nie wieder!]],
    { [[<name>, es ist Eure Aufgabe dafür zu sorgen, dass dieser Angriff der Brennenden Legion niemals stattfindet! Geht und erschlagt diese Schreckenslords! Ohne sie ist der Rest der Dämonen nur ein Haufen geistloser Untiere.]], [[Ihr findet Gorgannon im Dämonensturz südlich hinter dem Dämonenhügel in der Nähe des schwebenden Speers von Mannoroth.]], [[Diathorus der Sucher lebt irgendwo im Grabhügel auf dem Dämonensturz.]], [[Kehrt mit ihren Waffen als Beweis Eurer Taten zu mir zurück.]], },
    { [[Kill Gorgannon and Diathorus the Seeker, and then return to Velusha at Splintertree Post in Ashenvale.]], },
  },
  [9537] = {
    [[Keine Gnade mit dem Gnom]],
    { [[Wir müssen uns Funks vornehmen!]], [[Geht zum Strand und sucht nach diesem betrügerischen Gnom. Quetscht so viele Informationen wie möglich aus ihm heraus!]], [[Wer weiß, wie viele andere ebenfalls ein faules Spiel treiben, wenn schon ein hochrangiger Beamter der Allianz solch verräterische Taten begangen hat?]], [[Und <name>, wendet Gewalt an, wenn es nicht anders geht...]], },
    { [[Admiral Odesyus bei Odesyus' Ankerplatz möchte, dass Ihr die Mitteilung des Verräters von Ingenieur "Funks" Oberschleif an Euch bringt.]], },
  },
  [9538] = {
    [[Erlernen der Sprache]],
    { [[Ich glaube, ich hab's, <name>!]], [[<Aurren zeigt auf das Totem.>]], [[Alles, was wir über die Sprache der Furbolgs der Tannenruhfeste wissen müssen, ist auf ihren Totems verzeichnet. Wenn wir den grundlegenden Aufbau der Symbole verstehen, können wir die Totems studieren und somit lernen, mit den Furbolgs zu kommunizieren.]], [[Ich habe das Grundmuster der Symbole auf dieses Pergament gezeichnet. Studiert es und untersucht dann das Totem. Es sollte Euch zu dem nächsten Sprachtotem führen.]], [[Sprecht mit Arugoo, wenn Ihr als gelehrter <race> zurückkehrt!]], },
    { [[Lest die Übersetzungsfibel der Tannenruhfeste, die Aurren Euch gegeben hat. Kommuniziert anschließend mit dem Totem von Akida bei der Azurwacht.]], },
  },
  [9539] = {
    [[Totem von Coo]],
    { [[Ihr beginnt das Totem zu entschlüsseln, die komplizierten Begriffe lassen sich kaum aussprechen. Langsam formen sich Worte in Eurem Kopf. Das Totem scheint Euch sagen zu wollen: Geht auf dem nahegelegenen Hügel nach oben, Richtung Nordosten.]], },
    { [[Findet das Totem von Coo, indem Ihr den Hinweisen folgt.]], },
  },
  [9540] = {
    [[Totem von Tikti]],
    { [[Ihr beginnt das Totem zu entschlüsseln, die Begriffe lassen sich bereits etwas leichter aussprechen. Worte formen sich in Eurem Kopf. Das Totem scheint Euch sagen zu wollen: Überquert den Kristallfluss Richtung Osten. Vielleicht liegt Euer Ziel ja auf der anderen Uferseite des Flusses.]], },
    { [[Findet das Totem von Tikti, indem Ihr den Hinweisen folgt.]], },
  },
  [9541] = {
    [[Totem von Yor]],
    { [[Die Symbole des Totems scheinen sich viel schneller entschlüsseln zu lassen. Die Begriffe ergeben rasch einen Sinn. Ihr seid ziemlich sicher, dass das Totem Euch auf etwas den Fluss herab im Süden verweist. Etwas muss auf dem Grund des Flusses selbst liegen.]], },
    { [[Findet das Totem von Yor, indem Ihr den Hinweisen folgt.]], },
  },
  [9542] = {
    [[Totem von Vark]],
    { [[Die Symbole auf dem Totem lassen sich jetzt ganz einfach lesen. Es ist, als wäre die Beschriftung auf dem Totem von Yor in Eurer Muttersprache verfasst worden.]], [[Die Hinweise deuten auf einen anderen Furbolgbau im Westen hin. Ihr entschlüsselt meisterhaft die Sprache auf dem Totem und wisst, dass Euer Ziel das Dorf der Sichelklauen ist. Yor wird Euch dorthin führen, falls Ihr Euch dennoch verlaufen solltet, wisst Ihr, dass Ihr die alte Elfenstraße im Wald nehmen könnt, die Richtung Westen zu Eurem Ziel führt.]], },
    { [[Findet das Totem von Vark, indem Ihr den Hinweisen folgt.]], },
  },
  [9543] = {
    [[Sühne]],
    { [[Unsere Vergangenheit mit den Orcs ist gezeichnet von andauernden Konflikten, <name>. Einst hätten sie uns im Blutrausch beinahe vollständig ausgelöscht und selbst als der dämonische Einfluss von ihnen wich, haben wir uns weiter von den Orcs fern gehalten.]], [[Trotz dieser Vergangenheit... das Letzte, was Sedai gewollt hätte, wäre Rache und Blutvergießen. Sprecht mit unserem Ältesten, Amaan dem Weisen. Ihm wird es gelingen, Licht in diese Ereignisse zu bringen.]], },
    { [[Sprecht mit Amaan dem Weisen beim Tempel von Telhamat auf der Höllenfeuerhalbinsel.]], },
  },
  [9544] = {
    [[Die Prophezeiung von Akida]],
    { [[Die Abbildungen auf dem Totem stellen einen Krieg zwischen dem Furbolgstamm der Tannenruhfeste und dem der Sichelklauen dar. Die Motive zeigen Gräueltaten, die die Sichelklauen den Furbolgs der Tannenruhfeste angetan haben.]], [[Inmitten des Chaos wird eine Prophezeiung offenbart: Ein Held wird kommen - in seinen Adern fließt kein Furbolgblut - er wird den Stamm der Tannenruhfeste erretten.]], [[Tötet die Furbolgs der Sichelklauen im Dorf der Sichelklauen für ihre Schlüssel. Verwendet diese Schlüssel, um gefangene Furbolgs der Tannenruhfeste zu befreien. Sobald Ihr alle befreit habt, kehrt zu Arugoo der Tannenruhfeste zurück.]], },
    { [[Befreit 8 gefangene Furbolgs der Tannenruhfeste aus den Käfigen im Dorf der Sichelklauen. Kehrt anschließend zu Arugoo der Tannenruhfeste bei der Azurwacht zurück.]], },
  },
  [9545] = {
    [[Das Relikt des Sehers]],
    { [[Die Störrischen unter uns verschließen ihren Geist und glauben an die eine Wahrheit. Der Weise jedoch, hat ein offenes Ohr für die Existenz unterschiedlicher Möglichkeiten, die letztlich zur Gegenwart führen, oder von ihr ausgehen.]], [[Bringt dieses Relikt zu dem Ort, an dem Ihr Sedai gefunden habt. Seht nicht mit Euren Augen, <name>, sondern mit Eurem Verstand.]], },
    { [[Amaan der Weise beim Tempel von Telhamat auf der Höllenfeuerhalbinsel möchte, dass Ihr Sedais Leichnam erneut aufsucht, um dort das Relikt des Sehers zu benutzen. Kehrt nach Abschluss der Aufgabe zu ihm zurück.]], },
  },
  [9547] = {
    [[Ruf der Luft]],
    { [[<name>, Scharfseher Nobundo hat nach Euch verlangt. Es scheint für Euch an der Zeit zu sein, die Geheimnisse der Luft - des noch fehlenden Elements - zu erlernen!]], [[Ich bin sehr stolz auf all das, was Ihr bis jetzt erfolgreich gemeistert habt. Ich weiß, dass Euch ein besonderes Schicksal erwartet.]], },
    { [[Sprecht mit Scharfseher Nobundo in der Kristallhalle der Exodar auf der Azurmythosinsel.]], },
  },
  [9548] = {
    [[Gestohlene Ausrüstung]],
    { [[Schhh! Sprecht leise und lenkt keine Aufmerksamkeit auf mich.]], [[Wenn die Murlocs meiner Anwesenheit gewahr werden, ist es mit dem Experiment vorbei! Ich war schon sehr nahe daran, meine Arbeit zu gefährden.]], [[Als ich gestern am Herumkramen war, muss einer von ihnen zu nahe gekommen sein und mein Lager entdeckt haben. Ich kam noch rechtzeitig, um zwei von ihnen eine meiner Ausrüstungskisten fortschleppen zu sehen! Sie muss in einer ihrer Siedlungen hier zu finden sein, aber ich kann es nicht riskieren, meine Deckung aufzugeben. Wollt Ihr mir vielleicht helfen?]], },
    { [[Bringt Kloppers Ausrüstung zurück zu Klopper Knacksklug, der sich in einem angespülten Schildkrötenpanzer versteckt hält.]], },
  },
  [9549] = {
    [[Schwarzschlammartefakte]],
    { [[Meine Beobachtungen und Aufzeichnungen über die Murlocs der Schwarzschlammküste sind recht aufschlussreich, <name>, aber ich habe gerade erst damit angefangen. Ich habe vor, mit Gegenständen, die auf primitivste Weise von den Murlocs der Schwarzschlammküste hergestellt wurden, nach Eisenschmiede zurückzukehren.]], [[Das Problem ist allerdings, dass die Murlocs diese Gegenstände stets bei sich tragen. Ich könnte es nicht ertragen, wenn meine Hände mit ihrem Blut befleckt wären. Wollt Ihr mir vielleicht dabei helfen, Götzen von den Sehern der Schwarzschlammküste und kunstlosen Messer von den Kriegern und Küstenschlägern der Schwarzschlammküste einzusammeln? Ich werde Euch auch nicht fragen, wie Ihr sie Euch beschafft habt.]], },
    { [[Bringt 3 kunstlose Murlocgötzen und 6 kunstlose Murlocmesser zu Klopper Knacksklug, der sich in einem angespülten Schildkrötenpanzer versteckt hält.]], },
  },
  [9550] = {
    [[Wohin führt die Karte?]],
    { [[Die nicht gerade sorgfältig gezeichnete Karte verrät zwar nichts über ihren Verfasser, scheint aber mit zittrigen Händen angefertigt worden zu sein. Krakelige Linien und Symbole beschreiben einen kleinen Pavillon mit eingestürzten Säulen. Er befindet sich im Westen eines Turms, der teilweise unter Wasser steht.]], [[Glaubt man der Karte, so liegen diese Ruinen an der östlichen Küste der Insel. Es ist Euch ein Rätsel, was Ihr dort vorfinden werdet, es scheint für die Erstellung dieser Karte jedoch wichtig genug gewesen zu sein.]], },
    { [[Macht den auf der Karte beschriebenen Pavillon ausfindig und sucht, auf was der Verfasser der Karte hinweisen wollte.]], },
  },
  [9551] = {
    [[Ruf der Luft]],
    { [[<name>, Scharfseher Nobundo hat nach Euch verlangt. Es scheint für Euch an der Zeit zu sein, die Geheimnisse der Luft - des noch fehlenden Elements - zu erlernen!]], [[Ich bin sehr stolz auf all das, was Ihr bis jetzt erfolgreich gemeistert habt. Ich weiß, dass Euch ein besonderes Schicksal erwartet.]], },
    { [[Sprecht mit Scharfseher Nobundo in der Kristallhalle der Exodar auf der Azurmythosinsel.]], },
  },
  [9552] = {
    [[Ruf der Luft]],
    { [[Der mächtige Geist der Luft verlangt nach Euch, <name>, es ist an der Zeit, diesem Ruf zu folgen.]], [[Ich möchte, dass Ihr den Wildwindpfad im Norden hinaufwandert, dem Berg zugewandt, an dem die Exodar zerschellt ist. Auf seinem Gipfel werdet Ihr eine Höhle finden und darin eine meiner am besten ausgebildeten Schülerinnen, Velaada.]], [[Bitte, hört auf ihre Weisheit und befolgt ihren Rat. Wenn Ihr zurückkehrt, werdet Ihr ein größeres Verständnis für das Element der Luft haben.]], },
    { [[Sprecht mit Velaada beim Wildwindgipfel auf der Azurmythosinsel.]], },
  },
  [9553] = {
    [[Ruf der Luft]],
    { [[Die Luft scheint flüchtig zu sein, sie ist jedoch eine alles bedeckende und durchdringende Kraft, die uns umgibt.]], [[Auch wenn Ihr sie nicht sehen könnt, so bestehen die Felsen dieser Höhle doch hauptsächlich aus Luft, das Wasser der Ozeane ist mit ihr gesättigt und ohne sie könnte kein Feuer bestehen. Lasst dies die Elemente niemals hören!]], [[Derjenige, den Ihr wegen dieses Wissens wirklich zu sprechen wünscht, ist ganz in der Nähe. Am nördlichen Ende der Felsklippe erwartet Euch Susurrus. Mit seiner Hilfe werdet Ihr verstehen lernen.]], },
    { [[Sprecht mit Susurrus beim Wildwindgipfel auf der Azurmythosinsel.]], },
  },
  [9554] = {
    [[Ruf der Luft]],
    { [[Während meine Brüder und Schwestern der anderen Elemente die Erfüllung verschiedenster Aufgaben von Euch verlangten, werde ich keine weiteren Anforderungen an Euch stellen. Ich glaube, dass Ihr genug geleistet habt, um für eine 'Wiedergutmachung' für den Unfall, den der Absturz Eures Schiffs verursacht hat, zu sorgen. Ich kann sehen, dass Ihr bereits über die Erfahrenheit verfügt, die Ihr für einen weisen Umgang mit dem Element der Luft benötigt.]], [[Nehmt diesen Teil meiner Selbst zurück zu Scharfseher Nobundo, damit er Euch bei der Erstellung Eures Totems helfen kann. Sobald Ihr bereit seid, werde ich Euch zurück zur Exodar fliegen.]], },
    { [[Kehrt mit dem Luftwirbel zu Scharfseher Nobundo bei der Exodar auf der Azurmythosinsel zurück.]], },
  },
  [9555] = {
    [[Ruf des Feuers]],
    { [[Es freut mich, dass Ihr den schwierigen Pfad des Schamanen gewählt habt. Nur wenige entscheiden sich für diesen Weg, aber ich spüre, dass es für unsere Zukunft von großer Bedeutung sein wird, die Macht des Schamanismus von den Zerschlagenen zu erlernen.]], [[Man nennt sie so, aber einst waren auch sie Draenei. Teuflische Magie hat sie verwandelt und deshalb können sie sich nicht auf die Macht des Lichts berufen. Einige missbilligen sie und den Schamanismus, den sie lehren, aber sie sind nicht bösartig.]], [[Ich möchte, dass Ihr mit Scharfseher Nobundo sprecht. Er ist der weiseste Schamane, den ich kenne, er wird Euch viel beibringen können.]], },
    { [[Sprecht mit Scharfseher Nobundo in der Kristallhalle der Exodar.]], },
  },
  [9556] = {
    [[Dem Sieger...]],
    { [[Ihr habt zweifellos lange und hart um die Verteidigung der Türme gekämpft, <race>. Wir würden Euch gerne etwas im Gegenzug für Euren tapferen Einsatz geben, allerdings müsst Ihr uns zuerst beweisen, dass Ihr die Gewichtigkeit dieser hier stattgefundenen Tragödien auch versteht.]], [[Lauft durch Stratholme und sucht nach einer Phiole mit dem heiligen Wasser. Sobald Ihr sie gefunden habt, reist zum Magierviertel in Sturmwind oder nach Unterstadt... Dort werdet Ihr, insofern Ihr in diesen Landen siegreich wart, auf meinen Kollegen treffen.]], },
    { [[Sucht nach einer Phiole mit heiligem Wasser in Stratholme und bringt sie zu dem Magier von Lordaeron im Magierviertel von Sturmwind oder in Unterstadt, falls Eure Seite in den Östlichen Pestländern erfolgreich war.]], },
  },
  [9557] = {
    [[Entziffern des Buchs]],
    { [[Da Ihr über keinerlei Mittel verfügt, die es Euch ermöglichen würden, den Inhalt des Buchs zu entschlüsseln, versucht Ihr Euch jemanden ins Gedächtnis zu rufen, der Euch dabei helfen könnte, die ursprünglichen Eintragungen zwischen dem Gekritzel des Kartenverfassers herauszulesen.]], [[Ihr erinnert Euch an Anachoret Paetheus' Sammlung verschiedenster Artefakte, die die Draenei während ihrer Reisen entdeckten. Vielleicht können Euch ja seine erfahrenen Augen bei der Entzifferung des Buchs weiterhelfen.]], },
    { [[Bringt das alte Tagebuch zu Anachoret Paetheus bei der Blutwacht.]], },
  },
  [9558] = {
    [[Die Langbarts]],
    { [[Vor nicht allzu langer Zeit hatten wir einige Probleme mit Arakkoabanditen. Sie haben viele unserer Versorgungskarawanen überfallen, weswegen ich ein paar Zwerge angeheuert habe, die sich um sie kümmern sollten.]], [[Es waren Brüder, wobei Gremni Langbart der Älteste von ihnen war. Sie sind losgezogen und nie zurückgekehrt.]], [[Ich mache mir ein wenig Sorgen um sie, versteht Ihr? Diese verrückten Zwerge haben mehr Bier als Essen oder Munition eingepackt! Könntet Ihr vielleicht einmal nach ihnen sehen?]], [[Das Versteck der Arakkoa, der Haal'eshbau, befindet sich von hier aus weit im Westen, hinter der Großen Kluft. Sie müssen irgendwo dort in der Nähe ein Lager aufgeschlagen haben.]], },
    { [[Sucht nach Gremni Langbart nordwestlich des Haal'eshbaus, weit im Westen der Ehrenfeste auf der Höllenfeuerhalbinsel.]], },
  },
  [9559] = {
    [[Tannenruhfeste]],
    { [[Auserwählter, Ihr müsst nach Norden zur Heimstätte meiner Leute reisen - zur Tannenruhfeste. Dort werdet Ihr auf Eure schrecklichste Herausforderung treffen.]], [[Geht jetzt, der Oberhäuptling der Tannenruhfeste erwartet Euch.]], },
    { [[Sprecht mit dem Oberhäuptling bei der Tannenruhfeste.]], },
  },
  [9560] = {
    [[Biester aus der Apokalypse!]],
    { [[Mein Bruder und ich waren im östlich gelegenen Wald beim Jagen, als wir von seltsamen, insektenartigen Biestern angegriffen wurden. Solch fremdartige Geschöpfe hatten wir zuvor noch nie auf der Azurmythosinsel gesehen! Könnten es die Vorboten der angekündigten Apokalypse sein? Der Weltuntergang, den IHR verhindern sollt?]], [[Solltet Ihr wirklich der auserwählte Held sein, so werdet Ihr die Biester der Apokalypse bezwingen... und mir ihre Bälge bringen. Ich kann Euch aus diesem Material eine mächtige Rüstung anfertigen!]], [[Sucht Richtung Osten nach ihnen, abseits der Straße.]], },
    { [[Moordo bei der Tannenruhfeste auf der Azurmythosinsel möchte, dass Ihr ihm 8 Felshetzerbälge bringt.]], },
  },
  [9561] = {
    [[Nolkais Worte]],
    { [[Ich bin mit der Entschlüsselung jetzt fertig, <name>.]], [[<Anachoret Paetheus zeigt auf einen Textabschnitt am Ende seiner Abschrift.>]], [['Der Feind ist ganz nah. Wir haben alle Überlebenden zusammengerufen und uns mit ihnen zu dem östlich gelegenen Tempel geflüchtet. Es ist der letzte Ort auf dieser Insel, den wir noch halten können. Unter den Überlebenden hat sich der Glaube verbreitet, dass dies unsere endgültige Zuflucht sein wird. Ich habe Vorkehrungen getroffen und meine verbliebene Habe im Boden des Tempels vergraben. Ich hoffe, dass sie jemand finden und somit erfahren wird, was hier geschehen ist.']], },
    { [[Findet den in Nolkais Tagebuch beschriebenen Ort und sucht nach seinen seit langem vergrabenen Besitztümern.]], },
  },
  [9562] = {
    [[Murlocs... Warum hier? Warum jetzt?]],
    { [[Es herrschen dunkle Zeiten, <Auserwählter/Auserwählte>. Sogar die Murlocs trampeln uns auf den Füßen herum - im wahrsten Sinne des Wortes. Unglücklicherweise wurde nicht nur mein Stolz verletzt.]], [[Unser Verhältnis zu den Schlickflossenmurlocs war zwar schon immer recht bescheiden, jedoch hätten wir uns niemals vorstellen können, dass sie uns angreifen und von unseren Essensvorräten stehlen würden! Reist Richtung Westen zur Schlickerküste und bringt uns unsere gestohlenen Vorräte wieder!]], [[Und solltet Ihr einen dunkelhäutigen Murloc mit einem Stück weißen Fell zwischen den Schwimmhäuten sehen...]], },
    { [[Gurf bei der Tannenruhfeste auf der Azurmythosinsel möchte, dass Ihr 5 Säcke mit Tannenruhgetreide wiedererlangt.]], },
  },
  [9563] = {
    [[Mirrens Vertrauen erlangen]],
    { [[Ihr wollt also unser Vertrauen erlangen? In diesen Zeiten vertraue ich nicht vielen Leuten.]], [[Vor kurzer Zeit haben wir einen der unseren an die Arakkoa verloren. Das hat mich etwas nervös gemacht.]], [[Ich sag' Euch was, wenn Ihr Eure Loyalität gegenüber der Ehrenfeste beweisen könnt und... mir einen Krug Nethergarde Bitter bringt... ja, das klingt so weit ganz gut... dann biete ich Euch einen Handel an.]], [[Bernie in der Burg Nethergarde hat das Bitter auf Lager.]], },
    { [[Mirren Langbart auf der Höllenfeuerhalbinsel möchte, dass Ihr ihm 1 Nethergarde Bitter bringt. Diese Aufgabe erfordert zum Abschluss auch einen freundlichen Ruf bei der Ehrenfeste.]], },
  },
  [9564] = {
    [[Gurfs Würde]],
    { [[Nehmt dem Murloc das Stück Fell, das Gurf bei seiner Auseinandersetzung mit den Biestern "verloren" hat, ab und bringt es anschließend zu Gurf zurück. Lasst ihn sogleich wissen, dass der entsetzliche Murgurgula vernichtet wurde.]], },
    { [[Bringt Gurfs Würde zu Gurf bei der Tannenruhfeste auf der Azurmythosinsel.]], },
  },
  [9565] = {
    [[Sucht in der Tannenruhfeste]],
    { [[Als Oberhäuptling gilt meine erste Sorge der Ursache, die hinter der Verderbnis innerhalb der Tannenruhfeste steckt.]], [[Warum haben uns die Wildekin - einst unsere Verbündeten - angegriffen? Warum haben sie die Tannenruhfeste besetzt?]], [[Vielleicht finden wir im Innern der Feste ja eine Antwort. Sucht in der Tannenruhfeste nach dem Quell der Verderbnis.]], },
    { [[Sucht in der Tannenruhfeste nach Hinweisen.]], },
  },
  [9566] = {
    [[Blutkristalle]],
    { [[Außerhalb des Wassers sieht der Kristall wie ein Teil der Exodar aus. Allerdings scheint mit diesem Kristall etwas nicht zu stimmen, denn er ist blutrot und löst sich sehr schnell im Wasser des Kellers auf.]], [[Das könnte das ungewöhnlich aggressive Verhalten der Kreaturen auf der Azurmythosinsel erklären. Kehrt mit diesen Informationen zum Oberhäuptling der Tannenruhfeste zurück.]], },
    { [[Sprecht mit dem Oberhäuptling bei der Tannenruhfeste.]], },
  },
  [9567] = {
    [[Kenne deinen Feind]],
    { [[Es tut gut, neue Gesichter hier bei der Blutwacht zu sehen, <class>. Die Blutelfen stellen zwar die derzeit größte Bedrohung für uns dar, jedoch gibt es noch andere finstere Kreaturen, die die Blutmythosinsel zu ihrem Zuhause gemacht haben.]], [[Die Satyrn der Nazzivus beunruhigen uns besonders. Es sind üble, dämonische Kreaturen, die dunkle Magien beherrschen und sich an der Verderbnis nähren, die der Absturz der Exodar verursacht hat.]], [[Wir müssen sie in Schach halten, aber bevor wir handeln, gilt es noch etwas mehr über sie in Erfahrung zu bringen. Geht nach Nazzivian im Westen und versucht so viel wie möglich von ihrem grotesken Monument zu lernen.]], },
    { [[Findet das Monument der Nazzivus in Nazzivian und beschafft Euch eine Glyphe vom Monument der Nazzivus. Kehrt zu Verteidiger Aalesia bei der Blutwacht zurück, sobald Ihr die Glyphe habt.]], },
  },
  [9568] = {
    [[In der Offensive]],
    { [[Es ist schlimmer, als ich dachte, <name>. Wir Draenei wissen nur zu gut, was passiert, wenn man seine Seele an die Brennende Legion im Austausch für das Versprechen von Macht und Unsterblichkeit verkauft.]], [[Die Satyrn auf dieser Insel, die Nazzivus, sind Abkömmlinge der ersten abartigen Kreatur, die sich mit der Legion verbündet hatte. Ich fürchte, dass sie einen Weg finden werden, um die Kraft der Kristalle von der Exodar gegen uns zu verwenden.]], [[Ihr habt gesehen, was sie diesem Land angetan haben. Stellt Euch nur vor, was sie in den Händen der Satyrn anrichten könnten. Geht Richtung Südwesten nach Nazzivian und startet unsere Offensive!]], },
    { [[Verteidigerin Aalesia bei der Blutwacht möchte, dass Ihr 10 Satyrn der Nazzivus, 5 Schurken der Nazzivus und 5 Teufelsanbeter der Nazzivus tötet.]], },
  },
  [9569] = {
    [[Die Bedrohung bekämpfen]],
    { [[Jetzt, da wir bei den Satyrn Eindruck hinterlassen haben, müssen wir ihnen zu verstehen geben, dass wir Übeltäter der Brennenden Legion hier nicht dulden.]], [[Die Draenei sind schon viel zu lange im Schatten der Legion und ihrer Diener geflohen, <name>. Wo immer sie uns auch fanden, sie zeigten niemals Gnade.]], [[Wir dürfen es nicht zulassen, dass die Satyrn den Absturz der Exodar ausnutzen und die verderbten Kristalle als Waffe einsetzen. Geht nach Axxarien im Norden, dem Sitz ihres Anführers. Tötet ihn und nehmt die verderbten Kristalle, die er angesammelt hat, an Euch!]], },
    { [[Tötet 5 Schattenpirscher von Axxarien, 5 Höllenrufer von Axxarien und Zevrax. Sammelt außerdem 5 verderbte Kristalle ein und kehrt zu Verteidigerin Aalesia bei der Blutwacht zurück.]], },
  },
  [9570] = {
    [[Kurken lauert]],
    { [[Nachdem der Kristallsturm nachgelassen hatte, erschien eine mythische Bestie in unserem Dorf. Das Untier, Kurken, das von den einfallenden Wildekin hierher gebracht wurde, legte alles in Trümmer. Zahlreiche Furbolgs der Tannenruhfeste wurden bei der Verteidigung unseres Landes von der Bestie getötet. Beim Versuch, das Untier zu bezwingen, ließen viele weitere ihr Leben.]], [[In meinen Visionen sah ich jemanden, der Kurken besiegte. Diese Person müsst Ihr sein, <name>. Es kann nur einen geben. Betretet die Tannenruhfeste und tötet Kurken. Bringt mir seinen Balg als Beweis für Eure Tat.]], },
    { [[Kurz der Weise bei der Tannenruhfeste auf der Azurmythosinsel möchte, dass Ihr Kurken tötet und dass Ihr ihm Kurkens Balg bringt.]], },
  },
  [9571] = {
    [[Kurkens Balg]],
    { [[Bringt Kurkens Balg zu Moordo, auf der anderen Seite des Dorfes. Er wird daraus ein Rüstungsteil Eurer Wahl anfertigen.]], },
    { [[Bringt Kurkens Balg zu Moordo bei der Tannenruhfeste.]], },
  },
  [9572] = {
    [[Die Bollwerke schwächen]],
    { [[Schnell, wir haben nicht viel Zeit. Thrallmars Existenz steht auf Messers Schneide!]], [[Wir sitzen zwischen der Höllenfeuerzitadelle im Westen und der Brennenden Legion im Osten fest. Da die Legion ihre Aufmerksamkeit auf das Dunkle Portal gerichtet hat, müssen wir die Chance nutzen, um die Höllenorcs in der Zitadelle anzugreifen. Der Schlüssel dazu ist, zunächst die Bollwerke zu durchbrechen!]], [[Geht dorthin, tötet ihre Anführer und bringt einen Beweis für Eure Tat zu Caza'rez gegenüber von mir!]], },
    { [[Tötet den Wachhabenden Gargolmar, Omor den Narbenlosen und Nazan, den Drachen. Bringt Gargolmars Hand, Omors Huf und Nazans Kopf zu Caza'rez in Thrallmar auf der Höllenfeuerhalbinsel.]], },
  },
  [9573] = {
    [[Häuptling Oomooroo]],
    { [[Ich bin der Jüngling Tannenruh, Erbe des Vermächtnisses von Tannenruh. Wie Ihr sehen könnt, haben meine Verteidiger eine Todesfalle für die irren Wildekin aufgestellt, die von der Tannenruhfeste aus angreifen. Wir können sie zwar hier draußen abwehren, allerdings hat es noch keiner meiner Verteidiger geschafft, in die Feste vorzudringen. Wenn es Euch gelingen sollte, dort hinein zu gelangen und ihren Anführer zu töten, würde uns das vielleicht eine Gelegenheit zum Angriff verschaffen!]], [[Findet Häuptling Oomooroo und vernichtet ihn. Tötet auch alle wahnsinnigen Wildekin, die Euch im Innern der Feste in die Quere kommen.]], },
    { [[Der Jüngling Tannenruh bei der Tannenruhfeste auf der Azurmythosinsel möchte, dass Ihr Häuptling Oomooroo und 9 wahnsinnige Wildekin tötet.]], },
  },
  [9574] = {
    [[Opfer der Verderbnis]],
    { [[Unsere Ankunft hat die Blutmythosinsel für alle Zeit verändert. Der Absturz der Exodar hat Trümmer über die gesamte Insel verstreut und ich glaube, dass sich dies langsam negativ auf die Natur hier auswirkt.]], [[Ich muss mir alles erst genauer ansehen, bevor ich meine Schlussfolgerung bestätigen kann. Die Kristalle scheinen die Bäume als erstes beeinträchtigt zu haben, also müssten die Treants am ehesten die Symptome aufweisen. Wenn Ihr mir Proben von der Rinde verderbter Treants bringen würdet, könnte ich mich weiter in meine Studien vertiefen.]], },
    { [[Bringt 6 Stücke kristallisierte Rinde zu Morae bei der Blutwacht.]], },
  },
  [9575] = {
    [[Die Bollwerke schwächen]],
    { [[Schnell, wir haben nicht viel Zeit. Die Existenz der Ehrenfeste steht auf Messers Schneide!]], [[Wir sitzen zwischen der Höllenfeuerzitadelle im Westen und der Brennenden Legion im Osten fest. Da die Legion ihre Aufmerksamkeit auf das Dunkle Portal gerichtet hat, müssen wir die Chance nutzen, um die Höllenorcs in der Zitadelle anzugreifen. Der Schlüssel dazu ist, zunächst die Bollwerke zu durchbrechen!]], [[Geht dorthin, tötet ihre Anführer und bringt einen Beweis für Eure Tat zu Gunny gegenüber von mir!]], },
    { [[Tötet den Wachhabenden Gargolmar, Omor den Narbenlosen und Nazan, den Drachen. Bringt Gargolmars Hand, Omors Huf und Nazans Kopf zu Gunny in der Ehrenfeste auf der Höllenfeuerhalbinsel.]], },
  },
  [9576] = {
    [[Schandflosses Anhänger]],
    { [[Ihr untersucht den sehr simpel angefertigten Anhänger und stellt fest, dass der Kristall ganz sachte pulsiert. Er leuchtet ein wenig, aber Ihr spürt auch, dass irgendetwas Unnatürliches davon ausgeht.]], [[Ihr seid Euch zwar nicht sicher, was Schandflosse zur Anfertigung dieses Anhängers getrieben hat, aber der Fund scheint zu wichtig, als dass er ignoriert werden könnte. Ihr solltet ihn jemandem bei der Blutwacht zeigen.]], },
    { [[Bringt den roten Kristallanhänger zu Morae bei der Blutwacht.]], },
  },
  [9578] = {
    [[Suche nach Galaen]],
    { [[Denkt Ihr oft an Eure Familie, <name>? An Bord der Exodar befanden sich einige Familien, aber ich habe nur von sehr wenigen gehört, die sich nach dem Absturz wiedergefunden haben.]], [[Ich suche zum Beispiel nach Spuren von meinem Mann, Galaen. Er war Techniker im Kryokern. Die Trümmer davon wurden im Westen gefunden, allerdings wimmelt es dort nur so von Blutelfen!]], [[Ich muss wissen, was Galaen zugestoßen ist! Selbst die Nachricht von seinem Tod wäre noch besser als diese Ungewissheit. Mein Mann trägt den gleichen Anhänger wie ich; sie waren ein Geschenk meiner Schwester.]], },
    { [[Sucht nach Moraes Mann Galaen.]], },
  },
  [9579] = {
    [[Galaens Schicksal]],
    { [[Die Informationen von Morae ermöglichen es Euch nicht, den Leichnam eindeutig als den von Galaen zu identifizieren, allerdings scheint man die Kleidung bei der Suche nach Kostbarkeiten zerrissen zu haben.]], [[Die Blutelfen der Umgebung hätten bei den Wrackteilen jeglichen Gegenstand von Wert an sich genommen, einschließlich das mit Juwelen besetzte Amulett von Galaen.]], [[Auch wenn Moraes Mann den Absturz nicht überlebt hat, so wird sie mit Sicherheit dankbar sein, wenn Ihr das Amulett von den Blutelfen wiedererlangt.]], },
    { [[Bringt Galaens Amulett zu Morae bei der Blutwacht.]], },
  },
  [9580] = {
    [[Bärdarfsgüter]],
    { [[Seit Ihr auf der Blutmythosinsel seid, habt Ihr sicherlich nur wenig gesehen, dass sich nicht auf Grund unserer Ankunft verändert hat. Meine Ausbildung und Erfahrung haben mich gelehrt, dass sogar die Erzeugnisse einer derartigen Umgebung noch von Nutzen sein können.]], [[Mit so wenigen Ressourcen müssen wir uns auf unseren Einfallsreichtum verlassen, um versorgt zu sein. Ich habe unsere Läden mit Fleisch ausgestattet, das von Bären stammt, die auf dieser Insel umherstreifen. Wenn man es richtig zubereitet, kann es bedenkenlos verzehrt werden. Solltet Ihr da draußen unterwegs sein, so helft uns doch bitte unsere Vorräte aufzufüllen.]], },
    { [[Bringt 8 Flanken eines alten Braunbären zu Fährtenleser Lyceon bei der Blutwacht.]], },
  },
  [9581] = {
    [[Von den Kristallen lernen]],
    { [[Willkommen bei der Blutwacht, <class>. Meine hauptsächliche Sorge gilt der Verteidigung des Außenpostens, aber unsere Anführer bei der Exodar haben mir auch zahlreiche andere Pflichten überantwortet.]], [[Das Problem ist nur, dass es eigentlich zu unsicher ist, eine große Anzahl von Forschern auszusenden, wenn die Insel so gefährlich ist. Ich brauche jemanden, der für mich eine Reihe von Kristallproben von verschiedenen Stellen der Insel einsammelt.]], [[Die erste Probe muss von den von Felshetzern besetzten Schiffstrümmern kommen, die südlich des Dorfs und auf der anderen Seite der Brücke liegen.]], },
    { [[Benutzt die Kristallspitzhacke, um eine Kristallprobe der Absturzstelle von einem Kristall der Absturzstelle zu nehmen. Bringt die Probe zu Herold Mikolaas bei der Blutwacht.]], },
  },
  [9582] = {
    [[Die Stärke des Einen]],
    { [[Als das Schiff abstürzte, wurde unsere Menagerie an den nordöstlichen Stränden dieser Insel zerstreut. Mir wurde ein Schlüssel für den Käfig eines besonders gefährlichen Exemplars anvertraut, dem Todesfelshetzer. Die Kreatur ist zwar nur leicht gepanzert, widersteht aber fast jeglicher Magie. Man muss sie mit der einfachen Kraft des Schwerts erlegen.]], [[Nehmt diesen Schlüssel und befreit den Felshetzer aus seinem Gefängnis und von seiner Qual. Wir können es nicht zulassen, dass er aus dem Käfig ausbricht und der Umwelt hier noch weiteren Schaden zufügt.]], },
    { [[Öffnet den Felshetzerkäfig und tötet den darin befindlichen Todesfelshetzer. Kehrt dann zu Ruada in der Azurwacht auf der Azurmythosinsel zurück.]], },
  },
  [9584] = {
    [[Die zweite Probe]],
    { [[Da wir nun einen Kristall als Vergleichsgrundlage haben, benötigen wir jetzt Proben von gefährlicheren Gegenden der Insel, um das Set zu vervollständigen, das ich zur Exodar senden möchte.]], [[Die nächste Probe muss von den Kristallen kommen, die von den Blutelfen in der Umgebung des Messerwinkels, nordwestlich der Blutwacht, kontrolliert werden. Berichten zufolge verändern und ernten sie die Kristalle, um sie in einer Art magischer Apparatur zu verwenden. Versucht einen Kristall zu finden, der noch nicht in eines dieser Geräte eingebaut wurde, und entnehmt eine Probe davon.]], },
    { [[Benutzt die Kristallspitzhacke, um eine Veränderte Kristallprobe von einem veränderten Blutmythoskristall zu nehmen. Bringt die Probe zu Herold Mikolaas bei der Blutwacht.]], },
  },
  [9585] = {
    [[Die letzte Probe]],
    { [[Ich benötige nur noch eine letzte Probe, bevor ich das Set mit den Kristallen zur Exodar schicken kann, <name>. Die letzte Probe muss von der Feste der Satyrn im Nordwesten, Axxarien, kommen.]], [[Ich weiß nicht genau, was mir diese Probe aufzeigen wird, aber ich bin mir sicher, dass sie Spuren der Verderbnis enthalten wird. Achtet darauf, dass Ihr eine Probe von den Kristallen entnehmt, die wie die aussehen, von denen Ihr zuvor Proben entnommen habt, ansonsten können unsere Forscher keine korrekten Vergleiche mehr anstellen.]], },
    { [[Benutzt die Kristallspitzhacke, um eine Axxarienkristallprobe von einem Axxarienkristall zu nehmen. Bringt die Probe zu Herold Mikolaas bei der Blutwacht.]], },
  },
  [9586] = {
    [[Helft Tavara]],
    { [[Tavara, eine unserer Kräuterkundigen, ist bei ihrer Suche nach Kräutern viel zu weit gegangen. Ich befürchte sie wurde verletzt und braucht Hilfe, aber unglücklicherweise kann ich mich nicht selbst auf die Suche nach ihr begeben.]], [[Bitte findet und heilt sie, wenn nötig, <class>. Sie verließ die Azurwacht in Richtung Osten und machte sich auf den Weg zu einigen heißen Quellen.]], },
    { [[Guvan bei der Azurwacht möchte, dass Ihr Tavara findet und ihre Wunden heilt.]], },
  },
  [9587] = {
    [[Düstere Neuigkeiten]],
    { [[Die Durchsuchung der leblosen Hülle von Vazruden dem Herold fördert einen zusammengerollten Brief zu Tage. Ein kunstvolles "I" ziert das noch intakte, blutrote Siegel. Ihr brecht das Siegel auf und was Ihr lest, beunruhigt Euch zutiefst.]], [[Sicherlich würde Truppenkommandant Danath Trollbann in der Ehrenfeste gerne einen Blick auf den Brief werfen.]], },
    { [[Bringt den unheilvollen Brief zu Truppenkommandant Danath Trollbann in der Ehrenfeste auf der Höllenfeuerhalbinsel.]], },
  },
  [9588] = {
    [[Düstere Neuigkeiten]],
    { [[Die Durchsuchung der leblosen Hülle von Vazruden dem Herold fördert einen zusammengerollten Brief zu Tage. Ein kunstvolles "I" ziert das noch intakte, blutrote Siegel. Ihr brecht das Siegel auf und was Ihr lest, beunruhigt Euch zutiefst.]], [[Sicherlich würde Nazgrel in Thrallmar gerne einen Blick auf den Brief werfen!]], },
    { [[Bringt den unheilvollen Brief zu Nazgrel in Thrallmar auf der Höllenfeuerhalbinsel.]], },
  },
  [9589] = {
    [[Blut bedeutet Leben]],
    { [[Ich bin mir sicher, dass dieser Brief hier für einigen Aufruhr sorgen wird. Wir glauben, etwas gegen die Erschaffung immer neuer Höllenorcs unternehmen zu können, wenn wir nur mehr über ihren Entstehungsprozess in Erfahrung bringen. Es ist schon schlimm genug, dass wir im Osten gegen die Brennende Legion kämpfen müssen.]], [[Heute ist also Euer Glückstag, <mein Bester/meine Beste>! Ihr bekommt die Chance auf einen Spaziergang im Blutkessel, wie manche den Innenhof der Höllenfeuerzitadelle nennen. Klingt verdammt heiß!]], [[Zieht los, tötet ein paar Höllenorcs und bringt Proben ihres Blutes mit zurück.]], [[Fühlt Ihr Euch der Aufgabe gewachsen?]], },
    { [[Sammelt 10 Phiolen mit Höllenorcblut und bringt sie zu Gunny in der Ehrenfeste auf der Höllenfeuerhalbinsel.]], },
  },
  [9590] = {
    [[Blut bedeutet Leben]],
    { [[Dieser Brief beunruhigt mich zutiefst, und das nicht nur, weil er vom Verräter persönlich verfasst wurde. Wenn wir sie besiegen wollen, ist es von größter Wichtigkeit, herauszufinden, wie sie diese neuen Höllenorcs erschaffen! Schlimm genug, dass wir die Brennende Legion bei unserer östlichen Flanke bekämpfen müssen, jetzt auch noch eine Höllenorcarmee im Westen!]], [[Es liegt jetzt an Euch, den so genannten Blutkessel im Inneren der Höllenfeuerzitadelle zu infiltrieren. Bringt einige Blutproben von den dortigen Höllenorcs mit.]], },
    { [[Sammelt 10 Phiolen mit Höllenorcblut und bringt sie zu Zenturio Caza'rez in Thrallmar auf der Höllenfeuerhalbinsel.]], },
  },
  [9591] = {
    [[Die Zähmung der Bestie]],
    { [[Das Erste und Einzige, was <ein/eine><class> <class> am Anfang lernt, ist das Töten. Ihr scheint nun für den nächsten Schritt bereit zu sein.]], [[Die Furbolgs der Tannenruhfeste haben uns beigebracht, wie man Wildtiere zähmt, ohne monatelang zu studieren. Sie können ein Totem in der Gestalt eines Tiers formen, was sie für kurze Zeit mit einer Kreatur dieser Art verbindet.]], [[Lektion Nummer Eins: Ihr nehmt dieses Totem und wendet es auf einen Stachelkriecher an. Ihr findet dergleichen an der westlichen Küste der Insel.]], },
    { [[Benutzt das Zähmertotem, um einen Stachelkriecher zu bändigen. Kehrt nach gelungenem Unterfangen zu Jägerin Kella Nachtbogen zurück.]], },
  },
  [9592] = {
    [[Die Zähmung der Bestie]],
    { [[Der Kriecher war zweifellos widerborstig, aber der Grünschreiter wird Eure Beharrlichkeit mit Sicherheit auf eine noch härtere Probe stellen. Stärkere Verbündete benötigen mehr Entschlossenheit.]], [[Nehmt dieses Zähmertotem und bändigt damit einen großen Grünschreiter. Ihr findet diese Kreaturen im Norden der Azurmythosinsel.]], },
    { [[Benutzt das Zähmertotem, um einen großen Grünschreiter zu bändigen und kehrt anschließend zu Jägerin Kella Nachtbogen zurück.]], },
  },
  [9593] = {
    [[Die Zähmung der Bestie]],
    { [[Während der Zähmung des Grünschreiters sind Euch sicherlich die großen Katzen, die man Nachtpirscher nennt, aufgefallen. Es sind rasche und tödliche Jäger... und Verbündete.]], [[Nehmt dieses Zähmertotem und bändigt damit einen Nachtpirscher. Geht mit dem Nachtpirscher auf die Jagd und schützt ihn vor Angreifern. Kehrt anschließend zu mir zurück.]], },
    { [[Benutzt das Zähmertotem, um einen Nachtpirscher zu bändigen und kehrt anschließend zu Jägerin Kella Nachtbogen zurück.]], },
  },
  [9594] = {
    [[Zeichen der Legion]],
    { [[Das Rüstungsstück wurde aus einem tiefschwarzen Metall geschmiedet, das Licht zu absorbieren scheint. Es wirkt zwar nicht verzaubert, allerdings kommt es Euch alles andere als normal vor.]], [[Solch eine Rüstung wird nur von einer bestimmten Macht verwendet - der Brennenden Legion. Die Gegenwart der Legion kann nur bedeuten, dass sie vorhaben, die Satyrn der Insel gegen die Draenei aufzuhetzen.]], [[Geht gegen die einheimischen Satyrn vor und zeigt dieses Rüstungsstück Verteidigerin Aalesia bei der Blutwacht.]], },
    { [[Tötet 8 Satyrn der Nazzivus und 8 Teufelsanbeter der Nazzivus. Bringt anschließend Tzeraks Panzerplatte zu Verteidigerin Aalesia bei der Blutwacht.]], },
  },
  [9595] = {
    [[Kontrolle]],
    { [[Ihr habt Euch entschieden, als <class> durchs Leben zu gehen, also müsst Ihr jetzt Kontrolle erlernen. Ungezügelte Magie ist fähig, Seelen zu verderben und Welten zu zerstören.]], [[Um all das noch besser verstehen zu können, müsst Ihr einem Wesen gegenüber treten, das der eigens ausgeübten Magie erlegen ist. Reist zu den Stränden an der nordwestlichen Küste der Azurmythosinsel und setzt Eure Magie ein, um ein paar üble Murlocs zu vernichten. Euer Magiegebrauch wird wahrscheinlich die böse Macht hervorlocken, die sich dort herumtreibt - ein Magiergeist der Quel'dorei. Vernichtet ihn.]], [[Wenn Ihr fertig seid. macht Euch auf die Suche nach Bati in der Halle des Lichts der Exodar.]], },
    { [[Vernichtet die Murlocs der Schlickerküste, um einen Magiergeist der Quel'dorei zu rufen, und vernichtet ihn. Sprecht dann mit Bati in der Halle des Lichts in der Exodar.]], },
  },
  [9598] = {
    [[Erlösung]],
    { [[Ich bin dafür verantwortlich, die Paladine auf den Pfad der größten Tugend zu bringen, sobald sie sich dem Weg des Lichts verschreiben und unser Volk vor den Bedrohungen verteidigen.]], [[Die Zeit ist gekommen, den ersten Schritt hin zu wahrer Tugend zu tun. Wenn Ihr einverstanden seid, gebe ich Euch das Buch der Offenbarung. Wenn Ihr es studiert, erfahrt Ihr etwas über das Licht und was von Euch erwartet wird. Wenn Ihr es versteht und tüchtig seid, werdet Ihr erfolgreich größere Fähigkeiten erlangen.]], },
    { [[Lest das Buch der Offenbarung und sprecht mit Jol.]], },
  },
  [9599] = {
    [[<UNUSED>]],
    { [[Ein Paladin muss in allen Dingen das Licht widerspiegeln, das uns Kraft gibt. Wenn einer unserer Art nach dem Göttlichen strebt, bedeutet das nicht, dass er ein Gott sein möchte... wir trachten lediglich danach, in allem nur Gutes zu tun.]], [[Auch wenn Ihr in diesen schweren Zeiten berufen seid, das Böse zu zerschmettern, dürft Ihr nicht vergessen, dass die Unterstützung anderer Euch wahrhaft von den anderen Bürgern unterscheidet. Mitgefühl, Geduld, Tapferkeit... das alles bedeutet einem Paladin so viel wie Kraft im Kampf.]], [[Bedenket das und vergesst es niemals.]], },
    { [[Sprecht mit Jol in der Exodar.]], },
  },
  [9600] = {
    [[Erlösung]],
    { [[Es wird Eure Aufgabe sein, <name>, unseren neuen Verbündeten, den Furbolgs der Tannenruhfeste, zu helfen.]], [[Einige der jüngeren Schamanen der Tannenruhfeste sind in eine Enklave der Sichelklauen gestolpert und wurden allesamt getötet. Nehmt dieses Symbol des Lebens und erweckt einen der Schamanen wieder zum Leben.]], [[Die Enklave der Sichelklauen liegt östlich der Straße, direkt nachdem man die Brücke überquert, die die Azurmythosinsel und die Blutmythosinsel verbindet. Sprecht mit mir, wenn Ihr diese Aufgabe erfüllt habt.]], },
    { [[Jol möchte, dass Ihr einen jungen Schamanen der Furbolgs mit dem Symbol des Lebens wieder zum Leben erweckt und dass Ihr anschließend wieder zu ihr zurückkehrt.]], },
  },
  [9601] = {
    [[Zum Bollwerk]],
    { [[Der Blutritter Mehlar Dämmerklinge hat ein Treffen mit Euch gefordert, <name>. Ihr findet Mehlar beim Bollwerk, das den Übergang zwischen Tirisfal und den Westlichen Pestländern schützt.]], [[Er ist nicht gerade für seine Geduld bekannt. Ihr solltet ihn also nicht warten lassen.]], },
    { [[Sprecht mit Mehlar Dämmerklinge beim Bollwerk.]], },
  },
  [9602] = {
    [[Unheilvolle Mitteilung]],
    { [[Ihr müsst Euren Leuten diese Informationen überbringen, <name>. Auch wenn Euer mutiger Einsatz die Pläne von Kael'thas und seinem dunklen Meister sicherlich ins Stocken gebracht hat, werden mit Sicherheit auch weiterhin Angriffe verübt werden.]], [[Nehmt die Mitteilung des Verräters zu Euren Anführern. Sie müssen es mit ihren eigenen Augen sehen. Vielleicht verstehen sie einiges von dem Geschriebenen besser.]], },
    { [[Bringt die Mitteilung des Verräters zu Exarch Menelaous bei der Azurwacht.]], },
  },
  [9603] = {
    [[Betten, Verbände und noch viel mehr]],
    { [[Willkommen bei der Azurwacht, <class>. Bitte entschuldigt die Unordnung, die hier überall herrscht. Man betrachtet die einfachsten Dinge oft als selbstverständlich, wie zum Beispiel ein komplett eingerichtetes und ausgestattetes Gasthaus... bis man selbst ein neues aus dem Nichts errichten muss.]], [[Wir konnten dieses Wrackteil bergen und für das Gebäude verwenden, allerdings ist dieses Gasthaus lediglich ein besseres Zelt, ohne die entsprechende Einrichtung und die Notvorräte. Nurguni bei der Exodar hat uns angeboten, ein paar Vorräte mit den im Moment am dringendsten benötigten Dingen zusammenzustellen. Ich brauche jedoch jemanden, der ihm diese Liste hier bringt.]], },
    { [[Bringt Chellans Liste zu Zaldaan, dem Hippogryphenmeister bei der Azurwacht.]], },
  },
  [9604] = {
    [[Auf dem Rücken eines Hippogryphen]],
    { [[Ihr könnt einen meiner Hippogryphen gegen ein kleines Entgelt mieten, um zur Exodar zu fliegen.]], [[Einen Hippogryphen zu nehmen, ist eine schnellere und, was noch wichtiger ist, sichere Reisemöglichkeit für jemanden, der eine wichtige Nachricht zu überbringen hat. Wenn Ihr Euch für einen Hippogryphenritt entschieden habt, sprecht einfach nochmal mit mir und ich kümmere mich um den Rest.]], },
    { [[Erwerbt bei Zaldaan einen Hippogryphenritt zur Exodar. Bringt Chellans Liste nach der Landung zu Nurguni im Sitz der Naaru der Exodar.]], },
  },
  [9605] = {
    [[Hippogryphenmeister Stephanos]],
    { [[Hier ist die Kiste, <name>. Es sollte genug sein, damit Chellan das Gasthaus fertigstellen und eröffnen kann. Richtet ihr bitte aus, dass sie bei steigendem Bedarf jederzeit weitere Vorräte anfordern kann. Die Azurwacht braucht unbedingt einen Ort, an dem Verwundete versorgt und Reisende empfangen werden können.]], [[Sucht Stephanos auf, wenn Ihr zur Azurwacht zurück möchtet, er hält sich am Sitz der Naaru in der Exodar auf.]], },
    { [[Geht zu Stephanos am Sitz der Naaru in der Exodar und erwerbt einen Hippogryphenritt zurück zur Azurwacht.]], },
  },
  [9606] = {
    [[Zurück zu Versorgerin Chellan]],
    { [[Wenn ich mich recht erinnere, dann ist Zaldaan der Hippogryphenmeister bei der Azurwachtwacht. Falls Ihr schon einmal mit ihm gesprochen habt, könnt Ihr einen meiner Hippogryphen zurück zu ihm nehmen.]], [[Ihr solltet Euch etwas Wichtiges merken: Hippogryphen werden stets so ausgebildet, dass sie zu ihrer Hauptstadt zurückfliegen, sie bringen Euch aber nur zu einem entlegenen Hippogryphenmeister, wenn Ihr schon einmal dort gewesen seid.]], [[Da Ihr mit einem von Zaldaans Hippogryphen hier angekommen seid, könnt Ihr diese Route auch wieder zurück zur Azurwacht nehmen und die Vorräte bei der Versorgerin abliefern.]], },
    { [[Sprecht mit Stephanos und erwerbt einen Hippogryphenritt zurück zur Azurwacht. Bringt Nurgunis Vorräte nach Eurer Ankunft zu Versorgerin Chellan.]], },
  },
  [9607] = {
    [[Im Herzen des Hasses]],
    { [[<class>, sucht Euch sofort eine Gruppe und kundschaftet den Blutkessel im Inneren der Höllenfeuerzitadelle aus! Und ich meine heute noch Prinzesschen!]], [[Unsere magische Überwachung besagt, dass dort etwas Unnatürliches vor sich geht. Jedoch werden unsere weiteren arkanen Untersuchungen von einer großen Macht im Inneren abgeblockt. Das sagt schon alles!]], [[Rechnet mit starkem Widerstand; keine andere Truppe, die wir hineingeschickt haben, ist je zurückgekehrt.]], [[Geht hinein, untersucht das gesamte Areal und meldet Euch dann beim Truppenkommandant!]], },
    { [[Untersucht das gesamte Areal des Blutkessels und meldet Euch anschließend bei Truppenkommandant Danath Trollbann in der Ehrenfeste auf der Höllenfeuerhalbinsel.]], },
  },
  [9608] = {
    [[Im Herzen des Hasses]],
    { [[<class>, sucht Euch sofort eine Gruppe und kundschaftet den Blutkessel im Inneren der Höllenfeuerzitadelle aus!]], [[Unsere magische Überwachung besagt, dass dort etwas Unnatürliches vor sich geht. Jedoch werden unsere weiteren arkanen Untersuchungen von einer großen Macht im Inneren abgeblockt, also muss es sehr wichtig sein.]], [[Rechnet mit starkem Widerstand; keine andere Truppe, die wir hineingeschickt haben, ist je zurückgekehrt.]], [[Geht hinein, untersucht das gesamte Areal und meldet Euch dann bei Nazgrel, Thralls Berater.]], },
    { [[Untersucht das gesamte Areal des Blutkessels und meldet Euch anschließend bei Nazgrel in Thrallmar auf der Höllenfeuerhalbinsel.]], },
  },
  [9609] = {
    [[Helft Behüter Biggs]],
    { [[Magtoor hat mir gesagt, dass in der Nähe ein Behüter gestrandet ist. Offensichtlich wurde die Karawane, die er von Dunkelhain ab begleitet hat, von Verirrten angegriffen. Wie es aussieht ist er der einzige Überlebende.]], [[Er ist irgendwo im Süden. Bitte, <class>, helft ihm.]], },
    { [[Sucht Behüter Biggs in den Sümpfen des Elends.]], },
  },
  [9610] = {
    [[Tränenteich]],
    { [[Ich interessiere mich für die Geschichte von Azeroth und frage mich, ob Ihr mir helfen möchtet.]], [[In der Nähe im Sumpf gibt es einen Platz von historischer Bedeutung, zu dem ich Euch gerne schicken möchte. Der Tränenteich liegt im Südosten. Man sagt, dass sich an seinem Ufer und in seinen Tiefen vergrabene Artefakte der Zivilisation der Atal'aitrolle befänden.]], [[Aber seid vorsichtig, der Teich wird von Somnus, einem uralten grünen Drachen, und seinen Drachlingen sowie fleischfressenden Fischen bewacht!]], },
    { [[Obtain 10 Atal'ai Artifacts and return them to Holaaru at the Harborage in the Swamp of Sorrows.]], },
  },
  [9612] = {
    [[Herzlichen Dank!]],
    { [[Ihr habt mein Leben gerettet, <name>! Ich danke Euch von ganzem Herzen! Wärt Ihr nicht gewesen, hätte mich diese Bestie in Fetzen gerissen.]], [[Wenn Ihr den Exarch bei der Azurwacht seht, erzählt ihm von Eurer Tat. Ich bin mir sicher, dass er diese Neuigkeit mit Freuden aufnehmen wird!]], },
    { [[Sprecht mit Exarch Menelaous bei der Azurwacht.]], },
  },
  [9616] = {
    [[Banditen!]],
    { [[Es scheint sich um ein Kopfgeldverzeichnis zu handeln. Ihr könnt die Sprache der Blutelfen zwar nicht verstehen, aber Ihr bemerkt ein Bild, das einen toten Draenei darstellt, einen grinsenden Blutelfen, der sich über dessen Körper beugt, und ein Symbol, das Eurer Meinung nach für eine Art Währung der Blutelfen steht.]], [[Sie haben es auf die Draenei abgesehen! Ihr müsst den Exarch darüber informieren.]], },
    { [[Nehmt das Blutelfendokument zu Exarch Menelaous bei der Azurwacht.]], },
  },
  [9617] = {
    [[Sucht die Weltenwanderer auf]],
    { [[Leutnant Dämmerflucht in der Zuflucht der Weltenwanderer hat eine Audienz mit allen fähigen Jägern gefordert. Es wäre sicher von Vorteil für Euch, wenn Ihr Euch so schnell wie möglich bei Ihr meldet, <name>. Sie ist ein wahrer Quell der Weisheit für Euresgleichen.]], [[Die Zuflucht der Weltenwanderer liegt südlich von Silbermond.]], },
    { [[Sprecht mit Leutnant Dämmerflucht bei der Zuflucht der Weltenwanderer.]], },
  },
  [9618] = {
    [[Überbringen der Berichte]],
    { [[Eure Geschicklichkeit war äußerst beeindruckend. Es besteht kein Zweifel, ich kann Euch die Überbringung dieser Berichte an Zelanis anvertrauen.]], },
    { [[Bringt Keltus Dunkellaubs Berichte zu Zelanis in Silbermond.]], },
  },
  [9619] = {
    [[Beschwörungsrune]],
    { [[Der Leerstein offenbart Euch eine Vision von einem mehrstöckigen Gebäude, das von geisterhaften Figuren behaust wird. Die Szene wird schärfer und verschwimmt dann wieder, andere Gebäude kommen ins Bild, es scheint sich um den Ort Goldnebel zu handeln.]], [[Hausdächer und Baumwipfel schwirren plötzlich vorbei, sie verraten Euch den Weg nach Goldnebel, der den Elrendar entlang nach Westen führt.]], [[Verwendet den Leerstein, sobald Ihr dort angekommen seid, nahe der Beschwörungsrune auf dem Hauptgebäude und bezwingt den heraufbeschworenen Leerwandler. Erst dann könnt Ihr zu Eurem Lehrer zurückkehren.]], },
    { [[Verwendet den Leerstein, um einen Leerwandler in Goldnebel zu beschwören. Bezwingt ihn und kehrt zu Talionia in Silbermond zurück.]], },
  },
  [9620] = {
    [[Das vermisste Vermessungsteam]],
    { [[Als wir uns hier bei der Blutwacht langsam eingerichtet hatten, schickte ich Vermessungsteams aus, die zuverlässigere Karten der Insel erstellen sollten. Meistens kamen sie wieder unversehrt mit den von uns benötigten Angaben zurück, aber das Team, das ich nach Osten zu den Ruinen entsendet hatte, wird noch immer vermisst.]], [[Ich mache mir Sorgen um sie, aber leider kann ich niemanden entbehren, um ein weiteres Team nach ihnen suchen zu lassen. Findet sie, <name>, und helft ihnen soweit es geht, damit sie mit ihren gesammelten Informationen wieder zurückkehren können.]], },
    { [[Herold Mikolaas möchte, dass Ihr das Vermessungsteam findet, das er zu den Ruinen von Loreth'Aran entsendet hatte, und dass Ihr dem Team so gut es geht zur Seite steht.]], },
  },
  [9621] = {
    [[Gesandter zur Horde]],
    { [[Unter unseren neuen möglichen Verbündeten regen sich Zweifel über unsere Fähigkeiten. Wie könnten wir ihnen helfen, wenn sie glauben, dass wir nicht einmal mit unseren Problemen zuhause fertigwerden?]], [[Das ändert alles. Wir lassen keine Zweifel an unserer Stärke zu. Wir werden der Horde als Ebenbürtige beitreten.]], [[Bringt diesen Brief zu Sylvanas, Anführerin der Verlassenen. Sie war schon immer auf unserer Seite, aber die Nachricht von Dar'Khans Tod muss wie Musik in ihren Ohren klingen. Bereitet Euch auf eine lange Reise vor, <name>. Wenn alles gut geht, wird sie Euch nach Orgrimmar schicken.]], },
    { [[Bringt den Brief von Lor'themar Theron zu Fürstin Sylvanas Windläufer in Unterstadt, der Hauptstadt der Verlassenen in Tirisfal.]], },
  },
  [9622] = {
    [[Warnt vor der Gefahr]],
    { [[Die Feste ist jetzt wieder sicher und schon bald werden sich meine Leute auf den Weg zurück zu ihrem Ahnenstammsitz machen. Die Prophezeiung wurde Wirklichkeit und für die Furbolgs der Tannenruhfeste beginnt eine neue Ära des Wohlstands.]], [[Ich befürchte allerdings, dass die Dunkelheit für Eure eigenen Leute eine Bedrohung darstellt. Es gibt noch immer unbeantwortete Fragen - ungesehene Visionen aus der Prophezeiung.]], [[Kehrt zu Euren Anführern zurück und erzählt ihnen von all dem, was Ihr hier erlebt habt.]], [[Solltet Ihr jemals die Hilfe der Tannenruhfeste benötigen, lasst es uns einfach wissen.]], },
    { [[Sprecht mit Exarch Menelaous bei der Azurwacht.]], },
  },
  [9623] = {
    [[Reifer werden]],
    { [[Ihr habt in der Wildnis unserer neuen Heimat überlebt und Euch als etwas Besonderes unter den anderen hervorgetan. Ich glaube, dass Ihr nun reif genug seid, um die zu unterstützen, die an der Front der Blutmythosinsel kämpfen. Die Hand von Argus könnte <einen/eine> wie Euch brauchen.]], [[Meldet Euch bei Torallius dem Gruppenkommandanten. Ihr findet ihn außerhalb der Exodar bei dem Elekk. Gebt ihm diese Befehle, damit er Euch neue Aufträge auf der Blutmythosinsel erteilen kann.]], },
    { [[Exarch Menelaous bei der Azurwacht hat Euch gebeten, die Befehle des Exarchen zu Torallius dem Gruppenkommandanten, der außerhalb der Exodar steht, zu bringen.]], },
  },
  [9624] = {
    [[Lieblingsleckerli]],
    { [[Ich habe eine wundervolle Entdeckung gemacht, <class>. In den noch unberührten Gebieten dieser Insel wächst eine außergewöhnliche Frucht, die Sandbirne genannt wird. Man findet sie am Boden nahe der Bäume.]], [[Ich habe sie zum Kuchenbacken verwendet, aber wenn ich nicht Acht gebe, kommen die Elekk angelaufen und stehlen mir das Obst. Ich hatte genug eingesammelt, um mit den Tieren teilen zu können, aber mein Vorrat ist aufgebraucht und nun herrscht wieder großes Verlangen nach Kuchen. Würdet Ihr mir mit dem Sammeln der Früchte helfen, damit ich eine weitere Charge Kuchen backen kann? Ich werde teilen, ich verspreche es!]], },
    { [[Aonar bei Kessels Wegelager möchte, dass Ihr ihm 10 Sandbirnen bringt.]], },
  },
  [9625] = {
    [[Elekk sind starker Tobak]],
    { [[Ist er verrückt geworden? Wie kann er von mir erwarten, dass ich einen meiner wertvollen Lieblinge einem namenlosen <Jüngling/jungen Ding> wie Euch überlasse!]], [[Lasst Euch etwas gesagt sein, Kindchen: Ihr werdet es nie zu einem von der Hand von Argus bringen! Ihr habt nicht das Zeug dazu!]], [[Ich glaube, dieses Mal muss ich ihm seinen Willen lassen. Sprecht mit Vorkhan bei Kessels Wegelager auf der Blutmythosinsel. Folgt einfach der Straße von hier aus nach Norden und geht einfach nach Norden weiter, wenn sie abzweigt.]], },
    { [[Sprecht mit Vorkhan dem Elekktreiber auf der Blutmythosinsel.]], },
  },
  [9626] = {
    [[Ein Treffen mit dem Kriegshäuptling]],
    { [[Wie Ihr wisst, habe ich nichts von der Liebe zu meinem Heimatland oder seinem Volk verloren. Ich habe mit Krallen und Zähnen gekämpft, damit Silbermond am Verhandlungstisch einen Platz neben Unterstadt und Orgrimmar erhält.]], [[Dies sollte sämtliche Gegenstimmen zum Schweigen bringen. Bringt diesen Brief zum Kriegshäuptling in Orgrimmar. Als Anführer der Horde hat er das letzte Wort bei der Entscheidung über die Bitte Eures Volkes.]], [[Ich habe den Brief als Zeichen meiner persönlichen Bekräftigung mit meinem Siegel versehen. Geht in den Nordwesten der Stadt und steigt auf dem Turm in den Zeppelin nach Durotar.]], },
    { [[Fürstin Sylvanas Windläufer möchte, dass Ihr den Brief mit Sylvanas' Siegel zum Kriegshäuptling nach Orgrimmar, Hauptstadt von Durotar, bringt.]], },
  },
  [9627] = {
    [[Treue gegenüber der Horde]],
    { [[Euer Volk litt unter dem großen Verrat der Allianz. Ihr habt darnassische Angriffe zurückgeschlagen und Spione aus Eisenschmiede ausgeschaltet. Nun gelang es Euch, vor den Toren Eurer Heimat, einen der mächtigsten Anführer der Geißel zu vernichten. Euer Wert steht außer Frage.]], [[Es ist offensichtlich, dass wir einander brauchen. Kehrt zu Lor'themar zurück. Berichtet ihm, dass ich seine Botschaft laut und deutlich vernommen habe.]], [[<Der Kriegshäuptling zerknüllt den Brief in seiner Hand.>]], [[Willkommen in der Horde!]], },
    { [[Kehrt nach Silbermond zurück und erstattet Lor'themar Theron Bericht.]], },
  },
  [9628] = {
    [[Bergung der Daten]],
    { [[Es ist zu spät, um den Kartografen beim Abschluss ihrer Mission zu helfen, aber es scheint, als hätten sie die Angaben gesammelt, nach denen Herold Mikolaas gefragt hat. Der Kristall, der ihre Vermessungsdaten beinhaltet, befand sich nicht unter ihren durchwühlten Habseligkeiten, also muss er den Naga der Hasspranken in die Hände gefallen sein.]], [[Ihr wisst, dass Herold Mikolaas sehr betrübt über den Verlust des Teams sein wird, aber auch, dass er die Vermessungsdaten gerne retten würde.]], },
    { [[Erlangt den Vermessungsdatenkristall von den Hasspranken nahe der toten Kartografen wieder und bringt ihn zu Herold Mikolaas bei der Blutwacht.]], },
  },
  [9629] = {
    [[Kurz geschnappt]],
    { [[Die Murlocs der Schwarzschlammküste an der südwestlichen Küste sind merkwürdige Kreaturen. Oft verhalten sie sich nach keinem richtigen Verhaltensmuster und manchmal legen sie ein erstaunliches Organisationsgeschick an den Tag.]], [[Ich verfolge ein paar Theorien, die belegen könnten, wie sie das zustande bringen. Bis vor Kurzem konnte ich das Ganze jedoch noch nicht untersuchen. Ich habe eine Methode entwickelt, die es mir ermöglichen wird, die für mich interessanten Murlocs im Auge zu behalten, ohne ihnen dadurch längerfristig zu schaden oder ihr natürliches Verhalten zu stören. Wenn Ihr mir bei der Anbringung der Sender behilflich sein wollt, werde ich Euch gerne für Eure Unterstützung bezahlen.]], },
    { [[Morae bei der Blutwacht möchte, dass Ihr 6 Späher der Schwarzschlammküste mit einem Murlocsender 'markiert'.]], },
  },
  [9630] = {
    [[Medivhs Tagebuch]],
    { [[Euer Name spricht sich herum, <name>. Ihr habt es nach Karazhan und wieder heraus geschafft - das ist mehr, als die meisten Sterblichen von sich behaupten können.]], [[Ihr müsst mir einen ganz besonderen Gefallen tun. Im Rahmen unserer Forschungen hat das Violette Auge versucht, an Medivhs Tagebuch zu kommen.]], [[Unglücklicherweise kam keiner der Agenten, die wir hineingeschickt haben, wieder heraus. Wravien war der letzte, den wir geschickt haben. Sucht ihn und erkundigt Euch, ob es neue Anhaltspunkte zu diesem Tagebuch gibt.]], },
    { [[Archmage Alturus at Deadwind Pass wants you go into Karazhan and speak to Wravien.]], },
  },
  [9631] = {
    [[Hilfe unter Kollegen]],
    { [[Es gibt da ein kleines Detail, das ich vergessen habe. Die Überreste von Medivhs Essenz in diesem kleinen Knochenfragment ist völlig ohne Leben, genauso wie Arcanagos'.]], [[Es gibt jedoch einen Weg, sie wieder zum Leben zu erwecken. Und ich weiß auch genau, wer uns dabei helfen kann.]], [[Reist nach Area 52 und zeigt Kalynna Lathred das Knochenfragment. Sie wird wissen, was damit zu tun ist.]], },
    { [[Bringt das verkohlte Knochenfragment zu Kalynna Lathred in Area 52 im Nethersturm.]], },
  },
  [9632] = {
    [[Neue Verbündete]],
    { [[Herold Mikolaas hat bei der Sicherung der Blutwacht äußerst löbliche Arbeit geleistet, <class>, allerdings sind unsere Feinde zahlreich und unsere Ressourcen gering.]], [[Unsere Gegner kennen unsere Schwäche nur zu gut, aber sie haben dabei nicht an Eure Verbindung zu den Streitkräften der Allianz gedacht. Ich habe ein Vorstellungsschreiben für Auberdine vorbereitet, der nächstgelegenen Siedlung der Allianz, in dem ich ihnen eine Zusammenarbeit beim Kampf gegen unsere Feinde vorschlage. Nehmt das Schreiben an Euch und sucht Jägerin Kella Nachtbogen auf, sie befindet sich in der Nähe der Docks westlich der Exodar.]], },
    { [[Take the Letter of Introduction to Huntress Kella Nightbow.]], },
  },
  [9633] = {
    [[Der Weg nach Auberdine]],
    { [[Wenn Ihr am Dock wartet, werdet Ihr das Schiff sehen, das aus Auberdine von der Dunkelküste hier ankommt. Sobald alle Passagiere von Bord gegangen sind, könnt Ihr einsteigen.]], [[Das Schiff wird nach einer kurzen Fahrt in Auberdine anlegen und Ihr werdet Euer Schreiben überbringen können.]], [[Am besten bringt Ihr es zu Thundris Windwirker, er ist ein alter Freund von mir und genießt überall hohes Ansehen.]], },
    { [[Nehmt das Schiff in der Nähe von Jägerin Kella Nachtbogen nach Auberdine. Bringt das Vorstellungsschreiben zu Thundris Windwirker, sobald Ihr in Auberdine angekommen seid.]], },
  },
  [9634] = {
    [[Raubtiere aus einer anderen Welt]],
    { [[Eine Sache, die wir auf unseren Reisen gelernt haben, ist, dass man bei der Einführung von Wildtieren aus einer anderen Welt besonders vorsichtig sein muss. Als die Exodar abstürzte, hatte sie Exemplare verschiedenster Tierarten aus der Scherbenwelt an Bord, einschließlich eines insektenartigen Raubtiers, dem Felshetzer.]], [[Bei dem Absturz starben einige der Tiere, aber viele andere überlebten und suchten in der Wildnis das Weite. Wenn wir sie nicht in Schach halten, werden sie hier auf den Inseln das natürliche Gleichgewicht von Jägern und Gejagten stören.]], [[Ihr findet die Hetzer westlich von Kessels Wegelager, in der Nähe der Küste.]], },
    { [[Tötet 10 junge Blutfelshetzer und kehrt danach zu Vorkhan dem Elekktreiber bei Kessels Wegelager auf der Blutmythosinsel zurück.]], },
  },
  [9635] = {
    [[Der schockdrosselnde Partikelextraktor!]],
    { [[Das Sumpfgas ist wirklich erstaunlich und kann sehr nützlich sein, Mann! Aber zuerst müsst Ihr in der Lage sein, es aufzusaugen, und dazu, mein Freund, braucht Ihr einen schockdrosselnden Partikelextraktor.]], [[Nun, ich könnte Euch einfach die Baupläne geben, aber erst will ich etwas dafür haben. Wie wär's mit ein paar Ersatzteilen? Als ich das letzte Mal nachgeschaut habe, lagen eine Menge davon bei der kaputten Dampfpumpe im Todesmoor herum, ganz im Nordosten.]], [[Ich habe gehört, dass die siechenden Riesen die Teile überall in dem ausgetrockneten See verteilt haben.]], },
    { [[Bringt 15 Dampfpumpenteile zu Mack Dyver in Zabra'jin in den Zangarmarschen.]], },
  },
  [9636] = {
    [[Der schockdrosselnde Partikelextraktor!]],
    { [[Habt Ihr das Sumpfgas gesehen? Ist es nicht einfach klasse?]], [[Man braucht jedoch einen schockdrosselnden Partikelextraktor, um es aufzusaugen. Ich könnte Euch die Pläne dafür geben, aber zuerst brauche ich ganz dringend ein paar Ersatzteile, um ein paar Sachen hier zu reparieren.]], [[Als ich das letzte Mal nachgeschaut habe, lagen eine Menge davon bei der Dampfpumpe im Todesmoor im Nordosten herum. Ich glaube, die siechenden Riesen haben die Pumpe kaputtgemacht.]], [[Wollen wir uns gegenseitig helfen?]], },
    { [[Bringt 15 Dampfpumpenteile zu K. Lee Kleinkram in Telredor in den Zangarmarschen.]], },
  },
  [9637] = {
    [[Kalynnas Bitte]],
    { [[Ich habe die Kirin Tor verlassen, weil ich nach Wissen strebte, das von meinem Orden verboten wurde. Es ist mehr als ironisch, dass ich nun gebeten werde, sie mit genau diesem Wissen zu unterstützen.]], [[Wenn ich Euch diesen Gefallen tun soll, dann müsst Ihr erst etwas für mich tun. Beschafft mir die beiden Folianten, nach denen ich schon so lange suche.]], [[Der erste heißt Dämmerfoliant und gehört dem orcischen Hexenmeister Nethekurse. Der zweite ist als Buch der vergessenen Namen bekannt und gehört einem Arakkoa namens Dunkelwirker Syth.]], },
    { [[Kalynna Lathred möchte, dass Ihr den Dämmerfolianten vom Großhexenmeister Nethekurse in den Zerschmetterten Hallen der Höllenfeuerzitadelle und das Buch der vergessenen Namen von Dunkelwirker Syth in den Sethekkhallen in Auchindoun besorgt.]], [[Ihr müsst diese Quest auf dem Schwierigkeitsgrad 'Heroisch' abschließen.]], },
  },
  [9638] = {
    [[In guten Händen]],
    { [[Hätte ich nach diesem Tagebuch suchen sollen? Das kann nicht sein... Ich erinnere mich nicht daran...]], [[Aber natürlich! Ihr meint Gradav. Er war auf der Suche nach dem Buch, als ich hierher kam. Er hat es bestimmt schon gefunden, warum fragt Ihr ihn nicht einmal?]], [[Ich würde Euch ja helfen... aber diese Bücher sind einfach so... interessant!]], },
    { [[Sprecht mit Gradav in der Bibliothek des Wächters in Karazhan.]], },
  },
  [9639] = {
    [[Kamsis]],
    { [[Tagebuch? Welches Tagebuch? Ihr meint sicher Kamsis!]], [[Geht und fragt sie danach. Ich kann mein Projekt jetzt nicht allein lassen, sonst geht meine ganze Arbeit verloren!]], },
    { [[Sprecht mit Kamsis in der Bibliothek des Wächters in Karazhan.]], },
  },
  [9640] = {
    [[Arans Schemen]],
    { [[Lasst mich kurz nachdenken. Das Violette Auge... Medivhs Tagebuch... Oh ja, jetzt erinnere ich mich.]], [[Ich glaube, ich hatte das Tagebuch, nach dem Ihr sucht, bevor ich anfing, diese Schriftrollen zu lesen...]], [[Aran... genau, Aran hat mich darum gebeten. Und das auch noch so nett. Ich musste es ihm einfach geben. Schließlich ist er Medivhs Vater, wusstet Ihr das?]], },
    { [[Beschafft Medivhs Tagebuch und kehrt zu Kamsis in der Bibliothek des Wächters in Karazhan zurück.]], },
  },
  [9641] = {
    [[Bestrahlte Kristallsplitter]],
    { [[Der Absturz der Exodar hat auf der Blutmythosinsel überall Stücke schädlicher Kristalle verstreut, die das Wasser und den Rest des Landes verderben.]], [[Wir haben sogar schon Stücke der Kristalle im Fleisch von Tieren gefunden, die uns normalerweise als Nahrungsquelle dienen. Ich habe einen Weg gefunden, wie man die Splitter reinigen und somit in zweckmäßige Kristalle verwandeln kann. Wenn Ihr mir genug Splitter besorgt, werde ich Euch einen vollständigen Kristall im Tausch dafür geben. Bislang konnte ich die Splitter in den Körpern von Bären, Felshetzern, Faltern und Treants finden, die den verderbten Teil der Insel bewohnen.]], },
    { [[Verteidiger Boros bei der Blutwacht möchte, dass Ihr ihm 10 bestrahlte Kristallsplitter bringt.]], },
  },
  [9642] = {
    [[Mehr bestrahlte Kristallsplitter]],
  },
  [9643] = {
    [[Würgeranken]],
    { [[Ein guter Förster findet für jede der vorhandenen Ressourcen eine Verwendung, <class>. Auch die skurrilsten Kreaturen besitzen Eigenschaften, die wir zu unserem Vorteil nutzen können.]], [[Da die Blutwacht ständig von den Sonnenfalken angegriffen wird, suchen wir nach einer Vielzahl natürlicher Verteidigungsmechanismen, die auch als Fallen dienen können. Die dornigen Ranken der Peitscher eignen sich perfekt für den Bau von Fallen, die meiner Vorstellung entsprechen. Die Kombination von scharfen Dornen und tödlichem Gift wird unachtsame Angreifer ausschalten. Bringt mir Ranken von mutierten Würgern, damit ich mit der Arbeit beginnen kann.]], },
    { [[Fährtenleser Lyceon bei der Blutwacht möchte, dass Ihr ihm 6 dornige Würgeranken bringt.]], },
  },
  [9644] = {
    [[Schrecken der Nacht]],
    { [[Our preparations are complete.  I have put the materials you gathered into this urn.  Take it to the Master's Terrace, where the fight took place, and set it on fire.]], [[The energy released will bring life back to the charred remains of Arcanagos.  Defeat the creature and retrieve its essence immediately!  You will need to take it to Alturus.]], [[Should you fail, the stars predict you will have let a great terror loose upon the skies.]], [[A terror known as Nightbane!]], },
    { [[Go to the Master's Terrace in Karazhan and use Kalynna's Urn to summon Nightbane.  Retrieve the Faint Arcane Essence from Nightbane's corpse and bring it to Archmage Alturus.]], },
  },
  [9645] = {
    [[Die Terrasse des Meisters]],
    { [[Tagebuch? Welches Tagebuch?]], [[Oh, richtig. Dieses Tagebuch! Das Violette Auge hat mich deswegen hierher geschickt. Ich erinnere mich.]], [[<Kamsis zeigt auf eine Seite im Tagebuch>]], [[Dies ist die Seite, an der Alturus interessiert ist. Wusstet Ihr, dass andere Leute Tagebücher benutzen, um ihre Erinnerungen aufzuzeichnen, während Medivhs Tagebuch genau seine Erinnerungen sind?]], [[Probiert es aus. Geht zur Terrasse des Meisters und lest die Seite. Ihr werdet sehen, was ich meine. Wenn Ihr schon da seid, könnt Ihr auch gleich Alturus Bericht erstatten. Ich fürchte, ich bin hier noch eine Weile beschäftigt.]], },
    { [[Geht zur Terrasse des Meisters in Karazhan und lest Medivhs Tagebuch.]], },
  },
  [9646] = {
    [[GESUCHT: Todespranke]],
    { [[Auf Anweisung von Herold Mikolaas: Man wird demjenigen ein Kopfgeld von 18 Silber auszahlen, der den Beweis für den Tod des räuberischen Bären Todespranke erbringen kann.]], [[Todespranke wird als besonders gefährlich eingestuft und ist für den Tod eines Vermessungsteams an der Küste im Nordwesten der Blutwacht verantwortlich. Reisende werden angehalten, diese Gegend zu meiden.]], },
    { [[Bringt Todesprankes Tatze zu Herold Mikolaas bei der Blutwacht.]], },
  },
  [9647] = {
    [[Ausmerzen der Falter]],
    { [[Als wir den Faltern das erste Mal auf der Insel begegneten, zeigten sie nur wenige Anzeichen von Mutation. Als sich die Verderbnis immer weiter ausbreitete, wurden die Motten und andere Arten jedoch immer mehr verseucht.]], [[Es ist jetzt sogar soweit gekommen, dass sie fast alles, mit dem sie in Berührung kommen, beschädigen. Der Schaden wird von den Kristallsplittern, die beim Absturz der Exodar zerbrochen wurden, noch verstärkt. Uns bleibt keine andere Wahl, aber wir müssen die Motten, die das Land nördlich der Blutwacht behausen, ausmerzen.]], },
    { [[Fährtenleser Lyceon bei der Blutwacht möchte, dass Ihr 10 königsblaue Falter tötet.]], },
  },
  [9648] = {
    [[Pilzsammlung von Mac'Aree]],
    { [[Habt Ihr gewusst, dass das Sammeln von Pilzen gefährlicher als der Verzehr von Pilzen ist? Pilzsammler begeben sich ins Unbekannte - dazu kommen noch die Gefahren, die in der Wildnis lauern!]], [[Was wäre das Leben ohne ein paar Wagnisse? Ich benötige Pilze für meine Sammlung. In dieser unerforschten Natur werdet Ihr vier Arten finden: Wasserstinkmorchel, Blutpilz, ruinöser Birkensporling und Teufelszapfenfungus. Sie sind einzigartig in unterschiedlichen Gebieten der Blutmythosinsel. Bringt sie mir.]], },
    { [[Jessera von Mac'Aree bei der Blutwacht möchte 1 Wasserstinkmorchel, 1 Blutpilz, 1 ruinösen Birkensporling und 1 Teufelszapfenfungus.]], },
  },
  [9649] = {
    [[Yseras Tränen]],
    { [[Gut, Euch wiederzusehen, <name>! Ihr wart bei meiner letzten Aufgabe sehr tüchtig. Seid Ihr vielleicht an einer weiteren interessiert?]], [[Im Nordosten liegt die Insel Drachenfels, sie beheimatet eine sehr seltene Pilzart, die unter dem Namen Yseras Träne bekannt ist - benannt nach den grünen Drachlingen, die auf dieser Insel hausen. Ich brauche nicht viele von diesen Pilzen, möchte Euch aber dennoch warnen, denn die Bewohner dieser Insel mögen keine Besucher. Gebt auf Eure Schritte Acht.]], },
    { [[Jessera von Mac'Aree bei der Blutwacht möchte 2 Yseras Tränen.]], },
  },
  [9663] = {
    [[Der Korsallauf]],
    { [[Ihr kommt gerade richtig, <name>. Die Aktivitäten der Blutelfen haben sich in der letzten Woche fast verdoppelt. Unsere Agenten haben uns mitgeteilt, dass die Blutelfen, die sich selbst Sonnenfalken nennen, einen baldigen Angriff auf die Azurmythosinsel planen. Ihr müsst unseren Verbündeten und Landsmännern eine Warnung zukommen lassen.]], [[Warnt den Oberhäuptling bei der Tannenruhfeste, Exarch Menelaous bei der Azurwacht und Admiral Odesyus bei Odesyus' Ankerplatz.]], [[Die Zeit drängt, deshalb werde ich Euch meinen Elekk zur Verfügung stellen. Beeilt Euch!]], },
    { [[Kessel bei Kessels Wegelager möchte, dass Ihr den Oberhäuptling Tannenruh bei der Tannenruhfeste, Exarch Menelaous bei der Azurwacht und Admiral Odesyus bei Odesyus' Ankerplatz vor der unmittelbar bevorstehenden Blutelfeninvasion warnt. Ihr habt 15 Minuten. Kehrt anschließend zu Kessel zurück. Denkt daran, dass Elekk wasserscheu sind!]], },
  },
  [9664] = {
    [[Mehr Befestigungen]],
    { [[Willkommen in den Pestländern, <name>. Wie Ihr wisst, behaupten sich die Verlassenen und ihre Verbündeten entgegen all unserer Anstrengungen in den von hier aus westlich gelegenen Ländern. Die Kommandanten der Allianz haben beschlossen, dass hier im Osten neue Befestigungen errichtet werden sollen, um die Schlinge um unseren Feind noch etwas enger zu schnüren.]], [[Lange seit den Tagen Lordaerons verlassene und herrenlose Türme müssen für diesen Zweck erobert und wieder aufgebaut werden. Diese Länder hier sind gefährlich, wir brauchen Eure Hilfe.]], },
    { [[Erobert den Turm der Kronenwache, den Ostwallturm, den Nordpassturm und den Pestwaldturm in den Östlichen Pestländern. Kehrt anschließend zum Abgesandten Weißbart bei der Kapelle des Hoffnungsvollen Lichts zurück.]], },
  },
  [9665] = {
    [[Verstärkung der Verteidigung]],
    { [[Agenten der Verlassenen sind hinter das Vorhaben der Allianz gekommen. Sie wollen neue Stützpunkte bei verlassenen Türmen hier in den Östlichen Pestländern einrichten.]], [[Wir dürfen auf keinen Fall zulassen, dass sie sich an unserer östlichen Grenze dauerhaft festsetzen, wir müssen den Einfall der Allianz abwehren. Wir beabsichtigen sie aus jeglichen Türmen, die sie bereits besetzt haben, zu vertreiben. Dann gilt es diese Türme zu verteidigen und weitere Eroberungsversuche abzuwehren.]], },
    { [[Erobert den Turm der Kronenwache, den Ostwallturm, den Nordpassturm und den Pestwaldturm in den Östlichen Pestländern. Kehrt anschließend zum Abgesandten Gormok bei der Kapelle des Hoffnungsvollen Lichts zurück.]], },
  },
  [9666] = {
    [[Demonstration der Macht]],
    { [[Habt Ihr die Schlangenwesen gesehen? Die, die sich selbst Naga nennen?]], [[<Kessel spuckt.>]], [[Sie haben es vor drei Nächten gewagt, im Schutze der Dunkelheit bis zu diesem Lager vorzudringen. Denkt nur an die Dreistigkeit dieser primitiven Biester!]], [[Ihr müsst ihrem Anführer eine Botschaft überbringen, <name>. Im Nordosten liegt die Hassprankenzuflucht, der Sitz von Lord Xiz. Findet ihn, tötet ihn an Ort und Stelle und treibt diesen Banner durch seinen Körper.]], [[Lasst sie vor Furcht erschaudern, wann immer sie diese Standarte sehen!]], },
    { [[Kessel bei Kessels Wegelager auf der Blutmythosinsel möchte, dass Ihr Lord Xiz bei der Hassprankenzuflucht tötet und das Banner der Draenei durch seinen Körper stoßt.]], },
  },
  [9667] = {
    [[Rettet die Prinzessin der Tannenruhfeste]],
    { [[Helft mir, <name>! Die Sichelklauen haben mich gefangen genommen und wollen mich ihren üblen Göttern opfern!]], [[Der Oberhäuptling der Sichelklauen ist in Besitz der Schlüssel für den Käfig. Damit er auftaucht, müsst Ihr die bösartigen Furbolgs der Sichelklauen töten, die in der Enklave hausen. Sie liegt zwei Lager weiter im Westen. Wenn Ihr genug von ihnen niedergestreckt habt, wird er kommen, um sich zu rächen! Tötet ihn, sobald er sich zeigt, und nehmt die Schlüssel an Euch!]], [[Mein Vater wird Euch reichlich entlohnen, wenn Ihr es schafft mich zu rettet!]], },
    { [[Befreit die Prinzessin der Tannenruhfeste aus ihrem Käfig und sprecht anschließend mit Botschafter Frasaboo der Tannenruhfeste bei der Blutwacht.]], [[Ihr müsst den Oberhäuptling der Sichelklauen töten, um an den Schlüssel für den Käfig zu gelangen.]], },
  },
  [9668] = {
    [[Erstattet Exarch Admetius Bericht]],
    { [[Ein Soldat der Hand von Argus ist vielerlei: gehorsam, mutig, intelligent und vor allem ehrenhaft. Vielleicht verfügt Ihr schon jetzt über einige dieser Züge, <class>.]], [[Erstattet Exarch Admetius bei der Blutwacht Bericht, wenn Ihr der Hand noch immer beitreten möchtet. Er und seine Untergebenen werden Euch weiter auf dem Weg zum Draeneisoldaten ausbilden.]], [[Und vergesst dies nicht, <name>: Ehrenhafte Personen handeln nicht immer ehrenhaft. Manchmal ist es ihnen nicht möglich.]], [[Ihr findet die Blutwacht ganz einfach diese Straße entlang, in Richtung Norden.]], },
    { [[Sprecht mit Exarch Admetius bei der Blutwacht.]], },
  },
  [9669] = {
    [[Forschungsreise in den Tod]],
    { [[Vor etwa einer Woche war ich mit meinem Forschungsteam auf einem Erkundungstrip im Westen der Blutmythosinsel, als wir plötzlich von blutdurstigen Spinnen angegriffen wurden. Die Auswirkungen der Kristallverseuchung waren unverkennbar, da diese Spinnen extrem groß und aggressiv waren.]], [[Ich schaffte es zu fliehen, aber mein Team hatte nicht so viel Glück. Wir dürfen nicht zulassen, dass andere das gleiche Schicksal ereilt. Geht nach Westen zum Goldweberpass und vernichtet die Spinnen und ihre Matriarchin Zarakh.]], },
    { [[Tötet 8 Mythosspinner, 8 Mythosfänger und ihre Anführerin Zarakh. Kehrt anschließend zu Achelus zurück.]], },
  },
  [9670] = {
    [[Sie leben! Vielleicht...]],
    { [[Es war ein blutiges Gemetzel, <name>. Wir wurden von einer ganzen Horde dieser Spinnen aus dem Hinterhalt überrascht. Ihre Anführerin fiel unglaublich schnell über uns her und spann uns in Kokons ein.]], [[Nachdem wir eingesponnen waren, begann sie neben uns Eier zu legen. Wir sollten als Futter für ihre Jungen dienen! FUTTER!]], [[Ich schaffte es, mich aus dem Kokon freizuschneiden und floh. Ich möchte die anderen aber nicht zurücklassen. Allerdings konnte ich mich dem Pass bis jetzt nicht erfolgreich nähern. Würdet Ihr mir helfen?]], [[Zerstört diese Kokons! Hoffentlich sind noch ein paar Mitglieder unseres Teams am Leben!]], },
    { [[Forscher Cornelius hat Euch darum gebeten, 5 Expeditionsforscher zu befreien.]], },
  },
  [9671] = {
    [[Dringende Nachricht]],
  },
  [9672] = {
    [[Das Vermächtnis des Blutfluchs]],
    { [[Getreuer Freund,]], [[meine Hand zittert beim Schreiben dieses Briefs. Ich hoffe, dass er Euch rechtzeitig erreicht.]], [[Letzte Nacht erschien mir Edward Hanes, Kapitän des Schiffs VERLORENE HOFFNUNG, in meinem Zelt. Derselbe Edward Hanes, der vor 20 Jahren an Skorbut gestorben ist.]], [[Die Erscheinung erzählte mir eine Geschichte, die mein Herz erschütterte. Eine Geschichte, die Ihr hören müsst, und eine Aufgabe, die Ihr erfüllen sollt. Seid Ihr dabei? Werdet Ihr nach Edward auf der Insel Drachenfels, die nordöstlich von Eurer Basis liegt, suchen?]], [[Gruß,]], [[Admiral Odesyus]], },
    { [[Admiral Odesyus hat Euch gebeten, nach Kapitän Edward Hanes zu suchen und mit ihm zu sprechen.]], },
  },
  [9673] = {
    [[Bestienausbildung]],
    { [[Nachdem Ihr nun alles über die Zähmung eines Tieres wisst, ist es an der Zeit, sich mit den Feinheiten der Pflege und Ausbildung Eures Begleiters vertraut zu machen. Sucht dazu Halthenis den Tierausbilder von Silbermond auf, er wird Euch alles Nötige beibringen.]], [[Er respektiert meine Schüler in höchstem Maße, solange Ihr Euch ihm gegenüber genauso respektvoll verhaltet. Enttäuscht mich also nicht.]], },
    { [[Sprecht mit Halthenis in Silbermond.]], },
  },
  [9674] = {
    [[Die Naga des Blutfluchs]],
    { [[Man sagt, dass dieses Land am lautesten geschrien hat, als die Welt geteilt wurde. Hier fielen tausende von Nachtelfen und an ihrer Stelle erhoben sich fürchterliche Kreaturen des Hasses - die Naga.]], [[Ihrer wahnsinnigen Königin, Azshara, gehorsam ergeben, würden sie alles und jeden zerstören, der sich durch ihre Gewässer wagt. Es würde noch für tausende von Jahren so bleiben - bis zum heutigen Tag.]], [[Zuerst gilt es die Naga des Blutfluchs aus dem Riff zu entfernen.]], },
    { [[Kapitän Edward Hanes auf der Insel Drachenfels möchte, dass Ihr 10 Naga des Blutfluchs tötet.]], },
  },
  [9675] = {
    [[Bestienausbildung]],
    { [[Oh, da wäre noch etwas! Ihr solltet mit Ganaar bei der Exodar sprechen. Er wird Euch das letzte bisschen Wissen bezüglich der Pflege und Ausbildung Eures Begleiters lehren.]], [[Ihr findet ihn in der südwestlichen Ecke der Händlertreppe.]], },
    { [[Sprecht mit Ganaar bei der Händlertreppe der Exodar.]], },
  },
  [9676] = {
    [[Ausbildung zum Paladin]],
    { [[Mit dem Voranschreiten in Eurer Klasse, möchtet Ihr sicherlich von der Ausbildung profitieren, die Euch der Lehrer für Eure Klasse anbieten kann. Jesthenis Sonnensturm ist der Lehrer für junge Paladine hier auf der Insel der Sonnenwanderer. Sprecht mit Jesthenis und seht, welche Ausbildung er Euch anbieten kann. Es wird Euch etwas kosten, bringt also ein wenig Geld mit Euch.]], [[Euer Lehrer - wie alle anderen Lehrer auf der Insel der Sonnenwanderer auch - befindet sich im unteren Teil des Sonnenturms.]], },
    { [[Sprecht mit Jesthenis Sonnensturm - dem Paladinlehrer - im Sonnenturm auf der Insel der Sonnenwanderer.]], },
  },
  [9677] = {
    [[Aufforderung des Ritterfürsten Heldenblut]],
    { [[<name>, Ihr habt Euch in Eurer frühen Ausbildung bereits selbst übertroffen und von den anderen, die Blutritter werden möchten, hervorgehoben. Ihr seid reif für die nächste Ausbildungsphase und dazu kommt, dass Ritterfürst Heldenblut höchstpersönlich nach Euch verlangt hat.]], [[Ich zweifle nicht daran, dass Ritterfürst Heldenblut Eure Fähigkeiten und Eure Ausbildung einer Prüfung unterziehen möchte. Ihr findet ihn beim Hauptsitz der Blutritter in Silbermond beim Platz der Weltenwanderer. Alles Gute, <name>.]], },
    { [[Sprecht mit Ritterfürst Heldenblut beim Platz der Weltenwanderer in Silbermond.]], },
  },
  [9678] = {
    [[Die erste Prüfung]],
    { [[Ihr habt viel über das Kämpfen und Vergießen von Blut gelernt, Initiand, doch in unserem Orden geht es um viel mehr.]], [[Die Beherrschung des Lichts erfordert einen klaren Geist und enorme Willenskraft.]], [[Ohne diese Eigenschaften seid Ihr nicht mehr wert als ein räudiger Streuner.]], [[An den nordöstlichen Ausläufern der Geisterlande gibt es eine Insel, die Insel des Martyriums, auf der wir diese Eigenschaften formen. Versteckt in einer kleinen Höhle, steht eine Kohlenpfanne. Kniet vor ihr, entzündet sie und konzentriert Euch auf das, was Ihr vor Euren Augen seht.]], },
    { [[Reist zu der Insel, die Euch Ritterfürst Heldenblut beschrieben hat, sucht die Höhle und entzündet die Kohlenpfanne. Kehrt, nachdem Ihr die erste Prüfung bestanden habt, zu Ritterfürst Heldenblut in Silbermond zurück.]], },
  },
  [9680] = {
    [[Die Vergangenheit aufwühlen]],
    { [[Kamsis hatte Recht! Diese Information ist äußerst wichtig.]], [[Wenn Medivh seine eigene Essenz in die Magie, die er benutzt hat, um den Drachen zu besiegen, hineingewebt hat, dann ist es ziemlich wahrscheinlich, dass wir einen schwachen Rest dieser Essenz aus den Überresten des Drachen gewinnen können. Welch großartige Erkenntnisse mögen wohl unter der Asche des Gebirgspass der Totenwinde liegen, die nur darauf warten, entdeckt zu werden.]], [[Geht zu den Bergen südlich von Karazhan und bringt mir ein Knochenfragment deutlich drachischen Ursprungs.]], },
    { [[Erzmagier Alturus möchte, dass Ihr zu den Bergen südlich von Karazhan im Gebirgspass der Totenwinde geht und ein verkohltes Knochenfragment besorgt.]], },
  },
  [9681] = {
    [[Eine Studie der Macht]],
    { [[Haltet mich nicht für übermäßig grausam, <name>. Stillstreich hat sehr wohl gewusst, worauf er sich einlässt. Er verstand und akzeptierte seine Pflicht und wurde so ein leuchtendes Vorbild für alle aufstrebenden Blutritter.]], [[Meine Worte sind nicht als Grabesrede gedacht, <name>. Stillstreichs Dienste für uns gehören noch lange nicht der Vergangenheit an und Ihr werdet das Instrument seiner Wiederauferstehung sein.]], [[Eure Ausbildung in der Kunst des Ordens hat gerade erst begonnen. Angesichts Kael'thas' Verrat und des Verlusts unserer Energiequelle, müsst Ihr neue Wege zur Beherrschung des Lichts erlernen. Sucht Magister Astalor Blutschwur auf.]], },
    { [[Sprecht mit Magister Astalor Blutschwur in der Geheimen Kammer unter dem Hauptquartier der Blutelfen.]], },
  },
  [9682] = {
    [[Die Hoffnungslosen...]],
    { [[An einem Vorsprung des Riffs, noch tiefer im Wasser als die Naga des Blutfluchs, werdet Ihr die Hoffnungslosen finden: Reisende des Blutfluchs, Geister derer, die vom Blutfluch heimgesucht wurden.]], [[Es ist bedauerlich, was ich jetzt von Euch verlangen werde... aber sie müssen vernichtet und ihre Hüllen des Blutfluchs zu mir gebracht werden.]], [[Ihre Seelen müssen erlöst werden. Kehrt zu mir zurück, wenn Ihr dergleichen getan habt und ich werde Euch eine letzte Aufgabe stellen.]], },
    { [[Kapitän Edward Hanes auf der Insel Drachenfels möchte, dass Ihr 4 Seelen des Blutfluchs zu ihm bringt.]], },
  },
  [9683] = {
    [[Beendigung des Blutfluchs]],
    { [[Im Süden findet Ihr die Insel des Blutfluchs. Oben auf der Insel befindet sich ein Schrein, der der von den Naga geliebten Königin, Azshara, gewidmet wurde. Zerstört die Statue - verbrennt das Abbild! Dies wird mit Sicherheit den Zorn von Anführer Atoph des Blutfluchs erwecken.]], [[Tötet Atoph und kehrt zu mir zurück!]], },
    { [[Kapitän Edward Hanes auf der Insel Drachenfels möchte, dass Ihr die Statue der Königin Azshara zerstört und Atoph des Blutfluchs tötet.]], },
  },
  [9684] = {
    [[Die Beherrschung des Lichts]],
    { [[Take this vessel, <name>. The enchantment it bears will allow you to draw the last vestiges of the Light surrounding these magisters. They once tended the captive naaru and some of its energy yet remains within them.]], [[It will be just enough to restore your comrade to life.]], [[.Do not worry about the magisters, <name>. I have seen to their care, and Lord Solanar assures me that Lady Liadrin seeks a new source of power even now.]], },
    { [[Wendet das schimmernde Gefäß auf die Magister der Blutelfen an, um es zu füllen und kehrt anschließend zu Ritterfürst Heldenblut zurück.]], },
  },
  [9685] = {
    [[Die Erlösung der Toten]],
    { [[Während Ihr das Gefäß vorbereitet habt, habe ich eine Gruppe von Initianden entsandt, um Sangrias Stillstreich zu bergen. Ihr findet ihn, zusammen mit einem Ordensdiener, auf der obersten Etage des Gasthauses von Silbermond. Begebt Euch ohne Verzögerung dorthin und entfesselt die magischen Kräfte des Gefäßes, um ihn ins Leben zurückzuholen.]], [[Die Zeit drängt, <name>. Sollte er zu lange tot sein, können ihn nicht einmal mehr die Mächte des Lichts retten. Kehrt nach Stillstreichs Wiederbelebung zu mir zurück.]], },
    { [[Bringt das gefüllte schimmernde Gefäß zum Gasthaus von Silbermond und verwendet seine Magie, um Sangrias Stillstreich wiederzubeleben.]], },
  },
  [9686] = {
    [[Die zweite Prüfung]],
    { [[Ihr steht kurz davor, innerhalb des Ordens den Status eines Adepten zu erhalten, <name>. Zunächst werdet Ihr jedoch beweisen müssen, dass Ihr für eine solche Ehre schon bereit seid, indem Ihr eine weitere Prüfung absolviert.]], [[Die zweite Prüfung unterscheidet sich vor allem dadurch von der Ersten, dass Euch keine unliebsamen Überraschungen mehr erwarten. Die vor Euch liegende Aufgabe ist einfach; Ihr müsst Euch gegen die vier besten Champions der Blutritter im Kampf beweisen.]], [[Sobald Ihr die Herausforderung annehmt, erscheint in der Mitte des Pavillons ein Kristall. Berührt ihn, um mit Eurer Prüfung zu beginnen und entfernt Euch niemals zu weit von dem Gebäude.]], [[Seid Ihr bereit?]], },
    { [[Besteht die zweite Prüfung und besiegt Champion Rachblut, Champion Lichtrend, Champion Schnellklinge und Champion Sonnensturm im Pavillon in der Nähe von Meister Kelerun Blutpein.]], },
  },
  [9687] = {
    [[Wiederherstellung der Heiligkeit]],
    { [[Es ist noch nicht lange her, da kamen die einst so friedlichen Eulkin der Umgebung an die Ufer dieser Insel. Sie kamen einer nach dem anderen, um die Knochen der gefallenen Drachen zu stehlen. Ich weiß nicht, was die Kreaturen in diese Verzweiflung getrieben hat oder warum sie die Knochen der Dahingeschiedenen wollen. Allerdings ruhen die Geister der Brut seit dieser Schändung nicht mehr.]], [[Gen Westen, über die Purpurbrandung hinweg, liegt der Rachfederkamm. Reist zu den Siedlungen der Eulkin und nehmt die gestohlenen Drachenknochen wieder an Euch.]], },
    { [[Prinz Toreth auf der Insel Drachenfels möchte, dass Ihr 8 Drachenknochen wiedererlangt.]], },
  },
  [9688] = {
    [[Im Traum]],
    { [[Die Welpen, die Ihr auf dieser Insel seht, sind die ruhelosen Geister des grünen Drachenschwarms. Sie sind wie ich an das Land gebunden, außerstande ihre letzte Ruhestätte im Traum zu erreichen.]], [[Die Wiederbeschaffung der Knochen von den Gefallenen hat sie zwar etwas beruhigt, aber Ihr müsst noch mehr unternehmen.]], [[Zerstört ihre astralen Erscheinungen. Ich kann diesen Ort erst verlassen, wenn es geschehen ist. Sie müssen erlöst werden!]], },
    { [[Prinz Toreth auf der Insel Drachenfels hat von Euch verlangt, dass Ihr 5 Viridianwelpen und 5 Viridianbrutlinge tötet.]], },
  },
  [9689] = {
    [[Messerrachen]],
    { [[Während der Angriffe wurden viele des schwarzen Drachenschwarms getötet. Einer von ihnen, ein Offizier von Todesschwinge selbst, sucht noch immer diesen Ort heim. Blickt Ihr in den Himmel, so könnt Ihr sein spektrales Antlitz durch die Lüfte kreisen sehen. Sein Hass ist so groß, dass er sogar den Tod übersteht.]], [[Nehmt die Knochen, die Ihr bereits für mich eingesammelt habt, zu der Spitze des Berges dieser Insel und platziert sie im ewig brennenden Scheiterhaufen. Dieses Signal sollte Messerrachen herabschweben lassen. Sobald er da ist, tötet sein untotes Abbild.]], },
    { [[Prinz Toreth auf der Insel Drachenfels hat von Euch verlangt, dass Ihr Messerrachen tötet.]], },
  },
  [9690] = {
    [[Die zweite Prüfung]],
    { [[Initiand <name>, Ihr habt hart an Euch gearbeitet, um die Macht des Lichts zu beherrschen, über die wir Dank unserer Magister verfügen. Ihr wisst nun von der Quelle unserer Fähigkeiten und alles über die Pflichten eines Blutritters.]], [[Der Zeitpunkt ist nahe, da Ihr Euch der zweiten Prüfung unterziehen werdet und Ihr um den Rang eines Adepten der Blutritter kämpfen müsst. Die Prüfung findet unter der Aufsicht von Meister Kelerun Blutpein im nördlichen Teil von Silbermond statt. Reist durch die Ruinen von Silbermond nach Norden und nehmt den östlichen Pfad, der kurz vor der Brücke abzweigt, die zur Insel der Sonnenwanderer führt.]], },
    { [[Meldet Euch bei Meister Kelerun Blutpein.]], },
  },
  [9691] = {
    [[Rückkehr nach Silbermond]],
    { [[Kehrt umgehend nach Silbermond zurück und berichtet Ritterfürst Heldenblut. Eure Ankunft sollte ihm alles Notwendige über den Ausgang der Prüfung mitteilen.]], [[Er wird Euch Eure weiteren Aufgaben auf dem Weg zum Adepten erläutern. Ihr könnt stolz auf Euch sein, <name>. Viele versuchen sich an dieser Prüfung, doch nur die Wenigsten kehren zurück, um von ihrem Erfolg zu berichten.]], },
    { [[Kehrt zu Ritterfürst Heldenblut nach Silbermond zurück.]], },
  },
  [9692] = {
    [[Der Pfad des Adepten]],
    { [[Bevor ein frischgebackener Adept in die Ränge der Blutritter aufgenommen wird, muss er die Materialien beschaffen, die zum Schmieden der besonderen Waffe des Ordens benötigt werden: dem blutgehärteten Ranseur.]], [[Ihr müsst Euch außerdem das Recht verdienen, das Insigne unseres Ordens zu tragen. Bei der Gründung des Ordens wurde eine begrenzte Zahl dieser Insignien geschmiedet und verteilt, jeder Adept erbt sein Insigne von einem Veteran. Euer Insigne erhaltet Ihr von einem gefallenen Ritter, der einen Angriff auf die Todesfestung anführte.]], [[Ich habe Notizen vorbereitet, die Euch helfen sollen.]], },
    { [[Bringt Ritterfürst Heldenblut in Silbermond einen verderbten Koredelstein, eine Kiste mit blutgeschmiedeten Blöcken, etwas Blut des Hasserfüllten und ein Insigne der Blutritter.]], },
  },
  [9693] = {
    [[Was Argus für mich bedeutet]],
    { [[Was bedeutet Argus für Euch, <name>? Viele Draenei erinnern sich wahrscheinlich nicht mehr an unseren Heimatplaneten oder kennen ihn überhaupt nicht. Dort trennten wir uns von den Man'ari: unseren verdorbenen dämonischen Abbildern, die von Sargeras 'bedacht' wurden. Sie selbst nennen sich die Eredar.]], [[Eredar... dieses Wort hatte einst eine Bedeutung.]], [[<Admetius scheint das Wort förmlich zu schmecken.>]], [[Widerlich.]], [[Lasst uns nicht noch mehr Zeit verschwenden. Sucht nach Verteidiger Boros, damit Ihr mit Eurer Ausbildung beginnen könnt.]], },
    { [[Exarch Admetius bei der Blutwacht wies Euch an, mit Verteidiger Boros zu sprechen.]], },
  },
  [9694] = {
    [[Blutwacht]],
    { [[Wie Ihr wisst, ist die Blutwacht einer von zwei Vorposten, die der Hand von Argus als Operationsbasen dienen. Ihr wart bereits bei der anderen Basis, der Azurwacht; die Dinge laufen auf der Blutmythosinsel etwas anders. Als wir hier zu Beginn ankamen, war das Land genauso wunderschön und unberührt wie auf der Azurmythosinsel. Wir nannten es sogar Silberbrise, doch nun breitet sich die Seuche über das Land aus. Was noch schlimmer ist, die Blutelfen, die wir auf der Exodar bekämpften, haben den Absturz überlebt. Somit kommen wir zu Eurem ersten Auftrag: Reist nach Nordwesten zum Messerwinkel und vernichtet alle Blutelfen, die Euch über den Weg laufen.]], },
    { [[Verteidiger Boros bei der Blutwacht möchte, dass Ihr 10 Spione der Sonnenfalken tötet.]], },
  },
  [9696] = {
    [[Übersetzungen...]],
    { [[Unsere hochrangigste Vernehmungsbeauftragte Elysia spricht fließend Thalassisch. Bringt ihr dieses Dokument, mal sehen, was sie dazu zu sagen hat.]], },
    { [[Bringt das Schreiben der Sonnenfalken zur Vernehmungsbeauftragten Elysia bei der Blutwacht.]], },
  },
  [9697] = {
    [[Behüterin Leesa'oh]],
    { [[Es müssen wichtige Forschungen bezüglich der Ökologie der hiesigen Lebewesen betrieben werden, besonders bei den Sporlingen und den Sumpflords der Marschen. Und vertraut mir, wenn Ihr Euch einmal mit diesem Thema befasst habt, werdet Ihr die Arbeit faszinierend finden.]], [[Sucht Behüterin Leesa'oh im Wachposten des Cenarius im westlichen Teil der südlichen Marschen. Folgt dem Pfad nach Westen, dann werdet Ihr sie sicher finden. Sie ist manchmal etwas... übereifrig..., aber lasst Euch davon nicht abschrecken.]], [[Oh, und hütet Euch vor ihrer Katze.]], },
    { [[Sprecht mit Behüterin Leesa'oh im Wachposten des Cenarius in den Zangarmarschen.]], },
  },
  [9698] = {
    [[Audienz beim Propheten]],
    { [[Ihr müsst das übersetzte Dokument Velen persönlich überbringen, <name>. Ich vertraue diesem verstörten Boten nicht.]], [[Erklärt dem Propheten, dass die Blutelfen Verstärkung aus der Scherbenwelt erhalten und dass sie einen Spion unter den Draenei bei der Exodar haben. Velen hält sich in der Halle des Lichts in der Exodar auf.]], [[Beeilt Euch!]], },
    { [[Bringt das übersetzte Schreiben der Sonnenfalken zum Propheten Velen bei der Exodar.]], },
  },
  [9699] = {
    [[Was ist wahr?]],
    { [[Ich danke Euch für die rasche Zustellung, <name>. Ihr erweist Euch schließlich als einer der besseren Agenten der Hand.]], [[Wenn wir bloß wüssten, wem sie auf der Blutmythosinsel Bericht erstattet haben und noch wichtiger, wo dieses Sonnenportal ist... Sie scheinen es streng zu bewachen - wo immer es auch sein mag.]], [[Wärt Ihr an einer Erkundungsmission interessiert?]], [[Wenn ja, dann bringt diese Anordnungen zu Verteidiger Boros. Er wird Euch Euren nächsten Auftrag erteilen.]], },
    { [[Bringt Velens Anordnungen zu Verteidiger Boros bei der Blutwacht.]], },
  },
  [9700] = {
    [[Licht ins Dunkel bringen]],
    { [[Velen erklärt, dass falls die Blutelfen Verbindungen zwischen der Insel und der Scherbenwelt hergestellt haben, Überreste oder Anomalien rund um die geöffneten Portale zurückgelassen wurden. Ihr müsst die Insel nach Gebieten absuchen, in denen diese Anomalien vorkommen.]], [[Seid gewarnt, <name>, manche dieser Anomalien sind Kreaturen aus der Leere selbst. Solltet Ihr auf Anomalien der Leere treffen, tötet sie! Erstattet mir Bericht, sobald Ihr neue Informationen ausfindig gemacht habt. Beginnt mit Eurer Suche im Norden, nahe der Warpgondel.]], },
    { [[Verteidiger Boros bei der Blutwacht möchte, dass Ihr die Lage des Sonnenportals ausfindig macht und 5 Anomalien der Leere tötet.]], },
  },
  [9701] = {
    [[Sporlinge beobachten]],
    { [[Wir müssen alles über die Sporlinge von Sporeggar und ihre Beziehung zu den anderen Lebewesen in der Umgebung in Erfahrung bringen. Eine Möglichkeit dazu ist die, die ich anwende: passives Beobachten und ihr Verhalten notieren.]], [[Ich möchte, dass Ihr für mich nach Westen in das Pilzgeflecht geht. Untersucht die gesamte Gegend dort und berichtet mir dann davon.]], },
    { [[Untersucht das Pilzgeflecht in den Zangarmarschen und berichtet danach Behüterin Leesa'oh im Wachposten des Cenarius in den Zangarmarschen von Euren Beobachtungen.]], },
  },
  [9702] = {
    [[Eine Frage der Fressgier]],
    { [[Ich habe mich schon gefragt, warum die Sumpflords die Straße überqueren, um in das Pilzgeflecht zu gelangen - sie müssen aus irgendeinem Grund am verhungern sein. Das darf nicht sein!]], [[Ich meine, normalerweise würde ich mich nicht einmischen, aber wenn sie weiterhin alle Kapseln essen, werden sie die Sporlinge ausrotten!]], [[Hier ist mein Plan: Ich bleibe hier und Ihr geht zum Quaggkamm im Osten und seht Euch nach nahrungstechnischen Gründen um, warum sie die Sporlinge überfallen.]], },
    { [[Sammelt 10 Stück liegengelassene Nahrung und kehrt dann zu Behüterin Leesa'oh im Wachposten des Cenarius in den Zangarmarschen zurück.]], },
  },
  [9703] = {
    [[Der Kryokern]],
    { [[Wir haben noch andere, die nach Spuren suchen, die zu den Sonnenportalen führen, <name>. Ihr müsst Euch jetzt auf Euren bevorstehenden Einsatz konzentrieren.]], [[Boros erhielt Berichte über eine neue Absturzstelle. Wir haben herausgefunden, dass es sich bei dieser Absturzstelle eigentlich um den Kryokern der Exodar handelt. Eine Transportkammer, in der genau solche Kapseln untergebracht sind wie die, aus denen wir Draenei im Am'mental gekrochen sind.]], [[In diesem Bereich werden auch Heilvorräte aufbewahrt. Geht zum Kryokern genau westlich der Blutwacht. Sucht nach Vorräten und nach Überlebenden.]], },
    { [[Verteidiger Kuros bei der Blutwacht möchte, dass Ihr 12 Kisten mit Heilvorräten sichert.]], },
  },
  [9704] = {
    [[Von den Getriebenen getötet]],
    { [[Es ist ein hartes Stück Arbeit, ein Kundschafter zwischen der Insel der Sonnenwanderer und dem Falkenplatz zu sein. Knappe Zeitvorgaben, lange Arbeitszeiten und noch schlimmer... die Getriebenen!]], [[Die Straße im Süden führt durch die Ruinen von Silbermond... ein Ort, an dem es nur so von Getriebenen wimmelt, die darauf warten, Euch für ein paar Manakristalle töten zu können. Ich glaube, dieses Schicksal hat unsere letzte Kundschafterin ereilt. Wir hatten sie mit einem von Eronas Päckchen entsandt.]], [[Wie ich sehe, brennt Ihr bereits darauf, Euch beweisen zu können. Reist zum Dämmerweg und sucht nach Spuren unserer Kundschafterin.]], },
    { [[Geht nach Süden zu den Ruinen von Silbermond und haltet nach der getöteten Kundschafterin beim Dämmerweg Ausschau.]], },
  },
  [9705] = {
    [[Bergung des Päckchens]],
    { [[Ihr nehmt das Päckchen und stellt noch einmal sicher, dass es wirklich von Magistrix Erona stammt. Alarion würde es sicherlich gerne entgegennehmen.]], },
    { [[Kehrt mit Eronas Päckchen zu Kundschafterin Alarion zurück.]], },
  },
  [9706] = {
    [[Galaens Tagebuch - Das Schicksal von Verteidiger Saruan]],
    { [[Eure Hände zittern beim Aufheben des Buchs. Kuros muss von dieser Tragödie erfahren. Bringt ihm das Tagebuch.]], },
    { [[Bringt Galaens Tagebuch zu Verteidiger Kuros bei der Blutwacht.]], },
  },
  [9707] = {
    [[Das Schmieden der Waffe]],
    { [[Wieder habt Ihr Euch als würdig erwiesen, ein Mitglied des Ordens zu sein. Mit großer Freude verleihe ich Euch den Rang des Adepten, <name>. Prinz Kael'thas selbst wäre stolz auf Euch.]], [[Es ist eine Ehre für uns alle, an Eurer Seite auf dem Schlachtfeld zu kämpfen. Verhaltet Euch weiterhin vorbildlich, zeigt keine Gnade oder Schwäche, verteidigt unermüdlich Euer Volk und Eure Brüder und verfeinert Eure Beherrschung des Lichts.]], [[Nun holt Euch die Waffe, die Eurem neuen Rang angemessen ist.]], },
    { [[Bringt die Kiste mit Rohmaterialien zu Bemarrin in Silbermond.]], },
  },
  [9708] = {
    [[Bekannter Pilz]],
    { [[Ja, das ist es! Ich wusste doch, dass ich diesen Pilzstamm schon woanders in den Marschen gesehen habe!]], [[Im Norden gibt es einen Ort, der Prügelsumpf genannt wird. Komischer Name für einen Sumpf, wenn Ihr mich fragt, aber ich bin noch nie dort gewesen. Ich brauche Proben von diesen Pilzen um meine Theorie zu unterstützen.]], [[Aber seid vorsichtig, <class>. Ich habe gehört, dass die Oger in dieser Gegend einmarschiert sind. Ihr müsst also die Proben von ihnen besorgen. Könnte das der Grund sein, warum die Sumpflords dort nicht mehr ihre Nahrung suchen?]], },
    { [[Sammelt 15 Pilzproben und kehrt dann zu Behüterin Leesa'oh im Wachposten des Cenarius in den Zangarmarschen zurück.]], },
  },
  [9709] = {
    [[Die Pilze zurückstehlen]],
    { [[<class>, ich habe eine Idee. Ich möchte, dass Ihr Euch wieder nach Norden aufmacht, aber dieses Mal in das Herz des Ogergebiets!]], [[Nordwestlich des Prügelsumpfs gibt es einen Ort, der Festung der Ango'rosh genannt wird. Er befindet sich auf einer Insel jenseits einer großen Pilzbrücke. Dort lagern sie bestimmt die geernteten Pilze. Ihr könnt sie sicher überall finden, wahrscheinlich haben die Oger selbst sie sogar in den Taschen!]], [[Bringt mir genügend davon, und ich werde versuchen, die Sumpflords mit einer neuen Nahrungsquelle zu versorgen.]], },
    { [[Sammelt 10 Kisten Pilze und kehrt dann zu Behüterin Leesa'oh im Wachposten des Cenarius in den Zangarmarschen zurück.]], },
  },
  [9710] = {
    [[Der blutgehärtete Ranseur]],
    { [[Es ist alles vorbereitet, <name>. Bald werdet Ihr eine Waffe haben, die Eurem neuen Status angemessen ist.]], },
    { [[Wartet, während Bemarrin Eure Waffe schmiedet.]], },
  },
  [9711] = {
    [[Matis der Grausame]],
    { [[Matis will be brought to justice, <name> and I want you to be the one to find him and bring him back to the Triumvirate.]], [[Scour the island for this bastard. Should you find him, use this flare gun. When my trackers see the flare, they'll come and assist in capturing Matis. Return to me after he's been captured.]], },
    { [[Verteidiger Kuros bei der Blutwacht möchte, dass Ihr Matis den Grausamen gefangen nehmt.]], },
  },
  [9712] = {
    [[Das thalassische Schlachtross]],
    { [[Ihr habt Euch im Dienste des Ordens ausgezeichnet, <name>. Die Euch durch Magister Astalor und seiner Brüder gewährten Fähigkeiten, die Energien des Lichts zu beherrschen und Eurem Willen nach einzusetzen, und Euer offensichtliches Talent darin, machen Euch alle Ehre. Ihr wurdet für würdig befunden, Euer Wissen noch zu erweitern.]], [[Mit dem Licht unter Eurer Kontrolle seid Ihr in der Lage, ein furchterregendes Schlachtross herbeizurufen; ein treues Reittier an Eurer Seite.]], [[Dient auch weiterhin so voller Eifer und Ehre, <name>, und Ihr werdet belohnt werden.]], },
    { [[Sprecht mit Ritterfürst Heldenblut, um zu erfahren, wie Ihr Euer thalassisches Schlachtross beschwören könnt.]], },
  },
  [9713] = {
    [[Glowcap Harvesting Enabling Flag]],
  },
  [9714] = {
    [[Bringt mir ein weiteres Gebüsch!]],
  },
  [9715] = {
    [[Bringt mir ein Gebüsch!]],
    { [[Wir müssen unsere Vorräte an Bluthibiskus aufstocken, die wir für viele Zwecke brauchen. Früher gab es hier dicke Büsche davon, aber seitdem der Wasserspiegel der Marschen gesunken ist, findet man ihn nur noch im Tiefensumpf und im Echsenkessel, unter dem Schlangensee.]], [[Man sagt, er sei eine Delikatesse für die Schlurfer, Sumpfriesen und die Tiefenfledermaus. Wenn Ihr uns also helfen wollt, müsst Ihr die Blumen von ihnen besorgen.]], },
    { [[Sammelt 5 Stück Bluthibiskus und bringt sie zu Gzhun'tt in Sporeggar in den Zangarmarschen.]], },
  },
  [9716] = {
    [[Anomalie am Umbrafennsee]],
    { [[Wir kamen in die Scherbenwelt mit der Erwartung, hier kleine Ansammlungen von Leben zu finden. Doch was wir hier in den Zangarmarschen entdeckt haben, ist eine üppige Landschaft mit einem gedeihenden Ökosystem.]], [[Unsere ursprüngliche Einschätzung der Gegend war dennoch trügerisch. Die Wasserstände der Seen und Marschen sind seit unseren ersten Messungen stetig gesunken.]], [[Dies könnte verheerende Auswirkungen auf die Flora und Fauna der Gegend haben. Geht zum Umbrafennsee südwestlich von hier und seht Euch nach möglichen Gründen für das Sinken des Wasserspiegels um.]], },
    { [[Untersucht den Grund für die sinkenden Wasserspiegel am Umbrafennsee. Kehrt dann zu Ysiel Windsänger in der Zuflucht des Cenarius in den Zangarmarschen zurück.]], },
  },
  [9717] = {
    [[Los geht´s!]],
    { [[Auch wenn wir ein friedliebendes Volk sind, befinden wir uns im Krieg mit den Fungusgiganten, ob uns das gefällt oder nicht. Und es gibt ein Sprichwort bei den Sporlingen, das besagt, dass im Krieg und im Pilz alles erlaubt ist!]], [[Einer der Anführer der Fungusgiganten, Hungarfenn, pflegt seit Jahrtausenden mit Hingabe eine sehr besondere Pflanze, bekannt als die Tiefenspore. Er würde daran zerbrechen, wenn dieser Pflanze etwas zustoßen würde.]], [[Reist zum Tiefensumpf im Echsenkessel und stehlt einen Dorn von ihr. Vertraut mir, er wird es bemerken.]], },
    { [[Besorgt einen Tiefensporendorn und bringt ihn zu T'shu in Sporeggar in den Zangarmarschen.]], },
  },
  [9718] = {
    [[Der Flug der Krähe]],
    { [[Wir müssen genau herausfinden, wie weit reichend die Aktivität der Naga wirklich ist und entsprechend reagieren.]], [[Jedes Wasservorkommen in den Zangarmarschen zu untersuchen wäre ein sehr zeitaufwändiges Unterfangen. Wenn Ihr mir gestattet, werde ich meine Magie benutzen, um Euch in eine Gestalt zu verwandeln, die Euch das Erkunden der Marschen leichter machen wird.]], [[Haltet dieses Amulett über den Balkon in meiner Nähe, wenn Ihr bereit seid. Ich werde Euch zurück in die Sicherheit führen, wenn wir fertig sind.]], },
    { [[Ysiel Windsänger möchte, dass Ihr das Sturmkrähenamulett benutzt, um die größeren Seen in den Zangarmarschen zu erkunden.]], },
  },
  [9719] = {
    [[Tötet die Mutter]],
    { [[Geschichtlich gesehen waren die Marschenläufer die größte Gefahr für mein Volk. Jetzt, da wir mit den Problemen mit den Fungusgiganten zu kämpfen haben, können wir uns keinen weiteren Angriff unserer alten Feinde erlauben.]], [[Ihr habt Euch als fähig erwiesen, <class>. Meint Ihr, Ihr könntet zum Tiefensumpf im Echsenkessel reisen und die große Mutter der Marschenläufer, die als Schattenmutter bekannt ist, auslöschen?!]], [[Bringt mir ihr Gehirn als Beweis.]], },
    { [[Bringt das Gehirn der Schattenmutter zu Khn'nix in Sporeggar in den Zangarmarschen.]], },
  },
  [9720] = {
    [[Das Gleichgewicht muss erhalten bleiben]],
    { [[Ich kann nicht verstehen, warum die Naga ihren eigenen Lebensraum entwässern wollen. Ich weiß aber sicher, dass wir sie aufhalten müssen.]], [[Diese Samen tragen die Wut der Erde in sich. Bringt sie zu den Dampfpumpen, die von den Naga betrieben werden, und benutzt sie bei den Steuerungen.]], [[Die Ranken, die aus den Samen wachsen, werden so dick wie Euer Arm und so stark wie Eisen sein, <name>. Stellt das Gleichgewicht wieder her, das die Naga zerstört haben.]], },
    { [[Ysiel Windsänger möchte, dass Ihr die Eisenrankensamenkörner benutzt, um die Dampfpumpensteuerungen am Schlangensee, Umbrafennsee, Sumpflichtsee und der Lagune zu zerstören. Kehrt danach mit den übrigen Samen zu ihr in die Zuflucht des Cenarius in den Zangarmarschen zurück.]], },
  },
  [9721] = {
    [[Aufforderung von Lord Solanar]],
    { [[Eure Aufopferung für den Orden und Euer rascher Aufstieg innerhalb seiner Ränge sind nicht unbeachtet geblieben. Tatsächlich erbittet Lord Solanar Blutzorn, Lady Liadrins rechte Hand und verdienter Held, Eure Gegenwart.]], [[Das ist eine seltene Ehre, <name>. Normalerweise überlässt Lord Solanar mir die Verwaltung des Ordens und das damit verbundene Tagesgeschäft, doch scheint er ein besonderes Interesse an Euch zu haben. Wartet nicht zu lange, <name>.]], },
    { [[Sprecht mit Lord Solanar Blutzorn in Silbermond.]], },
  },
  [9722] = {
    [[Der Pfad des Meisters]],
    { [[Innerhalb meines Ordens existiert ein eingeschworener Zirkel von Eliterittern. Die Mitglieder des Zirkels repräsentieren die Hingebungsvollsten, Fähigsten und Vertrauenswürdigsten aller Blutritter, allesamt handverlesen und vereidigt durch die Ordensführung. Diese Meister sind leicht an ihrem thalassischen Schlachtross zu erkennen.]], [[Jeder Kandidat, der sich um die Aufnahme in diese erlesene Gemeinschaft bewirbt, benötigt einen Fürsprecher aus den Reihen des Zirkels und muss seine Hingabe für die zentralen Grundsätze des Ordens unter Beweis stellen.]], [[Ich habe beschlossen, Euch als Fürsprecher zur Seite zu stehen, sollte es Euer Wunsch sein, in die Reihen der Meister der Blutritter aufgenommen zu werden.]], },
    { [[Sprecht nochmals mit Lord Solanar, um seine angebotene Unterstützung für Eure Aufnahme zu akzeptieren.]], },
  },
  [9723] = {
    [[Eine Geste der Hingabe]],
    { [[Die Loyalität eines Blutritters endet nicht an der Spitze seiner Klinge, <name>. Der Orden verlangt mehr von seinen Mitgliedern, als schiere Ausbildung und Kampf. Ein wahrer Ritter trägt zu allen Aktivitäten des Ordens etwas bei, selbst zu denen, die nicht offensichtlich Ruhm verheißen.]], [[Diese erste Aufgabe fordert weder Eure Kampfeskunst, noch ist sie besonders ruhmreich, doch ohne solche Tätigkeiten würde der Orden auseinander fallen. Ich werde Euch eine Liste mit Gegenständen geben, die Magister Astalor Blutschwurs Männer benötigen, um uns mit Lichtenergie für unsere Magie zu versorgen.]], },
    { [[Bringt 40 Einheiten Runenstoff, 6 Arkanitbarren, 10 Einheiten Sonnengras, 5 Dunkelrunen und 150 Goldstücke zu Lord Solanar in Silbermond.]], },
  },
  [9724] = {
    [[Warnung an den Zirkel des Cenarius]],
    { [[Die Expedition des Cenarius ist zu einer großen, fast selbständigen Organisation geworden. Die Vorgänge hier sind so wichtig, dass wir den Zirkel des Cenarius warnen müssen.]], [[Keine Sorge, ich werde Euch nicht bitten, zur Mondlichtung zurückzureisen. Es gibt Abgesandte des Zirkels auf der Höllenfeuerhalbinsel.]], },
    { [[Sprecht mit Amythiel Nebelgänger beim Posten des Cenarius auf der Höllenfeuerhalbinsel.]], },
  },
  [9725] = {
    [[Eine Demonstration Eurer Loyalität]],
    { [[Wir Blutritter haben geschworen, Quel'Thalas zu verteidigen, <name>. Dies ist ein zentraler Teil unserer Ausbildung. Unsere Feinde sind wild und unbeugsam. Einzig unsere Anstrengungen und die Unterstützung unserer Verbündeten sind der Garant für die Freiheit eines jeden Bürgers von Silbermond.]], [[Ich habe einige beunruhigende Berichte über die verstärkte Präsenz der Geißel, südöstlich unserer Grenze zu den Östlichen Pestländern, erhalten. Eine Kompanie Belagerungsingenieure der Geißel, mitsamt Belagerungsgerät, scheinen sich dort für einen Angriff auf die Geisterlande zu sammeln. Auf keinen Fall dürfen sie Erfolg haben.]], },
    { [[Lord Solanar in Silbermond möchte, dass Ihr 3 Fleischwagen der Geißel zerstört und 15 Belagerungsingenieure der Geißel tötet.]], },
  },
  [9726] = {
    [[Jetzt, da wir Freunde sind...]],
    { [[Die Naga in der Enklave der Blutschuppen im Nordosten haben angefangen, unsere Leute umzubringen. Das können wir nicht tolerieren, <class>!]], [[Wir schlagen vor, dass Ihr ihnen als Beweis für unsere Freundschaft eine Lektion erteilt.]], },
    { [[Tötet 12 Sklaventreiber der Blutschuppen und 6 Verzauberinnen der Blutschuppen und kehrt dann zu Gzhun'tt in Sporeggar in den Zangarmarschen zurück.]], },
  },
  [9727] = {
    [[Jetzt, da wir immer noch Freunde sind...]],
    { [[Berichten zufolge töten die Naga bei der Enklave der Blutschuppen immer noch unsere Leute. Ich dachte, Ihr hättet Euch darum gekümmert, <class>!]], [[Ich schlage vor, dass Ihr ihnen als Beweis für unsere andauernde Freundschaft eine Lektion erteilt.]], },
    { [[Tötet 12 Sklaventreiber der Blutschuppen und 6 Verzauberinnen der Blutschuppen und kehrt dann zu Gzhun'tt in Sporeggar in den Zangarmarschen zurück.]], },
  },
  [9728] = {
    [[Ein herzliches Willkommen]],
    { [[Während die Druiden der Expedition damit beschäftigt waren die Pflanzen und Seen hier zu untersuchen, habe ich es zu meiner Aufgabe gemacht, mich mit den Feinden in dieser Umgebung auseinanderzusetzen.]], [[Vom ersten Moment an, als wir die Zangarmarschen betreten haben, haben die Naga uns ohne Vorwarnung angegriffen. Wir haben unser Bestes getan, um sie fern zu halten, aber unsere Mittel sind begrenzt.]], [[Ich möchte, dass Ihr zu den verschiedenen Lagern der Naga in den Zangarmarschen geht und ihnen zeigt, dass man sich besser nicht mit uns anlegt. Bringt mir ihre Klauen als Beweis Eurer Siege.]], },
    { [[Bringt 30 Nagaklauen zu Aufseher Hamuht in der Zuflucht des Cenarius.]], },
  },
  [9729] = {
    [[Fhwoor zerschmettern!]],
    { [[Kleine Naga machen Fhwoor wütend! Sporlinge gute Freunde von Fhwoor, darum Fhwoor holt Lade von Naga.]], [[Wir gehen in nahes Nagadorf. Du helfen?]], },
    { [[Begleitet Fwoohr in das Gebiet bei der Dampfpumpe am Sumpflichtsee, um die Lade von Ssslith zu holen. Bringt Fhwoor und die Lade sicher zurück und meldet Euch bei Gzhun'tt in Sporeggar in den Zangarmarschen.]], },
  },
  [9730] = {
    [[Anführer der Dunkelkämme]],
    { [[Hiermit wird von der Expedition des Cenarius ein Kopfgeld auf Rajah Haghazed, Anführer der Dunkelkämme, ausgesetzt. Haghazed wurde zuletzt von den Spähern des Cenarius südlich der Lagune gesichtet, mitten im Süden der Zangarmarschen.]], [[Die Dunkelkämme werden mit einer Reihe von Angriffen auf die Expedition des Cenarius in Verbindung gebracht, und ihr Anführer gilt als sehr gefährlich.]], [[Das Kopfgeld kann bei Aufseher Hamuht eingefordert werden.]], },
    { [[Tötet Rajah Haghazed bei der Lagune und kehrt danach zu Aufseher Hamuht bei der Expedition des Cenarius in den Zangarmarschen zurück, um eine Belohnung zu erhalten.]], },
  },
  [9731] = {
    [[Kanalisationspläne]],
    { [[Die Pläne, die Ihr bekommen habt, zeigen ein unterirdisches System von Rohren und Pumpen, die die Seen in der Umgebung mit dem Schlangensee verbinden.]], [[Wenn die Pläne korrekt sind, muss es einen riesigen Ablauf am Grund des Schlangensees geben. Bestätigt dies und bringt die Neuigkeiten zur Zuflucht des Cenarius.]], },
    { [[Search Serpent Lake for signs of a drain.  Return to Ysiel Windsinger at Cenarion Refuge with news of your discovery.]], },
  },
  [9732] = {
    [[Rückkehr in die Marschen]],
    { [[Kehrt zu Ysiel zurück und gebt ihr Bescheid, dass sie so bald wie möglich weitere Hilfe erhalten wird.]], [[Unsere Mitglieder waren bis jetzt mit Angelegenheiten in der Mondlichtung und in Silithus beschäftigt, aber wir werden unser bestes tun, um die notwendigen Mittel zur Zuflucht des Cenarius zu schicken.]], },
    { [[Kehrt zu Ysiel Windsänger in der Zuflucht des Cenarius in den Zangarmarschen zurück.]], },
  },
  [9733] = {
    [[Warnung an den Zirkel des Cenarius]],
    { [[Die Expedition des Cenarius ist zu einer großen, fast selbständigen Organisation geworden. Die Vorgänge hier sind so wichtig, dass wir den Zirkel des Cenarius warnen müssen.]], [[Keine Sorge, ich werde Euch nicht bitten, zur Mondlichtung zurückzureisen. Es gibt Abgesandte des Zirkels auf der Höllenfeuerhalbinsel.]], },
    { [[Sprecht mit Mahuram Starkhuf in Thrallmar auf der Höllenfeuerhalbinsel.]], },
  },
  [9734] = {
    [[Rückkehr in die Marschen]],
    { [[Kehrt zu Ysiel zurück und gebt ihr Bescheid, dass sie so bald wie möglich weitere Hilfe erhalten wird.]], [[Unsere Mitglieder waren bis jetzt mit Angelegenheiten in der Mondlichtung und in Silithus beschäftigt, aber wir werden unser bestes tun, um die notwendigen Mittel zur Zuflucht des Cenarius zu schicken.]], },
    { [[Kehrt zu Ysiel Windsänger in der Zuflucht des Cenarius in den Zangarmarschen zurück.]], },
  },
  [9735] = {
    [[Wahre Meister des Lichts]],
    { [[Ein Meister der Blutritter besitzt unglaubliche Fähigkeiten das Licht auf eine Art zu nutzen und zu formen, wie es die sogenannten anderen 'Paladine' nie verstehen werden. Das hält sie jedoch nicht davon ab, uns dafür zu ächten, wie wir das wahre Potenzial des Lichts entfesseln.]], [[Wir müssen ihnen klar machen, dass man mit unseresgleichen keine Spielchen spielt. Was ich mir vorgestellt habe, ist keine schnelle Angelegenheit und auch nicht einfach, doch wenn Ihr Euch dieser Aufgabe verpflichtet, wird es Euer erstes Ziel sein, den Bibliotheksflügel der Abtei von Tyrs Hand zu infiltrieren und eine Phiole heiligen Wassers von Tyrs Hand an Euch zu bringen.]], },
    { [[Bringt Lord Solanar in Silbermond eine Phiole heiligen Wassers von Tyrs Hand.]], },
  },
  [9736] = {
    [[Wahre Meister des Lichts]],
    { [[Jetzt, da wir das Wasser haben, benötigen wir eine Reihe von Zutaten in genau abgestimmten Mengen. Einige dieser Substanzen sind äußerst selten und wertvoll, <name>, doch ich habe Quellen für zwei von ihnen ausfindig gemacht.]], [[Ihr solltet einige arkane Katalysatoren bei Zalle, dem Reagenzienverkäufer in einem der Geschäfte auf dem Platz nahe der Todesschneise, bekommen. Darlia, die Giftlieferantin nahe den Schurkenausbildern in der Mördergasse, sollte über einen Vorrat an krepuskularem Pulver verfügen.]], [[Um meine Mixtur zu vervollständigen, benötige ich außerdem noch einen azerothischen Diamanten und einen makellosen schwarzen Diamanten.]], },
    { [[Bringt Lord Solanar in Silbermond 1 arkanen Katalysator, 1 Einheit krepuskulares Pulver, 1 azerothischen Diamanten und 1 makellosen schwarzen Diamanten.]], },
  },
  [9737] = {
    [[Wahre Meister des Lichts]],
    { [[Der einzig wahre Weg, das Licht zu nutzen, ist der unsrige, <name>. Um denen, die sich schändlicherweise 'Paladine' nennen, die Torheit ihrer Tradition zu verdeutlichen, habe ich vor, ihnen eine unmissverständliche Nachricht zukommen zu lassen.]], [[Die Alonsuskapelle, der Ort, an dem der Orden der Silbernen Hand seinen Anfang nahm, scheint gegen die Zerstörung in Stratholme immun zu sein. Die ewige Flamme der Kapelle gewährt ihr den Schutz des Lichts, doch wenn Ihr sie mit der Löschmischung erstickt, ist es aus mit dem Schutz. Die Kapelle wird brennen und die Welt wird unsere Vorherrschaft über das Licht anerkennen müssen.]], },
    { [[Verwendet die Löschmischung in der Nähe der ewigen Flamme in der Alonsuskapelle, um den Schutz des Lichts zu brechen. Seid bereit, jeden zu töten, der versucht, die Kapelle zu verteidigen.]], },
  },
  [9738] = {
    [[Bei der Arbeit verschollen]],
    { [[Ihr müsst mir helfen! Wir wurden von Ysiel hierher geschickt, um das Geheimnis der Dampfpumpen zu erkunden und herauszufinden, warum mit ihrer Hilfe das Wasser aus den Marschen gepumpt wird. Aber meine Freunde haben sich von mir getrennt und sind einfach ohne mich weitergegangen.]], [[Die vier können manchmal solche Dummköpfe sein! Aber ich mag sie trotzdem.]], [[Deshalb müsst Ihr mir helfen herauszufinden, was aus ihnen geworden ist, denn ich befürchte das Schlimmste. Sucht sie in den Sklavenunterkünften und im Tiefensumpf.]], [[Vielen Dank, <class>.]], },
    { [[Findet heraus, was mit Erdbinder Rayge, Naturalist Biss, Jäter Gründaum und Windrufer Klaue geschehen ist. Kehrt dann zu Behüterin Jhang im Echsenkessel in den Zangarmarschen zurück.]], },
  },
  [9739] = {
    [[Die Misere der Sporlinge]],
    { [[Ihr da! Ihr müsst uns helfen!]], [[Die Sumpflords fressen all unsere Sporenbeutel! Ihr müsst etwas tun, sonst verlieren wir unsere Jungen!]], [[Die Sporenbeutel sind zu Anfang ganz groß und werden dann kleiner, wenn die Luft darin verbraucht ist. Haltet nach den kleinen, reifen Sporenbeuteln Ausschau und rettet so viele Ihr nur könnt!]], },
    { [[Fahssn im Pilzgeflecht möchte, dass Ihr 10 reife Sporenbeutel sammelt.]], },
  },
  [9740] = {
    [[Das Sonnentor]],
    { [[Dank Euch und Eurer Bemühungen konnten meine Späher das Sonnentor ausfindig machen. Die Elfen sorgen dafür, dass der Standort des Portals stets mobil bleibt und jederzeit verändert werden kann. Deshalb war die Stelle, die Ihr untersucht habt, auch verlassen.]], [[Die Stärke eines solchen Systems ist ebenfalls seine größte Schwäche. Das Sonnentor wird von Portalaufsehern aufrechterhalten. Wenn es uns gelingt, die Aufseher auszuschalten, werden wir das Portal zerstören können. Der momentane Standort des Sonnentors liegt westlich von hier, südlich der Vektorspule. Findet und zerstört es!]], },
    { [[Verteidiger Aesom bei der Blutwacht möchte, dass Ihr das Sonnentor zerstört.]], },
  },
  [9741] = {
    [[Geschöpfe aus der Leere]],
    { [[Aesom hat meine Truppe vor ein paar Tagen auf eine Erkundungsmission geschickt, um nach dem Sonnentor zu suchen. Natürlich haben wir es gefunden...]], [[<Loryi zeigt auf Jorli.>]], [[Was auch immer die Elfen durch dieses Portal transportieren, es sorgt dafür, dass Energien der Leere freigesetzt werden. Die Gegend füllt sich mit Geschöpfen der Leere.]], [[Wir müssen sie vernichten, bevor sie sich in Anomalien der Leere verwandeln. Ihr findet die Geschöpfe südwestlich von hier, nahe des Sonnentors. Tötet sie und erstattet Aesom Bericht, sobald Eure Arbeit getan ist.]], },
    { [[Tötet 12 Geschöpfe der Leere und erstattet Verteidiger Aesom bei der Blutwacht Bericht.]], },
  },
  [9742] = {
    [[Noch ein paar Sporensäcke]],
  },
  [9743] = {
    [[Natürliche Feinde]],
    { [[Wir Sporlinge kamen noch nie gut mit den Sumpflords aus. In der Vergangenheit haben wir sie mit Speeren und Steinen erfolgreich fernhalten können. Aber in der letzten Zeit scheinen sie aggressiver geworden zu sein... oder verzweifelter. Sie haben keine Angst vor uns oder unseren Waffen.]], [[Ihr seid viel Furcht einflößender und besser bewaffnet als wir. Wenn Ihr die Sumpflords für uns bekämpfen könntet, stünden wir für immer in Eurer Schuld! Bringt mir ihre Ranken, damit ich weiß, wie viele Ihr getötet habt.]], },
    { [[Bringt 6 Sumpflordranken zu Fahssn im Pilzgeflecht in den Zangarmarschen.]], },
  },
  [9744] = {
    [[Mehr Ranken!]],
  },
  [9746] = {
    [[Grenze der Erschöpfung]],
    { [[Der Sieg ist zum Greifen nah, <name>. Jetzt, da wir das Sonnentor entdeckt haben, wird es nicht lange dauern, bis ihre Versorgungszufuhr total unterbrochen ist. Es ist an der Zeit ihre Zahl auszudünnen.]], [[Im Westen könnt Ihr zahlreiche Lager der Sonnenfalken finden. Die Pyromanten und Verteidiger der Sonnenfalken belagern den größten Teil der Gegend. Findet sie, tötet sie und kehrt zu mir zurück.]], },
    { [[Verteidiger Aesom bei der Blutwacht möchte, dass Ihr 10 Pyromanten der Sonnenfalken und 10 Verteidiger der Sonnenfalken tötet.]], },
  },
  [9747] = {
    [[Der Umbrafennstamm]],
    { [[Die Naga sind furchtbar, ja. Aber noch schlimmer sind die verräterischen Stämme der Zerschlagenen und der Verirrten, die ihnen geholfen haben!]], [[Die schlimmsten von allen sind die Verirrten des Umbrafennstamms. Sie benutzen dunkle Magie, um andere zu fangen und zu versklaven. Sie sind besonders grausam zu entflohenen Sklaven und würden mir die Kehle durchschneiden, wenn ich die Zuflucht auch nur für einen Tag verlassen würde.]], [[Reist nach Umbrafenn im Süden und tötet sie und ihren Anführer, Kataru. Bis dahin werde ich jeden Tag um mein Leben bangen.]], },
    { [[Ikeyen in der Zuflucht des Cenarius möchte, dass Ihr nach Umbrafenn reist und Kataru, 8 Seher von Umbrafenn, 6 Hexendoktoren von Umbrafenn und 6 Orakel von Umbrafenn tötet.]], },
  },
  [9748] = {
    [[Trinkt nicht das Wasser]],
    { [[Unser Schamane bekundet, dass die Wasserelemente durch die Verschmutzung der Wasserquelle sehr stark aus dem Gleichgewicht geraten sind. Wir wissen, dass der Kraftkern der Exodar im westlich gelegenen Berg liegt. Offenbar entweicht von dort eine flüchtige Substanz in die Wasserzufuhr! Ihr müsst für mich mehr Informationen sammeln.]], [[<Aesom deutet auf einen Behälter und nickt.>]], [[Nehmt diesen Behälter und folgt dem Strand Richtung Norden bis Ihr auf den Punkt trefft, an dem die Flüsse der Blutmythosinsel in das Meer fließen. Entnehmt an dieser Stelle eine Probe und kehrt zu mir zurück.]], },
    { [[Verteidiger Aesom bei der Blutwacht möchte, dass Ihr den Wasserprobenbehälter verwendet, um eine Wasserprobe der Blutmythosinsel zu entnehmen.]], },
  },
  [9749] = {
    [[Sie leben! Vielleicht...]],
    { [[Es war ein blutiges Gemetzel, <name>. Wir wurden von einer ganzen Horde dieser Spinnen aus dem Hinterhalt überrascht. Ihre Anführerin fiel unglaublich schnell über uns her und spann uns in Kokons ein.]], [[Nachdem wir eingesponnen waren, begann sie neben uns Eier zu legen. Wir sollten als Futter für ihre Jungen dienen! FUTTER!]], [[Ich schaffte es, mich aus dem Kokon freizuschneiden und floh. Ich möchte die anderen aber nicht zurücklassen. Allerdings konnte ich mich dem Pass bis jetzt nicht erfolgreich nähern. Würdet Ihr mir helfen?]], [[Zerstört diese Kokons! Hoffentlich sind noch ein paar Mitglieder unseres Teams am Leben!]], },
    { [[Forscher Cornelius hat Euch darum gebeten, 5 Expeditionsforscher zu befreien.]], },
  },
  [9750] = {
    [[Dringende Nachricht]],
  },
  [9751] = {
    [[Das Vermächtnis des Blutfluchs]],
    { [[Letzte Nacht erschien mir Edward Hanes, Kapitän des Schiffs VERLORENE HOFFNUNG, in meinem Zelt. Derselbe Edward Hanes, der vor 20 Jahren an Skorbut gestorben ist.]], [[Die Erscheinung erzählte mir eine Geschichte, die mein Herz erschütterte. Eine Geschichte, die Ihr hören müsst, und eine Aufgabe, die Ihr erfüllen sollt. Seid Ihr dabei? Werdet Ihr nach Edward auf der Insel Drachenfels, die nordöstlich der Blutwacht liegt, suchen?]], },
    { [[Admiral Odesyus hat Euch gebeten, nach Kapitän Edward Hanes zu suchen und mit ihm zu sprechen.]], },
  },
  [9752] = {
    [[Flucht aus Umbrafenn]],
    { [[Ihr müsst mir helfen hier rauszukommen!]], [[Ich habe in der Nähe Kräuter gesammelt, als ein paar dieser Wilden mich entführten. Ich habe versucht mich loszureißen, aber es waren zu viele.]], [[Helft mir zurück zur Zuflucht des Cenarius und weg von diesen grausamen Schuften und ihrer bösen Magie zu gelangen!]], },
    { [[Begleitet Kayra Langmähne zur Zuflucht des Cenarius in den Zangarmarschen. Gebt Ysiel Windsänger Bescheid, sobald Ihr diese Aufgabe erledigt habt.]], },
  },
  [9753] = {
    [[Was wir wissen...]],
    { [[Wir haben in den vergangenen Tagen viel über unsere Feinde gelernt. Die Blutelfen werden von einer gewissen Sironas angeführt und haben vor, uns zu zerstören. Letztendlich wollen sie noch O'ros versklaven und wieder die Kontrolle über die Exodar gewinnen.]], [[Dem Licht sei Dank, konnten wir ihre Versorgungszufuhr abschneiden, indem wir das Sonnentor zerstörten. Sie verwenden jedoch die Verunreinigung der Vektorspule, um das Land zu vergiften, und nutzen somit dessen mutagene Eigenschaften zu ihrem Vorteil.]], [[Admetius hat auf Grund dieser Informationen einen Plan ausgeklügelt. Geht zu ihm.]], },
    { [[Sprecht mit Exarch Admetius bei der Blutwacht.]], },
  },
  [9756] = {
    [[Was wir nicht wissen...]],
    { [[Unsere Vernehmungsversuche sind kläglich gescheitert, <name>. Folter kommt - natürlich - nicht in Frage. Was wir brauchen, ist <ein/eine><race> <race>, <der/die><race> an ihn herankommen kann.]], [[Ich kann einen Zauber auf Euch wirken, der Euch wie <einen Blutelfen/eine Blutelfe> aussehen lässt und Eure Sprache anpasst. Mit dieser List werden wir vielleicht die benötigten Informationen von dem gefangenen Agenten bekommen.]], [[Ich möchte, dass Ihr Euch mithilfe dieser Verkleidung das Wissen des Gefangenen erschleicht.]], [[Und <name>, verlasst die Blutwacht nicht, solange Ihr verkleidet seid, da sonst der Zauber aufgehoben wird.]], },
    { [[Sprecht mit einem gefangenen Agenten der Sonnenfalken bei der Blutwacht und eignet Euch Informationen der Sonnenfalken an. Kehrt zu Admetius zurück, sobald Ihr Eure Aufgabe erfüllt habt.]], },
  },
  [9757] = {
    [[Sucht Jägerin Kella Nachtbogen auf]],
    { [[Jägerin Kella Nachtbogen hat um eine Audienz mit allen qualifizierten Jägern gebeten. Sie lehrt das Zähmen von wilden Tieren, etwas, das Ihr vor allem anderen zum jetzigen Zeitpunkt Eurer Ausbildung lernen solltet.]], [[Ihr findet Kella in der Nähe von Valaars Steg, den Docks westlich der Exodar.]], },
    { [[Sprecht mit Jägerin Kella Nachtbogen nahe Valaars Steg.]], },
  },
  [9758] = {
    [[Rückkehr zu Arkanist Vandril]],
    { [[Nun, <mein Freund/meine Freundin>, wenn Ihr so nett wärt, zu Arkanist Vandril zurückzukehren? Ich weiß, er ist begierig, Euch auf Eure erste Mission hier in den Geisterlanden zu senden.]], [[Wir zählen auf Euch, <name>. Enttäuscht uns nicht.]], },
    { [[Kehrt zu Arkanist Vandril in Tristessa in den Geisterlanden zurück.]], },
  },
  [9759] = {
    [[Ihre Welt enden lassen]],
    { [[Nehmt diesen Auftrag nicht an, wenn Euch etwas an Eurem Leben liegt. Seht Ihr den gigantischen Kraftkern, der aus der Seite des Bergs dort drüben hervorragt? Wer auch immer diese Last auf sich nehmen möchte, muss sich seinen Weg durch den Berg kämpfen und eine Armee der Sonnenfalken abwehren, um mich zu beschützen, während ich die Zerstörung des Kerns vorbereite. Sironas der Eredar ist auch irgendwo dort oben.]], [[Ich werde Euch nicht weniger schätzen, wenn Ihr jetzt einfach geht, <name>.]], [[Vielleicht seid Ihr aber genau die Person, die die Vektorspule zerstören und Sironas zu Fall bringen kann. Seid Ihr es?]], },
    { [[Folgt und beschützt Demolierer Legoso, während er versucht, die Vektorspule zu zerstören und tötet Sironas. Kehrt zu Exarch Admetius bei der Blutwacht zurück, sobald Ihr Eure Aufgabe erfüllt habt.]], },
  },
  [9760] = {
    [[Verteidigers Ruh']],
    { [[Ich würde Euch nicht bitten, die Vektorspule und Sironas im Alleingang zu vernichten, <name>, dennoch glaube ich, dass Ihr uns helfen könnt, den Weg für andere abzusichern, die solch einen gefährlichen Auftrag auf sich nehmen werden.]], [[Nehmt die Straße, die nördlich aus der Blutwacht führt, und folgt dem Verlauf bis Ihr auf die Weggabelung stoßt. Biegt auf die linke Straße ab und geht solange weiter bis Ihr zu Verteidigers Ruh' kommt - es liegt leicht westlich von der Straße selbst. Corin wird dort auf Euch warten.]], },
    { [[Sprecht mit Verteidigerin Corin bei Verteidigers Ruh' auf der Blutmythosinsel.]], },
  },
  [9761] = {
    [[Den Weg frei machen]],
    { [[Nur die wirklich Kampferprobten, Edelmütigen und Furchtlosen werden sich Legosos Auftrag stellen. Der Exarch wird keinen Agenten der Hand damit beauftragen, die Vektorspule zu zerstören und Sironas zu stürzen. Er lehnt es ab, Draenei in ihren Tod zu schicken - genau darum geht es bei diesem Auftrag: Selbstmord...]], [[<Corin seufzt.>]], [[Ich bin dafür verantwortlich, die Verteidigung der Sonnenfalken zu schwächen. Eure Aufgabe besteht darin, Sonnenfalken nahe der Vektorspule zu töten und somit den Weg für unseren Helden frei zu machen - wer immer es auch sein mag.]], },
    { [[Verteidigerin Corin bei Verteidigers Ruh' möchte, dass Ihr 8 Saboteure der Sonnenfalken und 8 Agenten der Sonnenfalken tötet.]], },
  },
  [9762] = {
    [[Die ungeschriebene Prophezeiung]],
  },
  [9763] = {
    [[Das Versteck des Kriegsherren]],
    { [[Es hat Vorteile, sich heimlich am Eingang zum Versteck des Feindes zu verstecken.]], [[Vor ein paar Stunden sah ich eine Gruppe von Naga durch den steinernen Torbogen am zweiten Tunnel von links gehen. Ich bin in ihrer Sprache nicht sehr bewandert, aber ein Wort habe ich sehr deutlich verstanden: Kalithresh.]], [[Das muss der Eingang zu den Dampfkammern sein, von denen aus Kriegsherr Kalithresh seine Truppen befehligt. Ihr müsst ihn finden und aufhalten! Ich bin leider zu schwach, um Euch dabei helfen zu können.]], },
    { [[Behüterin Jhang möchte, dass Ihr Kriegsherr Kalithresh im Echsenkessel findet und tötet.]], },
  },
  [9764] = {
    [[Befehle von Lady Vashj]],
    { [[Dieser Brief enthält genaue Anweisungen für die Naga des Echsenkessels, die die Dampfpumpen bedienen. Er enthält eine Warnung vor der Ankunft von Druiden des Cenarius in dem Gebiet und die Anweisung, sie unverzüglich zu töten. Die Geschwindigkeit, mit der das Wasser aus den Seen in den Echsenkessel abgepumpt wird, soll verdoppelt werden. Die Befehle tragen das Siegel der berüchtigten Lady Vashj.]], [[Dieser Brief dürfte die Druiden der Expedition des Cenarius sehr interessieren. Bringt ihn zur Leiterin der Expedition, Ysiel Windsänger.]], },
    { [[Bringt die Befehle von Lady Vashj zu Ysiel Windsänger in der Zuflucht des Cenarius in den Zangarmarschen.]], },
  },
  [9765] = {
    [[Kriegsvorbereitungen]],
    { [[Die Expedition des Cenarius fing einst als friedliches Projekt an, doch nun stehen wir am Rande eines ausgewachsenen Kriegs gegen die Naga.]], [[Unter uns sind einige, die zwar gerne kämpfen würden, aber nur sehr schlecht ausgerüstet sind. Bringt mir die Waffen der Naga, damit wir sie zu Gleven und Pfeilspitzen umschmelzen können.]], [[Nur Waffen aus hochwertigem Material können für uns nützlich sein, <name>. Ihr werdet die der Naga im Echsenkessel stehlen müssen.]], },
    { [[Besorgt die Waffen des Echsenkessels von den Naga im Echsenkessel und kehrt dann zu Ysiel Windsänger in der Zuflucht des Cenarius in den Zangarmarschen zurück.]], },
  },
  [9766] = {
    [[Waffen des Echsenkessels]],
  },
  [9767] = {
    [[Den Feind kennen]],
    { [[Um einen Feind zu besiegen braucht man weit mehr als eine Überzahl an Männern oder Kraft. Oft hängt der Sieg davon ab, ob man die Schwächen des Feindes oder seine typischen Vorgehensweisen kennt.]], [[Wir wissen zwar viel über die Naga, aber ihre Magie bleibt uns immer noch fremd.]], [[Ich habe vor, mich mit der Hydromantie der Naga des Echsenkessels auseinanderzusetzen. Bringt mir eines ihrer Zauberbücher, damit ich mehr darüber erfahren kann.]], },
    { [[Besorgt ein Zauberbuch des Echsenkessels von den Naga im Echsenkessel und bringt es zu Ashyen in der Zuflucht des Cenarius in den Zangarmarschen.]], },
  },
  [9769] = {
    [[Mode kann man nicht erklären]],
    { [[<Magasha kichert still in sich hinein.>]], [[Ich wette, wenn sie wüssten, dass sie Insektenteile anhaben, wäre der Trend schnell vorbei.]], [[Zuerst haben wir angefangen, die Insekten in der Umgebung zu töten, weil sie bei den Bauarbeiten im Weg waren. Eines Tages kam ein geschäftstüchtiger Händler hierher, blieb über Nacht und plötzlich fing er an alle Insektenflügel, die er bekommen konnte, aufzukaufen!]], [[Sagte, er mache Kleidung daraus. Ich bleib lieber bei meiner Rüstung, danke. Aber wenn Ihr die Flügel der Glühwürmchenarten in der Umgebung sammelt, kaufe ich sie Euch gerne ab.]], },
    { [[Bringt 8 lichtdurchlässige Flügel von einer beliebigen Glühwürmchenart der Zangarmarschen zu Magasha im Sumpfrattenposten.]], },
  },
  [9770] = {
    [[Bedrohliche Marschenfänge]],
    { [[Manchmal wünschte ich, Denjai hätte uns einen Schamanen mitgeschickt oder uns wenigstens ein Totem gegeben, um die Marschfangreißer fernzuhalten.]], [[Es wimmelt hier nur so von diesem Ungeziefer! Das ist doch nicht gerecht. Sollte nicht irgendwas diese widerlichen Käfer auffressen?]], [[Wir haben alle schon abwechselnd Kammerjäger gespielt. Ihr seid genau zur richtigen Zeit gekommen, <name>. Glückwunsch! Und jetzt geht los und wischt ein paar Marschfänge auf!]], },
    { [[Reavij im Sumpfrattenposten möchte, dass Ihr 10 Marschfangreißer tötet.]], },
  },
  [9771] = {
    [[Auf der Suche nach Späher Jyoba]],
    { [[Jetzt, da Ihr mit dem Gelände vertraut seid, würde ich Euch gerne bei einer anderen Sache um Hilfe bitten. Das Todesmoor, so nennen wir das Gebiet im Nordwesten, ist ein schauriger Ort. Niemand hat dort je ein Lebewesen gesehen, aber es war auch nie jemand lange dort.]], [[Späher Jyoba hat sich freiwillig gemeldet, um das Gebiet zu untersuchen und von seinen Beobachtungen zu berichten. Er hat den Posten vor drei Tagen verlassen und ist noch nicht zurückgekehrt. Er würde nie so lange wegbleiben ohne einen Bericht zu schicken. Könntet Ihr nach ihm suchen, <name>?]], },
    { [[Search for Scout Jyoba.]], },
  },
  [9772] = {
    [[Jyobas Bericht]],
    { [[Fetzen von welkem Pilz befinden sich überall auf dem Körper von Späher Jyoba. Was auch immer ihn getötet hat muss aus demselben siechenden Pilz bestehen wie die riesigen welkenden Pilze des Todesmoors. Vielleicht hat ihn einer der siechenden Riesen überrascht, als er gerade seinen Bericht schreiben wollte.]], [[Ohne den Text des Berichts ist es fast unmöglich herauszufinden, was der Späher entdeckt hat. Wenn der Bericht gefunden werden könnte, sollte er unbedingt zu Zurai gebracht werden.]], },
    { [[Besorgt Späher Jyobas Bericht von den siechenden Riesen und kehrt dann zu Zurai im Sumpfrattenposten zurück.]], },
  },
  [9773] = {
    [[Keine Pilze mehr!]],
    { [[Pilzsuppe, Pilzeintopf, Pilzsoße!]], [[Pilze, Pilze, Pilze! Was anderes bekommt man hier nicht zu essen. Wenn man sagt, dass man Hunger hat, drückt der Koch einem eine Axt in die Hand und sagt: Schlag Dir Dein Abendessen selbst!]], [[Nun, ich habe Pilze satt. Ich habe Fische in den Seen hier gesehen. Klar, sie sehen aus, als würden sie sich wehren, aber sie müssen einfach besser schmecken als Pilze!]], [[Wie viel würde es mich kosten Euch zu beauftragen, mir mein erstes anständiges Abendessen seit Wochen zu bringen?]], },
    { [[Bringt 8 Aalfilets von Umbrafennaalen zu Magasha im Sumpfrattenposten.]], },
  },
  [9774] = {
    [[Dicke Hydraschuppen]],
    { [[Es war sehr klug von Reavij Lederrüstung mitzubringen. Der Sumpf wird meine Panzerrüstung innerhalb von kurzer Zeit wertlos machen.]], [[Ich habe schon so weit ich konnte nach und nach alle Schuppen ausgetauscht. Die Hydras in dem Marschen haben dicke, schützende Schuppen, die der Feuchtigkeit ihrer Heimat optimal standhalten.]], [[Geeignete Schuppen zu finden ist allerdings ziemlich langwierig. Oftmals sind sie zu kaputt, zu abgenutzt oder zu dünn um sie als Rüstung zu verwenden. Bringt mir ein paar geeignete Schuppen von den Hydras in den größeren Seen.]], },
    { [[Bringt 12 dicke Hydraschuppen zu Zurai im Sumpfrattenposten.]], },
  },
  [9775] = {
    [[Bericht an Schattenjäger Denjai]],
    { [[Schattenjäger Denjai erwartet sicher einen Bericht über meine Fortschritte bei der Errichtung unseres Außenpostens. Ich habe eine Zusammenfassung unserer bisherigen Errungenschaften vorbereitet, aber seit wir Späher Jyobe verloren haben, kann ich keinen Boten entbehren, um den Bericht zu Denjais Posten Zabra'jin im Westen zu bringen.]], [[Wenn Ihr in die Richtung geht, könntet Ihr ihm den Bericht vielleicht von mir geben? Schattenjäger Denjai kann auf den ersten Blick etwas einschüchternd wirken, aber wenn Ihr Euch an ihn gewöhnt habt, ist er kein übler Kerl.]], },
    { [[Bring Zurai's Report to Shadow Hunter Denjai in Zabra'jin]], },
  },
  [9776] = {
    [[Die Oreborzuflucht]],
    { [[Wie Ihr sicher wisst, sind viele Einwohner von Telredor Flüchtlinge aus dem Krieg, der die Draenei dezimiert hat. Es ist mein Ziel, Telredor als sicheren Hafen für all unsere Verbündeten in diesem Kampf zu gewinnen. Es gibt einige, die sich uns gerne anschließen möchten, aber spüren, dass sie noch nicht bereit sind.]], [[Wir schulden ihnen jede Hilfe, die wir ihnen bieten können, <name>. Eine Gruppe von Zerschlagenen unter der Führung von Ikuti hat in den Ruinen eines alten Tempels nordwestlich vom Schlangensee Zuflucht gefunden. Sucht sie und reicht ihnen Eure Hand in Freundschaft.]], },
    { [[Speak to Ikuti at the Orebor Harborage.]], },
  },
  [9777] = {
    [[Fulgorsporen]],
    { [[Es gibt nur wenig Brennbares in diesem Sumpf, <race>, daher müssen wir auf andere Mittel zugreifen, um Telredor zu erleuchten. Viele Tiere und Pflanzen in den Marschen strahlen ein schwaches Leuchten aus.]], [[Es ist schwierig und grausam die leuchtenden Tiere einzusperren, deshalb haben wir uns einen Pilz als Lichtquelle gesucht.]], [[Wir bauen die Fulgorpilze hier an, aber ab und zu benötigen wir auch neue Pflanzen. Die kleinen grünen Pilze wachsen an den größeren in der Nähe von Telredor.]], },
    { [[Bringt 6 Fulgorsporen zu Ruam in Telredor.]], },
  },
  [9778] = {
    [[Aufseher Hamuht]],
    { [[Sprecht auf jeden Fall noch mit Aufseher Hamuht, bevor Ihr die Zuflucht des Cenarius verlasst. Er ist für die Sicherheit der Expedition verantwortlich.]], [[Er würde mir nie verzeihen, wenn ich einen erfahrenen Abenteurer wie Euch nicht zu ihm schicken würde.]], },
    { [[Meldet Euch bei Aufseher Hamuht in der Zuflucht des Cenarius in den Zangarmarschen.]], },
  },
  [9779] = {
    [[Abfangen der Nachricht]],
    { [[Schön, Euch wiederzusehen, <name>. Ich hätte da eine heikle Aufgabe, die es zu lösen gilt.]], [[Unsere Agenten, die die Sonnenfalken ausspionieren, haben berichtet, dass Soldaten der Sonnenfalken ein ungewöhnliches Dokument untersucht haben.]], [[Unglücklicherweise konnten wir keine Kopie dieses Schreibens für uns selbst beschaffen. Ihr müsst für mich vor Ort gehen und das Dokument an Euch bringen. Ich bin mir sicher, dass Euch ein Sonnenfalke die von uns gesuchten Informationen geben wird, wenn Ihr genug Druck anwendet. Ich überlasse den Einsatz von Gewalt voll und ganz Eurem Ermessen.]], },
    { [[Tötet die Elfen der Sonnenfalken, bis Ihr das Schreiben der Sonnenfalken findet, und bringt es zu Verteidiger Boros bei der Blutwacht.]], },
  },
  [9780] = {
    [[Aalfilets von Umbrafenn]],
    { [[Schön, ein neues Gesicht hier zu sehen. Als Haalrun und ich nach Telredor kamen, waren wir die ersten von außerhalb, aber das scheint schon eine Ewigkeit her zu sein.]], [[Trotzdem heißen wir Neuankömmlinge immer willkommen und versuchen dafür zu sorgen, dass alle genug zu essen haben. Wir werden die Freundlichkeit der Anachoreten von Telredor niemals vergelten können, aber wir können ihrem Beispiel folgen und ebenso freundlich zu anderen sein.]], [[Wenn Ihr helfen möchtet, dann fangt ein paar der Fische, die sich in den Seen in der Gegend tummeln. Sie heißen Umbrafennaale und sind Hauptbestandteil unserer Nahrung.]], },
    { [[Bringt 8 Aalfilets zu Noraani in Telredor.]], },
  },
  [9781] = {
    [[Zu viele Mäuler zu stopfen]],
    { [[Auf meiner letzten Jagd habe ich gesehen, dass eine Gruppe von Trollen im Begriff ist, einen neuen Außenposten zu bauen. Ohne Zweifel haben sie auch angefangen, hier zu jagen.]], [[Wenn sie einen dauerhaften Standort hier errichten, wird es einen erbitterten Kampf um Nahrung geben. Wir können sie nicht direkt angreifen, ohne sie zu provozieren. Aber wir können unsere natürlichen Wettbewerber im Nahrungskampf reduzieren.]], [[Jede Morasthydra, die Ihr erlegt, bedeutet drei Mäuler weniger, die gestopft werden müssen!]], },
    { [[Tötet 10 Morasthydras und kehrt zu Haalrun in Telredor zurück.]], },
  },
  [9782] = {
    [[Das Todesmoor]],
    { [[Ich lebe nun seit einiger Zeit bei den Anachoreten von Telredor, <name>, und inzwischen kenne ich die Marschen ganz gut. In letzter Zeit habe ich eine erschreckende Veränderung des Landes im Nordosten bemerkt.]], [[Fast alles Leben dort hat begonnen zu welken und zu sterben, und das scheinbar ohne Grund. Ich habe ein paar Vermutungen, was dort vorgehen könnte, aber sie sind nur sehr vage. Wenn Ihr mir eine Bodenprobe dieser Gegend bringen könntet, dann könnte ich diesem Rätsel vielleicht auf den Grund kommen.]], },
    { [[Bringt eine Erdprobe vom Todesmoor zu Verteidiger Idaar in Telredor.]], },
  },
  [9783] = {
    [[Eine unnatürliche Dürre]],
    { [[Die Erdprobe, die Ihr mir gebracht habt, ist fast ausgetrocknet, <name>. Wenn schon der Boden seine Feuchtigkeit verliert, müssen die Lebewesen im Todesmoor furchtbar leiden.]], [[Ich habe gesehen, was passiert, wenn die Fungusgiganten der Gegend anfangen zu welken und zu sterben. Der Schmerz macht sie wahnsinnig und bringt sie dazu, alles in ihrer Nähe anzugreifen.]], [[Diese Lebewesen haben nicht die leiseste Ahnung, was mit ihnen geschieht. Sie zu retten liegt außerhalb unserer Macht. Das Einzige, was wir tun können, ist ihr Leiden zu beenden.]], },
    { [[Verteidiger Idaar möchte, dass Ihr 12 siechende Riesen tötet.]], },
  },
  [9784] = {
    [[Pflanzenteile identifizieren]],
  },
  [9785] = {
    [[Der Segen der Uralten]],
    { [[Ihr habt Euch das Vertrauen der Expedition des Cenarius verdient, <name>. Es ist an der Zeit, dass Ihr Euch mit den Urtumen unterhaltet, die sich unserer Sache angeschlossen haben, Ashyen und Keleth, und um ihren Segen bittet.]], [[Das Zeichen eines Urtums zu tragen bedeutet große Macht. Je höher Euer Ansehen bei uns steigt, umso wertvoller wird dieser Gefallen werden.]], [[Geht, <name>, und bittet die Urtume um ihren Segen.]], },
    { [[Windrufer Stolzhuf möchte, dass Ihr mit den Urtumen Ashyen und Keleth in der Zuflucht des Cenarius sprecht und um ihren Segen bittet.]], },
  },
  [9786] = {
    [[Die Ruinen von Boha'mu]],
    { [[Bevor mein Volk von den Orcs dezimiert wurde, diente Telredor als Zuflucht für die Anachoreten und Spiritualisten der Draenei. Ein kleiner Orden von Anachoreten und Verteidigern pflegte ein paar Schreine in dieser Region.]], [[Während des Krieges verloren wir den Kontakt zu den Anachoreten, die für diese Schreine zuständig waren, besonders zu denen im Tempel von Boha'mu im Südwesten. Ich habe gehört, dass ein Stamm von Verirrten sich in dieser Gegend niedergelassen hat und vielleicht sogar in den Ruinen lebt. Geht zu den Ruinen von Boha'mu und berichtet mir, was Ihr dort findet.]], },
    { [[Anachoret Ahuurn in Telredor möchte, dass Ihr die Ruinen von Boha'mu erforscht.]], },
  },
  [9787] = {
    [[Götzen des Wildfennstamms]],
    { [[Aus Eurem Bericht über die Ruinen von Boha'mu hörte ich heraus, dass dieser Stamm der Verirrten zur Verehrung von Tiergeistern zurückgekehrt ist. Bevor wir auf sie zugehen können, müssen wir mehr über ihren Glauben in Erfahrung bringen.]], [[Kehrt zurück nach Wildfenn und sammelt heimlich ein paar ihrer Götzen, damit ich sie untersuchen kann. Versucht jedoch, sie nicht wütend zu machen, sonst haben wir vielleicht keine Chance mehr, Kontakt mit ihnen aufzunehmen.]], },
    { [[Sammelt 6 Wildfenngötzen und kehrt dann zu Anachoret Ahuurn in Telredor zurück.]], },
  },
  [9788] = {
    [[Ein feuchter, düsterer Ort]],
    { [[Bald nachdem ich dem Umbrafennstamm entkommen war, habe ich mich in einer Höhle südlich des Dorfs verkrochen. Ich hatte ja keine Ahnung, dass die Tiere der Marschen und die Sporenmänner sich um die Höhle stritten!]], [[Ich musste sehr plötzlich von dort fliehen und eine Kiste mit meinem weltlichen Besitz zurücklassen.]], [[Es grenzte schon an Ironie, dass ich mit meinem ganzen Hab und Gut aus Umbrafenn entkommen konnte, nur um es so bald darauf wieder zu verlieren. Wenn Ihr mir meine Sachen wiederbringen könntet, würde ich Euch etwas aus meinem persönlichen Besitz schenken. Seid Ihr einverstanden, <name>?]], },
    { [[Sucht nach Ikeyens Habseligkeiten in einer Höhle südlich von Umbrafenn. Bringt sie zurück zu Ikeyen in der Zuflucht des Cenarius in den Zangarmarschen.]], },
  },
  [9789] = {
    [[Beherrschen der Grollhufjagd]],
    { [[Ihr möchtet Euch einen Platz in der Safari verdienen? Dann beweist Euch erst einmal.]], [[Mir ist es völlig schnuppe, was Ihr schon alles für Kreaturen erlegt habt, bevor Ihr nicht jagt, was wir jagen, seid Ihr in meinen Augen <kein Jäger/keine Jägerin>. Die Grollhufe wären ein guter Anfang, sofern Ihr <Manns genug/ mutig genug> seid.]], [[Ich bin mir sicher, dass ich sie in der direkten Umgebung der Safari und in der Nähe im Süden gesehen habe.]], },
    { [[Hunt down 30 Clefthoof and return to Hemet Nesingwary at Nesingwary's Safari in Nagrand to prove yourself.]], },
  },
  [9790] = {
    [[Lichtdurchlässige Flügel]],
    { [[An so einem abgeschiedenen Ort zu leben hat uns gelehrt, einen Nutzen aus allen verfügbaren Dingen zu ziehen. Dabei muss man natürlich lernen zu vergessen, woher einige der Materialien stammen.]], [[Ich habe herausgefunden, dass die Flügel der Umbraglimmstecher, die hier in der Gegend leben, sehr gut als Regenschutz dienen. Sie sind stabil, dünn und biegsam.]], [[<Haalrun kichert.>]], [[Ich sehe Euren Gesichtsausruck. Wie ich sagte, man muss sich erst an die Idee gewöhnen. Wenn Ihr in der Gegend herumstreicht, könnt Ihr mir dann vielleicht ein paar solcher Flügel mitbringen?]], },
    { [[Bringt 8 lichtdurchlässige Flügel von einer beliebigen Glühwürmchenart der Zangarmarschen zu Haalrun in Telredor.]], },
  },
  [9791] = {
    [[Bedrohliche Marschenfänge]],
    { [[Während der Kriege der Orcs gegen mein Volk haben die Nahrungssammler Telredor aus Angst, angegriffen oder gefangen zu werden, kaum verlassen. Nun gehen sie häufiger hinaus, aber die Marschen sind nicht weniger gefährlich.]], [[Es scheint, als wären die Marschenfänge in letzter Zeit aggressiver geworden. Ich weiß nicht, ob das an der erhöhten Konkurrenz für ihre gewöhnliche Nahrung liegt, aber sie greifen regelmäßig unsere Nahrungssucher an. Vielleicht würden sie uns in Ruhe lassen, wenn wir ihren Bestand um Telredor herum etwas dezimieren könnten.]], },
    { [[Tötet 10 Marschenfangreißer und kehrt dann zu Noraani in Telredor zurück.]], },
  },
  [9792] = {
    [[Eine Botschaft für Telaar]],
    { [[Ihr habt Euch in unseren Geschäften als vertrauenswürdig erwiesen, <name>, und das in Zeiten, in denen das Vertrauen meines Volkes nur schwer zu gewinnen ist.]], [[Eine große Gruppe meiner Brüder und Schwestern hat sich in der alten Draeneistadt Telaar in Nagrand im Süden niedergelassen. Um nach Telaar zu gelangen, müsst Ihr der Straße Richtung Süden nach Nagrand folgen und dann nach Südosten gehen, bis Ihr an die Klippen östlich des Oshu'guns gelangt.]], [[Jeder, der uns so sehr geholfen hat wie Ihr, ist dort willkommen.]], [[Sucht Aufseher Moi'bff Jill und sagt ihm, dass Ihr mein Vertrauen erlangt habt.]], },
    { [[Sprecht mit Aufseher Moi'bff in Telaar in Nagrand.]], },
  },
  [9793] = {
    [[Das Schicksal von Tuurem]],
    { [[Prophet Velens Suche nach Verbündeten hat die Draenei in der Scherbenwelt ermutigt, selbst eine Suche zu beginnen. Der Krieg meines Volks mit der Legion hat uns vielleicht aus unseren Städten und Tempeln vertrieben, aber er konnte nicht alle Spuren unserer Zivilisation verwischen.]], [[Tuurem, mein früheres Zuhause in den Wäldern von Terokkar, war eine der Städte, die von den Orcs überrannt wurden. Ich habe schon viel zu lange nichts mehr von dort gehört. Bringt diesen Brief zu Andarl in der Festung in Terokkar und schaut, was Ihr herausfinden könnt.]], },
    { [[Bringt Verteidiger Idaars Brief zu Andarl in der Festung in den Wäldern von Terokkar.]], },
  },
  [9794] = {
    [[Keine Zeit für Neugier]],
    { [[<name>, ich muss Euch um einen Gefallen bitten. Normalerweise würde ich das ja nicht tun, aber ich habe beide Hände bereits mehr als voll.]], [[Ich habe einen Verbündeten namens Kialon in Sylvanaar im Schergrat im Norden. Er erwartet, dass ich mich in naher Zukunft bei ihm melde, obwohl ich fürchte, dass ihm nicht gefallen wird, was ich zu sagen habe.]], [[Wenn Ihr ihm meine Antwort bringen könntet, wäre ich Euch sehr dankbar. Ich muss jedoch darauf bestehen, und das ist sehr wichtig, dass Ihr den Brief nicht lest. Er ist nur für Kialons Augen bestimmt.]], },
    { [[Bringt Timothys Brief zu Kialon in Sylvanaar im Schergrat.]], },
  },
  [9795] = {
    [[Die Ogerbedrohung]],
    { [[Als ob die Naga und ihre Verbündeten nicht schon genug wären, scheinen nun auch die Oger sich weiter in den Marschen auszubreiten. Sie haben schon weite Teile des Nordwestens erobert, und der Puffer zwischen uns und ihnen wird immer kleiner.]], [[Für jeden, den wir umbringen, sind gleich mehrere neue zur Stelle. Ich weiß nicht, wie lange wir Zabra'jin halten können, wenn sie sich entschließen uns anzugreifen. Geht nach Norden in die Berge zum orcischen Stützpunkt in der Donnerfeste und berichtet Nekthar von unserer Bedrängnis. Jede Hilfe, die er uns zur Verfügung stellen kann, ist äußerst willkommen.]], },
    { [[Reist zur Donnerfeste im Schergrat und sprecht mit Nekthar.]], },
  },
  [9796] = {
    [[Nachricht aus den Zangarmarschen]],
    { [[Schattenjäger Denjai ist nicht der einzige, der auf einen Bericht von mir wartet. Mokasa in der Steinbrecherfeste in Terokkar brennt darauf, von unseren Fortschritten zu hören, besonders seit die meisten ihrer Boten hier auf ihren Reisen vorbeikommen.]], [[Sie hat mich gebeten, ihr Bescheid zu geben, sobald der Sumpfrattenposten fertig ist. Wenn Ihr nichts dagegen habt, wäre ich Euch sehr dankbar, wenn Ihr ihr die Nachricht überbringen könntet. Wir haben zwar soweit schon alles aufgebaut, aber es gibt noch viel zu tun.]], [[Um zur Steinbrecherfeste zu gelangen, müsst Ihr der Straße nach Süden in Richtung Zangarmarschen folgen und Euch durch die Wälder von Terokkar an Tuurem vorbei begeben.]], },
    { [[Sprecht mit Mokasa in der Steinbrecherfeste in den Wäldern von Terokkar.]], },
  },
  [9797] = {
    [[Verstärkung für Garadar]],
    { [[<Immer noch nach Atem ringend bedeutet der Bote Euch, Euch hinzusetzen.>]], [[Sie werden vielleicht nicht überleben, <race>! Geht so schnell Euch Eure Beine tragen können nach Garadar.]], [[<Als Reaktion auf Euren fragenden Gesichtsausdruck führt der Bote näher aus.>]], [[Das Zuhause der Mag'har liegt im Süden, in Nagrand. Wir haben ein paar Leute dort, aber es sind nur wenige, und unsere Verbündeten der Mag'har werden von allen Seiten von Feinden angegriffen. Sucht dort unseren Kommandanten, Hauptmann Kroghan, und helft ihm, wo Ihr nur könnt! Garadar darf nicht fallen!]], },
    { [[Sprecht mit Hauptmann Kroghan in Garadar in Nagrand.]], },
  },
  [9798] = {
    [[Blutelfenpläne]],
    { [[Diese Pläne sehen wichtig aus. Ihr solltet sie Verteidiger Aldar bei der Absturzstelle zeigen.]], },
    { [[Bringt die Blutelfenpläne zu Verteidiger Aldar bei der Absturzstelle im Am'mental.]], },
  },
  [9799] = {
    [[Botanische Hilfsarbeit]],
    { [[Die Am'menfelder im Westen sind das wichtigste Vermehrungsgebiet der einheimischen Peitscherpopulation. Botanikerin Taerix befürchtet, dass sich die Peitscher auf Grund des verseuchten Grundwasserspiegels nicht mehr fortpflanzen können, nachdem wir den See gereinigt haben.]], [[Geht zu den Am'menfeldern und pflückt dort ein paar der verderbten Blumen für mich, damit ich sie untersuchen und sehen kann, was es zu tun gilt. Ihr solltet sie an ihren ungewöhnlichen roten Blüten erkennen.]], },
    { [[Sammelt 3 verderbte Blumen und kehrt zu Lehrling Vishael bei der Absturzstelle im Am'mental zurück.]], },
  },
  [9800] = {
    [[Eine seltene Bohne]],
    { [[Ihr seid zu schlecht ausgerüstet um den rastlosen Elementaren Nagrands gegenüber zu treten. Bevor Ihr dem Irdenen Ring helfen könnt, müsst Ihr erst Euch selbst helfen.]], [[Im Westen findet Ihr Kothaufen von den Wildtieren Nagrands. Ihr müsst den Dung nach den verdauten Überresten der Caracolibohne durchsuchen. Aus dieser Bohne kann ich eine Tablette herstellen, die Euch im Kampf gegen die ruhelosen Naturgewalten helfen wird.]], [[Kehrt zu mir zurück, wenn Ihr mindestens zwei Fäuste voller Caracolibohnen gesammelt habt... Ogerfäuste!]], },
    { [[Elementalist Lo'ap at the Throne of the Elements in Nagrand wants you to bring him 15 Digested Caracoli.]], },
  },
  [9801] = {
    [[Die Reagenzien sammeln]],
    { [[Ich habe das Gefühl, dass diese Verirrten nicht so gut auf <einen/eine> Draenei zu sprechen sein werden, aber vielleicht auf einen Arakkoa. Ich kann einen Zaubertrank herstellen, der Euch für kurze Zeit in einen Arakkoa verwandeln kann. Dazu benötige ich ein paar Reagenzien, die man nur sehr schlecht lagern kann, da man sie sofort nach dem Sammeln weiterverarbeiten muss.]], [[Ihr werdet mir ein paar Sporensegleraugen und einige Fennschreitertentakel bringen müssen. Beide Arten leben in der Wildnis um Telredor herum.]], },
    { [[Anchorite Ahuurn at Telredor wants you to bring him 4 Spore Bat Eyes and 4 Fen Strider Tentacles.]], },
  },
  [9802] = {
    [[Pflanzen der Zangarmarschen]],
    { [[Die Seen der Zangarmarschen werden langsam entwässert. Wir wissen nicht warum, aber viele Pflanzenarten drohen auszusterben.]], [[Als Katalogisiererin der Expedition ist es meine Aufgabe, diese Pflanzen zu identifizieren und zu verstehen, was mit ihnen geschieht.]], [[Wenn Ihr helfen wollt, bringt mir unbekannte Pflanzenteile. Wenn Ihr kräuterkundig seid, könnt Ihr sie direkt vom Boden pflücken, aber auch die einheimischen Wesen des Sumpfs tragen Pflanzenteile bei sich.]], },
    { [[Bringt 10 unbekannte Pflanzenteile zu Lauranna Thar'well in der Zuflucht des Cenarius in den Zangarmarschen.]], },
  },
  [9803] = {
    [[Botschaft für den Wildfennstamm]],
    { [[Es ist an der Zeit, dem Wildfennstamm die Hand zu reichen und sie zu lehren, dass das Licht sie nicht aufgegeben hat. Nehmt den Trank, den ich vorbereitet habe, und tragt ihn zu den Ruinen von Boha'mu.]], [[Sobald Ihr bei den Ruinen angekommen seid, trinkt den Trank um Euch in einen der Vogelgeister zu verwandeln. Sprecht mit dem Ältesten Kuruti und lasst ihn wissen, dass die Draenei bereit sind, sein Volk als verloren geglaubte Brüder und Schwestern zu empfangen.]], },
    { [[Trinkt Ahuurns Elixier in den Ruinen von Boha'mu. Sprecht mit dem Ältesten Kuruti in der Form eines Arakkoas.]], },
  },
  [9804] = {
    [[Aufgebrachte Geister des Himmelsweisensees]],
    { [[Die Unruhen in den Gezeiten der Gewässer von Nagrand hat die Wassergeister des Himmelsweisensees sehr aufgebracht. Sie haben im Gegenzug damit begonnen, Verwüstungen entlang der Wasserwege anzurichten.]], [[Watoosun des Wassers ist uns in einer Vision erschienen und hat uns erlaubt, das Problem zu lösen, solange wir auch die Ursache dafür beheben...]], [[Wir werden uns um die Ursache kümmern, doch zuerst müssen wir die direkten Bedrohungen für den Thron beseitigen. Zerstört die aufgebrachten Seegeister und kehrt dann zu mir zurück.]], },
    { [[Elementalist Lo'ap at the Throne of the Elements in Nagrand wants you to destroy 12 Lake Spirits.]], },
  },
  [9805] = {
    [[Segen des Incineratus]],
    { [[Die erbärmlichen Verirrten, einst verbannt in die Sümpfe der Zangarmarschen, versuchen nun, ihren Dreck auch in andere Teile der Welt zu tragen.]], [[Ich habe mich an die weisen Furien gewandt und ihren Segen erhalten. Um die Ursache zu beseitigen, müssen wir die Elemente auf sie hetzen!]], [[Ich gebe Euch beseeltes Feuer, entzündet an der feurigen Essenz von Incineratus selbst. Reist nach Schilftanz, südöstlich von hier, und entzündet die Hütten mit dem Feuer! Lasst sie die Wut der Elemente spüren!]], },
    { [[Elementarist Lo'ap am Thron der Elemente in Nagrand möchte, dass Ihr die große Hütte, die westliche Hütte, die südliche Hütte und die östliche Hütte in Schilftanz zerstört.]], [[Bringt das beseelte Feuer zurück zu Elementarist Lo'ap, wenn Ihr fertig seid.]], },
  },
  [9806] = {
    [[Fruchtbare Sporen]],
    { [[Wenn sie zwischen den Langstielpilzen hindurchwandern, sammeln sich allerlei fruchtbare Sporen an den Gliedmaßen der Sporensegler und Marschenläufer.]], [[Wie Ihr sehen könnt, ist unser Volk sehr klein, also haben wir nur wenige Möglichkeiten, diese Sporen zu sammeln. Haltet nach Sporenseglern und Marschenläufern Ausschau und sammelt die fruchtbaren Sporen. Ganz Sporeggar stünde tief in Eurer Schuld.]], },
    { [[Sammelt 6 fruchtbare Sporen von den Sporenseglern und den Marschenläufern und kehrt danach zu Gshaff in Sporeggar zurück.]], },
  },
  [9807] = {
    [[Noch mehr fruchtbare Sporen]],
  },
  [9808] = {
    [[Glühkappenpilze]],
    { [[Ich habe gesehen, dass Ihr glänzende runde Metallstücke benutzt, um sie gegen Dinge einzutauschen, die Ihr haben möchtet. Wir benutzen glänzende runde Pilze als Zahlungsmittel.]], [[Die Glühkappenpilze wachsen überall in den Marschen, man muss nur wissen, wie man sie richtig sammelt ohne sie zu zerstören. Ich werde Euch beibringen, wie man das tut, wenn Ihr mir versprecht, ein paar für unsere Vorräte zu sammeln. Einverstanden, <name>?]], },
    { [[Bringt 10 Glühkappen zu Msshi'fn in Sporeggar in den Zangarmarschen.]], },
  },
  [9809] = {
    [[Noch mehr Glühkappen]],
  },
  [9810] = {
    [[Der verschmutzte Geist]],
    { [[Watoosun konnte zwar den Seuchengestank loswerden, aber gleichzeitig erschuf er eine irrsinnige Geisteressenz. Eine Geisteressenz, die aus der übelsten Verderbnis entstanden ist, die es je unter Elementaren gegeben hat!]], [[Ihr könnt diesen verschmutzten Sumpfgeist südwestlich von hier, tief im Sonnenwindsee, finden.]], [[Sucht und vernichtet Watoosuns verschmutzte Essenz sowie jeglichen verseuchten Nachfahren, den sie erschaffen haben möge.]], },
    { [[Elementalist Lo'ap at the Throne of the Elements in Nagrand wants you to destroy Watoosun's Polluted Essence and 10 Lake Surgers.]], },
  },
  [9811] = {
    [[Freund der Sin'dorei]],
    { [[Dies ist der Beginn eines neuen Zeitalters in der Geschichte der Blutelfen <name>. Ihr habt geholfen, den Bund zwischen unserem und Eurem Volk zu festigen, <race>.]], [[Bringt den Kopf des Verräters nach Silbermond zum Lordregenten Lor'themar Theron. Er wird begierig sein, die Neuigkeit zu erfahren.]], },
    { [[Bringt Dar'Khans Kopf zu Lor'themar Theron in Silbermond.]], },
  },
  [9812] = {
    [[Gesandter zur Horde]],
    { [[Unter unseren neuen möglichen Verbündeten regen sich Zweifel über unsere Fähigkeiten. Wie könnten wir ihnen helfen, wenn sie glauben, dass wir nicht einmal mit unseren Problemen zuhause fertigwerden?]], [[Das ändert alles. Wir lassen keine Zweifel an unserer Stärke zu. Wir werden der Horde als Ebenbürtige beitreten.]], [[Bringt diesen Brief zu Sylvanas, Anführerin der Verlassenen. Sie war schon immer auf unserer Seite, aber die Nachricht von Dar'Khans Tod muss wie Musik in ihren Ohren klingen. Bereitet Euch auf eine lange Reise vor, <name>. Wenn alles gut geht, wird sie Euch nach Orgrimmar schicken.]], },
    { [[Bringt den Brief von Lor'themar Theron zu Fürstin Sylvanas Windläufer in Unterstadt, der Hauptstadt der Verlassenen in Tirisfal.]], },
  },
  [9813] = {
    [[Ein Treffen mit dem Kriegshäuptling]],
    { [[Wie Ihr wisst, habe ich nichts von der Liebe zu meinem Heimatland oder seinem Volk verloren. Ich habe mit Krallen und Zähnen gekämpft, damit Silbermond am Verhandlungstisch einen Platz neben Unterstadt und Orgrimmar erhält.]], [[Dies sollte sämtliche Gegenstimmen zum Schweigen bringen. Bringt diesen Brief zum Kriegshäuptling in Orgrimmar. Als Anführer der Horde hat er das letzte Wort bei der Entscheidung über die Bitte der Sin'dorei.]], [[Ich habe den Brief als Zeichen meiner persönlichen Bekräftigung mit meinem Siegel versehen. Geht in den Nordwesten der Stadt und steigt auf dem Turm in den Zeppelin nach Durotar.]], },
    { [[Fürstin Sylvanas Windläufer möchte, dass Ihr den Brief mit Sylvanas' Siegel zum Kriegshäuptling nach Orgrimmar, Hauptstadt von Durotar, bringt.]], },
  },
  [9814] = {
    [[Berstkappenpilze, Mann!]],
    { [[Mögt Ihr Pilze, Mann? Es gibt einen ganz mächtigen hier in den Zangarmarschen, wächst gleich in der Nähe von Zabra'jin.]], [[Ihr müsst aber vorsichtig sein, Mann. Man nennt sie aus gutem Grund Berstkappenpilze. Wenn man ohne entsprechende Vorsichtsmaßnahmen zu nahe an sie herangeht, geht ihnen der Hut hoch! Puff! Und dann steht man in einer Wolke von giftigen Sporen.]], [[Ich habe sie benutzt, um die Zukunft vorauszusagen, aber jetzt sind sie fast alle.]], },
    { [[Bringt Hexendoktor Tor'gash 6 Berstkappen.]], },
  },
  [9815] = {
    [[Schlammtauchen]],
    { [[Die am schwersten betroffenen Wasserelementare befinden sich im Halaanibecken, südwestlich von hier unterhalb der verlassenen Stadt Halaa. Während einige der verzweifelten Elementare ohne Folgen für die Umwelt zerstört werden können, müssen die Überreste der Schlammkreaturen nach ihrer Zerstörung entfernt werden.]], [[Die Schlammkreaturen hinterlassen nämlich verseuchte Leichenteile, die der Umwelt schaden können, wenn man sie liegen lässt.]], [[Vernichtet diese Schlammkreaturen und bringt mir ihre schlammbesudelten Kerne.]], },
    { [[Elementarist Lo'ap am Thron der Elemente in Nagrand möchte, dass Ihr 5 schlammbesudelte Kerne besorgt.]], },
  },
  [9816] = {
    [[Habt Ihr so was schon mal gesehen?]],
    { [[In diesen Marschen fehlt was, Mann! Keine Murlocs hier.]], [[Keine Sorge, Mann, ich habe eine Lösung für dieses Problem. Seht Ihr Gargel und Gurgel da drüben? Ich habe vor, den Verirrten ein paar Murlocs zu schenken!]], [[Bringt diesen Käfig nach Dolchfenn und lasst sie bei der Ansammlung von Hütten im Nordosten frei. Nun gut, ich möchte Euch nicht Gargel und Gurgel auf den Hals jagen, aber ich gebe Euch einen Käfig mit jungen bunten Murlocs. Setzt den Käfig einfach auf den flachen Stein, den Ihr dort finden werdet, und öffnet die Tür!]], },
    { [[Bringt den Murlockäfig nach Dolchfenn und stellt ihn auf den flachen Stein in der Nähe des Dorfs.]], },
  },
  [9817] = {
    [[Anführerin der Blutschuppen]],
    { [[Hiermit setzt die Expedition des Cenarius ein Kopfgeld auf Rajis Fyashe, Anführerin der Blutschuppennagas, aus. Die Nagazauberin wurde zuletzt am Schlangensee gesehen, im Nordwesten der Zangarmarschen.]], [[Die Blutschuppen sind als extrem gefährlich einzustufen, und ihre Anführerin sollte mit äußerster Vorsicht behandelt werden.]], [[Das Kopfgeld kann bei Aufseher Hamuht in der Zuflucht des Cenarius eingefordert werden.]], },
    { [[Tötet Rajis Fyashe am Schlangensee und kehrt dann zu Aufseher Hamuht in der Zuflucht des Cenarius in den Zangarmarschen zurück, um eine Belohnung zu erhalten.]], },
  },
  [9818] = {
    [[Im Untergrund]],
    { [[Etwas schändet den Untergrund. Es schlägt den Boden, als wollte es ihn bestrafen.]], [[*Rums*]], [[<Untrag schlägt sich mit der Faust auf die Brust.>]], [[So klingt das hunderttausendmal. Endlos. Die Erde taumelt. Sie ruft nach uns.]], [[Gordawg spricht mit mir. Gordawg weiß, dass etwas die Elementare hervortreibt. Etwas unter der Erde.]], [[Geht jetzt und sprecht mit Gordawg. Nein, hört ihm zu, sprecht nicht. Hört Gordawg an...]], },
    { [[Kommuniziert mit Gordawg.]], },
  },
  [9819] = {
    [[Die gequälte Erde]],
    { [[Bestraft den Thronräuber. Ihr werdet ihn finden. Folgt Pfad aus Erde. Zuerst bringt gequälte Erdgeister zur Ruhe. Müssen wieder schlafen.]], [[<Gordawg schlägt mit seiner Faust auf den Boden und schleudert faulig riechende Erde nach oben.>]], [[Müssen schlafen!]], [[<Gordawg zeigt nach Südosten.>]], [[Sucht Geister in Nähe von Bergen. Nähe von Hügeln. Nähe von Erde.]], [[Bringt zur Ruhe.]], [[Telaaribecken... Südosten.]], },
    { [[Gordawg at the Throne of the Elements in Nagrand has asked that you destroy 15 Tortured Earth Spirits.]], },
  },
  [9820] = {
    [[GESUCHT: Boss Grog'ak]],
    { [[Gesucht - Tot oder lebendig: Boss Grog'ak, Vorarbeiter der Ango'rosh Pilzsammeloperation.]], [[Diese grobschlächtigen, axtschwingenden Dummköpfe sind für das Fällen von Pilzen nördlich des Dorfs verantwortlich. Wenn er nicht aufgehalten wird, werden er und seine Leute einen Pfad bis zu den Mauern von Zabra'jin schlagen!]], [[Wer Boss Grog'aks Kopf zu Schattenjäger Denjai bringt, soll reich belohnt werden.]], },
    { [[Bringt den Kopf von Boss Grog'ak zu Schattenjäger Denjai in Zabra'jin.]], },
  },
  [9821] = {
    [[Verdammnis schmecken]],
    { [[<Gordawg nimmt einen Stein und fängt an darauf herumzukauen.>]], [[Gordawg schmeckt. Gordawg weiß wo diese Erde war.]], [[<Gordawg hebt eines seiner massiven Beine und stampft auf den Boden.>]], [[Schaut. Schaut Nagrand an.]], [[<Gordawg zeigt auf seinen Fußabdruck. Im Abdruck seht Ihr eine perfekte geologische Karte von Nagrand, auf der alle Stellen markiert sind, an denen die Elementare, die Ihr für Gordawg zerstören sollt, sich aufhalten.>]], [[ZERSTÖRT SIE! Bringt Gordawg die Stücke zum Essen.]], [[Nehmt die Karte.]], },
    { [[Gordawg at the Throne of the Elements in Nagrand has asked that you bring him 15 Enraged Crusher Cores.]], },
  },
  [9822] = {
    [[Drohender Angriff]],
    { [[Die Position der Pilzsammeloperation der Oger kann kein Zufall sein. Ich werde den Verdacht einfach nicht los, dass sie einen Angriff auf Zabra'jin planen.]], [[Ich muss das genau wissen, <name>. Die Oger haben einen weiteren Außenposten im Nordwesten beim Grund der Ango'rosh. Wenn sie tatsächlich vorhaben uns anzugreifen, dann werden wir dort sicher Beweise für ihre Pläne finden. Geht dorthin, <name>, und durchsucht das Lager. Bringt mir alles, was auch nur im Geringsten verdächtig sein könnte!]], },
    { [[Bringt die Angriffspläne der Ango'rosh zu Schattenjäger Denjai in Zabra'jin.]], },
  },
  [9823] = {
    [[Sie oder wir]],
    { [[Um die Bedrohung, die von den Ango'rosh ausgeht, zu beenden, müssen wir ihren Oberanführer Schmetterfaust töten. Aber wir werden sogar noch viel mehr tun. Wir werden mit dem Oberanführer und seinem Gefolge ein Zeichen setzen.]], [[Geht zur Festung der Ango'rosh und kümmert Euch um unsere Feinde. Die Feste befindet sich in einer Höhle auf einer kleinen Insel nordwestlich von Zabra'jin. Dort werdet Ihr eine Brücke, die aus einem umgefallenen Pilz besteht, finden.]], [[Zeigt kein Erbarmen, <name>. Sie würden sicher keinen von uns verschonen.]], },
    { [[Tötet Oberanführer Schmetterfaust, 10 Raufer der Ango'rosh, 10 Seelenfresser der Ango'rosh und meldet Euch dann bei Schattenjäger Denjai in Zabra'jin zurück.]], },
  },
  [9824] = {
    [[Arkane Störungen]],
    { [[Ich versuche, eine Aufzeichnung von Karazhans Energie zu erstellen... für ein privates Projekt natürlich. Unglücklicherweise empfange ich hier oben einfach nichts. Wenn ich in die Nähe einer unterirdischen Wasserquelle gelangen könnte, könnten meine Kristalle die Signale viel leichter empfangen.]], [[Ich habe sowohl von einem unterirdischen Teich als auch von einem Brunnen in einem Keller unter uns gehört. Dieser ganze Ort ist jedoch voll von Geistern, und sich dort hinzuwagen wäre für jemanden in meinem Alter viel zu gefährlich. Was sagt Ihr? Werdet Ihr einem alten Mann helfen und das Aufzeichnen für mich übernehmen?]], },
    { [[Benutzt den violetten Aufzeichnungskristall in der Nähe von unterirdischen Wasserquellen im Keller des Meisters und kehrt zu Erzmagier Alturus außerhalb von Karazhan zurück.]], },
  },
  [9825] = {
    [[Rastlose Aktivität]],
    { [[Wie Ihr sicher bemerkt habt, wimmelt es außerhalb von Karazhan nur so von Geistwesen. Der Grund ihrer Existenz ist unbekannt, und Unbekanntes mag ich gar nicht leiden!]], [[Bringt mir ihre geisterhafte Essenz, damit ich ihre Beschaffenheit untersuchen kann. Karazhan hat viele Geheimnisse, <name>. Und ich werde sie alle lüften.]], },
    { [[Bringt 10 geisterhafte Essenzen zu Erzmagier Alturus außerhalb von Karazhan.]], },
  },
  [9826] = {
    [[Dalarankontaktpersonen]],
    { [[These signs do not bode well for us, <name>.  I've never been one for secrecy so I will tell you a bit about my organization, the Violet Eye.]], [[We are agents of the Kirin Tor, the Magus Senate of Dalaran.  We were assigned to keep an eye on Karazhan and Medivh, in case things got out of hand.]], [[Which they did... very quickly, after Medivh disappeared.]], [[This report details my findings.  Take it to Archmage Cedric, also from the Violet Eye; you will find him in the outskirts of Dalaran.]], },
    { [[Bring Alturus's Report to Archmage Cedric in the outskirts of Dalaran.]], },
  },
  [9827] = {
    [[Siechendes Basidium]],
    { [[Bei einer genaueren Untersuchung des Basidiums lassen sich eine Zahl von Einkerbungen erkennen, in denen vielleicht mal etwas drinsteckte. Die fremdartige Pilzmasse scheint so etwas wie ein großes Rätsel zu sein. Sie sieht alles andere als gesund aus, aber Ihr habt keine Ahnung, wozu sie normalerweise dienen könnte.]], [[Vielleicht kann Euch ja jemand, der sich mit den Bewohnern der Zangarmarschen besser auskennt etwas mehr darüber erzählen.]], },
    { [[Take the Withered Basidium to Ruam in Telredor.]], },
  },
  [9828] = {
    [[Siechendes Basidium]],
    { [[Bei einer genaueren Untersuchung des Basidiums lassen sich eine Zahl von Einkerbungen erkennen, in denen vielleicht mal etwas drinsteckte. Die fremdartige Pilzmasse scheint so etwas wie ein großes Rätsel zu sein. Sie sieht alles andere als gesund aus, aber Ihr habt keine Ahnung, wozu sie normalerweise dienen könnte.]], [[Vielleicht kann Euch ja jemand, der sich mit den Bewohnern der Zangarmarschen besser auskennt etwas mehr darüber erzählen.]], },
    { [[Bring the Withered Basidium to Reavij at Swamprat Post.]], },
  },
  [9829] = {
    [[Khadgar]],
    { [[Alturus' Bericht ist beunruhigend. Wir dachten, es sei ein schlechtes Zeichen, als Karazhan sich von der Umwelt abgeschottet hat. Wir hatten dort Agenten, von denen wir nie wieder gehört haben. Und nun diese... dämonische Strömung. Wir müssen das untersuchen.]], [[Die Einzigen, die Karazhan wieder öffnen könnten, hielt man für tot: Khadgar und Medivh.]], [[Während wir hoffen, das Letzterer tatsächlich tot ist, haben wir kürzlich in Erfahrung gebracht, dass sein Lehrling, Khadgar, gesund und munter an einem Ort namens Shattrath lebt. Findet ihn und gebt ihm Alturus' Bericht.]], },
    { [[Bringt Alturus' Bericht zu Khadgar in Shattrath in den Wäldern von Terokkar.]], },
  },
  [9830] = {
    [[Blutergift]],
    { [[Wenn ich mich mit den Draenei unterhalte, habe ich immer den Eindruck, dass sie das Gefühl haben, aus ihrer natürlichen Umgebung herausgerissen zu sein. Viele scheinen meine Brüder und Schwestern, die Zerschlagenen, zu bedauern, weil sie glauben, dass wir etwas verloren haben.]], [[Wir Zerschlagenen haben vielleicht unsere Verbindung zum Licht verloren, aber unsere Erfahrung hat uns enger an das Land gebunden.]], [[Mein Volk erkennt Möglichkeiten, die den Draenei verschlossen bleiben. Nehmt zum Beispiel das Gift der Sumpflichtbluter, die im Süden und Westen leben. Es ist vielseitig einsetzbar, ob für die Jagd oder zur Heilung.]], },
    { [[Bringt 6 Einheiten Sumpflichtblutergift zu Puluu in der Oreborzuflucht.]], },
  },
  [9831] = {
    [[Nach Karazhan]],
    { [[Die Neuigkeiten in diesem Bericht beunruhigen mich sehr, <name>. Es wird jedoch schwierig, sich Zugang zu Karazhan zu beschaffen.]], [[Als klar wurde, dass ich hier gestrandet bin, habe ich den Schlüssel in drei Teile zerbrochen.]], [[Ich habe die Schlüsselfragmente in verzauberte Behälter gelegt, und diese dann an damals sicheren Plätzen versteckt.]], [[Wegen der dunklen Veränderungen, die die Scherbenwelt durchmachen musste, sind sie heute allerdings alles andere als sicher. Das erste Fragment ist in einem Teil von Auchindoun versteckt, der nun vom Schattenrat beherrscht wird. Bringt mir dieses Fragment zurück.]], },
    { [[Khadgar möchte, dass Ihr das Schattenlabyrinth von Auchindoun betretet und das erste Schlüsselfragment aus einem versteckten arkanen Behälter besorgt.]], },
  },
  [9832] = {
    [[Das zweite und das dritte Fragment]],
    { [[Ich habe das zweite Fragment am Boden des Schlangensees versteckt, noch lange bevor er so hieß. Der gigantische Ablauf, der dort installiert wurde, nachdem die Naga den Ort in Beschlag genommen hatten, hat den Behälter wahrscheinlich in die Tiefen des Echsenkessels gesogen.]], [[Das dritte Fragment wird eine besonders große Herausforderung werden, da ich es in der Festung der Stürme gelassen habe, als die Naaru dort noch herrschten. Kael'thas regiert dort jetzt, und das Zimmer, in dem sich das Fragment befindet, ist jetzt ein Gefängnis.]], [[Bringt mir diese beiden Fragmente, <name>. Von Eurem Erfolg hängt viel ab.]], },
    { [[Besorgt das zweite Schlüsselfragment aus einem arkanen Behälter im Echsenkessel und das dritte Schlüsselfragment aus einem arkanen Behälter in der Festung der Stürme. Kehrt dann zu Khadgar in Shattrath zurück.]], },
  },
  [9833] = {
    [[Nachrichtenwege]],
    { [[Einer der Gründe, warum uns Ikuti in die Zangarmarschen geführt hat, ist der Versuch, die Kommunikation zwischen den Draenei und Telaar, der Heimatstadt der Kurenai in Nagrand, zu verbessern. Damit die Boten schnell zwischen Telaar und Telredor hin- und herreisen können, müssen die Straßen sicher gemacht werden.]], [[Auf unserem Weg hierher wurden wir oft von Marschenfangschlitzern angegriffen. Wir konnten sie zwar abwehren, aber für einen einzelnen Boten wären sie eine besondere Bedrohung. Ihre Reihen etwas zu lichten würde viel zur Sicherheit unserer Boten beitragen.]], },
    { [[Puluu in der Oreborzuflucht möchte, dass Ihr 12 Marschenfangschlitzer tötet.]], },
  },
  [9834] = {
    [[Natürliche Rüstung]],
    { [[Maktu kann einen Draenei über Meilen hinweg an seiner schimmernden Rüstung erkennen, sogar in diesem düsteren Sumpf!]], [[Maktu will sie auslachen, aber dann erinnert er sich, dass sie nett zu den Kurenai waren, und will ihnen beibringen, wie Kurenai Rüstung herstellen.]], [[Die dunklen, schuppigen Bälge der Fennklauen ergeben die beste Rüstung, die Maktu je getragen hat. Wenn <name> Maktu ein paar dieser Bälge von den Fennklauenhauern tief im Schlangensee bringen kann, teilt er gerne seine Rüstung mit <name>.]], },
    { [[Bringt 8 Fennklauenbälge zu Maktu in der Oreborzuflucht.]], },
  },
  [9835] = {
    [[Vordringen der Ango'rosh]],
    { [[Die kleine Gruppe von Zerschlagenen, die Ihr dort seht, sind Gesandte einer Siedlung in Nagrand. Die Kurenai, wie wir uns selbst nennen, sind Zerschlagene, die sich gerne mit den Draenei wiedervereinen möchten.]], [[Meine Gruppe ist hierhergekommen, um die Bande zwischen den Kurenai und den Draenei in Telredor zu stärken.]], [[Wenn es nach den Ogern der Ango'rosh geht, wird dieser Tempel nicht mehr lange stehen. Sie haben die hohen Pilze des Prügelsumpfs vollständig gerodet und kommen immer näher an unsere Zuflucht heran. Wenn Ihr <ein Freund/eineFreundin> unseres Volkes sein möchtet, dann helft uns, diesen Feind zu besiegen.]], },
    { [[Tötet 10 Oger der Ango'rosh und 5 Schamanen der Ango'rosh und kehrt dann zu Ikuti in der Oreborzuflucht zurück.]], },
  },
  [9836] = {
    [[Die Berührung des Meisters]],
    { [[Ich habe den Schlüssel, den ich als Lehrling benutzt habe, wieder zusammengefügt. Er scheint völlig funktionstüchtig zu sein. Was jedoch die Öffnung von Karazhan angeht, ist er so wertlos wie ein Stück Alteisen.]], [[Ohne Medivhs Einverständnis ist der Schlüssel unbrauchbar. Ja, natürlich weiß ich, dass Medivh tot ist. Das macht die Sache zwar schwieriger, aber nicht unmöglich.]], [[Es gibt einen Ort in Azeroth, der Höhlen der Zeit genannt wird. Er wird von dem bronzenen Drachenschwarm bewacht. Findet einen Weg dort hinein, reist in der Zeit zurück und bringt Medivh dazu, den Schlüssel zu aktivieren.]], },
    { [[Geht in die Höhlen der Zeit und überzeugt Medivh davon, den wiederhergestellten Schlüssel des Lehrlings zu aktivieren.]], },
  },
  [9837] = {
    [[Rückkehr zu Khadgar]],
    { [[Ich scheine mich an zukünftige Ereignisse, die mit Euch und diesem Schlüssel zu tun haben, zu erinnern.]], [[<Medivh schaut Euch an, als hätte er einen Geist gesehen.>]], [[Euch stehen harte Zeiten bevor, <name>. Wollen wir hoffen, dass Euch Eure Stärke nicht im Stich lassen wird.]], [[Den Schlüssel werde ich Khadgar geben, wenn er zu mir geschickt wird. Ich könnte mir keinen besseren Lehrling vorstellen.]], [[Meiner Meinung nach solltet Ihr einen eigenen Schlüssel bekommen.]], [[<Medivh fasst in seine Robe und reicht Euch seinen eigenen Schlüssel.>]], [[Nun zieht weiter. Und viel Glück, <class>.]], },
    { [[Kehrt zu Khadgar in Shattrath zurück und zeigt ihm den Schlüssel des Meisters.]], },
  },
  [9838] = {
    [[Das Violette Auge]],
    { [[Ihr wurdet mit einer großen Verantwortung betraut, <name>. Wenn man den Zeichen, die das Violette Auge beobachtet hat, trauen kann, müsst Ihr ihnen helfen.]], [[Trotz all meiner Unstimmigkeiten mit Cedric halte ich die Arbeit von Männern wie Alturus immer noch für sehr wichtig. Achtet jedoch darauf, dass sie Euch nicht ausnutzen. Denkt immer daran, dass der Schlüssel an Euch gebunden ist, nicht an sie. Aber wenn die Bedrohung in Karazhan wirklich echt ist, dann sind sie am besten dafür geeignet, Euch darauf vorzubereiten. Kehrt zurück zu Alturus und helft ihm, wo immer Ihr könnt.]], },
    { [[Sprecht mit Erzmagier Alturus außerhalb von Karazhan.]], },
  },
  [9839] = {
    [[Oberanführer Schmetterfaust]],
    { [[Das Herzstück jedes Ogerstamms ist sein Anführer. Ohne Anführer werden die Ango'rosh sich zurückziehen und unter sich auskämpfen, wer der nächste Anführer wird.]], [[Obwohl wir die Oger überall verstreut in dieser Gegend gesichtet haben, ist die Festung der Ango'rosh im Nordwesten ihre eigentliche Machtbasis. Sie liegt in einer Höhle auf einer kleinen Insel, die nur durch einen umgestürzten Pilz mit den Marschen verbunden ist.]], [[Geht dort hin und besiegt Oberanführer Schmetterfaust. Das sollte uns eine wohlverdiente Ruhepause geben.]], },
    { [[Ikuti möchte, dass Ihr Oberanführer Schmetterfaust und 10 Raufer der Ango'rosh tötet. Kehrt zur Oreborzuflucht zurück, wenn Ihr die Aufgabe erfüllt habt.]], },
  },
  [9840] = {
    [[Einschätzung der Situation]],
    { [[Jetzt, da wir - oder besser Ihr - einen Weg zurück in Medivhs' Turm gefunden haben, müssen wir als Erstes herausfinden, wie die Situation im Inneren aussieht.]], [[Eine unserer Agentinnen hat es geschafft, sich in Karazhan einzuschleusen und hielt ihre Beobachtungen in verschlüsselten Aufzeichnungen fest. Unglücklicherweise wurden wir über ihren Tod informiert, kurz bevor Karazhan geschlossen wurde. Koren, der Schmied von Karazhan, wird von uns bezahlt. Auch wenn er kein Mitglied des Violetten Auges ist, wird er uns sicher helfen können, Keannas Aufzeichnungen zu finden. Sucht ihn innerhalb von Karazhan.]], },
    { [[Findet Koren in Karazhan.]], },
  },
  [9841] = {
    [[Tückische Mücken]],
    { [[Ich kann mit diesem infernalen Gesumme nicht arbeiten! Könnt Ihr es hören?]], [[Ich bekomme es nicht mehr aus meinem Kopf! Ich weiß genau, wer dieses Geräusch verursacht, Sumpflichtbluter! Das Geräusch kommt immer vom Süden und vom Westen des Dorfs her.]], [[Sie sind zu groß, um sie mit einer Fliegenklappe zu erschlagen, sie unter dem Fuß zu zerquetschen oder fortzuwedeln. Der einzige Weg dieses fürchterliche Dröhnen zu beenden ist sie zu töten. Mir ist ganz egal, wie Ihr es tut, <name>, aber bringt sie zum Schweigen!]], },
    { [[Tötet 8 Sumpflichtbluter und kehrt dann zu Gambarinka in Zabra'jin zurück.]], },
  },
  [9842] = {
    [[Die schärfsten Klingen]],
    { [[Das feuchte Klima der Marschen hat meine metallenen Werkzeuge stark in Mitleidenschaft gezogen. Das macht die Holzarbeiten schwerer als sonst, weshalb ich nun ganz auf Metallklingen verzichte.]], [[Es hat sich herausgestellt, dass die Marschenfangschlitzer im Süden und im Westen über Klingen verfügen, die schärfer sind als jede Metallklinge, die ich je besessen habe. Sie rosten auch nicht und nutzen sich nicht ab. Es ist nur ein wenig schwer, sie an einem Griff zu befestigen, aber ich glaube, ich habe dieses Problem bereits gelöst. Ich wäre Euch für jede Klinge, die Ihr mitbringen könnt, sehr dankbar.]], },
    { [[Bringt 10 Marschenfangschlitzerklingen zu Gambarinka in Zabra'jin.]], },
  },
  [9843] = {
    [[Keannas Aufzeichnungen]],
    { [[Wen sucht Ihr noch gleich? Keanna?]], [[Ich habe einst jemanden mit diesem Namen getroffen. Sie wohnte oben in den Gästezimmern. Das hätte ich nicht verraten dürfen, oder?]], [[Sagtet Ihr, Ihr gehört zum Violetten Auge? Nein, davon habe ich noch nie gehört.]], [[Ich muss jetzt zurück an die Arbeit, wenn es Euch nichts ausmacht.]], [[<Koren scheint Euch schon vergessen zu haben.>]], },
    { [[Durchsucht die Gästezimmer in Karazhan nach Keannas Aufzeichnungen und bringt sie zu Erzmagier Alturus außerhalb von Karazhan.]], },
  },
  [9844] = {
    [[Eine dämonische Präsenz]],
    { [[Keannas letzte Einträge bestätigen unseren Verdacht, dass sich eine dämonische Präsenz in Karazhan befindet.]], [[Sie hat die Ankunft eines Agenten der Brennenden Legion entdeckt, der sich in der Nähe der Turmspitze aufhalten soll.]], [[Ihr Eintrag endet hier.]], [[Karazhan ist ein sehr wichtiger Ort, <class>. Es ist ein Portal in den Nether, ein Leuchtfeuer, das in andere Welten scheint. Wenn es in falsche Hände gerät, sind die Folgen unvorstellbar. Das Eindringen der Dämonen muss unbedingt aufgehalten werden. Und außer Euch, <name>, hat niemand einen Schlüssel.]], },
    { [[Erzmagier Alturus möchte, dass Ihr die dämonische Präsenz an der Turmspitze von Karazhan zerstört.]], },
  },
  [9845] = {
    [[Anglerunglück]],
    { [[Welch Erleichterung, dass ich endlich an einem Ort mit Wasser angekommen bin! Ich hatte schon Angst, dass es keine anständigen Fischgründe hier in der Scherbenwelt gibt.]], [[Jetzt bin ich doch froh, dass ich meine Angelausrüstung mitgenommen habe. Und das Fischen läuft soweit auch ganz gut, wenn da nicht diese nichtsnutzigen Fennklauenhauer im Schlangensee wären.]], [[Sie verscheuchen mir nicht nur die Fische, sondern sind auch noch frech genug, mir die Köder direkt vom Haken zu stehlen! Ich verbringe mehr Zeit damit, neue Köder anzubringen, als tatsächlich etwas zu fangen!]], },
    { [[Tötet 10 Fennklauenhauer und kehrt dann zu Zurjaya in Zabra'jin zurück.]], },
  },
  [9846] = {
    [[Geister der Wildfenn]],
    { [[Der mächtigste Stamm der Verirrten in den Marschen, der Wildfennstamm, soll eine mächtige Medizin besitzen. Vielleicht ist an solchen Gerüchten nichts Wahres dran, aber ich muss allem nachgehen, was mir dabei helfen kann, die Geister dieser Welt zu lenken.]], [[Findet die Mystiker, Jäger und Druiden von Wildfenn, <name>, und nehmt Euch ihre Totems. Alles, was ich wissen muss, werde ich aus den Schnitzereien erfahren können.]], },
    { [[Bringt 10 Schutztotems von Wildfenn zu Seherin Janidi in Zabra'jin.]], },
  },
  [9847] = {
    [[Ein verbündeter Geist?]],
    { [[Wir müssen herausfinden, welche Kräfte dieser Schlangengeist von Wildfenn besitzt und ob wir sie für unsere Zwecke einsetzen können. Stellt Euch nur vor, was ich alles tun könnte, wenn ich über einen Geist der Scherbenwelt befehligen könnte!]], [[Nehmt dieses Totem und begebt Euch an den Fuß der Treppe zu den Ruinen von Boha'mu. Verankert das Totem dort in der Erde, den Rest wird es selbst erledigen. Der Geist dürfte nicht wissen, wer das Totem benutzt hat, bis er sich manifestiert hat.]], },
    { [[Bringt das Wildfenntotem an den Fuß der Treppe in den Ruinen von Boha'mu und verankert es im Boden. Berichtet Seherin Janidi in Zabra'jin von Euren Beobachtungen.]], },
  },
  [9848] = {
    [[Geheimnisse des Dolchfennstamms]],
    { [[Käfer, Käfer, Käfer! Die Marschen sind voll davon, und meine Gefährten hier scheinen ganz besessen von ihnen zu sein.]], [[Nun, ich habe genug von Käfern! Etwas viel Aufregenderes hat mein Interesse geweckt. Im Westen gibt es ein Dorf der Verirrten, das Dolchfenn genannt wird.]], [[Der gesamte Stamm besteht aus hochbegabten Auftragsmördern. Ich habe gesehen, wie sie ein bestimmtes hochwirksames Gift mischen und verwenden. Ich würde liebend gerne mehr darüber erfahren, aber dazu bräuchte ich eine Abschrift des Rezepts und eine Phiole mit einer Probe des Gifts.]], },
    { [[Bringt das Gifthandbuch von Dolchfenn und eine Giftphiole von Dolchfenn zu Timothy Daniels in der Oreborzuflucht.]], },
  },
  [9849] = {
    [[Die Maskerade durchschauen]],
    { [[<Gordawg bricht einen ebenholzfarbenen Fels von seinem Rücken ab.>]], [[Ich gewähre Euch ein Stück Wut der Erde. Nehmt Gordawgs Fels und werft ihn auf den Giftfels. Zerschlagt ihre Hüllen um die Wahrheit zu erfahren. Vernichtet die Diener des Thronräubers und kehrt mit dem Namen zu Gordawg zurück.]], [[Ihr findet die zerschlagenen Elemente am Ende der Welt. Südwesten.]], },
    { [[Gordawg am Thron der Elemente in Nagrand möchte, dass Ihr 30 Diener von Gurok tötet.]], },
  },
  [9850] = {
    [[Beherrschen der Grollhufjagd]],
    { [[You've proved to me that you can find and take down a regular clefthoof, but far more dangerous are the clefthoof bulls.]], [[Bag yourself thirty of those.  Then you can talk to me of your hunting prowess.]], [[Look for them all along the middle region of Nagrand to the west and south.]], },
    { [[Kill 30 Clefthoof Bulls and then speak to Hemet Nesingwary at Nesingwary's Safari in Nagrand about your prowess.]], },
  },
  [9851] = {
    [[Beherrschen der Grollhufjagd]],
    { [[Das Grollhufweibchen Banthar ist der meistgefürchtete und am schwersten zu fassende Grollhuf der gesamten Scherbenwelt. Sie ist außerdem das Exemplar mit dem längsten Fell ihrer Gattung.]], [[Man sagt, sie treibt sich auf den Geisterfeldern in der Nähe des Oshu'gun herum.]], [[Bringt mir ihr Horn, um mir endgültig Euer Können in den Jagdkünsten zu beweisen.]], },
    { [[Erlangt das Horn von Banthar und kehrt anschließend zu Hemet Nesingwary bei Nesingwarys Safari in Nagrand zurück.]], },
  },
  [9852] = {
    [[Die ultimative Beute]],
    { [[Die Königin aller Tiere in Nagrand ist Zahna. Sie ist ein riesiger Elekk von der Sorte, wie Ihr sie woanders nie zu sehen bekommen werdet!]], [[Ihr tut gut daran, Euch bei dem Namen zu erschrecken. Bach'lor, Aaskralle und Banthar sind nichts im Vergleich zu ihr. Man sagt, dass sie sich auf einer riesigen Klippe weit im Südwesten in der Nähe des Oshu'gun aufhält. Bringt mir ihr Herz, wenn Ihr Euch traut.]], [[Wollt Ihr mir einen Brief für Eure Familie dalassen, falls Ihr, was sehr wahrscheinlich ist, nicht zurückkommt?]], },
    { [[Bringt das Herz von Zahna zu Hemet Nesingwary bei Nesingwarys Safari in Nagrand.]], },
  },
  [9853] = {
    [[Gurok der Thronräuber]],
    { [[Gurok muss gerufen werden. Ihr ruft Gurok und Ihr vernichtet.]], [[Ihr müsst zum Totschlägerhügel im Nordwesten gehen und suchen, wo Oger Gurok verehren. Ihr setzt Ogerschädel von Totschläger auf Guroks irdenes Brandmal. Nur dann kommt Gurok. Sieben Schädel.]], [[Wenn Ihr lebt, dann bringt Guroks irdenen Kopf zu Gordawg. Gordawg zerschmettert Kopf.]], [[<Gordawg beißt die Zähne so fest zusammen, dass der Boden unter Euren Füßen bebt.>]], },
    { [[Gordawg am Thron der Elemente in Nagrand möchte, dass Ihr Gurok den Thronräuber zerstört und Guroks irdenen Kopf besorgt.]], },
  },
  [9854] = {
    [[Beherrschen der Windrocjagd]],
    { [[Lebt Ihr genauso für die Jagd wie ich, <class>? Sucht Ihr die Ruhe, die Euch überkommt, wenn Ihr Euch an Eure Beute heranpirscht? Wisst Ihr, was es bedeutet, die letzten Herzschläge der erlegten Beute zu ehren? Ich werde Euch helfen, dies zu erreichen.]], [[Nagrand ist ein blühendes Land, voller Leben inmitten dieser zerschlagenen Welt. Erlegt ein paar Windrocs und dann unterhalten wir uns weiter.]], [[Ihr könnt sie an vielen Orten um unser Lager herum finden.]], },
    { [[Kill 30 Windrocs and then return to Fitz at the Nesingwary Safari in Nagrand.]], },
  },
  [9855] = {
    [[Beherrschen der Windrocjagd]],
    { [[Ihr müsst immer im Training bleiben, wenn Ihr eines Tages die mächtigste Beute überhaupt erlegen möchtet! Jetzt seid Ihr noch nicht dafür bereit, aber ich werde Euch auf den richtigen Weg bringen.]], [[Es gibt noch eine tödlichere Art von Windrocs. Messt Euch an den gefräßigen Windrocs! Ihr findet sie im Herzen von Nagrand, im Süden und im Südwesten.]], },
    { [[Hunt down 30 Ravenous Windrocs and then return to Fitz at the Nesingwary Safari in Nagrand.]], },
  },
  [9856] = {
    [[Beherrschen der Windrocjagd]],
    { [[Ihr seid bereit, <class>. Bereit Euch der Mutter aller Windrocs zu stellen, Aaskralle! Nachdem Ihr so viele ihrer Kinder getötet habt, gebietet die Ehre, dass ihr eine Chance auf Rache gewährt wird. Ihr findet sie bei den großen Windrocs weit im Westen.]], [[Wenn Ihr überleben solltet, bringt mir ihr Auge.]], },
    { [[Bringt das Auge von Aaskralle zu Fitz bei Nesingwarys Safari in Nagrand.]], },
  },
  [9857] = {
    [[Beherrschen der Talbukjagd]],
    { [[Ich habe heute keine Lust zu jagen. Könnt Ihr das für mich übernehmen?]], [[Ich habe Hemet gesagt, dass ich mich um ein paar von den Talbuks kümmern würde, die hier überall herumlaufen. Die Böcke sind besonders gut zu jagen. Ihr könnt sie in kleinen Gruppen unter den Bäumen in der Nähe finden.]], [[Denkt Ihr, Ihr könntet das für mich tun?]], },
    { [[Kill 30 Talbuk Stags and then return to Harold Lane at the Nesingwary Safari in Nagrand.]], },
  },
  [9858] = {
    [[Beherrschen der Talbukjagd]],
    { [[Diese verdammte Schramme macht mir echt zu schaffen. Also, habt Ihr Lust, nochmals...<hust>...für mich einzuspringen?]], [[Dieses Mal werdet Ihr ein paar Dornzehrer erlegen. Sie sind härter und gerissener als die Hirsche, also seid auf der Hut. Sucht sie...<hust>..unter den Bäumen in den mittleren Gebieten Nagrands im Süden und Westen.]], [[Auf, geht los und...<hust>.. zeigt Hemet aus welchem Holz Ihr geschnitzt seid. Mir wird es schon gut gehen.]], },
    { [[Hunt down 30 Talbuk Thorngrazers and then return to Harold Lane at the Nesingwary Safari in Nagrand.]], },
  },
  [9859] = {
    [[Beherrschen der Talbukjagd]],
    { [[<Schlimmer Husten.>]], [[Macht Euch keine Sorgen um...<hust>... mich. Mir geht es gut. Es ist nur ein...<hust>... Kratzer.]], [[Dennoch müsst Ihr...<hust>... nochmal für mich...<hust>... einspringen.]], [[Ihr müsst den großen Bach'lor, König der Hirsche...<hust>... finden.]], [[Bringt mir einen seiner Hufe...<hust>, <hust>... als Beweis.]], [[Er führt seine Herde...<hust>... weit im Westen von Nagrand.]], },
    { [[Besorgt den Huf von Bach'lor und bringt ihn zu Harold Fährten bei Nesingwarys Safari in Nagrand.]], },
  },
  [9860] = {
    [[Neue Anweisungen]],
    { [[Kehrt sofort zu Cedric zurück, <name>. Die Unterstützung, die Ihr dem Violetten Auge geleistet habt, wird nicht unbelohnt bleiben.]], [[Haltet von uns und unserer Organisation was Ihr wollt, <class>, aber in der Gunst der Erzmagier von Dalaran zu stehen ist nichts, was Ihr je bereuen werdet.]], },
    { [[Speak to Archmage Cedric in the Outskirts of Dalaran.]], },
  },
  [9861] = {
    [[Der heulende Wind]],
    { [[Die formlose Elementarluftgestalt spricht in einer Sprache zu Euch, die Ihr nicht versteht. Ihr glaubt, dass es die Sprache der Elemente ist, Kalimag. Vielleicht kann Euch jemand beim Thron der Elemente mit dieser ungewöhnlichen Entdeckung weiterhelfen.]], },
    { [[Sucht beim Thron der Elemente nach jemandem, der Kalimag sprechen kann und zeigt der Person den heulenden Wind.]], },
  },
  [9862] = {
    [[Bösewichte der Finsterblut]],
    { [[Der Finsterblutstamm fängt die Luftgeister ein und quält sie! Die Finsterblut gehören zu meinem Volk. Sie sind ein Stamm von Zerschlagenen, der dem bösen Musel'ek, dem Sumpflord des Echsenkessels, dient.]], [[Der Luftgeist sagt, dass die Verderber der Finsterblut andere Luftgeister fangen und sie benutzen, um Chaos in den Reihen der Elemente zu säen. Wie es aussieht haben die Finsterblut vor Kurzem ein Orcdorf im Südwesten namens Sonnenwind geplündert und benutzen nun mächtige Elementarmagie, um den See aufzuwühlen. Sucht die Verderber und vernichtet sie!]], },
    { [[Elementalist Morgh at the Throne of the Elements in Nagrand wants you to slay 8 Murkblood Putrifiers.]], },
  },
  [9863] = {
    [[Abscheulicher Götzendienst]],
    { [[Ohne Führung sind wir von allen Seiten angreifbar! Die niederträchtigen Finsterblut hätten keinen besseren Zeitpunkt auswählen können, um uns anzugreifen.]], [[In den letzten Tagen mussten wir nicht weniger als fünf Angriffe von den Zerschlagenen ertragen! Sie haben sich sogar unserer Siedlung in dieser Gegend, Sonnenwind, bemächtigt.]], [[Wir müssen zurückschlagen und ihnen zeigen, das wir ganz und gar nicht machtlos sind. Erschlagt die widerlichen Götzendiener und bringt mir ihre abscheulichen Götzen! Möge der Blitz sie treffen!]], },
    { [[Farseer Kurkush at Garadar in Nagrand wants you to bring him 20 Murkblood Idols.]], },
  },
  [9864] = {
    [[Der verschollene Kampftrupp]],
    { [[Nach dem letzten Angriff der Finsterblut hat eine Gruppe von Jünglingen sich aufgemacht, um uns zu rächen. Wir haben versucht sie aufzuhalten, aber ihre Wut hat sie blind werden lassen. Es ist nun eine Woche vergangen seit wir sie das letzte Mal gesehen haben.]], [[Bitte, <race>, Ihr müsst sie finden! Sie sind doch gerade erst erwachsen geworden. Wie können sie erwarten, diese Wilden besiegen zu können?]], [[Sie haben die Straße nach Westen aus der Stadt genommen. Folgt dieser Straße und haltet nach Lebenszeichen unserer Jünglinge Ausschau.]], [[Und <name>, haltet Euch von Halaa fern, wenn Euch Euer Leben lieb ist. Dort ist Kriegsgebiet.]], },
    { [[Sucht den verschollenen Kriegstrupp von Garadar.]], },
  },
  [9865] = {
    [[Chroniken eines Kriegers]],
    { [[Ich kenne Euch nicht, Fremder, aber ich muss Euch um Hilfe bitten. Ich schäme mich dafür, aber ich bin nur ein einziger Orc.]], [[In mir kochen Emotionen hoch, die ich vorher nie gekannt habe. Ich.. Ich will Rache. Blut für Blut. Kennt Euer Volk dieses Gefühl? Ich möchte meine Axt nehmen und meine Feinde in Stücke schlagen. Meine Waffe schwingen, bis meine Arme nicht mehr können und erschlaffen.]], [[Der Sonnenwindposten ist gleich südlich von dieser Prozession hier. Dort werdet Ihr unsere Feinde finden. Tötet sie... Tötet sie alle...]], },
    { [[Kill 40 Murkblood Scavengers and 20 Murkblood Raiders.]], },
  },
  [9866] = {
    [[Weiter...]],
    { [[Würdet Ihr mir eine letzte Ehre erweisen, <name>? Ich möchte nicht nach Garadar zurück. Ich glaube, dass mein Schicksal woanders liegt, dass ich einst höheren Zielen folgen werde.]], [[Bitte geht zu Scharfseher Corhuk und sagt ihm, dass ich nicht zurückkehren werde. Dass ich nicht zurückkehren kann. Ich muss weiterziehen... Er wird es verstehen.]], },
    { [[Bringt die Nachricht zu Scharfseher Corhuk in Garadar.]], },
  },
  [9867] = {
    [[Anführer der Finsterblut...]],
    { [[Wir haben in den letzten Wochen viele Freunde verloren.]], [[Schon seit Jahrzehnten kämpfen wir gegen die Oger aus Nagrand, aber nun ist eine neue Bedrohung aufgetaucht. Der Finsterblutstamm der Zerschlagenen hat unser Fischerdorf im Südwesten, den Sonnenwindposten, angegriffen und alle Orcs, die dort lebten, getötet oder vertrieben.]], [[Die anderen sind der Meinung, dass man sie einfach wahllos töten sollte. Ich bin jedoch davon überzeugt, dass man ihren Anführer töten muss. Ich weiß zwar nicht, wer ihr Anführer ist, aber ich schätze, Ihr werdet es merken, wenn Ihr ihn seht.]], },
    { [[Bringt Ortor von Finsterbluts Kopf zu Scharfseher Margadesh in Garadar.]], },
  },
  [9868] = {
    [[Das Totem von Kar'dash]],
    { [[Habt Dank, Fremder. Wenn Ihr nicht gewesen wärt, hätten sie mich sicherlich getötet.]], [[Ich wurde vor drei Nächten von diesen Finsterblut bei einem Nachtangriff auf Garadar entführt. Was ich seitdem herausgefunden habe, wird wahrscheinlich die ganze Scherbenwelt in ihren Grundfesten erschüttern: diese Finsterlinge versuchen, das Totem von Kar'dash wiederzuerlangen! Wir können nicht zulassen, dass das Totem in die Hände der Finsterblut fällt!]], [[Ihr müsst mir helfen zu fliehen. Und Fremder, falls ich es nicht bis Garadar schaffe, müsst Ihr Garrosh erzählen, was hier passiert ist...]], },
    { [[Begleitet den festgehaltenen Mag'har aus dem Sonnenwindposten hinaus. Sprecht mit Garrosh in Garadar, wenn Ihr erfolgreich wart.]], },
  },
  [9869] = {
    [[Der Thron der Elemente]],
    { [[Grüße, <race>. Ich bin Ioki, Elementarist des Irdenen Rings. Ich bin nach Telaar gekommen, um die Kurenai über den Status der Elemente zu informieren.]], [[Kann ich Euch um einen Gefallen bitten? Könntet Ihr zum Thron der Elemente in den nördlichen Hügeln Nagrands, hinter dem Himmelsweisensee, reisen und Elementaristin Sharvak ausrichten, dass ich heil in Telaar angekommen bin? Sie wird Euch für diesen kleinen Gefallen sicher entlohnen.]], },
    { [[Elementarist Ioki möchte, dass Ihr zum Thron der Elemente reist und mit Elementaristin Sharvak sprecht.]], },
  },
  [9870] = {
    [[Der Thron der Elemente]],
    { [[Grüße, <race>. Ich bin Yal'hah, Elementarist des Irdenen Rings. Ich bin nach Garadar gekommen, um die Mag'har über den Status der Elemente zu informieren.]], [[Kann ich Euch um einen Gefallen bitten? Könntet Ihr zum Thron der Elemente in den nördlichen Hügeln Nagrands, hinter dem Himmelsweisensee, reisen und Elementaristin Sharvak ausrichten, dass ich heil in Garadar angekommen bin? Sie wird Euch für diesen kleinen Gefallen sicher entlohnen.]], },
    { [[Elementarist Yal'hah möchte, dass Ihr zum Thron der Elemente reist und mit Elementaristin Sharvak sprecht.]], },
  },
  [9871] = {
    [[Eindringlinge der Finsterblut]],
    { [[Auf dem Pergament ist eine grob gezeichnete Karte von Nagrand zu sehen. Anstelle von Telaar ist ein großes rotes X auf der Karte vermerkt. Es sieht aus, als planten die Feinde einen Angriff auf die Stadt. Bringt die Invasionspläne der Finsterblut zu Arechron in Telaar.]], },
    { [[Take the Murkblood Invasion Plans to Arechron.]], },
  },
  [9872] = {
    [[Eindringlinge der Finsterblut]],
    { [[Auf dem Pergament ist eine grob gezeichnete Karte von Nagrand zu sehen. Anstelle von Garadar ist ein großes rotes X auf der Karte vermerkt. Es sieht aus, als planten die Feinde einen Angriff auf die Stadt. Bringt die Invasionspläne der Finsterblut zu Garrosh in Garadar.]], },
    { [[Bringt die Invasionspläne der Finsterblut zu Garrosh.]], },
  },
  [9873] = {
    [[Mein alter Freund Ortor...]],
    { [[Diese Angriffpläne wurden von jemandem geschrieben, den ich einst kannte. Ortor von Finsterblut, ein adliger Zerschlagener, bewandert in der Beherrschung der Elemente, der nun den dummen, stinkenden Lakai für Sumpffürst Musel'ek spielt.]], [[Die Pläne besagen, dass sie ein Orcdorf nordwestlich von hier, das als Sonnenwindposten bekannt ist, eingenommen haben.]], [[Reist zum Sonnenwindposten und erlöst die Welt von Ortor von Finsterblut. Es geschieht zum Wohl aller...]], },
    { [[Arechron in Telaar in Nagrand möchte, dass Ihr Ortor von Finsterblut tötet.]], },
  },
  [9874] = {
    [[Die Ausbreitung eindämmen]],
    { [[Ich habe gerade von einer Katastrophe mit epischen Ausmaßen erfahren, <name>. Die niederträchtigen Finsterblut haben das Orcdorf Sonnenwind nordwestlich von hier eingenommen und seine Einwohner getötet. Was das Ganze noch verschlimmert ist, dass sie begonnen haben, die Leichen in den Sonnenwindsee zu werfen. Wenn die Leichen dort verrotten, wird unsere Hauptwasserquelle in dieser Gegend verseucht werden.]], [[Es ist eine grauenvolle Aufgabe, die ich Euch übertrage, aber sie muss erledigt werden. Nehmt diese verzauberte Fackel und kehrt zum Sonnenwindposten zurück. Verbrennt alle verrottenden Orcleichen...]], },
    { [[Otonbu der Weise in Telaar in Nagrand möchte, dass Ihr die Fackel des flüssigen Feuers benutzt, um 10 Leichen von Bewohnern von Sonnenwind zu zerstören. Bringt die Fackel des flüssigen Feuers zurück, wenn Ihr fertig seid.]], },
  },
  [9875] = {
    [[Nicht katalogisierte Gattung]],
    { [[Lauranna muss diese besondere Pflanze übersehen haben. Ihr seid Euch sicher, dass Ihr eine Gattung gefunden habt, die noch nicht in Laurannas Buch katalogisiert ist. Ihr solltet sie ihr am besten zeigen, um zu sehen, was sie davon hält.]], },
    { [[Bringt die nicht katalogisierte Gattung zu Lauranna Thar'well in der Zuflucht des Cenarius in den Zangarmarschen.]], },
  },
  [9876] = {
    [[Fehlgeschlagene Operation]],
    { [[Ich habe eine kleine Gruppe von Druiden losgeschickt, um den Ablauf, den Ihr im Schlangensee gefunden habt, zu untersuchen. Eigentlich hätten sie kurz darauf zurückkommen müssen. Nun ja, sie sind nicht zurückgekehrt.]], [[Ihr zählt zu unseren stärksten Verbündeten. Bitte sucht sie und findet heraus, was ihnen zugestoßen ist.]], },
    { [[Ysiel Windsinger wants you to look for signs of Cenarion Expedition Druids near the entrance to Coilfang Reservoir.]], },
  },
  [9877] = {
    [[Ein Regenerationsgebräu]],
    { [[Der Kampf gegen die Geißel verläuft nicht ohne Opfer. Viele fallen jeden Tag, doch einige erwartet ein noch schlimmeres Schicksal.]], [[Oft nimmt sie Gefangene. Der Tod wäre dem, was sie in der Todesfestung erwartet, vorzuziehen. Sie werden verhört und gefoltert, während sie unter dem Einfluss eines schrecklichen, willensbrechenden Giftes stehen.]], [[Apotheker Renzithen in Tristessa hat ein Gegenmittel entwickelt; geht zu ihm und holt das Regenerationsgebräu.]], },
    { [[Sprecht mit Apotheker Renzithen in Tristessa.]], },
  },
  [9878] = {
    [[Die Lösung des Problems]],
    { [[Das Auftauchen der Finsterblut in Nagrand hatte einen sehr negativen Einfluss auf unsere Umwelt. Die Elemente sind aufgebracht, was wiederum die Wildtiere stört. Und noch viel schlimmer: die Zerstörung des Sonnenwindpostens hat die Feindschaft der Orcs gegen mein Volk neu entflammt.]], [[Die Kurenai sind zwar ein ganz anderer Stamm als die Finsterblut, aber für einen Orc ist ein Zerschlagener ein Zerschlagener.]], [[Wir müssen versuchen, dieses Dilemma zu lösen. Reist nach Nordwesten zum Sonnenwindposten und erledigt das Finsterblutproblem.]], },
    { [[Poli'lukluk the Wiser at Telaar in Nagrand wants you to kill 20 Murkblood Scavengers and 10 Murkblood Raiders.]], },
  },
  [9879] = {
    [[Das Totem von Kar'dash]],
    { [[Ich wäre schon tot, wenn Ihr nicht vorbeigekommen wärt, Fremder. Sie haben schon so viele Kurenai auf der Suche nach einem verschollenen Relikt getötet.]], [[Sie suchen das Totem von Kar'dash. Ich kann nicht verstehen, warum sie glauben, dass wir etwas über dieses Totem wüssten. Ist ein Totem nicht eine typisch orcische Gerätschaft?]], [[Arechron muss davon erfahren. Vielleicht weiß er, warum die Finsterblut Kurenai verschleppen und foltern, um Informationen über ein orcisches Relikt zu erfahren.]], [[Lasst uns gehen bevor noch mehr kommen.]], },
    { [[Begleitet den gefangenen Kurenai aus dem Sonnenwindposten. Sprecht mit Arechron in Telaar, wenn Ihr erfolgreich wart.]], },
  },
  [9882] = {
    [[Von Dieben stehlen]],
    { [[Nexusprinz Haramad hat uns verboten, mit der Arbeit im Oshu'gun loszulegen, weil er einen bestimmten mächtigen Naaru nicht verärgern will. Das hält natürlich unsere ganze Operation auf.]], [[Ich glaube aber, dass ich ein Hintertürchen gefunden habe.]], [[Seht Ihr, wir sind nicht die einzigen Astralen, die von diesem riesigen Diamanten angelockt werden. Ein Dieb namens Gava'xi hat eine Gruppe von Schurken in den Oshu'gun geführt, und sie haben begonnen, große Teile aus dem Kristall herauszuschlagen.]], [[Eure Aufgabe, <race>, ist es, dorthin zu gehen und unser rechtmäßiges Eigentum zurückzufordern.]], },
    { [[Besorgt 10 Kristallfragmente von Oshu'gun und bringt sie zu Gezhe beim Aerissteg in Nagrand.]], },
  },
  [9883] = {
    [[Noch mehr Kristallfragmente]],
  },
  [9884] = {
    [[Mitgliedervorteil]],
  },
  [9885] = {
    [[Mitgliedervorteil]],
  },
  [9886] = {
    [[Mitgliedervorteil]],
  },
  [9887] = {
    [[Mitgliedervorteil]],
  },
  [9888] = {
    [[Der unfähige Anführer]],
    { [[Ich bin dem sicheren Tode entronnen, habe einen feigen Angriff bei Nacht von den Felsfäusten auf mein Dorf überlebt.]], [[Aber ich frage Euch, Fremder, wozu?]], [[<Jorin zeigt auf Garrosh.>]], [[Er hat uns jegliche Unterstützung verweigert. Oger toben durch meine Stadt und er sitzt nur da und heult den Mond an. Was ist aus uns geworden? Was wird aus uns werden?]], [[Ich habe einen Späher, Kilrath, zum Dorf, den jetzigen Ruinen des Blutenden Auges, geschickt. Könnt Ihr ihn bitte suchen und unterstützen? Er versteckt sich an der Grenze zwischen Nagrand und Terokkar.]], },
    { [[Jorin Totauge möchte, dass Ihr Kilrath sucht und mit ihm sprecht.]], },
  },
  [9889] = {
    [[Nicht den Fetten töten!]],
    { [[Ich beobachte diese Kreaturen nun schon seit Tagen. Der Fette sagt, dass er auf etwas wartet. Worauf bleibt jedoch ein Geheimnis.]], [[Ich habe einen Plan, aber er erfordert ein wenig Brutalität von Eurer Seite. Ihr werdet einige seiner Untertanen töten müssen und ihn dann fast tot schlagen. Wenn Ihr das tut, bin ich sicher, dass er uns alles erzählen wird, was wir wissen wollen.]], },
    { [[Kilrath in den Wäldern von Terokkar möchte, dass Ihr 10 Eindringlinge der Felsfäuste tötet und Unkor den Rücksichtslosen so lange schlagt, bis er redet.]], },
  },
  [9890] = {
    [[Erfolg!]],
    { [[Seit vielen Jahren hatten die Felsfäuste die Herrschaft über alle Oger der Scherbenwelt. Die Oger waren unter Krol vereint und zufrieden mit den Ländereien, die sie erhalten hatten. In den letzten Monaten haben wir jedoch viele Oger an einen Klan namens Totschläger verloren. Die Totschläger haben den größten Teil unseres Landes in Nagrand in Beschlag genommen und viele unserer Leute getötet.]], [[Wir hatten keine andere Wahl als dieses Dorf einzunehmen. Wir werden ausgerottet...]], [[Krol ist verschollen und die Felsfäuste sind in Aufruhr. Lantresor von der Klinge hat in seiner Abwesenheit die Führung übernommen.]], },
    { [[Teilt Kilrath in den Wäldern von Terokkar die Informationen, die Ihr aus dem Oger geprügelt habt, mit.]], },
  },
  [9891] = {
    [[Weil Kilrath ein Feigling ist]],
    { [[Erzählt Jorin in Garadar, was Ihr gehört habt. Ich bin mir sicher, dass er nicht erfreut sein wird zu hören, dass unser Dorf zerstört und viele unserer Leute getötet wurden, weil die Oger mehr Platz brauchen.]], [[Mögen die Ahnen über Euch wachen... Ich werde mich in den Büschen verstecken, falls Ihr mich noch braucht.]], },
    { [[Erzählt Jorin Totauge in Garadar in Nagrand, was Ihr aus Unkor herausprügeln konntet.]], },
  },
  [9892] = {
    [[Noch mehr Obsidiankriegsperlen]],
  },
  [9893] = {
    [[Obsidiankriegsperlen]],
    { [[Während wir hier darauf warten, dass wir endlich am Oshu'gun graben können, verlieren wir so viel Gewinn, <name>.]], [[Zum Glück habe ich eine andere Einkommensquelle entdeckt. Die Oger von Nagrand tragen Kriegsperlen aus Obsidian, ein Gestein, das in vielen anderen Welten sehr wertvoll ist.]], [[Zerid musste sich aus Sicherheitsgründen bereits mit diesen Rohlingen auseinandersetzen. Betrachtet die Kriegsperlen also als kleinen Bonus. Oder noch besser, betrachtet sie gar nicht und bringt sie mir einfach.]], },
    { [[Bringt 10 Obsidiankriegsperlen zu Gezhe beim Aerissteg in Nagrand.]], },
  },
  [9894] = {
    [[Schützt die Behüter]],
    { [[Während Eurer Reisen habt Ihr vielleicht ein paar der Behüter der Expedition getroffen. Sie kümmern sich hauptsächlich um die Feldstudien, das heißt die Dokumentation der einzigartigen Bewohner der Marschen, das Sammeln von Proben und die Kontaktaufnahme mit den vernunftbegabten Arten.]], [[Die Sicherheit der Behüter ist von äußerster Wichtigkeit, <class>. Wir mussten die Behüter, die sich aufgemacht haben, um die Funggorhöhle zu erkunden, wegen Angriffen von Sumpfmonstern zurückrufen. Aufseher Hamuht ist der Meinung, dass wir ihren Angriffen ein schnelles Ende machen könnten, wenn wir ihren Anführer, Lord Klaq, töten.]], },
    { [[Tötet Lord Klaq und kehrt danach zu Windrufer Schwarzhuf in der Zuflucht des Cenarius zurück.]], },
  },
  [9895] = {
    [[Aus dem Gleichgewicht geraten]],
    { [[Habt Ihr die Umgebung genau betrachtet, <name>? Dies ist eine dahinsiechende Landschaft, die verzweifelt versucht ihr Gleichgewicht zu halten und den Kampf verliert.]], [[Wenn man weiß, worauf man achten muss, kann man die Symptome deutlich erkennen. Einige Vertreter bestimmter Gattungen überholen ihre Artgenossen, was das Wachstum angeht, bereits und werden aggressiv.]], [[Ein gutes Beispiel für so ein unkontrolliertes Wachstum ist der Marschenläufer Sumpfschläger. Wenn ihr ihn selbst sehen wollt, müsst Ihr nur zu den Wasserwegen südlich der Zuflucht gehen.]], [[Aber haltet Euch bereit, Euch zu verteidigen. Diese Kreatur kennt kein Erbarmen.]], },
    { [[Sucht und vernichtet Sumpfschläger und kehrt dann zu Lethyn Mondfeuer in der Zuflucht des Cenarius zurück.]], },
  },
  [9896] = {
    [[Schwarzsichels Fluch]],
    { [[Bei Khaz'goroths brennendem Nabel, ist das heiß hier! Ich kann es der ersten Gruppe nicht verübeln, dass sie abgereist sind, aber wisst Ihr, es war nicht die Hitze, die sie verjagt hat. Nein, es war Schwarzsichel, auch wenn sie das natürlich nicht zugeben möchten.]], [[Sie hatten ihr Lager südlich von den leuchtenden Draeneitürmen im Westen aufgeschlagen, gleich nördlich von Wildfenn, als die riesige Wespe aus dem Nichts auftauchte und sie angriff! Ich kann es kaum erwarten, ihre Gesichter zu sehen, wenn ich den Stachel des Biests nach Eisenschmiede bringe!]], },
    { [[Bringt Schwarzsichels Stachel zu Ausgrabungsleiter Conall in Telredor.]], },
  },
  [9897] = {
    [[Ich bin gerettet!]],
    { [[Es macht mir nichts aus zu teilen. Ich bin nur froh, dass ich vor diesem Widerling Diebus sicher bin! Und ich habe Tonnen von Tierhäuten in den Zangarmarschen gesammelt, es ist also überhaupt kein Problem!]], },
    { [[Gebt Harold Fährten bei Nesingwarys Safari in Nagrand das Bündel mit Tierhäuten.]], },
  },
  [9898] = {
    [[Liebe ist ein großer Stachel]],
    { [[Sie wird mir gehören. Oh ja, sie wird mir gehören.]], [[Ist Magasha nicht einfach die schönste Trollfrau, die Ihr je gesehen habt? Ich muss einen Weg finden, ihr Herz zu gewinnen. Ja sicher, sie hat ein Auge auf Zurai geworfen, aber das wird sich ändern. Mein narrensicherer Plan wird schon dafür sorgen.]], [[Ich muss nur einen Weg finden, ihr meine überlegenen Jagdkünste zu beweisen, und da kommt Ihr ins Spiel, mein Freund. Es gibt da eine riesige Sumpfwespe namens Schwarzsichel, die sich in der Gegend südlich der leuchtenden Draeneitürme im Westen herumtreibt. Bringt mir ihren Stachel und ich lasse meinen Charme spielen.]], },
    { [[Bringt Schwarzsichels Stachel zu Reavij im Sumpfrattenposten.]], },
  },
  [9899] = {
    [[Unerledigte Arbeit]],
    { [[Jemand muss Reavij zur Vernunft bringen! Er vernachlässigt seine Pflichten und verschwendet stattdessen all seine Energie darauf, meine Aufmerksamkeit zu erlangen. Ich habe versucht, ihn zu ignorieren und ihm gesagt, dass er damit aufhören soll. Aber nichts scheint zu helfen.]], [[Bis er es endlich sein lässt, muss ich ständig hinter ihm aufräumen. Er hätte sich eigentlich um Sporenschwinge kümmern sollen, aber das Monster ist immer noch wohlauf. Wenn Ihr Zeit habt, könntet Ihr vielleicht in den westlichen Teil des Todesmoors gehen und den Segler töten? Ich kümmere mich in der Zwischenzeit um Reavij.]], },
    { [[Magasha im Sumpfrattenposten möchte, dass Ihr Sporenschwinge tötet.]], },
  },
  [9900] = {
    [[Gava'xi]],
    { [[Die Anwesenheit von astralen Schurken in dieser Gegend mag Gezhe, der in allem eine Möglichkeit sieht, vielleicht erheitern, aber sie sind eine wirkliche Bedrohung für die Sicherheit unserer Unternehmung.]], [[Ihr Anführer, Gava'xi, ist ein gefährlicher Krimineller, und ich habe es zu meinem höchsten Anliegen gemacht, ihn zur Strecke zu bringen.]], [[Wenn Ihr Euch nützlich machen wollt, dann zieht los und kümmert euch um Gava'xi. Ich werde mich darum kümmern, dass Ihr angemessen entlohnt werdet.]], },
    { [[Tötet Gava'xi in der Nähe des Oshu'gun und kehrt dann zu Zerid beim Aerissteg in Nagrand zurück.]], },
  },
  [9901] = {
    [[Offene Rechnungen]],
    { [[Die erste Expedition, die die Liga losgeschickt hatte, berichtete von einem riesigen Sporensegler namens Sporenschwinge. Sie mussten sehr hastig aufbrechen, deshalb hatten sie keine Gelegenheit, das Tier umzubringen. Deshalb dachte ich mir, ich erledige das für sie.]], [[In ihrem Bericht stand, dass die Kreatur sich im westlichen Teil des Todesmoors aufhält. Ich glaube Verteidiger Idaar erwähnte einmal, dass es sich nordöstlich von Telredor befindet.]], },
    { [[Ausgrabungsleiter Conall in Telredor möchte, dass Ihr Sporenschwinge tötet.]], },
  },
  [9902] = {
    [[Der Schrecken des Sumpflichtsees]],
    { [[Meine Reisen durch das Gebiet der Naga haben mir einen Eindruck vermittelt, wie schlimm es meinen unglücklichen Brüdern und Schwestern gehen muss. Am Sumpflichtsee im Südwesten habe ich beobachtet, welch tödliches Spiel einer der besonders bösartigen Zuchtmeister treibt. Er jagt seine Sklaven ins Wasser, wenn er weiß, dass Terrorklaue, ein riesiger Sumpfstrok, Hunger hat.]], [[Die Folgen brauche ich nicht weiter erläutern. Wir sind zu wenige, um die Naga direkt anzugreifen, aber wir können ihnen den Spaß verderben, indem wir ihr Haustier umbringen.]], },
    { [[Tötet Terrorklaue und kehrt dann zu Puluu in der Oreborzuflucht zurück.]], },
  },
  [9903] = {
    [[Der Größte von allen]],
    { [[Ihr seid zwar mit den meisten der kleineren Hydras fertiggeworden, aber ich glaube kaum, dass Mragesh von Eurer Vorstellung beeindruckt war. Er ist eine gerissene, grausame Bestie und hat schon so manchen Angler auf dem Gewissen.]], [[Ich habe mir vorgenommen, die Letzte zu sein, die er je belästigt. Er soll sich tief im Schlangensee im Nordosten verstecken. Er ist schon Geschichte, aber das weiß er noch nicht.]], },
    { [[Tötet Mragesh und kehrt dann zu Zurjaya in Zabra'jin zurück.]], },
  },
  [9904] = {
    [[Jagd auf Terrorklaue]],
    { [[Ich habe jeden erdenklichen Fisch in den Marschen gefangen, aber einer ist mir bisher entkommen. Sicher habe ich auch Aale und Morastflossenfrenzys gefangen. Aber als ich das letzte Mal am Sumpflichtsee war, hat einer angebissen, meine Schnur zerrissen und ist abgehauen.]], [[Terrorklaue wird er genannt, und ich werde nicht ruhen, bis er tot ist. Er ist zu groß um ihn mit einer Angel zu fangen, also müssen wir ihn wohl auf die altmodische Art umbringen.]], },
    { [[Tötet Terrorklaue und kehrt dann zu Zurjaya in Zabra'jin zurück.]], },
  },
  [9905] = {
    [[Maktus Rache]],
    { [[Als Maktu letztes Mal auf Hydrajagd am Schlangensee war, wurde er von größter Hydra, die er jemals gesehen hat, angegriffen. Maktu hat nach seinem Speer gegriffen, aber es war schon zu spät. Er konnte gerade noch entkommen, bevor er zu Hydrafutter wurde.]], [[Wer weiß? Vielleicht hat Maktu nächstes Mal nicht mehr so viel Glück. Maktu will, dass es kein nächstes Mal gibt. Werdet Ihr Maktu helfen, sich an Mragesh zu rächen?]], },
    { [[Sucht und tötet Mragesh und kehrt dann zu Maktu in der Oreborzuflucht zurück.]], },
  },
  [9906] = {
    [[Sag's mit Fäusten]],
    { [[Oger interessieren sich nur für das Eine: Macht. Wenn wir ihnen gegenüber Schwäche zeigen, werden sie sich das zum Vorteil machen und uns ausrotten.]], [[Garrosh ist vielleicht bereit dazu, die Hände in den Schoß zu legen und diese Hunde unser Land übernehmen zu lassen, aber ich bin es nicht! Ich möchte, dass Ihr den Totschlägern eine Nachricht zukommen lasst, die sie verstehen: Gewalt.]], [[Reist zu den Ruinen der Brennenden Klinge südöstlich von hier und zeigt ihnen die Macht der Mag'har.]], [[Und <name>, schaut bei den anderen vorbei, bevor Ihr geht.]], },
    { [[Jorin Deadeye at Garadar in Nagrand has asked that you slay 15 Boulderfist Crushers and 15 Boulderfist Mystics.]], },
  },
  [9907] = {
    [[Ein kühner Angriff]],
    { [[Direkt im Westen von hier findet Ihr das Halaanibecken. Im Becken befinden sich die Zwillingsschluchten von Nagrand: Nordwind und Südwind. Die Schocktruppen der Felsfäuste benutzen die Schluchten, um ihre Ogerhorte zu verstecken.]], [[Ich möchte, dass Ihr diese Truppen aufspürt und sie alle im Namen der Mag'har tötet.]], },
    { [[Jorin Deadeye at Garadar in Nagrand has asked that you slay 25 Boulderfist Warriors and 25 Boulderfist Mages.]], },
  },
  [9910] = {
    [[Standarten]],
    { [[Wenn Ihr in die Ruinen der Brennenden Klinge reist, habe ich eine Aufgabe für Euch. Stellt diese Schlachtstandarten der Mag'har an drei verschiedenen Orten in der Ogersiedlung auf. Vorzugsweise Orte, an denen man sie gut sehen kann. Haltet nach den großen lodernden Scheiterhaufen in den oberen Bereichen Ausschau. Stellt eine Standarte neben dem ersten, zweiten und dritten Scheiterhaufen der Brennenden Klinge auf.]], [[Die Standarten werden einen bleibenden Eindruck bei den Ogern hinterlassen, die die Schlacht verpasst haben.]], },
    { [[Mystiker Elakay'gan in Garadar in Nagrand möchte, dass Ihr eine Schlachtstandarte der Mag'har am ersten, zweiten und dritten Scheiterhaufen der Brennenden Klinge aufstellt. Bringt die übrigen Schlachtstandarten zurück, wenn Ihr fertig seid.]], },
  },
  [9911] = {
    [[Der Graf der Marschen]],
    { [[Dieses blutige Beißwerkzeug stammt von einem unglaublich großen Insekt. Der Netherrochen, zu dem es gehörte, war der größte, der je in den Zangarmarschen gesichtet wurde. Wenn man den Zustand der Beißwerkzeuge betrachtet, muss der Graf seine Nahrung von Aas auf frisches Fleisch umgestellt haben.]], [[Die Expedition des Cenarius wäre an einem solchen Objekt sicher sehr interessiert. Behüterin Leesa'oh hat ein kleines Lager im Westen des Quaggkamms.]], },
    { [[Bring "Count" Ungula's Mandible to Watcher Leesa'oh.]], },
  },
  [9912] = {
    [[Die Expedition des Cenarius]],
    { [[Die Hauptexpedition des Zirkels des Cenarius in der Scherbenwelt ist als Expedition des Cenarius bekannt. Sie fing als kleines Projekt unter der direkten Kontrolle des Zirkels an, aber inzwischen ist sie zu einer großen und ziemlich unabhängigen Organisation herangewachsen.]], [[Die Anführerin der Expedition, Ysiel Windsänger, hat uns gebeten, Freiwillige zur Zuflucht des Cenarius in den östlichen Zangarmarschen zu schicken. Nehmt den Pfad nach Westen und Ihr werdet direkt dorthin gelangen.]], },
    { [[Sprecht mit Ysiel Windsänger in der Zuflucht des Cenarius in den Zangarmarschen.]], },
  },
  [9913] = {
    [[Das Konsortium braucht Euch!]],
    { [[Meine Organisation, das Konsortium, sucht nach talentierten Personen, wie Ihr es seid. Ihr werdet kaum ehrlichere und härter arbeitende Astrale als uns in dieser Gegend hier finden.]], [[Alles, was wir Verlangen ist Loyalität, Einsatz und ein vernünftiges Maß an moralischer Flexibilität. Wenn Ihr Interesse habt, sprecht mit Gezhe, unserem Anführer hier. Ihr findet ihn beim Aerissteg, nördlich des Oshu'guns.]], },
    { [[Meldet Euch bei Gezhe beim Aerissteg in Nagrand.]], },
  },
  [9914] = {
    [[Ein ganzer Kopf voll Elfenbein]],
    { [[Elfenbein ist ein heißbegehrtes Material. Ich habe Kunden von weither, die Schlange stehen, um es zu kaufen. Ich würde mich überreden lassen, meinen Gewinn mit Euch zu teilen.]], [[Die Stoßzähne der wilden Elekk in Nagrand haben genau die Qualität, die ich brauche. Bleibt jedoch von den Bullen fern, ihre Stoßzähne sind zu minderwertig.]], [[Oh, und keine Sorge, das ist keine Wilderei, ich habe ein Abkommen mit den Orcs und den Draenei.]], [[Also, sind wir uns einig?]], },
    { [[Sammelt 3 Paar Elfenbeinstoßzähne und kehrt dann zu Shadrek beim Aerissteg in Nagrand zurück.]], },
  },
  [9915] = {
    [[Noch mehr Elfenbein]],
  },
  [9916] = {
    [[Vorratskisten des Blutenden Auges]],
    { [[Die Totschläger haben die Vorräte, die sie aus unserem Dorf gestohlen haben, zu den Zwillingsschluchten von Nagrand gebracht, die sich westlich von hier im Halaanibecken befinden.]], [[Wenn ihr Euch dort aufhalten solltet, haltet Ausschau nach den Vorratskisten. Wenn wir sie zurückbekommen könnten, würde das die Last, die die Matronen von Garadar zu tragen haben, erheblich mindern.]], },
    { [[Elkay'gan the Mystic at Garadar in Nagrand wants you to recover 20 Bleeding Hollow Supply Crates.]], },
  },
  [9917] = {
    [[Kann ich meinen Augen trauen?]],
    { [[Kann ich meinen Augen trauen? Ist das da drüben tatsächlich eine Jagdgesellschaft von Ogern? Was treiben Oger denn so weit von Ihrem Hügel entfernt? Mir gefällt das gar nicht, <Fremder/Fremde>. Zuerst die Finsterblut und jetzt die Oger. Wir haben einfach nicht genug Leute, um mit all diesen Gefahren fertigzuwerden.]], [[Könntet Ihr die Ogersiedlung im Osten untersuchen? Ihr Lager ist etwa 200 Schritte östlich von hier. Wenn Ihr die Jäger der Felsfäuste seht, seid Ihr richtig. Tötet sie um an ihre Pläne zu kommen!]], },
    { [[Jägerin Bintook in Telaar in Nagrand möchte, dass Ihr die Pläne der Felsfäuste besorgt.]], },
  },
  [9918] = {
    [[Nicht solange ich hier wache!]],
    { [[Die Schlachtpläne wurden von einem Leutnant der Oger geschrieben. Sein Name ist entweder Trocken oder Brocken.]], [[<Bintook dreht das Pergament und legt den Kopf schief.>]], [[Es könnte auch Billy sein.]], [[Wie auch immer, ich denke, Ihr solltet dorthin zurückgehen und ein paar Takte mit Billy oder Trocken oder Brocken oder was weiß ich reden! Versucht diplomatisch zu sein. Wenn das nichts nützt, dann setzt eben etwas Muskelkraft ein.]], },
    { [[Jägerin Bintook in Telaar in Nagrand möchte, dass Ihr mit Brocken sprecht. Kehrt zu Jägerin Bintook zurück, wenn Ihr die geforderten Informationen gesammelt habt.]], },
  },
  [9919] = {
    [[Sporeggar]],
    { [[Ihr seid <ein sehr netter/eine sehr nette><race> <race>, <name>. Nicht wie die anderen, die ich getroffen habe.]], [[Ihr solltet den Rest meines Volkes kennenlernen, sie wären Euch für Eure Hilfe sehr dankbar. Bringt diese Sporenbeutel zu meinem Anführer, Msshi'fn. Ihr findet ihn in Sporeggar, nördlich von hier.]], },
    { [[Go to Sporeggar, north of the Spawning Glen, and deliver the Salvaged Spore Sacs to Msshi'fn.]], },
  },
  [9920] = {
    [[Mo'mor der Brecher]],
    { [[Ihr müsst sofort zu Mo'mor! Ihr könnt ihn in der Mitte von Telaar finden, beim Springbrunnen. Erzählt ihm genau, was der Oger Euch gesagt hat!]], },
    { [[Jägerin Bintook in Telaar in Nagrand möchte, dass Ihr mit Mo'mor dem Brecher redet.]], },
  },
  [9921] = {
    [[Die Ruinen der Brennenden Klinge]],
    { [[Die Jagdgesellschaften der Oger sind Vorboten für einen Angriff. Wir müssen sie schlagen, bevor sie eine Chance haben, genug Leute zu sammeln, um unsere Stadt einzunehmen.]], [[Ihr werdet Euch wohl Eure Hände schmutzig machen müssen, <race>.]], [[Die größte Ansammlung von Felsfäusten in dieser Gegend befindet sich in den Ruinen der Brennenden Klinge, direkt im Osten von Telaar, an der Grenze zu Terokkar. Geht dort hin und zeigt den Felsfäusten, dass Telaar sich niemals ihrem Willen beugen wird!]], },
    { [[Mo'mor the Breaker at Telaar in Nagrand wants you to kill 15 Boulderfist Crushers and 15 Boulderfist Mystics.]], },
  },
  [9922] = {
    [[Die Zwillingsschluchten von Nagrand]],
    { [[Ihr habt eine gute Arbeit geleistet, <name>. Seid Ihr bereit für noch mehr davon?]], [[Ich habe Berichte über eine große Ansammlung von Felsfäusten in den zwei Schluchten von Nagrand, nordwestlich von hier im Halaanibecken.]], [[Begebt Euch zu den Schluchten und werdet mit den übrig gebliebenen Felsfäusten fertig.]], },
    { [[Mo'mor the Breaker at Telaar in Nagrand has asked that you slay 25 Boulderfist Warriors and 25 Boulderfist Mages.]], },
  },
  [9923] = {
    [[Hilfe!]],
    { [[HILFE! Sie werden mich fressen! Ich weiß es!]], [[Findet den Schlüssel, einer dieser Oger muss ihn haben.]], [[Wenn Ihr mir hier raushelft, wird mein Vater Euch fürstlich entlohnen! Er ist der Anführer von Telaar, Arechron.]], },
    { [[Findet den Schlüssel der Felsfäuste und befreit Corki. Sprecht mit Arechron in Telaar, wenn Ihr Erfolg hattet.]], },
  },
  [9924] = {
    [[Corki wird schon wieder vermisst!]],
    { [[<Arechron seufzt.>]], [[Das ist mir so peinlich. Erinnert Ihr Euch noch an meinen Sohn Corki? Ihr habt ihn vor Kurzem aus den Ruinen der Brennenden Klinge gerettet. Nun ja, seine Neugier hat ihn mal wieder gepackt. Er sagte, er wolle die Felsfaustbedrohung für Onkel Mo'mor untersuchen, und ist zu den Ogerhügeln im Halaanibecken gerannt, nördlich von hier.]], [[Könntet Ihr ihn vielleicht für mich ausfindig machen? Ich wäre Euch ewig dankbar.]], },
    { [[Arechron in Telaar bittet Euch, Corki zu suchen und zu befreien.]], },
  },
  [9925] = {
    [[Eine Frage der Sicherheit]],
    { [[Gezhe hat beschlossen, unsere Operation so nahe wie möglich am Oshu'gun zu postieren, ohne dabei einen Gedanken an die Sicherheit der Gruppe zu verschwenden. Warum? Weil er eine Erhöhung unserer Produktivität um zehn Prozent vorausgesehen hat, wenn er uns hier anstatt in den Bergen im Süden ansiedelt.]], [[Ungeachtet der durchgedrehten Ausgeburten der Leere, die sich gerade mal 10 Fuß von uns weg aufhalten. Aber das ist das Problem des Sicherheitsoffiziers, nicht Gezhes.]], [[Tötet ein paar dieser Ausgeburten für mich, und ich gebe Euch ein Zehntel meines Sicherheitsetats. Freut Euch nicht zu früh, viel Geld steht mir nicht zur Verfügung.]], },
    { [[Zerid at Aeris Landing in Nagrand wants you to kill 12 Voidspawn near Oshu'gun.]], },
  },
  [9926] = {
    [[FLAG Shadow Council/Warmaul Questline]],
  },
  [9927] = {
    [[Erbarmungslose Gerissenheit]],
    { [[<Lantresor reicht Euch einen Stapel Banner.>]], [[Dies sind die Ogerbanner der Totschläger, die ich kürzlich erworben habe.]], [[Ich möchte, dass Ihr zur Festung Kil'sorge reist und so viele wie möglich von diesem Abschaum von Kil'sorge tötet, wie Ihr nur könnt. Rammt ein Totschlägerbanner durch das Herz jedes gefallen Soldaten von Kil'sorge. Kehrt zu mir zurück wenn Ihr diese und die beiden anderen Aufgaben, die zu meinem Plan gehören, erledigt habt.]], },
    { [[Lantresor of the Blade at the Burning Blade Ruins in Nagrand wants you to drive 20 Warmaul Ogre Banners through the bodies of Kil'sorrow members at Kil'sorrow Fortress.]], [[Return any unused Warmaul Ogre Banners.]], },
  },
  [9928] = {
    [[Waffen zur Täuschung]],
    { [[Wenn mein Plan gelingen soll, müsst Ihr ein paar Waffen von Kil'sorge in der Festung Kil'sorge stehlen.]], [[Durchsucht die Festung nach Kisten mit Waffen von Kil'sorge. Bringt mir alle, die Ihr finden könnt.]], [[Die Festung Kil'sorge liegt im Süden, etwas westlich von den Ruinen der Brennenden Klinge.]], },
    { [[Lantresor of the Blade at the Burning Blade Ruins in Nagrand wants you to collect 20 Kil'sorrow Armaments.]], },
  },
  [9929] = {
    [[Der vermisste Händler]],
    { [[Ich nehme an, Ihr seid noch nicht lange genug hier um Dugar zu kennen, oder? Er ist ein Händler mit Kontakten zu den verschiedenen Fraktionen hier in Shattrath.]], [[Fragt mich nicht, wie er das anstellt, aber er schafft es, mit ihnen Waren zu handeln, an die nicht einmal der Rüstmeister kommt!]], [[Er ist vor drei Tagen aus Shattrath losgezogen und noch nicht zurückgekehrt. Normalerweise schafft er seine Runde in viel kürzerer Zeit. Wenn Ihr in den Wald reist, haltet Ihr dann ein Auge nach ihm offen?]], },
    { [[Reist nach Nordwesten entlang der Straße nach Shattrath und sucht nach Hinweisen auf den Verbleib von Dugar.]], },
  },
  [9930] = {
    [[Der vermisste Händler]],
    { [[Ich nehme an, Ihr seid noch nicht lange genug hier um Deirom zu kennen, oder? Er ist ein Händler mit Kontakten zu den verschiedenen Fraktionen hier in Shattrath.]], [[Fragt mich nicht, wie er das anstellt, aber er schafft es, mit ihnen Waren zu handeln, an die nicht einmal der Rüstmeister kommt!]], [[Er ist vor drei Tagen aus Shattrath losgezogen und noch nicht zurückgekehrt. Normalerweise schafft er seine Runde in viel kürzerer Zeit. Wenn Ihr in den Wald reist, haltet Ihr dann ein Auge nach ihm offen? Meldet Euch bei Thander, wenn Ihr etwas herausfindet.]], },
    { [[Reist nach Nordwesten entlang der Straße nach Shattrath und sucht nach Hinweisen auf den Verbleib von Deirom.]], },
  },
  [9931] = {
    [[Sich für den Gefallen revanchieren]],
    { [[Nun müsst Ihr den Wunsch nach Krieg in den fetten, nutzlosen Köpfen der Totschläger wecken. Im Nordwesten, bei den Bergen, findet Ihr die Ruinen des Lachenden Schädels.]], [[<Lantresor zeigt auf die Kiste mit den Waffen, die Ihr ihm gebracht habt.>]], [[Erkennt Ihr diese Banner? Es sind Banner von Kil'sorge. Ich möchte, dass Ihr einen Stapel Banner von Kil'sorge nehmt und sie in die Leichen der Totschlägeroger rammt. Denkt daran, Ihr findet sie im Nordwesten.]], },
    { [[Lantresor of the Blade at the Burning Blade Ruins in Nagrand wants you to drive 20 Kil'sorrow Banners through the bodies of Warmaul ogres at the Laughing Skull Ruins.]], [[Return any unused Kil'sorrow Banners.]], },
  },
  [9932] = {
    [[Beweismaterial]],
    { [[Euer Angriff auf die Festung Kil'sorge hat uns eine ganz besondere Möglichkeit eröffnet. Wir haben genügend Leichen, um es so aussehen zu lassen, als hätte eine große Schlacht stattgefunden.]], [[Reist zu den Ruinen des Lachenden Schädels und sucht den Hof des Lachenden Schädels. Dort solltet Ihr einen brennenden Scheiterhaufen finden. Vergewissert Euch, dass die Gegend sicher ist, und benutzt diese Decke, um Rauchzeichen zu senden. Wenn meine Oger den Rauch sehen, werden sie sich einschleichen und die Leichen der toten Soldaten von Kil'sorge dort platzieren.]], [[Das sollte unser letzter Streich sein.]], },
    { [[Lantresor von der Klinge in den Ruinen der Brennenden Klinge in Nagrand möchte, dass Ihr die feuchte Wolldecke am lodernden Scheiterhaufen der Totschläger im Hof des Lachenden Schädels benutzt, um Signale an seine Oger zu senden.]], },
  },
  [9933] = {
    [[Nachricht für Telaar]],
    { [[<Lantresor zeigt auf eine unbeschriftete Kiste.>]], [[Nehmt dies. Es ist mein Geschenk an Euer Volk. Ihr habt meinen Klan eigenhändig vor der Ausrottung bewahrt.]], [[Ihr könnt Eurem Anführer sagen, dass Lantresor von der Brennenden Klinge und seine Felsfäuste das Friedensangebot annehmen.]], [[<Lantresor verneigt sich.>]], },
    { [[Take the Burning Blade Peace Offering to Arechron at Telaar.]], },
  },
  [9934] = {
    [[Nachricht für Garadar]],
    { [[<Lantresor zeigt auf eine unbeschriftete Kiste.>]], [[Nehmt dies. Es ist mein Geschenk an Euer Volk. Ihr habt meinen Klan eigenhändig vor der Ausrottung bewahrt.]], [[Ihr könnt Eurem Anführer sagen, dass Lantresor von der Brennenden Klinge und seine Felsfäuste das Friedensangebot annehmen.]], [[<Lantresor verneigt sich.>]], },
    { [[Bringt das Friedensangebot der Brennenden Klinge zu Garrosh in Garadar.]], },
  },
  [9935] = {
    [[Gesucht: Giselda die Alte]],
    { [[Gesucht: Tot oder lebendig]], [[Giselda die Alte von Kil'sorge und ihre Lakaien wegen Mord, rituellen Opferungen, Verbrüderung mit der Legion, Entführung und Diebstahl.]], [[Aufenthaltsort in der Festung Kil'sorge im Südosten von Nagrand, an der Grenze zu Terokkar.]], [[Eine hohe Belohnung in Gold wird von Aufseher Bullrok in Garadar an denjenigen ausgezahlt, der Giselda ihre gerechte Strafe zukommen lässt.]], },
    { [[Slay Giselda the Crone and any combination of 15 Kil'sorrow Agents. Return to Warden Bullrok at Garadar should you complete this task.]], },
  },
  [9936] = {
    [[Gesucht: Giselda die Alte]],
    { [[Gesucht: Tot oder lebendig]], [[Giselda die Alte von Kil'sorge und ihre Lakaien wegen Mord, rituellen Opferungen, Verbrüderung mit der Legion, Entführung und Diebstahl.]], [[Aufenthaltsort in der Festung Kil'sorge im Südosten von Nagrand, an der Grenze zu Terokkar.]], [[Eine hohe Belohnung in Gold wird von Aufseher Moi'bff Jill in Telaar an denjenigen ausgezahlt, der Giselda ihre gerechte Strafe zukommen lässt.]], },
    { [[Slay Giselda the Crone and any combination of 15 Kil'sorrow Agents. Return to Warden Moi'bff Jill at Telaar should you complete this task.]], },
  },
  [9937] = {
    [[Gesucht: Durn der Nimmersatte]],
    { [[Ihr habt eine der beiden größten Bedrohungen in Nagrand vernichtet, <name>. Seid Ihr nun bereit für die Geißel der Geisterfelder?]], [[Durn der Nimmersatte regiert über das Gebiet beim Oshu'gun und vernichtet alles, was ihm über den Weg läuft. Bis jetzt ist es noch niemandem gelungen, Nagrand von diesem blutrünstigen Gronn zu befreien. Reist zu den Geisterfeldern, weit im Südwesten von hier, und findet Durn. Tötet ihn und ich verspreche Euch eine Belohnung, die der Schwierigkeit dieser Aufgabe angemessen ist.]], },
    { [[Tötet Durn den Nimmersatten. Meldet Euch bei Aufseher Bullrok, wenn Ihr diese Aufgabe erledigt habt.]], },
  },
  [9938] = {
    [[Gesucht: Durn der Nimmersatte]],
    { [[Ihr habt eine der beiden größten Bedrohungen in Nagrand vernichtet, <name>. Seid Ihr nun bereit für die Geißel der Geisterfelder?]], [[Durn der Nimmersatte regiert über das Gebiet beim Oshu'gun und vernichtet alles, was ihm über den Weg läuft. Bis jetzt ist es noch niemandem gelungen, Nagrand von diesem blutrünstigen Gronn zu befreien. Reist zu den Geisterfeldern, weit im Südwesten von hier, und findet Durn. Tötet ihn und ich verspreche Euch eine Belohnung, die der Schwierigkeit dieser Aufgabe angemessen ist.]], },
    { [[Tötet Durn den Nimmersatten. Meldet Euch bei Aufseher Moi'bff Jill, wenn Ihr diese Aufgabe erledigt habt.]], },
  },
  [9939] = {
    [[Gesucht: Zorbo der Berater]],
    { [[Gesucht: tot oder lebendig]], [[Zorbo der Berater, gesucht für mehrere geplante Angriffe auf Garadar. Zusammen mit seinen Häschern und Schamanen hat Zorbo unzählige Orcs getötet. Zorbo ist als bewaffnet, gefährlich und erbarmungslos einzustufen.]], [[Sein Zuhause befindet sich in den Ruinen des Lachenden Schädels in den nördlichen Bergen von Nagrand.]], [[Die Belohnung für den Tod oder die Gefangennahme von Zorbo dem Berater wird von Aufseher Bullrok in Garadar ausbezahlt.]], },
    { [[Slay Zorbo the Advisor, 10 Warmaul Shaman and 10 Warmaul Reavers. Return to Warden Bullrok if you manage to complete this task.]], },
  },
  [9940] = {
    [[Gesucht: Zorbo der Berater]],
    { [[Gesucht: tot oder lebendig]], [[Zorbo der Berater, gesucht für mehrere geplante Angriffe auf Telaar. Zusammen mit seinen Häschern und Schamanen hat Zorbo unzählige Zerschlagene getötet. Zorbo ist als bewaffnet, gefährlich und erbarmungslos einzustufen.]], [[Sein Zuhause befindet sich in den Ruinen des Lachenden Schädels in den nördlichen Bergen von Nagrand.]], [[Die Belohnung für den Tod oder die Gefangennahme von Zorbo dem Berater wird von Aufseher Moi'bff Jill in Telaar ausbezahlt.]], },
    { [[Slay Zorbo the Advisor, 10 Warmaul Shaman and 10 Warmaul Reavers. Return to Warden Moi'bff Jill if you manage to complete this task.]], },
  },
  [9941] = {
    [[Die Schuldigen aufspüren]],
    { [[Die Arakkoaspuren, bei denen auch ein paar flache Furchen zu erkennen sind, führen nach Norden über die Straße hinweg. Es sieht aus, als hätten die Arakkoa etwas vom Schlachtfeld weggeschleift, vielleicht zu einer Siedlung in der Nähe.]], [[Auch wenn ein kaputter Wagen auf einen Kampf hinweist, sind keine Hinweise auf Deiroms Schicksal erkennbar. Folgt den Arakkoaspuren nach Norden und sucht weiter nach dem Händler. Wenn er lebend gefangen wurde, besteht vielleicht immer noch die Chance, ihn zu retten.]], },
    { [[Sucht weiter in der Arakkoasiedlung nördlich des kaputten Wagens nach einem Lebenszeichen von Deirom.]], },
  },
  [9942] = {
    [[Die Schuldigen aufspüren]],
    { [[Die Arakkoaspuren, bei denen auch ein paar flache Furchen zu erkennen sind, führen nach Norden über die Straße hinweg. Es sieht aus, als hätten die Arakkoa etwas vom Schlachtfeld weggeschleift, vielleicht zu einer Siedlung in der Nähe.]], [[Auch wenn ein kaputter Wagen auf einen Kampf hinweist, sind keine Hinweise auf Dugars Schicksal erkennbar. Folgt den Arakkoaspuren nach Norden und sucht weiter nach dem Händler. Wenn er lebend gefangen wurde, besteht vielleicht immer noch die Chance, ihn zu retten.]], },
    { [[Sucht weiter in der Arakkoasiedlung nördlich des kaputten Wagens nach einem Lebenszeichen von Dugar.]], },
  },
  [9943] = {
    [[Rückkehr zu Thander]],
    { [[Deiroms Ladung scheint nicht beschädigt oder geöffnet worden zu sein. Die Ladung hat wohl ihr Interesse geweckt, doch der Gefangene scheint ihnen wichtiger gewesen zu sein. Was die Arakkoa sich von dem Angriff und der Entführung Deiroms erhofft haben, bleibt unklar, aber hier wird der Händler sicher nicht gefangen gehalten.]], [[Da Ihr keine Ahnung habt, wo Ihr die Suche fortsetzen sollt, beschließt Ihr, dass es an der Zeit ist, Thander Eure Entdeckungen mitzuteilen.]], },
    { [[Kehrt mit Euren neuen Erkenntnissen zu Thander in Allerias Feste zurück.]], },
  },
  [9944] = {
    [[Verschollene mag'harische Prozession]],
    { [[Großmutter sagt, dass wir durchhalten und unserem Volk dienen müssen. Sie sagt, dass wir Garrosh als Beispiel dienen müssen, zeigen, dass das Leben weitergeht.]], [[Ich muss mich um so viele Dinge hier kümmern, besonders seit Großmutter krank ist. Würdet Ihr den Mag'har eine Gefallen tun, indem Ihr einer ihrer Matronen helft?]], [[Vor ein paar Tagen haben wir einen Begräbniszug zum Ahnengrund geschickt, der noch nicht zurückgekehrt ist. Würdet Ihr nach ihnen suchen? Sie haben die westliche Straße aus Garadar hinaus genommen. Folgt ihr und meidet Halaa.]], },
    { [[Sucht nach der verschollenen mag'harischen Prozession.]], },
  },
  [9945] = {
    [[Krieg den Totschlägern]],
    { [[Wir waren auf dem Weg zum Ahnengrund, als wir in einen Hinterhalt der Totschläger geraten sind. Sie kamen von allen Seiten, haben viele der Wächter der Prozession getötet und auch viele andere. Sie haben sogar die Überreste des Ältesten im Wagen geschändet.]], [[Und warum? Um uns zu erniedrigen und ihre Macht zu beweisen. Nicht einmal die Felsfäuste hätten sich zu so etwas herabgelassen.]], [[Ich habe mich dem spirituellen Weg verschrieben, <name>, aber auch ich kenne Grenzen. Sucht die Angreifer im Totschlägerhügel im Nordwesten und vernichtet sie!]], },
    { [[Elder Yorley at the Mag'hari Procession in Nagrand has asked that you slay 15 Warmaul Brutes and 15 Warmaul Warlocks.]], },
  },
  [9946] = {
    [[Cho'war der Plünderer]],
    { [[Habt Ihr je einen Ogerkönig gesehen, <race>? Nur wenige haben es überlebt und können davon erzählen. Unglücklicherweise haben wir es genau jetzt mit einem zu tun.]], [[Der Anführer dieser Barbaren ist ein Oger namens Cho'war. Gerüchten zufolge soll er die Gronn aus den Höhlen vertrieben haben. Danach hat sich Cho'war selbst zum König ernannt und die Ogerklans, die unter den Gronn gedient haben, aufgelöst. So entstanden die Totschläger.]], [[Sucht tief in den Höhlen des Totschlägerhügels nach Cho'war. Tötet ihn und bringt mir seinen Kopf.]], },
    { [[Ältester Yorley bei der mag'harischen Prozession in Nagrand möchte, dass Ihr Cho'war den Plünderer tötet und Cho'wars Kopf besorgt.]], },
  },
  [9947] = {
    [[Rückkehr zu Rokag]],
    { [[Dugars Ladung scheint nicht beschädigt oder geöffnet worden zu sein. Die Ladung hat wohl ihr Interesse geweckt, doch der Gefangene scheint ihnen wichtiger gewesen zu sein. Was die Arakkoa sich von dem Angriff und der Entführung Deiroms erhofft haben, bleibt unklar, aber hier wird der Händler sicher nicht gefangen gehalten.]], [[Da Ihr keine Ahnung habt, wo Ihr die Suche fortsetzen sollt, beschließt Ihr, dass es an der Zeit ist, Rokag Eure Entdeckungen mitzuteilen.]], },
    { [[Kehrt mit Euren neuen Erkenntnissen zu Rokag in der Steinbrecherfeste zurück.]], },
  },
  [9948] = {
    [[Findet die Überlebenden]],
    { [[Wenn ich etwas über Oger weiß, dann dass sie Ihr Essen frisch mögen. Das bedeutet, dass die Mag'har, die sie gefangen haben, wahrscheinlich am Leben gehalten werden, bis sie verspeist werden sollen. Ich weiß, das ist ein grausamer Gedanke, aber wir müssen das zu unserem Vorteil ausnutzen.]], [[Geht zum Totschlägerhügel und sucht nach gefangenen Mag'har. Wenn Ihr Gefangene gefunden habt, holt Euch die Schlüssel von den Totschlägern in der Nähe.]], [[Tötet die Oger, um an die Schlüssel zu gelangen, und befreit die Gefangenen.]], },
    { [[Elder Ungriz at the Mag'hari Procession in Nagrand has asked that you free 15 Mag'har Prisoners.]], },
  },
  [9949] = {
    [[Aus der Vogelperspektive]],
    { [[Ohne weitere Informationen sind wir in einer schwierigen Lage. Ich werde die Suche nach Deirom nicht aufgeben. Seine Entführung war kein Zufall, <name>.]], [[Die Arakkoa planen etwas, aber was?]], [[Andarl im Magierturm kennt sich ein wenig mit ihrer besonderen Magie aus. Mit den richtigen Werkzeugen kann er uns neue Informationen besorgen. Er benötigt die Augenkristalle der Arakkoadörfer nördlich der Knochenwüste - dem Skithversteck, dem Reskkversetck und dem Shienorversteck.]], },
    { [[Bringt das Auge des Sktihverstecks, das Auge des Shienorverstecks und das Auge des Reskkverstecks zu Andarl in Allerias Feste.]], },
  },
  [9950] = {
    [[Aus der Vogelperspektive]],
    { [[Ohne weitere Informationen sind wir in einer schwierigen Lage. Ich werde die Suche nach Dugar nicht aufgeben. Seine Entführung war kein Zufall, <name>.]], [[Die Arakkoa planen etwas, aber was?]], [[Kurgatok der Hexenmeister kennt sich ein wenig mit ihrer besonderen Magie aus. Mit den richtigen Werkzeugen kann er uns neue Informationen besorgen. Er benötigt die Augenkristalle der Arakkoadörfer nördlich der Knochenwüste - dem Skithversteck, dem Reskkversetck und dem Shienorversteck.]], },
    { [[Bringt das Auge des Sktihverstecks, das Auge des Shienorverstecks und das Auge des Reskkverstecks zu Krugatok in der Steinbrecherfeste.]], },
  },
  [9951] = {
    [[Es beobachtet Euch!]],
    { [[Schnell, versteckt Euch! Naphthal'ars Auge beobachtet Euch... uns alle!]], [[Ich traue mich nicht, auf die Spitze des Turms zu gehen, um mit ihm zu sprechen, aber Ihr... Ihr seht aus, als könntet Ihr die richtigen Worte finden. Aber seid vorsichtig, es mag keine Witze und kann Dummköpfe nicht leiden.]], [[Überredet es, auf eine Tasse Tee hinunterzukommen. Aber bitte sorgt dafür, dass es mich nicht mehr anstarrt!]], },
    { [[Tötet Naphthal'ar und kehrt dann zu Aufseher Baumlas im Cenariusdickicht in den Wäldern von Terokkar zurück.]], },
  },
  [9952] = {
    [[Ausgrabungsleiter Balmoral]],
    { [[Andarl ist also sicher, dass Deirom im Shalasversteck gefangen gehalten wird? Das ist die größte Arakkoasiedlung, die wir kennen.]], [[Das Shalasversteck liegt weit im Süden der Wälder, nahe bei der östlichen Grenze der Knochenwüste. Ich weiß leider nicht sehr viel darüber, aber dort unten gibt es ein Lager eines Mitglieds der Forscherliga, der Euch vielleicht weiterhelfen kann. Ausgrabungsleiter Balmorals Lager befindet sich am äußersten Rand des Landes südwestlich vom Shalasversteck. Befragt ihn zunächst, bevor Ihr etwas gegen die Arakkoa unternehmt.]], },
    { [[Findet und sprecht mit Ausgrabungsleiter Balmoral.]], },
  },
  [9953] = {
    [[Aufklärer Nodak]],
    { [[Kurgatok ist also sicher, dass Dugar im Shalasversteck gefangen gehalten wird? Das ist die größte Arakkoasiedlung, die wir kennen.]], [[Das Shalasversteck liegt weit im Süden der Wälder, nahe bei der östlichen Grenze der Knochenwüste. Ich weiß leider nicht sehr viel darüber, aber einer unserer Späher hat dort unten sein Lager. Vielleicht kann er Euch weiterhelfen. Aufklärer Nodaks Lager befindet sich am äußersten Rand des Landes südwestlich vom Shalasversteck. Befragt ihn zunächst, bevor Ihr etwas gegen die Arakkoa unternehmt.]], },
    { [[Findet und sprecht mit Aufklärer Nodak.]], },
  },
  [9954] = {
    [[Corkis Lösegeld]],
    { [[<Arechron zeigt Euch einen rußverschmierten Brief.>]], [[Ich habe gerade diesen Brief erhalten. Er ist von einem Ogerstamm, die sich selbst Totschläger nennen. Sie herrschen über die nordwestlichen Gebiete von Nagrand. Hier steht, dass sie Corki in ihrer Gewalt haben, und sie drohen ihn aufzufressen, wenn wir nicht kooperieren. Nunja, Corki ist ein Dummkopf, aber er gehört zur Familie. Würdet Ihr ihn retten, <name>?]], [[Ich nehme an, dass er in einem Käfig auf dem Totschlägerhügel eingesperrt ist. Dort solltet Ihr zuerst nachsehen.]], },
    { [[Arechron möchte, dass Ihr Corki auf dem Totschlägerhügel findet.]], },
  },
  [9955] = {
    [[Cho'war der Plünderer]],
    { [[Sie nennen ihn Cho'war den Plünderer, <name>. Er ist der Oger, der die Gronn, die über die Oger geherrscht hatten, vertrieben hat. Ihr könnt mir ruhig glauben, dass er mächtig ist!]], [[Der Chef hat gesagt, dass Cho'war in der großen Höhle wohnt - wo immer das auch sein mag... Besorgt diesen Schlüssel und holt mich hier raus! Wenn wir das hier überleben, wird mein Vater Euch sicher reich belohnen! Geht zu ihm sobald ich frei bin.]], },
    { [[Corki möchte, dass Ihr Cho'wars Schlüssel besorgt, um ihn aus dem Käfig zu befreien. Kehrt zu Arechron in Telaar zurück, falls Ihr Erfolg haben solltet.]], },
  },
  [9956] = {
    [[Die Überfallene Karawane]],
    { [[Ich war bei einer Karawane dabei, die Vorräte zur Oreborzuflucht in den Zangarmarschen bringen sollte. An der Grenze zu den Marschen wurden wir jedoch von einer hinterlistigen Truppe von Totschlägerogern überfallen. Sie haben die anderen getötet und mich sowie all unsere Vorräte beschlagnahmt.]], [[Zum Glück haben sie vergessen, meinen Käfig zu verriegeln. Als sie eingeschlafen waren, bin ich geflohen.]], [[Es ist zwar zu spät für meine Kameraden, aber wir könnten die gestohlenen Vorräte wirklich gebrauchen. Den Totschlägerhügel findet Ihr in den nordwestlichen Bergen von Nagrand.]], },
    { [[Huntress Kima at Telaar in Nagrand wants you to recover 20 Telaar Supply Crates.]], },
  },
  [9957] = {
    [[Was ist los mit dem Cenariusdickicht?]],
    { [[Wir haben schon eine Weile nichts mehr von den Druiden des Cenariusdickichts in den Wäldern von Terokkar gehört.]], [[Ich möchte, dass Ihr dort hinüber geht und nachschaut, was los ist. Es gibt viele Wege dorthin, aber der kürzeste führt den Weg nach Süden entlang. Biegt die Straße nach Osten von Shattrath ab, wenn Ihr im Wald seid.]], },
    { [[Sprecht mit den Druiden im Cenariusdickicht in den Wäldern von Terokkar.]], },
  },
  [9958] = {
    [[Die Verteidigung ausspähen]],
    { [[Seit ich hier bin habe ich schon öfter mit den Arakkoa zu tun gehabt, und das war alles andere als freundlich. Sie scheinen sehr ortsgebunden zu sein, aber das sind wir ja auch, oder?]], [[Die Arakkoa verfügen über eine Art ursprüngliche Magie, die in Azeroth völlig unbekannt ist. Mit ihrer Hilfe haben sie die magische Verteidigung des Shalasverstecks gewirkt. Bevor wir gegen sie vorgehen können, wäre es klug, diese Verteidigungseinrichtungen zunächst auszuspähen, damit wir wissen, wogegen wir ankommen müssen.]], },
    { [[Begebt Euch in die Nähe des Shalasverstecks und beobachtet die Verteidigungseinrichtungen des Dorfes.]], },
  },
  [9959] = {
    [[Die Verteidigung ausspähen]],
    { [[Seit ich hier bin habe ich schon öfter mit den Arakkoa zu tun gehabt, und das war alles andere als freundlich. Sie scheinen sehr ortsgebunden zu sein, aber das sind wir ja auch, oder?]], [[Die Arakkoa verfügen über eine Art ursprüngliche Magie, die in Azeroth völlig unbekannt ist. Mit ihrer Hilfe haben sie die magische Verteidigung des Shalasverstecks gewirkt. Bevor wir gegen sie vorgehen können, wäre es klug, diese Verteidigungseinrichtungen zunächst auszuspähen, damit wir wissen, wogegen wir ankommen müssen.]], },
    { [[Begebt Euch in die Nähe des Shalasverstecks und beobachtet die Verteidigungseinrichtungen des Dorfes.]], },
  },
  [9960] = {
    [[Was ist los mit dem Cenariusdickicht?]],
    { [[Hey Ihr da, <class>! Wir haben schon eine Weile nichts mehr von den Druiden des Cenariusdickichts gehört.]], [[Ich möchte, dass Ihr dort hinüber geht und nachschaut, was los ist. Ihr findet es, wenn Ihr Richtung Nordwesten durch den Wald geht.]], },
    { [[Sprecht mit den Druiden im Cenariusdickicht in den Wäldern von Terokkar.]], },
  },
  [9961] = {
    [[Was ist los mit dem Cenariusdickicht?]],
    { [[<class>, ich habe eine eilige Aufgabe für Euch. Wir haben schon eine Weile nichts mehr von den Druiden des Cenariusdickichts gehört. Das ist sehr merkwürdig, da sie sich normalerweise alle paar Tage bei uns melden.]], [[Ich möchte, dass Ihr dort hinüber geht und nachschaut, was los ist. Ihr findet es, wenn Ihr Richtung Nordwesten durch den Wald geht.]], },
    { [[Sprecht mit den Druiden im Cenariusdickicht in den Wäldern von Terokkar.]], },
  },
  [9962] = {
    [[Der Ring des Blutes: Schmetterzehe]],
    { [[Seid Ihr bereit für den ersten Kampf? Wenn Ihr soweit seid, gebt mir Bescheid und ich bringe die Dinge ins Rollen. Ihr müsst nur im Ring des Blutes auf Euren ersten Gegner warten.]], [[Euer erster Herausforderer wird Schmetterzehe sein, ein grausamer Grollhuf.]], [[Viel Glück! Ich hoffe, Ihr habt ein paar Freunde mitgebracht.]], [[Und denkt daran, der Kampf ist erst zu Ende, wenn Ihr oder Euer Gegner mit dem Gesicht nach unten auf dem Boden liegt.]], },
    { [[Besiegt Schmetterzehe. Wenn Ihr gewinnen solltet, sprecht mit Wodin dem Trolldiener um Eure Belohnung zu erhalten.]], },
  },
  [9963] = {
    [[Hilfe von der Geißel]],
    { [[Die Schutzzauber, die Ihr bemerkt habt, sind tatsächlich sehr starke Magie. Die Fertigkeiten der Arakkoa entziehen sich unserem Verständnis, weswegen wir nur hoffen können, dass wir mit ihrer Hilfe einen Gegenzauber finden können.]], [[Ich weiß, was Ihr denkt. Warum sollte der Feind uns helfen wollen, seine Verteidigung zu durchbrechen? Die Antwort ist natürlich, dass er das nicht will. Habt Ihr die Geschichten über den verrückten alten Arakkoa namens Kokorek gehört? Man sagt, er hielte sich im Lithicversteck am westlichen Ende der Knochenwüste auf und warte auf die Rückkehr längst vergangener Zeiten.]], },
    { [[Sucht Kokorek im Lithicversteck und sprecht mit ihm.]], },
  },
  [9964] = {
    [[Hilfe von der Geißel]],
    { [[Die Schutzzauber, die Ihr bemerkt habt, sind tatsächlich sehr starke Magie. Die Fertigkeiten der Arakkoa entziehen sich unserem Verständnis, weswegen wir nur hoffen können, dass wir mit ihrer Hilfe einen Gegenzauber finden können.]], [[Ich weiß, was Ihr denkt. Warum sollte der Feind uns helfen wollen, seine Verteidigung zu durchbrechen? Die Antwort ist natürlich, dass er das nicht will. Habt Ihr die Geschichten über den verrückten alten Arakkoa namens Kokorek gehört? Man sagt, er hielte sich im Lithicversteck am westlichen Ende der Knochenwüste auf und warte auf die Rückkehr längst vergangener Zeiten.]], },
    { [[Sucht Kokorek im Lithicversteck und sprecht mit ihm.]], },
  },
  [9965] = {
    [[Guten Willen zeigen]],
    { [[Ich möchte Euch einen Handel vorschlagen: zeigt mir, dass Ihr gute Absichten habt, indem Ihr mir bei der Lösung eines kleinen Problems helft, und ich werde Euch bei der Rettung Eures Freundes unterstützen.]], [[Die Oger sind ein uralter Feind meines Volkes, und in letzter Zeit haben sie begonnen, vom Grasland in Nagrand zurück in unsere Wälder zu ziehen.]], [[Einer dieser Oger, Mug'gok, ist ein ziemlich abenteuerlicher Geselle, aber leider kein allzu kluger. Er reist die ganze Zeit hin und her, bereitet seinen Freunden in den Ruinen des Blutenden Auges Ärger und provoziert uns.]], },
    { [[Bringt Mug'goks Kopf zu Kokorek im Lithicversteck.]], },
  },
  [9966] = {
    [[Guten Willen zeigen]],
    { [[Ich möchte Euch einen Handel vorschlagen: zeigt mir, dass Ihr gute Absichten habt, indem Ihr mir bei der Lösung eines kleinen Problems helft, und ich werde Euch bei der Rettung Eures Freundes unterstützen.]], [[Die Oger sind ein uralter Feind meines Volkes, und in letzter Zeit haben sie begonnen, vom Grasland in Nagrand zurück in unsere Wälder zu ziehen.]], [[Einer dieser Oger, Mug'gok, ist ein ziemlich abenteuerlicher Geselle, aber leider kein allzu kluger. Er reist die ganze Zeit hin und her, bereitet seinen Freunden in den Ruinen des Blutenden Auges Ärger und provoziert uns.]], },
    { [[Bringt Mug'goks Kopf zu Kokorek im Lithicversteck.]], },
  },
  [9967] = {
    [[Der Ring des Blutes: Die Blaumänner]],
    { [[Im nächsten Kampf werdet Ihr auf gleich zwei Gegner treffen, Zwillinge um genau zu sein. Sie nennen sich selbst die "Blaumänner".]], [[Es ist noch nicht zu spät, um zu kneifen, <name>. Ich könnte das verstehen.]], [[Aber wenn Ihr bereit seid für ein wenig Action, tretet in den Ring des Blutes und wartet auf die Zwillinge der Finsterblut.]], [[Der Letzte, der noch steht, hat gewonnen!]], },
    { [[Besiegt die Zwillinge der Finsterblut, die sich selbst die Blaumänner nennen. Wenn Ihr gewinnen solltet, sprecht mit Wodin dem Trolldiener um Eure Belohnung zu erhalten.]], },
  },
  [9968] = {
    [[Fremde Energien]],
    { [[Etwas Merkwürdiges hat die Teromotten des Dickichts verändert.]], [[Wo einst eine Vielfalt von Arten herrschte, sind jetzt nur noch die Teromotten übrig. Und während sie früher friedlich waren, sind sie jetzt sehr aufgeregt und greifen alles an, was ihnen zu nahe kommt.]], [[Ich muss die bösartigen mit den normalen Motten vergleichen. Bringt mir ein paar Exemplare von beiden Sorten. Die gewöhnlichen Teromotten findet Ihr im Süden und im Norden.]], [[Ich bin mir sicher, dass dies mit dem Tod der Druiden zusammenhängt.]], },
    { [[Collect 4 Teromoth Samples and 4 Vicious Teromoth Samples.  Then return to Earthbinder Tavgren just outside the Cenarion Thicket in Terokkar Forest.]], },
  },
  [9969] = {
    [[Die letzten Reagenzien]],
    { [[Ich kann einen Gegenstand herstellen, der den Schutzzauber, der das Shalasversteck umgibt, aufheben kann, aber seid gewarnt, <name>, die Wirkung wird nur so lange andauern, bis Ihr Euren Freund befreit habt. Ich werde Euch keine Waffe geben, mit der Ihr mein Volk wann immer Ihr wollt angreifen könnt.]], [[Ich habe fast alle Zutaten, die ich für diesen Zauber brauche, aber Ihr müsst noch ein paar weitere für mich sammeln, bevor ich ihn abschließen kann. Ich brauche etwas Sphärenpirscheressenz und eine Handvoll Apfelbeeren von Terokkar. Ihr könnt beides um das Versteck herum finden.]], },
    { [[Bringt 5 Sphärenpirscheressenzen und 30 Apfelbeeren von Terokkar zu Kokorek im Lithicversteck.]], },
  },
  [9970] = {
    [[Der Ring des Blutes: Rokdar der Zerklüftete]],
    { [[Das wird ein harter Kampf, <name>. Habt Ihr vielleicht ein paar Freunde, die Euch helfen können? Den Ogern ist es gelungen, einen Riesen namens Rokdar aus dem Schergrat hierher zu bringen! Glaubt mir, Ihr werdet ein paar Freunde brauchen, er scheint unbesiegbar zu sein! Er ist hart wie Stein - im wahrsten Sinne des Wortes!]], [[Wenn Ihr immer noch gegen ihn antreten möchtet, tretet in den Ring des Blutes und er wird kommen.]], },
    { [[Besiegt Rokdar den Zerklüfteten. Wenn Ihr gewinnen solltet, sprecht mit Wodin dem Trolldiener um Eure Belohnung zu erhalten.]], },
  },
  [9971] = {
    [[Hinweise im Dickicht]],
    { [[Ich habe keine Ahnung, was all die Druiden hier getötet hat. Ich war ein paar Tage unterwegs, um die Knochenwüste im Süden zu untersuchen.]], [[Bitte, Ihr müsst mir helfen herauszufinden, was hinter dieser furchtbaren Tragödie steckt. Geht zum Dickicht und haltet nach einem Hinweis Ausschau, der uns weiterhelfen könnte. Als ich kurz dort war, hätte ich schwören können, dass ich einen toten Zerschlagenen und ein merkwürdiges Ding neben ihm gesehen habe.]], [[Und habt keine Angst vor Baumlas - er ist vielleicht durch die Geschehnisse verrückt geworden, aber ich glaube, er ist harmlos.]], },
    { [[Untersucht das merkwürdige Objekt neben der Leiche eines Zerschlagenen um herauszufinden, was hinter der Tragödie beim Cenariusdickicht steckt. Kehrt dann zu Erdbinder Tavgren außerhalb des Dickichts in den Wäldern von Terokkar zurück.]], },
  },
  [9972] = {
    [[Der Ring des Blutes: Skra'gath]],
    { [[Ihr bringt sie ja reihenweise um, <Kleiner/Kleine>! Dieses Mal haben sie einen Profi für den Kampf. Sie sagen er sei ein Leerenfürst oder so was... Sie wollen Euch auf keinen Fall gegen ihren Champion kämpfen lassen. Habt Ihr gehört wie Mogor, das Fallobst, Euch angekläfft hat? Die Wetten stehen astronomisch hoch!]], [[Steigt in den Ring und haut ihn um!]], },
    { [[Besiegt Skra'gath. Wenn Ihr gewinnen solltet, sprecht mit Wodin dem Trolldiener um Eure Belohnung zu erhalten.]], },
  },
  [9973] = {
    [[Der Ring des Blutes: Champion der Totschläger]],
    { [[Sie mussten den Champion aus den Gladiatorengruben im Schergrat einschiffen. Dort hat er mit den Bergriesen trainiert - mit gleich dreien auf einmal!]], [[Wenn Ihr gegen ihn gewinnt, seid Ihr der neue Champion. Glaubt Ihr, Ihr habt eine Chance? Könnt Ihr das schaffen? Sagt mir nur Bescheid, dann setze ich mein Haus auf Euch.]], [[Jetzt steigt in den Ring und gewinnt!]], },
    { [[Besiegt den Champion der Totschläger. Wenn Ihr gewinnen solltet, sprecht mit Wodin dem Trolldiener, um Eure Belohnung zu erhalten.]], },
  },
  [9974] = {
    [[Die letzten Reagenzien]],
    { [[Bringt 5 Sphärenpirscheressenzen und 30 Apfelbeeren von Terokkar zu Kokorek im Lithicversteck.]], },
    { [[Bringt 5 Sphärenpirscheressenzen und 30 Apfelbeeren von Terokkar zu Kokorek im Lithicversteck.]], },
  },
  [9975] = {
    [[Urmagie]],
    { [[Ich habe den Zauber in diesem Talisman aufbewahrt. Entfesselt ihn, wenn Ihr bei den Totems steht, die den Zugang zum Shalasversteck bewachen. Er sollte stark genug sein, um die Wächter außer Gefecht zu setzen.]], [[<Ihr bemerkt, dass die Figur des Talismans nicht wie ein Arakkoa aussieht.>]], [[Mein Volk sah nicht immer so aus wie jetzt. Einst waren wir prächtige Wesen, aber wir... wir haben die falschen Entscheidungen getroffen und sind bei unserer heiligen Aufgabe gescheitert. Das hat uns unsere Schönheit, unsere Macht und fast unser Leben gekostet. Wiederholt unsere Fehler nicht, <name>.]], },
    { [[Benutzt Kokoreks Talisman in der Nähe der Totems, die den Weg ins Shalasversteck bewachen, und sprecht dann mit Ausgrabungsleiter Balmoral.]], },
  },
  [9976] = {
    [[Urmagie]],
    { [[Ich habe den Zauber in diesem Talisman aufbewahrt. Entfesselt ihn, wenn Ihr bei den Totems steht, die den Zugang zum Shalasversteck bewachen. Er sollte stark genug sein, um die Wächter außer Gefecht zu setzen.]], [[<Ihr bemerkt, dass die Figur des Talismans nicht wie ein Arakkoa aussieht.>]], [[Mein Volk sah nicht immer so aus wie jetzt. Einst waren wir prächtige Wesen, aber wir... wir haben die falschen Entscheidungen getroffen und sind bei unserer heiligen Aufgabe gescheitert. Das hat uns unsere Schönheit, unsere Macht und fast unser Leben gekostet. Wiederholt unsere Fehler nicht, <name>.]], },
    { [[Benutzt Kokoreks Talisman in der Nähe der Totems, die den Weg ins Shalasversteck bewachen, und sprecht dann mit Aufklärer Nodak.]], },
  },
  [9977] = {
    [[Der Ring des Blutes: Die letzte Herausforderung]],
    { [[<Gurgthock zählt seinen riesigen Berg Gold.>]], [[Ihr habt viel Geld eingebracht, <Kleiner/Kleine>. Wenn Ihr Mogor besiegt, verspreche ich, dass es sich für euch lohnen wird. Die ganzen Schmerzen, die Ihr im Ring des Blutes aushalten musstet, werden sich bezahlt machen.]], [[Jetzt geht da rein und zeigt diesen Ogern, was in Euch steckt!]], },
    { [[Besiegt Mogor, Held der Totschläger. Wenn Ihr gewinnen solltet, sprecht mit Wodin dem Trolldiener um Eure Belohnung zu erhalten.]], },
  },
  [9978] = {
    [[Mit allen Mitteln]],
    { [[Ich bin mir nicht sicher, was das zu bedeuten hat, aber ich weiß, wie wir an weitere Hinweise kommen können.]], [[Empoor ist ein Händler der Zerschlagenen, der mit allen Arten von Waren und Informationen handelt. Wenn wir ihn zum Reden bringen könnten, bin ich mir sicher, dass er etwas Interessantes zu sagen hätte.]], [[Er reist auf der Straße zwischen Tuurem und Shattrath hin und her. Seid aber vorsichtig, er hat einen Leibwächter.]], },
    { [[Find and convince Empoor to tell you what he knows.]], },
  },
  [9979] = {
    [[Windhändler Lathrai]],
    { [[Ich wurde gebeten, ein paar Kästen mit Waren bei einem Astralen in Shattrath abzuholen und auszuliefern.]], [[Als ich ihn das letzte Mal gesehen habe, stand er außerhalb der Taverne Weltenend im Unteren Viertel. Sein Name ist Windhändler Lathrai, mehr weiß ich nicht!]], [[Wenn Ihr mehr wissen wollt, dann fragt ihn selbst!]], },
    { [[Sprecht mit Lathrai in der Nähe der Taverne Weltenend im Unteren Viertel in Shattrath.]], },
  },
  [9980] = {
    [[Rettet Deirom!]],
    { [[Macht Euch so schnell Ihr könnt ins Shalasversteck auf, <name>! Zeit ist von äußerster Wichtigkeit. Wenn die Arakkoa bemerken, was Ihr mit ihrer Verteidigung angestellt habt, werden sie wahrscheinlich versuchen, Deirom wegzubringen oder gar zu töten!]], [[Nehmt ein oder zwei Freunde mit, wenn Ihr könnt. Es wird wahrscheinlich hart werden. Die Vogelwesen haben alles getan, um ihren Gefangenen unter Verschluss zu halten, und sie werden ihn sicher nicht so leicht aufgeben. Wenn Ihr Deirom gefunden habt, kehrt zu Allerias Feste zurück und benachrichtigt Thander.]], },
    { [[Begebt Euch ins Shalasversteck, tötet Lunga und befreit Deirom. Meldet Euch bei Lady Dana Kennedy in Allerias Feste, wenn Ihr Euren Auftrag erledigt habt.]], },
  },
  [9981] = {
    [[Rettet Dugar!]],
    { [[Macht Euch so schnell Ihr könnt ins Shalasversteck auf, <name>! Zeit ist von äußerster Wichtigkeit. Wenn die Arakkoa bemerken, was Ihr mit ihrer Verteidigung angestellt habt, werden sie wahrscheinlich versuchen, Dugar wegzubringen oder gar zu töten!]], [[Nehmt ein oder zwei Freunde mit, wenn Ihr könnt. Es wird wahrscheinlich hart werden. Die Vogelwesen haben alles getan, um ihren Gefangenen unter Verschluss zu halten, und sie werden ihn sicher nicht so leicht aufgeben. Wenn Ihr Dugar gefunden habt, kehrt zur Steinbrecherfeste zurück und benachrichtigt Rokag.]], },
    { [[Begebt Euch ins Shalasversteck, tötet Lunga und befreit Dugar. Meldet Euch bei Rokag in der Steinbrecherfeste, wenn Ihr Euren Auftrag erledigt habt.]], },
  },
  [9982] = {
    [[Er nannte sich Altruis...]],
    { [[Ende letzter Woche kam ein Wesen auf einem Netherdrachen ins Dorf. Er stellte sich als Altruis vor und stellte uns ein paar Fragen über Nagrand.]], [[Er sagte, er würde die Legion jagen. Das so etwas Wahnsinniges tatsächlich sein Job wäre. Könnt Ihr Euch das vorstellen? Eine Person, die freiwillig Dämonen jagt?]], [[Vor seiner Abreise bat er uns noch, angehende Helden wissen zu lassen, dass er eine Weile im Westen Nagrands bleiben würde, falls sie auf der Suche nach einer Aufgabe wären.]], },
    { [[Wenn Ihr die Brennende Legion bekämpfen wollt, sucht Altruis den Leider im Westen von Nagrand.]], },
  },
  [9983] = {
    [[Er nannte sich Altruis...]],
    { [[Ende letzter Woche kam ein Wesen auf einem Netherdrachen ins Dorf. Er stellte sich als Altruis vor und stellte uns ein paar Fragen über Nagrand.]], [[Er sagte, er würde die Legion jagen. Das so etwas Wahnsinniges tatsächlich sein Job wäre. Könnt Ihr Euch das vorstellen? Eine Person, die freiwillig Dämonen jagt?]], [[Vor seiner Abreise bat er uns noch, angehende Helden wissen zu lassen, dass er eine Weile im Westen Nagrands bleiben würde, falls sie auf der Suche nach einer Aufgabe wären.]], },
    { [[Wenn Ihr die Brennende Legion bekämpfen wollt, sucht Altruis den Leider im Westen von Nagrand.]], },
  },
  [9984] = {
    [[Der Herr der versteckten Stadt]],
    { [[Auch wenn Ihr die merkwürdige Schrift auf der Rolle nicht lesen könnt, deutet das verzierte Siegel am Ende des Dokuments darauf hin, dass es von einem Arakkoa von höherem Rang geschrieben wurde. Luanga mag vielleicht hinter der Gefangennahme und dem Verhör von Deirom gesteckt haben, aber es scheint, als hätte er die Befehle einer anderen Person ausgeführt.]], [[Ich erinnert Euch, dass Thander erwähnt hat, dass Andarl die Arakkoa studiert hat. Vielleicht kann er den Inhalt der Schriftrolle entziffern.]], },
    { [[Take Luanga's Orders to Andarl at the Allerian Stronghold.]], },
  },
  [9985] = {
    [[Der Herr der versteckten Stadt]],
    { [[Auch wenn Ihr die merkwürdige Schrift auf der Rolle nicht lesen könnt, deutet das verzierte Siegel am Ende des Dokuments darauf hin, dass es von einem Arakkoa von höherem Rang geschrieben wurde. Luanga mag vielleicht hinter der Gefangennahme und dem Verhör von Dugar gesteckt haben, aber es scheint, als hätte er die Befehle einer anderen Person ausgeführt.]], [[Ich erinnert Euch, dass Rokag erwähnt hat, dass Kurgatok die Arakkoa studiert hat. Vielleicht kann er den Inhalt der Schriftrolle entziffern.]], },
    { [[Bring Luanga's Orders to Kurgatok at Stonebreaker Hold.]], },
  },
  [9986] = {
    [[Die Arakkoa hindern]],
    { [[Wir müssen den Arakkoa zeigen, dass sie unsere Leute nicht ungestraft angreifen können. Wenn wir sie jetzt nicht aufhalten, werden unsere Straßen für die Händler, Kuriere oder Soldaten nicht mehr sicher sein.]], [[Meine Späher haben die Anführer der größeren Arakkoasiedlungen in der Gegend ermittelt. Sucht Ashkaz, Ayit und Urdak im Reskkversteck, im Shienorversteck und im Skithversteck und tötet sie.]], },
    { [[Tötet Ashkaz im Reskkversteck, Ayit im Shienorversteck und Urdak im Skithversteck und kehrt dann zu Thander in Allerias Feste zurück.]], },
  },
  [9987] = {
    [[Die Arakkoa hindern]],
    { [[Wir müssen den Arakkoa zeigen, dass sie unsere Leute nicht ungestraft angreifen können. Wenn wir sie jetzt nicht aufhalten, werden unsere Straßen für die Händler, Kuriere oder Soldaten nicht mehr sicher sein.]], [[Meine Späher haben die Anführer der größeren Arakkoasiedlungen in der Gegend ermittelt. Sucht Ashkaz, Ayit und Urdak im Reskkversteck, im Shienorversteck und im Skithversteck und tötet sie.]], },
    { [[Tötet Ashkaz im Reskkversteck, Ayit im Shienorversteck und Urdak im Skithversteck und kehrt dann zu Rokag in der Steinbrecherfeste zurück.]], },
  },
  [9988] = {
    [[Der beste Freund eines Gecken]],
    { [[Lasst Euch von Thander und seinem Gefolge nicht täuschen. Sie sehen vielleicht aus wie eine Gruppe von Furcht einflößenden Kriegern, aber sie sind nichts als eine Bande von Gecken!]], [[Und sie lassen sich in der Öffentlichkeit nicht ohne ihre Hüte sehen. Das sind ganz schicke Teile aus Filz und Fell, mit den ausgefallensten Federn, die Ihr je gesehen habt.]], [[Die begehrtesten Federn stammen natürlich von den Arakkoa, und mein Vorrat geht langsam zur Neige. Ich würde Euch bezahlen, wenn Ihr sie für mich sammelt.]], },
    { [[Bringt 30 Arakkoafedern zu Ros'eleth in Allerias Feste.]], },
  },
  [9989] = {
    [[Fremde Geister]],
    { [[Als ich in die Scherbenwelt kam und meine Ohren den Stimmen seiner Geister geöffnet habe, war ich erstaunt, was ich zu hören bekam. Nach einiger Zeit fand ich auch heraus, welche Geister ich nicht hören konnte.]], [[Trotz all meiner Versuche habe ich niemals mit den Geistern der Arakkoaahnen Kontakt aufnehmen können. Ihre Magie unterscheidet sich grundlegend von unserer, aber ich bin mir nicht sicher, ob ihre Geister außerhalb meiner Reichweite liegen.]], [[Bringt mir die Federn vieler Arakkoa. Vielleicht kann ein Zeichen ihres Volks die nötige Verbindung herstellen.]], },
    { [[Bringt 30 Arakkoafedern zu Malukaz in der Steinbrecherfeste.]], },
  },
  [9990] = {
    [[Untersuchung von Tuurem]],
    { [[Wisst Ihr, jetzt, da Ihr es sagt, erinnere ich mich daran, dass ich Empoor vor etwa einem Monat mehrere Kästen mit merkwürdigen und fremdartigen Bauteilen verkauft habe. Ich weiß nicht, wozu sie gut sind, aber ich weiß, dass sie nach Tuurem geliefert werden sollten.]], [[Vielleicht sind dort ja noch ein paar Kästen davon?]], },
    { [[Findet den versiegelten Kasten und bringt ihn zu Erdbinder Tavgren beim Cenariusdickicht in den Wäldern von Terokkar.]], },
  },
  [9991] = {
    [[Inspektion des Lagers]],
    { [[Wenn Ihr meine Augen sein wollt, müsst Ihr sehr sorgfältig sein. Nethrandamus wird Euch durch die Lager der Legion führen, aber Ihr müsst alles, was Ihr seht, katalogisieren. Selbst das kleinste Detail, und wenn Euch noch so unwichtig erscheint, kann für unsere Zwecke hilfreich sein.]], [[Zuerst möchte ich, dass Ihr notiert, welchen Aufbau das Lager hat und wie seine Verteidigungsmaßnahmen aussehen.]], [[Nethrandamus ist bereit, wenn Ihr es seid, <name>. Habt keine Angst, er wird Euch beschützen.]], },
    { [[Lehnt Euch zurück und genießt Euren Flug. Sprecht mit Altruis wenn die Besichtigung abgeschlossen ist.]], },
  },
  [9992] = {
    [[Olembasamen]],
    { [[Sind die Bäume des Waldes nicht wunderschön? Ich habe mich schon oft gefragt, ob sie wohl auch in Azeroth wachsen würden.]], [[Ich scheue mich jedoch davor, Keimlinge zurückzuschicken. Es ist ein anstrengender Weg und sie sind viel schwieriger zu transportieren als ein Sack mit Samen. Die Samenkörner wachsen in harten Zapfen, die von den Ästen der Bäume fallen, wenn sie reif sind.]], [[Wenn Ihr auf Euren Reisen ein paar dieser Zapfen findet, könntet Ihr mir dann ein paar Samenkörner bringen?]], },
    { [[Sammelt 30 Olembasamenkörner und kehrt dann zu Ros'eleth in Allerias Feste zurück.]], },
  },
  [9993] = {
    [[Olembaöl]],
    { [[Die öligen Samen der Olembabäume könnten sich als sehr wertvoll erweisen. Ich würde gern eine größere Menge davon sammeln und schauen, was ich daraus machen kann.]], [[Stellt Euch nur vor, da steht ein ganzer Wald mit Bäumen, die so viele Samen produzieren, die alle ungenutzt bleiben. Mir ist egal, ob ich nur Salatöl, Waffenöl oder Lampenöl daraus herstellen kann, ich werde ihre Verschwendung nicht dulden!]], [[Bringt mir alle Samen, die Ihr von den Olembazapfen sammeln könnt. Sie liegen hier überall.]], },
    { [[Bringt 30 Olembasamenkörner zu Rakoria in der Steinbrecherfeste.]], },
  },
  [9994] = {
    [[Was ist das?]],
    { [[Ich weiß nicht, wie es Euch geht, aber ich habe keine Ahnung, was das ist. Ich weiß nur eins: sie sehen gefährlich aus!]], [[<name>, ich glaube, Ihr solltet diesen Kasten mit Ersatzteilen zu Jenai Sternwisper in Allerias Feste im Südosten bringen. Vielleicht hat sie ja eine Ahnung, was das ist.]], },
    { [[Bringt den Kasten mit Ersatzteilen zu Jenai Sternwisper in Allerias Feste in den Wäldern von Terokkar.]], },
  },
  [9995] = {
    [[Was ist das?]],
    { [[Ich weiß nicht, wie es Euch geht, aber ich habe keine Ahnung, was das ist. Ich weiß nur eins: sie sehen gefährlich aus!]], [[<name>, ich glaube, Ihr solltet diesen Kasten mit Ersatzteilen zu Tooki in die Steinbrecherfeste im Südosten bringen. Vielleicht hat er ja eine Ahnung, was das ist.]], },
    { [[Bringt den Kasten mit Ersatzteilen zu Tooki in der Steinbrecherfeste in den Wäldern von Terokkar.]], },
  },
  [9996] = {
    [[Angriff auf den Posten der Feuerschwingen]],
    { [[Wir wissen, dass die Blutelfen der Feuerschwingen für Illidan arbeiten. Und nach dem, was Ihr mir erzählt habt, benutzen sie die Zerschlagenen von Tuurem als Schmuggler, um eine furchtbare Waffe herzustellen.]], [[Was immer sie vorhaben mögen, das Beste, was wir nun tun können, ist, sie zu dezimieren.]], [[Die Blutelfen der Feuerschwingen müssen vernichtet werden! Begebt Euch zum Posten der Feuerschwingen im Norden und kümmert Euch darum!]], },
    { [[Tötet 10 Verteidiger der Feuerschwingen, 10 Blutwärter der Feuerschwingen und 10 Hexenmeister der Feuerschwingen. Meldet Euch dann bei Leutnant Meridian bei Allerias Posten in den Wäldern von Terokkar zurück.]], },
  },
  [9997] = {
    [[Angriff auf den Posten der Feuerschwingen]],
    { [[Wir wissen, dass die Blutelfen der Feuerschwingen für Illidan arbeiten. Und nach dem, was Ihr mir erzählt habt, benutzen sie die Zerschlagenen von Tuurem als Schmuggler, um eine furchtbare Waffe herzustellen.]], [[Was immer sie vorhaben mögen, das Beste, was wir nun tun können, ist, sie zu dezimieren.]], [[Die Blutelfen der Feuerschwingen müssen vernichtet werden! Begebt Euch zum Posten der Feuerschwingen im Nordosten und kümmert Euch darum!]], },
    { [[Tötet 10 Verteidiger der Feuerschwingen, 10 Blutwärter der Feuerschwingen und 10 Hexenmeister der Feuerschwingen. Meldet Euch dann bei Unteroffizier Chawni im Steinbrecherlager in den Wäldern von Terokkar zurück.]], },
  },
  [9998] = {
    [[Widerspenstige Nachbarn]],
    { [[Thander ist über die Ankunft der Höllenorcs bei den Ruinen vor Kurzem sowohl überrascht als auch besorgt. Sie haben keine Zeit verschwendet und sofort begonnen, Befestigungsanlagen zu bauen und die Gebäude zu reparieren.]], [[Die Bauarbeiten laufen Tag und Nacht, <name>. Ich habe ein paar meiner zuverlässigsten Agenten losgeschickt, um die Situation zu überwachen, aber wir können uns keine Verzögerungen leisten. Geht gegen ihre Arbeiter vor und sucht dann nach Theloria Dämmermantel bei Allerias Posten nördlich der Ruinen der Knochenmalmer. Sie wird Euch weitere Anweisungen geben.]], },
    { [[Kill 12 Warped Peons and then report to Theloria Shadecloak at Allerian Post.]], },
  },
  [9999] = {
    [[Spiel auf Zeit]],
    { [[Solange die Transporter noch stehen, ist nichts, was wir gegen die Konstruktionslager unternehmen, von Dauer. Wir müssen einen Weg finden, die Gebäude der Legion zu zerstören.]], [[Ich brauche Zeit, um einen Angriffsplan auszuarbeiten. Wenn Ihr die Ingenieure und Tüftler eine Weile beschäftigen könntet, würde das ihre Fortschritte verlangsamen, und ich hätte die Zeit, die ich benötige.]], },
    { [[Altruis der Leidende im westlichen Nagrand möchte, dass Ihr 2 Legionäre der Teufelswache, 3 Ingenieure der Mo'arg und 8 Tüftler der Gan'arg tötet.]], },
  },
  [10000] = {
    [[Unangenehme Gesellschaft]],
    { [[Unsere Streitkräfte in Thrallmar dachten, sie hätten die Höllenorcs der Höllenfeuerzitadelle unter Kontrolle, aber die Neuankömmlinge in den Ruinen im Osten sagen etwas anderes.]], [[Wir können ihnen nicht erlauben, hier Fuß zu fassen. Rokag hat Schattenpirscher Kaide zu den Ruinen geschickt, um sie zu beobachten, aber wir müssen sie aufhalten, bevor sie es sich zu bequem machen.]], [[Geht zu den Ruinen, dezimiert ihre Peons und haltet bei den Bäumen im Norden nach Kaide Ausschau. Er wird Euch sagen, wo Ihr danach hingehen sollt.]], },
    { [[Tötet 12 verunstaltete Peons und meldet Euch dann bei Schattenpirscher Kaide.]], },
  },
  [10001] = {
    [[Der Meisterplaner]],
    { [[Alle Konstruktionslager haben einen Meisterplaner. Der Meisterplaner bestimmt, welche Gebäude im Konstruktionslager vorhanden sein müssen, und wie sie platziert werden. Zudem werden diese Informationen auf Blaupausen festgehalten, die der Planer bei sich trägt.]], [[Wir brauchen die Blaupausen des Meisterplaners. Mit solch detaillierten Informationen könnten wir einen meiner Bekannten dazu bewegen, uns etwas Unterstützung zu schicken.]], [[Sucht den Meisterplaner der Mo'arg und stehlt seine Blaupausen. Er wird sich hinter den Konstruktionen aufhalten.]], },
    { [[Altruis der Leidende im westlichen Nagrand möchte, dass Ihr die Blaupausen des Meisterplaners besorgt.]], },
  },
  [10002] = {
    [[Vermittler der Feuerschwingen]],
    { [[In den letzten paar Tagen habe ich beobachtet, wie eine Blutelfenfrau immer wieder zur Höllenorcfeste gegangen ist, um mit dem Anführer der Höllenorcs zu reden. Ihrer Körpersprache nach verteilt sie Befehle. Aber warum? Wessen Befehle überbringt sie?]], [[Die Neugier hat über mich gesiegt, deshalb habe ich sie auf dem Weg zurück vom Höllenorclager beschattet. Sie ging nach Norden, zum Posten der Feuerschwingen. Ich musste mich zurückziehen, bevor ich den Blutelfenposten erreichte.]], [[Tötet sie, bevor sie das Höllenorclager wieder verlassen kann.]], },
    { [[Findet und tötet Lisaile Feuerweber und kehrt dann zu Theloria Dämmermantel zurück.]], },
  },
  [10003] = {
    [[Vermittler der Feuerschwingen]],
    { [[In den letzten paar Tagen habe ich beobachtet, wie eine Blutelfenfrau immer wieder zur Höllenorcfeste gegangen ist, um mit dem Anführer der Höllenorcs zu reden. Ihrer Körpersprache nach verteilt sie Befehle. Aber warum? Wessen Befehle überbringt sie?]], [[Die Neugier hat über mich gesiegt, deshalb habe ich sie auf dem Weg zurück vom Höllenorclager beschattet. Sie ging nach Norden, zum Posten der Feuerschwingen. Ich musste mich zurückziehen, bevor ich den Blutelfenposten erreichte.]], [[Tötet sie, bevor sie das Höllenorclager wieder verlassen kann.]], },
    { [[Findet und tötet Lisaile Feuerweber und kehrt dann zu Schattenpirscher Kaide zurück.]], },
  },
  [10004] = {
    [[Geduld und Verständnis]],
    { [[Nehmt die Blaupausen und reist nach Shattrath in den Wäldern von Terokkar. Wenn Ihr in Shattrath seid, geht ins Untere Viertel sucht nach einem Mo'arg namens Sal'salabim. Er ist wahrscheinlich an der Bar und ertränkt seine Sorgen.]], [[Da fällt mir ein, dass Ihr ihn wahrscheinlich überreden müsst, uns zu helfen. Versucht ihm viel Geduld und Verständnis entgegenzubringen. Wenn das nichts hilft, dann versucht es mit Gewalt.]], },
    { [[Altruis der Leidende im westlichen Nagrand möchte, dass Ihr die Blaupausen des Meisterplaners zu Sal'salabim in Shattrath bringt und ihn überredet, euch zu helfen.]], },
  },
  [10005] = {
    [[Erdbinder Tavgren Bescheid sagen]],
    { [[<name>, ich glaube, Ihr solltet zum Cenariusdickicht zurückgehen und mit Erdbinder Tavgren sprechen.]], [[Ich bin mir sicher, dass er wissen möchte, was hier wirklich passiert ist.]], },
    { [[Sprecht mit Erdbinder Tavgren außerhalb des Cenariusdickichts in den Wäldern von Terokkar.]], },
  },
  [10006] = {
    [[Erdbinder Tavgren Bescheid sagen]],
    { [[<name>, ich glaube, Ihr solltet zum Cenariusdickicht zurückgehen und mit Erdbinder Tavgren sprechen.]], [[Ich bin mir sicher, dass er wissen möchte, was hier wirklich passiert ist.]], },
    { [[Sprecht mit Erdbinder Tavgren außerhalb des Cenariusdickichts in den Wäldern von Terokkar.]], },
  },
  [10007] = {
    [[Die Reihen lichten]],
    { [[So wie ich Thander und Bertelm kenne, werden sie nicht dulden, dass eine feindliche Macht so ein großes Lager direkt vor unserer Haustüre errichtet. Aber sie zu bitten, ein paar Leute ihrer Garnison zu schicken, um das Lager anzugreifen, steht auch nicht zur Debatte. Wir wären dann viel zu leicht angreifbar.]], [[Wir müssen uns also selbst darum kümmern, die Bedrohung durch die Bauarbeiten der Höllenorcs zu verringern. Tötet alle von ihnen, die Ihr könnt, und ich sehe mich solange nach Verstärkung um.]], },
    { [[Tötet 10 Verwüster der Knochenmalmer und 6 Kreuzbrecher der Knochenmalmer und kehrt dann zu Theloria Dämmermantel zurück.]], },
  },
  [10008] = {
    [[Was in Terokkar geschieht, bleibt in Terokkar]],
    { [[Wenn Thrallmar erfährt, dass sie es nicht geschafft haben, die Höllenorcs unter Kontrolle zu halten, werden wir hier bald viel Gesellschaft haben. Ehrlich gesagt bin ich mir nicht sicher, ob ich so viel Aufmerksamkeit möchte, deswegen behalten wir dieses kleine Problem für uns, verstanden?]], [[Wenn wir den Höllenorcs genug Verluste beibringen können, besteht die Möglichkeit, dass niemand außerhalb der Steinbrecherfeste von ihrer Anwesenheit erfährt.]], [[Marschiert da rein, schwingt Eure Waffe, wedelt mit der Hand - oder was auch immer Ihr tut - und tötet die Höllenorcs!]], },
    { [[Schattenpirscher Kaide möchte, dass Ihr 10 Verwüster der Knochenmalmer und 6 Kreuzbrecher der Knochenmalmer tötet.]], },
  },
  [10009] = {
    [[Ein paar Schädel spalten]],
    { [[<Sal'salabim lacht.>]], [[Das Sal'salabims Entwurf.]], [[<Sal'salabim zeigt auf die Architektur und die Anordnungen auf den Blaupausen.>]], [[Schaut Türme an. Dämonenjäger kann nicht sehen, oder? Armer, armer Altruis. Er weinen wenn er Augen hätte, oder?]], [[Ja, Sal'salabim helfen, aber erst Ihr helfen Sal'salabim.]], [[<Sal'salabim gibt Euch einen Ordner.>]], [[Diese Wesen schulden Sal'salabim Gold. Viel Gold. Gehen für Sal'salabim eintreiben. Sie werden nicht einfach rausrücken Sal'salabims Gold. Müssen kämpfen, aber Ihr stark. Ihr besiegen Sal'salabim!]], },
    { [[Sal'salabim in Shattrath möchte, dass Ihr Raliqs Schulden, Kusch'kuschs Schulden und Floons Schulden eintreibt.]], },
  },
  [10010] = {
    [[So einfach ist das?]],
    { [[<Sal'salabim zeigt auf die Blaupause.>]], [[Seht Ihr, wo Teufelskanonen sind?]], [[<Sal'salabim zeichnet einen Pfeil, der einen Richtungswechsel anzeigt.>]], [[Ihr dreht Teufelskanone um und richtet sie auf Gebäude.]], [[Wartet! Nicht durchdrehen! Ihr wisst etwas nicht. Vorarbeiter haben Schlüssel, um Kanonen zu drehen. Ihr braucht erst Schlüssel! Einer von jedem Vorarbeiter!]], [[Nicht so leicht, was?]], [[Bringt Information zurück zu Altruis.]], },
    { [[Sal'salabim in Shattrath möchte, dass Ihr zu Altruis dem Leider im westlichen Nagrand zurückkehrt.]], },
  },
  [10011] = {
    [[Konstruktionslager: zerstört]],
    { [[Sammelt Euren Mut, <name>. Ihr werdet ihn brauchen, wenn Ihr einem Vorarbeiter gegenübersteht.]], [[Sobald Ihr bereit seid, sucht und tötet die Vorarbeiter in jedem Lager. Nehmt ihre Schlüssel und benutzt sie, um die Kanonen umzudrehen. Schießt damit auf die Lager. Kehrt zu mir zurück, wenn die Lager zerstört sind.]], },
    { [[Altruis der Leidende im westlichen Nagrand möchte, dass Ihr das Konstruktionslager: Hass und das Konstruktionslager: Furcht zerstört.]], },
  },
  [10012] = {
    [[Höllenorcpläne]],
    { [[Die Schrift auf diesen Plänen ist zu fein und genau, um von einem Höllenorc zu stammen. Ihr könnt nicht verstehen, was der Text bedeutet, aber die Zeichen sind eindeutig: die Höllenorcs erhalten Befehle von den Blutelfen im Posten der Feuerschwingen.]], [[Warum arbeiten die beiden Fraktionen zusammen? Vielleicht weiß Bertelm mehr über dieses merkwürdige Bündnis. Ihr beschließt, zu Allerias Feste zurückzukehren und ihm die Pläne zu zeigen.]], },
    { [[Bring the Fel Orc Plans to Bertelm at the Allerian Stronghold.]], },
  },
  [10013] = {
    [[Eine ungesehene Hand]],
    { [[Die Schrift auf diesen Plänen ist zu fein und genau, um von einem Höllenorc zu stammen. Ihr könnt nicht verstehen, was der Text bedeutet, aber die Zeichen sind eindeutig: die Höllenorcs erhalten Befehle von den Blutelfen im Posten der Feuerschwingen.]], [[Warum arbeiten die beiden Fraktionen zusammen? Vielleicht weiß Rokag mehr über dieses merkwürdige Bündnis. Ihr beschließt, zur Steinbrecherfeste zurückzukehren und ihm die Pläne zu zeigen.]], },
    { [[Bring the Fel Orc Plans to Rokag at Stonebreaker Hold.]], },
  },
  [10014] = {
    [[Das Projekt des Postens der Feuerschwingen]],
    { [[Wie ich bereits erwähnt habe, haben wir einige weitere Informationen erhalten, die besagen, dass wir ein weitaus größeres Problem haben.]], [[Die Blutelfen sind im Posten der Feuerschwingen mit dem Bau einer weiteren Manabombe beschäftigt. Ich kann nicht genug betonen, dass sie eindeutig vorhaben, diese Bombe gegen Allerias Feste einzusetzen!]], [[Meine Späher berichten mir, dass sie die Codes für die Waffe in vier kleineren Gebäuden um den mittleren Hof herum aufbewahren. Geht dorthin und besorgt die Codes, bevor es zu spät ist!]], },
    { [[Stehlt die vier Codetabellen der Manabombe und bringt sie zu Meridian in Allerias Posten in den Wäldern von Terokkar.]], },
  },
  [10015] = {
    [[Das Projekt des Postens der Feuerschwingen]],
    { [[Wie ich bereits erwähnt habe, haben wir einige weitere Informationen erhalten, die besagen, dass wir ein weitaus größeres Problem haben.]], [[Die Blutelfen sind im Posten der Feuerschwingen mit dem Bau einer weiteren Manabombe beschäftigt. Ich kann nicht genug betonen, dass sie eindeutig vorhaben, diese Bombe gegen die Steinbrecherfeste einzusetzen!]], [[Meine Späher berichten mir, dass sie die Codes für die Waffe in vier kleineren Gebäuden um den mittleren Hof herum aufbewahren. Geht dorthin und besorgt die Codes, bevor es zu spät ist!]], },
    { [[Stehlt die vier Codetabellen der Manabombe und bringt sie zu Unteroffizier Chawni im Steinbrecherlager in den Wäldern von Terokkar.]], },
  },
  [10016] = {
    [[Waldworgschwänze]],
    { [[Seid Ihr auf Euren Reisen den Waldworgs begegnet? Ich hatte die Gelegenheit, sie zu beobachten, als ich einige der nördlichen Regionen des Waldes kartografiert habe. Ich würde Euch nicht empfehlen, zu nahe an sie heranzugehen, es sei denn, Ihr seid bereit für einen Kampf.]], [[Sie haben keine Angst und scheuen vor nichts zurück, um ihr Territorium zu verteidigen.]], [[Es ist nicht verwunderlich, dass diese Kreaturen solch wundervolle Schwänze haben. Ich habe versucht, ein paar von ihnen zu sammeln, um einen neuen Mantel zu schneidern, aber die Worgs entkommen meinen Fallen dauernd. Wollt Ihr mir helfen?]], },
    { [[Bertelm in Allerias Feste möchte, dass Ihr ihm 12 Waldworgschwänze bringt. Die Schwänze können von Waldworgs oder Waldworgalphas stammen.]], },
  },
  [10017] = {
    [[Schwindende Vorräte]],
    { [[Immer mehr Flüchtlinge kommen Tag für Tag durch die Tore von Shattrath. Viele sind so schwer verwundet, dass es an ein Wunder grenzt, dass sie es überhaupt hierher geschafft haben.]], [[Als Heiler der Aldor kann ich die meisten gewöhnlichen Wunden verarzten. Vergiftete Wunden sind jedoch problematischer, da mir langsam die Vorräte an Gegengift ausgehen.]], [[Bringt mir ein paar Schreckensgiftbeutel von den Schreckensspinnen hier in der Gegend, damit ich mehr davon herstellen kann. Ihr findet eine Gruppe von diesen Spinnen im Südosten von Terokkar.]], },
    { [[Bringt 8 Schreckensgiftbeutel zu Sha'nir in Shattrath.]], [[Das Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.]], },
  },
  [10018] = {
    [[Gewänder des Wolfgeistes]],
    { [[Vor Generationen, als die Orcs die Wälder von Terokkar noch ihr Zuhause nannten, spielte der Wolfgeist eine große Rolle in ihrem Leben. Als die Orcs fortgingen, brauchten sie den Wolfgeist nicht mehr und haben ihn zurückgelassen.]], [[Mit der Zeit wurde der Geist einsam und verließ die Wälder ebenfalls.]], [[Ich glaube, dass ich den Wolfgeist zu seinem ursprünglichen Heim zurückbringen kann, wie auch wir Orcs zurückgekehrt sind. Das Ritual erfordert, dass ich ein Gewand aus den Pelzen der Wölfe der Wälder schneidere, um den Wolfgeist zu seinem Zuhause zurück zu geleiten.]], },
    { [[Bringt 12 Waldworgpelze zu Malukaz in der Steinbrecherfeste.]], },
  },
  [10019] = {
    [[Mehr Giftbeutel]],
  },
  [10020] = {
    [[Ein Heilmittel für Zahlia]],
    { [[Die Frau rechts von mir hat eine schwere Kopfverletzung erlitten. Sie hat zwar überlebt, aber ihr Augenlicht verloren.]], [[Ein traditionelles Heilmittel meines Volkes verlangt das Blut eines sehr alten Basilisken.]], [[Unglücklicherweise leben die Basilisken von Terokkar wegen der Wilderei verschiedener Lager in der Regel nicht sehr lange.]], [[Es gibt jedoch einen Basilisken, dessen Name ich schon als Kind kannte. Er ist als Steinstarrer bekannt und soll östlich von hier leben, in der Nähe der Landesgrenze.]], [[Bringt mir sein Blut, damit Zahlia wieder sehen kann.]], },
    { [[Bringt Steinstarrers Blut zu Sha'nir in Shattrath.]], [[Das Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.]], },
  },
  [10021] = {
    [[Das Licht wiederherstellen]],
    { [[Vor langer Zeit war ein Großteil dieses Gebiets von den Draenei bewohnt. Wir, die Aldor, hatten den Auftrag, unsere heiligen Stätten und Altäre zu beschützen. Doch seitdem wurden viele davon zerstört oder von unseren Feinden eingenommen.]], [[Östlich von hier gibt es einen Außenposten der Arakkoa. Sie haben ihn mit voller Absicht auf dem Gebiet errichtet, auf dem drei unserer heiligen Altäre stehen. Die Arakkoa haben ihre üble Magie benutzt, um die ureigene Macht unserer Schreine zu verändern und zu schänden.]], [[Bringt dies zum Arakkoaaußenposten und reinigt die drei Altäre.]], },
    { [[Ishanah möchte, dass Ihr zum Arakkoaaußenposten östlich von Shattrath geht. Benutzt die gesegnete Phiole, um den östlichen Altar, den westlichen Altar und den nördlichen Altar zu reinigen.]], [[Das Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.]], },
  },
  [10022] = {
    [[Der schwer zu fassende Eisenkiefer]],
    { [[Jetzt, da ich das Fell habe, das ich für mein Gewand benötige, dachte ich, dass der Pelz eines ganz bestimmten Wolfs eine schöne Kapuze für meinen Umhang abgeben würde. Stellt Euch nur vor, wie es wäre, Euren Feind im Pelz des gerissensten Raubtiers der Wälder zu sehen!]], [[Ich habe bisher vergeblich versucht, Eisenkiefer zu fangen. Ich habe versucht ihn in die Enge zu treiben, ihn zu überlisten und ihn zu locken. Nichts hat geklappt.]], [[Er streunt in den nordöstlichen Gebieten des Waldes herum, und ich würde Euch angemessen belohnen, wenn Ihr schafft, was mir nie gelang.]], },
    { [[Bringt Eisenkiefers Pelz zu Bertelm in Allerias Feste.]], },
  },
  [10023] = {
    [[Patriarch Eisenkiefer]],
    { [[Ihr habt gute Arbeit beim Sammeln der ersten Pelze geleistet.]], [[Das letzte Stück der Gewandung erfordert einen ganz besonderen Pelz. Es gibt einen bestimmten Wolf, der alt und gerissen ist, und sich dadurch den Rang des Patriarchen unter den Wölfen des Waldes verdient hat. Er ist unter ihnen als Eisenkiefer, der mächtige Jäger, bekannt.]], [[In meinen Visionen habe ich ihn im Nordosten der Wälder umherstreifen sehen. Sein Pelz in meiner Gewandung wird sicher den Pfad des Wolfgeistes erhellen.]], [[Beklagt Eisenkiefer nicht. Er wird die ihm gebührende Ehre erhalten.]], },
    { [[Bringt Eisenkiefers Pelz zu Malukaz in der Steinbrecherfeste.]], },
  },
  [10024] = {
    [[Voren'thals Visionen]],
    { [[Unser Anführer, Voren'thal der Seher, war einst ein enger Vertrauter von Kael'thas. Seine prophetischen Visionen habe ihn gewarnt, dass Kaels Weg in der Zerstörung unseres Volkes enden würde.]], [[Normalerweise sind diese Visionen nur sehr vage und ungenau, aber unsere Alchemisten haben ein Elixier entwickelt, mit dessen Hilfe seine Visionen deutlicher werden. Unglücklicherweise erfordert das Elixier eine sehr seltene Zutat: Basiliskenaugen. Normalerweise bekomme ich sie von diesem aufgeblasenen Astralen, aber er hat mich enttäuscht.]], [[Macht Euch auf die Suche nach den Dunstschuppenbasilisken im gebirgigen Terokkar und bringt mir ihre Augen.]], },
    { [[Bringt 8 Augen eines Dunstschuppenbasilisken zu Arkanist Adyria in Shattrath.]], [[Das Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor.]], },
  },
  [10025] = {
    [[Mehr Basiliskenaugen]],
  },
  [10026] = {
    [[Magische Störungen]],
    { [[Es ist mir ein wenig peinlich, aber mir ist hier in den Wäldern von Terokkar etwas widerfahren, was mir seit meinen Lehrjahren nicht mehr passiert ist.]], [[Manchmal scheitern meine Zauber, obwohl ich genau weiß, dass ich alles richtig gemacht habe. Ich bin fast wahnsinnig geworden, als das zum ersten Mal passiert ist.]], [[Ich glaube, ich habe den Grund dafür gefunden. Die Wälder sind voller Wesen, die Sphärenjäger genannt werden. Das sind dämonische Kreaturen mit einem Hang zur Magie und zur Störung magischer Ströme. Der beste Weg, um mit ihnen fertigzuwerden, ist, sie einfach zu töten.]], },
    { [[Andarl in Allerias Feste möchte, dass Ihr 10 Sphärenjäger tötet.]], },
  },
  [10027] = {
    [[Magische Störungen]],
    { [[Es gibt nichts Ärgerlicheres, als einen Zauber vorzubereiten, und dann von einer unsichtbaren Macht unterbrochen zu werden. Nach dem vierten Mal wusste ich, dass es kein Zufall sein konnte, und habe Keb'ezil losgeschickt, um die Ursache herauszufinden.]], [[Ich garantiere Euch, dass Ihr nie einen Wichtel treffen werdet, der widerspenstiger ist als Keb.]], [[Wie auch immer, er konnte die Quelle des Problems finden, eine verteufelte Kreatur namens Sphärenjäger. Sie haben die Angewohnheit, überall, wo sie auftauchen, die magischen Energien zu stören. Ich habe genug von ihren Störungen.]], },
    { [[Tötet 10 Sphärenjäger und kehrt dann zu Kurgatok in der Steinbrecherfeste zurück.]], },
  },
  [10028] = {
    [[Gefäße der Macht]],
    { [[Zwischen den Ruinen von Tuurem gibt es einige Gefäße mit roher, magischer Energie. Wir können von Glück sagen, dass die Zerschlagenen, die in der Stadt eingefallen sind, keine Ahnung von dieser Machtquelle mitten unter ihnen haben, aber wir dürfen nicht hoffen, dass das so bleibt.]], [[Mithilfe dieser Gefäße und ihrer Macht können die verstreuten Überlebenden meines Volkes der Verlockung der dämonischen Magie widerstehen. Jeder Quel'dorei, den wir vor dem Wahnsinn unseres Prinzen retten können, ist einer mehr, der uns beim Wiederaufbau unseres verlorenen Ruhms helfen kann. Bringt mir die Gefäße, <class>.]], },
    { [[Bringt 8 Draeneigefäße zu Ros'eleth in Allerias Feste.]], },
  },
  [10029] = {
    [[Die Geister rufen]],
    { [[Die Geister der Toten in der Knochenwüste rufen nach mir! Sie leiden!]], [[<class>, Ihr müsst zur Knochenwüste im Süden reisen und meinen Gehilfen Soolaveen bei der Flüchtlingskarawane aufsuchen. Er muss es auch spüren und wird Eure Hilfe dabei brauchen, die Geister dort zur Ruhe zu bringen.]], [[Wenn Ihr Euch beeilt, könnt Ihr sie vielleicht noch einholen. Ich weiß, dass sie in Norden der Knochenwüste Rast machen wollten, bevor sie weiter Richtung Süden nach Auchindoun ziehen wollten.]], },
    { [[Anachoret Lyteera möchte, dass Ihr ihren Gehilfen Soolaveen bei der Flüchtlingskarawane in der Knochenwüste der Wälder von Terokkar findet.]], [[Das Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern.]], },
  },
  [10030] = {
    [[Die Knochen zurückholen]],
    { [[Die Knochen der Draenei, die bei der Explosion von Auchindoun gestorben sind, sind überall in der Knochenwüste verstreut. Viele ihrer Geister sind daher ruhelos und können nicht ins nächste Leben übertreten.]], [[<name>, Ihr müsst mir helfen, ihre Knochen wieder zu besorgen und sie zu ihrer letzten Ruhestätte zu bringen. Nur dann werden sie Frieden finden und den Segen des Lichts erlangen.]], [[Sammelt die ruhelosen Knochen mit größter Vorsicht und bringt sie zu Ramdor dem Wahnsinnigen in Auchindoun in der Mitte der Knochenwüste. Trotz seines Namens wird er wissen, wie er sich um sie zu kümmern hat.]], },
    { [[Sammelt 10 ruhelose Knochen und bringt sie zu Ramdor dem Wahnsinnigen bei der westlichen Seite des Rings der Beobachtung in Auchindoun, das sich in der Mitte der Knochenwüste in den Wäldern von Terokkar befindet.]], },
  },
  [10031] = {
    [[Den Verirrten den Weg zeigen]],
    { [[Viele Geister der Draenei wurden von der Explosion in Auchindoun verderbt und wandeln nun voller Pein in der Wüste.]], [[<name>, ich flehe Euch an, gebt ihnen ihren Seelenfrieden wieder. Vernichtet die verirrten Geister und die Skelette der Zerschlagenen, die die Knochenwüste hinter den Mauern Auchindouns heimsuchen.]], [[Was? Ihr denkt, es wäre besser, sie in blinder Wut umherstreifen zu lassen?]], [[Kehrt danach zu Anachoret Soolaveen bei der Flüchtlingskarawane zurück und berichtet ihm, was Ihr erreicht habt.]], },
    { [[Tötet 10 verirrte Geister und 10 Skelette eines Zerschlagenen und kehrt dann zu Anachoret Soolaveen bei der Flüchtlingskarawane in der Knochenwüste in den Wäldern von Terokkar zurück.]], },
  },
  [10033] = {
    [[Aufruf: Tötet die Knochenpeitscher!]],
    { [[Es wurde ein Kopfgeld auf die Knochenpeitscher der Knochenwüste ausgesetzt. Als Überträger der Pest müssen sie von nun an vernichtet werden!]], [[Obgleich sie in der gesamten Wüste vertreten sind, ist die größte Ansammlung bekanntermaßen beim Aashügel im Norden anzutreffen.]], [[Meldet Euch bei Taela Immerlauf, um Eure Belohnung einzufordern.]], },
    { [[Tötet 20 Knochenpeitscher und meldet Euch dann bei Taela Immerlauf in Allerias Feste in den Wäldern von Terokkar.]], },
  },
  [10034] = {
    [[Aufruf: Tötet die Knochenpeitscher!]],
    { [[Die Knochenpeitscher der Knochenwüste haben Mawg gebissen und ihn krank gemacht. Jetzt möchte er, dass sie alle vernichtet werden!]], [[Obgleich sie in der gesamten Wüste vertreten sind, ist die größte Ansammlung bekanntermaßen beim Aashügel im Norden anzutreffen.]], [[Kehrt zu Mawg Grimmschlag zurück, wenn Ihr die Aufgabe erledigt habt, und er wird Euch gut belohnen.]], },
    { [[Tötet 20 Knochenpeitscher und meldet Euch dann bei Mawg Grimmschlag in der Steinbrecherfeste in den Wäldern von Terokkar.]], },
  },
  [10035] = {
    [[Torgos!]],
    { [[In der Knochenwüste gibt es ein Wesen, das so bösartig, so widerlich und so Furcht einflößend ist, dass nur die besten Jäger es wagen, ihm aufzulauern.]], [[Ich spreche von Torgos, dem König der Knochenpeitscher, der sich beim Rhazeversteck am westlichen Ende der Weiten herumtreibt.]], [[Unglücklicherweise haben die Arakkoa eine Vogelscheuche aufgestellt, die als 'Torgos' Fluch' bekannt ist und ihn von dort fernhält. Ich wette, dass er trotzdem herauskommen würde, wenn Ihr den Leichnam seines Weibchens Trachela am Fuß dieser Vogelscheuche platziert. Ihr findet sie beim Aashügel im nördlichen Teil der Wüste.]], [[Bringt mir eine Schwanzfeder von Torgos.]], },
    { [[Benutzt Trachelas Leichnam, um Torgos zu rufen. Besorgt eine Schwanzfeder von Torgos und kehrt dann zu Taela Immerlauf in Allerias Feste in den Wäldern von Terokkar zurück.]], },
  },
  [10036] = {
    [[Torgos!]],
    { [[Ich will Torgos, den König der Knochenpeitscher tot sehen!]], [[Vor nicht allzu langer Zeit, als ich beim Rhazeversteck am westlichen Ende der Wüste auf Jagd war, fiel dieses Tier mich an.]], [[<Mawg tippt seine Augenklappe an.>]], [[Er hat mir mein Auge genommen, und nun will ich sein Leben dafür! Bringt mir eine seiner Schwanzfedern als Beweis und ich werde Euch gebührlich belohnen.]], [[Ihr werdet den Leichnam seines Weibchens Trachela brauchen, um ihn hervorzulocken, da er Todesangst vor der alten Vogelscheuche der Arakkoa hat, die als Torgos' Fluch bekannt ist.]], [[Ihr findet sie beim Aashügel im Norden der Wüste.]], },
    { [[Benutzt Trachelas Leichnam, um Torgos zu rufen. Besorgt eine Schwanzfeder von Torgos und kehrt dann zu Mawg Grimmschlag in der Steinbrecherfeste in den Wäldern von Terokkar zurück.]], },
  },
  [10037] = {
    [[Ich würd' lieber angeln]],
    { [[Da wir gerade von Seen sprechen, da gibt es einen See im Nordosten, an dem man richtig toll angeln kann. Früher bin ich da immer mit meinen Freunden hin, aber meine Mutter erlaubt es mir nicht mehr.]], [[Sie sagt, es sei zu gefährlich, aber ich habe da oben noch nichts außer ein paar Spinnen gesehen. Als ob Spinnen einem irgendwas anhaben könnten...]], [[Ich mochte am liebsten die Aale von da oben. Wenn Ihr zum See hoch geht, könntet Ihr mir dann ein paar dieser Aale bringen? Sie sind störrisch, aber wenn man sie einmal gebändigt hat, kann man sie ganz leicht mitnehmen.]], },
    { [[Bring 8 Pristine Shimmerscale Eels to Seth in Shattrath.]], },
  },
  [10038] = {
    [[Sprecht mit Gefreiter Wöch]],
    { [[Ich habe gerade einen Bericht von einem unserer Späher in der Nähe von Grangol'var erhalten. Er sagt, dass eine schattenhafte Truppe in die Ruinen dort eingezogen ist.]], [[<name>, ich möchte, dass Ihr mit Gefreiter Wöch sprecht und schaut, wie Ihr ihm dabei helfen könnt herauszufinden, was diese Unbekannten dort im Schilde führen.]], [[Geht nach Westen an der Steinbrecherfeste vorbei. Sein letzter Bericht besagt, dass er sich in der Nähe des nördlichen Eingangs zum Dorf befindet.]], },
    { [[Locate and speak with Private Weeks near the north entrance to Grangol'var Village in Terokkar Forest.]], },
  },
  [10039] = {
    [[Sprecht mit Späherin Neftis]],
    { [[Ich wurde informiert, dass eine unserer Späherinnen in der Nähe von Grangol'var zurzeit eine schattenhafte Truppe beobachtet, die vor Kurzem dorthin gezogen ist.]], [[<name>, ich möchte, dass Ihr mit Späherin Neftis sprecht und schaut, wie Ihr ihr dabei helfen könnt herauszufinden, was diese Unbekannten dort im Schilde führen.]], [[Geht von hier aus nach Süden und dann nach Westen, um den Norden der Knochenwüste herum. Ihr letzter Bericht besagt, dass sie sich in der Nähe des südlichen Eingangs zum Dorf befindet.]], },
    { [[Locate and speak with Scout Neftis near the south entrance to Grangol'var Village in Terokkar Forest.]], },
  },
  [10040] = {
    [[Wer sind sie?]],
    { [[Ich weiß nicht, wer sie sind, aber ich weiß, dass sie uns feindlich gesonnen sind!]], [[Ich werde Euch eine Verkleidung geben und möchte, dass Ihr da hinein geht und mit den Dreien sprecht, die ich für redselig halte. Sprecht mit dem Initiand, dem Hilfsarbeiter und dem Berater.]], [[Seid aber vorsichtig; es gibt dort ein paar Jäger, die Eure Verkleidung durchschauen können, und sie schauen ganz genau! Und was auch immer Ihr tut, verlasst das Dorf nicht. Die Verkleidung ist etwas dürftig. Wenn Ihr sie verliert, kommt zu mir zurück und ich gebe Euch eine neue.]], },
    { [[Sprecht mit dem schattenhaften Initianden, dem schattenhaften Hilfsarbeiter und dem schattenhaften Berater. Kehrt dann zu Gefreiter Wöch in Grangol'var in den Wäldern von Terokkar zurück.]], },
  },
  [10041] = {
    [[Wer sind sie?]],
    { [[Ich weiß nicht, wer sie sind, aber ich weiß, dass sie uns feindlich gesonnen sind!]], [[Ich werde Euch eine Verkleidung geben und möchte, dass Ihr da hinein geht und mit den Dreien sprecht, die ich für redselig halte. Sprecht mit dem Initiand, dem Hilfsarbeiter und dem Berater.]], [[Seid aber vorsichtig; es gibt dort ein paar Jäger, die Eure Verkleidung durchschauen können, und sie schauen ganz genau! Und was auch immer Ihr tut, verlasst das Dorf nicht. Die Verkleidung ist etwas dürftig. Wenn Ihr sie verliert, kommt zu mir zurück und ich gebe Euch eine neue.]], },
    { [[Sprecht mit dem schattenhaften Initianden, dem schattenhaften Hilfsarbeiter und dem schattenhaften Berater. Kehrt dann zu Späherin Neftis in Grangol'var in den Wäldern von Terokkar zurück.]], },
  },
  [10042] = {
    [[Tötet den Schattenrat!]],
    { [[Was? Diese Kerle gehören zum Schattenrat?!]], [[<name>, Ihr müsst dorthin zurückgehen und sie erledigen! Ich möchte, dass Ihr ihre Anführer und so viele Henker und Beschwörer tötet, wie Ihr nur könnt.]], [[Wenn Ihr damit fertig seid, meldet Euch bei Leutnant Kieshammer in Allerias Feste.]], [[Viel Glück, <class>.]], },
    { [[Tötet 10 schattenhafte Henker, 10 schattenhafte Beschwörer und Schattenmeister Gram und kehrt dann zu Leutnant Kieshammer in Allerias Feste in den Wäldern von Terokkar zurück.]], },
  },
  [10043] = {
    [[Tötet den Schattenrat!]],
    { [[Was? Diese Kerle gehören zum Schattenrat?!]], [[<name>, Ihr müsst dorthin zurückgehen und sie erledigen! Ich möchte, dass Ihr ihre Anführer und so viele Henker und Beschwörer tötet, wie Ihr nur könnt.]], [[Wenn Ihr damit fertig seid, meldet Euch bei Beraterin Faila in der Steinbrecherfeste.]], [[Viel Glück, <class>!]], },
    { [[Tötet 10 schattenhafte Henker, 10 schattenhafte Beschwörer und Schattenmeister Gram und kehrt dann zu Beraterin Faila in der Steinbrecherfeste in den Wäldern von Terokkar zurück.]], },
  },
  [10044] = {
    [[Ein Besuch bei der Großmutter]],
    { [[Die Großmutter hat mich gebeten, Euch aufzusuchen und Euch eine Nachricht zu überbringen. Sie möchte unverzüglich mit Euch sprechen.]], [[Mein Wille mag vielleicht gebrochen sein, aber macht besser keinen Fehler, <name>. Wenn Ihr die Großmutter verärgert, werde ich Euch mit aller Kraft, die noch in mir steckt, bestrafen. Ihr findet sie in der Haupthalle.]], [[<Garrosh zeigt auf das große Gebäude im Norden.>]], [[Hört Euch an, was sie zu sagen hat, und tut, was sie Euch aufträgt.]], },
    { [[Sprecht mit Großmutter Geyah und hört Euch an, was sie zu sagen hat.]], },
  },
  [10045] = {
    [[Zutaten sammeln]],
    { [[Mutter Kashur möchte mit Euch sprechen, <name>. Bevor Ihr das könnt, müsst Ihr jedoch ein paar Kräuter sammeln, um einen Trank zuzubereiten, der Euren Geist erweitert, damit Ihr mit den Geistern sprechen könnt.]], [[Ihr müsst Olembawurzeln aus Terokkar, Marschenbeeren aus den Zangarmarschen, Telaarifarn aus Nagrand und Drachenwirbel aus dem Schergrat sammeln. Ich habe die Anleitung zum Anbau dieser Kräuter auf dieses Pergament geschrieben. Kehrt zu mir zurück, wenn Ihr die Zutaten beisammen habt.]], },
    { [[Großmutter Geyah in Garadar in Nagrand möchte, dass Ihr 1 Olembawurzel, 1 Marschenbeere, 1 Telaarifarn und 1 Drachenwirbel sammelt.]], },
  },
  [10046] = {
    [[Durch das Dunkle Portal]],
    { [[Vor zwei Wochen haben unsere Späher beobachtet, wie eine schwer bewaffnete Streitmacht der Allianz unter dem Kommando von Feldkommandant Romus aus der Burg Nethergarde aufgebrochen ist. Mit so vielen Soldaten, Vorräten und einem so erfahrenen Kommandanten können sie nur ein Ziel haben: die Scherbenwelt.]], [[Der Kriegshäuptling hat eine Vorhut durch das Portal geschickt, aber wenn die Allianz glaubt, dass so viele Truppen von Nöten sind, sollten vielleicht auch wir Verstärkung schicken.]], [[Reist durch das Dunkle Portal und meldet Euch bei Nazgrel in Thrallmar.]], },
    { [[Reist durch das Dunkle Portal und sprecht mit Nazgrel in Thrallmar.]], },
  },
  [10047] = {
    [[Der Pfad des Ruhms]],
    { [[Sie haben sie den Pfad des Ruhms genannt, eine Straße, die vom Herzen ihrer grausamen Festung zum Dunklen Portal führt, befestigt mit den Knochen meines Volks.]], [[Es war ihnen nicht genug, uns wie Tiere zu jagen, unsere Städte zu zerstören und die Überlebenden zu quälen. Nein, sie mussten auch noch unsere Knochen als Pflastersteine verwenden, und meinen Brüdern und Schwestern ein anständiges Begräbnis unter dem Licht verweigern.]], [[Diese gequälten Seelen wandeln immer noch durch diese Lande und schreien nach Erlösung. Ich bitte Euch, <name>, helft mir, sie beizusetzen.]], },
    { [[Reinigt 8 zertrampelte Skelette auf dem Pfad des Ruhms mit dem heiligen Wasser der Draenei und kehrt dann zu Seher Kryv in der Ehrenfeste zurück.]], },
  },
  [10048] = {
    [[Eine Handvoll Magiestaub]],
    { [[Ich brauche Staub, aber nicht irgendeinen Staub.]], [[Die königlichen Teromotten, die in den Wäldern in der Nähe leben, tragen magischen Staub auf ihren Flügeln, mit dem sie ihre Jäger betäuben. Diese Eigenschaft könnte ich mir zunutze machen. Die Arakkoaaugen, die Ihr mir gebracht habt, sind sehr widerspenstig. Mithilfe des Staubs werde ich diesen Widerstand lähmen. So kann ich dann endlich Deirom finden.]], [[Ich brauche nicht viel davon, vielleicht ein halbes Dutzend oder so.]], },
    { [[Sammelt 7 Handvoll besänftigender Staub für Andarl in Allerias Feste.]], },
  },
  [10049] = {
    [[Eine Handvoll Magiestaub]],
    { [[Ich brauche Staub, aber nicht irgendeinen Staub.]], [[Die königlichen Teromotten, die in den Wäldern in der Nähe leben, tragen magischen Staub auf ihren Flügeln, mit dem sie ihre Jäger betäuben. Diese Eigenschaft könnte ich mir zunutze machen. Die Arakkoaaugen, die Ihr mir gebracht habt, sind sehr widerspenstig. Mithilfe des Staubs werde ich diesen Widerstand lähmen. So kann ich dann endlich Dugar finden.]], [[Ich brauche nicht viel davon, vielleicht ein halbes Dutzend oder so.]], },
    { [[Sammelt 7 Handvoll besänftigenden Staubs für Kurgatok in der Steinbrecherfeste.]], },
  },
  [10050] = {
    [[Unerschütterliche Seelen]],
    { [[Am Ende unserer Invasion wurde das Rüstlager im Süden von den heimtückischen Todesrittern der Horde angegriffen. Diese bösartigen Schemen haben die Verteidiger des Rüstlagers verflucht und ihre Seelen zwischen dem Reich der Lebenden und dem der Toten gefangen.]], [[Diese ehrwürdigen Seelen müssen befreit werden! Ich bitte Euch, <name>, beendet das Leid meiner Kameraden, denn ich bringe es nicht selbst übers Herz, sie zu töten.]], },
    { [[Ehrenwache Wesilow möchte, dass Ihr 12 unerschütterliche Fußsoldaten, 10 unerschütterliche Zauberer und 5 unerschütterliche Ritter beim Expeditionsrüstlager tötet.]], },
  },
  [10051] = {
    [[Ausbruch aus dem Posten der Feuerschwingen!]],
    { [[Was für ein Glück, dass Ihr hier seid!]], [[<class>, Ihr müsst mir helfen zu entkommen. Ich habe Informationen, die für die Sicherheit von Allerias Feste und vielleicht der ganzen Scherbenwelt von äußerster Wichtigkeit sind!]], [[Ich bin von der Expedition des Cenarius mit einem diplomatischen Auftrag hierher geschickt worden, aber wie Ihr sehen könnt, waren sie mir nicht sehr freundlich gesonnen. Wenn Ihr mir helft in Sicherheit zu gelangen, verspreche ich Euch, dass Ihr ausreichend belohnt werdet!]], },
    { [[Geleitet Isla Sternenmähne in Sicherheit und meldet Euch dann bei Hauptmann Auric Sonnenjäger in Allerias Feste in den Wäldern von Terokkar.]], },
  },
  [10052] = {
    [[Ausbruch aus dem Posten der Feuerschwingen!]],
    { [[Was für ein Glück, dass Ihr hier seid!]], [[<class>, Ihr müsst mir helfen zu entkommen. Ich habe Informationen, die für die Sicherheit der Steinbrecherfeste und vielleicht der ganzen Scherbenwelt von äußerster Wichtigkeit sind!]], [[Ich bin von der Expedition des Cenarius mit einem diplomatischen Auftrag hierher geschickt worden, aber wie Ihr sehen könnt, waren sie mir nicht sehr freundlich gesonnen. Wenn Ihr mir helft in Sicherheit zu gelangen, verspreche ich Euch, dass Ihr ausreichend belohnt werdet!]], },
    { [[Geleitet Isla Sternenmähne in Sicherheit und meldet Euch dann bei Beraterin Faila in der Steinbrecherfeste in den Wäldern von Terkkokar.]], },
  },
  [10053] = {
    [[Zeth'Gor bewältigen]],
    { [[Es ist unerlässlich, dass wir den Vorteil, den wir durch Euren Sieg über Nekrogg errungen haben, ausnutzen. Wenn wir Glück haben, hat die Führung in Zeth'Gor noch nichts von Nekrogg erfahren.]], [[Die Knochenmalmer in Zeth'Gor werden normalerweise von Grillok "Finsterauge" in die Schlacht geführt. Findet ihn in seinem Lager, einer Höhle im Felsen unter dem Außenposten, und tötet ihn. Durchsucht die Höhle nach den Schlachtplänen der Höllenorcs, wenn er tot ist. Wir dürfen nicht überrascht werden.]], },
    { [[Tötet Grillok "Finsterauge" und durchsucht seinen Unterschlupf nach den Angriffsplänen des Blutenden Auges.]], },
  },
  [10054] = {
    [[Drohende Verdammnis]],
    { [[Eine schnelle Überprüfung der Pläne zeigt, dass der Anführer von Zeth'Gor, Kriegsherr Morkh, das Genie hinter den Angriffen, die von Knochenmagier Warlor geleitet werden, ist. Auf der Karte sind mehr als nur Morkhs Streitkräfte zu sehen.]], [[Die Pläne deuten an, dass die Streitkräfte der Knochenmalmer bewaffnet und zusammen mit den Kräften der Höllenfeuerzitadelle bereit für einen Angriff auf die Ehrenfeste sind.]], [[Es bleibt keine Zeit, um Leutnant Amadi zu informieren. Ihr müsst die Knochenmalmer aufhalten, bevor sie ihren Angriff starten können!]], },
    { [[Tötet 15 Grunzer des Blutenden Auges, 10 Nekrolyten des Blutenden Auges, 5 Peons des Blutenden Auges und Kriegsherr Morkh. Meldet Euch danach bei Leutnant Amadi im westlichen Turm der Ehrenfeste.]], },
  },
  [10055] = {
    [[Nichts verkommen lassen!]],
    { [[Verstärkung ist gut, aber ich sehe nicht, dass sie irgendwelche Karren mit Barren oder Holz durch die Gegend ziehen.]], [[Schaut Euch um, Freund. Die Bäume sind knapp und die Minen voller Legionsabschaum.]], [[Mir gehen die Vorräte aus. Ich versuche schon, sie, wo ich kann, zu ersetzen. Aber so langsam müssen wir anfangen, alles, was wir bekommen können, wiederzuverwenden.]], [[Am südlichen Ende des Pfads des Ruhms steht ein Haufen alter Belagerungsmaschinen 'rum. Ein paar der Teile kann man immer noch verwenden. Habt Ihr Lust, mir bei einer kleinen Wiederverwertungsaktion zu helfen?]], },
    { [[Bringt 8 Stück gebrauchtes Metall und 8 Stück gebrauchtes Holz zu Dumphry in der Ehrenfeste.]], },
  },
  [10056] = {
    [[Vorräte des Blutenden Auges]],
    { [[Die orcischen Ruinen von Zeth'Gor im Südosten dienen als Basislager für die immer weiter steigende Flut von Höllenorcs, die aus der Höllenfeuerzitadelle strömt.]], [[Die Söhne Lothars haben sie eine Zeitlang bekämpft, und ich würde ihre Geschichten nicht glauben, wenn ich diese Höllenorcs nicht mit eigenem Auge gesehen hätte.]], [[Etwas hat sie verändert, sie größer gemacht. Ich habe so etwas noch nie gesehen. Wir müssen alles in unserer Macht Stehende tun, um zu verhindern, dass sie noch stärker werden. Geht nach Zeth'Gor und zerstört sämtliche Vorräte, die Ihr dort finden könnt.]], },
    { [[Zerstört 6 Stapel Vorräte des Blutenden Auges und meldet Euch dann bei Leutnant Amadi in der Ehrenfeste zurück.]], },
  },
  [10057] = {
    [[Suche nach den Anführern]],
    { [[Trotz Eurer Bemühungen haben wir immer noch Probleme bei der Expeditionsrüstkammer. Niemand weiß mit Sicherheit, was dort vor sich geht, was genau die Geister der gefallenen Soldaten an die Ruinen bindet.]], [[Vielleicht gehen wir die Sache falsch an. In meiner Vision habe ich gesehen, dass jeder Geist eine Verbindung zu den Ruinen hat, aber was ist, wenn eine Verbindung zu ihren Anführern besteht?]], [[Erzmagier Xintor und Feldkommandant Thalvos standen ihren Männern besonders nahe. Vielleicht bringt die Vernichtung ihrer Geister die anderen zur Ruhe.]], },
    { [[Besiegt die Geister von Erzmagier Xintor und Feldkommandant Thalvos und kehrt dann zu Ehrenwache Wesilow im Turm nordwestlich der Ehrenfeste zurück.]], },
  },
  [10058] = {
    [[Ein altes Geschenk]],
    { [[<Der Blick des Priesters wandert langsam von seiner Tasse zu Euch.>]], [[Habt Ihr nichts Besseres zu tun, als einen Mann von seiner Tasse fernzuhalten?]], [[Ach, was soll's. Ich bin der Meinung, dass Neuankömmlinge ein gutes Zeichen sind. Was würde Turalyon davon halten?]], [[Ich erinnere mich noch an den Tag unserer Abreise, als ich ihm die Geheimnisse des Lichts gab. Während des Feldzugs und den folgenden Jahren hat er nicht einen einzigen Tag vergessen, seinen Mannen einen Abschnitt daraus vorzulesen.]], [[Als er uns wieder verließ, gab er mir das Buch zurück, damit ich bis zu seiner Rückkehr darauf aufpasse, aber ich Dummkopf dachte, es sei in meinem Quartier in der Waffenkammer sicher...]], },
    { [[Besorgt die Geheimnisse des Lichts und bringt das Buch zu Vater Malgor Devidicus in der Ehrenfeste zurück.]], },
  },
  [10059] = {
    [[Zeth'Gor bewältigen]],
    { [[Es ist unerlässlich, dass wir den Vorteil, den wir durch Euren Sieg über Nekrogg errungen haben, ausnutzen, <name>. Wenn wir Glück haben, hat die Führung in Zeth'Gor noch nichts von Nekrogg erfahren.]], [[Die Knochenmalmer in Zeth'Gor werden normalerweise von Grillok "Finsterauge" in die Schlacht geführt. Findet ihn in seinem Lager, einer Höhle im Felsen unter dem Außenposten, und tötet ihn. Durchsucht die Höhle nach den Schlachtplänen der Höllenorcs, wenn er tot ist. Thrallmar darf nicht überrascht werden.]], },
    { [[Tötet Grillok "Finsterauge" und durchsucht seinen Unterschlupf nach den Angriffsplänen des Blutenden Auges.]], },
  },
  [10060] = {
    [[Drohende Verdammnis]],
    { [[Eine schnelle Überprüfung der Pläne zeigt, dass der Anführer von Zeth'Gor, Kriegsherr Morkh, das Genie hinter den Angriffen, die von Knochenmagier Warlor geleitet werden, ist. Auf der Karte sind mehr als nur Morkhs Streitkräfte zu sehen.]], [[Die Pläne deuten an, dass die Streitkräfte der Knochenmalmer bewaffnet und zusammen mit den Kräften der Höllenfeuerzitadelle bereit für einen Angriff auf Thrallmar sind. Ihr müsst die Knochenmalmer aufhalten, bevor sie ihren Angriff starten können!]], },
    { [[Tötet 15 Grunzer des Blutenden Auges, 10 Nekrolyten des Blutenden Auges, 5 Peons des Blutenden Auges und Kriegsherr Morkh. Meldet Euch danach bei Steingardist Ambelan im Rückenbrechergrat.]], },
  },
  [10061] = {
    [[Die Unerschütterlichen]],
    { [[Als die Höllenorcs der Knochenmalmer die Expeditionswaffenkammer angegriffen haben, fiel unsere Garnison sehr schnell. Es war eine kurze, blutige und grausame Schlacht. Es gab keine Überlebenden.]], [[Obwohl wir die Toten begraben und uns an den Höllenorcs gerächt haben, spuken die Geister der Gefallenen immer noch in den Ruinen. Seit Kurzem habe ich Visionen, in denen diese Geister zu einer Gefahr für Thrallmar werden.]], [[Es gibt nur einen Weg, um die Geister zur Ruhe zu bringen - Ihr müsst sie im Kampf besiegen.]], },
    { [[Grelag möchte, dass Ihr 12 unerschütterliche Fußsoldaten, 10 unerschütterliche Zauberer und 5 unerschütterliche Ritter bei der Expeditionsrüstkammer südlich der Ehrenfeste tötet.]], },
  },
  [10062] = {
    [[Suche nach den Anführern]],
    { [[Trotz Eurer Bemühungen haben wir immer noch Probleme bei der Expeditionsrüstkammer. Niemand weiß mit Sicherheit, was dort vor sich geht, was genau die Geister der gefallenen Soldaten an die Ruinen bindet.]], [[Vielleicht gehen wir die Sache falsch an. In meiner Vision habe ich gesehen, dass jeder Geist eine Verbindung zu den Ruinen hat, aber was ist, wenn eine Verbindung zu ihren Anführern besteht?]], [[Erzmagier Xintor und Feldkommandant Thalvos standen ihren Männern besonders nahe. Vielleicht bringt die Vernichtung ihrer Geister die anderen zur Ruhe.]], },
    { [[Besiegt die Geister von Erzmagier Xintor und Feldkommandant Thalvos und kehrt dann zu Grelag im Rückenbrechergrat zurück.]], },
  },
  [10063] = {
    [[Forscherliga, für Gnome geeignet?]],
    { [[Kindermädchen! Glaubt Ihr das?]], [[Sie haben mich von einer viel versprechenden Grabung abgezogen und mit Odesyus' Expedition losgeschickt, um das Kindermädchen für diesen kleinen Gnom zu spielen!]], [[Ich habe noch nie von einem Gnom gehört, der Ausgrabungsleiter werden wollte, Ihr etwa?]], [[Anscheinend nehmen wir jetzt auch Gnome und dieser ist wohl besonders von Murlocs fasziniert!]], [[Nun gut, er ist losgezogen und hat sich in einem alten Schildkrötenpanzer an der nördlichen Küste der Insel eingerichtet. Geht und seht selbst, wenn Ihr mir nicht glaubt!]], },
    { [[Zieht los und sucht nach Klopper Knacksklug, der sich in einem alten Schildkrötenpanzer an der Küste nördlich der Blutwacht eingerichtet hat.]], },
  },
  [10064] = {
    [[Sprecht zu der Hand]],
    { [[Die Hand von Argus hat bei Verteidigers Ruh' einen Vorposten eingerichtet, allerdings sind sie dort den Blutelfen und auch der Wildnis zahlenmäßig weit unterlegen. Ihrem Posten droht ohne Verstärkung bald der Fall und somit würden wir die Ereignisse bei der Vektorspule nicht länger im Auge behalten können.]], [[Erstattet Späherin Jorli bei Verteidigers Ruh' im Nordwesten Bericht und bietet ihr Eure Hilfe an, <name>.]], },
    { [[Erstattet Späherin Jorli bei Verteidigers Ruh' Bericht.]], },
  },
  [10065] = {
    [[Eine Schneise schlagen]],
    { [[Während unserer Mission, die Sonnenfalken und ihr Portal auszukundschaften, wurde ich ernsthaft verletzt. Späherin Loryi konnte glücklicherweise die Blutung stillen und sucht gerade nach einem Weg, um mich zur Blutwacht zu bringen.]], [[Unser Problem liegt allerdings darin, dass wir unsere Stellung hier verlassen müssen, um eine sichere Rückkehr garantieren zu können.]], [[Sobald wir diesen sicheren Grund verlassen, werden uns Felshetzer anfallen. Wenn Ihr ein paar der wütenden Felshetzer aus dem Weg räumen könntet, würden wir höchstwahrscheinlich sicher an unser Ziel gelangen.]], },
    { [[Tötet 10 wütende Felshetzer und kehrt zu Späherin Jorli bei Verteidigers Ruh' zurück.]], },
  },
  [10066] = {
    [[Alles im Griff]],
    { [[Wir wagen es nicht, unseren Posten zu verlassen oder uns zu weit von Verteidigers Ruh' zu entfernen, <race>. Die mutierten Kreaturen, die sich in der unmittelbaren Nähe der Vektorspule aufhielten, wurden schwerwiegend von der Energie beeinflusst, die den Kristallen entweicht.]], [[Die mutierten Greifer scheinen bis zu einem gewissen Grad Empfindungen entwickelt zu haben. Sie haben gelernt, tückische Fallen zu erschaffen, aufzustellen und zu verbergen, damit sich selbst die umsichtigsten Kämpfer darin verfangen.]], [[Wir werden niemals gegen die Sonnenfalken vorgehen können, wenn wir ständig auf unsere Schritte Acht geben müssen. Packt das Problem bei der Wurzel, <name>.]], },
    { [[Tötet 8 mutierte Greifer und meldet Euch bei Verteidigerin Corin bei Verteidigers Ruh'.]], },
  },
  [10067] = {
    [[Besudelte Wassergeister]],
    { [[Die meisten der Kreaturen, die von der Energie der Kristalle berührt wurden, wurden auf die gleiche Art verdorben: seltsame Male auf ihrer Haut, aggressiveres Verhalten und kleine Kristallsplitter in ihren Körpern. Wie dem auch sei, die besudelten Wassergeister der Blutmythosinsel scheinen sich vollständig in Kristall zu verwandeln.]], [[Ich habe bis jetzt noch nichts gesehen, was derart auf die Kristalle reagiert hat. Als wir den Besudelten Teich untersucht haben, haben sie sich als besonders gefährlich herausgestellt. Wir müssen sie loswerden, bevor die Sonnenfalken sie als neue Energiequelle missbrauchen können.]], },
    { [[Tötet 6 besudelte Wassergeister und kehrt zu Verteidigerin Corin bei Verteidigers Ruh' zurück.]], },
  },
  [10068] = {
    [[Verderbnis]],
    { [[Mittlerweile habt Ihr die Gerüchte über unsereins, die Hexenmeister, gehört. Schenkt ihnen keine Beachtung; es sind diejenigen, die neidisch auf unsere furchteinflößende Macht sind, die solche Lügen verbreiten.]], [[Wenn Ihr Erfahrungen sammelt, werdet Ihr die Mysterien entschlüsseln, die es Euch erlauben, die Teufelsenergien zu kontrollieren.]], [[Geht, setzt Euren Weg fort, bis Ihr eine neue Technik entdeckt habt. Ich möchte, dass Ihr diesen neuen Zauber an den Trainingsattrappen vor dem Sonnenturm ausprobiert.]], },
    { [[Erreicht Stufe 3, um 'Verderbnis' zu erlernen. Macht eine Trainingsattrappe vor dem Sonnenturm ausfindig und übt daran fünfmal das Wirken von 'Verderbnis'.]], },
  },
